INSERT INTO Quran__Arabic_Tafseer_Muyassar (QuranChapterID, VerseNumber, Rukuh, Sajdah, Manzil, HizbQuarter, VerseText) VALUES (1, 1, '0', '0', '1', '1', 'سورة الفاتحة سميت هذه السورة بالفاتحة؛ لأنه يفتتح بها القرآن العظيم، وتسمى المثاني؛ لأنها تقرأ في كل ركعة، ولها أسماء أخر. أبتدئ قراءة القرآن باسم الله مستعينا به، (اللهِ) علم على الرب -تبارك وتعالى- المعبود بحق دون سواه، وهو أخص أسماء الله تعالى، ولا يسمى به غيره سبحانه. (الرَّحْمَنِ) ذي الرحمة العامة الذي وسعت رحمته جميع الخلق، (الرَّحِيمِ) بالمؤمنين، وهما اسمان من أسمائه تعالى، يتضمنان إثبات صفة الرحمة لله تعالى كما يليق بجلاله.');
INSERT INTO Quran__Arabic_Tafseer_Muyassar (QuranChapterID, VerseNumber, Rukuh, Sajdah, Manzil, HizbQuarter, VerseText) VALUES (1, 2, '0', '0', '0', '0', '(الحَمْدُ للهِ رَبِّ العَالَمِينَ) الثناء على الله بصفاته التي كلُّها أوصاف كمال، وبنعمه الظاهرة والباطنة، الدينية والدنيوية، وفي ضمنه أَمْرٌ لعباده أن يحمدوه، فهو المستحق له وحده، وهو سبحانه المنشئ للخلق، القائم بأمورهم، المربي لجميع خلقه بنعمه، ولأوليائه بالإيمان والعمل الصالح.');
INSERT INTO Quran__Arabic_Tafseer_Muyassar (QuranChapterID, VerseNumber, Rukuh, Sajdah, Manzil, HizbQuarter, VerseText) VALUES (1, 3, '0', '0', '0', '0', '(الرَّحْمَنِ) الذي وسعت رحمته جميع الخلق، (الرَّحِيمِ)، بالمؤمنين، وهما اسمان من أسماء الله تعالى.');
INSERT INTO Quran__Arabic_Tafseer_Muyassar (QuranChapterID, VerseNumber, Rukuh, Sajdah, Manzil, HizbQuarter, VerseText) VALUES (1, 4, '0', '0', '0', '0', 'وهو سبحانه وحده مالك يوم القيامة، وهو يوم الجزاء على الأعمال. وفي قراءة المسلم لهذه الآية في كل ركعة من صلواته تذكير له باليوم الآخر، وحثٌّ له على الاستعداد بالعمل الصالح، والكف عن المعاصي والسيئات.');
INSERT INTO Quran__Arabic_Tafseer_Muyassar (QuranChapterID, VerseNumber, Rukuh, Sajdah, Manzil, HizbQuarter, VerseText) VALUES (1, 5, '0', '0', '0', '0', 'إنا نخصك وحدك بالعبادة، ونستعين بك وحدك في جميع أمورنا، فالأمر كله بيدك، لا يملك منه أحد مثقال ذرة. وفي هذه الآية دليل على أن العبد لا يجوز له أن يصرف شيئًا من أنواع العبادة كالدعاء والاستغاثة والذبح والطواف إلا لله وحده، وفيها شفاء القلوب من داء التعلق بغير الله، ومن أمراض الرياء والعجب، والكبرياء.');
INSERT INTO Quran__Arabic_Tafseer_Muyassar (QuranChapterID, VerseNumber, Rukuh, Sajdah, Manzil, HizbQuarter, VerseText) VALUES (1, 6, '0', '0', '0', '0', 'دُلَّنا، وأرشدنا، ووفقنا إلى الطريق المستقيم، وثبتنا عليه حتى نلقاك، وهو الإسلام، الذي هو الطريق الواضح الموصل إلى رضوان الله وإلى جنته، الذي دلّ عليه خاتم رسله وأنبيائه محمد صلى الله عليه وسلم، فلا سبيل إلى سعادة العبد إلا بالاستقامة عليه.');
INSERT INTO Quran__Arabic_Tafseer_Muyassar (QuranChapterID, VerseNumber, Rukuh, Sajdah, Manzil, HizbQuarter, VerseText) VALUES (1, 7, '1', '0', '0', '0', 'طريق الذين أنعمت عليهم من النبيين والصدِّيقين والشهداء والصالحين، فهم أهل الهداية والاستقامة، ولا تجعلنا ممن سلك طريق المغضوب عليهم، الذين عرفوا الحق ولم يعملوا به، وهم اليهود، ومن كان على شاكلتهم، والضالين، وهم الذين لم يهتدوا، فضلوا الطريق، وهم النصارى، ومن اتبع سنتهم. وفي هذا الدعاء شفاء لقلب المسلم من مرض الجحود والجهل والضلال، ودلالة على أن أعظم نعمة على الإطلاق هي نعمة الإسلام، فمن كان أعرف للحق وأتبع له، كان أولى بالصراط المستقيم، ولا ريب أن أصحاب رسول الله صلى الله عليه وسلم هم أولى الناس بذلك بعد الأنبياء عليهم السلام، فدلت الآية على فضلهم، وعظيم منزلتهم، رضي الله عنهم. ويستحب للقارئ أن يقول في الصلاة بعد قراءة الفاتحة: (آمين)، ومعناها: اللهم استجب، وليست آية من سورة الفاتحة باتفاق العلماء؛ ولهذا أجمعوا على عدم كتابتها في المصاحف.');
INSERT INTO Quran__Arabic_Tafseer_Muyassar (QuranChapterID, VerseNumber, Rukuh, Sajdah, Manzil, HizbQuarter, VerseText) VALUES (2, 1, '0', '0', '0', '0', 'هذه الحروف وغيرها من الحروف المقطَّعة في أوائل السور فيها إشارة إلى إعجاز القرآن؛ فقد وقع به تحدي المشركين، فعجزوا عن معارضته، وهو مركَّب من هذه الحروف التي تتكون منها لغة العرب. فدَلَّ عجز العرب عن الإتيان بمثله -مع أنهم أفصح الناس- على أن القرآن وحي من الله.');
INSERT INTO Quran__Arabic_Tafseer_Muyassar (QuranChapterID, VerseNumber, Rukuh, Sajdah, Manzil, HizbQuarter, VerseText) VALUES (2, 2, '0', '0', '0', '0', 'ذلك القرآن هو الكتاب العظيم الذي لا شَكَّ أنه من عند الله، فلا يصح أن يرتاب فيه أحد لوضوحه، ينتفع به المتقون بالعلم النافع والعمل الصالح وهم الذين يخافون الله، ويتبعون أحكامه.');
INSERT INTO Quran__Arabic_Tafseer_Muyassar (QuranChapterID, VerseNumber, Rukuh, Sajdah, Manzil, HizbQuarter, VerseText) VALUES (2, 3, '0', '0', '0', '0', 'وهم الذين يُصَدِّقون بالغيب الذي لا تدركه حواسُّهم ولا عقولهم وحدها؛ لأنه لا يُعْرف إلا بوحي الله إلى رسله، مثل الإيمان بالملائكة، والجنة، والنار، وغير ذلك مما أخبر الله به أو أخبر به رسوله، (والإيمان: كلمة جامعة للإقرار بالله وملائكته وكتبه ورسله واليوم الآخر والقدر خيره وشره، وتصديق الإقرار بالقول والعمل بالقلب واللسان والجوارح) وهم مع تصديقهم بالغيب يحافظون على أداء الصلاة في مواقيتها أداءً صحيحًا وَفْق ما شرع الله لنبيه محمد صلى الله عليه وسلم، ومما أعطيناهم من المال يخرجون صدقة أموالهم الواجبة والمستحبة.');
INSERT INTO Quran__Arabic_Tafseer_Muyassar (QuranChapterID, VerseNumber, Rukuh, Sajdah, Manzil, HizbQuarter, VerseText) VALUES (2, 4, '0', '0', '0', '0', 'والذين يُصَدِّقون بما أُنزل إليك أيها الرسول من القرآن، وبما أنزل إليك من الحكمة، وهي السنة، وبكل ما أُنزل مِن قبلك على الرسل من كتب، كالتوراة والإنجيل وغيرهما، ويُصَدِّقون بدار الحياة بعد الموت وما فيها من الحساب والجزاء، تصديقا بقلوبهم يظهر على ألسنتهم وجوارحهم وخص يوم الآخرة؛ لأن الإيمان به من أعظم البواعث على فعل الطاعات، واجتناب المحرمات، ومحاسبة النفس.');
INSERT INTO Quran__Arabic_Tafseer_Muyassar (QuranChapterID, VerseNumber, Rukuh, Sajdah, Manzil, HizbQuarter, VerseText) VALUES (2, 5, '0', '0', '0', '0', 'أصحاب هذه الصفات يسيرون على نور من ربهم وبتوفيق مِن خالقهم وهاديهم، وهم الفائزون الذين أدركوا ما طلبوا، ونَجَوا من شرِّ ما منه هربوا.');
INSERT INTO Quran__Arabic_Tafseer_Muyassar (QuranChapterID, VerseNumber, Rukuh, Sajdah, Manzil, HizbQuarter, VerseText) VALUES (2, 6, '0', '0', '0', '0', 'إن الذين جحدوا ما أُنزل إليك من ربك استكبارًا وطغيانًا، لن يقع منهم الإيمان، سواء أخوَّفتهم وحذرتهم من عذاب الله، أم تركت ذلك؛ لإصرارهم على باطلهم.');
INSERT INTO Quran__Arabic_Tafseer_Muyassar (QuranChapterID, VerseNumber, Rukuh, Sajdah, Manzil, HizbQuarter, VerseText) VALUES (2, 7, '1', '0', '0', '0', 'طبع الله على قلوب هؤلاء وعلى سمعهم، وجعل على أبصارهم غطاء؛ بسبب كفرهم وعنادهم مِن بعد ما تبيَّن لهم الحق، فلم يوفقهم للهدى، ولهم عذاب شديد في نار جهنم.');
INSERT INTO Quran__Arabic_Tafseer_Muyassar (QuranChapterID, VerseNumber, Rukuh, Sajdah, Manzil, HizbQuarter, VerseText) VALUES (2, 8, '0', '0', '0', '0', 'ومن الناس فريق يتردد متحيِّرًا بين المؤمنين والكافرين، وهم المنافقون الذين يقولون بألسنتهم: صدَّقْنَا بالله وباليوم الآخر، وهم في باطنهم كاذبون لم يؤمنوا.');
INSERT INTO Quran__Arabic_Tafseer_Muyassar (QuranChapterID, VerseNumber, Rukuh, Sajdah, Manzil, HizbQuarter, VerseText) VALUES (2, 9, '0', '0', '0', '0', 'يعتقدون بجهلهم أنهم يخادعون الله والذين آمنوا بإظهارهم الإيمان وإضمارهم الكفر، وما يخدعون إلا أنفسهم؛ لأن عاقبة خداعهم تعود عليهم. ومِن فرط جهلهم لا يُحِسُّون بذلك؛ لفساد قلوبهم.');
INSERT INTO Quran__Arabic_Tafseer_Muyassar (QuranChapterID, VerseNumber, Rukuh, Sajdah, Manzil, HizbQuarter, VerseText) VALUES (2, 10, '0', '0', '0', '0', 'في قلوبهم شكٌّ وفساد فابْتُلوا بالمعاصي الموجبة لعقوبتهم، فزادهم الله شكًا، ولهم عقوبة موجعة بسبب كذبهم ونفاقهم.');
INSERT INTO Quran__Arabic_Tafseer_Muyassar (QuranChapterID, VerseNumber, Rukuh, Sajdah, Manzil, HizbQuarter, VerseText) VALUES (2, 11, '0', '0', '0', '0', 'وإذا نُصحوا ليكفُّوا عن الإفساد في الأرض بالكفر والمعاصي، وإفشاء أسرار المؤمنين، وموالاة الكافرين، قالوا كذبًا وجدالا إنما نحن أهل الإصلاح.');
INSERT INTO Quran__Arabic_Tafseer_Muyassar (QuranChapterID, VerseNumber, Rukuh, Sajdah, Manzil, HizbQuarter, VerseText) VALUES (2, 12, '0', '0', '0', '0', 'إنَّ هذا الذي يفعلونه ويزعمون أنه إصلاح هو عين الفساد، لكنهم بسبب جهلهم وعنادهم لا يُحِسُّون.');
INSERT INTO Quran__Arabic_Tafseer_Muyassar (QuranChapterID, VerseNumber, Rukuh, Sajdah, Manzil, HizbQuarter, VerseText) VALUES (2, 13, '0', '0', '0', '0', 'وإذا قيل للمنافقين: آمِنُوا -مثل إيمان الصحابة، وهو الإيمان بالقلب واللسان والجوارح-، جادَلوا وقالوا: أَنُصَدِّق مثل تصديق ضعاف العقل والرأي، فنكون نحن وهم في السَّفَهِ سواء؟ فردَّ الله عليهم بأن السَّفَهَ مقصور عليهم، وهم لا يعلمون أن ما هم فيه هو الضلال والخسران.');
INSERT INTO Quran__Arabic_Tafseer_Muyassar (QuranChapterID, VerseNumber, Rukuh, Sajdah, Manzil, HizbQuarter, VerseText) VALUES (2, 14, '0', '0', '0', '0', 'هؤلاء المنافقون إذا قابلوا المؤمنين قالوا: صدَّقنا بالإسلام مثلكم، وإذا انصرفوا وذهبوا إلى زعمائهم الكفرة المتمردين على الله أكَّدوا لهم أنهم على ملة الكفر لم يتركوها، وإنما كانوا يَسْتَخِفُّون بالمؤمنين، ويسخرون منهم.');
INSERT INTO Quran__Arabic_Tafseer_Muyassar (QuranChapterID, VerseNumber, Rukuh, Sajdah, Manzil, HizbQuarter, VerseText) VALUES (2, 15, '0', '0', '0', '0', 'الله يستهزئ بهم ويُمهلهم؛ ليزدادوا ضلالا وحَيْرة وترددًا، ويجازيهم على استهزائهم بالمؤمنين.');
INSERT INTO Quran__Arabic_Tafseer_Muyassar (QuranChapterID, VerseNumber, Rukuh, Sajdah, Manzil, HizbQuarter, VerseText) VALUES (2, 16, '0', '0', '0', '0', 'أولئك المنافقون باعوا أنفسهم في صفقة خاسرة، فأخذوا الكفر، وتركوا الإيمان، فما كسبوا شيئًا، بل خَسِروا الهداية. وهذا هو الخسران المبين.');
INSERT INTO Quran__Arabic_Tafseer_Muyassar (QuranChapterID, VerseNumber, Rukuh, Sajdah, Manzil, HizbQuarter, VerseText) VALUES (2, 17, '0', '0', '0', '0', 'حال المنافقين الذين آمنوا -ظاهرًا لا باطنًا- برسالة محمد صلى الله عليه وسلم، ثم كفروا، فصاروا يتخبطون في ظلماتِ ضلالهم وهم لا يشعرون، ولا أمل لهم في الخروج منها، تُشْبه حالَ جماعة في ليلة مظلمة، وأوقد أحدهم نارًا عظيمة للدفء والإضاءة، فلما سطعت النار وأنارت ما حوله، انطفأت وأعتمت، فصار أصحابها في ظلمات لا يرون شيئًا، ولا يهتدون إلى طريق ولا مخرج.');
INSERT INTO Quran__Arabic_Tafseer_Muyassar (QuranChapterID, VerseNumber, Rukuh, Sajdah, Manzil, HizbQuarter, VerseText) VALUES (2, 18, '0', '0', '0', '0', 'هم صُمٌّ عن سماع الحق سماع تدبر، بُكْم عن النطق به، عُمْي عن إبصار نور الهداية؛ لذلك لا يستطيعون الرجوع إلى الإيمان الذي تركوه، واستعاضوا عنه بالضلال.');
INSERT INTO Quran__Arabic_Tafseer_Muyassar (QuranChapterID, VerseNumber, Rukuh, Sajdah, Manzil, HizbQuarter, VerseText) VALUES (2, 19, '0', '0', '0', '0', 'أو تُشْبه حالُ فريق آخر من المنافقين يظهر لهم الحق تارة، ويشكون فيه تارة أخرى، حالَ جماعة يمشون في العراء، فينصب عليهم مطر شديد، تصاحبه ظلمات بعضها فوق بعض، مع قصف الرعد، ولمعان البرق، والصواعق المحرقة، التي تجعلهم من شدة الهول يضعون أصابعهم في آذانهم؛ خوفًا من الهلاك. والله تعالى محيط بالكافرين لا يفوتونه ولا يعجزونه.');
INSERT INTO Quran__Arabic_Tafseer_Muyassar (QuranChapterID, VerseNumber, Rukuh, Sajdah, Manzil, HizbQuarter, VerseText) VALUES (2, 20, '1', '0', '0', '0', 'يقارب البرق -من شدة لمعانه- أن يسلب أبصارهم، ومع ذلك فكلَّما أضاء لهم مشَوْا في ضوئه، وإذا ذهب أظلم الطريق عليهم فيقفون في أماكنهم. ولولا إمهال الله لهم لسلب سمعهم وأبصارهم، وهو قادر على ذلك في كل وقتٍ، إنه على كل شيء قدير.');
INSERT INTO Quran__Arabic_Tafseer_Muyassar (QuranChapterID, VerseNumber, Rukuh, Sajdah, Manzil, HizbQuarter, VerseText) VALUES (2, 21, '0', '0', '0', '0', 'نداء من الله للبشر جميعًا: أن اعبدوا الله الذي ربَّاكم بنعمه، وخافوه ولا تخالفوا دينه؛ فقد أوجدكم من العدم، وأوجد الذين من قبلكم؛ لتكونوا من المتقين الذين رضي الله عنهم ورضوا عنه.');
INSERT INTO Quran__Arabic_Tafseer_Muyassar (QuranChapterID, VerseNumber, Rukuh, Sajdah, Manzil, HizbQuarter, VerseText) VALUES (2, 22, '0', '0', '0', '0', 'ربكم الذي جعل لكم الأرض بساطًا؛ لتسهل حياتكم عليها، والسماء محكمة البناء، وأنزل المطر من السحاب فأخرج لكم به من ألوان الثمرات وأنواع النبات رزقًا لكم، فلا تجعلوا لله نظراء في العبادة، وأنتم تعلمون تفرُّده بالخلق والرزق، واستحقاقِه العبودية.');
INSERT INTO Quran__Arabic_Tafseer_Muyassar (QuranChapterID, VerseNumber, Rukuh, Sajdah, Manzil, HizbQuarter, VerseText) VALUES (2, 23, '0', '0', '0', '0', 'وإن كنتم -أيها الكافرون المعاندون- في شَكٍّ من القرآن الذي نَزَّلناه على عبدنا محمد صلى الله عليه وسلم، وتزعمون أنه ليس من عند الله، فهاتوا سورة تماثل سورة من القرآن، واستعينوا بمن تقدرون عليه مِن أعوانكم، إن كنتم صادقين في دعواكم.');
INSERT INTO Quran__Arabic_Tafseer_Muyassar (QuranChapterID, VerseNumber, Rukuh, Sajdah, Manzil, HizbQuarter, VerseText) VALUES (2, 24, '0', '0', '0', '0', 'فإن عجَزتم الآن -وستعجزون مستقبلا لا محالة- فاتقوا النار بالإيمان بالنبي صلى الله عليه وسلم وطاعة الله تعالى. هذه النار التي حَطَبُها الناس والحجارة، أُعِدَّتْ للكافرين بالله ورسله.');
INSERT INTO Quran__Arabic_Tafseer_Muyassar (QuranChapterID, VerseNumber, Rukuh, Sajdah, Manzil, HizbQuarter, VerseText) VALUES (2, 25, '0', '0', '0', '0', 'وأخبر -أيها الرسول- أهل الإيمان والعمل الصالح خبرًا يملؤهم سرورًا، بأن لهم في الآخرة حدائق عجيبة، تجري الأنهار تحت قصورها العالية وأشجارها الظليلة. كلَّما رزقهم الله فيها نوعًا من الفاكهة اللذيذة قالوا: قد رَزَقَنا الله هذا النوع من قبل، فإذا ذاقوه وجدوه شيئًا جديدًا في طعمه ولذته، وإن تشابه مع سابقه في اللون والمنظر والاسم. ولهم في الجنَّات زوجات مطهَّرات من كل ألوان الدنس الحسيِّ كالبول والحيض، والمعنوي كالكذب وسوء الخُلُق. وهم في الجنة ونعيمها دائمون، لا يموتون فيها ولا يخرجون منها.');
INSERT INTO Quran__Arabic_Tafseer_Muyassar (QuranChapterID, VerseNumber, Rukuh, Sajdah, Manzil, HizbQuarter, VerseText) VALUES (2, 26, '0', '0', '0', '1', 'إن الله تعالى لا يستحيي من الحق أن يذكر شيئًا ما، قلَّ أو كثر، ولو كان تمثيلا بأصغر شيء، كالبعوضة والذباب ونحو ذلك، مما ضربه الله مثلا لِعَجْز كل ما يُعْبَد من دون الله. فأما المؤمنون فيعلمون حكمة الله في التمثيل بالصغير والكبير من خلقه، وأما الكفار فَيَسْخرون ويقولون: ما مراد الله مِن ضَرْب المثل بهذه الحشرات الحقيرة؟ ويجيبهم الله بأن المراد هو الاختبار، وتمييز المؤمن من الكافر؛ لذلك يصرف الله بهذا المثل ناسًا كثيرين عن الحق لسخريتهم منه، ويوفق به غيرهم إلى مزيد من الإيمان والهداية. والله تعالى لا يظلم أحدًا؛ لأنه لا يَصْرِف عن الحق إلا الخارجين عن طاعته.');
INSERT INTO Quran__Arabic_Tafseer_Muyassar (QuranChapterID, VerseNumber, Rukuh, Sajdah, Manzil, HizbQuarter, VerseText) VALUES (2, 27, '0', '0', '0', '0', 'الذين ينكثون عهد الله الذي أخذه عليهم بالتوحيد والطاعة، وقد أكَّده بإرسال الرسل، وإنزال الكتب، ويخالفون دين الله كقطع الأرحام ونشر الفساد في الأرض، أولئك هم الخاسرون في الدنيا والآخرة.');
INSERT INTO Quran__Arabic_Tafseer_Muyassar (QuranChapterID, VerseNumber, Rukuh, Sajdah, Manzil, HizbQuarter, VerseText) VALUES (2, 28, '0', '0', '0', '0', 'كيف تنكرون -أيُّها المشركون- وحدانية الله تعالى، وتشركون به غيره في العبادة مع البرهان القاطع عليها في أنفسكم؟ فلقد كنتم أمواتًا فأوجدكم ونفخ فيكم الحياة، ثم يميتكم بعد انقضاء آجالكم التي حددها لكم، ثم يعيدكم أحياء يوم البعث، ثم إليه ترجعون للحساب والجزاء.');
INSERT INTO Quran__Arabic_Tafseer_Muyassar (QuranChapterID, VerseNumber, Rukuh, Sajdah, Manzil, HizbQuarter, VerseText) VALUES (2, 29, '1', '0', '0', '0', 'اللهُ وحده الذي خَلَق لأجلكم كل ما في الأرض من النِّعم التي تنتفعون بها، ثم قصد إلى خلق السموات، فسوَّاهنَّ سبع سموات، وهو بكل شيء عليم. فعِلْمُه -سبحانه- محيط بجميع ما خلق.');
INSERT INTO Quran__Arabic_Tafseer_Muyassar (QuranChapterID, VerseNumber, Rukuh, Sajdah, Manzil, HizbQuarter, VerseText) VALUES (2, 30, '0', '0', '0', '0', 'واذكر -أيها الرسول- للناس حين قال ربك للملائكة: إني جاعل في الأرض قومًا يخلف بعضهم بعضًا لعمارتها. قالت: يا ربَّنا علِّمْنا وأَرْشِدْنا ما الحكمة في خلق هؤلاء، مع أنَّ من شأنهم الإفساد في الأرض واراقة الدماء ظلما وعدوانًا ونحن طوع أمرك، ننزِّهك التنزيه اللائق بحمدك وجلالك، ونمجِّدك بكل صفات الكمال والجلال؟ قال الله لهم: إني أعلم ما لا تعلمون من الحكمة البالغة في خلقهم.');
INSERT INTO Quran__Arabic_Tafseer_Muyassar (QuranChapterID, VerseNumber, Rukuh, Sajdah, Manzil, HizbQuarter, VerseText) VALUES (2, 31, '0', '0', '0', '0', 'وبيانًا لفضل آدم عليه السلام علَّمه الله أسماء الأشياء كلها، ثم عرض مسمياتها على الملائكة قائلا لهم: أخبروني بأسماء هؤلاء الموجودات، إن كنتم صادقين في أنكم أَوْلى بالاستخلاف في الأرض منهم.');
INSERT INTO Quran__Arabic_Tafseer_Muyassar (QuranChapterID, VerseNumber, Rukuh, Sajdah, Manzil, HizbQuarter, VerseText) VALUES (2, 32, '0', '0', '0', '0', 'قالت الملائكة: ننزِّهك يا ربَّنا، ليس لنا علم إلا ما علَّمتنا إياه. إنك أنت وحدك العليم بشئون خلقك، الحكيم في تدبيرك.');
INSERT INTO Quran__Arabic_Tafseer_Muyassar (QuranChapterID, VerseNumber, Rukuh, Sajdah, Manzil, HizbQuarter, VerseText) VALUES (2, 33, '0', '0', '0', '0', 'قال الله: يا آدم أخبرهم بأسماء هذه الأشياء التي عجَزوا عن معرفتها. فلما أخبرهم آدم بها، قال الله للملائكة: لقد أخبرتكم أني أعلم ما خفي عنكم في السموات والأرض، وأعلم ما تظهرونه وما تخفونه.');
INSERT INTO Quran__Arabic_Tafseer_Muyassar (QuranChapterID, VerseNumber, Rukuh, Sajdah, Manzil, HizbQuarter, VerseText) VALUES (2, 34, '0', '0', '0', '0', 'واذكر -أيها الرسول- للناس تكريم الله لآدم حين قال سبحانه للملائكة: اسجدوا لآدم إكرامًا له وإظهارًا لفضله، فأطاعوا جميعًا إلا إبليس امتنع عن السجود تكبرًا وحسدًا، فصار من الجاحدين بالله، العاصين لأمره.');
INSERT INTO Quran__Arabic_Tafseer_Muyassar (QuranChapterID, VerseNumber, Rukuh, Sajdah, Manzil, HizbQuarter, VerseText) VALUES (2, 35, '0', '0', '0', '0', 'وقال الله: يا آدم اسكن أنت وزوجك حواء الجنة، وتمتعا بثمارها تمتعًا هنيئًا واسعًا في أي مكان تشاءان فيها، ولا تقربا هذه الشجرة حتى لا تقعا في المعصية، فتصيرا من المتجاوزين أمر الله.');
INSERT INTO Quran__Arabic_Tafseer_Muyassar (QuranChapterID, VerseNumber, Rukuh, Sajdah, Manzil, HizbQuarter, VerseText) VALUES (2, 36, '0', '0', '0', '0', 'فأوقعهما الشيطان في الخطيئة: بأنْ وسوس لهما حتى أكلا من الشجرة، فتسبب في إخراجهما من الجنة ونعيمها. وقال الله لهم: اهبطوا إلى الأرض، يعادي بعضكم بعضًا -أي آدم وحواء والشيطان- ولكم في الأرض استقرار وإقامة، وانتفاع بما فيها إلى وقت انتهاء آجالكم.');
INSERT INTO Quran__Arabic_Tafseer_Muyassar (QuranChapterID, VerseNumber, Rukuh, Sajdah, Manzil, HizbQuarter, VerseText) VALUES (2, 37, '0', '0', '0', '0', 'فتلقى آدمُ بالقبول كلماتٍ، ألهمه الله إياها توبة واستغفارًا، وهي قوله تعالى: {رَبَّنَا ظَلَمْنَا أَنْفُسَنَا وَإِنْ لَمْ تَغْفِرْ لَنَا وَتَرْحَمْنَا لَنَكُونَنَّ مِنَ الْخَاسِرِينَ (7:23)} فتاب الله عليه، وغفر له ذنبه إنه تعالى هو التواب لمن تاب مِن عباده، الرحيم بهم.');
INSERT INTO Quran__Arabic_Tafseer_Muyassar (QuranChapterID, VerseNumber, Rukuh, Sajdah, Manzil, HizbQuarter, VerseText) VALUES (2, 38, '0', '0', '0', '0', 'قال الله لهم: اهبطوا من الجنة جميعًا، وسيأتيكم أنتم وذرياتكم المتعاقبة ما فيه هدايتكم إلى الحق. فمن عمل بها فلا خوف عليهم فيما يستقبلونه من أمر الآخرة ولا هم يحزنون على ما فاتهم من أمور الدنيا.');
INSERT INTO Quran__Arabic_Tafseer_Muyassar (QuranChapterID, VerseNumber, Rukuh, Sajdah, Manzil, HizbQuarter, VerseText) VALUES (2, 39, '1', '0', '0', '0', 'والذين جحدوا وكذبوا بآياتنا المتلوة ودلائل توحيدنا، أولئك الذين يلازمون النار، هم فيها خالدون، لا يخرجون منها.');
INSERT INTO Quran__Arabic_Tafseer_Muyassar (QuranChapterID, VerseNumber, Rukuh, Sajdah, Manzil, HizbQuarter, VerseText) VALUES (2, 40, '0', '0', '0', '0', 'يا ذرية يعقوب اذكروا نعمي الكثيرة عليكم، واشكروا لي، وأتموا وصيتي لكم: بأن تؤمنوا بكتبي ورسلي جميعًا، وتعملوا بشرائعي. فإن فعلتم ذلك أُتمم لكم ما وعدتكم به من الرحمة في الدنيا، والنجاة في الآخرة. وإيَّايَ -وحدي- فخافوني، واحذروا نقمتي إن نقضتم العهد، وكفرتم بي.');
INSERT INTO Quran__Arabic_Tafseer_Muyassar (QuranChapterID, VerseNumber, Rukuh, Sajdah, Manzil, HizbQuarter, VerseText) VALUES (2, 41, '0', '0', '0', '0', 'وآمنوا- يا بني إسرائيل- بالقرآن الذي أنزَلْتُه على محمد نبي الله ورسوله، موافقًا لما تعلمونه من صحيح التوراة، ولا تكونوا أول فريق من أهل الكتاب يكفر به، ولا تستبدلوا بآياتي ثمنًا قليلا من حطام الدنيا الزائل، وإياي وحدي فاعملوا بطاعتي واتركوا معصيتي.');
INSERT INTO Quran__Arabic_Tafseer_Muyassar (QuranChapterID, VerseNumber, Rukuh, Sajdah, Manzil, HizbQuarter, VerseText) VALUES (2, 42, '0', '0', '0', '0', 'ولا تخلِطوا الحق الذي بيَّنته لكم بالباطل الذي افتريتموه، واحذروا كتمان الحق الصريح من صفة نبي الله ورسوله محمد صلى الله عليه وسلم التي في كتبكم، وأنتم تجدونها مكتوبة عندكم، فيما تعلمون من الكتب التي بأيديكم.');
INSERT INTO Quran__Arabic_Tafseer_Muyassar (QuranChapterID, VerseNumber, Rukuh, Sajdah, Manzil, HizbQuarter, VerseText) VALUES (2, 43, '0', '0', '0', '0', 'وادخلوا في دين الإسلام: بأن تقيموا الصلاة على الوجه الصحيح، كما جاء بها نبي الله ورسوله محمد صلى الله عليه وسلم، وتؤدوا الزكاة المفروضة على الوجه المشروع، وتكونوا مع الراكعين من أمته صلى الله عليه وسلم.');
INSERT INTO Quran__Arabic_Tafseer_Muyassar (QuranChapterID, VerseNumber, Rukuh, Sajdah, Manzil, HizbQuarter, VerseText) VALUES (2, 44, '0', '0', '0', '1', 'ما أقبح حالَكم وحالَ علمائكم حين تأمرون الناس بعمل الخيرات، وتتركون أنفسكم، فلا تأمرونها بالخير العظيم، وهو الإسلام، وأنتم تقرءون التوراة، التي فيها صفات محمد صلى الله عليه وسلم، ووجوب الإيمان به!! أفلا تستعملون عقولكم استعمالا صحيحًا؟');
INSERT INTO Quran__Arabic_Tafseer_Muyassar (QuranChapterID, VerseNumber, Rukuh, Sajdah, Manzil, HizbQuarter, VerseText) VALUES (2, 45, '0', '0', '0', '0', 'واستعينوا في كل أموركم بالصبر بجميع أنواعه، وكذلك الصلاة. وإنها لشاقة إلا على الخاشعين.');
INSERT INTO Quran__Arabic_Tafseer_Muyassar (QuranChapterID, VerseNumber, Rukuh, Sajdah, Manzil, HizbQuarter, VerseText) VALUES (2, 46, '1', '0', '0', '0', 'الذين يخشون الله ويرجون ما عنده، ويوقنون أنهم ملاقو ربِّهم جلَّ وعلا بعد الموت، وأنهم إليه راجعون يوم القيامة للحساب والجزاء.');
INSERT INTO Quran__Arabic_Tafseer_Muyassar (QuranChapterID, VerseNumber, Rukuh, Sajdah, Manzil, HizbQuarter, VerseText) VALUES (2, 47, '0', '0', '0', '0', 'يا ذرية يعقوب تذكَّروا نعمي الكثيرة عليكم، واشكروا لي عليها، وتذكروا أني فَضَّلْتكم على عالَمي زمانكم بكثرة الأنبياء، والكتب المنزَّلة كالتوراة والإنجيل.');
INSERT INTO Quran__Arabic_Tafseer_Muyassar (QuranChapterID, VerseNumber, Rukuh, Sajdah, Manzil, HizbQuarter, VerseText) VALUES (2, 48, '0', '0', '0', '0', 'وخافوا يوم القيامة، يوم لا يغني أحد عن أحد شيئًا، ولا يقبل الله شفاعة في الكافرين، ولا يقبل منهم فدية، ولو كانت أموال الأرض جميعًا، ولا يملك أحد في هذا اليوم أن يتقدم لنصرتهم وإنقاذهم من العذاب.');
INSERT INTO Quran__Arabic_Tafseer_Muyassar (QuranChapterID, VerseNumber, Rukuh, Sajdah, Manzil, HizbQuarter, VerseText) VALUES (2, 49, '0', '0', '0', '0', 'واذكروا نعمتنا عليكم حين أنقذناكم من بطش فرعون وأتباعه، وهم يُذيقونكم أشدَّ العذاب، فيُكثِرون مِن ذَبْح أبنائكم، وترك بناتكم للخدمة والامتهان. وفي ذلك اختبار لكم من ربكم، وفي إنجائكم منه نعمة عظيمة، تستوجب شكر الله تعالى في كل عصوركم وأجيالكم.');
INSERT INTO Quran__Arabic_Tafseer_Muyassar (QuranChapterID, VerseNumber, Rukuh, Sajdah, Manzil, HizbQuarter, VerseText) VALUES (2, 50, '0', '0', '0', '0', 'واذكروا نعمتنا عليكم، حين فَصَلْنا بسببكم البحر، وجعلنا فيه طرقًا يابسةً، فعبرتم، وأنقذناكم من فرعون وجنوده، ومن الهلاك في الماء. فلما دخل فرعون وجنوده طرقكم أهلكناهم في الماء أمام أعينكم.');
INSERT INTO Quran__Arabic_Tafseer_Muyassar (QuranChapterID, VerseNumber, Rukuh, Sajdah, Manzil, HizbQuarter, VerseText) VALUES (2, 51, '0', '0', '0', '0', 'واذكروا نعمتنا عليكم: حين واعدنا موسى أربعين ليلة لإنزال التوراة هدايةً ونورًا لكم، فإذا بكم تنتهزون فرصة غيابه هذه المدة القليلة، وتجعلون العجل الذي صنعتموه بأيديكم معبودًا لكم من دون الله - وهذا أشنع الكفر بالله- وأنتم ظالمون باتخاذكم العجل إلهًا.');
INSERT INTO Quran__Arabic_Tafseer_Muyassar (QuranChapterID, VerseNumber, Rukuh, Sajdah, Manzil, HizbQuarter, VerseText) VALUES (2, 52, '0', '0', '0', '0', 'ثمَّ تجاوزنا عن هذه الفعلة المنكرة، وقَبِلْنَا توبتكم بعد عودة موسى؛ رجاءَ أن تشكروا الله على نعمه وأفضاله، ولا تتمادوا في الكفر والطغيان.');
INSERT INTO Quran__Arabic_Tafseer_Muyassar (QuranChapterID, VerseNumber, Rukuh, Sajdah, Manzil, HizbQuarter, VerseText) VALUES (2, 53, '0', '0', '0', '0', 'واذكروا نعمتنا عليكم حين أعطينا موسى الكتاب الفارق بين الحق والباطل -وهو التوراة-؛ لكي تهتدوا من الضلالة.');
INSERT INTO Quran__Arabic_Tafseer_Muyassar (QuranChapterID, VerseNumber, Rukuh, Sajdah, Manzil, HizbQuarter, VerseText) VALUES (2, 54, '0', '0', '0', '0', 'واذكروا نعمتنا عليكم حين قال موسى لقومه: إنكم ظلمتم أنفسكم باتخاذكم العجل إلهًا، فتوبوا إلى خالقكم: بأن يَقْتل بعضكم بعضًا، وهذا خير لكم عند خالقكم من الخلود الأبدي في النار، فامتثلتم ذلك، فمنَّ الله عليكم بقَبول توبتكم. إنه تعالى هو التواب لمن تاب مِن عباده، الرحيم بهم.');
INSERT INTO Quran__Arabic_Tafseer_Muyassar (QuranChapterID, VerseNumber, Rukuh, Sajdah, Manzil, HizbQuarter, VerseText) VALUES (2, 55, '0', '0', '0', '0', 'واذكروا إذ قلتم: يا موسى لن نصدقك في أن الكلام الذي نسمعه منك هو كلام الله، حتى نرى الله عِيَانًا، فنزلت نار من السماء رأيتموها بأعينكم، فقَتَلَتْكم بسبب ذنوبكم، وجُرْأتكم على الله تعالى.');
INSERT INTO Quran__Arabic_Tafseer_Muyassar (QuranChapterID, VerseNumber, Rukuh, Sajdah, Manzil, HizbQuarter, VerseText) VALUES (2, 56, '0', '0', '0', '0', 'ثم أحييناكم مِن بعد موتكم بالصاعقة؛ لتشكروا نعمة الله عليكم، فهذا الموت عقوبة لهم، ثم بعثهم الله لاستيفاء آجالهم.');
INSERT INTO Quran__Arabic_Tafseer_Muyassar (QuranChapterID, VerseNumber, Rukuh, Sajdah, Manzil, HizbQuarter, VerseText) VALUES (2, 57, '0', '0', '0', '0', 'واذكروا نعمتنا عليكم حين كنتم تتيهون في الأرض؛ إذ جعلنا السحاب مظللا عليكم من حَرِّ الشمس، وأنزلنا عليكم المنَّ، وهو شيء يشبه الصَّمغ طعمه كالعسل، وأنزلنا عليكم السَّلوى وهو طير يشبه السُّمانَى، وقلنا لكم: كلوا من طيِّبات ما رزقناكم، ولا تخالفوا دينكم، فلم تمتثلوا. وما ظلمونا بكفران النعم، ولكن كانوا أنفسهم يظلمون؛ لأن عاقبة الظلم عائدة عليهم.');
INSERT INTO Quran__Arabic_Tafseer_Muyassar (QuranChapterID, VerseNumber, Rukuh, Sajdah, Manzil, HizbQuarter, VerseText) VALUES (2, 58, '0', '0', '0', '0', 'واذكروا نعمتنا عليكم حين قلنا: ادخلوا مدينة "بيت المقدس" فكلوا من طيباتها في أي مكان منها أكلا هنيئًا، وكونوا في دخولكم خاضعين لله، ذليلين له، وقولوا: ربَّنا ضَعْ عنَّا ذنوبنا، نستجب لكم ونعف ونسترها عليكم، وسنزيد المحسنين بأعمالهم خيرًا وثوابًا.');
INSERT INTO Quran__Arabic_Tafseer_Muyassar (QuranChapterID, VerseNumber, Rukuh, Sajdah, Manzil, HizbQuarter, VerseText) VALUES (2, 59, '1', '0', '0', '0', 'فبدَّل الجائرون الضالون من بني إسرائيل قول الله، وحرَّفوا القول والفعل جميعًا، إذ دخلوا يزحفون على أستاههم وقالوا: حبة في شعرة، واستهزءوا بدين الله. فأنزل الله عليهم عذابًا من السماء؛ بسبب تمردهم وخروجهم عن طاعة الله.');
INSERT INTO Quran__Arabic_Tafseer_Muyassar (QuranChapterID, VerseNumber, Rukuh, Sajdah, Manzil, HizbQuarter, VerseText) VALUES (2, 60, '0', '0', '0', '1', 'واذكروا نعمتنا عليكم -وأنتم عطاش في التِّيْه- حين دعانا موسى -بضراعة- أن نسقي قومه، فقلنا: اضرب بعصاك الحجر، فضرب، فانفجرت منه اثنتا عشرة عينًا، بعدد القبائل، مع إعلام كل قبيلة بالعين الخاصة بها حتى لا يتنازعوا. وقلنا لهم: كلوا واشربوا من رزق الله، ولا تسعوا في الأرض مفسدين.');
INSERT INTO Quran__Arabic_Tafseer_Muyassar (QuranChapterID, VerseNumber, Rukuh, Sajdah, Manzil, HizbQuarter, VerseText) VALUES (2, 61, '1', '0', '0', '0', 'واذكروا حين أنزلنا عليكم الطعام الحلو، والطير الشهي، فبطِرتم النعمة كعادتكم، وأصابكم الضيق والملل، فقلتم: يا موسى لن نصبر على طعام ثابت لا يتغير مع الأيام، فادع لنا ربك يخرج لنا من نبات الأرض طعامًا من البقول والخُضَر، والقثاء والحبوب التي تؤكل، والعدس، والبصل. قال موسى -مستنكرًا عليهم-: أتطلبون هذه الأطعمة التي هي أقل قدرًا، وتتركون هذا الرزق النافع الذي اختاره الله لكم؟ اهبطوا من هذه البادية إلى أي مدينة، تجدوا ما اشتهيتم كثيرًا في الحقول والأسواق. ولما هبطوا تبيَّن لهم أنهم يُقَدِّمون اختيارهم -في كل موطن- على اختيار الله، ويُؤْثِرون شهواتهم على ما اختاره الله لهم؛ لذلك لزمتهم صِفَةُ الذل وفقر النفوس، وانصرفوا ورجعوا بغضب من الله؛ لإعراضهم عن دين الله، ولأنهم كانوا يكفرون بآيات الله ويقتلون النبيين ظلمًا وعدوانًا؛ وذلك بسبب عصيانهم وتجاوزهم حدود ربهم.');
INSERT INTO Quran__Arabic_Tafseer_Muyassar (QuranChapterID, VerseNumber, Rukuh, Sajdah, Manzil, HizbQuarter, VerseText) VALUES (2, 62, '0', '0', '0', '0', 'إن المؤمنين من هذه الأمة، الذين صدَّقوا بالله ورسله، وعملوا بشرعه، والذين كانوا قبل بعثة محمد صلى الله عليه وسلم من الأمم السالفة من اليهود، والنصارى، والصابئين- وهم قوم باقون على فطرتهم، ولا دين مقرر لهم يتبعونه- هؤلاء جميعًا إذا صدَّقوا بالله تصديقًا صحيحًا خالصًا، وبيوم البعث والجزاء، وعملوا عملا مرضيًا عند الله، فثوابهم ثابت لهم عند ربهم، ولا خوف عليهم فيما يستقبلونه من أمر الآخرة، ولا هم يحزنون على ما فاتهم من أمور الدنيا. وأما بعد بعثة محمد صلى الله عليه وسلم خاتمًا للنبيين والمرسلين إلى الناس كافة، فلا يقبل الله من أحد دينًا غير ما جاء به، وهو الإسلام.');
INSERT INTO Quran__Arabic_Tafseer_Muyassar (QuranChapterID, VerseNumber, Rukuh, Sajdah, Manzil, HizbQuarter, VerseText) VALUES (2, 63, '0', '0', '0', '0', 'واذكروا -يا بني إسرائيل- حين أَخَذْنا العهد المؤكَّد منكم بالإيمان بالله وإفراده بالعبادة، ورفعنا جبل الطور فوقكم، وقلنا لكم: خذوا الكتاب الذي أعطيناكم بجدٍ واجتهاد واحفظوه، وإلا أطبقنا عليكم الجبل، ولا تنسوا التوراة قولا وعملا كي تتقوني وتخافوا عقابي.');
INSERT INTO Quran__Arabic_Tafseer_Muyassar (QuranChapterID, VerseNumber, Rukuh, Sajdah, Manzil, HizbQuarter, VerseText) VALUES (2, 64, '0', '0', '0', '0', 'ثم خالفتم وعصيتم مرة أخرى، بعد أَخْذِ الميثاق ورَفْع الجبل كشأنكم دائمًا. فلولا فَضْلُ الله عليكم ورحمته بالتوبة، والتجاوز عن خطاياكم، لصرتم من الخاسرين في الدنيا والآخرة.');
INSERT INTO Quran__Arabic_Tafseer_Muyassar (QuranChapterID, VerseNumber, Rukuh, Sajdah, Manzil, HizbQuarter, VerseText) VALUES (2, 65, '0', '0', '0', '0', 'ولقد علمتم -يا معشر اليهود- ما حلَّ من البأس بأسلافكم من أهل القرية التي عصت الله، فيما أخذه عليهم من تعظيم السبت، فاحتالوا لاصطياد السمك في يوم السبت، بوضع الشِّباك وحفر البِرَك، ثم اصطادوا السمك يوم الأحد حيلة إلى المحرم، فلما فعلوا ذلك، مسخهم الله قردة منبوذين.');
INSERT INTO Quran__Arabic_Tafseer_Muyassar (QuranChapterID, VerseNumber, Rukuh, Sajdah, Manzil, HizbQuarter, VerseText) VALUES (2, 66, '0', '0', '0', '0', 'فجعلنا هذه القرية عبرة لمن بحضرتها من القرى، يبلغهم خبرها وما حلَّ بها، وعبرة لمن يعمل بعدها مثل تلك الذُّنوب، وجعلناها تذكرة للصالحين؛ ليعلموا أنهم على الحق، فيثبتوا عليه.');
INSERT INTO Quran__Arabic_Tafseer_Muyassar (QuranChapterID, VerseNumber, Rukuh, Sajdah, Manzil, HizbQuarter, VerseText) VALUES (2, 67, '0', '0', '0', '0', 'واذكروا يا بني إسرائيل جناية أسلافكم، وكثرة تعنتهم وجدالهم لموسى عليه الصلاة والسلام، حين قال لهم: إن الله يأمركم أن تذبحوا بقرة، فقالوا -مستكبرين-: أتجعلنا موضعًا للسخرية والاستخفاف؟ فردَّ عليهم موسى بقوله: أستجير بالله أن أكون من المستهزئين.');
INSERT INTO Quran__Arabic_Tafseer_Muyassar (QuranChapterID, VerseNumber, Rukuh, Sajdah, Manzil, HizbQuarter, VerseText) VALUES (2, 68, '0', '0', '0', '0', 'قالوا: ادع لنا ربَّك يوضح لنا صفة هذه البقرة، فأجابهم: إن الله يقول لكم: صفتها ألا تكون مسنَّة هَرِمة، ولا صغيرة فَتِيَّة، وإنما هي متوسطة بينهما، فسارِعوا إلى امتثال أمر ربكم.');
INSERT INTO Quran__Arabic_Tafseer_Muyassar (QuranChapterID, VerseNumber, Rukuh, Sajdah, Manzil, HizbQuarter, VerseText) VALUES (2, 69, '0', '0', '0', '0', 'فعادوا إلى جدالهم قائلين: ادع لنا ربك يوضح لنا لونها. قال: إنه يقول: إنها بقرة صفراء شديدة الصُّفْرة، تَسُرُّ مَن ينظر إليها.');
INSERT INTO Quran__Arabic_Tafseer_Muyassar (QuranChapterID, VerseNumber, Rukuh, Sajdah, Manzil, HizbQuarter, VerseText) VALUES (2, 70, '0', '0', '0', '0', 'قال بنو إسرائيل لموسى: ادع لنا ربك يوضح لنا صفات أخرى غير ما سبق؛ لأن البقر -بهذه الصفات- كثير فاشْتَبَهَ علينا ماذا نختار؟ وإننا -إن شاء الله- لمهتدون إلى البقرة المأمور بذبحها.');
INSERT INTO Quran__Arabic_Tafseer_Muyassar (QuranChapterID, VerseNumber, Rukuh, Sajdah, Manzil, HizbQuarter, VerseText) VALUES (2, 71, '1', '0', '0', '0', 'قال لهم موسى: إن الله يقول: إنها بقرة غير مذللة للعمل في حراثة الأرض للزراعة، وغير معدة للسقي من الساقية، وخالية من العيوب جميعها، وليس فيها علامة من لون غير لون جلدها. قالوا: الآن جئت بحقيقة وصف البقرة، فاضطروا إلى ذبحها بعد طول المراوغة، وقد قاربوا ألا يفعلوا ذلك لعنادهم. وهكذا شددوا فشدَّد الله عليهم.');
INSERT INTO Quran__Arabic_Tafseer_Muyassar (QuranChapterID, VerseNumber, Rukuh, Sajdah, Manzil, HizbQuarter, VerseText) VALUES (2, 72, '0', '0', '0', '0', 'واذكروا إذ قتلتم نفسًا فتنازعتم بشأنها، كلٌّ يدفع عن نفسه تهمة القتل، والله مخرج ما كنتم تخفون مِن قَتْل القتيل.');
INSERT INTO Quran__Arabic_Tafseer_Muyassar (QuranChapterID, VerseNumber, Rukuh, Sajdah, Manzil, HizbQuarter, VerseText) VALUES (2, 73, '0', '0', '0', '0', 'فقلنا: اضربوا القتيل بجزء من هذه البقرة المذبوحة، فإن الله سيبعثه حيًا، ويخبركم عن قاتله. فضربوه ببعضها فأحياه الله وأخبر بقاتله. كذلك يُحيي الله الموتى يوم القيامة، ويريكم- يا بني إسرائيل- معجزاته الدالة على كمال قدرته تعالى؛ لكي تتفكروا بعقولكم، فتمتنعوا عن معاصيه.');
INSERT INTO Quran__Arabic_Tafseer_Muyassar (QuranChapterID, VerseNumber, Rukuh, Sajdah, Manzil, HizbQuarter, VerseText) VALUES (2, 74, '0', '0', '0', '0', 'ولكنكم لم تنتفعوا بذلك؛ إذ بعد كل هذه المعجزات الخارقة اشتدت قلوبكم وغلظت، فلم يَنْفُذ إليها خير، ولم تَلِنْ أمام الآيات الباهرة التي أريتكموها، حتى صارت قلوبكم مثل الحجارة الصمَّاء، بل هي أشد منها غلظة؛ لأن من الحجارة ما يتسع وينفرج حتى تنصبَّ منه المياه صبًا، فتصير أنهارًا جاريةً، ومن الحجارة ما يتصدع فينشق، فتخرج منه العيون والينابيع، ومن الحجارة ما يسقط من أعالي الجبال مِن خشية الله تعالى وتعظيمه. وما الله بغافل عما تعملون.');
INSERT INTO Quran__Arabic_Tafseer_Muyassar (QuranChapterID, VerseNumber, Rukuh, Sajdah, Manzil, HizbQuarter, VerseText) VALUES (2, 75, '0', '0', '0', '1', 'أيها المسلمون أنسيتم أفعال بني إسرائيل، فطمعت نفوسكم أن يصدِّق اليهودُ بدينكم؟ وقد كان علماؤهم يسمعون كلام الله من التوراة، ثم يحرفونه بِصَرْفِه إلى غير معناه الصحيح بعد ما عقلوا حقيقته، أو بتحريف ألفاظه، وهم يعلمون أنهم يحرفون كلام رب العالمين عمدًا وكذبًا.');
INSERT INTO Quran__Arabic_Tafseer_Muyassar (QuranChapterID, VerseNumber, Rukuh, Sajdah, Manzil, HizbQuarter, VerseText) VALUES (2, 76, '0', '0', '0', '0', 'هؤلاء اليهود إذا لقوا الذين آمنوا قالوا بلسانهم: آمنَّا بدينكم ورسولكم المبشَّر به في التوراة، وإذا خلا بعض هؤلاء المنافقين من اليهود إلى بعض قالوا في إنكار: أتحدِّثون المؤمنين بما بيَّن الله لكم في التوراة من أمر محمد؛ لتكون لهم الحجة عليكم عند ربكم يوم القيامة؟ أفلا تفقهون فتحذروا؟');
INSERT INTO Quran__Arabic_Tafseer_Muyassar (QuranChapterID, VerseNumber, Rukuh, Sajdah, Manzil, HizbQuarter, VerseText) VALUES (2, 77, '0', '0', '0', '0', 'أيفعلون كلَّ هذه الجرائم، ولا يعلمون أن الله يعلم جميع ما يخفونه وما يظهرونه؟');
INSERT INTO Quran__Arabic_Tafseer_Muyassar (QuranChapterID, VerseNumber, Rukuh, Sajdah, Manzil, HizbQuarter, VerseText) VALUES (2, 78, '0', '0', '0', '0', 'ومن اليهود جماعة يجهلون القراءة والكتابة، ولا يعلمون التوراة وما فيها من صفات نبي الله ورسوله محمد صلى الله عليه وسلم، وما عندهم من ذلك إلا أكاذيبُ وظنون فاسدة.');
INSERT INTO Quran__Arabic_Tafseer_Muyassar (QuranChapterID, VerseNumber, Rukuh, Sajdah, Manzil, HizbQuarter, VerseText) VALUES (2, 79, '0', '0', '0', '0', 'فهلاك ووعيد شديد لأحبار السوء من اليهود الذين يكتبون الكتاب بأيديهم، ثم يقولون: هذا من عند الله وهو مخالف لما أنزل الله على نبيِّه موسى عليه الصلاة والسلام؛ ليأخذوا في مقابل هذا عرض الدنيا. فلهم عقوبة مهلكة بسبب كتابتهم هذا الباطل بأيديهم، ولهم عقوبة مهلكة بسبب ما يأخذونه في المقابل من المال الحرام، كالرشوة وغيرها.');
INSERT INTO Quran__Arabic_Tafseer_Muyassar (QuranChapterID, VerseNumber, Rukuh, Sajdah, Manzil, HizbQuarter, VerseText) VALUES (2, 80, '0', '0', '0', '0', 'وقال بنو إسرائيل: لن تصيبنا النار في الآخرة إلا أيامًا قليلة العدد. قل لهم -أيها الرسول مبطلا دعواهم-: أعندكم عهد من الله بهذا، فإن الله لا يخلف عهده؟ بل إنكم تقولون على الله ما لا تعلمون بافترائكم الكذب.');
INSERT INTO Quran__Arabic_Tafseer_Muyassar (QuranChapterID, VerseNumber, Rukuh, Sajdah, Manzil, HizbQuarter, VerseText) VALUES (2, 81, '0', '0', '0', '0', 'فحُكْمُ الله ثابت: أن من ارتكب الآثام حتى جَرَّته إلى الكفر، واستولت عليه ذنوبه مِن جميع جوانبه وهذا لا يكون إلا فيمن أشرك بالله، فالمشركون والكفار هم الذين يلازمون نار جهنم ملازمة دائمةً لا تنقطع.');
INSERT INTO Quran__Arabic_Tafseer_Muyassar (QuranChapterID, VerseNumber, Rukuh, Sajdah, Manzil, HizbQuarter, VerseText) VALUES (2, 82, '1', '0', '0', '0', 'وحكم الله الثابتُ في مقابل هذا: أنَّ الذين صدَّقوا بالله ورسله تصديقًا خالصًا، وعملوا الأعمال المتفقة مع شريعة الله التي أوحاها إلى رسله، هؤلاء يلازمون الجنة في الآخرة ملازمةً دائمةً لا تنقطع.');
INSERT INTO Quran__Arabic_Tafseer_Muyassar (QuranChapterID, VerseNumber, Rukuh, Sajdah, Manzil, HizbQuarter, VerseText) VALUES (2, 83, '0', '0', '0', '0', 'واذكروا يا بني إسرائيل حين أخَذْنا عليكم عهدًا مؤكدًا: بأن تعبدوا الله وحده لا شريك له، وأن تحسنوا للوالدين، وللأقربين، وللأولاد الذين مات آباؤهم وهم دون بلوغ الحلم، وللمساكين، وأن تقولوا للناس أطيب الكلام، مع أداء الصلاة وإيتاء الزكاة، ثم أَعْرَضْتم ونقضتم العهد -إلا قليلا منكم ثبت عليه- وأنتم مستمرون في إعراضكم.');
INSERT INTO Quran__Arabic_Tafseer_Muyassar (QuranChapterID, VerseNumber, Rukuh, Sajdah, Manzil, HizbQuarter, VerseText) VALUES (2, 84, '0', '0', '0', '0', 'واذكروا -يا بني إسرائيل- حين أَخَذْنا عليكم عهدًا مؤكدًا في التوراة: يحرم سفك بعضكم دم بعض، وإخراج بعضكم بعضًا من دياركم، ثم اعترفتم بذلك، وأنتم تشهدون على صحته.');
INSERT INTO Quran__Arabic_Tafseer_Muyassar (QuranChapterID, VerseNumber, Rukuh, Sajdah, Manzil, HizbQuarter, VerseText) VALUES (2, 85, '0', '0', '0', '0', 'ثم أنتم يا هؤلاء يقتل بعضكم بعضًا، ويُخرج بعضكم بعضًا من ديارهم، ويَتَقَوَّى كل فريق منكم على إخوانه بالأعداء بغيًا وعدوانًا. وأن يأتوكم أسارى في يد الأعداء سعيتم في تحريرهم من الأسر، بدفع الفدية، مع أنه محرم عليكم إخراجهم من ديارهم. ما أقبح ما تفعلون حين تؤمنون ببعض أحكام التوراة وتكفرون ببعضها! فليس جزاء مَن يفعل ذلك منكم إلا ذُلا وفضيحة في الدنيا. ويوم القيامة يردُّهم الله إلى أفظع العذاب في النار. وما الله بغافل عما تعملون.');
INSERT INTO Quran__Arabic_Tafseer_Muyassar (QuranChapterID, VerseNumber, Rukuh, Sajdah, Manzil, HizbQuarter, VerseText) VALUES (2, 86, '1', '0', '0', '0', 'أولئك هم الذين آثروا الحياة الدنيا على الآخرة، فلا يخفف عنهم العذاب، وليس لهم ناصر ينصرهم مِن عذاب الله.');
INSERT INTO Quran__Arabic_Tafseer_Muyassar (QuranChapterID, VerseNumber, Rukuh, Sajdah, Manzil, HizbQuarter, VerseText) VALUES (2, 87, '0', '0', '0', '0', 'ولقد أعطينا موسى التوراة، وأتبعناه برسل من بني إسرائيل، وأعطينا عيسى ابن مريم المعجزات الواضحات، وقوَّيناه بجبريل عليه السلام. أفكلما جاءكم رسول بوحي من عند الله لا يوافق أهواءكم، استعليتم عليه، فكذَّبتم فريقًا وتقتلون فريقًا؟');
INSERT INTO Quran__Arabic_Tafseer_Muyassar (QuranChapterID, VerseNumber, Rukuh, Sajdah, Manzil, HizbQuarter, VerseText) VALUES (2, 88, '0', '0', '0', '0', 'وقال بنو إسرائيل لنبي الله ورسوله محمد صلى الله عليه وسلم: قلوبنا مغطاة، لا يَنْفُذ إليها قولك. وليس الأمر كما ادَّعَوْا، بل قلوبهم ملعونة، مطبوع عليها، وهم مطرودون من رحمة الله بسبب جحودهم، فلا يؤمنون إلا إيمانًا قليلا لا ينفعهم.');
INSERT INTO Quran__Arabic_Tafseer_Muyassar (QuranChapterID, VerseNumber, Rukuh, Sajdah, Manzil, HizbQuarter, VerseText) VALUES (2, 89, '0', '0', '0', '0', 'وحين جاءهم القرأن من عند الله مصدقا لما معهم من التوراة جحدوه، وأنكروا نبوة محمد صلى الله عليه وسلم، وكانوا قبل بعثته يستنصرون به على مشركي العرب، ويقولون: قَرُبَ مبعث نبيِّ آخرِ الزمان، وسنتبعه ونقاتلكم معه. فلمَّا جاءهم الرسول الذي عرفوا صفاتِه وصِدْقَه كفروا به وكذبوه. فلعنةُ الله على كل مَن كفر بنبي الله ورسوله محمد صلى الله عليه وسلم، وكتابه الذي أوحاه الله إليه.');
INSERT INTO Quran__Arabic_Tafseer_Muyassar (QuranChapterID, VerseNumber, Rukuh, Sajdah, Manzil, HizbQuarter, VerseText) VALUES (2, 90, '0', '0', '0', '0', 'قَبُحَ ما اختاره بنو إسرائيل لأنفسهم؛ إذ استبدلوا الكفر بالإيمان ظلمًا وحسدًا لإنزال الله من فضله القرآن على نبي الله ورسوله محمد صلى الله عليه وسلم، فرجعوا بغضب من الله عليهم بسبب جحودهم بالنبي محمد صلى الله عليه وسلم، بعد غضبه عليهم بسبب تحريفهم التوراة. وللجاحدين نبوَّة محمد صلى الله عليه وسلم عذابٌ يذلُّهم ويخزيهم.');
INSERT INTO Quran__Arabic_Tafseer_Muyassar (QuranChapterID, VerseNumber, Rukuh, Sajdah, Manzil, HizbQuarter, VerseText) VALUES (2, 91, '0', '0', '0', '0', 'وإذا قال بعض المسلمين لليهود: صدِّقوا بما أنزل الله من القرآن، قالوا: نحن نصدِّق بما أنزل الله على أنبيائنا، ويجحدون ما أنزل الله بعد ذلك، وهو الحق مصدقًا لما معهم. فلو كانوا يؤمنون بكتبهم حقًا لآمنوا بالقرآن الذي صدَّقها. قل لهم -يا محمد-: إن كنتم مؤمنين بما أنزل الله عليكم، فلماذا قتلتم أنبياء الله مِن قبل؟');
INSERT INTO Quran__Arabic_Tafseer_Muyassar (QuranChapterID, VerseNumber, Rukuh, Sajdah, Manzil, HizbQuarter, VerseText) VALUES (2, 92, '0', '0', '0', '1', 'ولقد جاءكم نبي الله موسى بالمعجزات الواضحات الدالة على صدقه، كالطوفان والجراد والقُمَّل والضفادع، وغير ذلك مما ذكره الله في القرآن العظيم، ومع ذلك اتخذتم العجل معبودًا، بعد ذهاب موسى إلى ميقات ربه، وأنتم متجاوزون حدود الله.');
INSERT INTO Quran__Arabic_Tafseer_Muyassar (QuranChapterID, VerseNumber, Rukuh, Sajdah, Manzil, HizbQuarter, VerseText) VALUES (2, 93, '0', '0', '0', '0', 'واذكروا حين أَخَذْنا عليكم عهدًا مؤكدًا بقَبول ما جاءكم به موسى من التوراة، فنقضتم العهد، فرفعنا جبل الطور فوق رؤوسكم، وقلنا لكم: خذوا ما آتيناكم بجدٍّ، واسمعوا وأطيعوا، وإلا أسقطنا الجبل عليكم، فقلتم: سمعنا قولك وعصينا أمرك؛ لأن عبادة العجل قد امتزجت بقلوبكم بسبب تماديكم في الكفر. قل لهم -أيها الرسول-: قَبُحَ ما يأمركم به إيمانكم من الكفر والضلال، إن كنتم مصدِّقين بما أنزل الله عليكم.');
INSERT INTO Quran__Arabic_Tafseer_Muyassar (QuranChapterID, VerseNumber, Rukuh, Sajdah, Manzil, HizbQuarter, VerseText) VALUES (2, 94, '0', '0', '0', '0', 'قل -أيها الرسول- لليهود الذين يدَّعون أن الجنة خاصة بهم؛ لزعمهم أنهم أولياء الله من دون الناس، وأنهم أبناؤه وأحباؤه: إن كان الأمر كذلك فادْعُوا على الكاذبين منكم أو من غيركم بالموت، إن كنتم صادقين في دعواكم هذه.');
INSERT INTO Quran__Arabic_Tafseer_Muyassar (QuranChapterID, VerseNumber, Rukuh, Sajdah, Manzil, HizbQuarter, VerseText) VALUES (2, 95, '0', '0', '0', '0', 'ولن يفعلوا ذلك أبدًا؛ لما يعرفونه من صدق النبي محمد صلى الله عليه وسلم ومن كذبهم وافترائهم، وبسبب ما ارتكبوه من الكفر والعصيان، المؤَدِّيَيْن إلى حرمانهم من الجنة ودخول النار. والله تعالى عليم بالظالمين من عباده، وسيجازيهم على ذلك.');
INSERT INTO Quran__Arabic_Tafseer_Muyassar (QuranChapterID, VerseNumber, Rukuh, Sajdah, Manzil, HizbQuarter, VerseText) VALUES (2, 96, '1', '0', '0', '0', 'ولتعلمَنَّ -أيها الرسول- أن اليهود أشد الناس رغبة في طول الحياة أيًّا كانت هذه الحياة من الذلَّة والمهانة، بل تزيد رغبتهم في طول الحياة على رغبات المشركين. يتمنى اليهودي أن يعيش ألف سنة، ولا يُبْعده هذا العمر الطويل إن حصل من عذاب الله. والله تعالى لا يخفى عليه شيء من أعمالهم وسيجازيهم عليها بما يستحقون من العذاب.');
INSERT INTO Quran__Arabic_Tafseer_Muyassar (QuranChapterID, VerseNumber, Rukuh, Sajdah, Manzil, HizbQuarter, VerseText) VALUES (2, 97, '0', '0', '0', '0', 'قل-أيها الرسول- لليهود حين قالوا: إن جبريل هو عدونا من الملائكة: من كان عدوًا لجبريل فإنه نزَّل القرآن على قلبك بإذن الله تعالى مصدِّقًا لما سبقه من كتب الله، وهاديًا إلى الحق، ومبشرًا للمصدِّقين به بكل خير في الدنيا والآخرة.');
INSERT INTO Quran__Arabic_Tafseer_Muyassar (QuranChapterID, VerseNumber, Rukuh, Sajdah, Manzil, HizbQuarter, VerseText) VALUES (2, 98, '0', '0', '0', '0', 'من عادى الله وملائكته، ورسله من الملائكة أو البشر، وبخاصة المَلَكان جبريلُ وميكالُ؛ لأن اليهود زعموا أن جبريل عدوهم، وميكال وليُّهم، فأعلمهم الله أنه من عادى واحدًا منهما فقد عادى الآخر، وعادى الله أيضًا، فإن الله عدو للجاحدين ما أنزل على رسوله محمد صلى الله عليه وسلم.');
INSERT INTO Quran__Arabic_Tafseer_Muyassar (QuranChapterID, VerseNumber, Rukuh, Sajdah, Manzil, HizbQuarter, VerseText) VALUES (2, 99, '0', '0', '0', '0', 'ولقد أنزلنا إليك-أيها الرسول- آيات بينات واضحات تدل على أنّك رسول من الله صدقا وحقا، وما ينكر تلك الآيات إلا الخارجون عن دين الله.');
INSERT INTO Quran__Arabic_Tafseer_Muyassar (QuranChapterID, VerseNumber, Rukuh, Sajdah, Manzil, HizbQuarter, VerseText) VALUES (2, 100, '0', '0', '0', '0', 'ما أقبح حال بني إسرائيل في نقضهم للعهود!! فكلما عاهدوا عهدًا طرح ذلك العهد فريق منهم، ونقضوه، فتراهم يُبْرِمون العهد اليوم وينقضونه غدًا، بل أكثرهم لا يصدِّقون بما جاء به نبي الله ورسوله محمد صلى الله عليه وسلم.');
INSERT INTO Quran__Arabic_Tafseer_Muyassar (QuranChapterID, VerseNumber, Rukuh, Sajdah, Manzil, HizbQuarter, VerseText) VALUES (2, 101, '0', '0', '0', '0', 'ولما جاءهم محمد رسول الله صلى الله عليه وسلم بالقرآن الموافق لما معهم من التوراة طرح فريق منهم كتاب الله، وجعلوه وراء ظهورهم، شأنهم شأن الجهال الذين لا يعلمون حقيقته.');
INSERT INTO Quran__Arabic_Tafseer_Muyassar (QuranChapterID, VerseNumber, Rukuh, Sajdah, Manzil, HizbQuarter, VerseText) VALUES (2, 102, '0', '0', '0', '0', 'واتبع اليهود ما تُحَدِّث الشياطينُ به السحرةَ على عهد ملك سليمان بن داود. وما كفر سليمان وما تَعَلَّم السِّحر، ولكنَّ الشياطين هم الذين كفروا بالله حين علَّموا الناس السحر؛ إفسادًا لدينهم. وكذلك اتبع اليهود السِّحر الذي أُنزل على الملَكَين هاروت وماروت، بأرض "بابل" في "العراق"؛ امتحانًا وابتلاء من الله لعباده، وما يعلِّم الملكان من أحد حتى ينصحاه ويحذِّراه من تعلم السحر، ويقولا له: لا تكفر بتعلم السِّحر وطاعة الشياطين. فيتعلم الناس من الملكين ما يُحْدِثون به الكراهية بين الزوجين حتى يتفرقا. ولا يستطيع السحرة أن يضروا به أحدًا إلا بإذن الله وقضائه. وما يتعلم السحرة إلا شرًا يضرهم ولا ينفعهم، وقد نقلته الشياطين إلى اليهود، فشاع فيهم حتى فضَّلوه على كتاب الله. ولقد علم اليهود أن من اختار السِّحر وترك الحق ما له في الآخرة من نصيب في الخير. ولبئس ما باعوا به أنفسهم من السحر والكفر عوضًا عن الإيمان ومتابعة الرسول، لو كان لهم عِلْمٌ يثمر العمل بما وُعِظوا به.');
INSERT INTO Quran__Arabic_Tafseer_Muyassar (QuranChapterID, VerseNumber, Rukuh, Sajdah, Manzil, HizbQuarter, VerseText) VALUES (2, 103, '1', '0', '0', '0', 'ولو أن اليهود آمنوا وخافوا الله لأيقنوا أن ثواب الله خير لهم من السِّحر ومما اكتسبوه به، لو كانوا يعلمون ما يحصل بالإيمان والتقوى من الثواب والجزاء علما حقيقيا لآمنوا.');
INSERT INTO Quran__Arabic_Tafseer_Muyassar (QuranChapterID, VerseNumber, Rukuh, Sajdah, Manzil, HizbQuarter, VerseText) VALUES (2, 104, '0', '0', '0', '0', 'يا أيها الذين آمنوا لا تقولوا للرسول محمد صلى الله عليه وسلم: راعنا، أي: راعنا سمعك، فافهم عنا وأفهمنا؛ لأن اليهود كانوا يقولونها للنبي صلى الله عليه وسلم يلوون ألسنتهم بها، يقصدون سبَّه ونسبته إلى الرعونة، وقولوا- أيها المؤمنون- بدلا منها: انظرنا، أي انظر إلينا وتعهَّدْنا، وهي تؤدي المعنى المطلوب نفسه واسمعوا ما يتلى عليكم من كتاب ربكم وافهموه. وللجاحدين عذاب موجع.');
INSERT INTO Quran__Arabic_Tafseer_Muyassar (QuranChapterID, VerseNumber, Rukuh, Sajdah, Manzil, HizbQuarter, VerseText) VALUES (2, 105, '0', '0', '0', '0', 'ما يحب الكفار من أهل الكتاب والمشركين أن يُنزَّل عليكم أدنى خير من ربكم قرآنًا أو علمًا، أو نصرًا أو بشارة. والله يختص برحمته مَن يشاء مِن عباده بالنبوة والرسالة. والله ذو العطاء الكثير الواسع.');
INSERT INTO Quran__Arabic_Tafseer_Muyassar (QuranChapterID, VerseNumber, Rukuh, Sajdah, Manzil, HizbQuarter, VerseText) VALUES (2, 106, '0', '0', '0', '1', 'ما نبدِّل من آية أو نُزِلها من القلوب والأذهان نأت بأنفع لكم منها، أو نأت بمثلها في التكليف والثواب، ولكلٍ حكمة. ألم تعلم -أيها النبي- أنت وأمتك أن الله قادر لا يعجزه شيء؟');
INSERT INTO Quran__Arabic_Tafseer_Muyassar (QuranChapterID, VerseNumber, Rukuh, Sajdah, Manzil, HizbQuarter, VerseText) VALUES (2, 107, '0', '0', '0', '0', 'أما علمتَ -أيها النبي- أنت وأمتك أن الله تعالى هو المالك المتصرف في السموات والأرض؟ يفعل ما يشاء، ويحكم ما يريد، ويأمر عباده وينهاهم كيفما شاء، وعليهم الطاعة والقَبول. وليعلم من عصى أن ليس لأحد من دون الله من وليٍّ يتولاهم، ولا نصير يمنعهم من عذاب الله.');
INSERT INTO Quran__Arabic_Tafseer_Muyassar (QuranChapterID, VerseNumber, Rukuh, Sajdah, Manzil, HizbQuarter, VerseText) VALUES (2, 108, '0', '0', '0', '0', 'بل أتريدون- أيها الناس- أن تطلبوا من رسولكم محمد صلى الله عليه وسلم أشياء بقصد العناد والمكابرة، كما طُلِبَ مثل ذلك من موسى. علموا أن من يختر الكفر ويترك الإيمان فقد خرج عن صراط الله المستقيم إلى الجهل والضَّلال.');
INSERT INTO Quran__Arabic_Tafseer_Muyassar (QuranChapterID, VerseNumber, Rukuh, Sajdah, Manzil, HizbQuarter, VerseText) VALUES (2, 109, '0', '0', '0', '0', 'تمنى كثير من أهل الكتاب أن يرجعوكم بعد إيمانكم كفارًا كما كنتم من قبلُ تعبدون الأصنام؛ بسبب الحقد الذي امتلأت به نفوسهم من بعد ما تبيَّن لهم صدق نبي الله ورسوله محمد صلى الله عليه وسلم فيما جاء به، فتجاوزوا عمَّا كان منهم من إساءة وخطأ، واصفحوا عن جهلهم، حتى يأتي الله بحكمه فيهم بقتالهم (وقد جاء ووقع)، وسيعاقبهم لسوء أفعالهم. إن الله على كل شيء قدير لا يعجزه شيء.');
INSERT INTO Quran__Arabic_Tafseer_Muyassar (QuranChapterID, VerseNumber, Rukuh, Sajdah, Manzil, HizbQuarter, VerseText) VALUES (2, 110, '0', '0', '0', '0', 'واشتغلوا -أيها المؤمنون- بأداء الصلاة على وجهها الصحيح، وإعطاء الزكاة المفروضة. واعلموا أنَّ كل خير تقدمونه لأنفسكم تجدون ثوابه عند الله في الآخرة. إنه تعالى بصير بكل أعمالكم، وسيجازيكم عليها.');
INSERT INTO Quran__Arabic_Tafseer_Muyassar (QuranChapterID, VerseNumber, Rukuh, Sajdah, Manzil, HizbQuarter, VerseText) VALUES (2, 111, '0', '0', '0', '0', 'ادَّعى كلٌّ من اليهود والنصارى أن الجنة خاصة بطائفته لا يدخلها غيرهم، تلك أوهامهم الفاسدة. قل لهم -أيها الرسول-: أحضروا دليلكم على صحة ما تدَّعون إن كنتم صادقين في دعواكم.');
INSERT INTO Quran__Arabic_Tafseer_Muyassar (QuranChapterID, VerseNumber, Rukuh, Sajdah, Manzil, HizbQuarter, VerseText) VALUES (2, 112, '1', '0', '0', '0', 'ليس الأمر كما زعموا أنَّ الجنة تختص بطائفة دون غيرها، وإنما يدخل الجنَّة مَن أخلص لله وحده لا شريك له، وهو متبع للرسول محمد صلى الله عليه وسلم في كل أقواله وأعماله. فمن فعل ذلك فله ثواب عمله عند ربه في الآخرة، وهو دخول الجنة، وهم لا يخافون فيما يستقبلونه من أمر الآخرة، ولا هم يحزنون على ما فاتهم من حظوظ الدنيا.');
INSERT INTO Quran__Arabic_Tafseer_Muyassar (QuranChapterID, VerseNumber, Rukuh, Sajdah, Manzil, HizbQuarter, VerseText) VALUES (2, 113, '0', '0', '0', '0', 'وقالت اليهود: ليست النصارى على شيء من الدين الصحيح، وكذلك قالت النصارى في اليهود وهم يقرؤون التوراة والإنجيل، وفيهما وجوب الإيمان بالأنبياء جميعًا. كذلك قال الذين لا يعلمون من مشركي العرب وغيرهم مثل قولهم، أي قالوا لكل ذي دين: لست على شيء، فالله يفصل بينهم يوم القيامة فيما اختلفوا فيه مِن أمر الدين، ويجازي كلا بعمله.');
INSERT INTO Quran__Arabic_Tafseer_Muyassar (QuranChapterID, VerseNumber, Rukuh, Sajdah, Manzil, HizbQuarter, VerseText) VALUES (2, 114, '0', '0', '0', '0', 'لا أحد أظلم من الذين منعوا ذِكْرَ الله في المساجد من إقام الصلاة، وتلاوة القرآن، ونحو ذلك، وجدُّوا في تخريبها بالهدم أو الإغلاق، أو بمنع المؤمنين منها. أولئك الظالمون ما كان ينبغي لهم أن يدخلوا المساجد إلا على خوف ووجل من العقوبة، لهم بذلك صَغار وفضيحة في الدنيا، ولهم في الآخرة عذاب شديد.');
INSERT INTO Quran__Arabic_Tafseer_Muyassar (QuranChapterID, VerseNumber, Rukuh, Sajdah, Manzil, HizbQuarter, VerseText) VALUES (2, 115, '0', '0', '0', '0', 'ولله جهتا شروق الشمس وغروبها وما بينهما، فهو مالك الأرض كلها. فأي جهة توجهتم إليها في الصلاة بأمر الله لكم فإنكم مبتغون وجهه، لم تخرجوا عن ملكه وطاعته. إن الله واسع الرحمة بعباده، عليم بأفعالهم، لا يغيب عنه منها شيء.');
INSERT INTO Quran__Arabic_Tafseer_Muyassar (QuranChapterID, VerseNumber, Rukuh, Sajdah, Manzil, HizbQuarter, VerseText) VALUES (2, 116, '0', '0', '0', '0', 'وقالت اليهود والنصارى والمشركون: اتخذ الله لنفسه ولدًا، تنزَّه الله -سبحانه- عن هذا القول الباطل، بل كل مَن في السموات والأرض ملكه وعبيده، وهم جميعًا خاضعون له، مسخَّرون تحت تدبيره.');
INSERT INTO Quran__Arabic_Tafseer_Muyassar (QuranChapterID, VerseNumber, Rukuh, Sajdah, Manzil, HizbQuarter, VerseText) VALUES (2, 117, '0', '0', '0', '0', 'والله تعالى هو خالق السموات والأرض على غير مثال سبق. وإذا قدَّر أمرًا وأراد كونه فإنما يقول له: "كن" فيكون.');
INSERT INTO Quran__Arabic_Tafseer_Muyassar (QuranChapterID, VerseNumber, Rukuh, Sajdah, Manzil, HizbQuarter, VerseText) VALUES (2, 118, '0', '0', '0', '0', 'وقال الجهلة من أهل الكتاب وغيرهم لنبي الله ورسوله محمد صلى الله عليه وسلم على سبيل العناد: هلا يكلمنا الله مباشرة ليخبرنا أنك رسوله، أو تأتينا معجزة من الله تدل على صدقك. ومثل هذا القول قالته الأمم من قبلُ لرسلها عنادًا ومكابرة؛ بسبب تشابه قلوب السابقين واللاحقين في الكفر والضَّلال، قد أوضحنا الآيات للذين يصدِّقون تصديقًا جازمًا؛ لكونهم مؤمنين بالله تعالى، متَّبعين ما شرعه لهم.');
INSERT INTO Quran__Arabic_Tafseer_Muyassar (QuranChapterID, VerseNumber, Rukuh, Sajdah, Manzil, HizbQuarter, VerseText) VALUES (2, 119, '0', '0', '0', '0', 'إنا أرسلناك -أيها الرسول- بالدين الحق المؤيد بالحجج والمعجزات، فبلِّغه للناس مع تبشير المؤمنين بخيري الدنيا والآخرة، وتخويف المعاندين بما ينتظرهم من عذاب الله، ولست -بعد البلاغ- مسئولا عن كفر مَن كفر بك؛ فإنهم يدخلون النار يوم القيامة، ولا يخرجون منها.');
INSERT INTO Quran__Arabic_Tafseer_Muyassar (QuranChapterID, VerseNumber, Rukuh, Sajdah, Manzil, HizbQuarter, VerseText) VALUES (2, 120, '0', '0', '0', '0', 'ولن ترضى عنك -أيها الرسول- اليهود ولا النصارى إلا إذا تركت دينك واتبعتَ دينهم. قل لهم: إن دين الإسلام هو الدين الصحيح. ولئن اتبعت أهواء هؤلاء بعد الذي جاءك من الوحي ما لك عند الله مِن وليٍّ ينفعك، ولا نصير ينصرك. هذا موجه إلى الأمّة عامة وإن كان خطابًا للنبي صلى الله عليه وسلم.');
INSERT INTO Quran__Arabic_Tafseer_Muyassar (QuranChapterID, VerseNumber, Rukuh, Sajdah, Manzil, HizbQuarter, VerseText) VALUES (2, 121, '1', '0', '0', '0', 'الذين أعطيناهم الكتاب من اليهود والنصارى، يقرؤونه القراءة الصحيحة، ويتبعونه حق الاتباع، ويؤمنون بما جاء فيه من الإيمان برسل الله، ومنهم خاتمهم نبينا ورسولنا محمد صلى الله عليه وسلم، ولا يحرفون ولا يبدِّلون ما جاء فيه. هؤلاء هم الذين يؤمنون بالنبي محمد صلى الله عليه وسلم وبما أنزل عليه، وأما الذين بدَّلوا بعض الكتاب وكتموا بعضه، فهؤلاء كفار بنبي الله محمد صلى الله عليه وسلم وبما أنزل عليه، ومن يكفر به فأولئك هم أشد الناس خسرانًا عند الله.');
INSERT INTO Quran__Arabic_Tafseer_Muyassar (QuranChapterID, VerseNumber, Rukuh, Sajdah, Manzil, HizbQuarter, VerseText) VALUES (2, 122, '0', '0', '0', '0', 'يا ذرية يعقوب اذكروا نعمي الكثيرة عليكم، وأني فَضَّلتكم على عالَمي زمانكم بكثرة أنبيائكم، وما أُنزل عليهم من الكتب.');
INSERT INTO Quran__Arabic_Tafseer_Muyassar (QuranChapterID, VerseNumber, Rukuh, Sajdah, Manzil, HizbQuarter, VerseText) VALUES (2, 123, '0', '0', '0', '0', 'وخافوا أهوال يوم الحساب إذ لا تغني نفس عن نفس شيئًا، ولا يقبل الله منها فدية تنجيها من العذاب، ولا تنفعها وساطة، ولا أحد ينصرها.');
INSERT INTO Quran__Arabic_Tafseer_Muyassar (QuranChapterID, VerseNumber, Rukuh, Sajdah, Manzil, HizbQuarter, VerseText) VALUES (2, 124, '0', '0', '0', '1', 'واذكر-أيها النبي- حين اختبر الله إبراهيم بما شرع له من تكاليف، فأدَّاها وقام بها خير قيام. قال الله له: إني جاعلك قدوة للناس. قال إبراهيم: ربِّ اجعل بعض نسلي أئمة فضلا منك، فأجابه الله سبحانه أنه لا تحصل للظالمين الإمامةُ في الدين.');
INSERT INTO Quran__Arabic_Tafseer_Muyassar (QuranChapterID, VerseNumber, Rukuh, Sajdah, Manzil, HizbQuarter, VerseText) VALUES (2, 125, '0', '0', '0', '0', 'واذكر -أيها النبي- حين جعلنا الكعبة مرجعًا للناس، يأتونه، ثم يرجعون إلى أهليهم، ثم يعودون إليه، ومجمعًا لهم في الحج والعمرة والطواف والصلاة، وأمنًا لهم، لا يُغِير عليهم عدو فيه. وقلنا: اتخِذوا من مقام إبراهيم مكانًا للصلاة فيه، وهو الحجر الذي وقف عليه إبراهيم عند بنائه الكعبة. وأوحينا إلى إبراهيم وابنه إسماعيل: أن طهِّرا بيتي من كل رجس ودنس؛ للمتعبدين فيه بالطواف حول الكعبة، أو الاعتكاف في المسجد، والصلاة فيه.');
INSERT INTO Quran__Arabic_Tafseer_Muyassar (QuranChapterID, VerseNumber, Rukuh, Sajdah, Manzil, HizbQuarter, VerseText) VALUES (2, 126, '0', '0', '0', '0', 'واذكر -أيها النبي- حين قال إبراهيم داعيًا: ربِّ اجعل "مكة" بلدًا آمنًا من الخوف، وارزق أهله من أنواع الثمرات، وخُصَّ بهذا الرزق مَن آمن منهم بالله واليوم الآخر. قال الله: ومن كفر منهم فأرزقه في الدنيا وأُمتعه متاعًا قليلا ثم أُلجئُه مرغمًا إلى عذاب النار. وبئس المرجع والمقام هذا المصير.');
INSERT INTO Quran__Arabic_Tafseer_Muyassar (QuranChapterID, VerseNumber, Rukuh, Sajdah, Manzil, HizbQuarter, VerseText) VALUES (2, 127, '0', '0', '0', '0', 'واذكر -أيها النبي- حين رفع إبراهيم وإسماعيل أسس الكعبة، وهما يدعوان الله في خشوع: ربنا تقبل منَّا صالح أعمالنا ودعاءنا، إنك أنت السميع لأقوال عبادك، العليم بأحوالهم.');
INSERT INTO Quran__Arabic_Tafseer_Muyassar (QuranChapterID, VerseNumber, Rukuh, Sajdah, Manzil, HizbQuarter, VerseText) VALUES (2, 128, '0', '0', '0', '0', 'ربنا واجعلنا ثابتَيْن على الإسلام، منقادَيْن لأحكامك، واجعل من ذريتنا أمة منقادة لك، بالإيمان، وبصِّرْنا بمعالم عبادتنا لك، وتجاوز عن ذنوبنا. إنك أنت كثير التوبة والرحمة لعبادك.');
INSERT INTO Quran__Arabic_Tafseer_Muyassar (QuranChapterID, VerseNumber, Rukuh, Sajdah, Manzil, HizbQuarter, VerseText) VALUES (2, 129, '1', '0', '0', '0', 'ربنا وابعث في هذه الأمة رسولا من ذرية إسماعيل يتلو عليهم آياتك ويعلمهم القرآن والسنة، ويطهرهم من الشرك وسوء الأخلاق. إنك أنت العزيز الذي لا يمتنع عليه شيء، الحكيم الذي يضع الأشياء في مواضعها.');
INSERT INTO Quran__Arabic_Tafseer_Muyassar (QuranChapterID, VerseNumber, Rukuh, Sajdah, Manzil, HizbQuarter, VerseText) VALUES (2, 130, '0', '0', '0', '0', 'ولا أحد يُعرض عن دين إبراهيم -وهو الإسلام- إلا سفيه جاهل، ولقد اخترنا إبراهيم في الدنيا نبيًّا ورسولا وإنه في الآخرة لمن الصالحين الذين لهم أعلى الدرجات.');
INSERT INTO Quran__Arabic_Tafseer_Muyassar (QuranChapterID, VerseNumber, Rukuh, Sajdah, Manzil, HizbQuarter, VerseText) VALUES (2, 131, '0', '0', '0', '0', 'وسبب هذا الاختيار مسارعته للإسلام دون تردد، حين قال له ربه: أخلص نفسك لله منقادًا له. فاستجاب إبراهيم وقال: أسلمت لرب العالمين إخلاصًا وتوحيدًا ومحبة وإنابة.');
INSERT INTO Quran__Arabic_Tafseer_Muyassar (QuranChapterID, VerseNumber, Rukuh, Sajdah, Manzil, HizbQuarter, VerseText) VALUES (2, 132, '0', '0', '0', '0', 'وحثَّ إبراهيمُ ويعقوبُ أبناءهما على الثبات على الإسلام قائلَيْن: يا أبناءنا إن الله اختار لكم هذا الدين- وهو دين الإسلام الذي جاء به محمد صلى الله عليه وسلم- فلا تفارقوه أيام حياتكم، ولا يأتكم الموت إلا وأنتم عليه.');
INSERT INTO Quran__Arabic_Tafseer_Muyassar (QuranChapterID, VerseNumber, Rukuh, Sajdah, Manzil, HizbQuarter, VerseText) VALUES (2, 133, '0', '0', '0', '0', 'أكنتم أيها اليهود حاضرين حين جاء الموتُ يعقوبَ، إذ جمع أبناءه وسألهم ما تعبدون من بعد موتي؟ قالوا: نعبد إلهك وإله آبائك إبراهيم وإسماعيل وإسحاق إلهًا واحدًا، ونحن له منقادون خاضعون.');
INSERT INTO Quran__Arabic_Tafseer_Muyassar (QuranChapterID, VerseNumber, Rukuh, Sajdah, Manzil, HizbQuarter, VerseText) VALUES (2, 134, '0', '0', '0', '0', 'تلك أُمَّة من أسلافكم قد مضَتْ، لهم أعمالهم، ولكم أعمَالكم، ولا تُسْألون عن أعمالهم، وهم لا يُسْألون عن أعمالكم، وكلٌّ سيجازى بما فعله، لا يؤاخذ أحد بذنب أحد، ولا ينفعُ أحدًا إلا إيمانُه وتقواه.');
INSERT INTO Quran__Arabic_Tafseer_Muyassar (QuranChapterID, VerseNumber, Rukuh, Sajdah, Manzil, HizbQuarter, VerseText) VALUES (2, 135, '0', '0', '0', '0', 'وقالت اليهود لأمَّة محمد صلى الله عليه وسلم: ادخلوا في دين اليهودية تجدوا الهداية، وقالت النصارى لهم مثل ذلك. قل لهم -أيها الرسول-: بل الهداية أن نتبع- جميعًا- ملة إبراهيم، الذي مال عن كل دين باطل إلى دين الحق، وما كان من المشركين بالله تعالى.');
INSERT INTO Quran__Arabic_Tafseer_Muyassar (QuranChapterID, VerseNumber, Rukuh, Sajdah, Manzil, HizbQuarter, VerseText) VALUES (2, 136, '0', '0', '0', '0', 'قولوا -أيها المؤمنون- لهؤلاء اليهود والنَّصارى: صدَّقنا بالله الواحد المعبود بحق، وبما أنزل إلينا من القرآن الذي أوحاه الله إلى نبيه ورسوله محمد صلى الله عليه وسلم، وما أنزل من الصحف إلى إبراهيم وابنيه إسماعيل وإسحاق، وإلى يعقوب والأسباط -وهم الأنبياء مِن ولد يعقوب الذين كانوا في قبائل بني إسرائيل الاثنتي عشرة- وما أُعطي موسى من التوراة، وعيسى من الإنجيل، وما أُعطي الأنبياء جميعًا من وحي ربهم، لا نفرق بين أحد منهم في الإيمان، ونحن خاضعون لله بالطاعة والعبادة.');
INSERT INTO Quran__Arabic_Tafseer_Muyassar (QuranChapterID, VerseNumber, Rukuh, Sajdah, Manzil, HizbQuarter, VerseText) VALUES (2, 137, '0', '0', '0', '0', 'فإنْ آمن الكفار من اليهود والنصارى وغيرهم بمثل الذي آمنتم به، مما جاء به الرسول، فقد اهتدوا إلى الحق، وإن أعرضوا فإنما هم في خلاف شديد، فسيكفيك الله -أيها الرسول- شرَّهم وينصرك عليهم، وهو السميع لأقوالكم، العليم بأحوالكم.');
INSERT INTO Quran__Arabic_Tafseer_Muyassar (QuranChapterID, VerseNumber, Rukuh, Sajdah, Manzil, HizbQuarter, VerseText) VALUES (2, 138, '0', '0', '0', '0', 'الزموا دين الله الذي فطركم عليه، فليس هناك أحسنُ مِن فطرة الله التي فطر الناس عليها، فالزموها وقولوا نحن خاضعون مطيعون لربنا في اتباعنا ملَّة إبراهيم.');
INSERT INTO Quran__Arabic_Tafseer_Muyassar (QuranChapterID, VerseNumber, Rukuh, Sajdah, Manzil, HizbQuarter, VerseText) VALUES (2, 139, '0', '0', '0', '0', 'قل -أيها الرسول لأهل الكتاب-: أتجادلوننا في توحيد الله والإخلاص له، وهو رب العالمين جميعًا، لا يختص بقوم دون قوم، ولنا أعمالنا ولكم أعمالكم، ونحن لله مخلصو العبادة والطَّاعة لا نشرك به شيئًا، ولا نعبد أحدًا غيره.');
INSERT INTO Quran__Arabic_Tafseer_Muyassar (QuranChapterID, VerseNumber, Rukuh, Sajdah, Manzil, HizbQuarter, VerseText) VALUES (2, 140, '0', '0', '0', '0', 'بل أتقولون مجادلين في الله: إن إبراهيم وإسماعيل وإسحاق ويعقوب والأسباط- وهم الأنبياء الذين كانوا في قبائل بني إسرائيل الاثنتي عشرة من ولد يعقوب- كانوا على دين اليهود أو النصارى؟ وهذا كذب؛ فقد بُعِثوا وماتوا قبل نزول التوراة والإنجيل. قل لهم -أيها الرسول-: أأنتم أعلم بدينهم أم الله تعالى؟ وقد أخبر في القرآن بأنهم كانوا حنفاء مسلمين، ولا أحد أظلم منكم حين تخفون شهادة ثابتة عندكم من الله تعالى، وتدَّعون خلافها افتراء على الله. وما الله بغافل عن شيء من أعمالكم، بل هو مُحْصٍ لها ومجازيكم عليها.');
INSERT INTO Quran__Arabic_Tafseer_Muyassar (QuranChapterID, VerseNumber, Rukuh, Sajdah, Manzil, HizbQuarter, VerseText) VALUES (2, 141, '1', '0', '0', '0', 'تلك أُمَّة من أسلافكم قد مضَتْ، لهم أعمالهم ولكم أعمالكم، ولا تُسْألون عن أعمالهم، وهم لا يُسْألون عن أعمالكم. وفي الآية قطع للتعلق بالمخلوقين، وعدم الاغترار بالانتساب إليهم، وأن العبرة بالإيمان بالله وعبادته وحده، واتباع رسله، وأن من كفر برسول منهم فقد كفر بسائر الرسل.');
INSERT INTO Quran__Arabic_Tafseer_Muyassar (QuranChapterID, VerseNumber, Rukuh, Sajdah, Manzil, HizbQuarter, VerseText) VALUES (2, 142, '0', '0', '0', '1', 'سيقول الجهال وضعاف العقول من اليهود وأمثالهم، في سخرية واعتراض: ما الذي صرف هؤلاء المسلمين عن قبلتهم التي كانوا يُصَلُّون إلى جهتها أول الإسلام؛ (وهي "بيت المقدس") قل لهم -أيها الرسول-: المشرق والمغرب وما بينهما ملك لله، فليست جهة من الجهات خارجة عن ملكه، يهدي مَن يشاء من عباده إلى طريق الهداية القويم. وفي هذا إشعار بأن الشأن كله لله في امتثال أوامره، فحيثما وَجَّهَنا تَوَجَّهْنا.');
INSERT INTO Quran__Arabic_Tafseer_Muyassar (QuranChapterID, VerseNumber, Rukuh, Sajdah, Manzil, HizbQuarter, VerseText) VALUES (2, 143, '0', '0', '0', '0', 'وكما هديناكم -أيها المسلمون- إلى الطريق الصحيح في الدين، جعلناكم أمة خيارًا عدولا لتشهدوا على الأمم في الآخرة أن رسلهم بلَّغتهم رسالات ربهم، ويكون الرسول في الآخرة كذلك شهيدًا عليكم أنَّه بلَّغكم رسالة ربه. وما جعلنا -أيها الرسول- قبلة "بيت المقدس" التي كنت عليها، ثم صرفناك عنها إلى الكعبة بـ "مكة"، إلا ليظهر ما علمناه في الأزل؛ علما يتعلق به الثواب والعقاب لنميز مَن يتبعك ويطيعك ويستقبل معك حيث توجهت، ومَن هو ضعيف الإيمان فينقلب مرتدًا عن دينه لشكه ونفاقه. وإن هذه الحال التي هي تحول المسلم في صلاته من استقبال بيت المقدس إلى استقبال الكعبة لثقيلة شاقة إلا على الذين هداهم ومنّ عليهم بالإيمان والتقوى وما كان الله ليضيع إيمانكم به واتباعكم لرسوله، ويبطل صلاتكم إلى القبلة السابقة. إنه سبحانه وتعالى بالناس لرءوف رحيم.');
INSERT INTO Quran__Arabic_Tafseer_Muyassar (QuranChapterID, VerseNumber, Rukuh, Sajdah, Manzil, HizbQuarter, VerseText) VALUES (2, 144, '0', '0', '0', '0', 'قد نرى تحوُّل وجهك -أيها الرسول- في جهة السماء، مرة بعد مرة؛ انتظارًا لنزول الوحي إليك في شأن القبلة، فلنصرفنك عن "بيت المقدس" إلى قبلة تحبها وترضاها، وهي وجهة المسجد الحرام بـ "مكة"، فولِّ وجهك إليها. وفي أي مكان كنتم -أيها المسلمون- وأردتم الصلاة فتوجهوا نحو المسجد الحرام. وإن الذين أعطاهم الله علم الكتاب من اليهود والنصارى لَيعلمون أن تحويلك إلى الكعبة هو الحق الثابت في كتبهم. وما الله بغافل عما يعمل هؤلاء المعترضون المشككون، وسيجازيهم على ذلك.');
INSERT INTO Quran__Arabic_Tafseer_Muyassar (QuranChapterID, VerseNumber, Rukuh, Sajdah, Manzil, HizbQuarter, VerseText) VALUES (2, 145, '0', '0', '0', '0', 'ولئن جئت -أيها الرسول- الذين أُعطوا التوراة والإنجيل بكل حجة وبرهان على أن توجُّهك إلى الكعبة في الصلاة هو الحق من عند الله، ما تبعوا قبلتك عنادًا واستكبارًا، وما أنت بتابع قبلتهم مرة أخرى، وما بعضهم بتابع قبلة بعض. ولئن اتبعت أهواءهم في شأن القبلة وغيرها بعد ما جاءك من العلم بأنك على الحق وهم على الباطل، إنك حينئذ لمن الظالمين لأنفسهم. وهذا خطاب لجميع الأمة وهو تهديد ووعيد لمن يتبع أهواء المخالفين لشريعة الإسلام.');
INSERT INTO Quran__Arabic_Tafseer_Muyassar (QuranChapterID, VerseNumber, Rukuh, Sajdah, Manzil, HizbQuarter, VerseText) VALUES (2, 146, '0', '0', '0', '0', 'الذين أعطيناهم التوراة والإنجيل من أحبار اليهود وعلماء النصارى يعرفون أنَّ محمدًا صلى الله عليه وسلم رسول الله بأوصافه المذكورة في كتبهم، مثل معرفتهم بأبنائهم. وإن فريقًا منهم ليكتمون الحق وهم يعلمون صِدْقه، وثبوت أوصافه.');
INSERT INTO Quran__Arabic_Tafseer_Muyassar (QuranChapterID, VerseNumber, Rukuh, Sajdah, Manzil, HizbQuarter, VerseText) VALUES (2, 147, '1', '0', '0', '0', 'الذي أنزل إليك -أيها النبي- هو الحق من ربك، فلا تكونن من الشاكين فيه. وهذا وإن كان خطابا للرسول صلى الله عليه وسلم فهو موجه للأمة.');
INSERT INTO Quran__Arabic_Tafseer_Muyassar (QuranChapterID, VerseNumber, Rukuh, Sajdah, Manzil, HizbQuarter, VerseText) VALUES (2, 148, '0', '0', '0', '0', 'ولكل أمة من الأمم قبلة يتوجَّه إليها كل واحد منها في صلاته، فبادروا - أيها المؤمنون- متسابقين إلى فِعْل الأعمال الصالحة التي شرعها الله لكم في دين الإسلام. وسيجمعكم الله جميعا يوم القيامة من أي موضع كنتم فيه. إن الله على كل شيء قدير.');
INSERT INTO Quran__Arabic_Tafseer_Muyassar (QuranChapterID, VerseNumber, Rukuh, Sajdah, Manzil, HizbQuarter, VerseText) VALUES (2, 149, '0', '0', '0', '0', 'ومن أي مكان خَرَجْتَ -أيها النبي- مسافرًا، وأردت الصلاة، فوجِّه وجهك نحو المسجد الحرام. وإنَّ توجُّهك إليه لهو الحق الثابت من ربك. وما الله بغافل عما تعملونه، وسيجازيكم على ذلك.');
INSERT INTO Quran__Arabic_Tafseer_Muyassar (QuranChapterID, VerseNumber, Rukuh, Sajdah, Manzil, HizbQuarter, VerseText) VALUES (2, 150, '0', '0', '0', '0', 'ومن أى مكان خرجت -أيها النبي- فتوجَّه إلى المسجد الحرام، وحيثما كنتم -أيها المسلمون-، بأي قطر من أقطار الأرض فولُّوا وجوهكم نحو المسجد الحرام؛ لكي لا يكون للناس المخالفين لكم احتجاج عليكم بالمخاصمة والمجادلة، بعد هذا التوجه إليه، إلا أهل الظلم والعناد منهم، فسيظلُّون على جدالهم، فلا تخافوهم وخافوني بامتثال أمري، واجتناب نهيي؛ ولكي أتم نعمتي عليكم باختيار أكمل الشرائع لكم، ولعلكم تهتدون إلى الحق والصواب.');
INSERT INTO Quran__Arabic_Tafseer_Muyassar (QuranChapterID, VerseNumber, Rukuh, Sajdah, Manzil, HizbQuarter, VerseText) VALUES (2, 151, '0', '0', '0', '0', 'كما أنعمنا عليكم باستقبال الكعبة أرسلنا فيكم رسولا منكم يتلو عليكم الآيات المبينة للحق من الباطل، ويطهركم من دنس الشرك وسوء الأخلاق، ويعلمكم الكتاب والسنة وأحكام الشريعة، ويعلمكم من أخبار الأنبياء، وقصص الأمم السابقة ما كنتم تجهلونه.');
INSERT INTO Quran__Arabic_Tafseer_Muyassar (QuranChapterID, VerseNumber, Rukuh, Sajdah, Manzil, HizbQuarter, VerseText) VALUES (2, 152, '1', '0', '0', '0', 'أمر تعالى المؤمنين بذكره، ووعد عليه أفضل الجزاء، وهو الثناء في الملأ الأعلى على مَنْ ذكره، وخصوني -أيها المؤمنون- بالشكر قولا وعملا ولا تجحدوا نعمي عليكم.');
INSERT INTO Quran__Arabic_Tafseer_Muyassar (QuranChapterID, VerseNumber, Rukuh, Sajdah, Manzil, HizbQuarter, VerseText) VALUES (2, 153, '0', '0', '0', '0', 'يا أيها المؤمنون اطلبوا العون من الله في كل أموركم: بالصبر على النوائب والمصائب، وترك المعاصي والذنوب، والصبر على الطاعات والقربات، والصلاة التي تطمئن بها النفس، وتنهى عن الفحشاء والمنكر. إن الله مع الصابرين بعونه وتوفيقه وتسديده. وفي الآية: إثبات معيَّة الله الخاصة بالمؤمنين، المقتضية لما سلف ذكره؛ أما المعية العامة، المقتضية للعلم والإحاطة فهي لجميع الخلق.');
INSERT INTO Quran__Arabic_Tafseer_Muyassar (QuranChapterID, VerseNumber, Rukuh, Sajdah, Manzil, HizbQuarter, VerseText) VALUES (2, 154, '0', '0', '0', '0', 'ولا تقولوا -أيها المؤمنون- فيمن يُقتلون مجاهدين في سبيل الله: هم أموات، بل هم أحياء حياة خاصة بهم في قبورهم، لا يعلم كيفيتها إلا الله - تعالى-، ولكنكم لا تُحسُّون بها. وفي هذا دليل على نعيم القبر.');
INSERT INTO Quran__Arabic_Tafseer_Muyassar (QuranChapterID, VerseNumber, Rukuh, Sajdah, Manzil, HizbQuarter, VerseText) VALUES (2, 155, '0', '0', '0', '0', 'ولنختبرنكم بشيء يسير من الخوف، ومن الجوع، وبنقص من الأموال بتعسر الحصول عليها، أو ذهابها، ومن الأنفس: بالموت أو الشهادة في سبيل الله، وبنقص من ثمرات النخيل والأعناب والحبوب، بقلَّة ناتجها أو فسادها. وبشِّر -أيها النبي- الصابرين على هذا وأمثاله بما يفرحهم ويَسُرُّهم من حسن العاقبة في الدنيا والآخرة.');
INSERT INTO Quran__Arabic_Tafseer_Muyassar (QuranChapterID, VerseNumber, Rukuh, Sajdah, Manzil, HizbQuarter, VerseText) VALUES (2, 156, '0', '0', '0', '0', 'من صفة هؤلاء الصابرين أنهم إذا أصابهم شيء يكرهونه قالوا: إنَّا عبيد مملوكون لله، مدبَّرون بأمره وتصريفه، يفعل بنا ما يشاء، وإنا إليه راجعون بالموت، ثم بالبعث للحساب والجزاء.');
INSERT INTO Quran__Arabic_Tafseer_Muyassar (QuranChapterID, VerseNumber, Rukuh, Sajdah, Manzil, HizbQuarter, VerseText) VALUES (2, 157, '0', '0', '0', '0', 'أولئك الصابرون لهم ثناء من ربهم ورحمة عظيمة منه سبحانه، وأولئك هم المهتدون إلى الرشاد.');
INSERT INTO Quran__Arabic_Tafseer_Muyassar (QuranChapterID, VerseNumber, Rukuh, Sajdah, Manzil, HizbQuarter, VerseText) VALUES (2, 158, '0', '0', '0', '1', 'إن الصفا والمروة- وهما جبلان صغيران قرب الكعبة من جهة الشرق- من معالم دين الله الظاهرة التي تعبَّد الله عباده بالسعي بينهما. فمَن قصد الكعبة حاجًّا أو معتمرًا، فلا إثم عليه ولا حرج في أن يسعى بينهما، بل يجب عليه ذلك، ومن فعل الطاعات طواعية من نفسه مخلصًا بها لله تعالى، فإن الله تعالى شاكر يثيب على القليل بالكثير، عليم بأعمال عباده فلا يضعها، ولا يبخس أحدًا مثقال ذرة.');
INSERT INTO Quran__Arabic_Tafseer_Muyassar (QuranChapterID, VerseNumber, Rukuh, Sajdah, Manzil, HizbQuarter, VerseText) VALUES (2, 159, '0', '0', '0', '0', 'إن الذين يُخْفون ما أنزلنا من الآيات الواضحات الدالة على نبوة محمد صلى الله عليه وسلم وما جاء به، وهم أحبار اليهود وعلماء النصارى وغيرهم ممن يكتم ما أنزل الله من بعد ما أظهرناه للناس في التوراة والإنجيل، أولئك يطردهم الله من رحمته، ويدعو عليهم باللعنة جميع الخليقة.');
INSERT INTO Quran__Arabic_Tafseer_Muyassar (QuranChapterID, VerseNumber, Rukuh, Sajdah, Manzil, HizbQuarter, VerseText) VALUES (2, 160, '0', '0', '0', '0', 'إلا الذين رجعوا مستغفرين الله من خطاياهم، وأصلحوا ما أفسدوه، وبَيَّنوا ما كتموه، فأولئك أقبل توبتهم وأجازيهم بالمغفرة، وأنا التواب على من تاب من عبادي، الرحيم بهم؛ إذ وفقتُهم للتوبة وقبلتها منهم.');
INSERT INTO Quran__Arabic_Tafseer_Muyassar (QuranChapterID, VerseNumber, Rukuh, Sajdah, Manzil, HizbQuarter, VerseText) VALUES (2, 161, '0', '0', '0', '0', 'إن الذين جحدوا الإيمان وكتموا الحق، واستمروا على ذلك حتى ماتوا، أولئك عليهم لعنة الله والملائكة والناس أجمعين بالطرد من رحمته.');
INSERT INTO Quran__Arabic_Tafseer_Muyassar (QuranChapterID, VerseNumber, Rukuh, Sajdah, Manzil, HizbQuarter, VerseText) VALUES (2, 162, '0', '0', '0', '0', 'دائمين في اللعنة والنار، لا يخفف عنهم العذاب، ولا هم يمهلون بمعذرة يعتذرون بها.');
INSERT INTO Quran__Arabic_Tafseer_Muyassar (QuranChapterID, VerseNumber, Rukuh, Sajdah, Manzil, HizbQuarter, VerseText) VALUES (2, 163, '1', '0', '0', '0', 'وإلهكم -أيها الناس- إله واحد متفرد في ذاته وأسمائه وصفاته وأفعاله وعبودية خلقه له، لا معبود بحق إلا هو، الرحمن المتصف بالرحمة في ذاته وأفعاله لجميع الخلق، الرحيم بالمؤمنين.');
INSERT INTO Quran__Arabic_Tafseer_Muyassar (QuranChapterID, VerseNumber, Rukuh, Sajdah, Manzil, HizbQuarter, VerseText) VALUES (2, 164, '0', '0', '0', '0', 'إن في خلق السماوات بارتفاعها واتساعها، والأرض بجبالها وسهولها وبحارها، وفي اختلاف الليل والنهار من الطول والقصر، والظلمة والنور، وتعاقبهما بأن يخلف كل منهما الآخر، وفي السفن الجارية في البحار، التي تحمل ما ينفع الناس، وما أنزل الله من السماء من ماء المطر، فأحيا به الأرض، فصارت مخضرَّة ذات بهجة بعد أن كانت يابسة لا نبات فيها، وما نشره الله فيها من كل ما دبَّ على وجه الأرض، وما أنعم به عليكم من تقليب الرياح وتوجيهها، والسحاب المسيَّر بين السماء والأرض -إن في كل الدلائل السابقة لآياتٍ على وحدانية الله، وجليل نعمه، لقوم يعقلون مواضع الحجج، ويفهمون أدلته سبحانه على وحدانيته، واستحقاقه وحده للعبادة.');
INSERT INTO Quran__Arabic_Tafseer_Muyassar (QuranChapterID, VerseNumber, Rukuh, Sajdah, Manzil, HizbQuarter, VerseText) VALUES (2, 165, '0', '0', '0', '0', 'ومع هذه البراهين القاطعة يتخذ فريق من الناس من دون الله أصنامًا وأوثانًا وأولياء يجعلونهم نظراء لله تعالى، ويعطونهم من المحبة والتعظيم والطاعة، ما لا يليق إلا بالله وحده. والمؤمنون أعظم حبا لله من حب هؤلاء الكفار لله ولآلهتهم؛ لأن المؤمنين أخلصوا المحبة كلها لله، وأولئك أشركوا في المحبة. ولو يعلم الذين ظلموا أنفسهم بالشرك في الحياة الدنيا، حين يشاهدون عذاب الآخرة، أن الله هو المتفرد بالقوة جميعًا، وأن الله شديد العذاب، لما اتخذوا من دون الله آلهة يعبدونهم من دونه، ويتقربون بهم إليه.');
INSERT INTO Quran__Arabic_Tafseer_Muyassar (QuranChapterID, VerseNumber, Rukuh, Sajdah, Manzil, HizbQuarter, VerseText) VALUES (2, 166, '0', '0', '0', '0', 'عند معاينتهم عذاب الآخرة يتبرأ الرؤساء المتبوعون ممن اتبعهم على الشرك، وتنقطع بينهم كل الصلات التي ارتبطوا بها في الدنيا: من القرابة، والاتِّباع، والدين، وغير ذلك.');
INSERT INTO Quran__Arabic_Tafseer_Muyassar (QuranChapterID, VerseNumber, Rukuh, Sajdah, Manzil, HizbQuarter, VerseText) VALUES (2, 167, '1', '0', '0', '0', 'وقال التابعون: يا ليت لنا عودة إلى الدنيا، فنعلن براءتنا من هؤلاء الرؤساء، كما أعلنوا براءتهم مِنَّا. وكما أراهم الله شدة عذابه يوم القيامة يريهم أعمالهم الباطلة ندامات عليهم، وليسوا بخارجين من النار أبدًا.');
INSERT INTO Quran__Arabic_Tafseer_Muyassar (QuranChapterID, VerseNumber, Rukuh, Sajdah, Manzil, HizbQuarter, VerseText) VALUES (2, 168, '0', '0', '0', '0', 'يا أيها الناس كلوا من رزق الله الذي أباحه لكم في الأرض، وهو الطاهر غير النجس، النافع غير الضار، ولا تتبعوا طرق الشيطان في التحليل والتحريم، والبدع والمعاصي. إنه عدو لكم ظاهر العداوة.');
INSERT INTO Quran__Arabic_Tafseer_Muyassar (QuranChapterID, VerseNumber, Rukuh, Sajdah, Manzil, HizbQuarter, VerseText) VALUES (2, 169, '0', '0', '0', '0', 'إنما يأمركم الشيطان بكل ذنب قبيح يسوءُكم، وبكل معصية بالغة القبح، وبأن تفتروا على الله الكذب من تحريم الحلال وغيره بدون علم.');
INSERT INTO Quran__Arabic_Tafseer_Muyassar (QuranChapterID, VerseNumber, Rukuh, Sajdah, Manzil, HizbQuarter, VerseText) VALUES (2, 170, '0', '0', '0', '0', 'وإذا قال المؤمنون ناصحين أهل الضلال: اتبعوا ما أنزل الله من القرآن والهدى، أصرُّوا على تقليد أسلافهم المشركين قائلين: لا نتبع دينكم، بل نتبع ما وجدنا عليه آباءنا. أيتبعون آباءهم ولو كانوا لا يعقلون عن الله شيئًا، ولا يدركون رشدًا؟');
INSERT INTO Quran__Arabic_Tafseer_Muyassar (QuranChapterID, VerseNumber, Rukuh, Sajdah, Manzil, HizbQuarter, VerseText) VALUES (2, 171, '0', '0', '0', '0', 'وصفة الذين كفروا وداعيهم إلى الهدى والإيمان كصفة الراعي الذي يصيح بالبهائم ويزجرها، وهي لا تفهم معاني كلامه، وإنما تسمع النداء ودَوِيَّ الصوت فقط. هؤلاء الكفار صُمٌّ سدُّوا أسماعهم عن الحق، بُكْم أخرسوا ألسنتهم عن النطق به، عُمْي لا ترى أعينهم براهينه الباهرة، فهم لا يعملون عقولهم فيما ينفعهم.');
INSERT INTO Quran__Arabic_Tafseer_Muyassar (QuranChapterID, VerseNumber, Rukuh, Sajdah, Manzil, HizbQuarter, VerseText) VALUES (2, 172, '0', '0', '0', '0', 'يا أيها المؤمنون كلوا من الأطعمة المستلَذَّة الحلال التي رزقناكم، ولا تكونوا كالكفار الذين يحرمون الطيبات، ويستحِلُّون الخبائث، واشكروا لله نعمه العظيمة عليكم بقلوبكم وألسنتكم وجوارحكم، إن كنتم حقًا منقادين لأمره، سامعين مطيعين له، تعبدونه وحده لا شريك له.');
INSERT INTO Quran__Arabic_Tafseer_Muyassar (QuranChapterID, VerseNumber, Rukuh, Sajdah, Manzil, HizbQuarter, VerseText) VALUES (2, 173, '0', '0', '0', '0', 'إنما حرم الله عليكم ما يضركم كالميتة التي لم تذبح بطريقة شرعية، والدم المسفوح، ولحم الخنزير، والذبائح التي ذبحت لغير الله. ومِنْ فَضْلِ الله عليكم وتيسيره أنه أباح لكم أكل هذه المحرمات عند الضرورة. فمن ألجأته الضرورة إلى أكل شيء منها، غير ظالم في أكله فوق حاجته، ولا متجاوز حدود الله فيما أُبيح له، فلا ذنب عليه في ذلك. إن الله غفور لعباده، رحيم بهم.');
INSERT INTO Quran__Arabic_Tafseer_Muyassar (QuranChapterID, VerseNumber, Rukuh, Sajdah, Manzil, HizbQuarter, VerseText) VALUES (2, 174, '0', '0', '0', '0', 'إن الذين يُخْفون ما أنزل الله في كتبه من صفة محمد صلى الله عليه وسلم وغير ذلك من الحق، ويحرصون على أخذ عوض قليل من عرض الحياة الدنيا مقابل هذا الإخفاء، هؤلاء ما يأكلون في مقابلة كتمان الحق إلا نار جهنم تتأجج في بطونهم، ولا يكلمهم الله يوم القيامة لغضبه وسخطه عليهم، ولا يطهرهم من دنس ذنوبهم وكفرهم، ولهم عذاب موجع.');
INSERT INTO Quran__Arabic_Tafseer_Muyassar (QuranChapterID, VerseNumber, Rukuh, Sajdah, Manzil, HizbQuarter, VerseText) VALUES (2, 175, '0', '0', '0', '0', 'أولئك المتصفون بهذه الصفات استبدلوا الضلالة بالهدى وعذاب الله بمغفرته، فما أشد جراءتهم على النار بعملهم أعمال أهل النار!! يعجب الله من إقدامهم على ذلك، فاعجبوا -أيها الناس- من جراءتهم، ومن صبرهم على النار ومكثهم فيها. وهذا على وجه الاستهانة بهم، والاستخفاف بأمرهم.');
INSERT INTO Quran__Arabic_Tafseer_Muyassar (QuranChapterID, VerseNumber, Rukuh, Sajdah, Manzil, HizbQuarter, VerseText) VALUES (2, 176, '1', '0', '0', '0', 'ذلك العذاب الذي استحقوه بسبب أن الله تعالى نزَّل كتبه على رسله مشتملة على الحق المبين، فكفروا به. وإن الذين اختلفوا في الكتاب فأمنوا ببعضه وكفروا ببعضه، لفي منازعة ومفارقة بعيدة عن الرشد والصواب.');
INSERT INTO Quran__Arabic_Tafseer_Muyassar (QuranChapterID, VerseNumber, Rukuh, Sajdah, Manzil, HizbQuarter, VerseText) VALUES (2, 177, '0', '0', '0', '1', 'ليس الخير عند الله- تعالى- في التوجه في الصلاة إلى جهة المشرق والمغرب إن لم يكن عن أمر الله وشرعه، وإنما الخير كل الخير هو إيمان من آمن بالله وصدَّق به معبودًا وحدَه لا شريك له، وآمن بيوم البعث والجزاء، وبالملائكة جميعًا، وبالكتب المنزلة كافة، وبجميع النبيين من غير تفريق، وأعطى المال تطوُّعًا -مع شدة حبه- ذوي القربى، واليتامى المحتاجين الذين مات آباؤهم وهم دون سن البلوغ، والمساكين الذين أرهقهم الفقر، والمسافرين المحتاجين الذين بَعُدوا عن أهلهم ومالهم، والسائلين الذين اضطروا إلى السؤال لشدة حاجتهم، وأنفق في تحرير الرقيق والأسرى، وأقام الصلاة، وأدى الزكاة المفروضة، والذين يوفون بالعهود، ومن صبر في حال فقره ومرضه، وفي شدة القتال. أولئك المتصفون بهذه الصفات هم الذين صدقوا في إيمانهم، وأولئك هم الذين اتقَوا عقاب الله فتجنبوا معاصيه.');
INSERT INTO Quran__Arabic_Tafseer_Muyassar (QuranChapterID, VerseNumber, Rukuh, Sajdah, Manzil, HizbQuarter, VerseText) VALUES (2, 178, '0', '0', '0', '0', 'يا أيها الذين صدقوا الله ورسوله وعملوا بشرعه فرض الله عليكم أن تقتصوا من القاتل عمدا بقتله، بشرط المساواة والمماثلة: يُقتل الحر بمثله، والعبد بمثله، والأنثى بمثلها. فمن سامحه ولي المقتول بالعفو عن الاقتصاص منه والاكتفاء بأخذ الدية -وهي قدر مالي محدد يدفعه الجاني مقابل العفو عنه- فليلتزم الطرفان بحسن الخلق، فيطالب الولي بالدية من غير عنف، ويدفع القاتل إليه حقه بإحسان، مِن غير تأخير ولا نقص. ذلك العفو مع أخذ الدية تخفيف من ربكم ورحمة بكم؛ لما فيه من التسهيل والانتفاع. فمَن قتل القاتل بعد العفو عنه وأَخْذِ الدية فله عذاب أليم بقتله قصاصًا في الدنيا، أو بالنار في الآخرة.');
INSERT INTO Quran__Arabic_Tafseer_Muyassar (QuranChapterID, VerseNumber, Rukuh, Sajdah, Manzil, HizbQuarter, VerseText) VALUES (2, 179, '0', '0', '0', '0', 'ولكم في تشريع القصاص وتنفيذه حياة آمنة -يا أصحاب العقول السليمة-؛ رجاء تقوى الله وخشيته بطاعته دائمًا.');
INSERT INTO Quran__Arabic_Tafseer_Muyassar (QuranChapterID, VerseNumber, Rukuh, Sajdah, Manzil, HizbQuarter, VerseText) VALUES (2, 180, '0', '0', '0', '0', 'فرض الله عليكم إذا حضر أحدكم علامات الموت ومقدماته -إن ترك مالا- الوصية بجزء من ماله للوالدين والأقربين مع مراعاة العدل؛ فلا يدع الفقير ويوصي للغني، ولا يتجاوز الثلث، وذلك حق ثابت يعمل به أهل التقوى الذين يخافون الله. وكان هذا قبل نزول آيات المواريث التي حدَّد الله فيها نصيب كل وارث.');
INSERT INTO Quran__Arabic_Tafseer_Muyassar (QuranChapterID, VerseNumber, Rukuh, Sajdah, Manzil, HizbQuarter, VerseText) VALUES (2, 181, '0', '0', '0', '0', 'فمَن غَيَّر وصية الميت بعدما سمعها منه قبل موته، فإنما الذنب على مَن غيَّر وبدَّل. إن الله سميع لوصيتكم وأقوالكم، عليم بما تخفيه صدوركم من الميل إلى الحق والعدل أو الجور والحيف، وسيجازيكم على ذلك.');
INSERT INTO Quran__Arabic_Tafseer_Muyassar (QuranChapterID, VerseNumber, Rukuh, Sajdah, Manzil, HizbQuarter, VerseText) VALUES (2, 182, '1', '0', '0', '0', 'فمَن علم مِن موصٍ ميلا عن الحق في وصيته على سبيل الخطأ أو العمد، فنصح الموصيَ وقت الوصية بما هو الأعدل، فإن لم يحصل له ذلك فأصلح بين الأطراف بتغيير الوصية؛ لتوافق الشريعة، فلا ذنب عليه في هذا الإصلاح. إن الله غفور لعباده، رحيم بهم.');
INSERT INTO Quran__Arabic_Tafseer_Muyassar (QuranChapterID, VerseNumber, Rukuh, Sajdah, Manzil, HizbQuarter, VerseText) VALUES (2, 183, '0', '0', '0', '0', 'يا أيها الذين صدَّقوا الله ورسوله وعملوا بشرعه، فرض الله عليكم الصيام كما فرضه على الأمم قبلكم؛ لعلكم تتقون ربكم، فتجعلون بينكم وبين المعاصي وقاية بطاعته وعبادته وحده.');
INSERT INTO Quran__Arabic_Tafseer_Muyassar (QuranChapterID, VerseNumber, Rukuh, Sajdah, Manzil, HizbQuarter, VerseText) VALUES (2, 184, '0', '0', '0', '0', 'فرض الله عليكم صيام أيام معلومة العدد وهي أيام شهر رمضان. فمن كان منكم مريضًا يشق عليه الصوم، أو مسافرًا فله أن يفطر، وعليه صيام عدد من أيام أُخَر بقدر التي أفطر فيها. وعلى الذين يتكلفون الصيام ويشقُّ عليهم مشقة غير محتملة كالشيخ الكبير، والمريض الذي لا يُرْجَى شفاؤه، فدية عن كل يوم يفطره، وهي طعام مسكين، فمن زاد في قدر الفدية تبرعًا منه فهو خير له، وصيامكم خير لكم -مع تحمُّل المشقة- من إعطاء الفدية، إن كنتم تعلمون الفضل العظيم للصوم عند الله تعالى.');
INSERT INTO Quran__Arabic_Tafseer_Muyassar (QuranChapterID, VerseNumber, Rukuh, Sajdah, Manzil, HizbQuarter, VerseText) VALUES (2, 185, '0', '0', '0', '0', 'شهر رمضان الذي ابتدأ الله فيه إنزال القرآن في ليلة القدر؛ هداية للناس إلى الحق، فيه أوضح الدلائل على هدى الله، وعلى الفارق بين الحق والباطل. فمن حضر منكم الشهر وكان صحيحًا مقيمًا فليصم نهاره. ويُرخَّص للمريض والمسافر في الفطر، ثم يقضيان عدد تلك الأيام. يريد الله تعالى بكم اليسر والسهولة في شرائعه، ولا يريد بكم العسر والمشقة، ولتكملوا عدة الصيام شهرًا، ولتختموا الصيام بتكبير الله في عيد الفطر، ولتعظموه على هدايته لكم، ولكي تشكروا له على ما أنعم به عليكم من الهداية والتوفيق والتيسير.');
INSERT INTO Quran__Arabic_Tafseer_Muyassar (QuranChapterID, VerseNumber, Rukuh, Sajdah, Manzil, HizbQuarter, VerseText) VALUES (2, 186, '0', '0', '0', '0', 'وإذا سألك -أيها النبي- عبادي عني فقل لهم: إني قريب منهم، أُجيب دعوة الداعي إذا دعاني، فليطيعوني فيما أمرتهم به ونهيتهم عنه، وليؤمنوا بي، لعلهم يهتدون إلى مصالح دينهم ودنياهم. وفي هذه الآية إخبار منه سبحانه عن قربه من عباده، القرب اللائق بجلاله.');
INSERT INTO Quran__Arabic_Tafseer_Muyassar (QuranChapterID, VerseNumber, Rukuh, Sajdah, Manzil, HizbQuarter, VerseText) VALUES (2, 187, '0', '0', '0', '0', 'أباح الله لكم في ليالي شهر رمضان جماعَ نسائكم، هنَّ ستر وحفظ لكم، وأنتم ستر وحفظ لهن. علم الله أنكم كنتم تخونون أنفسكم؛ بمخالفة ما حرَّمه الله عليكم من مجامعة النساء بعد العشاء في ليالي الصيام -وكان ذلك في أول الإسلام-، فتاب الله عليكم ووسَّع لكم في الأمر، فالآن جامعوهن، واطلبوا ما قدَّره الله لكم من الأولاد، وكلوا واشربوا حتى يتبَيَّن ضياء الصباح من سواد الليل، بظهور الفجر الصادق، ثم أتموا الصيام بالإمساك عن المفطرات إلى دخول الليل بغروب الشمس. ولا تجامعوا نساءكم أو تتعاطوا ما يفضي إلى جماعهن إذا كنتم معتكفين في المساجد؛ لأن هذا يفسد الاعتكاف (وهو الإقامة في المسجد مدة معلومة بنيَّة التقرب إلى الله تعالى). تلك الأحكام التي شرعها الله لكم هي حدوده الفاصلة بين الحلال والحرام، فلا تقربوها حتى لا تقعوا في الحرام. بمثل هذا البيان الواضح يبين الله آياته وأحكامه للناس؛ كي يتقوه ويخشَوْه.');
INSERT INTO Quran__Arabic_Tafseer_Muyassar (QuranChapterID, VerseNumber, Rukuh, Sajdah, Manzil, HizbQuarter, VerseText) VALUES (2, 188, '1', '0', '0', '0', 'ولا يأكل بعضكم مال بعض بسبب باطل كاليمين الكاذبة، والغصب، والسرقة، والرشوة، والربا ونحو ذلك، ولا تلقوا بالحجج الباطلة إلى الحكام؛ لتأكلوا عن طريق التخاصم أموال طائفة من الناس بالباطل، وأنتم تعلمون تحريم ذلك عليكم.');
INSERT INTO Quran__Arabic_Tafseer_Muyassar (QuranChapterID, VerseNumber, Rukuh, Sajdah, Manzil, HizbQuarter, VerseText) VALUES (2, 189, '0', '0', '0', '1', 'يسألك أصحابك -أيها النبي-: عن الأهلة وتغيُّر أحوالها، قل لهم: جعل اللهُ الأهلة علامات يعرف بها الناس أوقات عباداتهم المحددة بوقت مثل الصيام والحج، ومعاملاتهم. وليس الخير ما تعودتم عليه في الجاهلية وأول الإسلام من دخول البيوت من ظهورها حين تُحْرِمون بالحج أو العمرة، ظانين أن ذلك قربة إلى الله، ولكن الخير هو فِعْلُ مَنِ اتقى الله واجتنب المعاصي، وادخلوا البيوت من أبوابها عند إحرامكم بالحج أو العمرة، واخشوا الله تعالى في كل أموركم، لتفوزوا بكل ما تحبون من خيري الدنيا والآخرة.');
INSERT INTO Quran__Arabic_Tafseer_Muyassar (QuranChapterID, VerseNumber, Rukuh, Sajdah, Manzil, HizbQuarter, VerseText) VALUES (2, 190, '0', '0', '0', '0', 'وقاتلوا -أيها المؤمنون- لنصرة دين الله الذين يقاتلونكم، ولا ترتكبوا المناهي من المُثْلة، والغُلول، وقَتْلِ من لا يحل قتله من النساء والصبيان والشيوخ، ومن في حكمهم. إن الله لا يحب الذين يجاوزون حدوده، فيستحلون ما حرَّم الله ورسوله.');
INSERT INTO Quran__Arabic_Tafseer_Muyassar (QuranChapterID, VerseNumber, Rukuh, Sajdah, Manzil, HizbQuarter, VerseText) VALUES (2, 191, '0', '0', '0', '0', 'واقتلوا الذين يقاتلونكم من المشركين حيث وجدتموهم، وأخرجوهم من المكان الذي أخرجوكم منه وهو "مكة". والفتنة -وهي الكفر والشرك والصد عن الإسلام- أشد من قتلكم إياهم. ولا تبدؤوهم بالقتال عند المسجد الحرام تعظيمًا لحرماته حتى يبدؤوكم بالقتال فيه، فإن قاتلوكم في المسجد الحرام فاقتلوهم فيه. مثل ذلك الجزاء الرادع يكون جزاء الكافرين.');
INSERT INTO Quran__Arabic_Tafseer_Muyassar (QuranChapterID, VerseNumber, Rukuh, Sajdah, Manzil, HizbQuarter, VerseText) VALUES (2, 192, '0', '0', '0', '0', 'فإن تركوا ما هم فيه من الكفر وقتالكم عند المسجد الحرام، ودخلوا في الإيمان، فإن الله غفور لعباده، رحيم بهم.');
INSERT INTO Quran__Arabic_Tafseer_Muyassar (QuranChapterID, VerseNumber, Rukuh, Sajdah, Manzil, HizbQuarter, VerseText) VALUES (2, 193, '0', '0', '0', '0', 'واستمروا- أيها المؤمنون- في قتال المشركين المعتدين، حتى لا تكون فتنة للمسلمين عن دينهم ولا شرك بالله، ويبقى الدين لله وحده خالصًا لا يُعْبَد معه غيره. فإن كفُّوا عن الكفر والقتال فكُفُّوا عنهم؛ فالعقوبة لا تكون إلا على المستمرين على كفرهم وعدوانهم.');
INSERT INTO Quran__Arabic_Tafseer_Muyassar (QuranChapterID, VerseNumber, Rukuh, Sajdah, Manzil, HizbQuarter, VerseText) VALUES (2, 194, '0', '0', '0', '0', 'قتالكم -أيها المؤمنون- للمشركين في الشهر الذي حرَّم الله القتال فيه هو جزاء لقتالهم لكم في الشهر الحرام. والذي يعتدي على ما حَرَّم الله من المكان والزمان، يعاقب بمثل فعله، ومن جنس عمله. فمن اعتدى عليكم بالقتال أو غيره فأنزلوا به عقوبة مماثلة لجنايته، ولا حرج عليكم في ذلك؛ لأنهم هم البادئون بالعدوان، وخافوا الله فلا تتجاوزوا المماثلة في العقوبة، واعلموا أن الله مع الذين يتقونه ويطيعونه بأداء فرائضه وتجنب محارمه.');
INSERT INTO Quran__Arabic_Tafseer_Muyassar (QuranChapterID, VerseNumber, Rukuh, Sajdah, Manzil, HizbQuarter, VerseText) VALUES (2, 195, '0', '0', '0', '0', 'واستمروا- أيها المؤمنون- في إنفاق الأموال لنصرة دين الله تعالى، والجهاد في سبيله، ولا توقعوا أنفسكم في المهالك بترك الجهاد في سبيل الله، وعدم الإنفاق فيه، وأحسنوا في الانفاق والطاعة، واجعلوا عملكم كله خالصًا لوجه الله تعالى. إن الله يحب أهل الإخلاص والإحسان.');
INSERT INTO Quran__Arabic_Tafseer_Muyassar (QuranChapterID, VerseNumber, Rukuh, Sajdah, Manzil, HizbQuarter, VerseText) VALUES (2, 196, '1', '0', '0', '0', 'وأدُّوا الحج والعمرة تامَّيْنِ، خالصين لوجه الله تعالى. فإن منعكم عن الذهاب لإتمامهما بعد الإحرام بهما مانع كالعدو والمرض، فالواجب عليكم ذَبْحُ ما تيسر لكم من الإبل أو البقر أو الغنم تقربًا إلى الله تعالى؛ لكي تَخْرُجوا من إحرامكم بحلق شعر الرأس أو تقصيره، ولا تحلقوا رؤوسكم إذا كنتم محصرين حتى ينحر المحصر هديه في الموضع الذي حُصر فيه ثم يحل من إحرامه، كما نحر النبي صلى الله عليه وسلم في "الحديبية" ثم حلق رأسه، وغير المحصر لا ينحر الهدي إلا في الحرم، الذي هو محله في يوم العيد، اليوم العاشر وما بعده من أيام التشريق. فمن كان منكم مريضًا، أو به أذى من رأسه يحتاج معه إلى الحلق -وهو مُحْرِم- حَلَق، وعليه فدية: بأن يصوم ثلاثة أيام، أو يتصدق على ستة مساكين لكل مسكين نصف صاع من طعام، أو يذبح شاة لفقراء الحرم. فإذا كنتم في أمن وصحَّة: فمن استمتع بالعمرة إلى الحج وذلك باستباحة ما حُرِّم عليه بسبب الإحرام بعد انتهاء عمرته، فعليه ذبح ما تيسر من الهدي، فمن لم يجد هَدْيًا يذبحه فعليه صيام ثلاثة أيام في أشهر الحج، وسبعة إذا فرغتم من أعمال الحج ورجعتم إلى أهليكم، تلك عشرة كاملة لا بد من صيامها. ذلك الهَدْيُ وما ترتب عليه من الصيام لمن لم يكن أهله من ساكني أرض الحرم، وخافوا الله تعالى وحافظوا على امتثال أوامره واجتناب نواهيه، واعلموا أن الله شديد العقاب لمن خالف أمره، وارتكب ما عنه زجر.');
INSERT INTO Quran__Arabic_Tafseer_Muyassar (QuranChapterID, VerseNumber, Rukuh, Sajdah, Manzil, HizbQuarter, VerseText) VALUES (2, 197, '0', '0', '0', '0', 'وقت الحج أشهر معلومات، وهي: شوال، وذو القعدة، وعشر من ذي الحجة. فمن أوجب الحج على نفسه فيهن بالإحرام، فيحرم عليه الجماع ومقدماته القولية والفعلية، ويحرم عليه الخروج عن طاعة الله تعالى بفعل المعاصي، والجدال في الحج الذي يؤدي إلى الغضب والكراهية. وما تفعلوا من خير يعلمه الله، فيجازي كلا على عمله. وخذوا لأنفسكم زادًا من الطعام والشراب لسفر الحج، وزادًا من صالح الأعمال للدار الآخرة، فإن خير الزاد تقوى الله، وخافوني يا أصحاب العقول السليمة.');
INSERT INTO Quran__Arabic_Tafseer_Muyassar (QuranChapterID, VerseNumber, Rukuh, Sajdah, Manzil, HizbQuarter, VerseText) VALUES (2, 198, '0', '0', '0', '0', 'ليس عليكم حرج في أن تطلبوا رزقًا من ربكم بالربح من التجارة في أيام الحج. فإذا دفعتم بعد غروب الشمس راجعين من "عرفات" -وهي المكان الذي يقف فيه الحجاج يوم التاسع من ذي الحجة- فاذكروا الله بالتسبيح والتلبية والدعاء عند المشعر الحرام -"المزدلفة"-، واذكروا الله على الوجه الصحيح الذي هداكم إليه، ولقد كنتم من قبل هذا الهدى في ضلال لا تعرفون معه الحق.');
INSERT INTO Quran__Arabic_Tafseer_Muyassar (QuranChapterID, VerseNumber, Rukuh, Sajdah, Manzil, HizbQuarter, VerseText) VALUES (2, 199, '0', '0', '0', '0', 'وليكن اندفاعكم من "عرفات" التي أفاض منها إبراهيم عليه السلام مخالفين بذلك من لا يقف بها من أهل الجاهلية، واسألوا الله أن يغفر لكم ذنوبكم. إن الله غفور لعباده المستغفرين التائبين، رحيم بهم.');
INSERT INTO Quran__Arabic_Tafseer_Muyassar (QuranChapterID, VerseNumber, Rukuh, Sajdah, Manzil, HizbQuarter, VerseText) VALUES (2, 200, '0', '0', '0', '0', 'فإذا أتممتم عبادتكم، وفرغتم من أعمال الحج، فأكثروا من ذكر الله والثناء عليه، مثل ذكركم مفاخر آبائكم وأعظم من ذلك. فمن الناس فريق يجعل همه الدنيا فقط، فيدعو قائلا ربنا آتنا في الدنيا صحة، ومالا وأولادًا، وهؤلاء ليس لهم في الآخرة حظ ولا نصيب؛ لرغبتهم عنها وقَصْرِ هَمِّهم على الدنيا.');
INSERT INTO Quran__Arabic_Tafseer_Muyassar (QuranChapterID, VerseNumber, Rukuh, Sajdah, Manzil, HizbQuarter, VerseText) VALUES (2, 201, '0', '0', '0', '0', 'ومن الناس فريق مؤمن يقول في دعائه: ربنا آتنا في الدنيا عافية ورزقًا وعلمًا نافعًا، وعملا صالحًا، وغير ذلك من أمور الدين والدنيا، وفي الآخرة الجنة، واصرف عنَّا عذاب النار. وهذا الدعاء من أجمع الأدعية، ولهذا كان أكثر دعاء النبي صلى الله عليه وسلم، كما ثبت في الصحيحين.');
INSERT INTO Quran__Arabic_Tafseer_Muyassar (QuranChapterID, VerseNumber, Rukuh, Sajdah, Manzil, HizbQuarter, VerseText) VALUES (2, 202, '0', '0', '0', '0', 'أولئك الداعون بهذا الدعاء لهم ثواب عظيم بسبب ما كسبوه من الأعمال الصالحة. والله سريع الحساب، مُحْصٍ أعمال عباده، ومجازيهم بها.');
INSERT INTO Quran__Arabic_Tafseer_Muyassar (QuranChapterID, VerseNumber, Rukuh, Sajdah, Manzil, HizbQuarter, VerseText) VALUES (2, 203, '0', '0', '0', '1', 'واذكروا الله تسبيحًا وتكبيرًا في أيام قلائل، وهي أيام التشريق: الحادي عشر والثاني عشر والثالث عشر من شهر ذي الحجة. فمن أراد التعجل وخرج من "مِنى" قبل غروب شمس اليوم الثاني عشر بعد رمي الجمار فلا ذنب عليه، ومن تأخر بأن بات بـ "مِنى" حتى يرمي الجمار في اليوم الثالث عشر فلا ذنب عليه، لمن اتقى الله في حجه. والتأخر أفضل؛ لأنه تزوُّد في العبادة واقتداء بفعل النبي صلى الله عليه وسلم. وخافوا الله- أيها المسلمون- وراقبوه في كل أعمالكم، واعلموا أنكم إليه وحده تُحْشَرون بعد موتكم للحساب والجزاء.');
INSERT INTO Quran__Arabic_Tafseer_Muyassar (QuranChapterID, VerseNumber, Rukuh, Sajdah, Manzil, HizbQuarter, VerseText) VALUES (2, 204, '0', '0', '0', '0', 'وبعض الناس من المنافقين يعجبك -أيها الرسول- كلامه الفصيح الذي يريد به حظًّا من حظوظ الدنيا لا الآخرة، ويحلف مستشهدًا بالله على ما في قلبه من محبة الإسلام، وفي هذا غاية الجرأة على الله، وهو شديد العداوة والخصومة للإسلام والمسلمين.');
INSERT INTO Quran__Arabic_Tafseer_Muyassar (QuranChapterID, VerseNumber, Rukuh, Sajdah, Manzil, HizbQuarter, VerseText) VALUES (2, 205, '0', '0', '0', '0', 'وإذا خرج من عندك أيها الرسول، جَدَّ ونَشِط في الأرض ليفسد فيها، ويتلف زروع الناس، ويقتل ماشيتهم. والله لا يحب الفساد.');
INSERT INTO Quran__Arabic_Tafseer_Muyassar (QuranChapterID, VerseNumber, Rukuh, Sajdah, Manzil, HizbQuarter, VerseText) VALUES (2, 206, '0', '0', '0', '0', 'وإذا نُصِح ذلك المنافق المفسد، وقيل له: اتق الله واحذر عقابه، وكُفَّ عن الفساد في الأرض، لم يقبل النصيحة، بل يحمله الكبر وحميَّة الجاهلية على مزيد من الآثام، فَحَسْبُه جهنم وكافيته عذابًا، ولبئس الفراش هي.');
INSERT INTO Quran__Arabic_Tafseer_Muyassar (QuranChapterID, VerseNumber, Rukuh, Sajdah, Manzil, HizbQuarter, VerseText) VALUES (2, 207, '0', '0', '0', '0', 'وبعض الناس يبيع نفسه طلبًا لرضا الله عنه، بالجهاد في سبيله، والتزام طاعته. والله رءوف بالعباد، يرحم عباده المؤمنين رحمة واسعة في عاجلهم وآجلهم، فيجازبهم أحسن الجزاء.');
INSERT INTO Quran__Arabic_Tafseer_Muyassar (QuranChapterID, VerseNumber, Rukuh, Sajdah, Manzil, HizbQuarter, VerseText) VALUES (2, 208, '0', '0', '0', '0', 'يا أيها الذين آمنوا بالله ربًا وبمحمد نبيًا ورسولا وبالإسلام دينًا، ادخلوا في جميع شرائع الإسلام، عاملين بجميع أحكامه، ولا تتركوا منها شيئًا، ولا تتبعوا طرق الشيطان فيما يدعوكم إليه من المعاصي. إنه لكم عدو ظاهر العداوة فاحذروه.');
INSERT INTO Quran__Arabic_Tafseer_Muyassar (QuranChapterID, VerseNumber, Rukuh, Sajdah, Manzil, HizbQuarter, VerseText) VALUES (2, 209, '0', '0', '0', '0', 'فإن انحرفتم عن طريق الحق، من بعد ما جاءتكم الحجج الواضحة من القرآن والسنة، فاعلموا أن الله عزيز في ملكه لا يفوته شيء، حكيم في أمره ونهيه، يضع كل شيء في موضعه المناسب له.');
INSERT INTO Quran__Arabic_Tafseer_Muyassar (QuranChapterID, VerseNumber, Rukuh, Sajdah, Manzil, HizbQuarter, VerseText) VALUES (2, 210, '1', '0', '0', '0', 'ما ينتظر هؤلاء المعاندون الكافرون بعد قيام الأدلة البينة إلا أن يأتيهم الله عز وجل على الوجه اللائق به سبحانه في ظُلَل من السحاب يوم القيامة؛ ليفصل بينهم بالقضاء العادل، وأن تأتي الملائكة، وحينئذ يقضي الله تعالى فيهم قضاءه. وإليه وحده ترجع أمور الخلائق جميعها.');
INSERT INTO Quran__Arabic_Tafseer_Muyassar (QuranChapterID, VerseNumber, Rukuh, Sajdah, Manzil, HizbQuarter, VerseText) VALUES (2, 211, '0', '0', '0', '0', 'سل -أيها الرسول- بني إسرائيل المعاندين لك: كم أعطيناهم من آيات واضحات في كتبهم تهديهم إلى الحق، فكفروا بها كلها، وأعرضوا عنها، وحَرَّفوها عن مواضعها. ومن يبدل نعمة الله -وهي دينه- ويكفر بها من بعد معرفتها، وقيام الحجة عليه بها، فإن الله تعالى شديد العقاب له.');
INSERT INTO Quran__Arabic_Tafseer_Muyassar (QuranChapterID, VerseNumber, Rukuh, Sajdah, Manzil, HizbQuarter, VerseText) VALUES (2, 212, '0', '0', '0', '0', 'حُسِّن للذين جحدوا وحدانية الله الحياةُ الدنيا وما فيها من الشهوات والملذات، وهم يستهزئون بالمؤمنين. وهؤلاء الذين يخشون ربهم فوق جميع الكفار يوم القيامة؛ حيث يدخلهم الله أعلى درجات الجنة، وينزل الكافرين أسفل دركات النار. والله يرزق مَن يشاء مِن خلقه بغير حساب.');
INSERT INTO Quran__Arabic_Tafseer_Muyassar (QuranChapterID, VerseNumber, Rukuh, Sajdah, Manzil, HizbQuarter, VerseText) VALUES (2, 213, '0', '0', '0', '0', 'كان الناس جماعة واحدة، متفقين على الإيمان بالله ثم اختلفوا في دينهم، فبعث الله النبيين دعاة لدين الله، مبشرين مَن أطاع الله بالجنة، ومحذرين من كفر به وعصاه النار، وأنزل معهم الكتب السماوية بالحق الذي اشتملت عليه؛ ليحكموا بما فيها بين الناس فيما اختلفوا فيه، وما اخْتَلَف في أمر محمد صلى الله عليه وسلم وكتابه ظلمًا وحسدًا إلا الذين أعطاهم الله التوراة، وعرفوا ما فيها من الحجج والأحكام، فوفَّق الله المؤمنين بفضله إلى تمييز الحق من الباطل، ومعرفة ما اختلفوا فيه. والله يوفِّق من يشاء من عباده إلى طريق مستقيم.');
INSERT INTO Quran__Arabic_Tafseer_Muyassar (QuranChapterID, VerseNumber, Rukuh, Sajdah, Manzil, HizbQuarter, VerseText) VALUES (2, 214, '0', '0', '0', '0', 'بل أظننتم -أيها المؤمنون- أن تدخلوا الجنة، ولمَّا يصبكم من الابتلاء مثل ما أصاب المؤمنين الذين مضوا من قبلكم: من الفقر والأمراض والخوف والرعب، وزُلزلوا بأنواع المخاوف، حتى قال رسولهم والمؤمنون معه -على سبيل الاستعجال للنصر من الله تعالى-: متى نصر الله؟ ألا إن نصر الله قريب من المؤمنين.');
INSERT INTO Quran__Arabic_Tafseer_Muyassar (QuranChapterID, VerseNumber, Rukuh, Sajdah, Manzil, HizbQuarter, VerseText) VALUES (2, 215, '0', '0', '0', '0', 'يسألك أصحابك -أيها النبي- أي شيء ينفقون من أصناف أموالهم تقربًا إلى الله تعالى، وعلى مَن ينفقون؟ قل لهم: أنفقوا أيَّ خير يتيسر لكم من أصناف المال الحلال الطيب، واجعلوا نفقتكم للوالدين، والأقربين من أهلكم وذوي أرحامكم، واليتامى، والفقراء، والمسافر المحتاج الذي بَعُدَ عن أهله وماله. وما تفعلوا من خير فإن الله تعالى به عليم.');
INSERT INTO Quran__Arabic_Tafseer_Muyassar (QuranChapterID, VerseNumber, Rukuh, Sajdah, Manzil, HizbQuarter, VerseText) VALUES (2, 216, '1', '0', '0', '0', 'فرض الله عليكم -أيها المؤمنون- قتال الكفار، والقتال مكروه لكم من جهة الطبع؛ لمشقته وكثرة مخاطره، وقد تكرهون شيئًا وهو في حقيقته خير لكم، وقد تحبون شيئًا لما فيه من الراحة أو اللذة العاجلة، وهو شر لكم. والله تعالى يعلم ما هو خير لكم، وأنتم لا تعلمون ذلك. فبادروا إلى الجهاد في سبيله.');
INSERT INTO Quran__Arabic_Tafseer_Muyassar (QuranChapterID, VerseNumber, Rukuh, Sajdah, Manzil, HizbQuarter, VerseText) VALUES (2, 217, '0', '0', '0', '0', 'يسألك المشركون -أيها الرسول- عن الشهر الحرام: هل يحل فيه القتال؟ قل لهم: القتال في الشهر الحرام عظيم عند الله استحلاله وسفك الدماء فيه، ومَنْعكم الناس من دخول الإسلام بالتعذيب والتخويف، وجحودكم بالله وبرسوله وبدينه، ومَنْع المسلمين من دخول المسجد الحرام، وإخراج النبي والمهاجرين منه وهم أهله وأولياؤه، ذلك أكبر ذنبًا، وأعظم جرمًا عند الله من القتال في الشهر الحرام. والشرك الذي أنتم فيه أكبر وأشد من القتل في الشهر الحرام. وهؤلاء الكفار لم يرتدعوا عن جرائمهم، بل هم مستمرون عليها، ولا يزالون يقاتلونكم حتى يردوكم عن الإسلام إلى الكفر إن استطاعوا تحقيق ذلك. ومن أطاعهم منكم -أيها المسلمون- وارتدَّ عن دينه فمات على الكفر، فقد ذهب عمله في الدنيا والآخرة، وصار من الملازمين لنار جهنم لا يخرج منها أبدًا.');
INSERT INTO Quran__Arabic_Tafseer_Muyassar (QuranChapterID, VerseNumber, Rukuh, Sajdah, Manzil, HizbQuarter, VerseText) VALUES (2, 218, '0', '0', '0', '0', 'إن الذين صَدَّقوا بالله ورسوله وعملوا بشرعه والذين تركوا ديارهم، وجاهدوا في سبيل الله، أولئك يطمعون في فضل الله وثوابه. والله غفور لذنوب عباده المؤمنين، رحيم بهم رحمة واسعة.');
INSERT INTO Quran__Arabic_Tafseer_Muyassar (QuranChapterID, VerseNumber, Rukuh, Sajdah, Manzil, HizbQuarter, VerseText) VALUES (2, 219, '0', '0', '0', '1', 'يسألك المسلمون -أيها النبي- عن حكم تعاطي الخمر شربًا وبيعًا وشراءً، والخمر كل مسكر خامر العقل وغطاه مشروبًا كان أو مأكولا ويسألونك عن حكم القمار -وهو أَخْذُ المال أو إعطاؤه بالمقامرة وهي المغالبات التي فيها عوض من الطرفين-، قل لهم: في ذلك أضرار ومفاسد كثيرة في الدين والدنيا، والعقول والأموال، وفيهما منافع للناس من جهة كسب الأموال وغيرها، وإثمهما أكبر من نفعهما؛ إذ يصدَّان عن ذكر الله وعن الصلاة، ويوقعان العداوة والبغضاء بين الناس، ويتلفان المال. وكان هذا تمهيدًا لتحريمهما. ويسألونك عن القَدْر الذي ينفقونه من أموالهم تبرعًا وصدقة، قل لهم: أنفقوا القَدْر الذي يزيد على حاجتكم. مثل ذلك البيان الواضح يبيِّن الله لكم الآيات وأحكام الشريعة؛ لكي تتفكروا فيما ينفعكم في الدنيا والآخرة. ويسألونك -أيها النبي- عن اليتامى كيف يتصرفون معهم في معاشهم وأموالهم؟ قل لهم: إصلاحكم لهم خير، فافعلوا الأنفع لهم دائمًا، وإن تخالطوهم في سائر شؤون المعاش فهم إخوانكم في الدين. وعلى الأخ أن يرعى مصلحة أخيه. والله يعلم المضيع لأموال اليتامى من الحريص على إصلاحها. ولو شاء الله لضيَّق وشقَّ عليكم بتحريم المخالطة. إن الله عزيز في ملكه، حكيم في خلقه وتدبيره وتشريعه.');
INSERT INTO Quran__Arabic_Tafseer_Muyassar (QuranChapterID, VerseNumber, Rukuh, Sajdah, Manzil, HizbQuarter, VerseText) VALUES (2, 220, '0', '0', '0', '0', 'يسألك المسلمون -أيها النبي- عن حكم تعاطي الخمر شربًا وبيعًا وشراءً، والخمر كل مسكر خامر العقل وغطاه مشروبًا كان أو مأكولا ويسألونك عن حكم القمار -وهو أَخْذُ المال أو إعطاؤه بالمقامرة وهي المغالبات التي فيها عوض من الطرفين-، قل لهم: في ذلك أضرار ومفاسد كثيرة في الدين والدنيا، والعقول والأموال، وفيهما منافع للناس من جهة كسب الأموال وغيرها، وإثمهما أكبر من نفعهما؛ إذ يصدَّان عن ذكر الله وعن الصلاة، ويوقعان العداوة والبغضاء بين الناس، ويتلفان المال. وكان هذا تمهيدًا لتحريمهما. ويسألونك عن القَدْر الذي ينفقونه من أموالهم تبرعًا وصدقة، قل لهم: أنفقوا القَدْر الذي يزيد على حاجتكم. مثل ذلك البيان الواضح يبيِّن الله لكم الآيات وأحكام الشريعة؛ لكي تتفكروا فيما ينفعكم في الدنيا والآخرة. ويسألونك -أيها النبي- عن اليتامى كيف يتصرفون معهم في معاشهم وأموالهم؟ قل لهم: إصلاحكم لهم خير، فافعلوا الأنفع لهم دائمًا، وإن تخالطوهم في سائر شؤون المعاش فهم إخوانكم في الدين. وعلى الأخ أن يرعى مصلحة أخيه. والله يعلم المضيع لأموال اليتامى من الحريص على إصلاحها. ولو شاء الله لضيَّق وشقَّ عليكم بتحريم المخالطة. إن الله عزيز في ملكه، حكيم في خلقه وتدبيره وتشريعه.');
INSERT INTO Quran__Arabic_Tafseer_Muyassar (QuranChapterID, VerseNumber, Rukuh, Sajdah, Manzil, HizbQuarter, VerseText) VALUES (2, 221, '1', '0', '0', '0', 'ولا تتزوجوا -أيها المسلمون- المشركات عابدات الأوثان، حتى يدخلن في الإسلام. واعلموا أن امرأة مملوكة لا مال لها ولا حسب، مؤمنةً بالله، خير من امرأة مشركة، وإن أعجبتكم المشركة الحرة. ولا تُزَوِّجوا نساءكم المؤمنات -إماء أو حرائر- للمشركين حتى يؤمنوا بالله ورسوله. واعلموا أن عبدًا مؤمنًا مع فقره، خير من مشرك، وإن أعجبكم المشرك. أولئك المتصفون بالشرك رجالا ونساءً يدعون كل مَن يعاشرهم إلى ما يؤدي به إلى النار، والله سبحانه يدعو عباده إلى دينه الحق المؤدي بهم إلى الجنة ومغفرة ذنوبهم بإذنه، ويبين آياته وأحكامه للناس؛ لكي يتذكروا، فيعتبروا.');
INSERT INTO Quran__Arabic_Tafseer_Muyassar (QuranChapterID, VerseNumber, Rukuh, Sajdah, Manzil, HizbQuarter, VerseText) VALUES (2, 222, '0', '0', '0', '0', 'ويسألونك عن الحيض- وهو الدم الذي يسيل من أرحام النساء جِبِلَّة في أوقات مخصوصة-، قل لهم -أيها النبي-: هو أذى مستقذر يضر من يَقْرَبُه، فاجتنبوا جماع النساء مدة الحيض حتى ينقطع الدم، فإذا انقطع الدم، واغتسلن، فجامعوهن في الموضع الذي أحلَّه الله لكم، وهو القبل لا الدبر. إن الله يحب عباده المكثرين من الاستغفار والتوبة، ويحب عباده المتطهرين الذين يبتعدون عن الفواحش والأقذار.');
INSERT INTO Quran__Arabic_Tafseer_Muyassar (QuranChapterID, VerseNumber, Rukuh, Sajdah, Manzil, HizbQuarter, VerseText) VALUES (2, 223, '0', '0', '0', '0', 'نساؤكم موضع زرع لكم، تضعون النطفة في أرحامهن، فَيَخْرج منها الأولاد بمشيئة الله، فجامعوهن في محل الجماع فقط، وهو القبل بأي كيفية شئتم، وقَدِّموا لأنفسكم أعمالا صالحة بمراعاة أوامر الله، وخافوا الله، واعلموا أنكم ملاقوه للحساب يوم القيامة. وبشِّر المؤمنين -أيها النبي- بما يفرحهم ويسرُّهم من حسن الجزاء في الآخرة.');
INSERT INTO Quran__Arabic_Tafseer_Muyassar (QuranChapterID, VerseNumber, Rukuh, Sajdah, Manzil, HizbQuarter, VerseText) VALUES (2, 224, '0', '0', '0', '0', 'ولا تجعلوا -أيها المسلمون- حلفكم بالله مانعًا لكم من البر وصلة الرحم والتقوى والإصلاح بين الناس: بأن تُدْعَوا إلى فعل شيء منها، فتحتجوا بأنكم أقسمتم بالله ألا تفعلوه، بل على الحالف أن يعدل عن حلفه، ويفعل أعمال البر، ويكفر عن يمينه، ولا يعتاد ذلك. والله سميع لأقوالكم، عليم بجميع أحوالكم.');
INSERT INTO Quran__Arabic_Tafseer_Muyassar (QuranChapterID, VerseNumber, Rukuh, Sajdah, Manzil, HizbQuarter, VerseText) VALUES (2, 225, '0', '0', '0', '0', 'لا يعاقبكم الله بسبب أيمانكم التي تحلفونها بغير قصد، ولكن يعاقبكم بما قصدَتْه قلوبكم. والله غفور لمن تاب إليه، حليم بمن عصاه حيث لم يعاجله بالعقوبة.');
INSERT INTO Quran__Arabic_Tafseer_Muyassar (QuranChapterID, VerseNumber, Rukuh, Sajdah, Manzil, HizbQuarter, VerseText) VALUES (2, 226, '0', '0', '0', '0', 'للذين يحلفون بالله أن لا يجامعوا نساءهم، انتظار أربعة أشهر، فإن رجعوا قبل فوات الأشهر الأربعة، فإن الله غفور لما وقع منهم من الحلف بسبب رجوعهم، رحيم بهم.');
INSERT INTO Quran__Arabic_Tafseer_Muyassar (QuranChapterID, VerseNumber, Rukuh, Sajdah, Manzil, HizbQuarter, VerseText) VALUES (2, 227, '0', '0', '0', '0', 'وإن عقدوا عزمهم على الطلاق، باستمرارهم في اليمين، وترك الجماع، فإن الله سميع لأقوالهم، عليم بمقاصدهم، وسيجازيهم على ذلك.');
INSERT INTO Quran__Arabic_Tafseer_Muyassar (QuranChapterID, VerseNumber, Rukuh, Sajdah, Manzil, HizbQuarter, VerseText) VALUES (2, 228, '1', '0', '0', '0', 'والمطلقات ذوات الحيض، يجب أن ينتظرن دون نكاح بعد الطلاق مدة ثلاثة أطهار أو ثلاث حيضات على سبيل العدة؛ ليتأكدن من فراغ الرحم من الحمل. ولا يجوز لهن تزوج رجل آخر في أثناء هذه العدة حتى تنتهي. ولا يحل لهن أن يخفين ما خلق الله في أرحامهن من الحمل أو الحيض، إن كانت المطلقات مؤمنات حقًا بالله واليوم الآخر. وأزواج المطلقات أحق بمراجعتهن في العدة. وينبغي أن يكون ذلك بقصد الإصلاح والخير، وليس بقصد الإضرار تعذيبًا لهن بتطويل العدة. وللنساء حقوق على الأزواج، مثل التي عليهن، على الوجه المعروف، وللرجال على النساء منزلة زائدة من حسن الصحبة والعشرة بالمعروف والقِوامة على البيت وملك الطلاق. والله عزيز له العزة القاهرة، حكيم يضع كل شيء في موضعه المناسب.');
INSERT INTO Quran__Arabic_Tafseer_Muyassar (QuranChapterID, VerseNumber, Rukuh, Sajdah, Manzil, HizbQuarter, VerseText) VALUES (2, 229, '0', '0', '0', '0', 'الطلاق الذي تحصل به الرجعة مرتان، واحدة بعد الأخرى، فحكم الله بعد كل طلقة هو إمساك المرأة بالمعروف، وحسن العشرة بعد مراجعتها، أو تخلية سبيلها مع حسن معاملتها بأداء حقوقها، وألا يذكرها مطلقها بسوء. ولا يحل لكم- أيها الأزواج- أن تأخذوا شيئًا مما أعطيتموهن من المهر ونحوه، إلا أن يخاف الزوجان ألا يقوما بالحقوق الزوجية، فحينئذ يعرضان أمرهما على الأولياء، فإن خاف الأولياء عدم إقامة الزوجين حدود الله، فلا حرج على الزوجين فيما تدفعه المرأة للزوج مقابل طلاقها. تلك الأحكام هي حدود الله الفاصلة بين الحلال والحرام، فلا تتجاوزوها، ومن يتجاوز حدود الله تعالى فأولئك هم الظالمون أنفسهم بتعريضها لعذاب الله.');
INSERT INTO Quran__Arabic_Tafseer_Muyassar (QuranChapterID, VerseNumber, Rukuh, Sajdah, Manzil, HizbQuarter, VerseText) VALUES (2, 230, '0', '0', '0', '0', 'فإن طلَّق الرجل زوجته الطلقة الثالثة، فلا تحلُّ له إلا إذا تزوجت رجلا غيره زواجًا صحيحًا وجامعها فيه ويكون الزواج عن رغبة، لا بنية تحليل المرأة لزوجها الأول، فإن طلقها الزوج الآخر أو مات عنها وانقضت عدتها، فلا إثم على المرأة وزوجها الأول أن يتزوجا بعقد جديد، ومهر جديد، إن غلب على ظنهما أن يقيما أحكام الله التي شرعها للزوجين. وتلك أحكام الله المحددة يبينها لقوم يعلمون أحكامه وحدوده؛ لأنهم المنتفعون بها.');
INSERT INTO Quran__Arabic_Tafseer_Muyassar (QuranChapterID, VerseNumber, Rukuh, Sajdah, Manzil, HizbQuarter, VerseText) VALUES (2, 231, '1', '0', '0', '0', 'وإذا طَلَّقتم النساء فقاربن انتهاء عدتهن، فراجعوهن، ونيتكم القيام بحقوقهن على الوجه المستحسن شرعًا وعرفًا، أو اتركوهن حتى تنقضي عدتهن. واحذروا أن تكون مراجعتهن بقصد الإضرار بهن لأجل الاعتداء على حقوقهن. ومن يفعل ذلك فقد ظلم نفسه باستحقاقه العقوبة، ولا تتخذوا آيات الله وأحكامه لعبًا ولهوًا. واذكروا نعمة الله عليكم بالإسلام وتفصيل الأحكام. واذكروا ما أنزل الله عليكم من القرآن والسنة، واشكروا له سبحانه على هذه النعم الجليلة، يُذكِّركم الله بهذا، ويخوفكم من المخالفة، فخافوا الله وراقبوه، واعلموا أن الله عليم بكل شيء، لا يخفى عليه شيء، وسيجازي كلا بما يستحق.');
INSERT INTO Quran__Arabic_Tafseer_Muyassar (QuranChapterID, VerseNumber, Rukuh, Sajdah, Manzil, HizbQuarter, VerseText) VALUES (2, 232, '0', '0', '0', '0', 'واذا طلَّقتم نساءكم دون الثلاث وانتهت عدتهن من غير مراجعة لهن، فلا تضيقوا -أيها الأولياء- على المطلقات بمنعهن من العودة إلى أزواجهن بعقد جديد إذا أردن ذلك، وحدث التراضي شرعًا وعرفًا. ذلك يوعظ به من كان منكم صادق الإيمان بالله واليوم الآخر. إن تَرْكَ العضل وتمكين الأزواج من نكاح زوجاتهم أكثر نماء وطهارة لأعراضكم، وأعظم منفعة وثوابًا لكم. والله يعلم ما فيه صلاحكم وأنتم لا تعلمون ذلك.');
INSERT INTO Quran__Arabic_Tafseer_Muyassar (QuranChapterID, VerseNumber, Rukuh, Sajdah, Manzil, HizbQuarter, VerseText) VALUES (2, 233, '0', '0', '0', '1', 'وعلى الوالدات إرضاع أولادهن مدة سنتين كاملتين لمن أراد إتمام الرضاعة، ويجب على الآباء أن يكفُلوا للمرضعات المطلقات طعامهن وكسوتهن، على الوجه المستحسن شرعًا وعرفًا؛ لأن الله لا يكلف نفسًا إلا قدر طاقتها، ولا يحل للوالدين أن يجعلوا المولود وسيلة للمضارة بينهما، ويجب على الوارث عند موت الوالد مثل ما يجب على الوالد قبل موته من النفقة والكسوة. فإن أراد الوالدان فطام المولود قبل انتهاء السنتين فلا حرج عليهما إذا تراضيا وتشاورا في ذلك؛ ليصلا إلى ما فيه مصلحة المولود. وإن اتفق الوالدان على إرضاع المولود من مرضعة أخرى غير والدته فلا حرج عليهما، إذا سلَّم الوالد للأم حقَّها، وسلَّم للمرضعة أجرها بما يتعارفه الناس. وخافوا الله في جميع أحوالكم، واعلموا أن الله بما تعملون بصير، وسيجازيكم على ذلك.');
INSERT INTO Quran__Arabic_Tafseer_Muyassar (QuranChapterID, VerseNumber, Rukuh, Sajdah, Manzil, HizbQuarter, VerseText) VALUES (2, 234, '0', '0', '0', '0', 'والذين يموتون منكم، ويتركون زوجات بعدهم، يجب عليهن الانتظار بأنفسهن مدة أربعة أشهر وعشرة أيام، لا يخرجن من منزل الزوجية، ولا يتزيَّنَّ، ولا يتزوجن، فإذا انتهت المدة المذكورة فلا إثم عليكم يا أولياء النساء فيما يفعلن في أنفسهن من الخروج، والتزين، والزواج على الوجه المقرر شرعًا. والله سبحانه وتعالى خبير بأعمالكم ظاهرها وباطنها، وسيجازيكم عليها.');
INSERT INTO Quran__Arabic_Tafseer_Muyassar (QuranChapterID, VerseNumber, Rukuh, Sajdah, Manzil, HizbQuarter, VerseText) VALUES (2, 235, '1', '0', '0', '0', 'ولا إثم عليكم -أيها الرجال- فيما تُلَمِّحون به مِن طلب الزواج بالنساء المتوفَّى عنهنَّ أزواجهن، أو المطلقات طلاقًا بائنًا في أثناء عدتهن، ولا ذنب عليكم أيضًا فيما أضمرتموه في أنفسكم من نية الزواج بهن بعد انتهاء عدتهن. علم الله أنكم ستذكرون النساء المعتدَّات، ولن تصبروا على السكوت عنهن، لضعفكم؛ لذلك أباح لكم أن تذكروهن تلميحًا أو إضمارًا في النفس، واحذروا أن تواعدوهن على النكاح سرًا بالزنى أو الاتفاق على الزواج في أثناء العدة، إلا أن تقولوا قولا يُفْهَم منه أن مثلها يُرْغَبُ فيها الأزواج، ولا تعزموا على عقد النكاح في زمان العدة حتى تنقضي مدتها. واعلموا أن الله يعلم ما في أنفسكم فخافوه، واعلموا أن الله غفور لمن تاب من ذنوبه، حليم على عباده لا يعجل عليهم بالعقوبة.');
INSERT INTO Quran__Arabic_Tafseer_Muyassar (QuranChapterID, VerseNumber, Rukuh, Sajdah, Manzil, HizbQuarter, VerseText) VALUES (2, 236, '0', '0', '0', '0', 'لا إثم عليكم -أيها الأزواج- إن طلقتم النساء بعد العقد عليهن، وقبل أن تجامعوهن، أو تحددوا مهرًا لهن، ومتِّعوهن بشيء ينتفعن به جبرًا لهن، ودفعًا لوحشة الطلاق، وإزالة للأحقاد. وهذه المتعة تجب بحسب حال الرجل المطلِّق: على الغني قَدْر سَعَة رزقه، وعلى الفقير قَدْر ما يملكه، متاعًا على الوجه المعروف شرعًا، وهو حق ثابت على الذين يحسنون إلى المطلقات وإلى أنفسهم بطاعة الله.');
INSERT INTO Quran__Arabic_Tafseer_Muyassar (QuranChapterID, VerseNumber, Rukuh, Sajdah, Manzil, HizbQuarter, VerseText) VALUES (2, 237, '0', '0', '0', '0', 'وإن طلَّقتم النساء بعد العقد عليهن، ولم تجامعوهن، ولكنكم ألزمتم أنفسكم بمهر محدد لهن، فيجب عليكم أن تعطوهن نصف المهر المتفق عليه، إلا أنْ تُسامِح المطلقات، فيتركن نصف المهر المستحق لهن، أو يسمح الزوج بأن يترك للمطلقة المهر كله، وتسامحكم أيها الرجال والنساء أقرب إلى خشية الله وطاعته، ولا تنسوا -أيها الناس- الفضل والإحسان بينكم، وهو إعطاء ما ليس بواجب عليكم، والتسامح في الحقوق. إن الله بما تعملون بصير، يُرغِّبكم في المعروف، ويحثُّكم على الفضل.');
INSERT INTO Quran__Arabic_Tafseer_Muyassar (QuranChapterID, VerseNumber, Rukuh, Sajdah, Manzil, HizbQuarter, VerseText) VALUES (2, 238, '0', '0', '0', '0', 'حافظوا -أيها المسلمون- على الصلوات الخمس المفروضة بالمداومة على أدائها في أوقاتها بشروطها وأركانها وواجباتها، وحافظوا على الصلاة المتوسطة بينها وهي صلاة العصر، وقوموا في صلاتكم مطيعين لله، خاشعين ذليلين.');
INSERT INTO Quran__Arabic_Tafseer_Muyassar (QuranChapterID, VerseNumber, Rukuh, Sajdah, Manzil, HizbQuarter, VerseText) VALUES (2, 239, '0', '0', '0', '0', 'فإن خفتم من عدو لكم فصلوا صلاة الخوف ماشين، أو راكبين، على أي هيئة تستطيعونها ولو بالإيماء، أو إلى غير جهة القبلة، فإذا زال خوفكم فصلُّوا صلاة الأمن، واذكروا الله فيها، ولا تنقصوها عن هيئتها الأصلية، واشكروا له على ما علَّمكم من أمور العبادات والأحكام ما لم تكونوا على علم به.');
INSERT INTO Quran__Arabic_Tafseer_Muyassar (QuranChapterID, VerseNumber, Rukuh, Sajdah, Manzil, HizbQuarter, VerseText) VALUES (2, 240, '0', '0', '0', '0', 'والأزواج الذين يموتون ويتركون زوجات بعدهم، فعليهم وصيةً لهنَّ: أن يُمَتَّعن سنه تامة من يوم الوفاة، بالسكنى في منزل الزوج من غير إخراج الورثة لهن مدة السنة؛ جبرًا لخاطر الزوجة، وبرًا بالمتوفَّى. فإن خرجت الزوجات باختيارهن قبل انقضاء السنة فلا إثم عليكم -أيها الورثة- في ذلك، ولا حرج على الزوجات فيما فعلن في أنفسهن من أمور مباحة. والله عزيز في ملكه، حكيم في أمره ونهيه. وهذه الآية منسوخة بقوله تعالى: (والذين يتوفون منكم ويذرون أزواجًا يتربصن بأنفسهن أربعة أشهر وعشرًا).');
INSERT INTO Quran__Arabic_Tafseer_Muyassar (QuranChapterID, VerseNumber, Rukuh, Sajdah, Manzil, HizbQuarter, VerseText) VALUES (2, 241, '0', '0', '0', '0', 'وللمطلقات متاع من كسوة ونفقة على الوجه المعروف المستحسن شرعًا، حقًا على الذين يخافون الله ويتقونه في أمره ونهيه.');
INSERT INTO Quran__Arabic_Tafseer_Muyassar (QuranChapterID, VerseNumber, Rukuh, Sajdah, Manzil, HizbQuarter, VerseText) VALUES (2, 242, '1', '0', '0', '0', 'مثل ذلك البيان الواضح في أحكام الأولاد والنساء، يبيِّن الله لكم آياته وأحكامه في كل ما تحتاجونه في معاشكم ومعادكم؛ لكي تعقلوها وتعملوا بها.');
INSERT INTO Quran__Arabic_Tafseer_Muyassar (QuranChapterID, VerseNumber, Rukuh, Sajdah, Manzil, HizbQuarter, VerseText) VALUES (2, 243, '0', '0', '0', '1', 'ألم تعلم -أيها الرسول- قصة الذين فرُّوا من أرضهم ومنازلهم، وهم ألوف كثيرة؛ خشية الموت من الطاعون أو القتال، فقال لهم الله: موتوا، فماتوا دفعة واحدة عقوبة على فرارهم من قدر الله، ثم أحياهم الله تعالى بعد مدة؛ ليستوفوا آجالهم، وليتعظوا ويتوبوا؟ إن الله لذو فضل عظيم على الناس بنعمه الكثيرة، ولكن أكثر الناس لا يشكرون فضل الله عليهم.');
INSERT INTO Quran__Arabic_Tafseer_Muyassar (QuranChapterID, VerseNumber, Rukuh, Sajdah, Manzil, HizbQuarter, VerseText) VALUES (2, 244, '0', '0', '0', '0', 'وقاتلوا -أيها المسلمون- الكفار لنصرة دين الله، واعلموا أن الله سميع لأقوالكم، عليم بنيَّاتكم وأعمالكم.');
INSERT INTO Quran__Arabic_Tafseer_Muyassar (QuranChapterID, VerseNumber, Rukuh, Sajdah, Manzil, HizbQuarter, VerseText) VALUES (2, 245, '0', '0', '0', '0', 'من ذا الذي ينفق في سبيل الله إنفاقًا حسنًا احتسابًا للأجر، فيضاعفه له أضعافا كثيرة لا تحصى من الثواب وحسن الجزاء؟ والله يقبض ويبسط، فأنفقوا ولا تبالوا؛ فإنه هو الرزاق، يُضيِّق على مَن يشاء من عباده في الرزق، ويوسعه على آخرين، له الحكمة البالغة في ذلك، وإليه وحده ترجعون بعد الموت، فيجازيكم على أعمالكم.');
INSERT INTO Quran__Arabic_Tafseer_Muyassar (QuranChapterID, VerseNumber, Rukuh, Sajdah, Manzil, HizbQuarter, VerseText) VALUES (2, 246, '0', '0', '0', '0', 'ألم تعلم -أيها الرسول- قصة الأشراف والوجهاء من بني إسرائيل من بعد زمان موسى؛ حين طلبوا من نبيهم أن يولي عليهم ملكا، يجتمعون تحت قيادته، ويقاتلون أعداءهم في سبيل الله. قال لهم نبيهم: هل الأمر كما أتوقعه إنْ فُرِض عليكم القتال في سبيل الله أنكم لا تقاتلون؛ فإني أتوقع جبنكم وفراركم من القتال، قالوا مستنكرين توقع نبيهم: وأي مانع يمنعنا عن القتال في سبيل الله، وقد أَخْرَجَنَا عدوُّنا من ديارنا، وأبعدنا عن أولادنا بالقتل والأسر؟ فلما فرض الله عليهم القتال مع الملِك الذي عيَّنه لهم جَبُنوا وفرُّوا عن القتال، إلا قليلا منهم ثبتوا بفضل الله. والله عليم بالظالمين الناكثين عهودهم.');
INSERT INTO Quran__Arabic_Tafseer_Muyassar (QuranChapterID, VerseNumber, Rukuh, Sajdah, Manzil, HizbQuarter, VerseText) VALUES (2, 247, '0', '0', '0', '0', 'وقال لهم نبيهم: إن الله قد أرسل إليكم طالوت مَلِكًا إجابة لطلبكم، يقودكم لقتال عدوكم كما طلبتم. قال كبراء بني إسرائيل: كيف يكون طالوت مَلِكًا علينا، وهو لا يستحق ذلك؟ لأنه ليس من سبط الملوك، ولا من بيت النبوة، ولم يُعْط كثرة في الأموال يستعين بها في ملكه، فنحن أحق بالملك منه؛ لأننا من سبط الملوك ومن بيت النبوة. قال لهم نبيهم: إن الله اختاره عليكم وهو سبحانه أعلم بأمور عباده، وزاده سَعَة في العلم وقوة في الجسم ليجاهد العدو. والله مالك الملك يعطي ملكه مَن يشاء من عباده، والله واسع الفضل والعطاء، عليم بحقائق الأمور، لا يخفى عليه شيء.');
INSERT INTO Quran__Arabic_Tafseer_Muyassar (QuranChapterID, VerseNumber, Rukuh, Sajdah, Manzil, HizbQuarter, VerseText) VALUES (2, 248, '1', '0', '0', '0', 'وقال لهم نبيهم: إن علامة ملكه أن يأتيكم الصندوق الذي فيه التوراة -وكان أعداؤهم قد انتزعوه منهم- فيه طمأنينة من ربكم تثبت قلوب المخلصين، وفيه بقية من بعض أشياء تركها آل موسى وآل هارون، مثل العصا وفُتات الألواح تحمله الملائكة. إن في ذلك لأعظم برهان لكم على اختيار طالوت ملكًا عليكم بأمر الله، إن كنتم مصدقين بالله ورسله.');
INSERT INTO Quran__Arabic_Tafseer_Muyassar (QuranChapterID, VerseNumber, Rukuh, Sajdah, Manzil, HizbQuarter, VerseText) VALUES (2, 249, '0', '0', '0', '0', 'فلما خرج طالوت بجنوده لقتال العمالقة قال لهم: إن الله ممتحنكم على الصبر بنهر أمامكم تعبرونه؛ ليتميَّز المؤمن من المنافق، فمن شرب منكم من ماء النهر فليس مني، ولا يصلح للجهاد معي، ومن لم يذق الماء فإنه مني؛ لأنه مطيع لأمري وصالح للجهاد، إلا مَن ترخَّص واغترف غُرْفة واحدة بيده فلا لوم عليه. فلما وصلوا إلى النهر انكبوا على الماء، وأفرطوا في الشرب منه، إلا عددًا قليلا منهم صبروا على العطش والحر، واكتفوا بغُرْفة اليد، وحينئذ تخلف العصاة. ولما عبر طالوت النهر هو والقلة المؤمنة معه -وهم ثلاثمائة وبضعة عشر رجلا لملاقاة العدو، ورأوا كثرة عدوهم وعدَّتهم، قالوا: لا قدرة لنا اليوم بجالوت وجنوده الأشداء، فأجاب الذين يوقنون بلقاء الله، يُذَكِّرون إخوانهم بالله وقدرته قائلين: كم من جماعة قليلة مؤمنة صابرة، غلبت بإذن الله وأمره جماعة كثيرة كافرة باغية. والله مع الصابرين بتوفيقه ونصره، وحسن مثوبته.');
INSERT INTO Quran__Arabic_Tafseer_Muyassar (QuranChapterID, VerseNumber, Rukuh, Sajdah, Manzil, HizbQuarter, VerseText) VALUES (2, 250, '0', '0', '0', '0', 'ولما ظهروا لجالوت وجنوده، ورأوا الخطر رأي العين، فزعوا إلى الله بالدعاء والضراعة قائلين: ربنا أنزل على قلوبنا صبرًا عظيمًا، وثبت أقدامنا، واجعلها راسخة في قتال العدو، لا تفر مِن هول الحرب، وانصرنا بعونك وتأييدك على القوم الكافرين.');
INSERT INTO Quran__Arabic_Tafseer_Muyassar (QuranChapterID, VerseNumber, Rukuh, Sajdah, Manzil, HizbQuarter, VerseText) VALUES (2, 251, '0', '0', '0', '0', 'فهزموهم بإذن الله، وقتل داود -عليه السلام- جالوتَ قائدَ الجبابرة، وأعطى الله عز وجل داود بعد ذلك الملك والنبوة في بني إسرائيل، وعَلَّمه مما يشاء من العلوم. ولولا أن يدفع الله ببعض الناس -وهم أهل الطاعة له والإيمان به- بعضًا، وهم أهل المعصية لله والشرك به، لفسدت الأرض بغلبة الكفر، وتمكُّن الطغيان، وأهل المعاصي، ولكن الله ذو فضل على المخلوقين جميعًا.');
INSERT INTO Quran__Arabic_Tafseer_Muyassar (QuranChapterID, VerseNumber, Rukuh, Sajdah, Manzil, HizbQuarter, VerseText) VALUES (2, 252, '0', '0', '0', '0', 'تلك حجج الله وبراهينه، نقصُّها عليك -أيها النبي- بالصدق، وإنك لمن المرسلين الصادقين.');
INSERT INTO Quran__Arabic_Tafseer_Muyassar (QuranChapterID, VerseNumber, Rukuh, Sajdah, Manzil, HizbQuarter, VerseText) VALUES (2, 253, '1', '0', '0', '1', 'هؤلاء الرسل الكرام فضَّل الله بعضهم على بعض، بحسب ما منَّ الله به عليهم: فمنهم مَن كلمه الله كموسى ومحمد عليهما الصلاة والسلام، وفي هذا إثبات صفة الكلام لله عز وجل على الوجه اللائق بجلاله، ومنهم مَن رفعه الله درجاتٍ عاليةً كمحمد صلى الله عليه وسلم، بعموم رسالته، وختم النبوة به، وتفضيل أمته على جميع الأمم، وغير ذلك. وآتى الله تعالى عيسى ابن مريم عليه السلام البينات المعجزات الباهرات، كإبراء مَن ولد أعمى بإذن الله تعالى، ومَن به برص بإذن الله، وكإحيائه الموتى بإذن الله، وأيده بجبريل عليه السلام. ولو شاء الله ألا يقتتل الذين جاؤوا مِن بعد هؤلاء الرسل مِن بعد ما جاءتهم البينات ما اقتتلوا، ولكن وقع الاختلاف بينهم: فمنهم مَن ثبت على إيمانه، ومنهم مَن أصر على كفره. ولو شاء الله بعد ما وقع الاختلاف بينهم، الموجب للاقتتال، ما اقتتلوا، ولكن الله يوفق مَن يشاء لطاعته والإيمان به، ويخذل مَن يشاء، فيعصيه ويكفر به، فهو يفعل ما يشاء ويختار.');
INSERT INTO Quran__Arabic_Tafseer_Muyassar (QuranChapterID, VerseNumber, Rukuh, Sajdah, Manzil, HizbQuarter, VerseText) VALUES (2, 254, '0', '0', '0', '0', 'يا من آمنتم بالله وصدَّقتم رسوله وعملتم بهديه أخرجوا الزكاة المفروضة، وتصدَّقوا مما أعطاكم الله قبل مجيء يوم القيامة حين لا بيع فيكون ربح، ولا مال تفتدون به أنفسكم مِن عذاب الله، ولا صداقة صديق تُنقذكم، ولا شافع يملك تخفيف العذاب عنكم. والكافرون هم الظالمون المتجاوزون حدود الله.');
INSERT INTO Quran__Arabic_Tafseer_Muyassar (QuranChapterID, VerseNumber, Rukuh, Sajdah, Manzil, HizbQuarter, VerseText) VALUES (2, 255, '0', '0', '0', '0', 'الله الذي لا يستحق الألوهية والعبودية إلا هو، الحيُّ الذي له جميع معاني الحياة الكاملة كما يليق بجلاله، القائم على كل شيء، لا تأخذه سِنَة أي: نعاس، ولا نوم، كل ما في السماوات وما في الأرض ملك له، ولا يتجاسر أحد أن يشفع عنده إلا بإذنه، محيط علمه بجميع الكائنات ماضيها وحاضرها ومستقبلها، يعلم ما بين أيدي الخلائق من الأمور المستقبلة، وما خلفهم من الأمور الماضية، ولا يَطَّلعُ أحد من الخلق على شيء من علمه إلا بما أعلمه الله وأطلعه عليه. وسع كرسيه السماوات والأرض، والكرسي: هو موضع قدمي الرب -جل جلاله- ولا يعلم كيفيته إلا الله سبحانه، ولا يثقله سبحانه حفظهما، وهو العلي بذاته وصفاته على جميع مخلوقاته، الجامع لجميع صفات العظمة والكبرياء. وهذه الآية أعظم آية في القرآن، وتسمى: (آية الكرسي).');
INSERT INTO Quran__Arabic_Tafseer_Muyassar (QuranChapterID, VerseNumber, Rukuh, Sajdah, Manzil, HizbQuarter, VerseText) VALUES (2, 256, '0', '0', '0', '0', 'لكمال هذا الدين واتضاح آياته لا يُحتاج إلى الإكراه عليه لمن تُقبل منهم الجزية، فالدلائل بينة يتضح بها الحق من الباطل، والهدى من الضلال. فَمَن يكفر بكل ما عُبِد من دون الله ويؤمن بالله، فقد ثبت واستقام على الطريقة المثلى، واستمسك من الدين بأقوى سبب لا انقطاع له. والله سميع لأقوال عباده، عليم بأفعالهم ونياتهم، وسيجازيهم على ذلك.');
INSERT INTO Quran__Arabic_Tafseer_Muyassar (QuranChapterID, VerseNumber, Rukuh, Sajdah, Manzil, HizbQuarter, VerseText) VALUES (2, 257, '1', '0', '0', '0', 'الله يتولى المؤمنين بنصره وتوفيقه وحفظه، يخرجهم من ظلمات الكفر، إلى نور الإيمان. والذين كفروا أنصارهم وأولياؤهم الأنداد والأوثان الذين يعبدونهم من دون الله، يُخرجونهم من نور الإيمان إلى ظلمات الكفر، أولئك أصحاب النار الملازمون لها، هم فيها باقون بقاء أبديًا لا يخرجون منها.');
INSERT INTO Quran__Arabic_Tafseer_Muyassar (QuranChapterID, VerseNumber, Rukuh, Sajdah, Manzil, HizbQuarter, VerseText) VALUES (2, 258, '0', '0', '0', '0', 'هل رأيت -أيها الرسول- أعجب مِن حال هذا الذي جادل إبراهيم عليه السلام في توحيد الله تعالى وربوبيته؛ لأن الله أعطاه المُلْك فتجبَّر وسأل إبراهيمَ: مَن ربُّك؟ فقال عليه السلام: ربي الذي يحيي الخلائق فتحيا، ويسلبها الحياة فتموت، فهو المتفرد بالإحياء والإماتة، قال: أنا أحيي وأميت، أي أقتل مَن أردتُ قَتْلَه، وأستبقي مَن أردت استبقاءه، فقال له إبراهيم: إن الله الذي أعبده يأتي بالشمس من المشرق، فهل تستطيع تغيير هذه السُّنَّة الإلهية بأن تجعلها تأتي من المغرب؛ فتحيَّر هذا الكافر وانقطعت حجته، شأنه شأن الظالمين لا يهديهم الله إلى الحق والصواب.');
INSERT INTO Quran__Arabic_Tafseer_Muyassar (QuranChapterID, VerseNumber, Rukuh, Sajdah, Manzil, HizbQuarter, VerseText) VALUES (2, 259, '0', '0', '0', '0', 'أو هل رأيت -أيها الرسول- مثل الذي مرَّ على قرية قد تهدَّمت دورها، وخَوَتْ على عروشها، فقال: كيف يحيي الله هذه القرية بعد موتها؟ فأماته الله مائة عام، ثم ردَّ إليه روحه، وقال له: كم قدر الزمان الذي لبثت ميتًا؟ قال: بقيت يومًا أو بعض يوم، فأخبره بأنه بقي ميتًا مائة عام، وأمره أن ينظر إلى طعامه وشرابه، وكيف حفظهما الله من التغيُّر هذه المدة الطويلة، وأمره أن ينظر إلى حماره كيف أحياه الله بعد أن كان عظامًا متفرقة؟ وقال له: ولنجعلك آية للناس، أي: دلالة ظاهرة على قدرة الله على البعث بعد الموت، وأمره أن ينظر إلى العظام كيف يرفع الله بعضها على بعض، ويصل بعضها ببعض، ثم يكسوها بعد الالتئام لحمًا، ثم يعيد فيها الحياة؟ فلما اتضح له ذلك عِيانًا اعترف بعظمة الله، وأنه على كل شيء قدير، وصار آية للناس.');
INSERT INTO Quran__Arabic_Tafseer_Muyassar (QuranChapterID, VerseNumber, Rukuh, Sajdah, Manzil, HizbQuarter, VerseText) VALUES (2, 260, '1', '0', '0', '0', 'واذكر -أيها الرسول- طلب إبراهيم من ربه أن يريه كيفية البعث، فقال الله له: أَوَلم تؤمن؟ قال: بلى، ولكن أطلب ذلك لأزداد يقينًا على يقيني، قال: فخذ أربعة من الطير فاضممهن إليك واذبحهن وقطعهن، ثم اجعل على كل جبل منهن جزءًا، ثم نادِهن يأتينك مسرعات. فنادى إبراهيم عليه السلام، فإذا كل جزء يعود إلى موضعه، وإذا بها تأتي مسرعة. واعلم أن الله عزيز لا يغلبه شيء، حكيم في أقواله وأفعاله وشرعه وقدره.');
INSERT INTO Quran__Arabic_Tafseer_Muyassar (QuranChapterID, VerseNumber, Rukuh, Sajdah, Manzil, HizbQuarter, VerseText) VALUES (2, 261, '0', '0', '0', '0', 'ومِن أعظم ما ينتفع به المؤمنون الإنفاقُ في سبيل الله. ومثل المؤمنين الذين ينفقون أموالهم في سبيل الله كمثل حبة زُرِعتْ في أرض طيبة، فإذا بها قد أخرجت ساقًا تشعب منها سبع شعب، لكل واحدة سنبلة، في كل سنبلة مائة حبة. والله يضاعف الأجر لمن يشاء، بحسب ما يقوم بقلب المنفق من الإيمان والإخلاص التام. وفضل الله واسع، وهو سبحانه عليم بمن يستحقه، مطلع على نيات عباده.');
INSERT INTO Quran__Arabic_Tafseer_Muyassar (QuranChapterID, VerseNumber, Rukuh, Sajdah, Manzil, HizbQuarter, VerseText) VALUES (2, 262, '0', '0', '0', '0', 'الذين يخرجون أموالهم في الجهاد وأنواع الخير، ثم لا يتبعون ما أنفقوا من الخيرات مَنّاً على مَن أعطَوه ولا أذى بقول أو فِعْلٍ يشعره بالتفضل عليه، لهم ثوابهم العظيم عند ربهم، ولا خوف عليهم فيما يستقبلونه من أمر الآخرة، ولا هم يحزنون على شيء فاتهم في هذه الدنيا.');
INSERT INTO Quran__Arabic_Tafseer_Muyassar (QuranChapterID, VerseNumber, Rukuh, Sajdah, Manzil, HizbQuarter, VerseText) VALUES (2, 263, '0', '0', '0', '1', 'كلام طيب وعفو عما بدر مِن السائل مِن إلحافٍ في السؤال، خير من صدقة يتبعها من المتصدق أذى وإساءة. والله غني عن صدقات العباد، حليم لا يعاجلهم بالعقوبة.');
INSERT INTO Quran__Arabic_Tafseer_Muyassar (QuranChapterID, VerseNumber, Rukuh, Sajdah, Manzil, HizbQuarter, VerseText) VALUES (2, 264, '0', '0', '0', '0', 'يا من آمنتم بالله واليوم الآخر لا تُذْهِبُوا ثواب ما تتصدقون به بالمنِّ والأذى، فهذا شبيه بالذي يخرج ماله ليراه الناس، فيُثنوا عليه، وهو لا يؤمن بالله ولا يوقن باليوم الآخر، فمثل ذلك مثل حجر أملس عليه تراب هطل عليه مطر غزير فأزاح عنه التراب، فتركه أملس لا شيء عليه، فكذلك هؤلاء المراؤون تضمحلُّ أعمالهم عند الله، ولا يجدون شيئًا من الثواب على ما أنفقوه. والله لا يوفق الكافرين لإصابة الحق في نفقاتهم وغيرها.');
INSERT INTO Quran__Arabic_Tafseer_Muyassar (QuranChapterID, VerseNumber, Rukuh, Sajdah, Manzil, HizbQuarter, VerseText) VALUES (2, 265, '0', '0', '0', '0', 'ومثل الذين ينفقون أموالهم طلبًا لرضا الله واعتقادًا راسخًا بصدق وعده، كمثل بستان عظيم بأرض عالية طيبة هطلت عليه أمطار غزيرة، فتضاعفت ثمراته، وإن لم تسقط عليه الأمطار الغزيرة فيكفيه رذاذ المطر ليعطي الثمرة المضاعفة، وكذلك نفقات المخلصين تُقبل عند الله وتُضاعف، قلَّت أم كثُرت، فالله المُطَّلِع على السرائر، البصير بالظواهر والبواطن، يثيب كلا بحسب إخلاصه.');
INSERT INTO Quran__Arabic_Tafseer_Muyassar (QuranChapterID, VerseNumber, Rukuh, Sajdah, Manzil, HizbQuarter, VerseText) VALUES (2, 266, '1', '0', '0', '0', 'أيرغب الواحد منكم أن يكون له بستان فيه النخيل والأعناب، تجري من تحت أشجارِه المياه العذبة، وله فيه من كل ألوان الثمرات، وقد بلغ الكِبَر، ولا يستطيع أن يغرس مثل هذا الغرس، وله أولاد صغار في حاجة إلى هذا البستان وفي هذه الحالة هبَّت عليه ريح شديدة، فيها نار محرقة فأحرقته؛ وهكذا حال غير المخلصين في نفقاتهم، يأتون يوم القيامة ولا حسنة لهم. وبمثل هذا البيان يبيِّن الله لكم ما ينفعكم؛ كي تتأملوا، فتخلصوا نفقاتكم لله.');
INSERT INTO Quran__Arabic_Tafseer_Muyassar (QuranChapterID, VerseNumber, Rukuh, Sajdah, Manzil, HizbQuarter, VerseText) VALUES (2, 267, '0', '0', '0', '0', 'يا من آمنتم بي واتبعتم رسلي أنفقوا من الحلال الطيب الذي كسبتموه ومما أخرجنا لكم من الأرض، ولا تقصدوا الرديء منه لتعطوه الفقراء، ولو أُعطِيتموه لم تأخذوه إلا إذا تغاضيتم عما فيه من رداءة ونقص. فكيف ترضون لله ما لا ترضونه لأنفسكم؟ واعلموا أن الله الذي رزقكم غني عن صدقاتكم، مستحق للثناء، محمود في كل حال.');
INSERT INTO Quran__Arabic_Tafseer_Muyassar (QuranChapterID, VerseNumber, Rukuh, Sajdah, Manzil, HizbQuarter, VerseText) VALUES (2, 268, '0', '0', '0', '0', 'هذا البخل واختيار الرديء للصدقة من الشيطان الذي يخوفكم الفقر، ويغريكم بالبخل، ويأمركم بالمعاصي ومخالفة الله تعالى، والله سبحانه وتعالى يعدكم على إنفاقكم غفرانًا لذنوبكم ورزقا واسعا. والله واسع الفضل، عليم بالأعمال والنيَّات.');
INSERT INTO Quran__Arabic_Tafseer_Muyassar (QuranChapterID, VerseNumber, Rukuh, Sajdah, Manzil, HizbQuarter, VerseText) VALUES (2, 269, '0', '0', '0', '0', 'يؤتي الله الإصابة في القول والفعل مَن يشاء من عباده، ومن أنعم الله عليه بذلك فقد أعطاه خيرًا كثيرًا. وما يتذكر هذا وينتفع به إلا أصحاب العقول المستنيرة بنور الله وهدايته.');
INSERT INTO Quran__Arabic_Tafseer_Muyassar (QuranChapterID, VerseNumber, Rukuh, Sajdah, Manzil, HizbQuarter, VerseText) VALUES (2, 270, '0', '0', '0', '0', 'وما أعطيتم من مال أو غيره كثير أو قليل تتصدقون به ابتغاء مرضات الله أو أوجبتم على أنفسكم شيئًا من مال أو غيره، فإن الله يعلمه، وهو المُطَّلِع على نياتكم، وسوف يثيبكم على ذلك. ومَن منع حق الله فهو ظالم، والظالمون ليس لهم أنصار يمنعونهم من عذاب الله.');
INSERT INTO Quran__Arabic_Tafseer_Muyassar (QuranChapterID, VerseNumber, Rukuh, Sajdah, Manzil, HizbQuarter, VerseText) VALUES (2, 271, '0', '0', '0', '0', 'إن تظهروا ما تتصدقون به لله فنِعْمَ ما تصدقتم به، وإن تسرُّوا بها، وتعطوها الفقراء فهذا أفضل لكم؛ لأنه أبعد عن الرياء، وفي الصدقة -مع الإخلاص- محو لذنوبكم. والله الذي يعلم دقائق الأمور، لا يخفى عليه شيء من أحوالكم، وسيجازي كلا بعمله.');
INSERT INTO Quran__Arabic_Tafseer_Muyassar (QuranChapterID, VerseNumber, Rukuh, Sajdah, Manzil, HizbQuarter, VerseText) VALUES (2, 272, '0', '0', '0', '1', 'لست -أيها الرسول- مسئولا عن توفيق الكافرين للهداية، ولكن الله يشرح صدور مَن يشاء لدينه، ويوفقه له. وما تبذلوا من مال يَعُدْ عليكم نَفْعُه من الله، والمؤمنون لا ينفقون إلا طلبًا لمرضاة الله. وما تنفقوا من مال -مخلصين لله- توفوا ثوابه، ولا تُنْقَصُوا شيئا من ذلك. وفي الآية إثبات صفة الوجه لله تعالى على ما يليق به سبحانه.');
INSERT INTO Quran__Arabic_Tafseer_Muyassar (QuranChapterID, VerseNumber, Rukuh, Sajdah, Manzil, HizbQuarter, VerseText) VALUES (2, 273, '1', '0', '0', '0', 'اجعلوا صدقاتكم لفقراء المسلمين الذين لا يستطيعون السفر؛ طلبًا للرزق لاشتغالهم بالجهاد في سبيل الله، يظنهم مَن لا يعرفهم غير محتاجين إلى الصدقة؛ لتعففهم عن السؤال، تعرفهم بعلاماتهم وآثار الحاجة فيهم، لا يسألون الناس بالكُليَّة، وإن سألوا اضطرارًا لم يُلِحُّوا في السؤال. وما تنفقوا مِن مال في سبيل الله فلا يخفى على الله شيء منه، وسيجزي عليه أوفر الجزاء وأتمَّه يوم القيامة.');
INSERT INTO Quran__Arabic_Tafseer_Muyassar (QuranChapterID, VerseNumber, Rukuh, Sajdah, Manzil, HizbQuarter, VerseText) VALUES (2, 274, '0', '0', '0', '0', 'الذين يُخْرجون أموالهم مرضاة لله ليلا ونهارًا مسرِّين ومعلنين، فلهم أجرهم عند ربهم، ولا خوف عليهم فيما يستقبلونه من أمر الآخرة، ولا هم يحزنون على ما فاتهم من حظوظ الدنيا. ذلك التشريع الإلهي الحكيم هو منهاج الإسلام في الإنفاق لما فيه مِن سدِّ حاجة الفقراء في كرامة وعزة، وتطهير مال الأغنياء، وتحقيق التعاون على البر والتقوى؛ ابتغاء وجه الله دون قهر أو إكراه.');
INSERT INTO Quran__Arabic_Tafseer_Muyassar (QuranChapterID, VerseNumber, Rukuh, Sajdah, Manzil, HizbQuarter, VerseText) VALUES (2, 275, '0', '0', '0', '0', 'الذين يتعاملون بالربا -وهو الزيادة على رأس المال- لا يقومون في الآخرة من قبورهم إلا كما يقوم الذي يتخبطه الشيطان من الجنون؛ ذلك لأنهم قالوا: إنما البيع مثل الربا، في أن كلا منهما حلال، ويؤدي إلى زيادة المال، فأكذبهم الله، وبيَّن أنه أحل البيع وحرَّم الربا؛ لما في البيع والشراء من نفع للأفراد والجماعات، ولما في الربا من استغلال وضياع وهلاك. فمن بلغه نهي الله عن الربا فارتدع، فله ما مضى قبل أن يبلغه التحريم لا إثم عليه فيه، وأمره إلى الله فيما يستقبل من زمانه، فإن استمرَّ على توبته فالله لا يضيع أجر المحسنين، ومن عاد إلى الربا ففعله بعد بلوغه نهي الله عنه، فقد استوجب العقوبة، وقامت عليه الحجة، ولهذا قال سبحانه: (فَأُولَئِكَ أَصْحَابُ النَّارِ هُمْ فِيهَا خَالِدُونَ)');
INSERT INTO Quran__Arabic_Tafseer_Muyassar (QuranChapterID, VerseNumber, Rukuh, Sajdah, Manzil, HizbQuarter, VerseText) VALUES (2, 276, '0', '0', '0', '0', 'يذهب الله الربا كله أو يحرم صاحبه بركة ماله، فلا ينتفع به، وينمي الصدقات ويكثرها، ويضاعف الأجر للمتصدقين، ويبارك لهم في أموالهم. والله لا يحب كل مُصِرٍّ على كفره، مُسْتَحِلٍّ أكل الربا، متمادٍ في الإثم والحرام ومعاصي الله.');
INSERT INTO Quran__Arabic_Tafseer_Muyassar (QuranChapterID, VerseNumber, Rukuh, Sajdah, Manzil, HizbQuarter, VerseText) VALUES (2, 277, '0', '0', '0', '0', 'إن الذين صدقوا الله ورسوله، وعملوا الأعمال الطيبة، وأدَّوا الصلاة كما أمر الله ورسوله، وأخرجوا زكاة أموالهم، لهم ثواب عظيم خاص بهم عند ربهم ورازقهم، ولا يلحقهم خوف في آخرتهم، ولا حزن على ما فاتهم من حظوظ دنياهم.');
INSERT INTO Quran__Arabic_Tafseer_Muyassar (QuranChapterID, VerseNumber, Rukuh, Sajdah, Manzil, HizbQuarter, VerseText) VALUES (2, 278, '0', '0', '0', '0', 'يا من آمنتم بالله واتبعتم رسوله خافوا الله، واتركوا طلب ما بقي لكم من زيادة على رؤوس أموالكم التي كانت لكم قبل تحريم الربا، إن كنتم محققين إيمانكم قولا وعملا.');
INSERT INTO Quran__Arabic_Tafseer_Muyassar (QuranChapterID, VerseNumber, Rukuh, Sajdah, Manzil, HizbQuarter, VerseText) VALUES (2, 279, '0', '0', '0', '0', 'فإن لم ترتدعوا عما نهاكم الله عنه فاستيقنوا بحرب من الله ورسوله، وإن رجعتم إلى ربكم وتركتم أَكْلَ الربا فلكم أَخْذُ ما لكم من ديون دون زيادة، لا تَظْلمون أحدًا بأخذ ما زاد على رؤوس أموالكم، ولا يظلمكم أحد بنقص ما أقرضتم.');
INSERT INTO Quran__Arabic_Tafseer_Muyassar (QuranChapterID, VerseNumber, Rukuh, Sajdah, Manzil, HizbQuarter, VerseText) VALUES (2, 280, '0', '0', '0', '0', 'وإن كان المدين غير قادر على السداد فأمهلوه إلى أن ييسِّر الله له رزقًا فيدفع إليكم مالكم، وإن تتركوا رأس المال كله أو بعضه وتضعوه عن المدين فهو أفضل لكم، إن كنتم تعلمون فَضْلَ ذلك، وأنَّه خير لكم في الدنيا والآخرة.');
INSERT INTO Quran__Arabic_Tafseer_Muyassar (QuranChapterID, VerseNumber, Rukuh, Sajdah, Manzil, HizbQuarter, VerseText) VALUES (2, 281, '1', '0', '0', '0', 'واحذروا -أيها الناس- يومًا ترجعون فيه إلى الله، وهو يوم القيامة، حيث تعرضون على الله ليحاسبكم، فيجازي كل واحد منكم بما عمل من خير أو شر دون أن يناله ظلم. وفي الآية إشارة إلى أن اجتناب ما حرم الله من المكاسب الربوية، تكميل للإيمان وحقوقه من إقام الصلاة وإيتاء الزكاة وعمل الصالحات.');
INSERT INTO Quran__Arabic_Tafseer_Muyassar (QuranChapterID, VerseNumber, Rukuh, Sajdah, Manzil, HizbQuarter, VerseText) VALUES (2, 282, '0', '0', '0', '0', 'يا من آمنتم بالله واتبعتم رسوله محمدًا صلى الله عليه وسلم إذا تعاملتم بدَيْن إلى وقت معلوم فاكتبوه؛ حفظًا للمال ودفعًا للنزاع. ولْيقُم بالكتابة رجل أمين ضابط، ولا يمتنع مَن علَّمه الله الكتابة عن ذلك، ولْيقم المدين بإملاء ما عليه من الدَّيْن، وليراقب ربه، ولا ينقص من دينه شيئا. فإن كان المدين محجورًا عليه لتبذيره وإسرافه، أو كان صغيرًا أو مجنونًا، أو لا يستطيع النطق لخرس به أو عدم قدرة كاملة على الكلام، فليتولَّ الإملاء عن المدين القائم بأمره، واطلبوا شهادة رجلين مسلمَيْن بالِغَيْن عاقلَيْن من أهل العدالة. فإن لم يوجد رجلان، فاطلبوا شهادة رجل وامرأتين ترضون شهادتهم، حتى إذا نَسِيَتْ إحداهما ذكَّرتها الأخرى، وعلى الشهداء أن يجيبوا مَن دعاهم إلى الشهادة، وعليهم أداؤها إذا ما دعوا إليها، ولا تَمَلُّوا من كتابة الدَّين قليلا أو كثيرًا إلى وقته المعلوم. ذلكم أعدل في شرع الله وهديه، وأعظم عونًا على إقامة الشهادة وأدائها، وأقرب إلى نفي الشك في جنس الدَّين وقدره وأجله. لكن إن كانت المسألة مسألة بيع وشراء، بأخذ سلعة ودفع ثمنها في الحال، فلا حاجة إلى الكتابة، ويستحب الإشهاد على ذلك منعًا للنزاع والشقاق، ومن الواجب على الشاهد والكاتب أداء الشهادة على وجهها والكتابة كما أمر الله. ولا يجوز لصاحب الحق ومَن عليه الحق الإضرار بالكُتَّاب والشهود، وكذلك لا يجوز للكُتَّاب والشهود أن يضارُّوا بمن احتاج إلى كتابتهم أو شهادتهم، وإن تفعلوا ما نهيتم عنه فإنه خروج عن طاعة الله، وعاقبة ذلك حالَّة بكم. وخافوا الله في جميع ما أمركم به، ونهاكم عنه، ويعلمكم الله جميع ما يصلح دنياكم وأخراكم. والله بكل شيء عليم، فلا يخفى عليه شيء من أموركم، وسيجازيكم على ذلك.');
INSERT INTO Quran__Arabic_Tafseer_Muyassar (QuranChapterID, VerseNumber, Rukuh, Sajdah, Manzil, HizbQuarter, VerseText) VALUES (2, 283, '1', '0', '0', '1', 'وإن كنتم مسافرين ولم تجدوا مَن يكتب لكم فادفعوا إلى صاحب الحق شيئًا يكون عنده ضمانًا لحقِّه إلى أن يردَّ المدينُ ما عليه من دين، فإن وثق بعضكم ببعض فلا حرج في ترك الكتابة والإشهاد والرهن، ويبقى الدَّين أمانة في ذمَّة المدين، عليه أداؤه، وعليه أن يراقب الله فلا يخون صاحبه. فإن أنكر المدين ما عليه من دين، وكان هناك مَن حضر وشهد، فعليه أن يظهر شهادته، ومن أخفى هذه الشهادة فهو صاحب قلب غادر فاجر. والله المُطَّلِع على السرائر، المحيط علمه بكل أموركم، سيحاسبكم على ذلك.');
INSERT INTO Quran__Arabic_Tafseer_Muyassar (QuranChapterID, VerseNumber, Rukuh, Sajdah, Manzil, HizbQuarter, VerseText) VALUES (2, 284, '0', '0', '0', '0', 'لله ملك السماوات والأرض وما فيهما ملكًا وتدبيرًا وإحاطة، لا يخفى عليه شيء. وما تظهروه مما في أنفسكم أو تخفوه فإن الله يعلمه، وسيحاسبكم به، فيعفو عمن يشاء، ويؤاخذ من يشاء. والله قادر على كل شيء، وقد أكرم الله المسلمين بعد ذلك فعفا عن حديث النفس وخطرات القلب ما لم يتبعها كلام أو عمل، كما ثبت ذلك عن رسول الله صلى الله عليه وسلم.');
INSERT INTO Quran__Arabic_Tafseer_Muyassar (QuranChapterID, VerseNumber, Rukuh, Sajdah, Manzil, HizbQuarter, VerseText) VALUES (2, 285, '0', '0', '0', '0', 'صدَّق وأيقن رسول الله محمد صلى الله عليه وسلم بما أوحي إليه من ربه وحُقَّ له أن يُوقن، والمؤمنون كذلك صدقوا وعملوا بالقرآن العظيم، كل منهم صدَّق بالله رباً وإلهًا متصفًا بصفات الجلال والكمال، وأن لله ملائكة كرامًا، وأنه أنزل كتبًا، وأرسل إلى خلقه رسلا لا نؤمن -نحن المؤمنين- ببعضهم وننكر بعضهم، بل نؤمن بهم جميعًا. وقال الرسول والمؤمنون: سمعنا يا ربنا ما أوحيت به، وأطعنا في كل ذلك، نرجو أن تغفر -بفضلك- ذنوبنا، فأنت الذي ربَّيتنا بما أنعمت به علينا، وإليك -وحدك- مرجعنا ومصيرنا.');
INSERT INTO Quran__Arabic_Tafseer_Muyassar (QuranChapterID, VerseNumber, Rukuh, Sajdah, Manzil, HizbQuarter, VerseText) VALUES (2, 286, '1', '0', '0', '0', 'دين الله يسر لا مشقة فيه، فلا يطلب الله مِن عباده ما لا يطيقونه، فمن فعل خيرًا نال خيرًا، ومن فعل شرّاً نال شرّاً. ربنا لا تعاقبنا إن نسينا شيئًا مما افترضته علينا، أو أخطأنا في فِعْل شيء نهيتنا عن فعله، ربَّنا ولا تكلفنا من الأعمال الشاقة ما كلفته مَن قبلنا من العصاة عقوبة لهم، ربنا ولا تُحَمِّلْنَا ما لا نستطيعه من التكاليف والمصائب، وامح ذنوبنا، واستر عيوبنا، وأحسن إلينا، أنت مالك أمرنا ومدبره، فانصرنا على مَن جحدوا دينك وأنكروا وحدانيتك، وكذَّبوا نبيك محمدًا صلى الله عليه وسلم، واجعل العاقبة لنا عليهم في الدنيا والآخرة.');
INSERT INTO Quran__Arabic_Tafseer_Muyassar (QuranChapterID, VerseNumber, Rukuh, Sajdah, Manzil, HizbQuarter, VerseText) VALUES (3, 1, '0', '0', '0', '0', 'سبق الكلام عليها في أول سورة البقرة.');
INSERT INTO Quran__Arabic_Tafseer_Muyassar (QuranChapterID, VerseNumber, Rukuh, Sajdah, Manzil, HizbQuarter, VerseText) VALUES (3, 2, '0', '0', '0', '0', 'هو الله، لا معبود بحق إلا هو، المتصف بالحياة الكاملة كما يليق بجلاله، القائم على كل شيء.');
INSERT INTO Quran__Arabic_Tafseer_Muyassar (QuranChapterID, VerseNumber, Rukuh, Sajdah, Manzil, HizbQuarter, VerseText) VALUES (3, 3, '0', '0', '0', '0', 'نَزَّل عليك القرآن بالحق الذي لا ريب فيه، مصدِّقًا لما قبله من كتب ورسل، وأنزل التوراة على موسى علبه السلام، والإنجيل على عيسى عليه السلام من قبل نزول القرآن؛ لإرشاد المتقين إلى الإيمان، وصلاح دينهم ودنياهم، وأنزل ما يفرق بين الحق والباطل. والذين كفروا بآيات الله المنزلة، لهم عذاب عظيم. والله عزيز لا يُغَالَب، ذو انتقام بمن جحد حججه وأدلته، وتفرُّده بالألوهية.');
INSERT INTO Quran__Arabic_Tafseer_Muyassar (QuranChapterID, VerseNumber, Rukuh, Sajdah, Manzil, HizbQuarter, VerseText) VALUES (3, 4, '0', '0', '0', '0', 'نَزَّل عليك القرآن بالحق الذي لا ريب فيه، مصدِّقًا لما قبله من كتب ورسل، وأنزل التوراة على موسى علبه السلام، والإنجيل على عيسى عليه السلام من قبل نزول القرآن؛ لإرشاد المتقين إلى الإيمان، وصلاح دينهم ودنياهم، وأنزل ما يفرق بين الحق والباطل. والذين كفروا بآيات الله المنزلة، لهم عذاب عظيم. والله عزيز لا يُغَالَب، ذو انتقام بمن جحد حججه وأدلته، وتفرُّده بالألوهية.');
INSERT INTO Quran__Arabic_Tafseer_Muyassar (QuranChapterID, VerseNumber, Rukuh, Sajdah, Manzil, HizbQuarter, VerseText) VALUES (3, 5, '0', '0', '0', '0', 'إن الله محيط علمه بالخلائق، لا يخفى عليه شيء في الأرض ولا في السماء، قلَّ أو كثر.');
INSERT INTO Quran__Arabic_Tafseer_Muyassar (QuranChapterID, VerseNumber, Rukuh, Sajdah, Manzil, HizbQuarter, VerseText) VALUES (3, 6, '0', '0', '0', '0', 'هو وحده الذي يخلقكم في أرحام أمهاتكم كما يشاء، من ذكر وأنثى، وحسن وقبيح، وشقي وسعيد، لا معبود بحق سواه، العزيز الذي لا يُغالَب، الحكيم في أمره وتدبيره.');
INSERT INTO Quran__Arabic_Tafseer_Muyassar (QuranChapterID, VerseNumber, Rukuh, Sajdah, Manzil, HizbQuarter, VerseText) VALUES (3, 7, '0', '0', '0', '0', 'هو وحده الذي أنزل عليك القرآن: منه آيات واضحات الدلالة، هن أصل الكتاب الذي يُرجع إليه عند الاشتباه، ويُرَدُّ ما خالفه إليه، ومنه آيات أخر متشابهات تحتمل بعض المعاني، لا يتعيَّن المراد منها إلا بضمها إلى المحكم، فأصحاب القلوب المريضة الزائغة، لسوء قصدهم يتبعون هذه الآيات المتشابهات وحدها؛ ليثيروا الشبهات عند الناس، كي يضلوهم، ولتأويلهم لها على مذاهبهم الباطلة. ولا يعلم حقيقة معاني هذه الآيات إلا الله. والمتمكنون في العلم يقولون: آمنا بهذا القرآن، كله قد جاءنا من عند ربنا على لسان رسوله محمد صلى الله عليه وسلم، ويردُّون متشابهه إلى محكمه، وإنما يفهم ويعقل ويتدبر المعاني على وجهها الصحيح أولو العقول السليمة.');
INSERT INTO Quran__Arabic_Tafseer_Muyassar (QuranChapterID, VerseNumber, Rukuh, Sajdah, Manzil, HizbQuarter, VerseText) VALUES (3, 8, '0', '0', '0', '0', 'ويقولون: يا ربنا لا تَصْرِف قلوبنا عن الإيمان بك بعد أن مننت علينا بالهداية لدينك، وامنحنا من فضلك رحمة واسعة، إنك أنت الوهاب: كثير الفضل والعطاء، تعطي مَن تشاء بغير حساب.');
INSERT INTO Quran__Arabic_Tafseer_Muyassar (QuranChapterID, VerseNumber, Rukuh, Sajdah, Manzil, HizbQuarter, VerseText) VALUES (3, 9, '1', '0', '0', '0', 'يا ربنا إنا نُقِرُّ ونشهد بأنك ستجمع الناس في يوم لا شَكَّ فيه، وهو يوم القيامة، إنَّك لا تُخلف ما وعَدْتَ به عبادك.');
INSERT INTO Quran__Arabic_Tafseer_Muyassar (QuranChapterID, VerseNumber, Rukuh, Sajdah, Manzil, HizbQuarter, VerseText) VALUES (3, 10, '0', '0', '0', '0', 'إن الذين جحدوا الدين الحق وأنكروه، لن تنفعهم أموالهم ولا أولادهم من عذاب الله شيئًا إن وقع بهم في الدنيا، ولن تدفعه عنهم في الآخرة، وهؤلاء هم حطب النار يوم القيامة.');
INSERT INTO Quran__Arabic_Tafseer_Muyassar (QuranChapterID, VerseNumber, Rukuh, Sajdah, Manzil, HizbQuarter, VerseText) VALUES (3, 11, '0', '0', '0', '0', 'شأن الكافرين في تكذيبهم وما ينزل بهم، شأن آل فرعون والذين من قبلهم من الكافرين، أنكروا آيات الله الواضحة، فعاجلهم بالعقوبة بسبب تكذيبهم وعنادهم. والله شديد العقاب لمن كفر به وكذَّب رسله.');
INSERT INTO Quran__Arabic_Tafseer_Muyassar (QuranChapterID, VerseNumber, Rukuh, Sajdah, Manzil, HizbQuarter, VerseText) VALUES (3, 12, '0', '0', '0', '0', 'قل -أيها الرسول-، للذين كفروا من اليهود وغيرهم والذين استهانوا بنصرك في "بَدْر": إنكم ستُهْزَمون في الدنيا وستموتون على الكفر، وتحشرون إلى نار جهنم؛ لتكون فراشًا دائمًا لكم، وبئس الفراش.');
INSERT INTO Quran__Arabic_Tafseer_Muyassar (QuranChapterID, VerseNumber, Rukuh, Sajdah, Manzil, HizbQuarter, VerseText) VALUES (3, 13, '0', '0', '0', '0', 'قد كان لكم -أيها اليهود المتكبرون المعاندون- دلالة عظيمة في جماعتين تقابلتا في معركة "بَدْر": جماعة تقاتل من أجل دين الله، وهم محمد صلى الله عليه وسلم وأصحابه، وجماعة أخرى كافرة بالله، تقاتل من أجل الباطل، ترى المؤمنين في العدد مثليهم رأي العين، وقد جعل الله ذلك سببًا لنصر المسلمين عليهم. والله يؤيِّد بنصره من يشاء من عباده. إن في هذا الذي حدث لَعِظة عظيمة لأصحاب البصائر الذين يهتدون إلى حكم الله وأفعاله.');
INSERT INTO Quran__Arabic_Tafseer_Muyassar (QuranChapterID, VerseNumber, Rukuh, Sajdah, Manzil, HizbQuarter, VerseText) VALUES (3, 14, '0', '0', '0', '0', 'حُسِّن للناس حبُّ الشهوات من النساء والبنين، والأموال الكثيرة من الذهب والفضة، والخيل الحسان، والأنعام من الإبل والبقر والغنم، والأرض المتَّخَذة للغراس والزراعة. ذلك زهرة الحياة الدنيا وزينتها الفانية. والله عنده حسن المرجع والثواب، وهو الجنَّة.');
INSERT INTO Quran__Arabic_Tafseer_Muyassar (QuranChapterID, VerseNumber, Rukuh, Sajdah, Manzil, HizbQuarter, VerseText) VALUES (3, 15, '0', '0', '0', '1', 'قل -أيها الرسول-: أأخبركم بخير مما زُيِّن للنَّاس في هذه الحياة الدنيا، لمن راقب الله وخاف عقابه جنات تجري من تحت قصورها وأشجارها الأنهار، خالدين فيها، ولهم فيها أزواج مطهرات من الحيض والنفاس وسوء الخلق، ولهم أعظم من ذلك: رضوان من الله. والله مطَّلِع على سرائر خلقه، عالم بأحوالهم، وسيجازيهم على ذلك.');
INSERT INTO Quran__Arabic_Tafseer_Muyassar (QuranChapterID, VerseNumber, Rukuh, Sajdah, Manzil, HizbQuarter, VerseText) VALUES (3, 16, '0', '0', '0', '0', 'هؤلاء العباد المتقون يقولون: إننا آمنا بك، واتبعنا رسولك محمدًا صلى الله عليه وسلم، فامْحُ عنا ما اقترفناه من ذنوب، ونجنا من عذاب النار.');
INSERT INTO Quran__Arabic_Tafseer_Muyassar (QuranChapterID, VerseNumber, Rukuh, Sajdah, Manzil, HizbQuarter, VerseText) VALUES (3, 17, '0', '0', '0', '0', 'هم الذين اتصفوا بالصبر على الطاعات، وعن المعاصي، وعلى ما يصيبهم من أقدار الله المؤلمة، وبالصدق في الأقوال والأفعال وبالطاعة التامة، وبالإنفاق سرا وعلانية، وبالاستغفار في آخر الليل؛ لأنه مَظِنَّة القبول وإجابة الدعاء.');
INSERT INTO Quran__Arabic_Tafseer_Muyassar (QuranChapterID, VerseNumber, Rukuh, Sajdah, Manzil, HizbQuarter, VerseText) VALUES (3, 18, '0', '0', '0', '0', 'شهد الله أنه المتفرد بالإلهية، وقَرَنَ شهادته بشهادة الملائكة وأهل العلم، على أجلِّ مشهود عليه، وهو توحيده تعالى وقيامه بالعدل، لا إله إلا هو العزيز الذي لا يمتنع عليه شيء أراده، الحكيم في أقواله وأفعاله.');
INSERT INTO Quran__Arabic_Tafseer_Muyassar (QuranChapterID, VerseNumber, Rukuh, Sajdah, Manzil, HizbQuarter, VerseText) VALUES (3, 19, '0', '0', '0', '0', 'إن الدين الذي ارتضاه الله لخلقه وأرسل به رسله، ولا يَقْبَل غيره هو الإسلام، وهو الانقياد لله وحده بالطاعة والاستسلام له بالعبودية، واتباع الرسل فيما بعثهم الله به في كل حين حتى خُتموا بمحمد صلى الله عليه وسلم، الذي لا يقبل الله مِن أحد بعد بعثته دينًا سوى الإسلام الذي أُرسل به. وما وقع الخلاف بين أهل الكتاب من اليهود والنصارى، فتفرقوا شيعًا وأحزابًا إلا من بعد ما قامت الحجة عليهم بإرسال الرسل وإنزال الكتب؛ بغيًا وحسدًا طلبًا للدنيا. ومن يجحد آيات الله المنزلة وآياته الدالة على ربوبيته وألوهيته، فإن الله سريع الحساب، وسيجزيهم بما كانوا يعملون.');
INSERT INTO Quran__Arabic_Tafseer_Muyassar (QuranChapterID, VerseNumber, Rukuh, Sajdah, Manzil, HizbQuarter, VerseText) VALUES (3, 20, '1', '0', '0', '0', 'فإن جادلك -أيها الرسول- أهل الكتاب في التوحيد بعد أن أقمت الحجة عليهم فقل لهم: إنني أخلصت لله وحده فلا أشرك به أحدًا، وكذلك من اتبعني من المؤمنين، أخلصوا لله وانقادوا له. وقل لهم ولمشركي العرب وغيرهم: إن أسلمتم فأنتم على الطريق المستقيم والهدى والحق، وإن توليتم فحسابكم على الله، وليس عليَّ إلا البلاغ، وقد أبلغتكم وأقمت عليكم الحجة. والله بصير بالعباد، لا يخفى عليه من أمرهم شيء.');
INSERT INTO Quran__Arabic_Tafseer_Muyassar (QuranChapterID, VerseNumber, Rukuh, Sajdah, Manzil, HizbQuarter, VerseText) VALUES (3, 21, '0', '0', '0', '0', 'إن الذين يجحدون بالدلائل الواضحة وما جاء به المرسلون، ويقتلون أنبياء الله ظلمًا بغير حق، ويقتلون الذين يأمرون بالعدل واتباع طريق الأنبياء، فبشِّرهم بعذاب موجع.');
INSERT INTO Quran__Arabic_Tafseer_Muyassar (QuranChapterID, VerseNumber, Rukuh, Sajdah, Manzil, HizbQuarter, VerseText) VALUES (3, 22, '0', '0', '0', '0', 'أولئك الذين بطلت أعمالهم في الدنيا والآخرة، فلا يُقبل لهم عمل، وما لهم من ناصرٍ ينصرهم من عذاب الله.');
INSERT INTO Quran__Arabic_Tafseer_Muyassar (QuranChapterID, VerseNumber, Rukuh, Sajdah, Manzil, HizbQuarter, VerseText) VALUES (3, 23, '0', '0', '0', '0', 'أرأيت -أيها الرسول- أعجب من حال هؤلاء اليهود الذين أتاهم الله حظا من الكتاب فعلموا أن ما جئت به هو الحق، يُدْعون إلى ما جاء في كتاب الله -وهو القرآن- ليفصل بينهم فيما اختلفوا فيه، فإن لم يوافق أهواءهم يَأْبَ كثير منهم حكم الله؛ لأن من عادتهم الإعراض عن الحق؟');
INSERT INTO Quran__Arabic_Tafseer_Muyassar (QuranChapterID, VerseNumber, Rukuh, Sajdah, Manzil, HizbQuarter, VerseText) VALUES (3, 24, '0', '0', '0', '0', 'ذلك الانصراف عن الحق سببه اعتقاد فاسد لدى أهل الكتاب؛ بأنهم لن يعذَّبوا إلا أيامًا قليلة، وهذا الاعتقاد أدى إلى جرأتهم على الله واستهانتهم بدينه، واستمرارهم على دينهم الباطل الذي خَدَعوا به أنفسهم.');
INSERT INTO Quran__Arabic_Tafseer_Muyassar (QuranChapterID, VerseNumber, Rukuh, Sajdah, Manzil, HizbQuarter, VerseText) VALUES (3, 25, '0', '0', '0', '0', 'فكيف يكون حالهم إذا جمعهم الله ليحاسَبوا في يوم لا شك في وقوعه -وهو يوم القيامة-، وأخذ كل واحد جزاءَ ما اكتسب، وهم لا يظلمون شيئا؟');
INSERT INTO Quran__Arabic_Tafseer_Muyassar (QuranChapterID, VerseNumber, Rukuh, Sajdah, Manzil, HizbQuarter, VerseText) VALUES (3, 26, '0', '0', '0', '0', 'قل -أيها النبي متوجها إلى ربك بالدعاء-: يا مَن لك الملك كلُّه، أنت الذي تمنح الملك والمال والتمكين في الأرض مَن تشاء مِن خلقك، وتسلب الملك ممن تشاء، وتهب العزة في الدنيا والآخرة مَن تشاء، وتجعل الذلَّة على من تشاء، بيدك الخير، إنك -وحدك- على كل شيء قدير. وفي الآية إثبات لصفة اليد لله تعالى على ما يليق به سبحانه.');
INSERT INTO Quran__Arabic_Tafseer_Muyassar (QuranChapterID, VerseNumber, Rukuh, Sajdah, Manzil, HizbQuarter, VerseText) VALUES (3, 27, '0', '0', '0', '0', 'ومن دلائل قدرتك أنك تُدخل الليل في النهار، وتُدخل النهار في الليل، فيطول هذا ويقصر ذاك، وتُخرج الحي من الميت الذي لا حياة فيه، كإخراج الزرع من الحب، والمؤمن من الكافر، وتُخرج الميت من الحي كإخراج البيض من الدجاج، وترزق من تشاء مَن خلقك بغير حساب.');
INSERT INTO Quran__Arabic_Tafseer_Muyassar (QuranChapterID, VerseNumber, Rukuh, Sajdah, Manzil, HizbQuarter, VerseText) VALUES (3, 28, '0', '0', '0', '0', 'ينهى الله المؤمنين أن يتخذوا الكافرين أولياء بالمحبة والنصرة من دون المؤمنين، ومَن يتولهم فقد برِئ من الله، والله برِيء منه، إلا أن تكونوا ضعافًا خائفين فقد رخَّص الله لكم في مهادنتهم اتقاء لشرهم، حتى تقوى شوكتكم، ويحذركم الله نفسه، فاتقوه وخافوه. وإلى الله وحده رجوع الخلائق للحساب والجزاء.');
INSERT INTO Quran__Arabic_Tafseer_Muyassar (QuranChapterID, VerseNumber, Rukuh, Sajdah, Manzil, HizbQuarter, VerseText) VALUES (3, 29, '0', '0', '0', '0', 'قل -أيها النبي- للمؤمنين: إن تكتموا ما استقر في قلوبكم من ممالاة الكافرين ونصرتهم أم تظهروا ذلك لا يَخْفَ على الله منه شيء، فإنَّ علمه محيط بكل ما في السماوات وما في الأرض، وله القدرة التامة على كل شيء.');
INSERT INTO Quran__Arabic_Tafseer_Muyassar (QuranChapterID, VerseNumber, Rukuh, Sajdah, Manzil, HizbQuarter, VerseText) VALUES (3, 30, '1', '0', '0', '0', 'وفي يوم القيامة يوم الجزاء تجد كل نفس ما عملت من خير ينتظرها موفرًا لتُجزَى به، وما عملت من عمل سيِّئ تجده في انتظارها أيضًا، فتتمنى لو أن بينها وبين هذا العمل زمنًا بعيدًا. فاستعدوا لهذا اليوم، وخافوا بطش الإله الجبار. ومع شدَّة عقابه فإنه سبحانه رءوف بالعباد.');
INSERT INTO Quran__Arabic_Tafseer_Muyassar (QuranChapterID, VerseNumber, Rukuh, Sajdah, Manzil, HizbQuarter, VerseText) VALUES (3, 31, '0', '0', '0', '0', 'قل -أيها الرسول-: إن كنتم تحبون الله حقا فاتبعوني وآمنوا بي ظاهرًا وباطنًا، يحببكم الله، ويمحُ ذنوبكم، فإنه غفور لذنوب عباده المؤمنين، رحيم بهم. وهذه الآية الكريمة حاكمة على كل من ادعى محبة الله -تعالى- وليس متبعًا لنبيه محمد صلى الله عيه وسلم حق الاتباع، مطيعًا له في أمره ونهيه، فإنه كاذب في دعواه حتى يتابع الرسول صلى الله عليه وسلم حق الاتباع.');
INSERT INTO Quran__Arabic_Tafseer_Muyassar (QuranChapterID, VerseNumber, Rukuh, Sajdah, Manzil, HizbQuarter, VerseText) VALUES (3, 32, '0', '0', '0', '0', 'قل -أيها الرسول-: أطيعوا الله باتباع كتابه، وأطيعوا الرسول باتباع سنته في حياته وبعد مماته، فإن هم أعرضوا عنك، وأصروا على ما هم عليه مِن كفر وضلال، فليسوا أهلا لمحبة الله؛ فإن الله لا يحب الكافرين.');
INSERT INTO Quran__Arabic_Tafseer_Muyassar (QuranChapterID, VerseNumber, Rukuh, Sajdah, Manzil, HizbQuarter, VerseText) VALUES (3, 33, '0', '0', '0', '1', 'إن الله اختار آدم ونوحًا وآل إبراهيم وآل عمران، وجعلهم أفضل أهل زمانهم.');
INSERT INTO Quran__Arabic_Tafseer_Muyassar (QuranChapterID, VerseNumber, Rukuh, Sajdah, Manzil, HizbQuarter, VerseText) VALUES (3, 34, '0', '0', '0', '0', 'هؤلاء الأنبياء والرسل سلسلة طُهْر متواصلة في الإخلاص لله وتوحيده والعمل بوحيه. والله سميع لأقوال عباده، عليم بأفعالهم، وسيجازيهم على ذلك.');
INSERT INTO Quran__Arabic_Tafseer_Muyassar (QuranChapterID, VerseNumber, Rukuh, Sajdah, Manzil, HizbQuarter, VerseText) VALUES (3, 35, '0', '0', '0', '0', 'اذكر -أيها الرسول- ما كان من أمر مريم وأمها وابنها عيسى عليه السلام؛ لتردَّ بذلك على من ادعوا أُلوهية عيسى أو بنوَّته لله سبحانه، إذ قالت امرأة عمران حين حملت: يا ربِّ إني جعلت لك ما في بطني خالصا لك، لخدمة "بيت المقدس"، فتقبَّل مني؛ إنك أنت وحدك السميع لدعائي، العليم بنيتي.');
INSERT INTO Quran__Arabic_Tafseer_Muyassar (QuranChapterID, VerseNumber, Rukuh, Sajdah, Manzil, HizbQuarter, VerseText) VALUES (3, 36, '0', '0', '0', '0', 'فلما تمَّ حملها ووضعت مولودها قالت: ربِّ إني وضعتها أنثى لا تصلح للخدمة في "بيت المقدس" -والله أعلم بما وضَعَتْ، وسوف يجعل الله لها شأنًا- وقالت: وليس الذكر الذي أردت للخدمة كالأنثى في ذلك؛ لأن الذكر أقوى على الخدمة وأقْوَم بها، وإني سمَّيتها مريم، وإني حصَّنتها بك هي وذريَّتها من الشيطان المطرود من رحمتك.');
INSERT INTO Quran__Arabic_Tafseer_Muyassar (QuranChapterID, VerseNumber, Rukuh, Sajdah, Manzil, HizbQuarter, VerseText) VALUES (3, 37, '0', '0', '0', '0', 'فاستجاب الله دعاءها وقبل منها نَذْرها أحسن قَبول، وتولَّى ابنتها مريم بالرعاية فأنبتها نباتًا حسنًا، ويسَّر الله لها زكريا عليه السلام كافلا فأسكنها في مكان عبادته، وكان كلَّما دخل عليها هذا المكان وجد عندها رزقًا هنيئًا معدّاً قال: يا مريم من أين لكِ هذا الرزق الطيب؟ قالت: هو رزق من عند الله. إن الله -بفضله- يرزق مَن يشاء مِن خلقه بغير حساب.');
INSERT INTO Quran__Arabic_Tafseer_Muyassar (QuranChapterID, VerseNumber, Rukuh, Sajdah, Manzil, HizbQuarter, VerseText) VALUES (3, 38, '0', '0', '0', '0', 'عندما رأى زكريا ما أكرم الله به مريم مِن رزقه وفضله توجه إلى ربه قائلا يا ربِّ أعطني من عندك ولدًا صالحًا مباركًا، إنك سميع الدعاء لمن دعاك.');
INSERT INTO Quran__Arabic_Tafseer_Muyassar (QuranChapterID, VerseNumber, Rukuh, Sajdah, Manzil, HizbQuarter, VerseText) VALUES (3, 39, '0', '0', '0', '0', 'فنادته الملائكة وهو واقف بين يدي الله في مكان صلاته يدعوه: أن الله يخبرك بخبر يسرُّك، وهو أنك سترزق بولد اسمه يحيى، يُصَدِّق بكلمة من الله -وهو عيسى ابن مريم عليه السلام-، ويكون يحيى سيدًا في قومه، له المكانة والمنزلة العالية، وحصورًا لا يأتي الذنوب والشهوات الضارة، ويكون نبيّاً من الصالحين الذين بلغوا في الصَّلاح ذروته.');
INSERT INTO Quran__Arabic_Tafseer_Muyassar (QuranChapterID, VerseNumber, Rukuh, Sajdah, Manzil, HizbQuarter, VerseText) VALUES (3, 40, '0', '0', '0', '0', 'قال زكريا فرحًا متعجبًا: ربِّ أنَّى يكون لي غلام مع أن الشيخوخة قد بلغت مني مبلغها، وامرأتي عقيم لا تلد؟ قال: كذلك يفعل الله ما يشاء من الأفعال العجيبة المخالفة للعادة.');
INSERT INTO Quran__Arabic_Tafseer_Muyassar (QuranChapterID, VerseNumber, Rukuh, Sajdah, Manzil, HizbQuarter, VerseText) VALUES (3, 41, '1', '0', '0', '0', 'قال زكريَّا: رب اجعل لي علامةً أستدلُّ بها على وجود الولد مني؛ ليحصل لي السرور والاستبشار، قال: علامتك التي طلبتها: ألا تستطيع التحدث إلى الناس ثلاثة أيام إلا بإشارة إليهم، مع أنك سويٌّ صحيح، وفي هذه المدة أكثِرْ من ذكر ربك، وصلِّ له أواخر النهار وأوائله.');
INSERT INTO Quran__Arabic_Tafseer_Muyassar (QuranChapterID, VerseNumber, Rukuh, Sajdah, Manzil, HizbQuarter, VerseText) VALUES (3, 42, '0', '0', '0', '0', 'واذكر -أيها الرسول- حين قالت الملائكة: يا مريم إن الله اختاركِ لطاعته وطهَّركِ من الأخلاق الرذيلة، واختاركِ على نساء العالمين في زمانك.');
INSERT INTO Quran__Arabic_Tafseer_Muyassar (QuranChapterID, VerseNumber, Rukuh, Sajdah, Manzil, HizbQuarter, VerseText) VALUES (3, 43, '0', '0', '0', '0', 'يا مريم داومي على الطاعة لربك، وقومي في خشوع وتواضع، واسجدي واركعي مع الراكعين؛ شكرًا لله على ما أولاكِ من نعمه.');
INSERT INTO Quran__Arabic_Tafseer_Muyassar (QuranChapterID, VerseNumber, Rukuh, Sajdah, Manzil, HizbQuarter, VerseText) VALUES (3, 44, '0', '0', '0', '0', 'ذلك الذي قصصناه عليك -أيها الرسول- من أخبار الغيب التي أوحاها الله إليك، إذ لم تكن معهم حين اختلفوا في كفالة مريم أيُّهم أحق بها وأولى، ووقع بينهم الخصام، فأجْرَوْا القرعة لإلقاء أقلامهم، ففاز زكريا عليه السلام بكفالتها.');
INSERT INTO Quran__Arabic_Tafseer_Muyassar (QuranChapterID, VerseNumber, Rukuh, Sajdah, Manzil, HizbQuarter, VerseText) VALUES (3, 45, '0', '0', '0', '0', 'وما كنت - يا نبي الله - هناك حين قالت الملائكة: يا مريم إن الله يُبَشِّرْكِ بولد يكون وجوده بكلمة من الله، أي يقول له: "كن"، فيكون، اسمه المسيح عيسى ابن مريم، له الجاه العظيم في الدنيا والآخرة، ومن المقربين عند الله يوم القيامة.');
INSERT INTO Quran__Arabic_Tafseer_Muyassar (QuranChapterID, VerseNumber, Rukuh, Sajdah, Manzil, HizbQuarter, VerseText) VALUES (3, 46, '0', '0', '0', '0', 'ويكلم الناس في المهد بعد ولادته، وكذلك يكلمهم في حال كهولته بما أوحاه الله إليه. وهذا تكليم النبوَّة والدعوة والإرشاد، وهو معدود من أهل الصلاح والفضل في قوله وعمله.');
INSERT INTO Quran__Arabic_Tafseer_Muyassar (QuranChapterID, VerseNumber, Rukuh, Sajdah, Manzil, HizbQuarter, VerseText) VALUES (3, 47, '0', '0', '0', '0', 'قالت مريم متعجبة من هذا الأمر: أنَّى يكون لي ولد وأنا لست بذات زوج ولا بَغِيٍّ؟ قال لها المَلَك: هذا الذي يحدث لكِ ليس بمستبعد على الإله القادر، الذي يوجِد ما يشاء من العدم، فإذا أراد إيجاد شيء فإنما يقول له: "كُن" فيكون.');
INSERT INTO Quran__Arabic_Tafseer_Muyassar (QuranChapterID, VerseNumber, Rukuh, Sajdah, Manzil, HizbQuarter, VerseText) VALUES (3, 48, '0', '0', '0', '0', 'ويعلمه الكتابة، والسداد في القول والفعل، والتوراة التي أوحاها الله إلى موسى عليه السلام، والإنجيل الذي أنزل الله عليه.');
INSERT INTO Quran__Arabic_Tafseer_Muyassar (QuranChapterID, VerseNumber, Rukuh, Sajdah, Manzil, HizbQuarter, VerseText) VALUES (3, 49, '0', '0', '0', '0', 'ويجعله رسولا إلى بني إسرائيل، ويقول لهم: إني قد جئتكم بعلامة من ربكم تدلُّ على أني مرسل من الله، وهي أني أصنع لكم من الطين مثل شكل الطير، فأنفخ فيه فيكون طيرًا حقيقيا بإذن الله، وأَشفي مَن وُلِد أعمى، ومَن به برص، وأُحيي من كان ميتًا بإذن الله، وأخبركم بما تأكلون وتدَّخرون في بيوتكم من طعامكم. إن في هذه الأمور العظيمة التي ليست في قدرة البشر لدليلا على أني نبي الله ورسوله، إن كنتم مصدِّقين حجج الله وآياته، مقرِّين بتوحيده.');
INSERT INTO Quran__Arabic_Tafseer_Muyassar (QuranChapterID, VerseNumber, Rukuh, Sajdah, Manzil, HizbQuarter, VerseText) VALUES (3, 50, '0', '0', '0', '0', 'وجئتكم مصدقًا بما في التوراة، ولأحلَّ لكم بوحي من الله بعض ما حرَّمه الله عليكم تخفيفًا من الله ورحمة، وجئتكم بحجة من ربكم على صدق ما أقول لكم، فاتقوا الله ولا تخالفوا أمره، وأطيعوني فيما أبلغكم به عن الله.');
INSERT INTO Quran__Arabic_Tafseer_Muyassar (QuranChapterID, VerseNumber, Rukuh, Sajdah, Manzil, HizbQuarter, VerseText) VALUES (3, 51, '0', '0', '0', '0', 'إن الله الذي أدعوكم إليه هو وحده ربي وربكم فاعبدوه، فأنا وأنتم سواء في العبودية والخضوع له، وهذا هو الطريق الذي لا اعوجاج فيه.');
INSERT INTO Quran__Arabic_Tafseer_Muyassar (QuranChapterID, VerseNumber, Rukuh, Sajdah, Manzil, HizbQuarter, VerseText) VALUES (3, 52, '0', '0', '0', '1', 'فلما استشعر عيسى منهم التصميم على الكفر نادى في أصحابه الخُلَّص: مَن يكون معي في نصرة دين الله؟ قال أصفياء عيسى: نحن أنصار دين الله والداعون إليه، صدَّقنا بالله واتبعناك، واشهد أنت يا عيسى بأنا مستسلمون لله بالتوحيد والطاعة.');
INSERT INTO Quran__Arabic_Tafseer_Muyassar (QuranChapterID, VerseNumber, Rukuh, Sajdah, Manzil, HizbQuarter, VerseText) VALUES (3, 53, '0', '0', '0', '0', 'ربنا صدَّقنا بما أنزلت من الإنجيل، واتبعنا رسولك عيسى عليه السلام، فاجعلنا ممن شهدوا لك بالوحدانية ولأنبيائك بالرسالة، وهم أمة محمد صلى الله عليه وسلم الذين يشهدون للرسل بأنهم بلَّغوا أممهم.');
INSERT INTO Quran__Arabic_Tafseer_Muyassar (QuranChapterID, VerseNumber, Rukuh, Sajdah, Manzil, HizbQuarter, VerseText) VALUES (3, 54, '1', '0', '0', '0', 'ومكر الذين كفروا من بني إسرائيل بعيسى عليه السلام، بأن وكَّلوا به من يقتله غِيْلة، فألقى الله شَبَه عيسى على رجل دلَّهم عليه فأمسكوا به، وقتلوه وصلبوه ظناً منهم أنه عيسى عليه السلام، والله خير الماكرين. وفي هذا إثبات صفة المكر لله -تعالى- على ما يليق بجلاله وكماله؛ لأنه مكر بحق، وفي مقابلة مكر الماكرين.');
INSERT INTO Quran__Arabic_Tafseer_Muyassar (QuranChapterID, VerseNumber, Rukuh, Sajdah, Manzil, HizbQuarter, VerseText) VALUES (3, 55, '0', '0', '0', '0', 'ومكر الله بهم حين قال الله لعيسى: إني قابضك من الأرض من غير أن ينالك سوء، ورافعك إليَّ ببدنك وروحك، ومخلصك من الذين كفروا بك، وجاعل الذين اتبعوك أي على دينك وما جئت به عن الله من الدين والبشارة بمحمد صلى الله عليه وسلم وآمَنوا بمحمد صلى الله عليه وسلم، بعد بعثنه، والتزموا شريعته ظاهرين على الذين جحدوا نبوتك إلى يوم القيامة، ثم إليّ مصيركم جميعًا يوم الحساب، فأفصِل بينكم فيما كنتم فيه تختلفون من أمر عيسى عليه السلام.');
INSERT INTO Quran__Arabic_Tafseer_Muyassar (QuranChapterID, VerseNumber, Rukuh, Sajdah, Manzil, HizbQuarter, VerseText) VALUES (3, 56, '0', '0', '0', '0', 'فأمَّا الذين كفروا بالمسيح من اليهود أو غَلَوا فيه من النصارى، فأعذبهم عذابًا شديدًا في الدنيا: بالقتل وسلْبِ الأموال وإزالة الملك، وفي الآخرة بالنار، وما لهم مِن ناصر ينصرهم ويدفع عنهم عذاب الله.');
INSERT INTO Quran__Arabic_Tafseer_Muyassar (QuranChapterID, VerseNumber, Rukuh, Sajdah, Manzil, HizbQuarter, VerseText) VALUES (3, 57, '0', '0', '0', '0', 'وأما الذين آمنوا بالله ورسله وعملوا الأعمال الصالحة، فيعطيهم الله ثواب أعمالهم كاملا غير منقوص. والله لا يحب الظالمين بالشرك والكفر.');
INSERT INTO Quran__Arabic_Tafseer_Muyassar (QuranChapterID, VerseNumber, Rukuh, Sajdah, Manzil, HizbQuarter, VerseText) VALUES (3, 58, '0', '0', '0', '0', 'ذلك الذي نقصُّه عليك في شأن عيسى، من الدلائل الواضحة على صحة رسالتك، وصحة القرآن الحكيم الذي يفصل بين الحق والباطل، فلا شك فيه ولا امتراء.');
INSERT INTO Quran__Arabic_Tafseer_Muyassar (QuranChapterID, VerseNumber, Rukuh, Sajdah, Manzil, HizbQuarter, VerseText) VALUES (3, 59, '0', '0', '0', '0', 'إنَّ خَلْقَ الله لعيسى من غير أب مثَلُه كمثل خلق الله لآدم من غير أب ولا أم، إذ خلقه من تراب الأرض، ثم قال له: "كن بشرًا" فكان. فدعوى إلهية عيسى لكونه خلق من غير أب دعوى باطلة؛ فآدم عليه السلام خلق من غير أب ولا أم، واتفق الجميع على أنه عَبْد من عباد الله.');
INSERT INTO Quran__Arabic_Tafseer_Muyassar (QuranChapterID, VerseNumber, Rukuh, Sajdah, Manzil, HizbQuarter, VerseText) VALUES (3, 60, '0', '0', '0', '0', 'الحق الذي لا شك فيه في أمر عيسى هو الذي جاءك -أيها الرسول- من ربك، فدل على يقينك، وعلى ما أنت عليه من ترك الافتراء، ولا تكن من الشاكِّين، وفي هذا تثبيت وطمأنة لرسول الله صلى الله عليه وسلم.');
INSERT INTO Quran__Arabic_Tafseer_Muyassar (QuranChapterID, VerseNumber, Rukuh, Sajdah, Manzil, HizbQuarter, VerseText) VALUES (3, 61, '0', '0', '0', '0', 'فمَن جادلك -أيها الرسول- في المسيح عيسى ابن مريم من بعد ما جاءك من العلم في أمر عيسى عليه السلام، فقل لهم: تعالوا نُحْضِر أبناءنا وأبناءكم، ونساءنا ونساءكم، وأنفسنا وأنفسكم، ثم نتجه إلى الله بالدعاء أن يُنزل عقوبته ولعنته على الكاذبين في قولهم، المصرِّين على عنادهم.');
INSERT INTO Quran__Arabic_Tafseer_Muyassar (QuranChapterID, VerseNumber, Rukuh, Sajdah, Manzil, HizbQuarter, VerseText) VALUES (3, 62, '0', '0', '0', '0', 'إن هذا الذي أنبأتك به من أمر عيسى لهو النبأ الحق الذي لا شك فيه، وما من معبود يستحق العبادة إلا الله وحده، وإن الله لهو العزيز في ملكه، الحكيم في تدبيره وفعله.');
INSERT INTO Quran__Arabic_Tafseer_Muyassar (QuranChapterID, VerseNumber, Rukuh, Sajdah, Manzil, HizbQuarter, VerseText) VALUES (3, 63, '1', '0', '0', '0', 'فإن أعرضوا عن تصديقك واتباعك فهم المفسدون، والله عليم بهم، وسيجازيهم على ذلك.');
INSERT INTO Quran__Arabic_Tafseer_Muyassar (QuranChapterID, VerseNumber, Rukuh, Sajdah, Manzil, HizbQuarter, VerseText) VALUES (3, 64, '0', '0', '0', '0', 'قل -أيها الرسول- لأهل الكتاب من اليهود والنصارى: تعالَوْا إلى كلمة عدل وحق نلتزم بها جميعًا: وهي أن نَخُص الله وحده بالعبادة، ولا نتخذ أي شريك معه، من وثن أو صنم أو صليب أو طاغوت أو غير ذلك، ولا يدين بعضنا لبعض بالطاعة من دون الله. فإن أعرضوا عن هذه الدعوة الطيبة فقولوا لهم - أيها المؤمنون -: اشهدوا علينا بأنا مسلمون منقادون لربنا بالعبودية والإخلاص. والدعوة إلى كلمة سواء، كما تُوجَّه إلى اليهود والنصارى، توجَّه إلى من جرى مجراهم.');
INSERT INTO Quran__Arabic_Tafseer_Muyassar (QuranChapterID, VerseNumber, Rukuh, Sajdah, Manzil, HizbQuarter, VerseText) VALUES (3, 65, '0', '0', '0', '0', 'يا أصحاب الكتب المنزلة من اليهود والنصارى، كيف يجادل كل منكم في أن إبراهيم عليه السلام كان على ملَّته، وما أُنزلت التوراة والإنجيل إلا من بعده؟ أفلا تفقهون خطأ قولكم: إن إبراهيم كان يهودياً أو نصرانياً، وقد علمتم أن اليهودية والنصرانية حدثت بعد وفاته بحين؟');
INSERT INTO Quran__Arabic_Tafseer_Muyassar (QuranChapterID, VerseNumber, Rukuh, Sajdah, Manzil, HizbQuarter, VerseText) VALUES (3, 66, '0', '0', '0', '0', 'ها أنتم يا هؤلاء جادلتم رسول الله محمدًا صلى الله عليه وسلم فيما لكم به علم مِن أمر دينكم، مما تعتقدون صحته في كتبكم، فلِمَ تجادلون فيما ليس لكم به علم من أمر إبراهيم؟ والله يعلم الأمور على خفائها، وأنتم لا تعلمون.');
INSERT INTO Quran__Arabic_Tafseer_Muyassar (QuranChapterID, VerseNumber, Rukuh, Sajdah, Manzil, HizbQuarter, VerseText) VALUES (3, 67, '0', '0', '0', '0', 'ما كان إبراهيم يهودياً ولا نصرانياً، فلم تكن اليهودية ولا النصرانية إلا من بعده، ولكن كان متبعًا لأمر الله وطاعته، مستسلمًا لربه، وما كان من المشركين.');
INSERT INTO Quran__Arabic_Tafseer_Muyassar (QuranChapterID, VerseNumber, Rukuh, Sajdah, Manzil, HizbQuarter, VerseText) VALUES (3, 68, '0', '0', '0', '0', 'إنَّ أحق الناس بإبراهيم وأخصهم به، الذين آمنوا به وصدقوا برسالته واتبعوه على دينه، وهذا النبي محمد صلى الله عليه وسلم والذين آمنوا به. والله وليُّ المؤمنين به المتبعين شرعه.');
INSERT INTO Quran__Arabic_Tafseer_Muyassar (QuranChapterID, VerseNumber, Rukuh, Sajdah, Manzil, HizbQuarter, VerseText) VALUES (3, 69, '0', '0', '0', '0', 'تمنَّت جماعة من اليهود والنصارى لو يضلونكم - أيها المسلمون - عن الإسلام، وما يضلون إلا أنفسهم وأتباعهم، وما يدرون ذلك ولا يعلمونه.');
INSERT INTO Quran__Arabic_Tafseer_Muyassar (QuranChapterID, VerseNumber, Rukuh, Sajdah, Manzil, HizbQuarter, VerseText) VALUES (3, 70, '0', '0', '0', '0', 'يا أهل التوراة والإنجيل لم تجحدون آيات الله التي أنزلها على رسله في كتبكم، وفيها أن محمدًا صلى الله عليه وسلم هو الرسول المنتظر، وأن ما جاءكم به هو الحق، وأنتم تشهدون بذلك؟ ولكنكم تنكرونه.');
INSERT INTO Quran__Arabic_Tafseer_Muyassar (QuranChapterID, VerseNumber, Rukuh, Sajdah, Manzil, HizbQuarter, VerseText) VALUES (3, 71, '1', '0', '0', '0', 'يا أهل التوراة والإنجيل لِمَ تخلطون الحق في كتبكم بما حرفتموه وكتبتموه من الباطل بأيديكم، وتُخْفون ما فيهما من صفة محمد صلى الله عليه وسلم، وأن دينه هو الحق، وأنتم تعلمون ذلك؟');
INSERT INTO Quran__Arabic_Tafseer_Muyassar (QuranChapterID, VerseNumber, Rukuh, Sajdah, Manzil, HizbQuarter, VerseText) VALUES (3, 72, '0', '0', '0', '0', 'وقالت جماعة من أهل الكتاب من اليهود: صدِّقوا بالذي أُنزل على الذين آمنوا أول النهار واكفروا آخره؛ لعلهم يتشككون في دينهم، ويرجعون عنه.');
INSERT INTO Quran__Arabic_Tafseer_Muyassar (QuranChapterID, VerseNumber, Rukuh, Sajdah, Manzil, HizbQuarter, VerseText) VALUES (3, 73, '0', '0', '0', '0', 'ولا تصدِّقوا تصديقًا صحيحًا إلا لمَن تبع دينكم فكان يهودياً، قل لهم -أيها الرسول-: إن الهدى والتوفيق هدى الله وتوفيقه للإيمان الصحيح. وقالوا: لا تظهروا ما عندكم من العلم للمسلمين فيتعلمون منكم فيساووكم في العلم به، وتكون لهم الأفضلية عليكم، أو أن يتخذوه حجة عند ربكم يغلبونكم بها. قل لهم -أيها الرسول-: إن الفضل والعطاء والأمور كلها بيد الله وتحت تصرفه، يؤتيها من يشاء ممن آمن به وبرسوله. والله واسع عليم، يَسَعُ بعلمه وعطائه جميع مخلوقاته، ممن يستحق فضله ونعمه.');
INSERT INTO Quran__Arabic_Tafseer_Muyassar (QuranChapterID, VerseNumber, Rukuh, Sajdah, Manzil, HizbQuarter, VerseText) VALUES (3, 74, '0', '0', '0', '0', 'إن الله يختص مِن خلقه مَن يشاء بالنبوة والهداية إلى أكمل الشرائع، والله ذو الفضل العظيم.');
INSERT INTO Quran__Arabic_Tafseer_Muyassar (QuranChapterID, VerseNumber, Rukuh, Sajdah, Manzil, HizbQuarter, VerseText) VALUES (3, 75, '0', '0', '0', '1', 'ومن أهل الكتاب من اليهود مَن إنْ تأمنه على كثير من المال يؤدِّه إليك من غير خيانة، ومنهم مَن إنْ تأمنه على دينار واحد لا يؤدِّه اليك، إلا إذا بذلت غاية الجهد في مطالبته. وسبب ذلك عقيدة فاسدة تجعلهم يستحلُّون أموال العرب بالباطل، ويقولون: ليس علينا في أكل أموالهم إثم ولا حرج؛ لأن الله أحلَّها لنا. وهذا كذب على الله، يقولونه بألسنتهم، وهم يعلمون أنهم كاذبون.');
INSERT INTO Quran__Arabic_Tafseer_Muyassar (QuranChapterID, VerseNumber, Rukuh, Sajdah, Manzil, HizbQuarter, VerseText) VALUES (3, 76, '0', '0', '0', '0', 'ليس الأمر كما زعم هؤلاء الكاذبون، فإن المتقي حقاً هو من أوفى بما عاهد الله عليه من أداء الأمانة والإيمان به وبرسله والتزم هديه وشرعه، وخاف الله عز وجل فامتثل أمره وانتهى عما نهى عنه. والله يحب المتقين الذين يتقون الشرك والمعاصي.');
INSERT INTO Quran__Arabic_Tafseer_Muyassar (QuranChapterID, VerseNumber, Rukuh, Sajdah, Manzil, HizbQuarter, VerseText) VALUES (3, 77, '0', '0', '0', '0', 'إن الذين يستبدلون بعهد الله ووصيته التي أوصى بها في الكتب التي أنزلها على أنبيائهم، عوضًا وبدلا خسيسًا من عرض الدنيا وحطامها، أولئك لا نصيب لهم من الثواب في الآخرة، ولا يكلمهم الله بما يسرهم، ولا ينظر إليهم يوم القيامة بعين الرحمة، ولا يطهرهم من دنس الذنوب والكفر، ولهم عذاب موجع.');
INSERT INTO Quran__Arabic_Tafseer_Muyassar (QuranChapterID, VerseNumber, Rukuh, Sajdah, Manzil, HizbQuarter, VerseText) VALUES (3, 78, '0', '0', '0', '0', 'وإن مِن اليهود لَجماعةً يحرفون الكلام عن مواضعه، ويبدلون كلام الله؛ ليوهموا غيرهم أن هذا من الكلام المنزل، وهو التوراة، وما هو منها في شيء، ويقولون: هذا من عند الله أوحاه الله إلى نبيه موسى، وما هو من عند الله، وهم لأجل دنياهم يقولون على الله الكذب وهم يعلمون أنهم كاذبون.');
INSERT INTO Quran__Arabic_Tafseer_Muyassar (QuranChapterID, VerseNumber, Rukuh, Sajdah, Manzil, HizbQuarter, VerseText) VALUES (3, 79, '0', '0', '0', '0', 'ما ينبغي لأحد من البشر أن يُنزِّل الله عليه كتابه ويجعله حكمًا بين خلقه ويختاره نبياً، ثم يقول للناس: اعبدوني من دون الله، ولكن يقول: كونوا حكماء فقهاء علماء بما كنتم تُعَلِّمونه غيركم من وحي الله تعالى، وبما تدرسونه منه حفظًا وعلمًا وفقهًا.');
INSERT INTO Quran__Arabic_Tafseer_Muyassar (QuranChapterID, VerseNumber, Rukuh, Sajdah, Manzil, HizbQuarter, VerseText) VALUES (3, 80, '1', '0', '0', '0', 'وما كان لأحد منهم أن يأمركم باتخاذ الملائكة والنبيين أربابًا تعبدونهم من دون الله. أَيُعْقَلُ -أيها الناس- أن يأمركم بالكفر بالله بعد انقيادكم لأمره؟');
INSERT INTO Quran__Arabic_Tafseer_Muyassar (QuranChapterID, VerseNumber, Rukuh, Sajdah, Manzil, HizbQuarter, VerseText) VALUES (3, 81, '0', '0', '0', '0', 'واذكر -أيها الرسول- إذ أخذ الله سبحانه العهد المؤكد على جميع الأنبياء: لَئِنْ آتيتكم من كتاب وحكمة، ثم جاءكم رسول من عندي، مصدق لما معكم لتؤمنن به ولتنصرنَّه. فهل أقررتم واعترفتم بذلك وأخذتم على ذلك عهدي الموثق؟ قالوا: أقررنا بذلك، قال: فليشهدْ بعضكم على بعض، واشهدوا على أممكم بذلك، وأنا معكم من الشاهدين عليكم وعليهم. وفي هذا أن الله أخذ الميثاق على كل نبي أن يؤمن بمحمد صلى الله عليه وسلم، وأخذ الميثاق على أمم الأنبياء بذلك.');
INSERT INTO Quran__Arabic_Tafseer_Muyassar (QuranChapterID, VerseNumber, Rukuh, Sajdah, Manzil, HizbQuarter, VerseText) VALUES (3, 82, '0', '0', '0', '0', 'فمن أعرض عن دعوة الإسلام بعد هذا البيان وهذا العهد الذي أخذه الله على أنبيائه، فأولئك هم الخارجون عن دين الله وطاعة ربهم.');
INSERT INTO Quran__Arabic_Tafseer_Muyassar (QuranChapterID, VerseNumber, Rukuh, Sajdah, Manzil, HizbQuarter, VerseText) VALUES (3, 83, '0', '0', '0', '0', 'أيريد هؤلاء الفاسقون من أهل الكتاب غير دين الله -وهو الإسلام الذي بعث الله به محمدا صلى الله عليه وسلم-، مع أن كل مَن في السموات والأرض استسلم وانقاد وخضع لله طواعية -كالمؤمنين- ورغمًا عنهم عند الشدائد، حين لا ينفعهم ذلك وهم الكفار، كما خضع له سائر الكائنات، وإليه يُرجَعون يوم المعاد، فيجازي كلا بعمله. وهذا تحذير من الله تعالى لخلقه أن يرجع إليه أحد منهم على غير ملة الإسلام.');
INSERT INTO Quran__Arabic_Tafseer_Muyassar (QuranChapterID, VerseNumber, Rukuh, Sajdah, Manzil, HizbQuarter, VerseText) VALUES (3, 84, '0', '0', '0', '0', 'قل لهم -أيها الرسول-: صدَّقنا بالله وأطعنا، فلا رب لنا غيره، ولا معبود لنا سواه، وآمنَّا بالوحي الذي أنزله الله علينا، والذي أنزله على إبراهيم خليل الله، وابنيه إسماعبل وإسحاق، وابن ابنه يعقوب بن إسحاق، والذي أنزله على الأسباط -وهم الأنبياء الذين كانوا في قبائل بني إسرائيل الاثنتي عشرة مِن ولد يعقوب- وما أوتي موسى وعيسى من التوراة والإنجيل، وما أنزله الله على أنبيائه، نؤمن بذلك كله، ولا نفرق بين أحد منهم، ونحن لله وحده منقادون بالطاعة، مُقِرُّون له بالربوبية والألوهية والعبادة.');
INSERT INTO Quran__Arabic_Tafseer_Muyassar (QuranChapterID, VerseNumber, Rukuh, Sajdah, Manzil, HizbQuarter, VerseText) VALUES (3, 85, '0', '0', '0', '0', 'ومن يطلب دينًا غير دين الإسلام الذي هو الاستسلام لله بالتوحيد والانقياد له بالطاعة، والعبودية، ولرسوله النبي الخاتم محمد صلى الله عليه وسلم بالإيمان به وبمتابعته ومحبته ظاهرًا وباطنًا، فلن يُقبل منه ذلك، وهو في الآخرة من الخاسرين الذين بخسوا أنفسهم حظوظها.');
INSERT INTO Quran__Arabic_Tafseer_Muyassar (QuranChapterID, VerseNumber, Rukuh, Sajdah, Manzil, HizbQuarter, VerseText) VALUES (3, 86, '0', '0', '0', '0', 'كيف يوفق الله للإيمان به وبرسوله قومًا جحدوا نبوة محمد صلى الله عليه وسلم بعد إيمانهم به، وشهدوا أن محمدًا صلى الله عليه وسلم حق وما جاء به هو الحق، وجاءهم الحجج من عند الله والدلائل بصحة ذلك؟ والله لا يوفق للحق والصواب الجماعة الظلمة، وهم الذين عدلوا عن الحق إلى الباطل، فاختاروا الكفر على الإيمان.');
INSERT INTO Quran__Arabic_Tafseer_Muyassar (QuranChapterID, VerseNumber, Rukuh, Sajdah, Manzil, HizbQuarter, VerseText) VALUES (3, 87, '0', '0', '0', '0', 'أولئك الظالمون جزاؤهم أنَّ عليهم لعنة الله والملائكة والناسِ أجمعين، فهم مطرودون من رحمة الله.');
INSERT INTO Quran__Arabic_Tafseer_Muyassar (QuranChapterID, VerseNumber, Rukuh, Sajdah, Manzil, HizbQuarter, VerseText) VALUES (3, 88, '0', '0', '0', '0', 'ماكثين في النار، لا يُرفع عنهم العذاب قليلا ليستريحوا، ولا يُؤخر عنهم لمعذرة يعتذرون بها.');
INSERT INTO Quran__Arabic_Tafseer_Muyassar (QuranChapterID, VerseNumber, Rukuh, Sajdah, Manzil, HizbQuarter, VerseText) VALUES (3, 89, '0', '0', '0', '0', 'إلا الذين رجعوا إلى ربهم بالتوبة النصوح من بعد كفرهم وظلمهم، وأصلحوا ما أفسدوه بتوبتهم فإن الله يقبلها، فهو غفور لذنوب عباده، رحيم بهم.');
INSERT INTO Quran__Arabic_Tafseer_Muyassar (QuranChapterID, VerseNumber, Rukuh, Sajdah, Manzil, HizbQuarter, VerseText) VALUES (3, 90, '0', '0', '0', '0', 'إن الذين كفروا بعد إيمانهم واستمروا على الكفر إلى الممات لن تُقبل لهم توبة عند حضور الموت، وأولئك هم الذين ضلُّوا السبيل، فأخطَؤُوا منهجه.');
INSERT INTO Quran__Arabic_Tafseer_Muyassar (QuranChapterID, VerseNumber, Rukuh, Sajdah, Manzil, HizbQuarter, VerseText) VALUES (3, 91, '1', '0', '0', '0', 'إن الذين جحدوا نبوة محمد صلى الله عليه وسلم، وماتوا على الكفر بالله ورسوله، فلن يُقبل من أحدهم يوم القيامة ملء الأرض ذهبًا؛ ليفتدي به نفسه من عذاب الله، ولو افتدى به نفسه فِعْلا. أولئك لهم عذاب موجع، وما لهم من أحد ينقذهم من عذاب الله.');
INSERT INTO Quran__Arabic_Tafseer_Muyassar (QuranChapterID, VerseNumber, Rukuh, Sajdah, Manzil, HizbQuarter, VerseText) VALUES (3, 92, '0', '0', '0', '0', 'لن تدركوا الجنة حتى تتصدقوا مما تحبون، وأي شيء تتصدقوا به مهما كان قليلا أو كثيرًا فإن الله به عليم، وسيجازي كل منفق بحسب عمله.');
INSERT INTO Quran__Arabic_Tafseer_Muyassar (QuranChapterID, VerseNumber, Rukuh, Sajdah, Manzil, HizbQuarter, VerseText) VALUES (3, 93, '0', '0', '0', '1', 'كل الأطعمة الطيِّبة كانت حلالا لأبناء يعقوب عليه السلام إلا ما حرَّم يعقوب على نفسه لمرض نزل به، وذلك مِن قبل أن تُنَزَّل التوراة. فلما نُزِّلت التوراة حرَّم الله على بني إسرائيل بعض الأطعمة التي كانت حلالا لهم؛ وذلك لظلمهم وبغيهم. قل لهم -أيها الرسول-: هاتوا التوراة، واقرؤوا ما فيها إن كنتم محقين في دعواكم أن الله أنزل فيها تحريم ما حرَّمه يعقوب على نفسه، حتى تعلموا صدق ما جاء في القرآن من أن الله لم يحرم على بني إسرائيل شيئًا من قبل نزول التوراة، إلا ما حرَّمه يعقوب على نفسه.');
INSERT INTO Quran__Arabic_Tafseer_Muyassar (QuranChapterID, VerseNumber, Rukuh, Sajdah, Manzil, HizbQuarter, VerseText) VALUES (3, 94, '0', '0', '0', '0', 'فمَن كذب على الله من بعد قراءة التوراة ووضوح الحقيقة، فأولئك هم الظالمون القائلون على الله بالباطل.');
INSERT INTO Quran__Arabic_Tafseer_Muyassar (QuranChapterID, VerseNumber, Rukuh, Sajdah, Manzil, HizbQuarter, VerseText) VALUES (3, 95, '0', '0', '0', '0', 'قل لهم -أيها الرسول- صَدَق الله فيما أخبر به وفيما شرعه. فإن كنتم صادقين في محبتكم وانتسابكم لخليل الله إبراهيم عليه السلام فاتبعوا ملَّته التي شرعها الله على لسان محمد صلى الله عليه وسلم، فإنها الحق الذي لا شك فيه. وما كان إبراهيم عليه السلام من المشركين بالله في توحيده وعبادته أحدًا.');
INSERT INTO Quran__Arabic_Tafseer_Muyassar (QuranChapterID, VerseNumber, Rukuh, Sajdah, Manzil, HizbQuarter, VerseText) VALUES (3, 96, '0', '0', '0', '0', 'إن أول بيت بُني لعبادة الله في الأرض لهو بيت الله الحرام الذي في "مكة"، وهذا البيت مبارك تضاعف فيه الحسنات، وتتنزل فيه الرحمات، وفي استقباله في الصلاة، وقصده لأداء الحج والعمرة، صلاح وهداية للناس أجمعين.');
INSERT INTO Quran__Arabic_Tafseer_Muyassar (QuranChapterID, VerseNumber, Rukuh, Sajdah, Manzil, HizbQuarter, VerseText) VALUES (3, 97, '0', '0', '0', '0', 'في هذا البيت دلالات ظاهرات أنه من بناء إبراهيم، وأن الله عظَّمه وشرَّفه، منها: مقام إبراهيم عليه السلام، وهو الحَجَر الذي كان يقف عليه حين كان يرفع القواعد من البيت هو وابنه إسماعيل، ومن دخل هذا البيت أَمِنَ على نفسه فلا يناله أحد بسوء. وقد أوجب الله على المستطيع من الناس في أي مكان قَصْدَ هذا البيت لأداء مناسك الحج. ومن جحد فريضة الحج فقد كفر، والله غني عنه وعن حجِّه وعمله، وعن سائر خَلْقه.');
INSERT INTO Quran__Arabic_Tafseer_Muyassar (QuranChapterID, VerseNumber, Rukuh, Sajdah, Manzil, HizbQuarter, VerseText) VALUES (3, 98, '0', '0', '0', '0', 'قل -أيها الرسول- لأهل الكتاب من اليهود والنصارى: لِمَ تجحدون حجج الله التي دلَّتْ على أن دين الله هو الإسلام، وتنكرون ما في كتبهم من دلائل وبراهين على ذلك، وأنتم تعلمون؟ والله شهيد على صنيعكم. وفي ذلك تهديد ووعيد لهم.');
INSERT INTO Quran__Arabic_Tafseer_Muyassar (QuranChapterID, VerseNumber, Rukuh, Sajdah, Manzil, HizbQuarter, VerseText) VALUES (3, 99, '0', '0', '0', '0', 'قل -أيها الرسول- لليهود والنصارى: لِمَ تمنعون من الإسلام من يريد الدخول فيه تطلبون له زيغًا وميلا عن القصد والاستقامة، وأنتم تعلمون أن ما جئتُ به هو الحق؟ وما الله بغافل عما تعملون، وسوف يجازيكم على ذلك.');
INSERT INTO Quran__Arabic_Tafseer_Muyassar (QuranChapterID, VerseNumber, Rukuh, Sajdah, Manzil, HizbQuarter, VerseText) VALUES (3, 100, '0', '0', '0', '0', 'يا أيها الذين صدَّقوا الله ورسوله وعملوا بشرعه، إن تطيعوا جماعة من اليهود والنصارى ممن آتاهم الله التوراة والإنجيل، يضلوكم، ويلقوا إليكم الشُّبَه في دينكم؛ لترجعوا جاحدين للحق بعد أن كنتم مؤمنين به، فلا تأمنوهم على دينكم، ولا تقبلوا لهم رأيًا أو مشورة.');
INSERT INTO Quran__Arabic_Tafseer_Muyassar (QuranChapterID, VerseNumber, Rukuh, Sajdah, Manzil, HizbQuarter, VerseText) VALUES (3, 101, '1', '0', '0', '0', 'وكيف تكفرون بالله -أيها المؤمنون -، وآيات القرآن تتلى عليكم، وفيكم رسول الله محمد صلى الله عليه وسلم يبلغها لكم؟ ومَن يتوكل على الله ويستمسك بالقرآن والسنة فقد وُفِّق لطريق واضح، ومنهاج مستقيم.');
INSERT INTO Quran__Arabic_Tafseer_Muyassar (QuranChapterID, VerseNumber, Rukuh, Sajdah, Manzil, HizbQuarter, VerseText) VALUES (3, 102, '0', '0', '0', '0', 'يا أيها الذين صدَّقوا الله ورسوله، وعملوا بشرعه، خافوا الله حق خوفه: وذلك بأن يطاع فلا يُعصى، ويُشكَر فلا يكفر، ويُذكَر فلا ينسى، وداوموا على تمسككم بإسلامكم إلى آخر حياتكم؛ لتلقوا الله وأنتم عليه.');
INSERT INTO Quran__Arabic_Tafseer_Muyassar (QuranChapterID, VerseNumber, Rukuh, Sajdah, Manzil, HizbQuarter, VerseText) VALUES (3, 103, '0', '0', '0', '0', 'وتمسَّكوا جميعًا بكتاب ربكم وهدي نبيكم، ولا تفعلوا ما يؤدي إلى فرقتكم. واذكروا نعمة جليلة أنعم الله بها عليكم: إذ كنتم -أيها المؤمنون- قبل الإسلام أعداء، فجمع الله قلوبكم على محبته ومحبة رسوله، وألقى في قلوبكم محبة بعضكم لبعض، فأصبحتم -بفضله- إخوانا متحابين، وكنتم على حافة نار جهنم، فهداكم الله بالإسلام ونجَّاكم من النار. وكما بيَّن الله لكم معالم الإيمان الصحيح فكذلك يبيِّن لكم كل ما فيه صلاحكم؛ لتهتدوا إلى سبيل الرشاد، وتسلكوها، فلا تضلوا عنها.');
INSERT INTO Quran__Arabic_Tafseer_Muyassar (QuranChapterID, VerseNumber, Rukuh, Sajdah, Manzil, HizbQuarter, VerseText) VALUES (3, 104, '0', '0', '0', '0', 'ولتكن منكم -أيها المؤمنون- جماعة تدعو إلى الخير وتأمر بالمعروف، وهو ما عُرف حسنه شرعًا وعقلا وتنهى عن المنكر، وهو ما عُرف قبحه شرعًا وعقلا وأولئك هم الفائزون بجنات النعيم.');
INSERT INTO Quran__Arabic_Tafseer_Muyassar (QuranChapterID, VerseNumber, Rukuh, Sajdah, Manzil, HizbQuarter, VerseText) VALUES (3, 105, '0', '0', '0', '0', 'ولا تكونوا -أيها المؤمنون- كأهل الكتاب الذين وقعت بينهم العداوة والبغضاء فتفرَّقوا شيعًا وأحزابًا، واختلفوا في أصول دينهم من بعد أن اتضح لهم الحق، وأولئك مستحقون لعذابٍ عظيم موجع.');
INSERT INTO Quran__Arabic_Tafseer_Muyassar (QuranChapterID, VerseNumber, Rukuh, Sajdah, Manzil, HizbQuarter, VerseText) VALUES (3, 106, '0', '0', '0', '0', 'يوم القيامة تَبْيَضُّ وجوه أهل السعادة الذين آمنوا بالله ورسوله، وامتثلوا أمره، وتَسْوَدُّ وجوه أهل الشقاوة ممن كذبوا رسوله، وعصوا أمره. فأما الذين اسودَّت وجوههم، فيقال لهم توبيخًا: أكفرتم بعد إيمانكم، فاخترتم الكفر على الإيمان؟ فذوقوا العذاب بسبب كفركم.');
INSERT INTO Quran__Arabic_Tafseer_Muyassar (QuranChapterID, VerseNumber, Rukuh, Sajdah, Manzil, HizbQuarter, VerseText) VALUES (3, 107, '0', '0', '0', '0', 'وأما الذين ابيضَّتْ وجوهم بنضرة النعيم، وما بُشِّروا به من الخير، فهم في جنة الله ونعيمها، وهم باقون فيها، لا يخرجون منها أبدًا.');
INSERT INTO Quran__Arabic_Tafseer_Muyassar (QuranChapterID, VerseNumber, Rukuh, Sajdah, Manzil, HizbQuarter, VerseText) VALUES (3, 108, '0', '0', '0', '0', 'هذه آيات الله وبراهينه الساطعة، نتلوها ونقصُّها عليك -أيها الرسول- بالصدق واليقين. وما الله بظالم أحدًا من خلقه، ولا بمنقص شيئًا من أعمالهم؛ لأنه الحاكم العدل الذي لا يجور.');
INSERT INTO Quran__Arabic_Tafseer_Muyassar (QuranChapterID, VerseNumber, Rukuh, Sajdah, Manzil, HizbQuarter, VerseText) VALUES (3, 109, '1', '0', '0', '0', 'ولله ما في السموات وما في الأرض، ملكٌ له وحده خلقًا وتدبيرًا، ومصير جميع الخلائق إليه وحده، فيجازي كلا على قدر استحقاقه.');
INSERT INTO Quran__Arabic_Tafseer_Muyassar (QuranChapterID, VerseNumber, Rukuh, Sajdah, Manzil, HizbQuarter, VerseText) VALUES (3, 110, '0', '0', '0', '0', 'أنتم - يا أمة محمد - خير الأمم وأنفع الناس للناس، تأمرون بالمعروف، وهو ما عُرف حسنه شرعًا وعقلا وتنهون عن المنكر، وهو ما عُرف قبحه شرعًا وعقلا وتصدقون بالله تصديقًا جازمًا يؤيده العمل. ولو آمن أهل الكتاب من اليهود والنصارى بمحمد صلى الله عليه وسلم وما جاءهم به من عند الله كما آمنتم، لكان خيرا لهم في الدنيا والآخرة، منهم المؤمنون المصدقون برسالة محمد صلى الله عليه وسلم العاملون بها، وهم قليل، وأكثرهم الخارجون عن دين الله وطاعته.');
INSERT INTO Quran__Arabic_Tafseer_Muyassar (QuranChapterID, VerseNumber, Rukuh, Sajdah, Manzil, HizbQuarter, VerseText) VALUES (3, 111, '0', '0', '0', '0', 'لن يضركم هؤلاء الفاسقون من أهل الكتاب إلا ما يؤذي أسماعكم من ألفاظ الشرك والكفر وغير ذلك، فإن يقاتلوكم يُهْزَموا، ويهربوا مولِّين الأدبار، ثم لا ينصرون عليكم بأي حال.');
INSERT INTO Quran__Arabic_Tafseer_Muyassar (QuranChapterID, VerseNumber, Rukuh, Sajdah, Manzil, HizbQuarter, VerseText) VALUES (3, 112, '0', '0', '0', '0', 'جعل الله الهوان والصغار أمرًا لازمًا لا يفارق اليهود، فهم أذلاء محتقرون أينما وُجِدوا، إلا بعهد من الله وعهد من الناس يأمنون به على أنفسهم وأموالهم، وذلك هو عقد الذمة لهم وإلزامهم أحكام الإسلام، ورجعوا بغضب من الله مستحقين له، وضُربت عليهم الذلَّة والمسكنة، فلا ترى اليهوديَّ إلا وعليه الخوف والرعب من أهل الإيمان؛ ذلك الذي جعله الله عليهم بسبب كفرهم بالله، وتجاوزهم حدوده، وقَتْلهم الأنبياء ظلمًا واعتداء، وما جرَّأهم على هذا إلا ارتكابهم للمعاصي، وتجاوزهم حدود الله.');
INSERT INTO Quran__Arabic_Tafseer_Muyassar (QuranChapterID, VerseNumber, Rukuh, Sajdah, Manzil, HizbQuarter, VerseText) VALUES (3, 113, '0', '0', '0', '1', 'ليس أهل الكتاب متساوين: فمنهم جماعة مستقيمة على أمر الله مؤمنة برسوله محمد صلى الله عليه وسلم، يقومون الليل مرتلين آيات القرآن الكريم، مقبلين على مناجاة الله في صلواتهم.');
INSERT INTO Quran__Arabic_Tafseer_Muyassar (QuranChapterID, VerseNumber, Rukuh, Sajdah, Manzil, HizbQuarter, VerseText) VALUES (3, 114, '0', '0', '0', '0', 'يؤمنون بالله واليوم الآخر، ويأمرون بالخير كله، وينهون عن الشر كلِّه، ويبادرون إلى فعل الخيرات، وأولئك مِن عباد الله الصالحين.');
INSERT INTO Quran__Arabic_Tafseer_Muyassar (QuranChapterID, VerseNumber, Rukuh, Sajdah, Manzil, HizbQuarter, VerseText) VALUES (3, 115, '0', '0', '0', '0', 'وأيُّ عمل قلَّ أو كَثُر من أعمال الخير تعمله هذه الطائفة المؤمنة فلن يضيع عند الله، بل يُشكر لهم، ويجازون عليه. والله عليم بالمتقين الذين فعلوا الخيرات وابتعدوا عن المحرمات؛ ابتغاء رضوان الله، وطلبًا لثوابه.');
INSERT INTO Quran__Arabic_Tafseer_Muyassar (QuranChapterID, VerseNumber, Rukuh, Sajdah, Manzil, HizbQuarter, VerseText) VALUES (3, 116, '0', '0', '0', '0', 'إن الذين كفروا بآيات الله، وكذبوا رسله، لن تدفع عنهم أموالهم ولا أولادهم شيئًا من عذاب الله في الدنيا ولا في الآخرة، وأولئك أصحاب النار الملازمون لها، لا يخرجون منها.');
INSERT INTO Quran__Arabic_Tafseer_Muyassar (QuranChapterID, VerseNumber, Rukuh, Sajdah, Manzil, HizbQuarter, VerseText) VALUES (3, 117, '0', '0', '0', '0', 'مَثَلُ ما ينفق الكافرون في وجوه الخير في هذه الحياة الدنيا وما يؤملونه من ثواب، كمثل ريح فيها برد شديد هَبَّتْ على زرع قوم كانوا يرجون خيره، وبسبب ذنوبهم لم تُبْقِ الريح منه شيئًا. وهؤلاء الكافرون لا يجدون في الآخرة ثوابًا، وما ظلمهم الله بذلك، ولكنهم ظلموا أنفسهم بكفرهم وعصيانهم.');
INSERT INTO Quran__Arabic_Tafseer_Muyassar (QuranChapterID, VerseNumber, Rukuh, Sajdah, Manzil, HizbQuarter, VerseText) VALUES (3, 118, '0', '0', '0', '0', 'يا أيها الذين صدَّقوا الله ورسوله وعملوا بشرعه، لا تتخذوا الكافرين أولياء من دون المؤمنين، تُطْلعونهم على أسراركم، فهؤلاء لا يَفْتُرون عن إفساد حالكم، وهم يفرحون بما يصيبكم من ضرر ومكروه، وقد ظهرت شدة البغض في كلامهم، وما تخفي صدورهم من العداوة لكم أكبر وأعظم. قد بيَّنَّا لكم البراهين والحجج، لتتعظوا وتحذروا، إن كنتم تعقلون عن الله مواعظه وأمره ونهيه.');
INSERT INTO Quran__Arabic_Tafseer_Muyassar (QuranChapterID, VerseNumber, Rukuh, Sajdah, Manzil, HizbQuarter, VerseText) VALUES (3, 119, '0', '0', '0', '0', 'ها هوذا الدليل على خطئكم في محبتهم، فأنتم تحبونهم وتحسنون إليهم، وهم لا يحبونكم ويحملون لكم العداوة والبغضاء، وأنتم تؤمنون بالكتب المنزلة كلها ومنها كتابهم، وهم لا يؤمنون بكتابكم، فكيف تحبونهم؟ وإذا لقوكم قالوا -نفاقًا-: آمنَّا وصدَّقْنا، وإذا خلا بعضهم إلى بعض بدا عليهم الغم والحزن، فعَضُّوا أطراف أصابعهم من شدة الغضب، لما يرون من ألفة المسلمين واجتماع كلمتهم، وإعزاز الإسلام، وإذلالهم به. قل لهم -أيها الرسول-: موتوا بشدة غضبكم. إن الله مطَّلِع على ما تخفي الصدور، وسيجازي كلا على ما قدَّم مِن خير أو شر.');
INSERT INTO Quran__Arabic_Tafseer_Muyassar (QuranChapterID, VerseNumber, Rukuh, Sajdah, Manzil, HizbQuarter, VerseText) VALUES (3, 120, '1', '0', '0', '0', 'ومن عداوة هؤلاء أنكم -أيها المؤمنون- إن نزل بكم أمرٌ حسن مِن نصر وغنيمة ظهرت عليهم الكآبة والحزن، وإن وقع بكم مكروه من هزيمة أو نقص في الأموال والأنفس والثمرات فرحوا بذلك، وإن تصبروا على ما أصابكم، وتتقوا الله فيما أمركم به ونهاكم عنه، لا يضركم أذى مكرهم. والله بجميع ما يعمل هؤلاء الكفار من الفساد محيط، وسيجازيهم على ذلك.');
INSERT INTO Quran__Arabic_Tafseer_Muyassar (QuranChapterID, VerseNumber, Rukuh, Sajdah, Manzil, HizbQuarter, VerseText) VALUES (3, 121, '0', '0', '0', '0', 'واذكر -أيها الرسول- حين خَرَجْتَ من بيتك لابسًا عُدَّة الحرب، تنظم صفوف أصحابك، وتُنْزِل كل واحد في منزله للقاء المشركين في غزوة "أُحُد". والله سميع لأقوالكم، عليم بأفعالكم.');
INSERT INTO Quran__Arabic_Tafseer_Muyassar (QuranChapterID, VerseNumber, Rukuh, Sajdah, Manzil, HizbQuarter, VerseText) VALUES (3, 122, '0', '0', '0', '0', 'اذكر -أيها الرسول- ما كان من أمر بني سَلِمة وبني حارثة حين حدثتهم أنفسهم بالرجوع مع زعيمهم المنافق عبد الله بن أُبيٍّ؛ خوفًا من لقاء العدو، ولكن الله عصمهم وحفظهم، فساروا معك متوكلين على الله. وعلى الله وحده فليتوكل المؤمنون.');
INSERT INTO Quran__Arabic_Tafseer_Muyassar (QuranChapterID, VerseNumber, Rukuh, Sajdah, Manzil, HizbQuarter, VerseText) VALUES (3, 123, '0', '0', '0', '0', 'ولقد نصركم الله -أيها المؤمنون- بـ "بدر" على أعدائكم المشركين مع قلة عَدَدكم وعُدَدكم، فخافوا الله بفعل أوامره واجتناب نواهيه؛ لعلكم تشكرون له نعمه.');
INSERT INTO Quran__Arabic_Tafseer_Muyassar (QuranChapterID, VerseNumber, Rukuh, Sajdah, Manzil, HizbQuarter, VerseText) VALUES (3, 124, '0', '0', '0', '0', 'اذكر -أيها الرسول- ما كان من أمر أصحابك في "بدر" حين شقَّ عليهم أن يأتي مَدَد للمشركين، فأوحينا إليك أن تقول لهم: ألن تكفيكم معونة ربكم بأن يمدكم بثلاثة آلاف من الملائكة مُنْزَلين من السماء إلى أرض المعركة، يثبتونكم، ويقاتلون معكم؟');
INSERT INTO Quran__Arabic_Tafseer_Muyassar (QuranChapterID, VerseNumber, Rukuh, Sajdah, Manzil, HizbQuarter, VerseText) VALUES (3, 125, '0', '0', '0', '0', 'بلى يكفيكم هذا المَدَد. وبشارة أخرى لكم: إن تصبروا على لقاء العدو وتتقوا الله بفِعْل ما أمركم به واجتناب ما نهاكم عنه، ويأت كفار "مكة" على الفور مسرعين لقتالكم، يظنون أنهم يستأصلونكم، فإن الله يمدكم بخمسة آلاف من الملائكة مسوِّمين أي: قد أعلموا أنفسهم وخيولهم بعلامات واضحات.');
INSERT INTO Quran__Arabic_Tafseer_Muyassar (QuranChapterID, VerseNumber, Rukuh, Sajdah, Manzil, HizbQuarter, VerseText) VALUES (3, 126, '0', '0', '0', '0', 'وما جعل الله هذا الإمداد بالملائكة إلا بشرى لكم يبشركم بها ولتطمئن قلوبكم، وتطيب بوعد الله لكم. وما النصر إلا من عند الله العزيز الذي لا يغالَب، الحكيم في تدبيره وفعله.');
INSERT INTO Quran__Arabic_Tafseer_Muyassar (QuranChapterID, VerseNumber, Rukuh, Sajdah, Manzil, HizbQuarter, VerseText) VALUES (3, 127, '0', '0', '0', '0', 'وكان نصر الله لكم بـ "بدْر" ليهلك فريقًا من الكفار بالقتل، ومن نجا منهم من القتل رجع حزينًا قد ضاقت عليه نفسه، يَظْهر عليه الخزي والعار.');
INSERT INTO Quran__Arabic_Tafseer_Muyassar (QuranChapterID, VerseNumber, Rukuh, Sajdah, Manzil, HizbQuarter, VerseText) VALUES (3, 128, '0', '0', '0', '0', 'ليس لك -أيها الرسول- من أمر العباد شيء، بل الأمر كله لله تعالى وحده لا شريك له، ولعل بعض هؤلاء الذين قاتلوك تنشرح صدورهم للإسلام فيسلموا، فيتوب الله عليهم. ومن بقي على كفره يعذبه الله في الدنيا والآخرة بسبب ظلمه وبغيه.');
INSERT INTO Quran__Arabic_Tafseer_Muyassar (QuranChapterID, VerseNumber, Rukuh, Sajdah, Manzil, HizbQuarter, VerseText) VALUES (3, 129, '1', '0', '0', '0', 'ولله وحده ما في السموات وما في الأرض، يغفر لمن يشاء من عباده برحمته، ويعذب من يشاء بعدله. والله غفور لذنوب عباده، رحيم بهم.');
INSERT INTO Quran__Arabic_Tafseer_Muyassar (QuranChapterID, VerseNumber, Rukuh, Sajdah, Manzil, HizbQuarter, VerseText) VALUES (3, 130, '0', '0', '0', '0', 'يا أيها الذين صدّقوا الله ورسوله وعملوا بشرعه احذروا الربا بجميع أنواعه، ولا تأخذوا في القرض زيادة على رؤوس أموالكم وإن قلَّت، فكيف إذا كانت هذه الزيادة تتضاعف كلما حان موعد سداد الدين؟ واتقوا الله بالتزام شرعه؛ لتفوزوا في الدنيا والآخرة.');
INSERT INTO Quran__Arabic_Tafseer_Muyassar (QuranChapterID, VerseNumber, Rukuh, Sajdah, Manzil, HizbQuarter, VerseText) VALUES (3, 131, '0', '0', '0', '0', 'اجعلوا لأنفسكم وقاية بينكم وبين النار التي هُيِّئت للكافرين.');
INSERT INTO Quran__Arabic_Tafseer_Muyassar (QuranChapterID, VerseNumber, Rukuh, Sajdah, Manzil, HizbQuarter, VerseText) VALUES (3, 132, '0', '0', '0', '0', 'وأطيعوا الله -أيها المؤمنون- فيما أمركم به من الطاعات وفيما نهاكم عنه من أكل الربا وغيره من الأشياء، وأطيعوا الرسول؛ لترحموا، فلا تعذبوا.');
INSERT INTO Quran__Arabic_Tafseer_Muyassar (QuranChapterID, VerseNumber, Rukuh, Sajdah, Manzil, HizbQuarter, VerseText) VALUES (3, 133, '0', '0', '0', '1', 'وبادروا بطاعتكم لله ورسوله لاغتنام مغفرة عظيمة من ربكم وجنة واسعة، عرضها السموات والأرض، أعدها الله للمتقين.');
INSERT INTO Quran__Arabic_Tafseer_Muyassar (QuranChapterID, VerseNumber, Rukuh, Sajdah, Manzil, HizbQuarter, VerseText) VALUES (3, 134, '0', '0', '0', '0', 'الذين ينفقون أموالهم في اليسر والعسر، والذين يمسكون ما في أنفسهم من الغيظ بالصبر، وإذا قَدَروا عَفَوا عمَّن ظلمهم. وهذا هو الإحسان الذي يحب الله أصحابه.');
INSERT INTO Quran__Arabic_Tafseer_Muyassar (QuranChapterID, VerseNumber, Rukuh, Sajdah, Manzil, HizbQuarter, VerseText) VALUES (3, 135, '0', '0', '0', '0', 'والذين إذا ارتكبوا ذنبًا كبيرًا أو ظلموا أنفسهم بارتكاب ما دونه، ذكروا وعد الله ووعيده فلجأوا إلى ربهم تائبين، يطلبون منه أن يغفر لهم ذنوبهم، وهم موقنون أنه لا يغفر الذنوب إلا الله، فهم لذلك لا يقيمون على معصية، وهم يعلمون أنهم إن تابوا تاب الله عليهم.');
INSERT INTO Quran__Arabic_Tafseer_Muyassar (QuranChapterID, VerseNumber, Rukuh, Sajdah, Manzil, HizbQuarter, VerseText) VALUES (3, 136, '0', '0', '0', '0', 'أولئك الموصوفون بتلك الصفات العظيمة جزاؤهم أن يستر الله ذنوبهم، ولهم جنات تجري من تحت أشجارها وقصورها المياه العذبة، خالدين فيها لا يخرجون منها أبدًا. ونِعْمَ أجر العاملين المغفرة والجنة.');
INSERT INTO Quran__Arabic_Tafseer_Muyassar (QuranChapterID, VerseNumber, Rukuh, Sajdah, Manzil, HizbQuarter, VerseText) VALUES (3, 137, '0', '0', '0', '0', 'يخاطب الله المؤمنين لـمَّا أُصيبوا يوم "أُحد" تعزية لهم بأنه قد مضت من قبلكم أمم، ابتُلي المؤمنون منهم بقتال الكافرين فكانت العاقبة لهم، فسيروا في الأرض معتبرين بما آل إليه أمر أولئك المكذبين بالله ورسله.');
INSERT INTO Quran__Arabic_Tafseer_Muyassar (QuranChapterID, VerseNumber, Rukuh, Sajdah, Manzil, HizbQuarter, VerseText) VALUES (3, 138, '0', '0', '0', '0', 'هذا القرآن بيان وإرشاد إلى طريق الحق، وتذكير تخشع له قلوب المتقين، وهم الذين يخشون الله، وخُصُّوا بذلك؛ لأنهم هم المنتفعون به دون غيرهم.');
INSERT INTO Quran__Arabic_Tafseer_Muyassar (QuranChapterID, VerseNumber, Rukuh, Sajdah, Manzil, HizbQuarter, VerseText) VALUES (3, 139, '0', '0', '0', '0', 'ولا تضْعُفوا -أيها المؤمنون- عن قتال عدوكم، ولا تحزنوا لما أصابكم في "أُحد"، وأنتم الغالبون والعاقبة لكم، إن كنتم مصدقين بالله ورسوله متَّبعين شرعه.');
INSERT INTO Quran__Arabic_Tafseer_Muyassar (QuranChapterID, VerseNumber, Rukuh, Sajdah, Manzil, HizbQuarter, VerseText) VALUES (3, 140, '0', '0', '0', '0', 'إن أصابتكم -أيها المؤمنون- جراح أو قتل في غزوة "أُحد" فحزنتم لذلك، فقد أصاب المشركين جراح وقتل مثل ذلك في غزوة "بدر". وتلك الأيام يُصَرِّفها الله بين الناس، نصر مرة وهزيمة أخرى، لما في ذلك من الحكمة، حتى يظهر ما علمه الله في الأزل ليميز الله المؤمن الصادق مِن غيره، ويُكْرِمَ أقوامًا منكم بالشهادة. والله لا يحب الذين ظلموا أنفسهم، وقعدوا عن القتال في سبيله.');
INSERT INTO Quran__Arabic_Tafseer_Muyassar (QuranChapterID, VerseNumber, Rukuh, Sajdah, Manzil, HizbQuarter, VerseText) VALUES (3, 141, '0', '0', '0', '0', 'وهذه الهزيمة التي وقعت في "أُحد" كانت اختبارًا وتصفية للمؤمنين، وتخليصًا لهم من المنافقين وهلاكًا للكافرين.');
INSERT INTO Quran__Arabic_Tafseer_Muyassar (QuranChapterID, VerseNumber, Rukuh, Sajdah, Manzil, HizbQuarter, VerseText) VALUES (3, 142, '0', '0', '0', '0', 'يا أصحاب محمد -صلى الله عليه وسلم- أظننتم أن تدخلوا الجنة، ولم تُبْتَلوا بالقتال والشدائد؟ لا يحصل لكم دخولها حتى تُبْتلوا، ويعلم الله -علما ظاهرا للخلق- المجاهدين منكم في سبيله، والصابرين على مقاومة الأعداء.');
INSERT INTO Quran__Arabic_Tafseer_Muyassar (QuranChapterID, VerseNumber, Rukuh, Sajdah, Manzil, HizbQuarter, VerseText) VALUES (3, 143, '1', '0', '0', '0', 'ولقد كنتم -أيها المؤمنون- قبل غزوة "أُحد" تتمنون لقاء العدو؛ لتنالوا شرف الجهاد والاستشهاد في سبيل الله الذي حَظِي به إخوانكم في غزوة "بدر"، فها هو ذا قد حصل لكم الذي تمنيتموه وطلبتموه، فدونكم فقاتلوا وصابروا.');
INSERT INTO Quran__Arabic_Tafseer_Muyassar (QuranChapterID, VerseNumber, Rukuh, Sajdah, Manzil, HizbQuarter, VerseText) VALUES (3, 144, '0', '0', '0', '0', 'وما محمد إلا رسول من جنس الرسل الذين قبله يبلغ رسالة ربه. أفإن مات بانقضاء أجله أو قُتِل كما أشاعه الأعداء رجعتم عن دينكم،، تركتم ما جاءكم به نبيكم؟ ومن يرجِعُ منكم عن دينه فلن يضر الله شيئًا، إنما يضر نفسه ضررًا عظيمًا. أما مَن ثبت على الإيمان وشكر ربه على نعمة الإسلام، فإن الله يجزيه أحسن الجزاء.');
INSERT INTO Quran__Arabic_Tafseer_Muyassar (QuranChapterID, VerseNumber, Rukuh, Sajdah, Manzil, HizbQuarter, VerseText) VALUES (3, 145, '0', '0', '0', '0', 'لن يموت أحد إلا بإذن الله وقدره وحتى يستوفي المدة التي قدرها الله له كتابًا مؤجَّلا. ومن يطلب بعمله عَرَض الدنيا، نعطه ما قسمناه له من رزق، ولا حظَّ له في الآخرة، ومن يطلب بعمله الجزاء من الله في الآخرة نمنحه ما طلبه، ونؤته جزاءه وافرًا مع ما لَه في الدنيا من رزق مقسوم، فهذا قد شَكَرَنا بطاعته وجهاده، وسنجزي الشاكرين خيرًا.');
INSERT INTO Quran__Arabic_Tafseer_Muyassar (QuranChapterID, VerseNumber, Rukuh, Sajdah, Manzil, HizbQuarter, VerseText) VALUES (3, 146, '0', '0', '0', '0', 'كثير من الأنبياء السابقين قاتل معهم جموع كثيرة من أصحابهم، فما ضعفوا لِمَا نزل بهم من جروح أو قتل؛ لأن ذلك في سبيل ربهم، وما عَجَزوا، ولا خضعوا لعدوهم، إنما صبروا على ما أصابهم. والله يحب الصابرين.');
INSERT INTO Quran__Arabic_Tafseer_Muyassar (QuranChapterID, VerseNumber, Rukuh, Sajdah, Manzil, HizbQuarter, VerseText) VALUES (3, 147, '0', '0', '0', '0', 'وما كان قول هؤلاء الصابرين إلا أن قالوا: ربنا اغفر لنا ذنوبنا، وما وقع منا مِن تجاوزٍ في أمر ديننا، وثبِّت أقدامنا حتى لا نفرَّ من قتال عدونا، وانصرنا على مَن جحد وحدانيتك ونبوة أنبيائك.');
INSERT INTO Quran__Arabic_Tafseer_Muyassar (QuranChapterID, VerseNumber, Rukuh, Sajdah, Manzil, HizbQuarter, VerseText) VALUES (3, 148, '1', '0', '0', '0', 'فأعطى الله أولئك الصابرين جزاءهم في الدنيا بالنصر على أعدائهم، وبالتمكين لهم في الأرض، وبالجزاء الحسن العظيم في الآخرة، وهو جنات النعيم. والله يحب كلَّ مَن أحسن عبادته لربه ومعاملته لخلقه.');
INSERT INTO Quran__Arabic_Tafseer_Muyassar (QuranChapterID, VerseNumber, Rukuh, Sajdah, Manzil, HizbQuarter, VerseText) VALUES (3, 149, '0', '0', '0', '0', 'يا أيها الذين صدَّقوا الله ورسوله وعملوا بشرعه، إن تطيعوا الذين جحدوا ألوهيتي، ولم يؤمنوا برسلي من اليهود والنصارى والمنافقين والمشركين فيما يأمرونكم به وينهونكم عنه، يضلوكم عن طريق الحق، وترتدُّوا عن دينكم، فتعودوا بالخسران المبين والهلاك المحقق.');
INSERT INTO Quran__Arabic_Tafseer_Muyassar (QuranChapterID, VerseNumber, Rukuh, Sajdah, Manzil, HizbQuarter, VerseText) VALUES (3, 150, '0', '0', '0', '0', 'إنهم لن ينصروكم، بل الله ناصركم، وهو خير ناصر، فلا يحتاج معه إلى نصرة أحد.');
INSERT INTO Quran__Arabic_Tafseer_Muyassar (QuranChapterID, VerseNumber, Rukuh, Sajdah, Manzil, HizbQuarter, VerseText) VALUES (3, 151, '0', '0', '0', '0', 'سنقذف في قلوب الذين كفروا أشدَّ الفزع والخوف بسبب إشراكهم بالله آلهة مزعومة، ليس لهم دليل أو برهان على استحقاقها للعبادة مع الله، فحالتهم في الدنيا: رعب وهلع من المؤمنين، أما مكانهم في الآخرة الذي يأوون إليه فهو النار؛ وذلك بسبب ظلمهم وعدوانهم، وساء هذا المقام مقامًا لهم.');
INSERT INTO Quran__Arabic_Tafseer_Muyassar (QuranChapterID, VerseNumber, Rukuh, Sajdah, Manzil, HizbQuarter, VerseText) VALUES (3, 152, '0', '0', '0', '0', 'ولقد حقق الله لكم ما وعدكم به من نصر، حين كنتم تقتلون الكفار في غزوة "أُحد" بإذنه تعالى، حتى إذا جَبُنتم وضعفتم عن القتال واختلفتم: هل تبقون في مواقعكم أو تتركونها لجمع الغنانم مع مَن يجمعها؟ وعصيتم أمر رسولكم حين أمركم ألا تفارفوا أماكنكم بأي حال، حلَّت بكم الهزيمة من بعد ما أراكم ما تحبون من النصر، وتبيَّن أن منكم مَن يريد الغنائم، وأن منكم مَن يطلب الآخرة وثوابها، ثم صرف الله وجوهكم عن عدوكم؛ ليختبركم، وقد علم الله ندمكم وتوبتكم فعفا عنكم، والله ذو فضل عظيم على المؤمنين.');
INSERT INTO Quran__Arabic_Tafseer_Muyassar (QuranChapterID, VerseNumber, Rukuh, Sajdah, Manzil, HizbQuarter, VerseText) VALUES (3, 153, '0', '0', '0', '1', 'اذكروا -يا أصحاب محمد- ما كان مِن أمركم حين أخذتم تصعدون الجبل هاربين من أعدائكم، ولا تلتفتون إلى أحد لِمَا اعتراكم من الدهشة والخوف والرعب، ورسول الله صلى الله عليه وسلم ثابت في الميدان يناديكم من خلفكم قائلا إليَّ عبادَ الله، وأنتم لا تسمعون ولا تنظرون، فكان جزاؤكم أن أنزل الله بكم ألمًا وضيقًا وغمًّا؛ لكي لا تحزنوا على ما فاتكم من نصر وغنيمة، ولا ما حلَّ بكم من خوف وهزيمة. والله خبير بجميع أعمالكم، لا يخفى عليه منها شيء.');
INSERT INTO Quran__Arabic_Tafseer_Muyassar (QuranChapterID, VerseNumber, Rukuh, Sajdah, Manzil, HizbQuarter, VerseText) VALUES (3, 154, '0', '0', '0', '0', 'ثم كان من رحمة الله بالمؤمنين المخلصين أن ألقى في قلوبهم من بعد ما نزل بها من همٍّ وغمٍّ اطمئنانًا وثقة في وعد الله، وكان من أثره نعاس غَشِي طائفة منهم، وهم أهل الإخلاص واليقين، وطائفة أُخرى أهمَّهم خلاص أنفسهم خاصة، وضَعُفَتْ عزيمتهم وشُغِلوا بأنفسهم، وأساؤوا الظن بربهم وبدينه وبنبيه، وظنوا أن الله لا يُتِمُّ أمر رسوله، وأن الإسلام لن تقوم له قائمة، ولذلك تراهم نادمين على خروجهم، يقول بعضهم لبعض: هل كان لنا من اختيار في الخروج للقتال؟ قل لهم -أيها الرسول-: إن الأمر كلَّه لله، فهو الذي قدَّر خروجكم وما حدث لكم، وهم يُخْفون في أنفسهم ما لا يظهرونه لك من الحسرة على خروجهم للقتال، يقولون: لو كان لنا أدنى اختيار ما قُتِلنا هاهنا. قل لهم: إن الآجال بيد الله، ولو كنتم في بيوتكم، وقدَّر الله أنكم تموتون، لخرج الذين كتب الله عليهم الموت إلى حيث يُقْتلون، وما جعل الله ذلك إلا ليختبر ما في صدوركم من الشك والنفاق، وليميز الخبيث من الطيب، ويظهر أمر المؤمن من المنافق للناس في الأقوال والأفعال. والله عليم بما في صدور خلقه، لا يخفى عليه شيء من أمورهم.');
INSERT INTO Quran__Arabic_Tafseer_Muyassar (QuranChapterID, VerseNumber, Rukuh, Sajdah, Manzil, HizbQuarter, VerseText) VALUES (3, 155, '1', '0', '0', '0', 'إن الذين فرُّوا منكم -يا أصحاب- محمد عن القتال يوم التقى المؤمنون والمشركون في غزوة "أُحد"، إنما أوقعهم الشيطان في هذا الذنب ببعض ما عملوا من الذنوب، ولقد تجاوز الله عنهم فلم يعاقبهم. إن الله غفور للمذنبين التائبين، حليم لا يعاجل من عصاه بالعقوبة.');
INSERT INTO Quran__Arabic_Tafseer_Muyassar (QuranChapterID, VerseNumber, Rukuh, Sajdah, Manzil, HizbQuarter, VerseText) VALUES (3, 156, '0', '0', '0', '0', 'يا أيها الذين صدَّقوا الله ورسوله وعملوا بشرعه، لا تُشابهوا الكافرين الذين لا يؤمنون بربهم، فهم يقولون لإخوانهم من أهل الكفر إذا خرجوا يبحثون في أرض الله عن معاشهم أو كانوا مع الغزاة المقاتلين فماتوا أو قُتِلوا: لو لم يخرج هؤلاء ولم يقاتلوا وأقاموا معنا ما ماتوا وما قُتلوا. وهذا القول يزيدهم ألمًا وحزنًا وحسرة تستقر في قلوبهم، أما المؤمنون فإنهم يعلمون أن ذلك بقدر الله فيهدي الله قلوبهم، ويخفف عنهم المصيبة، والله يحيي مَن قدَّر له الحياة -وإن كان مسافرًا أو غازيًا- ويميت مَنِ انتهى أجله -وإن كان مقيمًا- والله بكل ما تعملونه بصير، فيجازيكم به.');
INSERT INTO Quran__Arabic_Tafseer_Muyassar (QuranChapterID, VerseNumber, Rukuh, Sajdah, Manzil, HizbQuarter, VerseText) VALUES (3, 157, '0', '0', '0', '0', 'ولئن قُتِلتم -أيها المؤمنون- وأنتم تجاهدون في سبيل الله أو متم في أثناء القتال، ليغفرن الله لكم ذنوبكم، وليرحمنكم رحمة من عنده، فتفوزون بجنات النعيم، وذلك خير من الدنيا وما يجمعه أهلها.');
INSERT INTO Quran__Arabic_Tafseer_Muyassar (QuranChapterID, VerseNumber, Rukuh, Sajdah, Manzil, HizbQuarter, VerseText) VALUES (3, 158, '0', '0', '0', '0', 'ولئن انقضت آجالكم في هذه الحياة الدنيا، فمتم على فُرُشكم، أو قتلتم في ساحة القتال، لإلى الله وحده تُحشرون، فيجازيكم بأعمالكم.');
INSERT INTO Quran__Arabic_Tafseer_Muyassar (QuranChapterID, VerseNumber, Rukuh, Sajdah, Manzil, HizbQuarter, VerseText) VALUES (3, 159, '0', '0', '0', '0', 'فبرحمة من الله لك ولأصحابك -أيها النبي- منَّ الله عليك فكنت رفيقًا بهم، ولو كنت سيِّئ الخُلق قاسي القلب، لانْصَرَفَ أصحابك من حولك، فلا تؤاخذهم بما كان منهم في غزوة "أُحد"، واسأل الله -أيها النبي- أن يغفر لهم، وشاورهم في الأمور التي تحتاج إلى مشورة، فإذا عزمت على أمر من الأمور -بعد الاستشارة- فأَمْضِه معتمدًا على الله وحده، إن الله يحب المتوكلين عليه.');
INSERT INTO Quran__Arabic_Tafseer_Muyassar (QuranChapterID, VerseNumber, Rukuh, Sajdah, Manzil, HizbQuarter, VerseText) VALUES (3, 160, '0', '0', '0', '0', 'إن يمددكم الله بنصره ومعونته فلا أحد يستطيع أن يغلبكم، وإن يخذلكم فمن هذا الذي يستطيع أن ينصركم من بعد خذلانه لكم؟ وعلى الله وحده فليتوكل المؤمنون.');
INSERT INTO Quran__Arabic_Tafseer_Muyassar (QuranChapterID, VerseNumber, Rukuh, Sajdah, Manzil, HizbQuarter, VerseText) VALUES (3, 161, '0', '0', '0', '0', 'وما كان لنبيٍّ أن يَخُونَ أصحابه بأن يأخذ شيئًا من الغنيمة غير ما اختصه الله به، ومن يفعل ذلك منكم يأت بما أخذه حاملا له يوم القيامة؛ ليُفضَح به في الموقف المشهود، ثم تُعطى كل نفس جزاءَ ما كسبت وافيًا غير منقوص دون ظلم.');
INSERT INTO Quran__Arabic_Tafseer_Muyassar (QuranChapterID, VerseNumber, Rukuh, Sajdah, Manzil, HizbQuarter, VerseText) VALUES (3, 162, '0', '0', '0', '0', 'لا يستوي من كان قصده رضوان الله ومن هو مُكِبٌ على المعاصي، مسخط لربه، فاستحق بذلك سكن جهنم، وبئس المصير.');
INSERT INTO Quran__Arabic_Tafseer_Muyassar (QuranChapterID, VerseNumber, Rukuh, Sajdah, Manzil, HizbQuarter, VerseText) VALUES (3, 163, '0', '0', '0', '0', 'أصحاب الجنة المتبعون لما يرضي الله متفاوتون في الدرجات، وأصحاب النار المتبعون لما يسخط الله متفاوتون في الدركات، لا يستوون. والله بصير بأعمالهم لا يخفى عليه منها شيء.');
INSERT INTO Quran__Arabic_Tafseer_Muyassar (QuranChapterID, VerseNumber, Rukuh, Sajdah, Manzil, HizbQuarter, VerseText) VALUES (3, 164, '0', '0', '0', '0', 'لقد أنعم الله على المؤمنين من العرب؛ إذ بعث فيهم رسولا من أنفسهم، يتلو عليهم آيات القرآن، ويطهرهم من الشرك والأخلاق الفاسدة، ويعلمهم القرآن والسنة، وإن كانوا من قبل هذا الرسول لفي غيٍّ وجهل ظاهر.');
INSERT INTO Quran__Arabic_Tafseer_Muyassar (QuranChapterID, VerseNumber, Rukuh, Sajdah, Manzil, HizbQuarter, VerseText) VALUES (3, 165, '0', '0', '0', '0', 'أولما أصابتكم -أيها المؤمنون- مصيبة، وهي ما أُصيب منكم يوم "أُحد" قد أصبتم مثليها من المشركين في يوم "بدْر"، قلتم متعجبين: كيف يكون هذا ونحن مسلمون ورسول الله صلى الله عليه وسلم فينا وهؤلاء مشركون؟ قل لهم -أيها النبي-: هذا الذي أصابكم هو من عند أنفسكم بسبب مخالفتكم أمْرَ رسولكم وإقبالكم على جمع الغنائم. إن الله يفعل ما يشاء ويحكم ما يريد، لا معقِّب ‌‌‌‌لحكمه.');
INSERT INTO Quran__Arabic_Tafseer_Muyassar (QuranChapterID, VerseNumber, Rukuh, Sajdah, Manzil, HizbQuarter, VerseText) VALUES (3, 166, '0', '0', '0', '0', 'وما وقع بكم مِن جراح أو قتل في غزوة "أُحد" يوم التقى جَمْعُ المؤمنين وجمع المشركين فكان النصر للمؤمنين أولا ثم للمشركين ثانيًا، فذلك كله بقضاء الله وقدره، وليظهر ما علمه الله في الأزل؛ ليميز المؤمنين الصادقين منكم.');
INSERT INTO Quran__Arabic_Tafseer_Muyassar (QuranChapterID, VerseNumber, Rukuh, Sajdah, Manzil, HizbQuarter, VerseText) VALUES (3, 167, '0', '0', '0', '0', 'وليعلم المنافقين الذين كشف الله ما في قلوبهم حين قال المؤمنون لهم: تعالوا قاتلوا معنا في سبيل الله، أو كونوا عونًا لنا بتكثيركم سوادنا، فقالوا: لو نعلم أنكم تقاتلون أحدًا لكنا معكم عليهم، هم للكفر في هذا اليوم أقرب منهم للإيمان؛ لأنهم يقولون بأفواههم ما ليس في قلوبهم. والله أعلم بما يُخفون في صدورهم.');
INSERT INTO Quran__Arabic_Tafseer_Muyassar (QuranChapterID, VerseNumber, Rukuh, Sajdah, Manzil, HizbQuarter, VerseText) VALUES (3, 168, '0', '0', '0', '0', 'هؤلاء المنافقون هم الذين قعدوا وقالوا لإخوانهم الذين أصيبوا مع المسلمين في حربهم المشركين يوم "أُحد": لو أطاعَنا هؤلاء ما قتلوا. قل لهم -أيها الرسول-: فادفعوا عن أنفسكم الموت إن كنتم صادقين في دعواكم أنهم لو أطاعوكم ما قتلوا، وأنكم قد نجوتم منه بقعودكم عن القتال.');
INSERT INTO Quran__Arabic_Tafseer_Muyassar (QuranChapterID, VerseNumber, Rukuh, Sajdah, Manzil, HizbQuarter, VerseText) VALUES (3, 169, '0', '0', '0', '0', 'ولا تظنَّنَّ -أيها النبي- أن الذين قتلوا في سبيل الله أموات لا يُحِسُّون شيئًا، بل هم أحياء حياة برزخية في جوار ربهم الذي جاهدوا من أجله، وماتوا في سبيله، يجري عليهم رزقهم في الجنة، ويُنعَّمون.');
INSERT INTO Quran__Arabic_Tafseer_Muyassar (QuranChapterID, VerseNumber, Rukuh, Sajdah, Manzil, HizbQuarter, VerseText) VALUES (3, 170, '0', '0', '0', '0', 'لقد عَمَّتهم السعادة حين مَنَّ الله عليهم، فأعطاهم مِن عظيم جوده وواسع كرمه من النعيم والرضا ما تَقَرُّ به أعينهم، وهم يفرحون بإخوانهم المجاهدين الذين فارقوهم وهم أحياء؛ ليفوزوا كما فازوا، لِعِلْمِهم أنهم سينالون من الخير الذي نالوه، إذا استشهدوا في سبيل الله مخلصين له، وأن لا خوف عليهم فيما يستقبلون من أمور الآخرة، ولا هم يحزنون على ما فاتهم من حظوظ الدنيا.');
INSERT INTO Quran__Arabic_Tafseer_Muyassar (QuranChapterID, VerseNumber, Rukuh, Sajdah, Manzil, HizbQuarter, VerseText) VALUES (3, 171, '1', '0', '0', '1', 'وإنهم في فرحة غامرة بما أُعطوا من نعم الله وجزيل عطائه، وأن الله لا يضيع أجر المؤمنين به، بل ينمِّيه ويزيده من فضله.');
INSERT INTO Quran__Arabic_Tafseer_Muyassar (QuranChapterID, VerseNumber, Rukuh, Sajdah, Manzil, HizbQuarter, VerseText) VALUES (3, 172, '0', '0', '0', '0', 'الذين لبُّوا نداء الله ورسوله وخرجوا في أعقاب المشركين إلى "حمراء الأسد" بعد هزيمتهم في غزوة "أُحد" مع ما كان بهم من آلام وجراح، وبذلوا غاية جهدهم، والتزموا بهدي نبيهم، للمحسنين منهم والمتقين ثواب عظيم.');
INSERT INTO Quran__Arabic_Tafseer_Muyassar (QuranChapterID, VerseNumber, Rukuh, Sajdah, Manzil, HizbQuarter, VerseText) VALUES (3, 173, '0', '0', '0', '0', 'وهم الذين قال لهم بعض المشركين: إن أبا سفيان ومن معه قد أجمعوا أمرهم على الرجوع إليكم لاستئصالكم، فاحذروهم واتقوا لقاءهم، فإنه لا طاقة لكم بهم، فزادهم ذلك التخويف يقينًا وتصديقًا بوعد الله لهم، ولم يَثْنِهم ذلك عن عزمهم، فساروا إلى حيث شاء الله، وقالوا: حسبنا الله أي: كافينا، ونِعْم الوكيل المفوَّض إليه تدبير عباده.');
INSERT INTO Quran__Arabic_Tafseer_Muyassar (QuranChapterID, VerseNumber, Rukuh, Sajdah, Manzil, HizbQuarter, VerseText) VALUES (3, 174, '0', '0', '0', '0', 'فرجعوا من "حمراء الأسد" إلى "المدينة" بنعمة من الله بالثواب الجزيل وبفضل منه بالمنزلة العالية، وقد ازدادوا إيمانًا ويقينًا، وأذلوا أعداء الله، وفازوا بالسلامة من القتل والقتال، واتبعوا رضوان الله بطاعتهم له ولرسوله. والله ذو فضل عظيم عليهم وعلى غيرهم.');
INSERT INTO Quran__Arabic_Tafseer_Muyassar (QuranChapterID, VerseNumber, Rukuh, Sajdah, Manzil, HizbQuarter, VerseText) VALUES (3, 175, '0', '0', '0', '0', 'إنَّما المثبِّط لكم في ذلك هو الشيطان جاءكم يخوِّفكم أنصاره، فلا تخافوا المشركين؛ لأنّهم ضعاف لا ناصر لهم، وخافوني بالإقبال على طاعتي إن كنتم مصدِّقين بي، ومتبعين رسولي.');
INSERT INTO Quran__Arabic_Tafseer_Muyassar (QuranChapterID, VerseNumber, Rukuh, Sajdah, Manzil, HizbQuarter, VerseText) VALUES (3, 176, '0', '0', '0', '0', 'لا يُدْخِل الحزنَ إلى قلبك -أيها الرسول- هؤلاء الكفارُ بمسارعتهم في الجحود والضلال، إنهم بذلك لن يضروا الله، إنما يضرون أنفسهم بحرمانها حلاوة الإيمان وعظيم الثواب، يريد الله ألا يجعل لهم ثوابًا في الآخرة؛ لأنهم انصرفوا عن دعوة الحق، ولهم عذاب شديد.');
INSERT INTO Quran__Arabic_Tafseer_Muyassar (QuranChapterID, VerseNumber, Rukuh, Sajdah, Manzil, HizbQuarter, VerseText) VALUES (3, 177, '0', '0', '0', '0', 'إن الذين استبدلوا الكفر بالإيمان لن يضروا الله شيئًا، بل ضرر فِعْلِهم يعود على أنفسهم، ولهم في الآخرة عذاب موجع.');
INSERT INTO Quran__Arabic_Tafseer_Muyassar (QuranChapterID, VerseNumber, Rukuh, Sajdah, Manzil, HizbQuarter, VerseText) VALUES (3, 178, '0', '0', '0', '0', 'ولا يظننَّ الجاحدون أننا إذا أَطَلْنا أعمارهم، ومتعناهم بمُتع الدنيا، ولم تؤاخذهم بكفرهم وذنوبهم، أنهم قد نالوا بذلك خيرًا لأنفسهم، إنما نؤخر عذابهم وآجالهم؛ ليزدادوا ظلمًا وطغيانًا، ولهم عذاب يهينهم ويذلُّهم.');
INSERT INTO Quran__Arabic_Tafseer_Muyassar (QuranChapterID, VerseNumber, Rukuh, Sajdah, Manzil, HizbQuarter, VerseText) VALUES (3, 179, '0', '0', '0', '0', 'ما كان الله ليَدَعَكم أيها المصدقون بالله ورسوله العاملون بشرعه على ما أنتم عليه من التباس المؤمن منكم بالمنافق حتى يَمِيزَ الخبيث من الطيب، فيُعرف المنافق من المؤمن الصادق. وما كان مِن حكمة الله أن يطلعكم -أيها المؤمنون- على الغيب الذي يعلمه من عباده، فتعرفوا المؤمن منهم من المنافق، ولكنه يميزهم بالمحن والابتلاء، غير أن الله تعالى يصطفي من رسله مَن يشاء؛ ليطلعه على بعض علم الغيب بوحي منه، فآمنوا بالله ورسوله، وإن تؤمنوا إيمانًا صادقًا وتتقوا ربكم بطاعته، فلكم أجر عظيم عند الله.');
INSERT INTO Quran__Arabic_Tafseer_Muyassar (QuranChapterID, VerseNumber, Rukuh, Sajdah, Manzil, HizbQuarter, VerseText) VALUES (3, 180, '1', '0', '0', '0', 'ولا يظنن الذين يبخلون بما أنعم الله به عليهم تفضلا منه أن هذا البخل خير لهم، بل هو شرٌّ لهم؛ لأن هذا المال الذي جمعوه سيكون طوقًا من نار يوضع في أعناقهم يوم القيامة. والله سبحانه وتعالى هو مالك الملك، وهو الباقي بعد فناء جميع خلقه، وهو خبير بأعمالكم جميعها، وسيجازي كلا على قدر استحقاقه.');
INSERT INTO Quran__Arabic_Tafseer_Muyassar (QuranChapterID, VerseNumber, Rukuh, Sajdah, Manzil, HizbQuarter, VerseText) VALUES (3, 181, '0', '0', '0', '0', 'لقد سمع الله قول اليهود الذين قالوا: إن الله فقير إلينا يطلب منا أن نقرضه أموالا ونحن أغنياء. سنكتب هذا القول الذي قالوه، وسنكتب أنهم راضون بما كان مِن قَتْل آبائهم لأنبياء الله ظلمًا وعدوانًا، وسوف نؤاخذهم بذلك في الآخرة، ونقول لهم وهم في النار يعذبون: ذوقوا عذاب النار المحرقة.');
INSERT INTO Quran__Arabic_Tafseer_Muyassar (QuranChapterID, VerseNumber, Rukuh, Sajdah, Manzil, HizbQuarter, VerseText) VALUES (3, 182, '0', '0', '0', '0', 'ذلك العذاب الشديد بسبب ما قدَّمتموه في حياتكم الدنيا من المعاصي القولية والفعلية والاعتقادية، وأن الله ليس بظلام للعببد.');
INSERT INTO Quran__Arabic_Tafseer_Muyassar (QuranChapterID, VerseNumber, Rukuh, Sajdah, Manzil, HizbQuarter, VerseText) VALUES (3, 183, '0', '0', '0', '0', 'هؤلاء اليهود حين دُعُوا إلى الإسلام قالوا: إن الله أوصانا في التوراة ألا نصدِّق مَن جاءنا يقول: إنه رسول من الله، حتى يأتينا بصدقة يتقرب بها إلى الله، فتنزل نار من السماء فتحرقها. قل لهم -أيها الرسول-: أنتم كاذبون في قولكم؛ لأنه قد جاء آباءكم رسلٌ من قِبلي بالمعجزات والدلائل على صدقهم، وبالذي قلتم من الإتيان بالقربان الذي تأكله النار، فَلِمَ قَتَل آباؤكم هؤلاء الأنبياء إن كنتم صادقين في دعواكم؟');
INSERT INTO Quran__Arabic_Tafseer_Muyassar (QuranChapterID, VerseNumber, Rukuh, Sajdah, Manzil, HizbQuarter, VerseText) VALUES (3, 184, '0', '0', '0', '0', 'فإن كذَّبك -أيها الرسول- هؤلاء اليهود وغيرهم من أهل الكفر، فقد كذَّب المبطلون كثيرًا من المرسلين مِن قبلك، جاءوا أقوامهم بالمعجزات الباهرات والحجج الواضحات، والكتب السماوية التي هي نور يكشف الظلمات، والكتابِ البيِّن الواضح.');
INSERT INTO Quran__Arabic_Tafseer_Muyassar (QuranChapterID, VerseNumber, Rukuh, Sajdah, Manzil, HizbQuarter, VerseText) VALUES (3, 185, '0', '0', '0', '0', 'كل نفس لا بدَّ أن تذوق الموت، وبهذا يرجع جميع الخلق إلى ربهم؛ ليحاسبهم. وإنما تُوفَّون أجوركم على أعمالكم وافية غير منقوصة يوم القيامة، فمن أكرمه ربه ونجَّاه من النار وأدخله الجنة فقد نال غاية ما يطلب. وما الحياة الدنيا إلا متعة زائلة، فلا تغترُّوا بها.');
INSERT INTO Quran__Arabic_Tafseer_Muyassar (QuranChapterID, VerseNumber, Rukuh, Sajdah, Manzil, HizbQuarter, VerseText) VALUES (3, 186, '0', '0', '0', '1', 'لَتُخْتَبَرُنَّ -أيها المؤمنون- في أموالكم بإخراج النفقات الواجبة والمستحبَّة، وبالجوائح التي تصيبها، وفي أنفسكم بما يجب عليكم من الطاعات، وما يحلُّ بكم من جراح أو قتل وفَقْد للأحباب، وذلك حتى يتميَّز المؤمن الصادق من غيره. ولتَسمعُنَّ من اليهود والنصارى والمشركين ما يؤذي أسماعكم من ألفاظ الشرك والطعن في دينكم. وإن تصبروا -أيها المؤمنون- على ذلك كله، وتتقوا الله بلزوم طاعته واجتناب معصيته، فإن ذلك من الأمور التي يُعزم عليها، وينافس فيها.');
INSERT INTO Quran__Arabic_Tafseer_Muyassar (QuranChapterID, VerseNumber, Rukuh, Sajdah, Manzil, HizbQuarter, VerseText) VALUES (3, 187, '0', '0', '0', '0', 'واذكر -أيها الرسول- إذ أخذ الله العهد الموثق على الذين آتاهم الله الكتاب من اليهود والنصارى، فلليهود التوراة وللنصارى الإنجيل؛ ليعملوا بهما، ويبينوا للناس ما فيهما، ولا يكتموا ذلك ولا يخفوه، فتركوا العهد ولم يلتزموا به، وأخذوا ثمنا بخسًا مقابل كتمانهم الحق وتحريفهم الكتاب، فبئس الشراء يشترون، في تضييعهم الميثاق، وتبديلهم الكتاب.');
INSERT INTO Quran__Arabic_Tafseer_Muyassar (QuranChapterID, VerseNumber, Rukuh, Sajdah, Manzil, HizbQuarter, VerseText) VALUES (3, 188, '0', '0', '0', '0', 'ولا تظنن الذين يفرحون بما أَتَوا من أفعال قبيحة كاليهود والمنافقين وغيرهم، ويحبون أن يثني عليهم الناس بما لم يفعلوا، فلا تظننهم ناجين من عذاب الله في الدنيا، ولهم في الآخرة عذاب موجع. وفي الآية وعيد شديد لكل آت لفعل السوء معجب به، ولكل مفتخر بما لم يعمل، ليُثنيَ عليه الناس ويحمدوه.');
INSERT INTO Quran__Arabic_Tafseer_Muyassar (QuranChapterID, VerseNumber, Rukuh, Sajdah, Manzil, HizbQuarter, VerseText) VALUES (3, 189, '1', '0', '0', '0', 'ولله وحده ملك السموات والأرض وما فيهما، والله على كل شيء قدير.');
INSERT INTO Quran__Arabic_Tafseer_Muyassar (QuranChapterID, VerseNumber, Rukuh, Sajdah, Manzil, HizbQuarter, VerseText) VALUES (3, 190, '0', '0', '0', '0', 'إن في خلق السموات والأرض على غير مثال سابق، وفي تعاقُب الليل والنهار، واختلافهما طولا وقِصَرًا لدلائل وبراهين عظيمة على وحدانية الله لأصحاب العقول السليمة.');
INSERT INTO Quran__Arabic_Tafseer_Muyassar (QuranChapterID, VerseNumber, Rukuh, Sajdah, Manzil, HizbQuarter, VerseText) VALUES (3, 191, '0', '0', '0', '0', 'الذين يذكرون الله في جميع أحوالهم: قيامًا وقعودًا وعلى جنوبهم، وهم يتدبرون في خلق السموات والأرض، قائلين: يا ربنا ما أوجدت هذا الخلق عبثًا، فأنت منزَّه عن ذلك، فاصْرِف عنا عذاب النار.');
INSERT INTO Quran__Arabic_Tafseer_Muyassar (QuranChapterID, VerseNumber, Rukuh, Sajdah, Manzil, HizbQuarter, VerseText) VALUES (3, 192, '0', '0', '0', '0', 'يا ربنا نجِّنا من النار، فإنك -يا ألله- مَن تُدخِلْه النار بذنوبه فقد فضحته وأهنته، وما للمذنبين الظالمين لأنفسهم من أحد يدفع عنهم عقاب الله يوم القيامة.');
INSERT INTO Quran__Arabic_Tafseer_Muyassar (QuranChapterID, VerseNumber, Rukuh, Sajdah, Manzil, HizbQuarter, VerseText) VALUES (3, 193, '0', '0', '0', '0', 'يا ربنا إننا سمعنا مناديا -هو نبيك محمد صلى الله عليه وسلم- ينادي الناس للتصديق بك، والإقرار بوحدانيتك، والعمل بشرعك، فأجبنا دعوته وصدَّقنا رسالته، فاغفر لنا ذنوبنا، واستر عيوبنا، وألحقنا بالصالحين.');
INSERT INTO Quran__Arabic_Tafseer_Muyassar (QuranChapterID, VerseNumber, Rukuh, Sajdah, Manzil, HizbQuarter, VerseText) VALUES (3, 194, '0', '0', '0', '0', 'يا ربنا أعطنا ما وعدتنا على ألسنة رسلك من نصر وتمكين وتوفيق وهداية، ولا تفضحنا بذنوبنا يوم القيامة، فإنك كريم لا تُخْلف وعدًا وَعَدْتَ به عبادك.');
INSERT INTO Quran__Arabic_Tafseer_Muyassar (QuranChapterID, VerseNumber, Rukuh, Sajdah, Manzil, HizbQuarter, VerseText) VALUES (3, 195, '0', '0', '0', '0', 'فأجاب الله دعاءهم بأنه لا يضيع جهد مَن عمل منهم عملا صالحًا ذكرًا كان أو أنثى، وهم في أُخُوَّة الدين وقَبول الأعمال والجزاء عليها سواء، فالذين هاجروا رغبةً في رضا الله تعالى، وأُخرجوا من ديارهم، وأوذوا في طاعة ربهم وعبادتهم إيّاه، وقاتلوا وقُتِلوا في سبيل الله لإعلاء كلمته، ليسترنَّ الله عليهم ما ارتكبوه من المعاصي، كما سترها عليهم في الدنيا، فلا يحاسبهم عليها، وليدخلنَّهم جنات تجري من تحت قصورها وأشجارها الأنهار جزاء من عند الله، والله عنده حسن الثواب.');
INSERT INTO Quran__Arabic_Tafseer_Muyassar (QuranChapterID, VerseNumber, Rukuh, Sajdah, Manzil, HizbQuarter, VerseText) VALUES (3, 196, '0', '0', '0', '0', 'لا تغتر -أيها الرسول- بما عليه أهل الكفر بالله من بسطة في العيش، وسَعَة في الرزق، وانتقالهم من مكان إلى مكان للتجارات وطلب الأرباح والأموال، فعمَّا قليل يزول هذا كلُّه عنهم، ويصبحون مرتهنين بأعمالهم السيئة.');
INSERT INTO Quran__Arabic_Tafseer_Muyassar (QuranChapterID, VerseNumber, Rukuh, Sajdah, Manzil, HizbQuarter, VerseText) VALUES (3, 197, '0', '0', '0', '0', 'متاع قليل زائل، ثم يكون مصيرهم يوم القيامة إلى النار، وبئس الفراش.');
INSERT INTO Quran__Arabic_Tafseer_Muyassar (QuranChapterID, VerseNumber, Rukuh, Sajdah, Manzil, HizbQuarter, VerseText) VALUES (3, 198, '0', '0', '0', '0', 'لكن الذين خافوا ربهم، وامتثلوا أوامره، واجتنبوا نواهيه، قد أعدَّ الله لهم جنات تجري من تحت أشجارها الأنهار، هي منزلهم الدائم لا يخرجون منه. وما عد الله أعظم وأفضل لأهل الطاعة مما يتقلب فيه الذين كفروا من نعيم الدنيا.');
INSERT INTO Quran__Arabic_Tafseer_Muyassar (QuranChapterID, VerseNumber, Rukuh, Sajdah, Manzil, HizbQuarter, VerseText) VALUES (3, 199, '0', '0', '0', '0', 'وإن بعضًا من أهل الكتاب لَيصدِّق بالله ربًّا واحدًا وإلهًا معبودًا، وبما أُنزِل إليكم من هذا القرآن، وبما أُنزِل إليهم من التوراة والإنجيل متذللين لله، خاضعين له، لا يشترون بآيات الله ثمنًا قليلا من حطام الدنيا، ولا يكتمون ما أنزل الله، ولا يحرفونه كغيرهم من أهل الكتاب. أولئك لهم ثواب عظيم عنده يوم يلقونه، فيوفيهم إياه غير منقوص. إنَّ الله سريع الحساب، لا يعجزه إحصاء أعمالهم، ومحاسبتهم عليها.');
INSERT INTO Quran__Arabic_Tafseer_Muyassar (QuranChapterID, VerseNumber, Rukuh, Sajdah, Manzil, HizbQuarter, VerseText) VALUES (3, 200, '1', '0', '0', '0', 'يا أيها الذين صدَّقوا الله ورسوله وعملوا بشرعه اصبروا على طاعة ربكم، وعلى ما ينزل بكم من ضر وبلاء، وصابروا أعداءكم حتى لا يكونوا أشد صبرًا منكم، وأقيموا على جهاد عدوي وعدوكم، وخافوا الله في جميع أحوالكم؛ رجاء أن تفوزوا برضاه في الدنيا والآخرة.');
INSERT INTO Quran__Arabic_Tafseer_Muyassar (QuranChapterID, VerseNumber, Rukuh, Sajdah, Manzil, HizbQuarter, VerseText) VALUES (4, 1, '0', '0', '0', '1', 'يا أيها الناس خافوا الله والتزموا أوامره، واجتنبوا نواهيه؛ فهو الذي خلقكم من نفس واحدة هي آدم عليه السلام، وخلق منها زوجها وهي حواء، ونشر منهما في أنحاء الأرض رجالا كثيرًا ونساء كثيرات، وراقبوا الله الذي يَسْأل به بعضكم بعضًا، واحذروا أن تقطعوا أرحامكم. إن الله مراقب لجميع أحوالكم.');
INSERT INTO Quran__Arabic_Tafseer_Muyassar (QuranChapterID, VerseNumber, Rukuh, Sajdah, Manzil, HizbQuarter, VerseText) VALUES (4, 2, '0', '0', '0', '0', 'وأعطوا مَن مات آباؤهم وهم دون البلوغ، وكنتم عليهم أوصياء، أموالهم إذا وصلوا سن البلوغ، ورأيتم منهم قدرة على حفظ أموالهم، ولا تأخذوا الجيِّد من أموالهم، وتجعلوا مكانه الرديء من أموالكم، ولا تخلطوا أموالهم بأموالكم؛ لتحتالوا بذلك على أكل أموالهم. إن من تجرأ على ذلك فقد ارتكب إثمًا عظيمًا.');
INSERT INTO Quran__Arabic_Tafseer_Muyassar (QuranChapterID, VerseNumber, Rukuh, Sajdah, Manzil, HizbQuarter, VerseText) VALUES (4, 3, '0', '0', '0', '0', 'وإن خفتم ألا تعدلوا في يتامى النساء اللاتي تحت أيديكم بأن لا تعطوهن مهورهن كغيرهن، فاتركوهن وانكحوا ما طاب لكم من النساء من غيرهن: اثنتين أو ثلاثًا أو أربعًا، فإن خشيتم ألا تعدلوا بينهن فاكتفوا بواحدة، أو بما عندكم من الإماء. ذلك الذي شرعته لكم في اليتيمات والزواج من واحدة إلى أربع، أو الاقتصار على واحدة أو ملك اليمين، أقرب إلى عدم الجَوْرِ والتعدي.');
INSERT INTO Quran__Arabic_Tafseer_Muyassar (QuranChapterID, VerseNumber, Rukuh, Sajdah, Manzil, HizbQuarter, VerseText) VALUES (4, 4, '0', '0', '0', '0', 'وأعطوا النساء مهورهن، عطية واجبة وفريضة لازمة عن طيب نفس منكم. فإن طابت أنفسهن لكم عن شيء من المهر فوهَبْنه لكم فخذوه، وتصرَّفوا فيه، فهو حلال طيب.');
INSERT INTO Quran__Arabic_Tafseer_Muyassar (QuranChapterID, VerseNumber, Rukuh, Sajdah, Manzil, HizbQuarter, VerseText) VALUES (4, 5, '0', '0', '0', '0', 'ولا تؤتوا -أيها الأولياء- من يُبَذِّر من الرجال والنساء والصبيان أموالهم التي تحت أيديكم فيضعوها في غير وجهها، فهذه الأموال هي التي عليها قيام حياة الناس، وأنفقوا عليهم منها واكسوهم، وقولوا لهم قولا معروفًا من الكلام الطيب والخلق الحسن.');
INSERT INTO Quran__Arabic_Tafseer_Muyassar (QuranChapterID, VerseNumber, Rukuh, Sajdah, Manzil, HizbQuarter, VerseText) VALUES (4, 6, '0', '0', '0', '0', 'واختبروا مَن تحت أيديكم من اليتامى لمعرفة قدرتهم على حسن التصرف في أموالهم، حتى إذا وصلوا إلى سن البلوغ، وعَلمتم منهم صلاحًا في دينهم، وقدرة على حفظ أموالهم، فسلِّموها لهم، ولا تعتدوا عليها بإنفاقها في غير موضعها إسرافًا ومبادرة لأكلها قبل أن يأخذوها منكم. ومَن كان صاحب مال منكم فليستعفف بغناه، ولا يأخذ من مال اليتيم شيئًا، ومن كان فقيرًا فليأخذ بقدر حاجته عند الضرورة. فإذا علمتم أنهم قادرون على حفظ أموالهم بعد بلوغهم الحُلُم وسلمتموها إليهم، فأَشْهِدوا عليهم؛ ضمانًا لوصول حقهم كاملا إليهم؛ لئلا ينكروا ذلك. ويكفيكم أن الله شاهد عليكم، ومحاسب لكم على ما فعلتم.');
INSERT INTO Quran__Arabic_Tafseer_Muyassar (QuranChapterID, VerseNumber, Rukuh, Sajdah, Manzil, HizbQuarter, VerseText) VALUES (4, 7, '0', '0', '0', '0', 'للذكور -صغارًا أو كبارًا- نصيب شرعه الله فيما تركه الوالدان والأقربون من المال، قليلا كان أو كثيرًا، في أنصبة محددة واضحة فرضها الله عز وجل لهؤلاء، وللنساء كذلك.');
INSERT INTO Quran__Arabic_Tafseer_Muyassar (QuranChapterID, VerseNumber, Rukuh, Sajdah, Manzil, HizbQuarter, VerseText) VALUES (4, 8, '0', '0', '0', '0', 'وإذا حضر قسمةَ الميراث أقاربُ الميت ممن لا حقَّ لهم في التركة، أو حضرها من مات آباؤهم وهم صغار، أو مَن لا مال لهم فأعطوهم شيئًا من المال على وجه الاستحباب قبل تقسيم التركة على أصحابها، وقولوا لهم قولا حسنًا غير فاحش ولا قبيح.');
INSERT INTO Quran__Arabic_Tafseer_Muyassar (QuranChapterID, VerseNumber, Rukuh, Sajdah, Manzil, HizbQuarter, VerseText) VALUES (4, 9, '0', '0', '0', '0', 'ولْيَخَفِ الذين لو ماتوا وتركوا من خلفهم أبناء صغارًا ضعافًا خافوا عليهم الظلم والضياع، فليراقبوا الله فيمن تحت أيديهم من اليتامى وغيرهم، وذلك بحفظ أموالهم، وحسن تربيتهم، ودَفْع الأذى عنهم، وليقولوا لهم قولا موافقا للعدل والمعروف.');
INSERT INTO Quran__Arabic_Tafseer_Muyassar (QuranChapterID, VerseNumber, Rukuh, Sajdah, Manzil, HizbQuarter, VerseText) VALUES (4, 10, '1', '0', '0', '0', 'إن الذين يعْتَدون على أموال اليتامى، فيأخذونها بغير حق، إنما يأكلون نارًا تتأجّج في بطونهم يوم القيامة، وسيدخلون نارا يقاسون حرَّها.');
INSERT INTO Quran__Arabic_Tafseer_Muyassar (QuranChapterID, VerseNumber, Rukuh, Sajdah, Manzil, HizbQuarter, VerseText) VALUES (4, 11, '0', '0', '0', '0', 'يوصيكم الله ويأمركم في شأن أولادكم: إذا مات أحد منكم وترك أولادًا: ذكورًا وإناثًا، فميراثه كله لهم: للذكر مثل نصيب الأنثيين، إذا لم يكن هناك وارث غيرهم. فإن ترك بنات فقط فللبنتين فأكثر ثلثا ما ترك، وإن كانت ابنة واحدة، فلها النصف. ولوالِدَي الميت لكل واحد منهما السدس إن كان له ولد: ذكرًا كان أو أنثى، واحدًا أو أكثر. فإن لم يكن له ولد وورثه والداه فلأمه الثلث ولأبيه الباقي. فإن كان للميت إخوة اثنان فأكثر، ذكورًا كانوا أو إناثًا، فلأمه السدس، وللأب الباقي ولا شيء للإخوة. وهذا التقسيم للتركة إنما يكون بعد إخراج وصية الميت في حدود الثلث أو إخراج ما عليه من دَيْن. آباؤكم وأبْناؤكم الذين فُرِض لهم الإرث لا تعرفون أيهم أقرب لكم نفعًا في دنياكم وأخراكم، فلا تفضلوا واحدًا منهم على الآخر. هذا الذي أوصيتكم به مفروض عليكم من الله. إن الله كان عليمًا بخلقه، حكيمًا فيما شرعه لهم.');
INSERT INTO Quran__Arabic_Tafseer_Muyassar (QuranChapterID, VerseNumber, Rukuh, Sajdah, Manzil, HizbQuarter, VerseText) VALUES (4, 12, '0', '0', '0', '1', 'ولكم -أيها الرجال- نصف ما ترك أزواجكم بعد وفاتهن إن لم يكن لهن ولد ذكرًا كان أو أنثى، فإن كان لهن ولد فلكم الربع مما تركن، ترثونه من بعد إنفاذ وصيتهن الجائزة، أو ما يكون عليهن من دَيْن لمستحقيه. ولأزواجكم - أيها الرجال - الربع مما تركتم، إن لم يكن لكم ابن أو ابنة منهن أو من غيرهن، فإن كان لكم ابن أو ابنة فلهن الثمن مما تركتم، يقسم الربع أو الثمن بينهن، فإن كانت زوجة واحدة كان هذا ميراثًا لها، من بعد إنفاذ ما كنتم أوصيتم به من الوصايا الجائزة، أو قضاء ما يكون عليكم من دَيْن. وإن مات رجل أو امراة وليس له أو لها ولد ولا والد، وله أو لها أخ أو أخت من أم فلكل واحد منهما السدس. فإن كان الإخوة أو الأخوات لأم أكثر من ذلك فهم شركاء في الثلث يقسم بينهم بالسوية لا فرق بين الذكر والأنثى، وهذا الذي فرضه الله للإخوة والأخوات لأم يأخذونه ميراثًا لهم من بعد قضاء ديون الميت، وإنفاذ وصيته إن كان قد أوصى بشيء لا ضرر فيه على الورثة. بهذا أوصاكم ربكم وصية نافعة لكم. والله عليم بما يصلح خلقه، حليم لا يعاجلهم بالعقوبة.');
INSERT INTO Quran__Arabic_Tafseer_Muyassar (QuranChapterID, VerseNumber, Rukuh, Sajdah, Manzil, HizbQuarter, VerseText) VALUES (4, 13, '0', '0', '0', '0', 'تلك الأحكام الإلهية التي شرعها الله في اليتامى والنساء والمواريث، شرائعه الدالة على أنها مِن عند الله العليم الحكيم. ومَن يطع الله ورسوله فيما شرع لعباده من هذه الأحكام وغيرها، يدخله جنات كثيرة الأشجار والقصور، تجري من تحتها الأنهار بمياهها العذبة، وهم باقون في هذا النعيم، لا يخرجون منه، وذلك الثواب هو الفلاح العظيم.');
INSERT INTO Quran__Arabic_Tafseer_Muyassar (QuranChapterID, VerseNumber, Rukuh, Sajdah, Manzil, HizbQuarter, VerseText) VALUES (4, 14, '1', '0', '0', '0', 'ومَن يَعْصِ الله ورسوله، بإنكاره لأحكام الله، وتجاوزه ما شرعه الله لعباده بتغييرها، أو تعطيل العمل بها، يدخله نارًا ماكثًا فيها، وله عذاب يخزيه ويهينه.');
INSERT INTO Quran__Arabic_Tafseer_Muyassar (QuranChapterID, VerseNumber, Rukuh, Sajdah, Manzil, HizbQuarter, VerseText) VALUES (4, 15, '0', '0', '0', '0', 'واللاتي يزنين من نسائكم، فاستشهدوا -أيها الولاة والقضاة- عليهن أربعة رجال عدول من المسلمين، فإن شهدوا عليهن بذلك فاحبسوهن في البيوت حتى تنتهي حياتهن بالموت، أو يجعل الله لهن طريقًا للخلاص من ذلك.');
INSERT INTO Quran__Arabic_Tafseer_Muyassar (QuranChapterID, VerseNumber, Rukuh, Sajdah, Manzil, HizbQuarter, VerseText) VALUES (4, 16, '0', '0', '0', '0', 'واللذان يقعان في فاحشة الزنى، فآذُوهما بالضرب والهجر والتوبيخ، فإن تابا عمَّا وقع منهما وأصلحا بما يقدِّمان من الأعمال الصالحة فاصفحوا عن أذاهما. ويستفاد من هذه الآية والتي قبلها أن الرجال إذا فعلوا الفاحشة يُؤْذَوْن، والنساء يُحْبَسْنَ ويُؤذَيْنَ، فالحبس غايتة الموت، والأذية نهايتها إلى التوبة والصلاح. وكان هذا في صدر الإسلام، ثم نُسخ بما شرع الله ورسوله، وهو الرجم للمحصن والمحصنة، وهما الحران البالغان العاقلان، اللذان جامعا في نكاح صحيح، والجلدُ مائة جلدة، وتغريب عام لغيرهما. إن الله كان توابا على عباده التائبين، رحيمًا بهم.');
INSERT INTO Quran__Arabic_Tafseer_Muyassar (QuranChapterID, VerseNumber, Rukuh, Sajdah, Manzil, HizbQuarter, VerseText) VALUES (4, 17, '0', '0', '0', '0', 'إنَّما يقبل الله التوبة من الذين يرتكبون المعاصي والذنوب بجهل منهم لعاقبتها، وإيجابها لسخط الله -فكل عاص لله مخطئًا أو متعمِّدًا فهو جاهل بهذا الاعتبار، وإن كان عالمًا بالتحريم -ثم يرجعون إلى ربهم بالإنابة والطاعة قبل معاينة الموت، فأولئك يقبل الله توبتهم. وكان الله عليمًا بخلقه، حكيمًا في تدبيره وتقديره.');
INSERT INTO Quran__Arabic_Tafseer_Muyassar (QuranChapterID, VerseNumber, Rukuh, Sajdah, Manzil, HizbQuarter, VerseText) VALUES (4, 18, '0', '0', '0', '0', 'وليس قَبول التوبة للذين يُصِرُّون على ارتكاب المعاصي، ولا يرجعون إلى ربهم إلى أن تأتيهم سكرات الموت، فيقول أحدهم: إني تبت الآن، كما لا تُقبل توبة الذين يموتون وهم جاحدون، منكرون لوحدانية الله ورسالة رسوله محمد صلى الله عليه وسلم. أولئك المصرُّون على المعاصي إلى أن ماتوا، والجاحدون الذين يموتون وهم كفار، أعتدنا لهم عذابًا موجعًا.');
INSERT INTO Quran__Arabic_Tafseer_Muyassar (QuranChapterID, VerseNumber, Rukuh, Sajdah, Manzil, HizbQuarter, VerseText) VALUES (4, 19, '0', '0', '0', '0', 'يا أيها الذين آمنوا لا يجوز لكم أن تجعلوا نساء آبائكم من جملة تَرِكتهم، تتصرفون فيهن بالزواج منهن، أو المنع لهن، أو تزويجهن للآخرين، وهن كارهات لذلك كله، ولا يجوز لكم أن تضارُّوا أزواجكم وأنتم كارهون لهن؛ ليتنازلن عن بعض ما آتيتموهن من مهر ونحوه، إلا أن يرتكبن أمرا فاحشا كالزنى، فلكم حينئذ إمساكهن حتى تأخذوا ما أعطيتموهن. ولتكن مصاحبتكم لنسائكم مبنية على التكريم والمحبة، وأداء ما لهن من حقوق. فإن كرهتموهن لسبب من الأسباب الدنيوية فاصبروا؛ فعسى أن تكرهوا أمرًا من الأمور ويكون فيه خير كثير.');
INSERT INTO Quran__Arabic_Tafseer_Muyassar (QuranChapterID, VerseNumber, Rukuh, Sajdah, Manzil, HizbQuarter, VerseText) VALUES (4, 20, '0', '0', '0', '0', 'وإن أردتم استبدال زوجة مكان أخرى، وكنتم قد أعطيتم مَن تريدون طلاقها مالا كثيرًا مهرًا لها، فلا يحل لكم أن تأخذوا منه شيئًا، أتأخذونه كذبًا وافتراءً واضحًا؟');
INSERT INTO Quran__Arabic_Tafseer_Muyassar (QuranChapterID, VerseNumber, Rukuh, Sajdah, Manzil, HizbQuarter, VerseText) VALUES (4, 21, '0', '0', '0', '0', 'وكيف يحلُّ لكم أن تأخذوا ما أعطيتموهن من مهر، وقد استمتع كل منكما بالآخر بالجماع، وأخَذْنَ منكم ميثاقًا غليظًا من إمساكهن بمعروف أو تسريحهن بإحسان؟');
INSERT INTO Quran__Arabic_Tafseer_Muyassar (QuranChapterID, VerseNumber, Rukuh, Sajdah, Manzil, HizbQuarter, VerseText) VALUES (4, 22, '1', '0', '0', '0', 'ولا تتزوجوا مَن تزوجه آباؤكم من النساء إلا ما قد سلف منكم ومضى في الجاهلية فلا مؤاخذة فيه. إن زواج الأبناء من زوجات آبائهم أمر قبيح يفحش ويعظم قبحه، وبغيض يمقت الله فاعله، وبئس طريقًا ومنهجًا ما كنتم تفعلونه في جاهليتكم.');
INSERT INTO Quran__Arabic_Tafseer_Muyassar (QuranChapterID, VerseNumber, Rukuh, Sajdah, Manzil, HizbQuarter, VerseText) VALUES (4, 23, '0', '0', '0', '0', 'حرَّم الله عليكم نكاح أمهاتكم، ويدخل في ذلك الجدَّات مِن جهة الأب أو الأم، وبناتكم: ويشمل بنات الأولاد وإن نزلن، وأخواتكم الشقيقات أو لأب أو لأم، وعماتكم: أخوات آبائكم وأجدادكم، وخالاتكم: أخوات أمهاتكم وجداتكم، وبنات الأخ، وبنات الأخت: ويدخل في ذلك أولادهن، وأمهاتكم اللاتي أرضعنكم، وأخواتكم من الرضاعة -وقد حرَّم رسول الله صلى الله عليه وسلم من الرضاع ما يحرم من النسب- وأمهات نسائكم، سواء دخلتم بنسائكم، أم لم تدخلوا بهن، وبنات نسائكم من غيركم اللاتي يتربَّيْنَ غالبًا في بيوتكم وتحت رعايتكم، وهن مُحرَّمَات فإن لم يكنَّ في حجوركم، ولكن بشرط الدخول بأمهاتهن، فإن لم تكونوا دخلتم بأمهاتهن وطلقتموهن أو متْنَ قبل الدخول فلا جناح عليكم أن تنكحوهن، كما حرَّم الله عليكم أن تنكحوا زوجات أبنائكم الذين من أصلابكم، ومن أُلحق بهم مِن أبنائكم من الرضاع، وهذا التحريم يكون بالعقد عليها، دخل الابن بها أم لم يدخل، وحرَّم عليكم كذلك الجمع في وقت واحد بين الأختين بنسب أو رضاع إلا ما قد سلف ومضى منكم في الجاهلية. ولا يجوز كذلك الجمع بين المرأة وعمتها أو خالتها كما جاء في السنة. إن الله كان غفورًا للمذنبين إذا تابوا، رحيمًا بهم، فلا يكلفهم ما لا يطيقون.');
INSERT INTO Quran__Arabic_Tafseer_Muyassar (QuranChapterID, VerseNumber, Rukuh, Sajdah, Manzil, HizbQuarter, VerseText) VALUES (4, 24, '0', '0', '0', '1', 'ويحرم عليكم نكاح المتزوجات من النساء، إلا مَنْ سَبَيْتُم منهن في الجهاد، فإنه يحل لكم نكاحهن، بعد استبراء أرحامهن بحيضة، كتب الله عليكم تحريم نكاح هؤلاء، وأجاز لكم نكاح مَن سواهن، ممَّا أحله الله لكم أن تطلبوا بأموالكم العفة عن اقتراف الحرام. فما استمتعتم به منهن بالنكاح الصحيح، فأعطوهن مهورهن، التي فرض الله لهن عليكم، ولا إثم عليكم فيما تمَّ التراضي به بينكم، من الزيادة أو النقصان في المهر، بعد ثبوت الفريضة. إن الله تعالى كان عليمًا بأمور عباده، حكيما في أحكامه وتدبيره.');
INSERT INTO Quran__Arabic_Tafseer_Muyassar (QuranChapterID, VerseNumber, Rukuh, Sajdah, Manzil, HizbQuarter, VerseText) VALUES (4, 25, '1', '0', '0', '0', 'ومن لا قدرة له على مهور الحرائر المؤمنات، فله أن ينكح غيرهن، من فتياتكم المؤمنات المملوكات. والله تعالى هو العليم بحقيقة إيمانكم، بعضكم من بعض، فتزوجوهن بموافقة أهلهن، وأعطوهن مهورهن على ما تراضيتم به عن طيب نفس منكم، متعففات عن الحرام، غير مجاهرات بالزنى، ولا مسرات به باتخاذ أخلاء، فإذا تزوجن وأتين بفاحشة الزنى فعليهن من الحدِّ نصف ما على الحرائر. ذلك الذي أبيح مِن نكاح الإماء بالصفة المتقدمة إنما أبيح لمن خاف على نفسه الوقوع في الزنى، وشق عليه الصبر عن الجماع، والصبر عن نكاح الإماء مع العفة أولى وأفضل. والله تعالى غفور لكم، رحيم بكم إذ أذن لكم في نكاحهن عند العجز عن نكاح الحرائر.');
INSERT INTO Quran__Arabic_Tafseer_Muyassar (QuranChapterID, VerseNumber, Rukuh, Sajdah, Manzil, HizbQuarter, VerseText) VALUES (4, 26, '0', '0', '0', '0', 'يريد الله تعالى بهذه التشريعات، أن يوضح لكم معالم دينه القويم، وشرعه الحكيم، ويدلكم على طرق الأنبياء والصالحين من قبلكم في الحلال والحرام، ويتوب عليكم بالرجوع بكم إلى الطاعات، وهو سبحانه عليم بما يصلح شأن عباده، حكيم فيما شرعه لكم.');
INSERT INTO Quran__Arabic_Tafseer_Muyassar (QuranChapterID, VerseNumber, Rukuh, Sajdah, Manzil, HizbQuarter, VerseText) VALUES (4, 27, '0', '0', '0', '0', 'والله يريد أن يتوب عليكم، ويتجاوز عن خطاياكم، ويريد الذين ينقادون لشهواتهم وملذاتهم أن تنحرفوا عن الدين انحرافًا كبيرًا.');
INSERT INTO Quran__Arabic_Tafseer_Muyassar (QuranChapterID, VerseNumber, Rukuh, Sajdah, Manzil, HizbQuarter, VerseText) VALUES (4, 28, '0', '0', '0', '0', 'يريد الله تعالى بما شرعه لكم التيسير، وعدم التشديد عليكم؛ لأنكم خلقتم ضعفاء.');
INSERT INTO Quran__Arabic_Tafseer_Muyassar (QuranChapterID, VerseNumber, Rukuh, Sajdah, Manzil, HizbQuarter, VerseText) VALUES (4, 29, '0', '0', '0', '0', 'يا أيها الذين صدَّقوا الله ورسوله وعملوا بشرعه، لا يحل لكم أن يأكل بعضكم مال بعض بغير حق، إلا أن يكون وَفْقَ الشرع والكسب الحلال عن تراض منكم، ولا يقتل بعضكم بعضًا فتهلكوا أنفسكم بارتكاب محارم الله ومعاصيه. إن الله كان بكم رحيمًا في كل ما أمركم به، ونهاكم عنه.');
INSERT INTO Quran__Arabic_Tafseer_Muyassar (QuranChapterID, VerseNumber, Rukuh, Sajdah, Manzil, HizbQuarter, VerseText) VALUES (4, 30, '0', '0', '0', '0', 'ومن يرتكب ما نهى الله عنه من أخذ المال الحرام كالسرقة والغصب والغش معتديًا متجاوزًا حد الشرع، فسوف يدخله الله نارًا يقاسي حرَّها، وكان ذلك على الله يسيرًا.');
INSERT INTO Quran__Arabic_Tafseer_Muyassar (QuranChapterID, VerseNumber, Rukuh, Sajdah, Manzil, HizbQuarter, VerseText) VALUES (4, 31, '0', '0', '0', '0', 'إن تبتعدوا -أيها المؤمنون- عن كبائر الذنوب كالإشراك بالله وعقوق الوالدين وقَتْلِ النفس بغير الحق وغير ذلك، نكفِّر عنكم ما دونها من الصغائر، وندخلكم مدخلا كريمًا، وهو الجنَّة.');
INSERT INTO Quran__Arabic_Tafseer_Muyassar (QuranChapterID, VerseNumber, Rukuh, Sajdah, Manzil, HizbQuarter, VerseText) VALUES (4, 32, '0', '0', '0', '0', 'ولا تتمنوا ما فضَّل الله به بعضكم على بعض، في المواهب والأرزاق وغير ذلك، فقد جعل الله للرجال نصيبًا مقدَّرًا من الجزاء بحسب عملهم، وجعل للنساء نصيبًا مما عملن، واسألوا الله الكريم الوهاب يُعْطِكم من فضله بدلا من التمني. إن الله كان بكل شيء عليمًا، وهو أعلم بما يصلح عباده فيما قسمه لهم من خير.');
INSERT INTO Quran__Arabic_Tafseer_Muyassar (QuranChapterID, VerseNumber, Rukuh, Sajdah, Manzil, HizbQuarter, VerseText) VALUES (4, 33, '1', '0', '0', '0', 'ولكل واحد منكم جعلنا ورثة يرثون مما ترك الوالدان والأقربون، والذين تحالفتم معهم بالأيمان المؤكدة على النصرة وإعطائهم شيئًا من الميراث فأعطوهم ما قُدِّر لهم. والميراث بالتحالف كان في أول الإسلام، ثم رُفع حكمه بنزول آيات المواريث. إن الله كان مُطَّلِعًا على كل شيء من أعمالكم، وسيجازيكم على ذلك.');
INSERT INTO Quran__Arabic_Tafseer_Muyassar (QuranChapterID, VerseNumber, Rukuh, Sajdah, Manzil, HizbQuarter, VerseText) VALUES (4, 34, '0', '0', '0', '0', 'الرجال قوَّامون على توجيه النساء ورعايتهن، بما خصهم الله به من خصائص القِوامَة والتفضيل، وبما أعطوهن من المهور والنفقات. فالصالحات المستقيمات على شرع الله منهن، مطيعات لله تعالى ولأزواجهن، حافظات لكل ما غاب عن علم أزواجهن بما اؤتمنَّ عليه بحفظ الله وتوفيقه، واللاتي تخشون منهن ترفُّعهن عن طاعتكم، فانصحوهن بالكلمة الطيبة، فإن لم تثمر معهن الكلمة الطيبة، فاهجروهن في الفراش، ولا تقربوهن، فإن لم يؤثر فعل الهِجْران فيهن، فاضربوهن ضربًا لا ضرر فيه، فإن أطعنكم فاحذروا ظلمهن، فإن الله العليَّ الكبير وليُّهن، وهو منتقم ممَّن ظلمهنَّ وبغى عليهن.');
INSERT INTO Quran__Arabic_Tafseer_Muyassar (QuranChapterID, VerseNumber, Rukuh, Sajdah, Manzil, HizbQuarter, VerseText) VALUES (4, 35, '0', '0', '0', '0', 'وإن علمتم -يا أولياء الزوجين- شقاقًا بينهما يؤدي إلى الفراق، فأرسلوا إليهما حكمًا عدلا من أهل الزوج، وحكمًا عدلا من أهل الزوجة؛ لينظرا ويحكما بما فيه المصلحة لهما، وبسبب رغبة الحكمين في الإصلاح، واستعمالهما الأسلوب الطيب يوفق الله بين الزوجين. إن الله تعالى عليم، لا يخفى عليه شيء من أمر عباده، خبير بما تنطوي عليه نفوسهم.');
INSERT INTO Quran__Arabic_Tafseer_Muyassar (QuranChapterID, VerseNumber, Rukuh, Sajdah, Manzil, HizbQuarter, VerseText) VALUES (4, 36, '0', '0', '0', '1', 'واعبدوا الله وانقادوا له وحده، ولا تجعلوا له شريكًا في الربوبية والعبادة، وأحسنوا إلى الوالدين، وأدُّوا حقوقهما، وحقوق الأقربين، واليتامى والمحتاجين، والجار القريب منكم والبعيد، والرفيق في السفر وفي الحضر، والمسافر المحتاج، والمماليك من فتيانكم وفتياتكم. إن الله تعالى لا يحب المتكبرين من عباده، المفتخرين على الناس.');
INSERT INTO Quran__Arabic_Tafseer_Muyassar (QuranChapterID, VerseNumber, Rukuh, Sajdah, Manzil, HizbQuarter, VerseText) VALUES (4, 37, '0', '0', '0', '0', 'الذين يمتنعون عن الإنفاق والعطاء مما رزقهم الله، ويأمرون غيرهم بالبخل، ويجحدون نِعَمَ الله عليهم، ويخفون فضله وعطاءه. وأعددنا للجاحدين عذابًا مخزيًا.');
INSERT INTO Quran__Arabic_Tafseer_Muyassar (QuranChapterID, VerseNumber, Rukuh, Sajdah, Manzil, HizbQuarter, VerseText) VALUES (4, 38, '0', '0', '0', '0', 'وأعتدنا هذا العذاب كذلك للذين ينفقون أموالهم رياءً وسمعةً، ولا يصدقون بالله اعتقادًا وعملا ولا بيوم القيامة. وهذه الأعمال السيئة مما يدعو إليها الشيطان. ومن يكن الشيطان له ملازمًا فبئس الملازم والقرين.');
INSERT INTO Quran__Arabic_Tafseer_Muyassar (QuranChapterID, VerseNumber, Rukuh, Sajdah, Manzil, HizbQuarter, VerseText) VALUES (4, 39, '0', '0', '0', '0', 'وأيُّ ضرر يلحقهم لو صدَّقوا بالله واليوم الآخر اعتقادًا وعملا وأنفقوا مما أعطاهم الله باحتساب وإخلاص، والله تعالى عليم بهم وبما يعملون، وسيحاسبهم على ذلك.');
INSERT INTO Quran__Arabic_Tafseer_Muyassar (QuranChapterID, VerseNumber, Rukuh, Sajdah, Manzil, HizbQuarter, VerseText) VALUES (4, 40, '0', '0', '0', '0', 'إن الله تعالى لا ينقص أحدًا من جزاء عمله مقدار ذرة، وإن تكن زنة الذرة حسنة فإنه سبحانه يزيدها ويكثرها لصاحبها، ويتفضل عليه بالمزيد، فيعطيه من عنده ثوابًا كبيرًا هو الجنة.');
INSERT INTO Quran__Arabic_Tafseer_Muyassar (QuranChapterID, VerseNumber, Rukuh, Sajdah, Manzil, HizbQuarter, VerseText) VALUES (4, 41, '0', '0', '0', '0', 'فكيف يكون حال الناس يوم القيامة، إذا جاء الله من كل أمة برسولها ليشهد عليها بما عملت، وجاء بك -أيها الرسول- لتكون شهيدًا على أمتك أنك بلغتهم رسالة ربِّك.');
INSERT INTO Quran__Arabic_Tafseer_Muyassar (QuranChapterID, VerseNumber, Rukuh, Sajdah, Manzil, HizbQuarter, VerseText) VALUES (4, 42, '1', '0', '0', '0', 'يوم يكون ذلك، يتمنى الذين كفروا بالله تعالى وخالفوا الرسول ولم يطيعوه، لو يجعلهم الله والأرض سواء، فيصيرون ترابًا، حتى لا يبعثوا وهم لا يستطيعون أن يُخفوا عن الله شيئًا مما في أنفسهم، إذ ختم الله على أفواههم، وشَهِدَتْ عليهم جوارحهم بما كانوا يعملون.');
INSERT INTO Quran__Arabic_Tafseer_Muyassar (QuranChapterID, VerseNumber, Rukuh, Sajdah, Manzil, HizbQuarter, VerseText) VALUES (4, 43, '0', '0', '0', '0', 'يا أيها الذين صدَّقوا بالله ورسوله وعملوا بشرعه، لا تقربوا الصلاة ولا تقوموا إليها حال السكر حتى تميزوا وتعلموا ما تقولون، وقد كان هذا قبل التحريم القاطع للخمر في كل حال، ولا تقربوا الصلاة في حال الجنابة، ولا تقربوا مواضعها وهي المساجد، إلا من كان منكم مجتازًا من باب إلى باب، حتى تتطهروا. وإن كنتم في حال مرض لا تقدرون معه على استعمال الماء، أو حال سفر، أو جاء أحد منكم من الغائط، أو جامعتم النساء، فلم تجدوا ماء للطهارة فاقصدوا ترابًا طاهرًا، فامسحوا بوجوهكم وأيديكم منه. إن الله تعالى كان عفوًّا عنكم، غفورًا لكم.');
INSERT INTO Quran__Arabic_Tafseer_Muyassar (QuranChapterID, VerseNumber, Rukuh, Sajdah, Manzil, HizbQuarter, VerseText) VALUES (4, 44, '0', '0', '0', '0', 'ألم تعلم -أيها الرسول- أمر اليهود الذين أُعطوا حظًّا من العلم مما جاءهم من التوراة، يستبدلون الضلالة بالهدى، ويتركون ما لديهم من الحجج والبراهين، الدالة على صدق رسالة الرسول محمد صلى الله عليه وسلم، ويتمنون لكم -أيها المؤمنون المهتدون- أن تنحرفوا عن الطريق المستقيم؛ لتكونوا ضالين مثلهم.');
INSERT INTO Quran__Arabic_Tafseer_Muyassar (QuranChapterID, VerseNumber, Rukuh, Sajdah, Manzil, HizbQuarter, VerseText) VALUES (4, 45, '0', '0', '0', '0', 'والله سبحانه وتعالى أعلم منكم -أيها المؤمنون- بعداوة هؤلاء اليهود لكم، وكفى بالله وليًّا يتولاكم، وكفى به نصيرًا ينصركم على أعدائكم.');
INSERT INTO Quran__Arabic_Tafseer_Muyassar (QuranChapterID, VerseNumber, Rukuh, Sajdah, Manzil, HizbQuarter, VerseText) VALUES (4, 46, '0', '0', '0', '0', 'من اليهود فريق دأبوا على تبديل كلام الله وتغييره عمَّا هو عليه افتراء على الله، ويقولون للرسول صلى الله عليه وسلم: سمعنا قولك وعصينا أمرك واسمع منَّا لا سمعت، ويقولون: راعنا سمعك أي: افهم عنا وأفهمنا، يلوون ألسنتهم بذلك، وهم يريدون الدعاء عليه بالرعونة حسب لغتهم، والطعن في دين الإسلام. ولو أنهم قالوا: سمعنا وأطعنا، بدل و"عصينا"، واسمع دون "غير مسمع"، وانظرنا بدل "راعنا" لكان ذلك خيرًا لهم عند الله وأعدل قولا ولكن الله طردهم من رحمته؛ بسبب كفرهم وجحودهم نبوة محمد صلى الله عليه وسلم، فلا يصدقون بالحق إلا تصديقًا قليلا لا ينفعهم.');
INSERT INTO Quran__Arabic_Tafseer_Muyassar (QuranChapterID, VerseNumber, Rukuh, Sajdah, Manzil, HizbQuarter, VerseText) VALUES (4, 47, '0', '0', '0', '0', 'يا أهل الكتاب، صدِّقوا واعملوا بما نزَّلنا من القرآن، مصدقًا لما معكم من الكتب من قبل أن نأخذكم بسوء صنيعكم، فنمحو الوجوه ونحولها قِبَلَ الظهور، أو نلعن هؤلاء المفسدين بمسخهم قردة وخنازير، كما لعنَّا اليهود مِن أصحاب السبت، الذين نُهوا عن الصيد فيه فلم ينتهوا، فغضب الله عليهم، وطردهم من رحمته، وكان أمر الله نافذًا في كل حال.');
INSERT INTO Quran__Arabic_Tafseer_Muyassar (QuranChapterID, VerseNumber, Rukuh, Sajdah, Manzil, HizbQuarter, VerseText) VALUES (4, 48, '0', '0', '0', '0', 'إن الله تعالى لا يغفر ولا يتجاوز عمَّن أشرك به أحدًا من مخلوقاته، أو كفر بأي نوع من أنواع الكفر الأكبر، ويتجاوز ويعفو عمَّا دون الشرك من الذنوب، لمن يشاء من عباده، ومن يشرك بالله غيره فقد اختلق ذنبًا عظيمًا.');
INSERT INTO Quran__Arabic_Tafseer_Muyassar (QuranChapterID, VerseNumber, Rukuh, Sajdah, Manzil, HizbQuarter, VerseText) VALUES (4, 49, '0', '0', '0', '0', 'ألم تعلم -أيها الرسول- أمر أولئك الذين يُثنون على أنفسهم وأعمالهم، ويصفونها بالطهر والبعد عن السوء؟ بل الله تعالى وحده هو الذي يثني على مَن يشاء مِن عباده، لعلمه بحقيقة أعمالهم، ولا يُنقَصون من أعمالهم شيئًا مقدار الخيط الذي يكون في شق نَواة التمرة.');
INSERT INTO Quran__Arabic_Tafseer_Muyassar (QuranChapterID, VerseNumber, Rukuh, Sajdah, Manzil, HizbQuarter, VerseText) VALUES (4, 50, '1', '0', '0', '0', 'انظر إليهم -أيها الرسول- متعجبًا من أمرهم، كيف يختلقون على الله الكذب، وهو المنزَّه عن كل ما لا يليق به؟ وكفى بهذا الاختلاق ذنبًا كبيرًا كاشفًا عن فساد معتقدهم.');
INSERT INTO Quran__Arabic_Tafseer_Muyassar (QuranChapterID, VerseNumber, Rukuh, Sajdah, Manzil, HizbQuarter, VerseText) VALUES (4, 51, '0', '0', '0', '0', 'ألم تعلم -أيها الرسول- أمر أولئك اليهود الذين أُعطوا حظًّا من العلم يصدقون بكل ما يُعبد من دون الله من الأصنام وشياطين الإنس والجن تصديقا يحملهم على التحاكم إلى غير شرع الله، ويقولون للذين كفروا بالله تعالى وبرسوله محمد صلى الله عليه وسلم: هؤلاء الكافرون أقْومُ، وأعدلُ طريقًا من أولئك الذين آمنوا؟');
INSERT INTO Quran__Arabic_Tafseer_Muyassar (QuranChapterID, VerseNumber, Rukuh, Sajdah, Manzil, HizbQuarter, VerseText) VALUES (4, 52, '0', '0', '0', '0', 'أولئك الذين كَثُرَ فسادهم وعمَّ ضلالهم، طردهم الله تعالى من رحمته، ومَن يطرده الله من رحمته فلن تجد له من ينصره، ويدفع عنه سوء العذاب.');
INSERT INTO Quran__Arabic_Tafseer_Muyassar (QuranChapterID, VerseNumber, Rukuh, Sajdah, Manzil, HizbQuarter, VerseText) VALUES (4, 53, '0', '0', '0', '0', 'بل ألهم حظ من الملك، ولو أوتوه لما أعطوا أحدًا منه شيئًا، ولو كان مقدار النقرة التي تكون في ظهر النَّواة؟');
INSERT INTO Quran__Arabic_Tafseer_Muyassar (QuranChapterID, VerseNumber, Rukuh, Sajdah, Manzil, HizbQuarter, VerseText) VALUES (4, 54, '0', '0', '0', '0', 'بل أيحسدون محمدًا صلى الله عليه وسلم على ما أعطاه الله من نعمة النبوة والرسالة، ويحسدون أصحابه على نعمة التوفيق إلى الإيمان، والتصديق بالرسالة، واتباع الرسول، والتمكين في الأرض، ويتمنون زوال هذا الفضل عنهم؟ فقد أعطينا ذرية إبراهيم عليه السلام -من قَبْلُ- الكتب، التي أنزلها الله عليهم وما أوحي إليهم مما لم يكن كتابا مقروءا، وأعطيناهم مع ذلك ملكا واسعا.');
INSERT INTO Quran__Arabic_Tafseer_Muyassar (QuranChapterID, VerseNumber, Rukuh, Sajdah, Manzil, HizbQuarter, VerseText) VALUES (4, 55, '0', '0', '0', '0', 'فمن هؤلاء الذين أوتوا حظًّا من العلم، مَن صدَّق برسالة محمد صلى الله عليه وسلم، وعمل بشرعه، ومنهم مَن أعرض ولم يستجب لدعوته، ومنع الناس من اتباعه. وحسبكم -أيها المكذبون- نار جهنم تسعَّر بكم.');
INSERT INTO Quran__Arabic_Tafseer_Muyassar (QuranChapterID, VerseNumber, Rukuh, Sajdah, Manzil, HizbQuarter, VerseText) VALUES (4, 56, '0', '0', '0', '0', 'إن الذين جحدوا ما أنزل الله من آياته ووحي كتابه ودلائله وحججه، سوف ندخلهم نارًا يقاسون حرَّها، كلما احترقت جلودهم بدَّلْناهم جلودًا أخرى؛ ليستمر عذابهم وألمهم. إن الله تعالى كان عزيزًا لا يمتنع عليه شيء، حكيمًا في تدبيره وقضائه.');
INSERT INTO Quran__Arabic_Tafseer_Muyassar (QuranChapterID, VerseNumber, Rukuh, Sajdah, Manzil, HizbQuarter, VerseText) VALUES (4, 57, '0', '0', '0', '0', 'والذين اطمأنت قلوبهم بالإيمان بالله تعالى والتصديق برسالة رسوله محمد صلى الله عليه وسلم، واستقاموا على الطاعة، سندخلهم جنات تجري من تحتها الأنهار، ينعمون فيها أبدًا ولا يخرجون منها، ولهم فيها أزواج طهرها الله مِن كل أذى، وندخلهم ظلا كثيفًا ممتدًا في الجنة.');
INSERT INTO Quran__Arabic_Tafseer_Muyassar (QuranChapterID, VerseNumber, Rukuh, Sajdah, Manzil, HizbQuarter, VerseText) VALUES (4, 58, '0', '0', '0', '1', 'إن الله تعالى يأمركم بأداء مختلف الأمانات، التي اؤتمنتم عليها إلى أصحابها، فلا تفرطوا فيها، ويأمركم بالقضاء بين الناس بالعدل والقسط، إذا قضيتم بينهم، ونِعْمَ ما يعظكم الله به ويهديكم إليه. إن الله تعالى كان سميعًا لأقوالكم، مُطَّلعًا على سائر أعمالكم، بصيرًا بها.');
INSERT INTO Quran__Arabic_Tafseer_Muyassar (QuranChapterID, VerseNumber, Rukuh, Sajdah, Manzil, HizbQuarter, VerseText) VALUES (4, 59, '1', '0', '0', '0', 'يا أيها الذين صدَّقوا الله ورسوله وعملوا بشرعه، استجيبوا لأوامر الله تعالى ولا تعصوه، واستجيبوا للرسول صلى الله عليه وسلم فيما جاء به من الحق، وأطيعوا ولاة أمركم في غير معصية الله، فإن اختلفتم في شيء بينكم، فأرجعوا الحكم فيه إلى كتاب الله تعالى وسنة رسوله محمد صلى الله عليه وسلم، إن كنتم تؤمنون حق الإيمان بالله تعالى وبيوم الحساب. ذلك الردُّ إلى الكتاب والسنة خير لكم من التنازع والقول بالرأي، وأحسن عاقبة ومآلا.');
INSERT INTO Quran__Arabic_Tafseer_Muyassar (QuranChapterID, VerseNumber, Rukuh, Sajdah, Manzil, HizbQuarter, VerseText) VALUES (4, 60, '0', '0', '0', '0', 'ألم تعلم -أيها الرسول- أمر أولئك المنافقين الذين يدَّعون الإيمان بما أُنزل إليك -وهو القرآن- وبما أُنزل إلى الرسل من قبلك، وهم يريدون أن يتحاكموا في فَصْل الخصومات بينهم إلى غير ما شرع الله من الباطل، وقد أُمروا أن يكفروا بالباطل؟ ويريد الشيطان أن يبعدهم عن طريق الحق، بعدًا شديدًا. وفي هذه الآية دليل على أن الإيمان الصادق، يقتضي الانقياد لشرع الله، والحكم به في كل أمر من الأمور، فمن زعم أنه مؤمن واختار حكم الطاغوت على حكم الله، فهو كاذب في زعمه.');
INSERT INTO Quran__Arabic_Tafseer_Muyassar (QuranChapterID, VerseNumber, Rukuh, Sajdah, Manzil, HizbQuarter, VerseText) VALUES (4, 61, '0', '0', '0', '0', 'وإذا نُصح هؤلاء، وقيل لهم: تعالوا إلى ما أنزل الله، وإلى الرسول محمد صلى الله عليه وسلم، وهديه، أبصَرْتَ الذين يظهرون الإيمان ويبطنون الكفر، يعرضون عنك إعراضًا.');
INSERT INTO Quran__Arabic_Tafseer_Muyassar (QuranChapterID, VerseNumber, Rukuh, Sajdah, Manzil, HizbQuarter, VerseText) VALUES (4, 62, '0', '0', '0', '0', 'فكيف يكون حال أولئك المناففين، إذا حلَّت بهم مصيبة بسبب ما اقترفوه بأيديهم، ثم جاؤوك -أيها الرسول- يعتذرون، ويؤكدون لك أنهم ما قصدوا بأعمالهم تلك إلا الإحسان والتوفيق بين الخصوم؟');
INSERT INTO Quran__Arabic_Tafseer_Muyassar (QuranChapterID, VerseNumber, Rukuh, Sajdah, Manzil, HizbQuarter, VerseText) VALUES (4, 63, '0', '0', '0', '0', 'أولئك هم الذين يعلم الله حقيقة ما في قلوبهم من النفاق، فتولَّ عنهم، وحذِّرهم من سوء ما هم عليه، وقل لهم قولا مؤثرًا فيهم زاجرًا لهم.');
INSERT INTO Quran__Arabic_Tafseer_Muyassar (QuranChapterID, VerseNumber, Rukuh, Sajdah, Manzil, HizbQuarter, VerseText) VALUES (4, 64, '0', '0', '0', '0', 'وما بعَثْنَا من رسول من رسلنا، إلا ليستجاب له، بأمر الله تعالى وقضائه. ولو أن هؤلاء الذين ظلموا أنفسهم باقتراف السيئات، جاؤوك -أيها الرسول- في حياتك تائبين سائلين الله أن يغفر لهم ذنوبهم، واستغفرت لهم، لوجدوا الله توابًا رحيمًا.');
INSERT INTO Quran__Arabic_Tafseer_Muyassar (QuranChapterID, VerseNumber, Rukuh, Sajdah, Manzil, HizbQuarter, VerseText) VALUES (4, 65, '0', '0', '0', '0', 'أقسم الله تعالى بنفسه الكريمة أن هؤلاء لا يؤمنون حقيقة حتى يجعلوك حكمًا فيما وقع بينهم من نزاع في حياتك، ويتحاكموا إلى سنتك بعد مماتك، ثم لا يجدوا في أنفسهم ضيقًا مما انتهى إليه حكمك، وينقادوا مع ذلك انقيادًا تاماً، فالحكم بما جاء به رسول الله صلى الله عليه وسلم من الكتاب والسنة في كل شأن من شؤون الحياة من صميم الإيمان مع الرضا والتسليم.');
INSERT INTO Quran__Arabic_Tafseer_Muyassar (QuranChapterID, VerseNumber, Rukuh, Sajdah, Manzil, HizbQuarter, VerseText) VALUES (4, 66, '0', '0', '0', '0', 'ولو أوجبنا على هؤلاء المنافقين المتحاكمين إلى الطاغوت أن يقتل بعضهم بعضًا، أو أن يخرجوا من ديارهم، ما استجاب لذلك إلا عدد قليل منهم، ولو أنهم استجابوا لما يُنصحون به لكان ذلك نافعًا لهم، وأقوى لإيمانهم، ولأعطيناهم من عندنا ثوابًا عظيمًا في الدنيا والآخرة، ولأرشدناهم ووفقناهم إلى طريق الله القويم.');
INSERT INTO Quran__Arabic_Tafseer_Muyassar (QuranChapterID, VerseNumber, Rukuh, Sajdah, Manzil, HizbQuarter, VerseText) VALUES (4, 67, '0', '0', '0', '0', 'ولو أوجبنا على هؤلاء المنافقين المتحاكمين إلى الطاغوت أن يقتل بعضهم بعضًا، أو أن يخرجوا من ديارهم، ما استجاب لذلك إلا عدد قليل منهم، ولو أنهم استجابوا لما يُنصحون به لكان ذلك نافعًا لهم، وأقوى لإيمانهم، ولأعطيناهم من عندنا ثوابًا عظيمًا في الدنيا والآخرة، ولأرشدناهم ووفقناهم إلى طريق الله القويم.');
INSERT INTO Quran__Arabic_Tafseer_Muyassar (QuranChapterID, VerseNumber, Rukuh, Sajdah, Manzil, HizbQuarter, VerseText) VALUES (4, 68, '0', '0', '0', '0', 'ولو أوجبنا على هؤلاء المنافقين المتحاكمين إلى الطاغوت أن يقتل بعضهم بعضًا، أو أن يخرجوا من ديارهم، ما استجاب لذلك إلا عدد قليل منهم، ولو أنهم استجابوا لما يُنصحون به لكان ذلك نافعًا لهم، وأقوى لإيمانهم، ولأعطيناهم من عندنا ثوابًا عظيمًا في الدنيا والآخرة، ولأرشدناهم ووفقناهم إلى طريق الله القويم.');
INSERT INTO Quran__Arabic_Tafseer_Muyassar (QuranChapterID, VerseNumber, Rukuh, Sajdah, Manzil, HizbQuarter, VerseText) VALUES (4, 69, '0', '0', '0', '0', 'ومن يستجب لأوامر الله تعالى وهدي رسوله محمد صلى الله عليه وسلم فأولئك الذين عَظُمَ شأنهم وقدرهم، فكانوا في صحبة مَن أنعم الله تعالى عليهم بالجنة من الأنبياء والصديقين الذين كمُل تصديقهم بما جاءت به الرسل، اعتقادًا وقولا وعملا والشهداء في سبيل الله وصالح المؤمنين، وحَسُنَ هؤلاء رفقاء في الجنة.');
INSERT INTO Quran__Arabic_Tafseer_Muyassar (QuranChapterID, VerseNumber, Rukuh, Sajdah, Manzil, HizbQuarter, VerseText) VALUES (4, 70, '1', '0', '0', '0', 'ذلك العطاء الجزيل من الله وحده. وكفى بالله عليما يعلم أحوال عباده، ومَن يَستحقُّ منهم الثواب الجزيل بما قام به من الأعمال الصالحة.');
INSERT INTO Quran__Arabic_Tafseer_Muyassar (QuranChapterID, VerseNumber, Rukuh, Sajdah, Manzil, HizbQuarter, VerseText) VALUES (4, 71, '0', '0', '0', '0', 'يا أيها الذين آمنوا خذوا حذركم بالاستعداد لعدوكم، فاخرجوا لملاقاته جماعة بعد جماعة أو مجتمعين.');
INSERT INTO Quran__Arabic_Tafseer_Muyassar (QuranChapterID, VerseNumber, Rukuh, Sajdah, Manzil, HizbQuarter, VerseText) VALUES (4, 72, '0', '0', '0', '0', 'وإنَّ منكم لنفرًا يتأخر عن الخروج لملاقاة الأعداء متثاقلا ويثبط غيره عن عمد وإصرار، فإن قُدِّر عليكم وأُصِبتم بقتل وهزيمة، قال مستبشرًا: قد حفظني الله، حين لم أكن حاضرًا مع أولئك الذين وقع لهم ما أكرهه لنفسي، وسرَّه تخلفه عنكم.');
INSERT INTO Quran__Arabic_Tafseer_Muyassar (QuranChapterID, VerseNumber, Rukuh, Sajdah, Manzil, HizbQuarter, VerseText) VALUES (4, 73, '0', '0', '0', '0', 'ولئن نالكم فضل من الله وغنيمة، ليقولن -حاسدًا متحسرًا، كأن لم تكن بينكم وبينه مودة في الظاهر-: يا ليتني كنت معهم فأظفر بما ظَفِروا به من النجاة والنصرة والغنيمة.');
INSERT INTO Quran__Arabic_Tafseer_Muyassar (QuranChapterID, VerseNumber, Rukuh, Sajdah, Manzil, HizbQuarter, VerseText) VALUES (4, 74, '0', '0', '0', '1', 'فليجاهد في سبيل نصرة دين الله، وإعلاء كلمته، الذين يبيعون الحياة الدنيا بالدار الآخرة وثوابها. ومن يجاهد في سبيل الله مخلصًا، فيُقْتَلْ أو يَغْلِبْ، فسوف نؤتيه أجرًا عظيمًا.');
INSERT INTO Quran__Arabic_Tafseer_Muyassar (QuranChapterID, VerseNumber, Rukuh, Sajdah, Manzil, HizbQuarter, VerseText) VALUES (4, 75, '0', '0', '0', '0', 'وما الذي يمنعكم -أيها المؤمنون- عن الجهاد في سبيل نصرة دين الله، ونصرة عباده المستضعفين من الرجال والنساء والصغار الذين اعتُدي عليهم، ولا حيلة لهم ولا وسيلة لديهم إلا الاستغاثة بربهم، يدعونه قائلين: ربنا أخرجنا من هذه القرية -يعني "مكة "- التي ظَلَم أهلها أنفسهم بالكفر والمؤمنين بالأذى، واجعل لنا من عندك وليّاً يتولى أمورنا، ونصيرًا ينصرنا على الظالمين؟');
INSERT INTO Quran__Arabic_Tafseer_Muyassar (QuranChapterID, VerseNumber, Rukuh, Sajdah, Manzil, HizbQuarter, VerseText) VALUES (4, 76, '1', '0', '0', '0', 'الذين صدَقُوا في إيمانهم اعتقادًا وعملا يجاهدون في سبيل نصرة الحق وأهله، والذين كفروا يقاتلون في سبيل البغي والفساد في الأرض، فقاتلوا أيها المؤمنون أهل الكفر والشرك الذين يتولَّون الشيطان، ويطيعون أمره، إن تدبير الشيطان لأوليائه كان ضعيفًا.');
INSERT INTO Quran__Arabic_Tafseer_Muyassar (QuranChapterID, VerseNumber, Rukuh, Sajdah, Manzil, HizbQuarter, VerseText) VALUES (4, 77, '0', '0', '0', '0', 'ألم تعلم -أيها الرسول- أمر أولئك الذين قيل لهم قبل الإذن بالجهاد: امنعوا أيديكم عن قتال أعدائكم من المشركين، وعليكم أداء ما فرضه الله عليكم من الصلاة، والزكاة، فلما فرض عليهم القتال إذا جماعة منهم قد تغير حالهم، فأصبحوا يخافون الناس ويرهبونهم، كخوفهم من الله أو أشد، ويعلنون عما اعتراهم من شدة الخوف، فيقولون: ربنا لِمَ أَوْجَبْتَ علينا القتال؟ هلا أمهلتنا إلى وقت قريب، رغبة منهم في متاع الحياة الدنيا، قل لهم -أيها الرسول-: متاع الدنيا قليل، والآخرة وما فيها أعظم وأبقى لمن اتقى، فعمل بما أُمر به، واجتنب ما نُهي عنه.، لا يظلم ربك أحدًا شيئًا، ولو كان مقدار الخيط الذي يكون في شق نَواة التمرة.');
INSERT INTO Quran__Arabic_Tafseer_Muyassar (QuranChapterID, VerseNumber, Rukuh, Sajdah, Manzil, HizbQuarter, VerseText) VALUES (4, 78, '0', '0', '0', '0', 'أينما تكونوا يلحقكم الموت في أي مكان كنتم فيه عند حلول آجالكم، ولو كنتم في حصون منيعة بعيدة عن ساحة المعارك والقتال. وإن يحصل لهم ما يسرُّهم من متاع هذه الحياة، ينسبوا حصوله إلى الله تعالى، وإن وقع عليهم ما يكرهونه ينسبوه إلى الرسول محمد صلى الله عليه وسلم جهالة وتشاؤمًا، وما علموا أن ذلك كله من عند الله وحده، بقضائه وقدره، فما بالهم لا يقاربون فَهْمَ أيِّ حديث تحدثهم به؟');
INSERT INTO Quran__Arabic_Tafseer_Muyassar (QuranChapterID, VerseNumber, Rukuh, Sajdah, Manzil, HizbQuarter, VerseText) VALUES (4, 79, '0', '0', '0', '0', 'ما أصابك -أيها الإنسان- مِن خير ونعمة فهو من الله تعالى وحده، فضلا وإحسانًا، وما أصابك من جهد وشدة فبسبب عملك السيئ، وما اقترفته يداك من الخطايا والسيئات. وبعثناك -أيها الرسول- لعموم الناس رسولا تبلغهم رسالة ربك، وكفى بالله شهيدًا على صدق رسالتك.');
INSERT INTO Quran__Arabic_Tafseer_Muyassar (QuranChapterID, VerseNumber, Rukuh, Sajdah, Manzil, HizbQuarter, VerseText) VALUES (4, 80, '0', '0', '0', '0', 'من يستجب للرسول صلى الله عليه وسلم، ويعمل بهديه، فقد استجاب لله تعالى وامتثل أمره، ومن أعرض عن طاعة الله ورسوله فما بعثناك -أيها الرسول- على هؤلاء المعترضين رقيبًا تحفظ أعمالهم وتحاسبهم عليها، فحسابهم علينا.');
INSERT INTO Quran__Arabic_Tafseer_Muyassar (QuranChapterID, VerseNumber, Rukuh, Sajdah, Manzil, HizbQuarter, VerseText) VALUES (4, 81, '0', '0', '0', '0', 'ويُظْهر هؤلاء المعرضون، وهم في مجلس رسول الله صلى الله عليه وسلم، طاعتهم للرسول وما جاء به، فإذا ابتعدوا عنه وانصرفوا عن مجلسه، دبَّر جماعة منهم ليلا غير ما أعلنوه من الطاعة، وما علموا أن الله يحصي عليهم ما يدبرون، وسيجازيهم عليه أتم الجزاء، فتول عنهم -أيها الرسول- ولا تبال بهم، فإنهم لن يضروك، وتوكل على الله، وحسبك به وليّاً وناصرًا.');
INSERT INTO Quran__Arabic_Tafseer_Muyassar (QuranChapterID, VerseNumber, Rukuh, Sajdah, Manzil, HizbQuarter, VerseText) VALUES (4, 82, '0', '0', '0', '0', 'أفلا ينظر هؤلاء في القرآن، وما جاء به من الحق، نظر تأمل وتدبر، حيث جاء على نسق محكم يقطع بأنه من عند الله وحده؟ ولو كان مِن عند غيره لوجدوا فيه اختلافًا كثيرًا.');
INSERT INTO Quran__Arabic_Tafseer_Muyassar (QuranChapterID, VerseNumber, Rukuh, Sajdah, Manzil, HizbQuarter, VerseText) VALUES (4, 83, '0', '0', '0', '0', 'وإذا جاء هؤلاء الذين لم يستقر الإيمان في قلوبهم أمْرٌ يجب كتمانه متعلقًا بالأمن الذي يعود خيره على الإسلام والمسلمين، أو بالخوف الذي يلقي في قلوبهم عدم الاطمئنان، أفشوه وأذاعوا به في الناس، ولو ردَّ هؤلاء ما جاءهم إلى رسول الله صلى الله عليه وسلم وإلى أهل العلم والفقه لَعَلِمَ حقيقة معناه أهل الاستنباط منهم. ولولا أنْ تَفَضَّلَ الله عليكم ورحمكم لاتبعتم الشيطان ووساوسه إلا قليلا منكم.');
INSERT INTO Quran__Arabic_Tafseer_Muyassar (QuranChapterID, VerseNumber, Rukuh, Sajdah, Manzil, HizbQuarter, VerseText) VALUES (4, 84, '0', '0', '0', '0', 'فجاهد -أيها النبي- في سبيل الله لإعلاء كلمته، لا تلزم فعل غيرك ولا تؤاخذ به، وحُضَّ المؤمنين على القتال والجهاد، ورغِّبهم فيه، لعل الله يمنع بك وبهم بأس الكافرين وشدتهم. والله تعالى أشد قوة وأعظم عقوبة للكافرين.');
INSERT INTO Quran__Arabic_Tafseer_Muyassar (QuranChapterID, VerseNumber, Rukuh, Sajdah, Manzil, HizbQuarter, VerseText) VALUES (4, 85, '0', '0', '0', '0', 'من يَسْعَ لحصول غيره على الخير يكن له بشفاعته نصيب من الثواب، ومن يَسْعَ لإيصال الشر إلى غيره يكن له نصيب من الوزر والإثم. وكان الله على كل شيء شاهدًا وحفيظًا.');
INSERT INTO Quran__Arabic_Tafseer_Muyassar (QuranChapterID, VerseNumber, Rukuh, Sajdah, Manzil, HizbQuarter, VerseText) VALUES (4, 86, '0', '0', '0', '0', 'وإذا سلَّم عليكم المسلم فردُّوا عليه بأفضل مما سلَّم لفظًا وبشاشةً، أو ردوا عليه بمثل ما سلَّم، ولكل ثوابه وجزاؤه. إن الله تعالى كان على كل شيء مجازيًا.');
INSERT INTO Quran__Arabic_Tafseer_Muyassar (QuranChapterID, VerseNumber, Rukuh, Sajdah, Manzil, HizbQuarter, VerseText) VALUES (4, 87, '1', '0', '0', '0', 'الله وحده المتفرد بالألوهية لجميع الخلق، ليجمعنكم يوم القيامة، الذي لا شك فيه، للحساب والجزاء. ولا أحد أصدق من الله حديثًا فيما أخبر به.');
INSERT INTO Quran__Arabic_Tafseer_Muyassar (QuranChapterID, VerseNumber, Rukuh, Sajdah, Manzil, HizbQuarter, VerseText) VALUES (4, 88, '0', '0', '0', '1', 'فما لكم -أيها المؤمنون- في شأن المنافقين إذ اختلفتم فرقتين: فرقة تقول بقتالهم وأخرى لا تقول بذلك؟ والله تعالى قد أوقعهم في الكفر والضلال بسبب سوء أعمالهم. أتودون هداية من صرف الله تعالى قلبه عن دينه؟ ومن خذله الله عن دينه، واتباع ما أمره به، فلا طريق له إلى الهدى.');
INSERT INTO Quran__Arabic_Tafseer_Muyassar (QuranChapterID, VerseNumber, Rukuh, Sajdah, Manzil, HizbQuarter, VerseText) VALUES (4, 89, '0', '0', '0', '0', 'تمنَّى المنافقون لكم أيها المؤمنون، لو تنكرون حقيقة ما آمنت به قلوبكم، مثلما أنكروه بقلوبهم، فتكونون معهم في الإنكار سواء، فلا تتخذوا منهم أصفياء لكم، حتى يهاجروا في سبيل الله، برهانًا على صدق إيمانهم، فإن أعرضوا عما دعوا إليه، فخذوهم أينما كانوا واقتلوهم، ولا تتخذوا منهم وليّاً من دون الله ولا نصيرًا تستنصرونه به.');
INSERT INTO Quran__Arabic_Tafseer_Muyassar (QuranChapterID, VerseNumber, Rukuh, Sajdah, Manzil, HizbQuarter, VerseText) VALUES (4, 90, '0', '0', '0', '0', 'لكن الذين يتصلون بقوم بينكم وبينهم عهد وميثاق فلا تقاتلوهم، وكذلك الذين أتَوا إليكم وقد ضاقت صدورهم وكرهوا أن يقاتلوكم، كما كرهوا أن يقاتلوا قومهم، فلم يكونوا معكم ولا مع قومهم، فلا تقاتلوهم، ولو شاء الله تعالى لسلَّطهم عليكم، فلقاتلوكم مع أعدائكم من المشركين، ولكن الله تعالى صرفهم عنكم بفضله وقدرته، فإن تركوكم فلم يقاتلوكم، وانقادوا اليكم مستسلمين، فليس لكم عليهم من طريق لقتالهم.');
INSERT INTO Quran__Arabic_Tafseer_Muyassar (QuranChapterID, VerseNumber, Rukuh, Sajdah, Manzil, HizbQuarter, VerseText) VALUES (4, 91, '1', '0', '0', '0', 'ستجدون قومًا آخرين من المنافقين يودون الاطمئنان على أنفسهم من جانبكم، فيظهرون لكم الإيمان، ويودون الاطمئنان على أنفسهم من جانب قومهم الكافرين، فيظهرون لهم الكفر، كلما أعيدوا إلى موطن الكفر والكافرين، وقعوا في أسوأ حال. فهؤلاء إن لم ينصرفوا عنكم، ويقدموا إليكم الاستسلام التام، ويمنعوا أنفسهم عن قتالكم فخذوهم بقوة واقتلوهم أينما كانوا، وأولئك الذين بلغوا في هذا المسلك السيِّئ حدّاً يميزهم عمَّن عداهم، فهم الذين جعلنا لكم الحجة البينة على قتلهم وأسرهم.');
INSERT INTO Quran__Arabic_Tafseer_Muyassar (QuranChapterID, VerseNumber, Rukuh, Sajdah, Manzil, HizbQuarter, VerseText) VALUES (4, 92, '0', '0', '0', '0', 'ولا يحق لمؤمن الاعتداء على أخيه المؤمن وقتله بغير حق، إلا أن يقع منه ذلك على وجه الخطأ الذي لا عمد فيه، ومن وقع منه ذلك الخطأ فعليه عتق رقبة مؤمنة، وتسليم دية مقدرة إلى أوليائه، إلا أن يتصدقوا بها عليه ويعفوا عنه. فإن كان المقتول من قوم كفار أعداء للمؤمنين، وهو مؤمن بالله تعالى، وبما أنزل من الحق على رسوله محمد صلى الله عليه وسلم، فعلى قاتله عتق رقبة مؤمنة، وإن كان من قوم بينكم وبينهم عهد وميثاق، فعلى قاتله دية تسلم إلى أوليائه وعتق رقبة مؤمنة، فمن لم يجد القدرة على عتق رقبة مؤمنة، فعليه صيام شهرين متتابعين؛ ليتوب الله تعالى عليه. وكان الله تعالى عليما بحقيقة شأن عباده، حكيمًا فيما شرعه لهم.');
INSERT INTO Quran__Arabic_Tafseer_Muyassar (QuranChapterID, VerseNumber, Rukuh, Sajdah, Manzil, HizbQuarter, VerseText) VALUES (4, 93, '0', '0', '0', '0', 'ومن يَعْتَدِ على مؤمن فيقتله عن عمد بغير حق فعاقبته جهنم، خالدًا فيها مع سخط الله تعالى عليه وطَرْدِهِ من رحمته، إن جازاه على ذنبه وأعدَّ الله له أشد العذاب بسبب ما ارتكبه من هذه الجناية العظيمة. ولكنه سبحانه يعفو ويتفضل على أهل الإيمان فلا يجازيهم بالخلود في جهنم.');
INSERT INTO Quran__Arabic_Tafseer_Muyassar (QuranChapterID, VerseNumber, Rukuh, Sajdah, Manzil, HizbQuarter, VerseText) VALUES (4, 94, '0', '0', '0', '0', 'يا أيها الذين صدَّقوا الله ورسوله وعملوا بشرعه إذا خرجتم في الأرض مجاهدين في سبيل الله فكونوا على بينة مما تأتون وتتركون، ولا تنفوا الإيمان عمن بدا منه شيء من علامات الإسلام ولم يقاتلكم؛ لاحتمال أن يكون مؤمنًا يخفي إيمانه، طالبين بذلك متاع الحياة الدنيا، والله تعالى عنده من الفضل والعطاء ما يغنيكم به، كذلك كنتم في بدء الإسلام تخفون إيمانكم عن قومكم من المشركين فمَنَّ الله عليكم، وأعزَّكم بالإيمان والقوة، فكونوا على بيِّنة ومعرفة في أموركم. إن الله تعالى عليم بكل أعمالكم، مطَّلع على دقائق أموركم، وسيجازيكم عليها.');
INSERT INTO Quran__Arabic_Tafseer_Muyassar (QuranChapterID, VerseNumber, Rukuh, Sajdah, Manzil, HizbQuarter, VerseText) VALUES (4, 95, '0', '0', '0', '0', 'لا يتساوى المتخلفون عن الجهاد في سبيل الله -غير أصحاب الأعذار منهم- والمجاهدون في سبيل الله، بأموالهم وأنفسهم، فضَّل الله تعالى المجاهدين على القاعدين، ورفع منزلتهم درجة عالية في الجنة، وقد وعد الله كلا من المجاهدين بأموالهم وأنفسهم والقاعدين من أهل الأعذار الجنة لِما بذلوا وضحَّوا في سبيل الحق، وفضَّل الله تعالى المجاهدين على القاعدين ثوابًا جزيلا.');
INSERT INTO Quran__Arabic_Tafseer_Muyassar (QuranChapterID, VerseNumber, Rukuh, Sajdah, Manzil, HizbQuarter, VerseText) VALUES (4, 96, '1', '0', '0', '0', 'هذا الثواب الجزيل منازل عالية في الجنات من الله تعالى لخاصة عباده المجاهدين في سبيله، ومغفرة لذنوبهم ورحمة واسعة ينعمون فيها. وكان الله غفورًا لمن تاب إليه وأناب، رحيمًا بأهل طاعته، المجاهدين في سبيله.');
INSERT INTO Quran__Arabic_Tafseer_Muyassar (QuranChapterID, VerseNumber, Rukuh, Sajdah, Manzil, HizbQuarter, VerseText) VALUES (4, 97, '0', '0', '0', '0', 'إن الذين توفَّاهم الملائكة وقد ظلموا أنفسهم بقعودهم في دار الكفر وترك الهجرة، تقول لهم الملائكة توبيخًا لهم: في أي شيء كنتم من أمر دينكم؟ فيقولون: كنا ضعفاء في أرضنا، عاجزين عن دفع الظلم والقهر عنا، فيقولون لهم توبيخا: ألم تكن أرض الله واسعة فتخرجوا من أرضكم إلى أرض أخرى بحيث تأمنون على دينكم؟ فأولئك مثواهم النار، وقبح هذا المرجع والمآب.');
INSERT INTO Quran__Arabic_Tafseer_Muyassar (QuranChapterID, VerseNumber, Rukuh, Sajdah, Manzil, HizbQuarter, VerseText) VALUES (4, 98, '0', '0', '0', '0', 'ويعذر من ذاك المصير العجزة من الرجال والنساء والصغار الذين لا يقدرون على دفع القهر والظلم عنهم، ولا يعرفون طريقًا يخلصهم مما هم فيه من المعاناة.');
INSERT INTO Quran__Arabic_Tafseer_Muyassar (QuranChapterID, VerseNumber, Rukuh, Sajdah, Manzil, HizbQuarter, VerseText) VALUES (4, 99, '0', '0', '0', '0', 'فهؤلاء الضعفاء هم الذين يُرجى لهم من الله تعالى العفو؛ لعلمه تعالى بحقيقة أمرهم. وكان الله عفوًا غفورًا.');
INSERT INTO Quran__Arabic_Tafseer_Muyassar (QuranChapterID, VerseNumber, Rukuh, Sajdah, Manzil, HizbQuarter, VerseText) VALUES (4, 100, '1', '0', '0', '1', 'ومَن يخرج من أرض الشرك إلى أرض الإسلام فرارًا بدينه، راجيًا فضل ربه، قاصدًا نصرة دينه، يجد في الأرض مكانًا ومتحولا ينعم فيه بما يكون سببًا في قوته وذلة أعدائه، مع السعة في رزقه وعيشه، ومن يخرج من بيته قاصدًا نصرة دين الله ورسوله صلى الله عليه وسلم، وإعلاء كلمة الله، ثم يدركه الموت قبل بلوغه مقصده، فقد ثبت له جزاء عمله على الله، فضلا منه وإحسانًا. وكان الله غفورًا رحيمًا بعباده.');
INSERT INTO Quran__Arabic_Tafseer_Muyassar (QuranChapterID, VerseNumber, Rukuh, Sajdah, Manzil, HizbQuarter, VerseText) VALUES (4, 101, '0', '0', '0', '0', 'وإذا سافرتم -أيها المؤمنون- في أرض الله، فلا حرج ولا إثم عليكم في قصر الصلاة إن خفتم من عدوان الكفار عليكم في حال صلاتكم، وكانت غالب أسفار المسلمين في بدء الإسلام مخوفة، والقصر رخصة في السفر حال الأمن أو الخوف. إن الكافرين مجاهرون لكم بعداوتهم، فاحذروهم.');
INSERT INTO Quran__Arabic_Tafseer_Muyassar (QuranChapterID, VerseNumber, Rukuh, Sajdah, Manzil, HizbQuarter, VerseText) VALUES (4, 102, '0', '0', '0', '0', 'وإذا كنت -أيها النبي- في ساحة القتال، فأردت أن تصلي بهم، فلتقم جماعة منهم معك للصلاة، وليأخذوا سلاحهم، فإذا سجد هؤلاء فلتكن الجماعة الأخرى من خلفكم في مواجهة عدوكم، وتتم الجماعة الأولى ركعتهم الثانية ويُسلِّمون، ثم تأتي الجماعة الأخرى التي لم تبدأ الصلاة فليأتموا بك في ركعتهم الأولى، ثم يكملوا بأنفسهم ركعتهم الثانية، وليحذروا مِن عدوهم وليأخذوا أسلحتهم. ودَّ الجاحدون لدين الله أن تغفُلوا عن سلاحكم وزادكم؛ ليحملوا عليكم حملة واحلة فيقضوا عليكم، ولا إثم عليكم حيننذ إن كان بكم أذى من مطر، أو كنتم في حال مرض، أن تتركوا أسلحتكم، مع أخذ الحذر. إن الله تعالى أعدَّ للجاحدين لدينه عذابًا يهينهم، ويخزيهم.');
INSERT INTO Quran__Arabic_Tafseer_Muyassar (QuranChapterID, VerseNumber, Rukuh, Sajdah, Manzil, HizbQuarter, VerseText) VALUES (4, 103, '0', '0', '0', '0', 'فإذا أدَّيتم الصلاة، فأديموا ذكر الله في جميع أحوالكم، فإذا زال الخوف فأدُّوا الصلاة كاملة، ولا تفرِّطوا فيها فإنها واجبة في أوقات معلومة في الشرع.');
INSERT INTO Quran__Arabic_Tafseer_Muyassar (QuranChapterID, VerseNumber, Rukuh, Sajdah, Manzil, HizbQuarter, VerseText) VALUES (4, 104, '1', '0', '0', '0', 'ولا تضعفوا في طلب عدوكم وقتاله، إن تكونوا تتألمون من القتال وآثاره، فأعداؤكم كذلك يتألمون منه أشد الألم، ومع ذلك لا يكفون عن قتالكم، فأنتم أولى بذلك منهم، لما ترجونه من الثواب والنصر والتأييد، وهم لا يرجون ذلك. وكان الله عليمًا بكل أحوالكم، حكيمًا في أمره وتدبيره.');
INSERT INTO Quran__Arabic_Tafseer_Muyassar (QuranChapterID, VerseNumber, Rukuh, Sajdah, Manzil, HizbQuarter, VerseText) VALUES (4, 105, '0', '0', '0', '0', 'إنا أنزلنا إليك -أيها الرسول- القرآن مشتملا على الحق؛ لتفصل بين الناس جميعًا بما أوحى الله إليك، وبَصَّرك به، فلا تكن للذين يخونون أنفسهم -بكتمان الحق- مدافعًا عنهم بما أيدوه لك من القول المخالف للحقيقة.');
INSERT INTO Quran__Arabic_Tafseer_Muyassar (QuranChapterID, VerseNumber, Rukuh, Sajdah, Manzil, HizbQuarter, VerseText) VALUES (4, 106, '0', '0', '0', '0', 'واطلب من الله تعالى المغفرة في جميع أحوالك، إن الله تعالى كان غفورًا لمن يرجو فضله ونوال مغفرته، رحيمًا به.');
INSERT INTO Quran__Arabic_Tafseer_Muyassar (QuranChapterID, VerseNumber, Rukuh, Sajdah, Manzil, HizbQuarter, VerseText) VALUES (4, 107, '0', '0', '0', '0', 'ولا تدافع عن الذين يخونون أنفسهم بمعصية الله. إن الله -سبحانه- لا يحب مَن عَظُمَتْ خيانته، وكثر ذنبه.');
INSERT INTO Quran__Arabic_Tafseer_Muyassar (QuranChapterID, VerseNumber, Rukuh, Sajdah, Manzil, HizbQuarter, VerseText) VALUES (4, 108, '0', '0', '0', '0', 'يستترون من الناس خوفًا من اطلاعهم على أعمالهم السيئة، ولا يستترون من الله تعالى ولا يستحيون منه، وهو عزَّ شأنه معهم بعلمه، مطلع عليهم حين يدبِّرون -ليلا- ما لا يرضى من القول، وكان الله -تعالى- محيطًا بجميع أقوالهم وأفعالهم، لا يخفى عليه منها شيء.');
INSERT INTO Quran__Arabic_Tafseer_Muyassar (QuranChapterID, VerseNumber, Rukuh, Sajdah, Manzil, HizbQuarter, VerseText) VALUES (4, 109, '0', '0', '0', '0', 'ها أنتم -أيها المؤمنون- قد حاججتم عن هؤلاء الخائنين لأنفسهم في هذه الحياة الدنيا، فمن يحاجج الله تعالى عنهم يوم البعث والحساب؟ ومن ذا الذي يكون على هؤلاء الخائنين وكيلا يوم القيامة؟');
INSERT INTO Quran__Arabic_Tafseer_Muyassar (QuranChapterID, VerseNumber, Rukuh, Sajdah, Manzil, HizbQuarter, VerseText) VALUES (4, 110, '0', '0', '0', '0', 'ومن يُقْدِمْ على عمل سيِّئ قبيح، أو يظلم نفسه بارتكاب ما يخالف حكم الله وشرعه، ثم يرجع إلى الله نادمًا على ما عمل، راجيًا مغفرته وستر ذنبه، يجد الله تعالى غفورًا له، رحيمًا به.');
INSERT INTO Quran__Arabic_Tafseer_Muyassar (QuranChapterID, VerseNumber, Rukuh, Sajdah, Manzil, HizbQuarter, VerseText) VALUES (4, 111, '0', '0', '0', '0', 'ومن يعمد إلى ارتكاب ذنب فإنما يضر بذلك نفسه وحدها، وكان الله تعالى عليمًا بحقيقة أمر عباده، حكيمًا فيما يقضي به بين خلقه.');
INSERT INTO Quran__Arabic_Tafseer_Muyassar (QuranChapterID, VerseNumber, Rukuh, Sajdah, Manzil, HizbQuarter, VerseText) VALUES (4, 112, '1', '0', '0', '0', 'ومن يعمل خطيئة بغير عمد، أو يرتكب ذنبًا متعمدًا ثم يقذف بما ارتكبه نفسًا بريئة لا جناية لها، فقد تحمَّل كذبًا وذنبًا بيّنا.');
INSERT INTO Quran__Arabic_Tafseer_Muyassar (QuranChapterID, VerseNumber, Rukuh, Sajdah, Manzil, HizbQuarter, VerseText) VALUES (4, 113, '0', '0', '0', '0', 'ولولا أن الله تعالى قد مَنَّ عليك -أيها الرسول- ورحمك بنعمة النبوة، فعصمك بتوفيقه بما أوحى إليك، لعزمت جماعة من الذين يخونون أنفسهم أن يُزِلُّوكَ عن طريق الحق، وما يُزِلُّونَ بذلك إلا أنفسهم، وما يقدرون على إيذائك لعصمة الله لك، وأنزل الله عليك القرآن والسنة المبينة له، وهداك إلى علم ما لم تكن تعلمه مِن قبل، وكان ما خصَّك الله به من فضلٍ أمرًا عظيمًا.');
INSERT INTO Quran__Arabic_Tafseer_Muyassar (QuranChapterID, VerseNumber, Rukuh, Sajdah, Manzil, HizbQuarter, VerseText) VALUES (4, 114, '0', '0', '0', '1', 'لا نفع في كثير من كلام الناس سرّاً فيما بينهم، إلا إذا كان حديثًا داعيًا إلى بذل المعروف من الصدقة، أو الكلمة الطيبة، أو التوفيق بين الناس، ومن يفعل تلك الأمور طلبًا لرضا الله تعالى راجيًا ثوابه، فسوف نؤتيه ثوابًا جزيلا واسعًا.');
INSERT INTO Quran__Arabic_Tafseer_Muyassar (QuranChapterID, VerseNumber, Rukuh, Sajdah, Manzil, HizbQuarter, VerseText) VALUES (4, 115, '1', '0', '0', '0', 'ومن يخالف الرسول صلى الله عليه وسلم من بعد ما ظهر له الحق، ويسلك طريقًا غير طريق المؤمنين، وما هم عليه من الحق، نتركه وما توجَّه إليه، فلا نوفقه للخير، وندخله نار جهنم يقاسي حرَّها، وبئس هذا المرجع والمآل.');
INSERT INTO Quran__Arabic_Tafseer_Muyassar (QuranChapterID, VerseNumber, Rukuh, Sajdah, Manzil, HizbQuarter, VerseText) VALUES (4, 116, '0', '0', '0', '0', 'إن الله تعالى لا يغفر أن يشرك به، ويغفر ما دون الشرك من الذنوب لمن يشاء من عباده. ومن يجعل لله تعالى الواحد الأحد شريكًا من خلقه، فقد بَعُدَ عن الحق بعدًا كبيرًا.');
INSERT INTO Quran__Arabic_Tafseer_Muyassar (QuranChapterID, VerseNumber, Rukuh, Sajdah, Manzil, HizbQuarter, VerseText) VALUES (4, 117, '0', '0', '0', '0', 'ما يعبد المشركون من دون الله تعالى إلا أوثانًا لا تنفع ولا تضر، وما يعبدون إلا شيطانًا متمردًا على الله، بلغ في الفساد والإفساد حدّاً كبيرًا.');
INSERT INTO Quran__Arabic_Tafseer_Muyassar (QuranChapterID, VerseNumber, Rukuh, Sajdah, Manzil, HizbQuarter, VerseText) VALUES (4, 118, '0', '0', '0', '0', 'طرده الله تعالى من رحمته. وقال الشيطان: لأتخذن مِن عبادك جزءًا معلومًا في إغوائهم قولا وعملا.');
INSERT INTO Quran__Arabic_Tafseer_Muyassar (QuranChapterID, VerseNumber, Rukuh, Sajdah, Manzil, HizbQuarter, VerseText) VALUES (4, 119, '0', '0', '0', '0', 'ولأصرفَنَّ مَن تبعني منهم عن الحق، ولأعِدَنَّهم بالأماني الكاذبة، ولأدعونَّهم إلى تقطيع آذان الأنعام وتشقيقها لما أزينه لهم من الباطل، ولأدعونَّهم إلى تغيير خلق الله في الفطرة، وهيئة ما عليه الخلق. ومن يستجب للشيطان ويتخذه ناصرًا له من دون الله القوي العزيز، فقد هلك هلاكًا بيِّنًا.');
INSERT INTO Quran__Arabic_Tafseer_Muyassar (QuranChapterID, VerseNumber, Rukuh, Sajdah, Manzil, HizbQuarter, VerseText) VALUES (4, 120, '0', '0', '0', '0', 'يعد الشيطان أتباعه بالوعود الكاذبة، ويغريهم بالأماني الباطلة الخادعة، وما يَعِدهم إلا خديعة لا صحة لها، ولا دليل عليها.');
INSERT INTO Quran__Arabic_Tafseer_Muyassar (QuranChapterID, VerseNumber, Rukuh, Sajdah, Manzil, HizbQuarter, VerseText) VALUES (4, 121, '0', '0', '0', '0', 'أولئك مآلهم جهنم، ولا يجدون عنها معدلا ولا ملجأً.');
INSERT INTO Quran__Arabic_Tafseer_Muyassar (QuranChapterID, VerseNumber, Rukuh, Sajdah, Manzil, HizbQuarter, VerseText) VALUES (4, 122, '0', '0', '0', '0', 'والذين صَدَقوا في إيمانهم بالله تعالى، وأتبعوا الإيمان بالأعمال الصالحة سيدخلهم الله -بفضله- جنات تجري من تحت أشجارها الأنهار ماكثين فيها أبدًا، وعدا من الله تعالى الذي لا يخلف وعده. ولا أحد أصدق من الله تعالى في قوله ووعده.');
INSERT INTO Quran__Arabic_Tafseer_Muyassar (QuranChapterID, VerseNumber, Rukuh, Sajdah, Manzil, HizbQuarter, VerseText) VALUES (4, 123, '0', '0', '0', '0', 'لا يُنال هذا الفضل العظيم بالأماني التي تتمنونها أيها المسلمون، ولا بأماني أهل الكتاب من اليهود والنصارى، وإنما يُنال بالإيمان الصادق بالله تعالى، وإحسان العمل الذي يرضيه. ومن يعمل عملا سيئًا يجز به، ولا يجد له سوى الله تعالى وليّاً يتولى أمره وشأنه، ولا نصيرًا ينصره، ويدفع عنه سوء العذاب.');
INSERT INTO Quran__Arabic_Tafseer_Muyassar (QuranChapterID, VerseNumber, Rukuh, Sajdah, Manzil, HizbQuarter, VerseText) VALUES (4, 124, '0', '0', '0', '0', 'ومن يعمل من الأعمال الصالحة من ذكر أو أنثى، وهو مؤمن بالله تعالى وبما أنزل من الحق، فأولئك يدخلهم الله الجنة دار النعيم المقيم، ولا يُنْقَصون من ثواب أعمالهم شيئًا، ولو كان مقدار النقرة في ظهر النواة.');
INSERT INTO Quran__Arabic_Tafseer_Muyassar (QuranChapterID, VerseNumber, Rukuh, Sajdah, Manzil, HizbQuarter, VerseText) VALUES (4, 125, '0', '0', '0', '0', 'لا أحد أحسن دينًا ممن انقاد بقلبه وسائر جوارحه لله تعالى وحده، وهو محسن، واتبع دين إبراهيم وشرعه، مائلا عن العقائد الفاسدة والشرائع الباطلة. وقد اصطفى الله إبراهيم -عليه الصلاة والسلام- واتخذه صفيّاً من بين سائر خلقه. وفي هذه الآية، إثبات صفة الخُلّة لله -تعالى- وهي أعلى مقامات المحبة، والاصطفاء.');
INSERT INTO Quran__Arabic_Tafseer_Muyassar (QuranChapterID, VerseNumber, Rukuh, Sajdah, Manzil, HizbQuarter, VerseText) VALUES (4, 126, '1', '0', '0', '0', 'ولله جميع ما في هذا الكون من المخلوقات، فهي ملك له تعالى وحده. وكان الله تعالى بكل شيء محيطًا، لا يخفى عليه شيء من أمور خلقه.');
INSERT INTO Quran__Arabic_Tafseer_Muyassar (QuranChapterID, VerseNumber, Rukuh, Sajdah, Manzil, HizbQuarter, VerseText) VALUES (4, 127, '0', '0', '0', '0', 'يطلب الناس منك -أيها النبي- أن تبين لهم ما أشكل عليهم فَهْمُه من قضايا النساء وأحكامهن، قل الله تعالى يبيِّن لكم أمورهن، وما يتلى عليكم في الكتاب في يتامى النساء اللاتي لا تعطونهن ما فرض الله تعالى لهن من المهر والميراث وغير ذلك من الحقوق، وتحبون نكاحهن أو ترغبون عن نكاحهن، ويبيِّن الله لكم أمر الضعفاء من الصغار، ووجوب القيام لليتامى بالعدل وترك الجور عليهم في حقوقهم. وما تفعلوا من خير فإن الله تعالى كان به عليمًا، لا يخفى عليه شيء منه ولا من غيره.');
INSERT INTO Quran__Arabic_Tafseer_Muyassar (QuranChapterID, VerseNumber, Rukuh, Sajdah, Manzil, HizbQuarter, VerseText) VALUES (4, 128, '0', '0', '0', '0', 'وإن علمت امرأة من زوجها ترفعًا عنها، وتعاليًا عليها أو انصرافًا عنها فلا إثم عليهما أن يتصالحا على ما تطيب به نفوسهما من القسمة أو النفقة، والصلح أولى وأفضل. وجبلت النفوس على الشح والبخل. وإن تحسنوا معاملة زوجاتكم وتخافوا الله فيهن، فإن الله كان بما تعملون من ذلك وغيره عالمًا لا يخفى عليه شيء، وسيجازيكم على ذلك.');
INSERT INTO Quran__Arabic_Tafseer_Muyassar (QuranChapterID, VerseNumber, Rukuh, Sajdah, Manzil, HizbQuarter, VerseText) VALUES (4, 129, '0', '0', '0', '0', 'ولن تقدروا -أيها الرجال- على تحقيق العدل التام بين النساء في المحبة وميل القلب، مهما بذلتم في ذلك من الجهد، فلا تعرضوا عن المرغوب عنها كل الإعراض، فتتركوها كالمرأة التي ليست بذات زوج ولا هي مطلقة فتأثموا. وإن تصلحوا أعمالكم فتعدلوا في قَسْمكم بين زوجاتكم، وتراقبوا الله تعالى وتخشوه فيهن، فإن الله تعالى كان غفورًا لعباده، رحيمًا بهم.');
INSERT INTO Quran__Arabic_Tafseer_Muyassar (QuranChapterID, VerseNumber, Rukuh, Sajdah, Manzil, HizbQuarter, VerseText) VALUES (4, 130, '0', '0', '0', '0', 'وإن وقعت الفرقة بين الرجل وامرأته، فإن الله تعالى يغني كلا منهما من فضله وسعته؛ فإنه سبحانه وتعالى واسع الفضل والمنة، حكيم فيما يقضي به بين عباده.');
INSERT INTO Quran__Arabic_Tafseer_Muyassar (QuranChapterID, VerseNumber, Rukuh, Sajdah, Manzil, HizbQuarter, VerseText) VALUES (4, 131, '0', '0', '0', '0', 'ولله ملك ما في السموات وما في الأرض وما بينهما. ولقد عهدنا إلى الذين أُعطوا الكتاب من قبلكم من اليهود والنصارى، وعهدنا إليكم كذلك -يا أمة محمد- بتقوى الله تعالى، والقيام بأمره واجتناب نهيه، وبيَّنَّا لكم أنكم إن تجحدوا وحدانية الله تعالى وشرعه فإنه سبحانه غني عنكم؛ لأن له جميع ما في السموات والأرض. وكان الله غنيّاً عن خلقه، حميدًا في صفاته وأفعاله.');
INSERT INTO Quran__Arabic_Tafseer_Muyassar (QuranChapterID, VerseNumber, Rukuh, Sajdah, Manzil, HizbQuarter, VerseText) VALUES (4, 132, '0', '0', '0', '0', 'ولله ملك ما في هذا الكون من الكائنات، وكفى به سبحانه قائمًا بشؤون خلقه حافظًا لها.');
INSERT INTO Quran__Arabic_Tafseer_Muyassar (QuranChapterID, VerseNumber, Rukuh, Sajdah, Manzil, HizbQuarter, VerseText) VALUES (4, 133, '0', '0', '0', '0', 'إن يشأ الله يُهلكُّم أيها الناس، ويأت بقوم آخرين غيركم. وكان الله على ذلك قديرًا.');
INSERT INTO Quran__Arabic_Tafseer_Muyassar (QuranChapterID, VerseNumber, Rukuh, Sajdah, Manzil, HizbQuarter, VerseText) VALUES (4, 134, '1', '0', '0', '0', 'من يرغب منكم -أيها الناس- في ثواب الدنيا ويعرض عن الآخرة، فعند الله وحده ثواب الدنيا والآخرة، فليطلب من الله وحده خيري الدنيا والآخرة، فهو الذي يملكهما. وكان الله سميعًا لأقوال عباده، بصيرًا بأعمالهم ونياتهم، وسيجازيهم على ذلك.');
INSERT INTO Quran__Arabic_Tafseer_Muyassar (QuranChapterID, VerseNumber, Rukuh, Sajdah, Manzil, HizbQuarter, VerseText) VALUES (4, 135, '0', '0', '0', '1', 'يا أيها الذين صدَّقوا الله ورسوله وعملوا بشرعه، كونوا قائمين بالعدل، مؤدين للشهادة لوجه الله تعالى، ولو كانت على أنفسكم، أو على آبائكم وأمهاتكم، أو على أقاربكم، مهما كان شأن المشهود عليه غنيًّا أو فقيرًا؛ فإن الله تعالى أولى بهما منكم، وأعلم بما فيه صلاحهما، فلا يحملنَّكم الهوى والتعصب على ترك العدل، وإن تحرفوا الشهادة بألسنتكم فتأتوا بها على غير حقيقتها، أو تعرضوا عنها بترك أدائها أو بكتمانها، فإن الله تعالى كان عليمًا بدقائق أعمالكم، وسيجازيكم بها.');
INSERT INTO Quran__Arabic_Tafseer_Muyassar (QuranChapterID, VerseNumber, Rukuh, Sajdah, Manzil, HizbQuarter, VerseText) VALUES (4, 136, '0', '0', '0', '0', 'يا أيها الذين صدَّقوا الله ورسوله وعملوا بشرعه داوموا على ما أنتم عليه من التصديق الجازم بالله تعالى وبرسوله محمد صلى الله عليه وسلم، ومن طاعتهما، وبالقرآن الذي نزله عليه، وبجميع الكتب التي أنزلها الله على الرسل. ومن يكفر بالله تعالى، وملائكته المكرمين، وكتبه التي أنزلها لهداية خلقه، ورسله الذين اصطفاهم لتبليغ رسالته، واليوم الآخر الذي يقوم الناس فيه بعد موتهم للعرض والحساب، فقد خرج من الدين، وبَعُدَ بعدًا كبيرًا عن طريق الحق.');
INSERT INTO Quran__Arabic_Tafseer_Muyassar (QuranChapterID, VerseNumber, Rukuh, Sajdah, Manzil, HizbQuarter, VerseText) VALUES (4, 137, '0', '0', '0', '0', 'إن الذين دخلوا في الإيمان، ثم رجعوا عنه إلى الكفر، ثم عادوا إلى الإيمان، ثم رجعوا إلى الكفر مرة أخرى، ثم أصرُّوا على كفرهم واستمروا عليه، لم يكن الله ليغفر لهم، ولا ليدلهم على طريق من طرق الهداية، التي ينجون بها من سوء العاقبة.');
INSERT INTO Quran__Arabic_Tafseer_Muyassar (QuranChapterID, VerseNumber, Rukuh, Sajdah, Manzil, HizbQuarter, VerseText) VALUES (4, 138, '0', '0', '0', '0', 'بشّر -أيها الرسول- المنافقين -وهم الذين يظهرون الإيمان ويبطنون الكفر- بأن لهم عذابًا موجعًا.');
INSERT INTO Quran__Arabic_Tafseer_Muyassar (QuranChapterID, VerseNumber, Rukuh, Sajdah, Manzil, HizbQuarter, VerseText) VALUES (4, 139, '0', '0', '0', '0', 'الذين يوالون الكافرين، ويتخذونهم أعوانًا لهم، ويتركون ولاية المؤمنين، ولا يرغبون في مودتهم. أيطلبون بذلك النصرة والمنعة عند الكافرين؟ إنهم لا يملكون ذلك، فالنصرة والعزة والقوة جميعها لله تعالى وحده.');
INSERT INTO Quran__Arabic_Tafseer_Muyassar (QuranChapterID, VerseNumber, Rukuh, Sajdah, Manzil, HizbQuarter, VerseText) VALUES (4, 140, '0', '0', '0', '0', 'وقد نزل عليكم -أيها المؤمنون- في كتاب ربكم أنه إذا سمعتم الكفر بآيات الله والاستهزاء بها فلا تجلسوا مع الكافرين والمستهزئين، إلا إذا أخذوا في حديث غير حديث الكفر والاستهزاء بآيات الله. إنكم إذا جالستموهم، وهم على ما هم عليه، فأنتم مثلهم؛ لأنكم رضيتم بكفرهم واستهزائهم، والراضي بالمعصية كالفاعل لها. إن الله تعالى جامع المنافقين والكافرين في نار جهنم جميعًا، يلْقَون فيها سوء العذاب.');
INSERT INTO Quran__Arabic_Tafseer_Muyassar (QuranChapterID, VerseNumber, Rukuh, Sajdah, Manzil, HizbQuarter, VerseText) VALUES (4, 141, '1', '0', '0', '0', 'المنافقون هم الذين ينتظرون ما يحلُّ بكم -أيها المؤمنون- من الفتن والحرب، فإن منَّ الله عليكم بفضله، ونصركم على عدوكم وغنمتم، قالوا لكم: ألم نكن معكم نؤازركم؟ وإن كان للجاحدين لهذا الدين قَدْرٌ من النصر والغنيمة، قالوا لهم: ألم نساعدكم بما قدَّمناه لكم ونَحْمِكُم من المؤمنين؟ فالله تعالى يقضي بينكم وبينهم يوم القيامة، ولن يجعل الله للكافرين طريقًا للغلبة على عباده الصالحين، فالعاقبة للمتقين في الدنيا والآخرة.');
INSERT INTO Quran__Arabic_Tafseer_Muyassar (QuranChapterID, VerseNumber, Rukuh, Sajdah, Manzil, HizbQuarter, VerseText) VALUES (4, 142, '0', '0', '0', '0', 'إنَّ طريقة هؤلاء المنافقين مخادعة الله تعالى، بما يظهرونه من الإيمان وما يبطنونه من الكفر، ظنًّا أنه يخفى على الله، والحال أن الله خادعهم ومجازيهم بمثل عملهم، وإذا قام هؤلاء المنافقون لأداء الصلاة، قاموا إليها في فتور، يقصدون بصلاتهم الرياء والسمعة، ولا يذكرون الله تعالى إلا ذكرًا قليلا.');
INSERT INTO Quran__Arabic_Tafseer_Muyassar (QuranChapterID, VerseNumber, Rukuh, Sajdah, Manzil, HizbQuarter, VerseText) VALUES (4, 143, '0', '0', '0', '0', 'إنَّ مِن شأن هؤلاء المنافقين التردد والحَيْرة والاضطراب، لا يستقرون على حال، فلا هم مع المؤمنين ولا هم مع الكافرين. ومن يصرف الله قلبه عن الإيمان به والاستمساك بهديه، فلن تجد له طريقًا إلى الهداية واليقين.');
INSERT INTO Quran__Arabic_Tafseer_Muyassar (QuranChapterID, VerseNumber, Rukuh, Sajdah, Manzil, HizbQuarter, VerseText) VALUES (4, 144, '0', '0', '0', '0', 'يا أيها الذين صدَّقوا الله ورسوله وعملوا بشرعه، لا توالوا الجاحدين لدين الله، وتتركوا موالاة المؤمنين ومودتهم. أتريدون بمودَّة أعدائكم أن تجعلوا لله تعالى عليكم حجة ظاهرة على عدم صدقكم في إيمانكم؟');
INSERT INTO Quran__Arabic_Tafseer_Muyassar (QuranChapterID, VerseNumber, Rukuh, Sajdah, Manzil, HizbQuarter, VerseText) VALUES (4, 145, '0', '0', '0', '0', 'إن المنافقين في أسفل منازل النار يوم القيامة، ولن تجد لهم -أيها الرسول- ناصرًا يدفع عنهم سوء هذا المصير.');
INSERT INTO Quran__Arabic_Tafseer_Muyassar (QuranChapterID, VerseNumber, Rukuh, Sajdah, Manzil, HizbQuarter, VerseText) VALUES (4, 146, '0', '0', '0', '0', 'إلا الذين رجعوا إلى الله تعالى وتابوا إليه، وأصلحوا ما أفسدوا من أحوالهم باطنًا وظاهرًا، ووالوا عباده المؤمنين، واستمسكوا بدين الله، وأخلصوا له سبحانه، فأولئك مع المؤمنين في الدنيا والآخرة، وسوف يعطي الله المؤمنين ثوابًا عظيمًا.');
INSERT INTO Quran__Arabic_Tafseer_Muyassar (QuranChapterID, VerseNumber, Rukuh, Sajdah, Manzil, HizbQuarter, VerseText) VALUES (4, 147, '0', '0', '0', '0', 'ما يفعل الله بعذابكم إن أصلحتم العمل وآمنتم بالله ورسوله، فإن الله سبحانه غني عمَّن سواه، وإنما يعذب العباد بذنوبهم. وكان الله شاكرًا لعباده على طاعتهم له، عليمًا بكل شيء.');
INSERT INTO Quran__Arabic_Tafseer_Muyassar (QuranChapterID, VerseNumber, Rukuh, Sajdah, Manzil, HizbQuarter, VerseText) VALUES (4, 148, '0', '0', '0', '1', 'لا يُحِبُّ الله أن يَجهر أحدٌ بقول السوء، لكن يُباح للمظلوم أن يَذكُر ظالمه بما فيه من السوء؛ ليبيِّن مَظْلمته. وكان الله سميعًا لما تجهرون به، عليمًا بما تخفون من ذلك.');
INSERT INTO Quran__Arabic_Tafseer_Muyassar (QuranChapterID, VerseNumber, Rukuh, Sajdah, Manzil, HizbQuarter, VerseText) VALUES (4, 149, '0', '0', '0', '0', 'نَدَب الله تعالى إلى العفو، ومهَّد له بأنَّ المؤمن: إمَّا أن يُظهر الخير، وإمَّا أن يُخفيه، وكذلك مع الإساءة: إما أن يظهرها في حال الانتصاف من المسيء، وإما أن يعفو ويصفح، والعفوُ أفضلُ؛ فإن من صفاته تعالى العفو عن عباده مع قدرته عليهم.');
INSERT INTO Quran__Arabic_Tafseer_Muyassar (QuranChapterID, VerseNumber, Rukuh, Sajdah, Manzil, HizbQuarter, VerseText) VALUES (4, 150, '0', '0', '0', '0', 'إن الذين يكفرون بالله ورسله من اليهود والنصارى، ويريدون أن يفرقوا بين الله ورسله بأن يؤمنوا بالله ويكذبوا رسله الذين أرسلهم إلى خلقه، أو يعترفوا بصدق بعض الرسل دون بعض، ويزعموا أنَّ بعضهم افتروا على ربِّهم، ويريدون أن يتخذوا طريقًا إلى الضلالة التي أحدثوها والبدعة التي ابتدعوها.');
INSERT INTO Quran__Arabic_Tafseer_Muyassar (QuranChapterID, VerseNumber, Rukuh, Sajdah, Manzil, HizbQuarter, VerseText) VALUES (4, 151, '0', '0', '0', '0', 'أولئك هم أهل الكفر المحقَّق الذي لا شك فيه، وأعتدنا للكافرين عذابًا يخزيهم ويهينهم.');
INSERT INTO Quran__Arabic_Tafseer_Muyassar (QuranChapterID, VerseNumber, Rukuh, Sajdah, Manzil, HizbQuarter, VerseText) VALUES (4, 152, '1', '0', '0', '0', 'والذين صَدَّقوا بوحدانية الله، وأقرُّوا بنبوَّة رسله أجمعين، ولم يفرقوا بين أحد منهم، وعملوا بشريعة الله، أولئك سوف يعطيهم جزاءهم وثوابهم على إيمانهم به وبرسله. وكان الله غفورًا رحيمًا.');
INSERT INTO Quran__Arabic_Tafseer_Muyassar (QuranChapterID, VerseNumber, Rukuh, Sajdah, Manzil, HizbQuarter, VerseText) VALUES (4, 153, '0', '0', '0', '0', 'يسألك اليهود -أيها الرسول- معجزة مثل معجزة موسى تشهد لك بالصدق: بأن تنزل عليهم صُحُفًا من الله مكتوبةً، مثل مجيء موسى بالألواح من عند الله، فلا تعجب -أيها الرسول- فقد سأل أسلافهم موسى -عليه السلام- ما هو أعظم: سألوه أن يريهم الله علانيةً، فَصُعِقوا بسبب ظلمهم أنفسهم حين سألوا أمرًا ليس من حقِّهم. وبعد أن أحياهم الله بعد الصعق، وشاهدوا الآيات البينات على يد موسى القاطعة بنفي الشرك، عبدوا العجل من دون الله، فعَفونا عن عبادتهم العجل بسبب توبتهم، وآتينا موسى حجة عظيمة تؤيِّد صِدق نُبُوَّتِه.');
INSERT INTO Quran__Arabic_Tafseer_Muyassar (QuranChapterID, VerseNumber, Rukuh, Sajdah, Manzil, HizbQuarter, VerseText) VALUES (4, 154, '0', '0', '0', '0', 'ورفعنا فوق رؤوسهم جبل الطور حين امتنعوا عن الالتزام بالعهد المؤكد الذي أعطوه بالعمل بأحكام التوراة، وأمرناهم أن يدخلوا باب "بيت المقدس" سُجَّدًا، فدخلوا يزحفون على أستاههم، وأمرناهم ألا يَعْتَدُوا بالصيد في يوم السبت فاعتدَوا، وصادوا، وأخذنا عليهم عهدًا مؤكدًا، فنقضوه.');
INSERT INTO Quran__Arabic_Tafseer_Muyassar (QuranChapterID, VerseNumber, Rukuh, Sajdah, Manzil, HizbQuarter, VerseText) VALUES (4, 155, '0', '0', '0', '0', 'فلعنَّاهم بسبب نقضهم للعهود، وكفرهم بآيات الله الدالة على صدق رسله، وقتلهم للأنبياء ظلمًا واعتداءً، وقولهم: قلوبنا عليها أغطية فلا تفقه ما تقول، بل طمس الله عليها بسبب كفرهم، فلا يؤمنون إلا إيمانًا قليلا لا ينفعهم.');
INSERT INTO Quran__Arabic_Tafseer_Muyassar (QuranChapterID, VerseNumber, Rukuh, Sajdah, Manzil, HizbQuarter, VerseText) VALUES (4, 156, '0', '0', '0', '0', 'وكذلك لعنَّاهم بسبب كفرهم وافترائهم على مريم بما نسبوه إليها من الزنى، وهي بريئة منه.');
INSERT INTO Quran__Arabic_Tafseer_Muyassar (QuranChapterID, VerseNumber, Rukuh, Sajdah, Manzil, HizbQuarter, VerseText) VALUES (4, 157, '0', '0', '0', '0', 'وبسبب قولهم -على سبيل التهكم والاستهزاء-: هذا الذي يدعي لنفسه هذا المنصب (قتلناه)، وما قتلوا عيسى وما صلبوه، بل صلبوا رجلا شبيهًا به ظنًّا منهم أنه عيسى. ومن ادَّعى قَتْلَهُ من اليهود، ومن أسلمه إليهم من النصارى، كلهم واقعون في شك وحَيْرَة، لا عِلْمَ لديهم إلا اتباع الظن، وما قتلوه متيقنين بل شاكين متوهمين.');
INSERT INTO Quran__Arabic_Tafseer_Muyassar (QuranChapterID, VerseNumber, Rukuh, Sajdah, Manzil, HizbQuarter, VerseText) VALUES (4, 158, '0', '0', '0', '0', 'بل رفع الله عيسى إليه ببدنه وروحه حيًّا، وطهَّره من الذين كفروا. وكان الله عزيزًا في ملكه، حكيمًا في تدبيره وقضائه.');
INSERT INTO Quran__Arabic_Tafseer_Muyassar (QuranChapterID, VerseNumber, Rukuh, Sajdah, Manzil, HizbQuarter, VerseText) VALUES (4, 159, '0', '0', '0', '0', 'وإنه لا يبقى أحدٌ من أهل الكتاب بعد نزول عيسى آخر الزمان إلا آمن به قبل موته عليه السلام، ويوم القيامة يكون عيسى -عليه السلام- شهيدًا بتكذيب مَن كذَّبه، وتصديق مَن صدَّقه.');
INSERT INTO Quran__Arabic_Tafseer_Muyassar (QuranChapterID, VerseNumber, Rukuh, Sajdah, Manzil, HizbQuarter, VerseText) VALUES (4, 160, '0', '0', '0', '0', 'فبسبب ظلم اليهود بما ارتكبوه من الذنوب العظيمة حَرَّم الله عليهم طيبات من المأكل كانت حلالا لهم، وبسبب صدِّهم أنفسهم وغيرهم عن دين الله القويم.');
INSERT INTO Quran__Arabic_Tafseer_Muyassar (QuranChapterID, VerseNumber, Rukuh, Sajdah, Manzil, HizbQuarter, VerseText) VALUES (4, 161, '0', '0', '0', '0', 'وبسبب تناولهم الربا الذي نهوا عنه، واستحلالهم أموال الناس بغير استحقاق، وأعتدنا للكافرين بالله ورسوله مِن هؤلاء اليهود عذابًا موجعًا في الآخرة.');
INSERT INTO Quran__Arabic_Tafseer_Muyassar (QuranChapterID, VerseNumber, Rukuh, Sajdah, Manzil, HizbQuarter, VerseText) VALUES (4, 162, '1', '0', '0', '0', 'لكنِ المتمكنون في العلم بأحكام الله من اليهود، والمؤمنون بالله ورسوله، يؤمنون بالذي أنزله الله إليك -أيها الرسول- وهو القرآن، وبالذي أنزل إلى الرسل من قبلك كالتوراة والإنجيل، ويؤدُّون الصلاة في أوقاتها، ويخرجون زكاة أموالهم، ويؤمنون بالله وبالبعث والجزاء، أولئك سيعطيهم الله ثوابًا عظيمًا، وهو الجنة.');
INSERT INTO Quran__Arabic_Tafseer_Muyassar (QuranChapterID, VerseNumber, Rukuh, Sajdah, Manzil, HizbQuarter, VerseText) VALUES (4, 163, '0', '0', '0', '1', 'إنا أوحينا اليك -أيها الرسول- بتبليغ الرسالة كما أوحينا إلى نوح والنبيين من بعده، وأوحينا إلى إبراهيم وإسماعيل وإسحاق ويعقوب والأسباط -وهم الأنبياء الذين كانوا في قبائل بني إسرائيل الاثنتي عشرة من ولد يعقوب- وعيسى وأيوب ويونس وهارون وسليمان. وآتينا داود زبورًا، وهو كتاب وصحف مكتوبة.');
INSERT INTO Quran__Arabic_Tafseer_Muyassar (QuranChapterID, VerseNumber, Rukuh, Sajdah, Manzil, HizbQuarter, VerseText) VALUES (4, 164, '0', '0', '0', '0', 'وأرسلنا رسلا قد قصصناهم عليك في القرآن من قبل هذه الآية، ورسلا لم نقصصهم عليك لحكمة أردناها. وكلم الله موسى تكليمًا؛ تشريفًا له بهذه الصفة. وفي هذه الآية الكريمة، إثبات صفة الكلام لله -تعالى- كما يليق بجلاله، وأنه سبحانه كلم نبيه موسى -عليه السلام- حقيقة بلا وساطة.');
INSERT INTO Quran__Arabic_Tafseer_Muyassar (QuranChapterID, VerseNumber, Rukuh, Sajdah, Manzil, HizbQuarter, VerseText) VALUES (4, 165, '0', '0', '0', '0', 'أرسَلْتُ رسلا إلى خَلْقي مُبشِّرين بثوابي، ومنذرين بعقابي؛ لئلا يكون للبشر حجة يعتذرون بها بعد إرسال الرسل. وكان الله عزيزًا في ملكه، حكيمًا في تدبيره.');
INSERT INTO Quran__Arabic_Tafseer_Muyassar (QuranChapterID, VerseNumber, Rukuh, Sajdah, Manzil, HizbQuarter, VerseText) VALUES (4, 166, '0', '0', '0', '0', 'إن يكفر بك اليهود وغيرهم -أيها الرسول- فالله يشهد لك بأنك رسوله الذي أَنْزَلَ عليه القرآن العظيم، أنزله بعلمه، وكذلك الملائكة يشهدون بصدق ما أوحي إليك، وشهادة الله وحدها كافية.');
INSERT INTO Quran__Arabic_Tafseer_Muyassar (QuranChapterID, VerseNumber, Rukuh, Sajdah, Manzil, HizbQuarter, VerseText) VALUES (4, 167, '0', '0', '0', '0', 'إن الذين جحدوا نُبُوَّتك، وصدوا الناس عن الإسلام، قد بَعُدوا عن طريق الحق بُعْدًا شديدًا.');
INSERT INTO Quran__Arabic_Tafseer_Muyassar (QuranChapterID, VerseNumber, Rukuh, Sajdah, Manzil, HizbQuarter, VerseText) VALUES (4, 168, '0', '0', '0', '0', 'إن الذين كفروا بالله وبرسوله، وظلموا باستمرارهم على الكفر، لم يكن الله ليغفر ذنوبهم، ولا ليدلهم على طريق ينجيهم.');
INSERT INTO Quran__Arabic_Tafseer_Muyassar (QuranChapterID, VerseNumber, Rukuh, Sajdah, Manzil, HizbQuarter, VerseText) VALUES (4, 169, '0', '0', '0', '0', 'إلا طريق جهنم ماكثين فيها أبدًا، وكان ذلك على الله يسيرًا، فلا يعجزه شيء.');
INSERT INTO Quran__Arabic_Tafseer_Muyassar (QuranChapterID, VerseNumber, Rukuh, Sajdah, Manzil, HizbQuarter, VerseText) VALUES (4, 170, '0', '0', '0', '0', 'يا أيها الناس قد جاءكم رسولنا محمد صلى الله عليه وسلم بالإسلام دين الحق من ربكم، فَصَدِّقوه واتبعوه، فإن الإيمان به خيرلكم، وإن تُصرُّوا على كفركم فإن الله غني عنكم وعن إيمانكم؛ لأنه مالك ما في السموات والأرض. وكان الله عليمًا بأقوالكم وأفعالكم، حكيمًا في تشريعه وأمره. فإذا كانت السموات والأرض قد خضعتا لله تعالى كونًا وقدرًا خضوع سائر ملكه، فأولى بكم أن تؤمنوا بالله وبرسوله محمد صلى الله عليه وسلم، وبالقرآن الذي أنزله عليه، وأن تنقادوا لذلك شرعًا حتى يكون الكون كلُّه خاضعًا لله قدرًا وشرعًا. وفي الآية دليل على عموم رسالة نبي الله ورسوله محمد صلى الله عليه وسلم.');
INSERT INTO Quran__Arabic_Tafseer_Muyassar (QuranChapterID, VerseNumber, Rukuh, Sajdah, Manzil, HizbQuarter, VerseText) VALUES (4, 171, '1', '0', '0', '0', 'يا أهل الإنجيل لا تتجاوزوا الاعتقاد الحق في دينكم، ولا تقولوا على الله إلا الحق، فلا تجعلوا له صاحبةً ولا ولدًا. إنما المسيح عيسى ابن مريم رسول الله أرسله الله بالحق، وخَلَقَه بالكلمة التي أرسل بها جبريل إلى مريم، وهي قوله: "كن"، فكان، وهي نفخة من الله تعالى نفخها جبريل بأمر ربه، فَصدِّقوا بأن الله واحد وأسلموا له، وصدِّقوا رسله فيما جاؤوكم به من عند الله واعملوا به، ولا تجعلوا عيسى وأمه مع الله شريكين. انتهوا عن هذه المقالة خيرًا لكم مما أنتم عليه، إنما الله إله واحد سبحانه. ما في السموات والأرض مُلْكُه، فكيف يكون له منهم صاحبة أو ولد؟ وكفى بالله وكيلا على تدبير خلقه وتصريف معاشهم، فتوكَّلوا عليه وحده فهو كافيكم.');
INSERT INTO Quran__Arabic_Tafseer_Muyassar (QuranChapterID, VerseNumber, Rukuh, Sajdah, Manzil, HizbQuarter, VerseText) VALUES (4, 172, '0', '0', '0', '0', 'لن يَأْنف ولن يمتنع المسيح أن يكون عبدًا لله، وكذلك لن يأنَفَ الملائكة المُقَرَّبون من الإقرار بالعبودية لله تعالى. ومن يأنف عن الانقياد والخضوع ويستكبر فسيحشرهم كلهم إليه يوم القيامة، ويفصلُ بينهم بحكمه العادل، ويجازي كلا بما يستحق.');
INSERT INTO Quran__Arabic_Tafseer_Muyassar (QuranChapterID, VerseNumber, Rukuh, Sajdah, Manzil, HizbQuarter, VerseText) VALUES (4, 173, '0', '0', '0', '0', 'فأمَّا الذين صَدَّقوا بالله اعتقادًا وقولا وعملا واستقاموا على شريعته فيوفيهم ثواب أعمالهم، ويزيدُهم من فضله، وأما الذين امتنعوا عن طاعة الله، واستكبروا عن التذلل له فيعذبهم عذابًا موجعًا، ولا يجدون لهم وليًّا ينجيهم من عذابه، ولا ناصرًا ينصرهم من دون الله.');
INSERT INTO Quran__Arabic_Tafseer_Muyassar (QuranChapterID, VerseNumber, Rukuh, Sajdah, Manzil, HizbQuarter, VerseText) VALUES (4, 174, '0', '0', '0', '0', 'يا أيها الناس قد جاءكم برهان من ربكم، وهو رسولنا محمد، وما جاء به من البينات والحجج القاطعة، وأعظمها القرآن الكريم، مما يشهد بصدق نبوته ورسالته الخاتمة، وأنزلنا إليكم القرآن هدًى ونورًا مبينًا.');
INSERT INTO Quran__Arabic_Tafseer_Muyassar (QuranChapterID, VerseNumber, Rukuh, Sajdah, Manzil, HizbQuarter, VerseText) VALUES (4, 175, '0', '0', '0', '0', 'فأمَّا الذين صدَّقوا بالله اعتقادًا وقولا وعملا واستمسكوا بالنور الذي أُنزل إليهم، فسيدخلهم الجنة رحمة منه وفضلا ويوفقهم إلى سلوك الطريق المستقيم المفضي إلى روضات الجنات.');
INSERT INTO Quran__Arabic_Tafseer_Muyassar (QuranChapterID, VerseNumber, Rukuh, Sajdah, Manzil, HizbQuarter, VerseText) VALUES (4, 176, '1', '0', '0', '0', 'يسألونك -أيها الرسول- عن حكم ميراث الكلالة، وهو من مات وليس له ولدٌ ولا والد، قل: الله يُبيِّن لكم الحكم فيها: إن مات امرؤ ليس له ولد ولا والد، وله أخت لأبيه وأمه، أو لأبيه فقط، فلها نصف تركته، ويرث أخوها شقيقًا كان أو لأب جميع مالها إذا ماتت وليس لها ولد ولا والد. فإن كان لمن مات كلالةً أختان فلهما الثلثان مما ترك. وإذا اجتمع الذكور من الإخوة لغير أم مع الإناث فللذكر مثل نصيب الأنثيين من أخواته. يُبيِّن الله لكم قسمة المواريث وحكم الكلالة، لئلا تضلوا عن الحقِّ في أمر المواريث. والله عالم بعواقب الأمور، وما فيها من الخير لعباده.');
INSERT INTO Quran__Arabic_Tafseer_Muyassar (QuranChapterID, VerseNumber, Rukuh, Sajdah, Manzil, HizbQuarter, VerseText) VALUES (5, 1, '0', '0', '1', '1', 'يا أيها الذين صدَّقوا الله ورسوله وعملوا بشرعه، أتِمُّوا عهود الله الموثقة، من الإيمان بشرائع الدين، والانقياد لها، وأَدُّوا العهود لبعضكم على بعض من الأمانات، والبيوع وغيرها، مما لم يخالف كتاب الله، وسنة رسوله محمد صلى الله عليه وسلم. وقد أحَلَّ الله لكم البهيمة من الأنعام، وهي الإبلُ والبقر والغنم، إلا ما بيَّنه لكم من تحريم الميتة والدم وغير ذلك، ومن تحريم الصيد وأنتم محرمون. إن الله يحكم ما يشاء وَفْق حكمته وعدله.');
INSERT INTO Quran__Arabic_Tafseer_Muyassar (QuranChapterID, VerseNumber, Rukuh, Sajdah, Manzil, HizbQuarter, VerseText) VALUES (5, 2, '0', '0', '0', '0', 'يا أيها الذين صدَّقوا الله ورسوله وعملوا بشرعه لا تتعدَّوا حدود الله ومعالمه، ولا تستحِلُّوا القتال في الأشهر الحرم، وهي: ذو القعدة وذو الحجة والمحرم ورجب، وكان ذلك في صدر الإسلام، ولا تستحِلُّوا حرمة الهَدْي، ولا ما قُلِّدَ منه؛ إذ كانوا يضعون القلائد، وهي ضفائر من صوف أو وَبَر في الرقاب علامةً على أن البهيمة هَدْيٌ وأن الرجل يريد الحج، ولا تَسْتَحِلُّوا قتال قاصدي البيت الحرام الذين يبتغون من فضل الله ما يصلح معايشهم ويرضي ربهم. وإذا حللتم من إحرامكم حلَّ لكم الصيد، ولا يحمِلَنَّكم بُغْض قوم من أجل أن منعوكم من الوصول إلى المسجد الحرام -كما حدث عام "الحديبية"- على ترك العدل فيهم. وتعاونوا -أيها المؤمنون فيما بينكم- على فِعْل الخير، وتقوى الله، ولا تعاونوا على ما فيه إثم ومعصية وتجاوز لحدود الله، واحذروا مخالفة أمر الله فإنه شديد العقاب.');
INSERT INTO Quran__Arabic_Tafseer_Muyassar (QuranChapterID, VerseNumber, Rukuh, Sajdah, Manzil, HizbQuarter, VerseText) VALUES (5, 3, '0', '0', '0', '0', 'حرَّم الله عليكم الميتة، وهي الحيوان الذي تفارقه الحياة بدون ذكاة، وحرَّم عليكم الدم السائل المُراق، ولحم الخنزير، وما ذُكِر عليه غير اسم الله عند الذبح، والمنخنقة التي حُبِس نَفَسُها حتى ماتت، والموقوذة وهي التي ضُربت بعصا أو حجر حتى ماتت، والمُتَرَدِّية وهي التي سقطت من مكان عال أو هَوَت في بئر فماتت، والنطيحة وهي التي ضَرَبَتْها أخرى بقرنها فماتت، وحرَّم الله عليكم البهيمة التي أكلها السبُع، كالأسد والنمر والذئب، ونحو ذلك. واستثنى -سبحانه- مما حرَّمه من المنخنقة وما بعدها ما أدركتم ذكاته قبل أن يموت فهو حلال لكم، وحرَّم الله عليكم ما ذُبِح لغير الله على ما يُنصب للعبادة من حجر أو غيره، وحرَّم الله عليكم أن تطلبوا عِلْم ما قُسِم لكم أو لم يقسم بالأزلام، وهي القداح التي كانوا يستقسمون بها إذا أرادوا أمرًا قبل أن يقدموا عليه. ذلكم المذكور في الآية من المحرمات -إذا ارتُكبت- خروج عن أمر الله وطاعته إلى معصيته. الآن انقطع طمع الكفار من دينكم أن ترتدوا عنه إلى الشرك بعد أن نصَرْتُكم عليهم، فلا تخافوهم وخافوني. اليوم أكملت لكم دينكم دين الإسلام بتحقيق النصر وإتمام الشريعة، وأتممت عليكم نعمتي بإخراجكم من ظلمات الجاهلية إلى نور الإيمان، ورضيت لكم الإسلام دينًا فالزموه، ولا تفارقوه. فمن اضطرَّ في مجاعة إلى أكل الميتة، وكان غير مائل عمدًا لإثم، فله تناوله، فإن الله غفور له، رحيم به.');
INSERT INTO Quran__Arabic_Tafseer_Muyassar (QuranChapterID, VerseNumber, Rukuh, Sajdah, Manzil, HizbQuarter, VerseText) VALUES (5, 4, '0', '0', '0', '0', 'يسألك أصحابك -أيها النبي-: ماذا أُحِلَّ لهم أَكْلُه؟ قل لهم: أُحِلَّ لكم الطيبات وصيدُ ما دَرَّبتموه من ذوات المخالب والأنياب من الكلاب والفهود والصقور ونحوها مما يُعَلَّم، تعلمونهن طلب الصيد لكم، مما علمكم الله، فكلوا مما أمسكن لكم، واذكروا اسم الله عند إرسالها للصيد، وخافوا الله فيما أمركم به وفيما نهاكم عنه. إن الله سريع الحساب.');
INSERT INTO Quran__Arabic_Tafseer_Muyassar (QuranChapterID, VerseNumber, Rukuh, Sajdah, Manzil, HizbQuarter, VerseText) VALUES (5, 5, '1', '0', '0', '0', 'ومن تمام نعمة الله عليكم اليوم -أيها المؤمنون- أن أَحَلَّ لكم الحلال الطيب، وذبائحُ اليهود والنصارى -إن ذكَّوها حَسَبَ شرعهم- حلال لكم وذبائحكم حلال لهم. وأَحَلَّ لكم -أيها المؤمنون- نكاح المحصنات، وهُنَّ الحرائر من النساء المؤمنات، العفيفات عن الزنى، وكذلك نكاحَ الحرائر العفيفات من اليهود والنصارى إذا أعطيتموهُنَّ مهورهن، وكنتم أعِفَّاء غير مرتكبين للزنى، ولا متخذي عشيقات، وأمِنتم من التأثر بدينهن. ومن يجحد شرائع الإيمان فقد بطل عمله، وهو يوم القيامة من الخاسرين.');
INSERT INTO Quran__Arabic_Tafseer_Muyassar (QuranChapterID, VerseNumber, Rukuh, Sajdah, Manzil, HizbQuarter, VerseText) VALUES (5, 6, '0', '0', '0', '0', 'يا أيها الذين آمنوا إذا أردتم القيام إلى الصلاة، وأنتم على غير طهارة فاغسلوا وجوهكم وأيديكم مع المرافق (والمِرْفَق: المِفْصَل الذي بين الذراع والعَضُد) وامسحوا رؤوسكم، واغسلوا أرجلكم مع الكعبين (وهما: العظمان البارزان عند ملتقى الساق بالقدم). وإن أصابكم الحدث الأكبر فتطهروا بالاغتسال منه قبل الصلاة. فإن كنتم مرضى، أو على سفر في حال الصحة، أو قضى أحدكم حاجته، أو جامع زوجته فلم تجدوا ماء فاضربوا بأيديكم وجه الأرض، وامسحوا وجوهكم وأيديكم منه. ما يريد الله في أمر الطهارة أن يُضَيِّق عليكم، بل أباح التيمم توسعةً عليكم، ورحمة بكم، إذ جعله بديلا للماء في الطهارة، فكانت رخصة التيمُّم من تمام النعم التي تقتضي شكر المنعم؛ بطاعته فيما أمر وفيما نهى.');
INSERT INTO Quran__Arabic_Tafseer_Muyassar (QuranChapterID, VerseNumber, Rukuh, Sajdah, Manzil, HizbQuarter, VerseText) VALUES (5, 7, '0', '0', '0', '0', 'واذكروا نعمة الله عليكم فيما شَرَعه لكم، واذكروا عهده الذي أخذه تعالى عليكم من الإيمان بالله ورسوله محمد صلى الله عليه وسلم، والسمع والطاعة لهما، واتقوا الله فيما أمركم به ونهاكم عنه. إن الله عليمٌ بما تُسِرُّونه في نفوسكم.');
INSERT INTO Quran__Arabic_Tafseer_Muyassar (QuranChapterID, VerseNumber, Rukuh, Sajdah, Manzil, HizbQuarter, VerseText) VALUES (5, 8, '0', '0', '0', '0', 'يا أيها الذين آمَنوا بالله ورسوله محمد صلى الله عليه وسلم كونوا قوَّامين بالحق، ابتغاء وجه الله، شُهداء بالعدل، ولا يحملنكم بُغْضُ قوم على ألا تعدلوا، اعدِلوا بين الأعداء والأحباب على درجة سواء، فذلك العدل أقرب لخشية الله، واحذروا أن تجوروا. إن الله خبير بما تعملون، وسيجازيكم به.');
INSERT INTO Quran__Arabic_Tafseer_Muyassar (QuranChapterID, VerseNumber, Rukuh, Sajdah, Manzil, HizbQuarter, VerseText) VALUES (5, 9, '0', '0', '0', '0', 'وعد الله الذين صدَّقوا الله ورسوله وعملوا الصالحات أن يغفر لهم ذنوبهم، وأن يثيبهم على ذلك الجنة، والله لا يخلف وعده.');
INSERT INTO Quran__Arabic_Tafseer_Muyassar (QuranChapterID, VerseNumber, Rukuh, Sajdah, Manzil, HizbQuarter, VerseText) VALUES (5, 10, '0', '0', '0', '0', 'والذين جحدوا وحدانية الله الدالة على الحق المبين، وكذَّبوا بأدلته التي جاءت بها الرسل، هم أهل النار الملازمون لها.');
INSERT INTO Quran__Arabic_Tafseer_Muyassar (QuranChapterID, VerseNumber, Rukuh, Sajdah, Manzil, HizbQuarter, VerseText) VALUES (5, 11, '1', '0', '0', '0', 'يا أيها الذين صدَّقوا الله ورسوله وعملوا بشرعه اذكروا ما أنعم الله به عليكم من نعمة الأمنِ، وإلقاءِ الرعب في قلوب أعدائكم الذين أرادوا أن يبطشوا بكم، فصرفهم الله عنكم، وحال بينهم وبين ما أرادوه بكم، واتقوا الله واحذروه، وتوكلوا على الله وحده في أموركم الدينية والدنيوية، وثِقوا بعونه ونصره.');
INSERT INTO Quran__Arabic_Tafseer_Muyassar (QuranChapterID, VerseNumber, Rukuh, Sajdah, Manzil, HizbQuarter, VerseText) VALUES (5, 12, '0', '0', '0', '1', 'ولقد أخذ الله العهد المؤكَّد على بني إسرائيل أن يخلصوا له العبادة وحده، وأمر الله موسى أن يجعل عليهم اثني عشر عريفًا بعدد فروعهم، يأخذون عليهم العهد بالسمع والطاعة لله ولرسوله ولكتابه، وقال الله لبني إسرائيل: إني معكم بحفظي ونصري، لئن أقمتم الصلاة، وأعطيتم الزكاة المفروضة مستحقيها، وصدَّقتم برسلي فيما أخبروكم به ونصرتموهم، وأنفقتم في سبيلي، لأكفِّرنَّ عنكم سيئاتكم، ولأدْخِلَنَّكُم جناتٍ تجري من تحت قصورها الأنهار، فمن جحد هذا الميثاق منكم فقد عدل عن طريق الحق إلى طريق الضلال.');
INSERT INTO Quran__Arabic_Tafseer_Muyassar (QuranChapterID, VerseNumber, Rukuh, Sajdah, Manzil, HizbQuarter, VerseText) VALUES (5, 13, '0', '0', '0', '0', 'فبسبب نقض هؤلاء اليهود لعهودهم المؤكَّدة طردناهم من رحمتنا، وجعلنا قلوبهم غليظة لا تلين للإيمان، يبدلون كلام الله الذي أنزله على موسى، وهو التوراة، وتركوا نصيبًا مما ذُكِّروا به، فلم يعملوا به. ولا تزال -أيها الرسول- تجد من اليهود خيانةً وغَدرًا، فهم على منهاج أسلافهم إلا قليلا منهم، فاعف عن سوء معاملتهم لك، واصفح عنهم، فإن الله يحب مَن أحسن العفو والصفح إلى من أساء إليه. (وهكذا يجد أهل الزيغ سبيلا إلى مقاصدهم السيئة بتحريف كلام الله وتأويله على غير وجهه، فإن عجَزوا عن التحريف والتأويل تركوا ما لا يتفق مع أهوائهم مِن شرع الله الذي لا يثبت عليه إلا القليل ممن عصمه الله منهم).');
INSERT INTO Quran__Arabic_Tafseer_Muyassar (QuranChapterID, VerseNumber, Rukuh, Sajdah, Manzil, HizbQuarter, VerseText) VALUES (5, 14, '0', '0', '0', '0', 'وأخذنا على الذين ادَّعوا أنهم أتباع المسيح عيسى -وليسوا كذلك- العهد المؤكد الذي أخذناه على بني إسرائيل: بأن يُتابعوا رسولهم وينصروه ويؤازروه، فبدَّلوا دينهم، وتركوا نصيبًا مما ذكروا به، فلم يعملوا به، كما صنع اليهود، فألقينا بينهم العداوة والبغضاء إلى يوم القيامة، وسوف ينبئهم الله بما كانوا يصنعون يوم الحساب، وسيعاقبهم على صنيعهم.');
INSERT INTO Quran__Arabic_Tafseer_Muyassar (QuranChapterID, VerseNumber, Rukuh, Sajdah, Manzil, HizbQuarter, VerseText) VALUES (5, 15, '0', '0', '0', '0', 'يا أهل الكتاب من اليهود والنصارى، قد جاءكم رسولنا محمد صلى الله عليه وسلم يبيِّن لكم كثيرًا مما كنتم تُخْفونه عن الناس مما في التوراة والإنجيل، ويترك بيان ما لا تقتضيه الحكمة. قد جاءكم من الله نور وكتاب مبين: وهو القرآن الكريم.');
INSERT INTO Quran__Arabic_Tafseer_Muyassar (QuranChapterID, VerseNumber, Rukuh, Sajdah, Manzil, HizbQuarter, VerseText) VALUES (5, 16, '0', '0', '0', '0', 'يهدي الله بهذا الكتاب المبين من اتبع رضا الله تعالى، طرق الأمن والسلامة، ويخرجهم بإذنه من ظلمات الكفر إلى نور الإيمان، ويوفقهم إلى دينه القويم.');
INSERT INTO Quran__Arabic_Tafseer_Muyassar (QuranChapterID, VerseNumber, Rukuh, Sajdah, Manzil, HizbQuarter, VerseText) VALUES (5, 17, '0', '0', '0', '0', 'لقد كفر النصارى القائلون بأن الله هو المسيح ابن مريم، قل -أيها الرسول- لهؤلاء الجهلة من النصارى: لو كان المسيح إلهًا كما يدَّعون لقَدرَ أن يدفع قضاء الله إذا جاءه بإهلاكه وإهلاك أُمِّه ومَن في الأرض جميعًا، وقد ماتت أم عيسى فلم يدفع عنها الموت، كذلك لا يستطيع أن يدفع عن نفسه؛ لأنهما عبدان من عباد الله لا يقدران على دفع الهلاك عنهما، فهذا دليلٌ على أنه بشر كسائر بني آدم. وجميع الموجودات في السماوات والأرض ملك لله، يخلق ما يشاء ويوجده، وهو على كل شيء قدير. فحقيقة التوحيد توجب تفرُّد الله تعالى بصفات الربوبية والألوهية، فلا يشاركه أحد من خلقه في ذلك، وكثيرًا ما يقع الناس في الشرك والضلال بغلوهم في الأنبياء والصالحين، كما غلا النصارى في المسيح، فالكون كله لله، والخلق بيده وحده، وما يظهر من خوارق وآيات مَرَدُّه إلى الله. يخلق سبحانه ما يشاء، ويفعل ما يريد.');
INSERT INTO Quran__Arabic_Tafseer_Muyassar (QuranChapterID, VerseNumber, Rukuh, Sajdah, Manzil, HizbQuarter, VerseText) VALUES (5, 18, '0', '0', '0', '0', 'وزعم اليهود والنصارى أنهم أبناء الله وأحباؤه، قل لهم -أيها الرسول-: فَلأيِّ شيء يعذِّبكم بذنوبكم؟ فلو كنتم أحبابه ما عذبكم، فالله لا يحب إلا من أطاعه، وقل لهم: بل أنتم خلقٌ مثلُ سائر بني آدم، إن أحسنتُم جوزيتم بإحسانكم خيرا، وإن أسَأْتُم جوزيتم بإساءتكم شرًّا، فالله يغفر لمن يشاء، ويعذب من يشاء، وهو مالك الملك، يُصَرِّفه كما يشاء، وإليه المرجع، فيحكم بين عباده، ويجازي كلا بما يستحق.');
INSERT INTO Quran__Arabic_Tafseer_Muyassar (QuranChapterID, VerseNumber, Rukuh, Sajdah, Manzil, HizbQuarter, VerseText) VALUES (5, 19, '1', '0', '0', '0', 'يا أيها اليهود والنصارى قد جاءكم رسولنا محمد صلى الله عليه وسلم، يُبيِّن لكم الحق والهدى بعد مُدَّة من الزمن بين إرساله بإرسال عيسى ابن مريم؛ لئلا تقولوا: ما جاءنا من بشير ولا نذير، فلا عُذرَ لكم بعد إرساله إليكم، فقد جاءكم من الله رسولٌ يبشِّر مَن آمن به، ويُنذِز مَن عصاه. والله على كل شيء قدير من عقاب العاصي وثواب المطيع.');
INSERT INTO Quran__Arabic_Tafseer_Muyassar (QuranChapterID, VerseNumber, Rukuh, Sajdah, Manzil, HizbQuarter, VerseText) VALUES (5, 20, '0', '0', '0', '0', 'واذكر -أيها الرسول- إذ قال موسى عليه السلام لقومه: يا بني إسرائيل اذكروا نعمة الله عليكم، إذ جعل فيكم أنبياء، وجعلكم ملوكًا تملكون أمركم بعد أن كنتم مملوكين لفرعون وقومه، وقد منحكم من نعمه صنوفًا لم يمنحها أحدًا من عالَمي زمانكم.');
INSERT INTO Quran__Arabic_Tafseer_Muyassar (QuranChapterID, VerseNumber, Rukuh, Sajdah, Manzil, HizbQuarter, VerseText) VALUES (5, 21, '0', '0', '0', '0', 'يا قوم ادخلوا الأرض المقدسة -أي المطهرة، وهي "بيت المقدس" وما حولها- التي وعد الله أن تدخلوها وتقاتلوا مَن فيها من الكفار، ولا ترجعوا عن قتال الجبارين، فتخسروا خير الدنيا وخير الآخرة.');
INSERT INTO Quran__Arabic_Tafseer_Muyassar (QuranChapterID, VerseNumber, Rukuh, Sajdah, Manzil, HizbQuarter, VerseText) VALUES (5, 22, '0', '0', '0', '0', 'قالوا: يا موسى، إن فيها قومًا أشداء أقوياء، لا طاقة لنا بحربهم، وإنَّا لن نستطيع دخولها وهم فيها، فإن يخرجوا منها فإنَّا داخلون.');
INSERT INTO Quran__Arabic_Tafseer_Muyassar (QuranChapterID, VerseNumber, Rukuh, Sajdah, Manzil, HizbQuarter, VerseText) VALUES (5, 23, '0', '0', '0', '0', 'قال رجلان من الذين يخشون الله تعالى، أنعم الله عليهما بطاعته وطاعة نبيِّه، لبني إسرائيل: ادخلوا على هؤلاء الجبارين باب مدينتهم، أخْذًا بالأسباب، فإذا دخلتم الباب غلبتموهم، وعلى الله وحده فتوكَّلوا، إن كنتم مُصدِّقين رسوله فيما جاءكم به، عاملين بشرعه.');
INSERT INTO Quran__Arabic_Tafseer_Muyassar (QuranChapterID, VerseNumber, Rukuh, Sajdah, Manzil, HizbQuarter, VerseText) VALUES (5, 24, '0', '0', '0', '0', 'قال قوم موسى له: إنا لن ندخل المدينة أبدًا ما دام الجبارون فيها، فاذهب أنت وربك فقاتلاهم، أما نحن فقاعدون هاهنا ولن نقاتلهم. وهذا إصرارٌ منهم على مخالفة موسى عليه السلام.');
INSERT INTO Quran__Arabic_Tafseer_Muyassar (QuranChapterID, VerseNumber, Rukuh, Sajdah, Manzil, HizbQuarter, VerseText) VALUES (5, 25, '0', '0', '0', '0', 'توجَّه موسى إلى ربه داعيًا: إني لا أقدر إلا على نفسي وأخي، فاحكم بيننا وبين القوم الفاسقين.');
INSERT INTO Quran__Arabic_Tafseer_Muyassar (QuranChapterID, VerseNumber, Rukuh, Sajdah, Manzil, HizbQuarter, VerseText) VALUES (5, 26, '1', '0', '0', '0', 'قال الله لنبيه موسى عليه السلام: إن الأرض المقدَّسة محرَّم على هؤلاء اليهود دخولها أربعين سنة، يتيهون في الأرض حائرين، فلا تأسف -يا موسى- على القوم الخارجين عن طاعتي.');
INSERT INTO Quran__Arabic_Tafseer_Muyassar (QuranChapterID, VerseNumber, Rukuh, Sajdah, Manzil, HizbQuarter, VerseText) VALUES (5, 27, '0', '0', '0', '1', 'واقصص -أيها الرسول- على بني إسرائيل خَبَر ابنَيْ آدم قابيل وهابيل، وهو خبرٌ حقٌ: حين قَدَّم كلٌّ منهما قربانًا -وهو ما يُتَقرَّب به إلى الله تعالى - فتقبَّل الله قُربان هابيل؛ لأنه كان تقيًّا، ولم يتقبَّل قُربان قابيل؛ لأنه لم يكن تقيًّا، فحسد قابيلُ أخاه، وقال: لأقتلنَّك، فَردَّ هابيل: إنما يتقبل الله ممن يخشونه.');
INSERT INTO Quran__Arabic_Tafseer_Muyassar (QuranChapterID, VerseNumber, Rukuh, Sajdah, Manzil, HizbQuarter, VerseText) VALUES (5, 28, '0', '0', '0', '0', 'وقال هابيلُ واعظًا أخاه: لَئنْ مَدَدْتَ إليَّ يدكَ لتقتُلني لا تَجِدُ مني مثل فعْلك، وإني أخشى الله ربَّ الخلائق أجمعين.');
INSERT INTO Quran__Arabic_Tafseer_Muyassar (QuranChapterID, VerseNumber, Rukuh, Sajdah, Manzil, HizbQuarter, VerseText) VALUES (5, 29, '0', '0', '0', '0', 'إني أريد أن ترجع حاملا إثم قَتْلي، وإثمك الذي عليك قبل ذلك، فتكون من أهل النار وملازميها، وذلك جزاء المعتدين.');
INSERT INTO Quran__Arabic_Tafseer_Muyassar (QuranChapterID, VerseNumber, Rukuh, Sajdah, Manzil, HizbQuarter, VerseText) VALUES (5, 30, '0', '0', '0', '0', 'فَزَيَّنت لقابيلَ نفسُه أن يقتل أخاه، فقتله، فأصبح من الخاسرين الذين باعوا آخرتهم بدنياهم.');
INSERT INTO Quran__Arabic_Tafseer_Muyassar (QuranChapterID, VerseNumber, Rukuh, Sajdah, Manzil, HizbQuarter, VerseText) VALUES (5, 31, '0', '0', '0', '0', 'لما قتل قابيلُ أخاه لم يعرف ما يصنع بجسده، فأرسل الله غرابًا يحفر حفرةً في الأرض ليدفن فيها غرابًا مَيِّتًا؛ ليدل قابيل كيف يدفن جُثمان أخيه؟ فتعجَّب قابيل، وقال: أعجزتُ أن أصنع مثل صنيع هذا الغراب فأستُرَ عورة أخي؟ فدَفَنَ قابيل أخاه، فعاقبه الله بالندامة بعد أن رجع بالخسران.');
INSERT INTO Quran__Arabic_Tafseer_Muyassar (QuranChapterID, VerseNumber, Rukuh, Sajdah, Manzil, HizbQuarter, VerseText) VALUES (5, 32, '0', '0', '0', '0', 'بسبب جناية القتل هذه شَرَعْنا لبني اسرائيل أنه من قتل نفسا بغير سبب من قصاص، أو فساد في الأرض بأي نوع من أنواع الفساد، الموجب للقتل كالشرك والمحاربة فكأنما قتل الناس جميعًا فيما استوجب من عظيم العقوبة من الله، وأنه من امتنع عن قَتْل نفس حرَّمها الله فكأنما أحيا الناس جميعًا؛ فالحفاظ على حرمة إنسان واحد حفاظ على حرمات الناس كلهم. ولقد أتت بني إسرائيل رسلُنا بالحجج والدلائل على صحة ما دعَوهم إليه من الإيمان بربهم، وأداء ما فُرِضَ عليهم، ثم إن كثيرًا منهم بعد مجيء الرسل إليهم لمتجاوزون حدود الله بارتكاب محارم الله وترك أوامره.');
INSERT INTO Quran__Arabic_Tafseer_Muyassar (QuranChapterID, VerseNumber, Rukuh, Sajdah, Manzil, HizbQuarter, VerseText) VALUES (5, 33, '0', '0', '0', '0', 'إنما جزاء الذين يحاربون الله، ويبارزونه بالعداوة، ويعتدون على أحكامه، وعلى أحكام رسوله، ويفسدون في الأرض بقتل الأنفس، وسلب الأموال، أن يُقَتَّلوا، أو يُصَلَّبوا مع القتل (والصلب: أن يُشَدَّ الجاني على خشبة) أو تُقْطَع يدُ المحارب اليمنى ورجله اليسرى، فإن لم يَتُبْ تُقطعْ يدُه اليسرى ورجلُه اليمنى، أو يُنفَوا إلى بلد غير بلدهم، ويُحبسوا في سجن ذلك البلد حتى تَظهر توبتُهم. وهذا الجزاء الذي أعدَّه الله للمحاربين هو ذلّ في الدنيا، ولهم في الآخرة عذاب شديد إن لم يتوبوا.');
INSERT INTO Quran__Arabic_Tafseer_Muyassar (QuranChapterID, VerseNumber, Rukuh, Sajdah, Manzil, HizbQuarter, VerseText) VALUES (5, 34, '1', '0', '0', '0', 'لكن مَن أتى من المحاربين من قبل أن تقدروا عليهم وجاء طائعًا نادمًا فإنه يسقط عنه ما كان لله، فاعلموا -أيها المؤمنون- أن الله غفور لعباده، رحيم بهم.');
INSERT INTO Quran__Arabic_Tafseer_Muyassar (QuranChapterID, VerseNumber, Rukuh, Sajdah, Manzil, HizbQuarter, VerseText) VALUES (5, 35, '0', '0', '0', '0', 'يا أيها الذين صدَّقوا الله ورسوله وعملوا بشرعه، خافوا الله، وتَقَرَّبوا إليه بطاعته والعمل بما يرضيه، وجاهدوا في سبيله؛ كي تفوزوا بجناته.');
INSERT INTO Quran__Arabic_Tafseer_Muyassar (QuranChapterID, VerseNumber, Rukuh, Sajdah, Manzil, HizbQuarter, VerseText) VALUES (5, 36, '0', '0', '0', '0', 'إن الذين جحدوا وحدانية الله، وشريعته، لو أنهم ملكوا جميع ما في الأرض، وملكوا مثله معه، وأرادوا أن يفتدوا أنفسهم يوم القيامة من عذاب الله بما ملكوا، ما تَقبَّل الله ذلك منهم، ولهم عذاب مُوجع.');
INSERT INTO Quran__Arabic_Tafseer_Muyassar (QuranChapterID, VerseNumber, Rukuh, Sajdah, Manzil, HizbQuarter, VerseText) VALUES (5, 37, '0', '0', '0', '0', 'يريد هؤلاء الكافرون الخروج من النار لما يلاقونه من أهوالها، ولا سبيل لهم إلى ذلك، ولهم عذاب دائم.');
INSERT INTO Quran__Arabic_Tafseer_Muyassar (QuranChapterID, VerseNumber, Rukuh, Sajdah, Manzil, HizbQuarter, VerseText) VALUES (5, 38, '0', '0', '0', '0', 'والسارق والسارقة فاقطعوا -يا ولاة الأمر- أيديهما بمقتضى الشرع، مجازاة لهما على أَخْذهما أموال الناس بغير حق، وعقوبةً يمنع الله بها غيرهما أن يصنع مثل صنيعهما. والله عزيز في ملكه، حكيم في أمره ونهيه.');
INSERT INTO Quran__Arabic_Tafseer_Muyassar (QuranChapterID, VerseNumber, Rukuh, Sajdah, Manzil, HizbQuarter, VerseText) VALUES (5, 39, '0', '0', '0', '0', 'فمن تاب مِن بعد سرقته، وأصلح في كل أعماله، فإن الله يقبل توبته. إن الله غفور لعباده، رحيم بهم.');
INSERT INTO Quran__Arabic_Tafseer_Muyassar (QuranChapterID, VerseNumber, Rukuh, Sajdah, Manzil, HizbQuarter, VerseText) VALUES (5, 40, '0', '0', '0', '0', 'ألم تعلم -أيها الرسول- أن الله خالق الكون ومُدبِّره ومالكه، وأنه تعالى الفعَّال لما يريد، يعذب من يشاء، ويغفر لمن يشاء، وهو على كل شيء قدير.');
INSERT INTO Quran__Arabic_Tafseer_Muyassar (QuranChapterID, VerseNumber, Rukuh, Sajdah, Manzil, HizbQuarter, VerseText) VALUES (5, 41, '0', '0', '0', '1', 'يا أيها الرسول لا يحزنك الذين يسارعون في جحود نبوتك من المنافقين الذين أظهروا الإسلام وقلوبهم خالية منه، فإني ناصرك عليهم. ولا يحزنك تسرُّع اليهود إلى إنكار نبوتك، فإنهم قوم يستمعون للكذب، ويقبلون ما يَفْتَريه أحبارُهم، ويستجيبون لقوم آخرين لا يحضرون مجلسك، وهؤلاء الآخرون يُبَدِّلون كلام الله من بعد ما عَقَلوه، ويقولون: إن جاءكم من محمد ما يوافق الذي بدَّلناه وحرَّفناه من أحكام التوراة فاعملوا به، وإن جاءكم منه ما يخالفه فاحذروا قبوله، والعمل به. ومن يشأ الله ضلالته فلن تستطيع -أيها الرسول- دَفْعَ ذلك عنه، ولا تقدر على هدايته. وإنَّ هؤلاء المنافقين واليهود لم يُرِدِ الله أن يطهِّر قلوبهم من دنس الكفر، لهم الذلُّ والفضيحة في الدنيا، ولهم في الآخرة عذاب عظيم.');
INSERT INTO Quran__Arabic_Tafseer_Muyassar (QuranChapterID, VerseNumber, Rukuh, Sajdah, Manzil, HizbQuarter, VerseText) VALUES (5, 42, '0', '0', '0', '0', 'هؤلاء اليهود يجمعون بين استماع الكذب وأكل الحرام، فإن جاؤوك يتحاكمون إليك فاقض بينهم، أو اتركهم، فإن لم تحكم بينهم فلن يقدروا على أن يضروك بشيء، وإن حكمت فاحكم بينهم بالعدل. إن الله يحب العادلين.');
INSERT INTO Quran__Arabic_Tafseer_Muyassar (QuranChapterID, VerseNumber, Rukuh, Sajdah, Manzil, HizbQuarter, VerseText) VALUES (5, 43, '1', '0', '0', '0', 'إنَّ صنيع هؤلاء اليهود عجيب، فهم يحتكمون إليك -أيها الرسول- وهم لا يؤمنون بك، ولا بكتابك، مع أن التوراة التي يؤمنون بها عندهم، فيها حكم الله، ثم يتولَّون مِن بعد حكمك إذا لم يُرضهم، فجمعوا بين الكفر بشرعهم، والإعراض عن حكمك، وليس أولئك المتصفون بتلك الصفات، بالمؤمنين بالله وبك وبما تحكم به.');
INSERT INTO Quran__Arabic_Tafseer_Muyassar (QuranChapterID, VerseNumber, Rukuh, Sajdah, Manzil, HizbQuarter, VerseText) VALUES (5, 44, '0', '0', '0', '0', 'إنا أنزلنا التوراة فيها إرشاد من الضلالة، وبيان للأحكام، وقد حكم بها النبيُّون -الذين انقادوا لحكم الله، وأقروا به- بين اليهود، ولم يخرجوا عن حكمها ولم يُحَرِّفوها، وحكم بها عُبَّاد اليهود وفقهاؤهم الذين يربُّون الناس بشرع الله؛ ذلك أن أنبياءهم قد استأمنوهم على تبليغ التوراة، وفِقْه كتاب الله والعمل به، وكان الربانيون والأحبار شهداء على أن أنبياءهم قد قضوا في اليهود بكتاب الله. ويقول تعالى لعلماء اليهود وأحبارهم: فلا تخشوا الناس في تنفيذ حكمي؛ فإنهم لا يقدرون على نفعكم ولا ضَرِّكم، ولكن اخشوني فإني أنا النافع الضار، ولا تأخذوا بترك الحكم بما أنزلتُ عوضًا حقيرًا. الحكم بغير ما أنزل الله من أعمال أهل الكفر، فالذين يبدلون حكم الله الذي أنزله في كتابه، فيكتمونه ويجحدونه ويحكمون بغيره معتقدين حله وجوازه فأولئك هم الكافرون.');
INSERT INTO Quran__Arabic_Tafseer_Muyassar (QuranChapterID, VerseNumber, Rukuh, Sajdah, Manzil, HizbQuarter, VerseText) VALUES (5, 45, '0', '0', '0', '0', 'وفَرَضنا عليهم في التوراة أن النفس تُقْتَل بالنفس، والعين تُفْقَأ بالعين، والأنف يُجْدَع بالأنف، والأذُن تُقْطع بالأذُن، والسنَّ تُقْلَعُ بالسنِّ، وأنَّه يُقْتَصُّ في الجروح، فمن تجاوز عن حقه في الاقتصاص من المُعتدي فذلك تكفير لبعض ذنوب المعتدى عليه وإزالةٌ لها. ومن لم يحكم بما أنزل الله في القصاص وغيره، فأولئك هم المتجاوزون حدود الله.');
INSERT INTO Quran__Arabic_Tafseer_Muyassar (QuranChapterID, VerseNumber, Rukuh, Sajdah, Manzil, HizbQuarter, VerseText) VALUES (5, 46, '0', '0', '0', '0', 'وأتبعنا أنبياء بني إسرائيل عيسى ابن مريم مؤمنًا بما في التوراة، عاملا بما فيها مما لم ينسخه كتابه، وأنزلنا إليه الإنجيل هاديا إلى الحق، ومبيِّنًا لما جهله الناس مِن حكم الله، وشاهدًا على صدق التوراة بما اشتمل عليه من أحكامها، وقد جعلناه بيانًا للذين يخافون الله وزاجرًا لهم عن ارتكاب المحرَّمات.');
INSERT INTO Quran__Arabic_Tafseer_Muyassar (QuranChapterID, VerseNumber, Rukuh, Sajdah, Manzil, HizbQuarter, VerseText) VALUES (5, 47, '0', '0', '0', '0', 'وليحكم أهل الإنجيل الذين أُرسِل إليهم عيسى بما أنزل الله فيه. ومن لم يحكم بما أنزل الله فأولئك هم الخارجون عن أمره، العاصون له.');
INSERT INTO Quran__Arabic_Tafseer_Muyassar (QuranChapterID, VerseNumber, Rukuh, Sajdah, Manzil, HizbQuarter, VerseText) VALUES (5, 48, '0', '0', '0', '0', 'وأنزلنا إليك -أيها الرسول- القرآن، وكل ما فيه حقّ يشهد على صدق الكتب قبله، وأنها من عند الله، مصدقًا لما فيها من صحة، ومبيِّنًا لما فيها من تحريف، ناسخًا لبعض شرائعها، فاحكم بين المحتكمين إليك من اليهود بما أنزل الله إليك في هذا القرآن، ولا تنصرف عن الحق الذي أمرك الله به إلى أهوائهم وما اعتادوه، فقد جعلنا لكل أمة شريعة، وطريقة واضحة يعملون بها. ولو شاء الله لجعل شرائعكم واحدة، ولكنه تعالى خالف بينها ليختبركم، فيظهر المطيع من العاصي، فسارعوا إلى ما هو خير لكم في الدارين بالعمل بما في القرآن، فإن مصيركم إلى الله، فيخبركم بما كنتم فيه تختلفون، ويجزي كلا بعمله.');
INSERT INTO Quran__Arabic_Tafseer_Muyassar (QuranChapterID, VerseNumber, Rukuh, Sajdah, Manzil, HizbQuarter, VerseText) VALUES (5, 49, '0', '0', '0', '0', 'واحكم -أيها الرسول- بين اليهود بما أنزل الله إليك في القرآن، ولا تتبع أهواء الذين يحتكمون إليك، واحذرهم أن يصدُّوك عن بعض ما أنزل الله إليك فتترك العمل به، فإن أعرض هؤلاء عمَّا تحكم به فاعلم أن الله يريد أن يصرفهم عن الهدى بسبب ذنوبٍ اكتسبوها من قبل. وإن كثيرًا من الناس لَخارجون عن طاعة ربهم.');
INSERT INTO Quran__Arabic_Tafseer_Muyassar (QuranChapterID, VerseNumber, Rukuh, Sajdah, Manzil, HizbQuarter, VerseText) VALUES (5, 50, '1', '0', '0', '0', 'أيريد هؤلاء اليهود أن تحكم بينهم بما تعارف عليه المشركون عبدةُ الأوثان من الضلالات والجهالات؟! لا يكون ذلك ولا يليق أبدًا ومَن أعدل مِن الله في حكمه لمن عقل عن الله شَرْعه، وآمن به، وأيقن أن حكم الله هو الحق؟');
INSERT INTO Quran__Arabic_Tafseer_Muyassar (QuranChapterID, VerseNumber, Rukuh, Sajdah, Manzil, HizbQuarter, VerseText) VALUES (5, 51, '0', '0', '0', '1', 'يا أيها الذين آمنوا لا تتخذوا اليهود والنصارى حلفاءَ وأنصارًا على أهل الإيمان؛ ذلك أنهم لا يُوادُّون المؤمنين، فاليهود يوالي بعضهم بعضًا، وكذلك النصارى، وكلا الفريقين يجتمع على عداوتكم. وأنتم -أيها المؤمنون- أجدرُ بأن ينصر بعضُكم بعضًا. ومن يتولهم منكم فإنه يصير من جملتهم، وحكمه حكمهم. إن الله لا يوفق الظالمين الذين يتولون الكافرين.');
INSERT INTO Quran__Arabic_Tafseer_Muyassar (QuranChapterID, VerseNumber, Rukuh, Sajdah, Manzil, HizbQuarter, VerseText) VALUES (5, 52, '0', '0', '0', '0', 'يخبر الله تعالى عن جماعة من المنافقين أنهم كانوا يبادرون في موادة اليهود لما في قلوبهم من الشكِّ والنفاق، ويقولون: إنما نوادُّهم خشية أن يظفروا بالمسلمين فيصيبونا معهم، قال الله تعالى ذكره: فعسى الله أن يأتي بالفتح -أي فتح "مكة"- وينصر نَبِيَّه، ويُظْهِر الإسلام والمسلمين على الكفار، أو يُهيِّئ من الأمور ما تذهب به قوةُ اليهود والنَّصارى، فيخضعوا للمسلمين، فحينئذٍ يندم المنافقون على ما أضمروا في أنفسهم من موالاتهم.');
INSERT INTO Quran__Arabic_Tafseer_Muyassar (QuranChapterID, VerseNumber, Rukuh, Sajdah, Manzil, HizbQuarter, VerseText) VALUES (5, 53, '0', '0', '0', '0', 'وحينئذ يقول بعض المؤمنين لبعض مُتعجِّبين من حال المنافقين -إذا كُشِف أمرهم-: أهؤلاء الذين أقسموا بأغلظ الأيمان إنهم لَمَعَنا؟! بطلت أعمال المنافقين التي عملوها في الدنيا، فلا ثواب لهم عليها؛ لأنهم عملوها على غير إيمان، فخسروا الدنيا والآخرة.');
INSERT INTO Quran__Arabic_Tafseer_Muyassar (QuranChapterID, VerseNumber, Rukuh, Sajdah, Manzil, HizbQuarter, VerseText) VALUES (5, 54, '0', '0', '0', '0', 'يا أيها الذين صدَّقوا الله ورسوله وعملوا بشرعه من يرجع منكم عن دينه، ويستبدل به اليهودية أو النصرانية أو غير ذلك، فلن يضرُّوا الله شيئًا، وسوف يأتي الله بقوم خير منهم يُحِبُّهم ويحبونه، رحماء بالمؤمنين أشدَّاء على الكافرين، يجاهدون أعداء الله، ولا يخافون في ذات الله أحدًا. ذلك الإنعام مِن فضل الله يؤتيه من أراد، والله واسع الفضل، عليم بمن يستحقه من عباده.');
INSERT INTO Quran__Arabic_Tafseer_Muyassar (QuranChapterID, VerseNumber, Rukuh, Sajdah, Manzil, HizbQuarter, VerseText) VALUES (5, 55, '0', '0', '0', '0', 'إنما ناصركم -أيُّها المؤمنون- الله ورسوله، والمؤمنون الذين يحافظون على الصلاة المفروضة، ويؤدون الزكاة عن رضا نفس، وهم خاضعون لله.');
INSERT INTO Quran__Arabic_Tafseer_Muyassar (QuranChapterID, VerseNumber, Rukuh, Sajdah, Manzil, HizbQuarter, VerseText) VALUES (5, 56, '1', '0', '0', '0', 'ومن وثق بالله وتولَّى الله ورسوله والمؤمنين، فهو من حزب الله، وحزب الله هم الغالبون المنتصرون.');
INSERT INTO Quran__Arabic_Tafseer_Muyassar (QuranChapterID, VerseNumber, Rukuh, Sajdah, Manzil, HizbQuarter, VerseText) VALUES (5, 57, '0', '0', '0', '0', 'يا أيها الذين صدَّقوا الله ورسوله وعملوا بشرعه، لا تتخذوا الذين يستهزئون ويتلاعبون بدينكم من أهل الكتاب والكفارَ أولياءَ، وخافوا الله إن كنتم مؤمنين به وبشرعه.');
INSERT INTO Quran__Arabic_Tafseer_Muyassar (QuranChapterID, VerseNumber, Rukuh, Sajdah, Manzil, HizbQuarter, VerseText) VALUES (5, 58, '0', '0', '0', '0', 'وإذا أذَّن مؤذنكم -أيها المؤمنون- بالصلاة سخر اليهود والنصارى والمشركون واستهزؤوا من دعوتكم إليها؛ وذلك بسبب جهلهم بربهم، وأنهم لا يعقلون حقيقة العبادة.');
INSERT INTO Quran__Arabic_Tafseer_Muyassar (QuranChapterID, VerseNumber, Rukuh, Sajdah, Manzil, HizbQuarter, VerseText) VALUES (5, 59, '0', '0', '0', '0', 'قل -أيها الرسول- لهؤلاء المستهزئين من أهل الكتاب: ما تَجِدُونه مطعنًا أو عيبًا هو محمدة لنا: من إيماننا بالله وكتبه المنزلة علينا، وعلى من كان قبلنا، وإيماننا بأن أكثركم خارجون عن الطريق المستقيم!');
INSERT INTO Quran__Arabic_Tafseer_Muyassar (QuranChapterID, VerseNumber, Rukuh, Sajdah, Manzil, HizbQuarter, VerseText) VALUES (5, 60, '0', '0', '0', '0', 'قل -أيها النبي- للمؤمنين: هل أخبركم بمن يُجازَى يوم القيامة جزاءً أشدَّ مِن جزاء هؤلاء الفاسقين؟ إنهم أسلافهم الذين طردهم الله من رحمته وغَضِب عليهم، ومَسَخَ خَلْقهم، فجعل منهم القردة والخنازير، بعصيانهم وافترائهم وتكبرهم، كما كان منهم عُبَّاد الطاغوت (وهو كل ما عُبِد من دون الله وهو راضٍ)، لقد ساء مكانهم في الآخرة، وضلَّ سَعْيُهم في الدنيا عن الطريق الصحيح.');
INSERT INTO Quran__Arabic_Tafseer_Muyassar (QuranChapterID, VerseNumber, Rukuh, Sajdah, Manzil, HizbQuarter, VerseText) VALUES (5, 61, '0', '0', '0', '0', 'وإذا جاءكم -أيها المؤمنون- منافقو اليهود، قالوا: آمنَّا، وهم مقيمون على كفرهم، قد دخلوا عليكم بكفرهم الذي يعتقدونه بقلوبهم، ثم خرجوا وهم مصرُّون عليه، والله أعلم بسرائرهم، وإن أظهروا خلاف ذلك.');
INSERT INTO Quran__Arabic_Tafseer_Muyassar (QuranChapterID, VerseNumber, Rukuh, Sajdah, Manzil, HizbQuarter, VerseText) VALUES (5, 62, '0', '0', '0', '0', 'وترى -أيها الرسول- كثيرًا من اليهود يبادرون إلى المعاصي من قول الكذب والزور، والاعتداء على أحكام الله، وأكْل أموال الناس بالباطل، لقد ساء عملهم واعتداؤهم.');
INSERT INTO Quran__Arabic_Tafseer_Muyassar (QuranChapterID, VerseNumber, Rukuh, Sajdah, Manzil, HizbQuarter, VerseText) VALUES (5, 63, '0', '0', '0', '0', 'هلا ينهى هؤلاء الذين يسارعون في الإثم والعدوان أئمتُهم وعلماؤهم، عن قول الكذب والزور، وأكل أموال الناس بالباطل، لقد ساء صنيعهم حين تركوا النهي عن المنكر.');
INSERT INTO Quran__Arabic_Tafseer_Muyassar (QuranChapterID, VerseNumber, Rukuh, Sajdah, Manzil, HizbQuarter, VerseText) VALUES (5, 64, '0', '0', '0', '0', 'يُطلع الله نَبِيَّه على شيء من مآثم اليهود -وكان مما يُسرُّونه فيما بينهم- أنهم قالوا: يد الله محبوسة عن فعل الخيرات، بَخِلَ علينا بالرزق والتوسعة، وذلك حين لحقهم جَدْب وقحط. غُلَّتْ أيديهم، أي: حبست أيديهم هم عن فِعْلِ الخيرات، وطردهم الله من رحمته بسبب قولهم. وليس الأمر كما يفترونه على ربهم، بل يداه مبسوطتان لا حَجْرَ عليه، ولا مانع يمنعه من الإنفاق، فإنه الجواد الكريم، ينفق على مقتضى الحكمة وما فيه مصلحة العباد. وفي الآية إثبات لصفة اليدين لله سبحانه وتعالى كما يليق به من غير تشبيه ولا تكييف. لكنهم سوف يزدادون طغيانًا وكفرًا بسبب حقدهم وحسدهم؛ لأن الله قد اصطفاك بالرسالة. ويخبر تعالى أن طوائف اليهود سيظلون إلى يوم القيامة يعادي بعضهم بعضًا، وينفر بعضهم من بعض، كلما تآمروا على الكيد للمسلمين بإثارة الفتن وإشعال نار الحرب ردَّ الله كيدهم، وفرَّق شملهم، ولا يزال اليهود يعملون بمعاصي الله مما ينشأ عنها الفساد والاضطراب في الأرض. والله تعالى لا يحب المفسدين.');
INSERT INTO Quran__Arabic_Tafseer_Muyassar (QuranChapterID, VerseNumber, Rukuh, Sajdah, Manzil, HizbQuarter, VerseText) VALUES (5, 65, '0', '0', '0', '0', 'ولو أن اليهود والنصارى صدَّقوا الله ورسوله، وامتثلوا أوامر الله واجتنبوا نواهيه، لكفَّرنا عنهم ذنوبهم، ولأدخلناهم جنات النعيم في الدار الآخرة.');
INSERT INTO Quran__Arabic_Tafseer_Muyassar (QuranChapterID, VerseNumber, Rukuh, Sajdah, Manzil, HizbQuarter, VerseText) VALUES (5, 66, '1', '0', '0', '0', 'ولو أنَّهم عملوا بما في التوراة والإنجيل، وبما أُنْزِل عليك أيها الرسول - وهو القرآن الكريم - لرُزِقوا من كلِّ سبيلٍ، فأنزلنا عليهم المطر، وأنبتنا لهم الثمر، وهذا جزاء الدنيا. وإنَّ مِن أهل الكتاب فريقًا معتدلا ثابتًا على الحق، وكثير منهم ساء عملُه، وضل عن سواء السبيل.');
INSERT INTO Quran__Arabic_Tafseer_Muyassar (QuranChapterID, VerseNumber, Rukuh, Sajdah, Manzil, HizbQuarter, VerseText) VALUES (5, 67, '0', '0', '0', '1', 'يا أيها الرسول بلِّغ وحي الله الذي أنزِل إليك من ربك، وإن قصَّرت في البلاغ فَكَتَمْتَ منه شيئًا، فإنك لم تُبَلِّغ رسالة ربِّك، وقد بلَّغ صلى الله عليه وسلم رسالة ربه كاملة، فمن زعم أنه كتم شيئًا مما أنزِل عليه، فقد أعظم على الله ورسوله الفرية. والله تعالى حافظك وناصرك على أعدائك، فليس عليك إلا البلاغ. إن الله لا يوفق للرشد مَن حاد عن سبيل الحق، وجحد ما جئت به من عند الله.');
INSERT INTO Quran__Arabic_Tafseer_Muyassar (QuranChapterID, VerseNumber, Rukuh, Sajdah, Manzil, HizbQuarter, VerseText) VALUES (5, 68, '0', '0', '0', '0', 'قل -أيها الرسول- لليهود والنصارى: إنكم لستم على حظٍّ من الدين ما دمتم لم تعملوا بما في التوراة والإنجيل، وما جاءكم به محمد من القرآن، وإن كثيرًا من أهل الكتاب لا يزيدهم إنزالُ القرآن إليك إلا تجبُّرًا وجحودًا، فهم يحسدونك؛ لأن الله بعثك بهذه الرسالة الخاتمة، التي بَيَّن فيها معايبهم، فلا تحزن -أيها الرسول- على تكذيبهم لك.');
INSERT INTO Quran__Arabic_Tafseer_Muyassar (QuranChapterID, VerseNumber, Rukuh, Sajdah, Manzil, HizbQuarter, VerseText) VALUES (5, 69, '0', '0', '0', '0', 'إن الذين آمنوا (وهم المسلمون) واليهود، والصابئين (وهم قوم باقون على فطرتهم، ولا دين مقرر لهم يتبعونه) والنصارى (وهم أتباع المسيح) من آمن منهم بالله الإيمان الكامل، وهو توحيد الله والتصديق بمحمد صلى الله عليه وسلم وبما جاء به، وآمن باليوم الآخر، وعمل العمل الصالح، فلا خوف عليهم من أهوال يوم القيامة، ولا هم يحزنون على ما تركوه وراءهم في الدنيا.');
INSERT INTO Quran__Arabic_Tafseer_Muyassar (QuranChapterID, VerseNumber, Rukuh, Sajdah, Manzil, HizbQuarter, VerseText) VALUES (5, 70, '0', '0', '0', '0', 'لقد أخذنا العهد المؤكَّد على بني إسرائيل في التوراة بالسمع والطاعة، وأرسلنا إليهم بذلك رسلنا، فَنَقَضوا ما أُخذ عليهم من العهد، واتبعوا أهواءهم، وكانوا كلما جاءهم رسول من أولئك الرسل بما لا تشتهيه أنفسهم عادَوْه: فكذبوا فريقًا من الرسل، وقتلوا فريقًا آخر.');
INSERT INTO Quran__Arabic_Tafseer_Muyassar (QuranChapterID, VerseNumber, Rukuh, Sajdah, Manzil, HizbQuarter, VerseText) VALUES (5, 71, '0', '0', '0', '0', 'وظنَّ هؤلاء العُصاة أن الله لن يأخذهم بالعذاب جزاء عصيانهم وعُتُوِّهم، فمضوا في شهواتهم، وعمُوا عن الهدى فلم يبصروه، وصَمُّوا عن سماع الحقِّ فلم ينتفعوا به، فأنزل الله بهم بأسه، فتابوا فتاب الله عليهم، ثم عَمِي كثيرٌ منهم، وصمُّوا، بعدما تبين لهم الحقُّ، والله بصير بأعمالهم خيرها وشرها وسيجازيهم عليها.');
INSERT INTO Quran__Arabic_Tafseer_Muyassar (QuranChapterID, VerseNumber, Rukuh, Sajdah, Manzil, HizbQuarter, VerseText) VALUES (5, 72, '0', '0', '0', '0', 'يقسم الله تعالى بأن الذين قالوا: إن الله هو المسيح ابن مريم، قد كفروا بمقالتهم هذه، وأخبر تعالى أن المسيح قال لبني إسرائيل: اعبدوا الله وحده لا شريك له، فأنا وأنتم في العبودية سواء. إنه من يعبد مع الله غيره فقد حرَّم الله عليه الجنة، وجعل النار مُستَقَرَّه، وليس له ناصرٌ يُنقذُه منها.');
INSERT INTO Quran__Arabic_Tafseer_Muyassar (QuranChapterID, VerseNumber, Rukuh, Sajdah, Manzil, HizbQuarter, VerseText) VALUES (5, 73, '0', '0', '0', '0', 'لقد كفر من النصارى من قال: إنَّ الله مجموع ثلاثة أشياء: هي الأب، والابن، وروح القدس. أما عَلِمَ هؤلاء النصارى أنه ليس للناس سوى معبود واحد، لم يلد ولم يولد، وإن لم ينته أصحاب هذه المقالة عن افترائهم وكذبهم ليُصِيبَنَّهم عذاب مؤلم موجع بسبب كفرهم بالله.');
INSERT INTO Quran__Arabic_Tafseer_Muyassar (QuranChapterID, VerseNumber, Rukuh, Sajdah, Manzil, HizbQuarter, VerseText) VALUES (5, 74, '0', '0', '0', '0', 'أفلا يرجع هؤلاء النصارى إلى الله تعالى، ويتولون عمَّا قالوا، ويسألون الله تعالى المغفرة؟ والله تعالى متجاوز عن ذنوب التائبين، رحيمٌ بهم');
INSERT INTO Quran__Arabic_Tafseer_Muyassar (QuranChapterID, VerseNumber, Rukuh, Sajdah, Manzil, HizbQuarter, VerseText) VALUES (5, 75, '0', '0', '0', '0', 'ما المسيح ابن مريم عليه السلام إلا رسولٌ كمن تقدمه من الرسل، وأُمُّه قد صَدَّقت تصديقًا جازمًا علمًا وعملا وهما كغيرهما من البشر يحتاجان إلى الطعام، ولا يكون إلهًا مَن يحتاج الى الطعام ليعيش. فتأمَّل -أيها الرسول- حال هؤلاء الكفار. لقد وضحنا العلاماتِ الدالةَ على وحدانيتنا، وبُطلان ما يَدَّعونه في أنبياء الله. ثم هم مع ذلك يَضِلُّون عن الحق الذي نَهديهم إليه، ثم انظر كيف يُصرفون عن الحق بعد هذا البيان؟');
INSERT INTO Quran__Arabic_Tafseer_Muyassar (QuranChapterID, VerseNumber, Rukuh, Sajdah, Manzil, HizbQuarter, VerseText) VALUES (5, 76, '0', '0', '0', '0', 'قل -أيها الرسول- لهؤلاء الكفرة: كيف تشركون مع الله من لا يَقْدِرُ على ضَرِّكم، ولا على جَلْبِ نفع لكم؟ والله هو السميع لأقوال عباده، العليم بأحوالهم.');
INSERT INTO Quran__Arabic_Tafseer_Muyassar (QuranChapterID, VerseNumber, Rukuh, Sajdah, Manzil, HizbQuarter, VerseText) VALUES (5, 77, '1', '0', '0', '0', 'قل -أيها الرسول- للنصارى: لا تتجاوزوا الحقَّ فيما تعتقدونه من أمر المسيح، ولا تتبعوا أهواءكم، كما اتَّبع اليهود أهواءهم في أمر الدين، فوقعوا في الضلال، وحملوا كثيرًا من الناس على الكفر بالله، وخرجوا عن طريق الاستقامة الى طريق الغَواية والضلال.');
INSERT INTO Quran__Arabic_Tafseer_Muyassar (QuranChapterID, VerseNumber, Rukuh, Sajdah, Manzil, HizbQuarter, VerseText) VALUES (5, 78, '0', '0', '0', '0', 'يخبر تعالى أنه طرد من رحمته الكافرين من بني إسرائيل في الكتاب الذي أنزله على داود -عليه السلام- وهو الزَّبور، وفي الكتاب الذي أنزله على عيسى - عليه السلام - وهو الإنجيل؛ بسبب عصيانهم واعتدائهم على حرمات الله.');
INSERT INTO Quran__Arabic_Tafseer_Muyassar (QuranChapterID, VerseNumber, Rukuh, Sajdah, Manzil, HizbQuarter, VerseText) VALUES (5, 79, '0', '0', '0', '0', 'كان هؤلاء اليهود يُجاهرون بالمعاصي ويرضونها، ولا يَنْهى بعضُهم بعضًا عن أيِّ منكر فعلوه، وهذا من أفعالهم السيئة، وبه استحقوا أن يُطْرَدُوا من رحمة الله تعالى.');
INSERT INTO Quran__Arabic_Tafseer_Muyassar (QuranChapterID, VerseNumber, Rukuh, Sajdah, Manzil, HizbQuarter, VerseText) VALUES (5, 80, '0', '0', '0', '0', 'تَرَى -أيها الرسول- كثيرًا من هؤلاء اليهود يتخذون المشركين أولياء لهم، ساء ما عملوه من الموالاة التي كانت سببًا في غضب الله عليهم، وخلودهم في عذاب الله يوم القيامة.');
INSERT INTO Quran__Arabic_Tafseer_Muyassar (QuranChapterID, VerseNumber, Rukuh, Sajdah, Manzil, HizbQuarter, VerseText) VALUES (5, 81, '0', '0', '0', '0', 'ولو أن هؤلاء اليهود الذين يناصرون المشركين كانوا قد آمنوا بالله تعالى والنبي محمد صلى الله عليه وسلم، وأقرُّوا بما أنزل إليه -وهو القرآن الكريم- ما اتخذوا الكفار أصحابًا وأنصارًا، ولكن كثيرًا منهم خارجون عن طاعة الله ورسوله.');
INSERT INTO Quran__Arabic_Tafseer_Muyassar (QuranChapterID, VerseNumber, Rukuh, Sajdah, Manzil, HizbQuarter, VerseText) VALUES (5, 82, '0', '0', '0', '1', 'لتجدنَّ -أيها الرسول- أشدَّ الناس عداوة للذين صدَّقوك وآمنوا بك واتبعوك، اليهودَ؛ لعنادهم، وجحودهم، وغمطهم الحق، والذين أشركوا مع الله غيره، كعبدة الأوثان وغيرهم، ولتجدنَّ أقربهم مودة للمسلمين الذين قالوا: إنا نصارى، ذلك بأن منهم علماء بدينهم متزهدين وعبَّادًا في الصوامع متنسكين، وأنهم متواضعون لا يستكبرون عن قَبول الحق، وهؤلاء هم الذين قبلوا رسالة محمد صلى الله عليه وسلم، وآمنوا بها.');
INSERT INTO Quran__Arabic_Tafseer_Muyassar (QuranChapterID, VerseNumber, Rukuh, Sajdah, Manzil, HizbQuarter, VerseText) VALUES (5, 83, '0', '0', '0', '0', 'ومما يدل على قرب مودتهم للمسلمين أن فريقًا منهم (وهم وفد الحبشة لما سمعوا القرآن) فاضت أعينهم من الدمع فأيقنوا أنه حقٌّ منزل من عند الله تعالى، وصدَّقوا بالله واتبعوا رسوله، وتضرعوا إلى الله أن يكرمهم بشرف الشهادة مع أمَّة محمد عليه السلام على الأمم يوم القيامة.');
INSERT INTO Quran__Arabic_Tafseer_Muyassar (QuranChapterID, VerseNumber, Rukuh, Sajdah, Manzil, HizbQuarter, VerseText) VALUES (5, 84, '0', '0', '0', '0', 'وقالوا: وأيُّ لوم علينا في إيماننا بالله، وتصديقنا بالحق الذي جاءنا به محمد صلى الله عليه وسلم من عند الله، واتباعنا له، ونرجو أن يدخلنا ربنا مع أهل طاعته في جنته يوم القيامة؟');
INSERT INTO Quran__Arabic_Tafseer_Muyassar (QuranChapterID, VerseNumber, Rukuh, Sajdah, Manzil, HizbQuarter, VerseText) VALUES (5, 85, '0', '0', '0', '0', 'فجزاهم الله بما قالوا من الاعتزاز بإيمانهم بالإسلام، وطلبهم أن يكونوا مع القوم الصالحين، جنات تجري من تحت أشجارها الأنهار، ماكثين فيها لا يخرجون منها، ولا يُحوَّلون عنها، وذلك جزاء إحسانهم في القول والعمل.');
INSERT INTO Quran__Arabic_Tafseer_Muyassar (QuranChapterID, VerseNumber, Rukuh, Sajdah, Manzil, HizbQuarter, VerseText) VALUES (5, 86, '1', '0', '0', '0', 'والذين جحدوا وحدانية الله وأنكروا نبوة محمد صلى الله عليه وسلم، وكذَّبوا بآياته المنزلة على رسله، أولئك هم أصحاب النار الملازمون لها.');
INSERT INTO Quran__Arabic_Tafseer_Muyassar (QuranChapterID, VerseNumber, Rukuh, Sajdah, Manzil, HizbQuarter, VerseText) VALUES (5, 87, '0', '0', '0', '0', 'يا أيها الذين آمنوا لا تحرِّموا طيبات أحلَّها الله لكم من المطاعم والمشارب ونكاح النساء، فتضيقوا ما وسَّع الله عليكم، ولا تتجاوزوا حدود ما حرَّم الله. إن الله لا يحب المعتدين.');
INSERT INTO Quran__Arabic_Tafseer_Muyassar (QuranChapterID, VerseNumber, Rukuh, Sajdah, Manzil, HizbQuarter, VerseText) VALUES (5, 88, '0', '0', '0', '0', 'وتمتعوا -أيها المؤمنون- بالحلال الطيب مما أعطاكم الله ومنحكم إياه، واتقوا الله بامتثال أوامره، واجتناب نواهيه؛ فإن إيمانكم بالله يوجب عليكم تقواه ومراقبته.');
INSERT INTO Quran__Arabic_Tafseer_Muyassar (QuranChapterID, VerseNumber, Rukuh, Sajdah, Manzil, HizbQuarter, VerseText) VALUES (5, 89, '0', '0', '0', '0', 'لا يعاقبكم الله -أيها المسلمون- فيما لا تقصدون عَقْدَه من الأيمان، مثل قول بعضكم: لا والله، وبلى والله، ولكن يعاقبكم فيما قصدتم عقده بقلوبكم، فإذا لم تَفُوا باليمين فإثم ذلك يمحوه الله بما تقدِّمونه مما شرعه الله لكم كفارة من إطعام عشرة مساكين، لكل مسكين نصف صاع من أوسط طعام أهل البلد، أو كسوتهم، لكل مسكين ما يكفي في الكسوة عُرفًا، أو إعتاق مملوك من الرق، فالحالف الذي لم يف بيمينه مخير بين هنا الأمور الثلاثة، فمن لم يجد شيئًا من ذلك فعليه صيام ثلاثة أيام. تلك مكفرات عدم الوفاء بأيمانكم، واحفظوا -أيها المسلمون- أيمانكم: باجتناب الحلف، أو الوفاء إن حلفتم، أو الكفارة إذا لم تفوا بها. وكما بيَّن الله لكم حكم الأيمان والتحلل منها يُبيِّن لكم أحكام دينه؛ لتشكروا له على هدايته إياكم إلى الطريق المستقيم.');
INSERT INTO Quran__Arabic_Tafseer_Muyassar (QuranChapterID, VerseNumber, Rukuh, Sajdah, Manzil, HizbQuarter, VerseText) VALUES (5, 90, '0', '0', '0', '0', 'يا أيها الذين صدَّقوا الله ورسوله وعملوا بشرعه، إنما الخمر: وهي كل مسكر يغطي العقل، والميسر: وهو القمار، وذلك يشمل المراهنات ونحوها، مما فيه عوض من الجانبين، وصدٌّ عن ذكر الله، والأنصاب: وهي الحجارة التي كان المشركون يذبحون عندها تعظيمًا لها، وما ينصب للعبادة تقربًا إليه، والأزلام: وهي القِداح التي يستقسم بها الكفار قبل الإقدام على الشيء، أو الإحجام عنه، إن ذلك كله إثمٌ مِن تزيين الشيطان، فابتعدوا عن هذه الآثام، لعلكم تفوزون بالجنة.');
INSERT INTO Quran__Arabic_Tafseer_Muyassar (QuranChapterID, VerseNumber, Rukuh, Sajdah, Manzil, HizbQuarter, VerseText) VALUES (5, 91, '0', '0', '0', '0', 'إنما يريد الشيطان بتزيين الآثام لكم أن يُلقِي بينكم ما يوجد العداوة والبغضاء، بسبب شرب الخمر ولعب الميسر، ويصرفكم عن ذكر الله وعن الصلاة بغياب العقل في شرب الخمر، والاشتغال باللهو في لعب الميسر، فانتهوا عن ذلك.');
INSERT INTO Quran__Arabic_Tafseer_Muyassar (QuranChapterID, VerseNumber, Rukuh, Sajdah, Manzil, HizbQuarter, VerseText) VALUES (5, 92, '0', '0', '0', '0', 'وامتثلوا -أيها المسلمون- طاعة الله وطاعة رسوله محمد صلى الله عليه وسلم في كل ما تفعلون وتتركون، واتقوا الله وراقبوه في ذلك، فإن أعرضتم عن الامتثال فعملتم ما نهيتم عنه، فاعلموا أنما على رسولنا محمد صلى الله عليه وسلم البلاغ المبين.');
INSERT INTO Quran__Arabic_Tafseer_Muyassar (QuranChapterID, VerseNumber, Rukuh, Sajdah, Manzil, HizbQuarter, VerseText) VALUES (5, 93, '1', '0', '0', '0', 'ليس على المؤمنين الذين شربوا الخمر قبل تحريمها إثم في ذلك، إذا تركوها واتقوا سخط الله وآمنوا به، وقدَّموا الأعمال الصالحة التي تدل على إيمانهم ورغبتهم في رضوان الله تعالى عنهم، ثم ازدادوا بذلك مراقبة لله عز وجل وإيمانا به، حتى أصبحوا مِن يقينهم يعبدونه، وكأنهم يرونه. وإن الله تعالى يحب الذين بلغوا درجة الإحسان حتى أصبح إيمانهم بالغيب كالمشاهدة.');
INSERT INTO Quran__Arabic_Tafseer_Muyassar (QuranChapterID, VerseNumber, Rukuh, Sajdah, Manzil, HizbQuarter, VerseText) VALUES (5, 94, '0', '0', '0', '0', 'يا أيها الذين صدقوا الله ورسوله وعملوا بشرعه، ليبلونكم الله بشيء من الصيد يقترب منكم على غير المعتاد حيث تستطيعون أَخْذَ صغاره بغير سلاح وأخذ كباره بالسلاح؛ ليعلم الله علمًا ظاهرًا للخلق الذين يخافون ربهم بالغيب، ليقينهم بكمال علمه بهم، وذلك بإمساكهم عن الصيد، وهم محرمون. فمن تجاوز حَدَّه بعد هذا البيان فأقدم على الصيد -وهو مُحْرِم- فإنه يستحق العذاب الشديد.');
INSERT INTO Quran__Arabic_Tafseer_Muyassar (QuranChapterID, VerseNumber, Rukuh, Sajdah, Manzil, HizbQuarter, VerseText) VALUES (5, 95, '0', '0', '0', '0', 'يا أيها الذين صدَّقوا الله ورسوله وعملوا بشرعه لا تقتلوا صيد البر، وأنتم محرمون بحج أو عمرة، أو كنتم داخل الحرم ومَن قتل أيَّ نوعٍ من صيد البرِّ متعمدًا فجزاء ذلك أن يذبح مثل ذلك الصيد من بهيمة الأنعام: الإبل أو البقر أو الغنم، بعد أن يُقَدِّره اثنان عدلان، وأن يهديه لفقراء الحرم، أو أن يشتري بقيمة مثله طعامًا يهديه لفقراء الحرم لكل مسكين نصف صاع، أو يصوم بدلا من ذلك يوما عن كل نصف صاع من ذلك الطعام، فَرَضَ الله عليه هذا الجزاء؛ ليلقى بإيجاب الجزاء المذكور عاقبة فِعْله. والذين وقعوا في شيء من ذلك قبل التحريم فإن الله تعالى قد عفا عنهم، ومَن عاد إلى المخالفة متعمدًا بعد التحريم، فإنه مُعَرَّض لانتقام الله منه. والله تعالى عزيز قويٌّ منيع في سلطانه، ومِن عزته أنه ينتقم ممن عصاه إذا أراد، لا يمنعه من ذلك مانع.');
INSERT INTO Quran__Arabic_Tafseer_Muyassar (QuranChapterID, VerseNumber, Rukuh, Sajdah, Manzil, HizbQuarter, VerseText) VALUES (5, 96, '0', '0', '0', '0', 'أحل الله لكم -أيها المسلمون- في حال إحرامكم صيد البحر، وهو ما يصاد منه حيًّا، وطعامه: وهو الميت منه؛ من أجل انتفاعكم به مقيمين أو مسافرين، وحرم عليكم صيد البَرِّ ما دمتم محرمين بحج أو عمرة. واخشوا الله ونفذوا جميع أوامِره، واجتنبوا جميع نواهيه؛ حتى تظفَروا بعظيم ثوابه، وتَسْلموا من أليم عقابه عندما تحشرون للحساب والجزاء.');
INSERT INTO Quran__Arabic_Tafseer_Muyassar (QuranChapterID, VerseNumber, Rukuh, Sajdah, Manzil, HizbQuarter, VerseText) VALUES (5, 97, '0', '0', '0', '1', 'امتنَّ الله على عباده بأن جعل الكعبة البيت الحرام صلاحًا لدينهم، وأمنًا لحياتهم؛ وذلك حيث آمنوا بالله ورسوله وأقاموا فرائضه، وحرَّم العدوان والقتال في الأشهر الحرم (وهي ذو القعدة وذو الحجة والمحرم ورجب) فلا يعتدي فيها أحد على أحد، وحرَّم تعالى الاعتداء على ما يُهدَى إلى الحرم من بهيمة الأنعام، وحرَّم كذلك الاعتداء على القلائد، وهي ما قُلِّد إشعارًا بأنه بقصد به النسك؛ ذلك لتعلموا أن الله يعلم جميع ما في السموات وما في الأرض، ومن ذلك ما شرعه لحماية خلقه بعضهم من بعض، وأن الله بكل شيء عليم، فلا تخفى عليه خافية.');
INSERT INTO Quran__Arabic_Tafseer_Muyassar (QuranChapterID, VerseNumber, Rukuh, Sajdah, Manzil, HizbQuarter, VerseText) VALUES (5, 98, '0', '0', '0', '0', 'اعلموا -أيها الناس- أن الله جل وعلا شديد العقاب لمن عصاه، وأن الله غفور رحيم لمن تاب وأناب.');
INSERT INTO Quran__Arabic_Tafseer_Muyassar (QuranChapterID, VerseNumber, Rukuh, Sajdah, Manzil, HizbQuarter, VerseText) VALUES (5, 99, '0', '0', '0', '0', 'يبيِّن الله تعالى أن مهمة رسوله صلى الله عليه وسلم هداية الدلالة والتبليغ، وبيد الله -وحده- هداية التوفيق، وأن ما تنطوي عليه نفوس الناس مما يُسرون أو يعلنون من الهداية أو الضلال يعلمه الله.');
INSERT INTO Quran__Arabic_Tafseer_Muyassar (QuranChapterID, VerseNumber, Rukuh, Sajdah, Manzil, HizbQuarter, VerseText) VALUES (5, 100, '1', '0', '0', '0', 'قل -أيها الرسول-: لا يستوي الخبيث والطيب من كل شيء، فالكافر لا يساوي المؤمن، والعاصي لا يساوي المطيع، والجاهل لا يساوي العالم، والمبتدع لا يساوي المتبع، والمال الحرام لا يساوي الحلال، ولو أعجبك -أيها الإنسان- كثرة الخبيث وعدد أهله. فاتقوا الله يا أصحاب العقول الراجحة باجتناب الخبائث، وفعل الطيبات؛ لتفلحوا بنيل المقصود الأعظم، وهو رضا الله تعالى والفوز بالجنة.');
INSERT INTO Quran__Arabic_Tafseer_Muyassar (QuranChapterID, VerseNumber, Rukuh, Sajdah, Manzil, HizbQuarter, VerseText) VALUES (5, 101, '0', '0', '0', '0', 'يا أيها الذين صدَّقوا الله ورسوله وعملوا بشرعه لا تسألوا عن أشياء من أمور الدين لم تؤمروا فيها بشيء، كالسؤال عن الأمور غير الواقعة، أو التي يترتب عليها تشديدات في الشرع، ولو كُلِّفتموها لشقَّتْ عليكم، وإن تسألوا عنها في حياة رسول الله صلى الله عليه وسلم وحين نزول القرآن عليه تُبيَّن لكم، وقد تُكلَّفونها فتعجزون عنها، تركها الله معافيًا لعباده منها. والله غفور لعباده إذا تابوا، حليم عليهم فلا يعاقبهم وقد أنابوا إليه.');
INSERT INTO Quran__Arabic_Tafseer_Muyassar (QuranChapterID, VerseNumber, Rukuh, Sajdah, Manzil, HizbQuarter, VerseText) VALUES (5, 102, '0', '0', '0', '0', 'إن مثل تلك الأسئلة قد سألها قومٌ مِن قبلكم رسلَهم، فلما أُمِروا بها جحدوها، ولم ينفذوها، فاحذروا أن تكونوا مثلهم.');
INSERT INTO Quran__Arabic_Tafseer_Muyassar (QuranChapterID, VerseNumber, Rukuh, Sajdah, Manzil, HizbQuarter, VerseText) VALUES (5, 103, '0', '0', '0', '0', 'ما شرع الله للمشركين ما ابتدعوه في بهيمة الأنعام مِن تَرْك الانتفاع ببعضها وجعلها للأصنام، وهي: البَحيرة التي تُقطع أذنها إذا ولدت عددًا من البطون، والسائبة وهي التي تُترك للأصنام، والوصيلة وهي التي تتصل ولادتها بأنثى بعد أنثى، والحامي وهو الذكر من الإبل إذا وُلد من صلبه عدد من الإبل، ولكن الكفار نسبوا ذلك إلى الله تعالى افتراء عليه، وأكثر الكافرين لا يميزون الحق من الباطل.');
INSERT INTO Quran__Arabic_Tafseer_Muyassar (QuranChapterID, VerseNumber, Rukuh, Sajdah, Manzil, HizbQuarter, VerseText) VALUES (5, 104, '0', '0', '0', '0', 'وإذا قيل لهؤلاء الكفار المحرِّمين ما أحل الله: تعالوا إلى تنزيل الله وإلى رسوله ليتبين لكم الحلال والحرام، قالوا: يكفينا ما ورثناه عن آبائنا من قول وعمل، أيقولون ذلك ولو كان آباؤهم لا يعلمون شيئًا أي: لا يفهمون حقًّا ولا يعرفونه، ولا يهتدون إليه؟ فكيف يتبعونهم، والحالة هذه؟ فإنه لا يتبعهم إلا من هو أجهل منهم وأضل سبيلا.');
INSERT INTO Quran__Arabic_Tafseer_Muyassar (QuranChapterID, VerseNumber, Rukuh, Sajdah, Manzil, HizbQuarter, VerseText) VALUES (5, 105, '0', '0', '0', '0', 'يا أيها الذين صدَّقوا الله ورسوله وعملوا بشرعه ألزموا أنفسكم بالعمل بطاعة الله واجتناب معصيته، وداوموا على ذلك وإن لم يستجب الناس لكم، فإذا فعلتم ذلك فلا يضركم ضلال مَن ضلَّ إذا لزمتم طريق الاستقامة، وأمرتم بالمعروف ونهيتم عن المنكر، إلى الله مرجعكم جميعًا في الآخرة، فيخبركم بأعمالكم، ويجازيكم عليها.');
INSERT INTO Quran__Arabic_Tafseer_Muyassar (QuranChapterID, VerseNumber, Rukuh, Sajdah, Manzil, HizbQuarter, VerseText) VALUES (5, 106, '0', '0', '0', '0', 'يا أيها الذين صدَّقوا الله ورسوله وعملوا بشرعه إذا قرب الموت من أحدكم، فلْيُشْهِد على وصيته اثنين أمينين من المسلمين أو آخرين من غير المسلمين عند الحاجة، وعدم وجود غيرهما من المسلمين، تُشهدونهما إن أنتم سافرتم في الأرض فحلَّ بكم الموت، وإن ارتبتم في شهادتهما فقفوهما من بعد الصلاة -أي صلاة المسلمين، وبخاصة صلاة العصر-، فيقسمان بالله قسمًا خالصًا لا يأخذان به عوضًا من الدنيا، ولا يحابيان به ذا قرابة منهما، ولا يكتمان به شهادة لله عندهما، وأنهما إن فَعَلا ذلك فهما من المذنبين.');
INSERT INTO Quran__Arabic_Tafseer_Muyassar (QuranChapterID, VerseNumber, Rukuh, Sajdah, Manzil, HizbQuarter, VerseText) VALUES (5, 107, '0', '0', '0', '0', 'فإن اطلع أولياء الميت على أن الشاهدين المذكورين قد أثما بالخيانة في الشهادة أو الوصية فليقم مقامهما في الشهادة اثنان من أولياء الميت فيقسمان بالله: لَشهادتنا الصادقة أولى بالقبول من شهادتهما الكاذبة، وما تجاوزنا الحق في شهادتنا، إنا إن اعتدينا وشهدنا بغير الحق لمن الظالمين المتجاوزين حدود الله.');
INSERT INTO Quran__Arabic_Tafseer_Muyassar (QuranChapterID, VerseNumber, Rukuh, Sajdah, Manzil, HizbQuarter, VerseText) VALUES (5, 108, '1', '0', '0', '0', 'ذلك الحكم عند الارتياب في الشاهدين من الحلف بعد الصلاة وعدم قبول شهادتهما، أقرب إلى أن يأتوا بالشهادة على حقيقتها خوفًا من عذاب الآخرة، أو خشية من أن ترد اليمين الكاذبة من قِبَل أصحاب الحق بعد حلفهم، فيفتضح الكاذب الذي ردت يمينه في الدنيا وقت ظهور خيانته. وخافوا الله -أيها الناس- وراقبوه أن تحلفوا كذبًا، وأن تقتطعوا بأيمانكم مالا حرامًا، واسمعوا ما توعظون به. والله لا يهدي القوم الفاسقين الخارجين عن طاعته.');
INSERT INTO Quran__Arabic_Tafseer_Muyassar (QuranChapterID, VerseNumber, Rukuh, Sajdah, Manzil, HizbQuarter, VerseText) VALUES (5, 109, '0', '0', '0', '1', 'واذكروا -أيها الناس- يوم القيامة يوم يجمع الله الرسل عليهم السلام، فيسألهم عن جواب أممهم لهم حينما دعوهم إلى التوحيد فيجيبون: لا علم لنا، فنحن لا نعلم ما في صدور الناس، ولا ما أحدثوا بعدنا. إنك أنت عليم بكل شيء مما ظهر وخفي.');
INSERT INTO Quran__Arabic_Tafseer_Muyassar (QuranChapterID, VerseNumber, Rukuh, Sajdah, Manzil, HizbQuarter, VerseText) VALUES (5, 110, '0', '0', '0', '0', 'إذ قال الله يوم القيامة: يا عيسى ابن مريم اذكر نعمتي عليك إذ خلقتك من غير أب، وعلى والدتك حيث اصطفيتها على نساء العالمين، وبرأتها مما نُسِب إليها، ومن هذه النعم على عيسى أنه قوَّاه وأعانه بجبريل عليه السلام، يكلم الناس وهو رضيع، ويدعوهم إلى الله وهو كبير بما أوحاه الله إليه من التوحيد، ومنها أن الله تعالى علَّمه الكتابة والخط بدون معلم، ووهبه قوة الفهم والإدراك، وعَلَّمه التوراة التي أنزلها على موسى عليه السلام، والإنجيل الذي أنزل عليه هداية للناس، ومن هذه النعم أنه يصوِّر من الطين كهيئة الطير فينفخ في تلك الهيئة، فتكون طيرًا بإذن الله، ومنها أنه يشفي الذي وُلِد أعمى فيبصر، ويشفي الأبرص، فيعود جلده سليمًا بإذن الله، ومنها أنه يدعو الله أن يحييَ الموتى فيقومون من قبورهم أحياء، وذلك كله بإرادة الله تعالى وإذنه، وهي معجزات باهرة تؤيد نبوة عيسى عليه السلام، ثم يذكِّره الله جل وعلا نعمته عليه إذ منع بني إسرائيل حين همُّوا بقتله، وقد جاءهم بالمعجزات الواضحة الدالة على نبوته، فقال الذين كفروا منهم: إنَّ ما جاء به عيسى من البينات سحر ظاهر.');
INSERT INTO Quran__Arabic_Tafseer_Muyassar (QuranChapterID, VerseNumber, Rukuh, Sajdah, Manzil, HizbQuarter, VerseText) VALUES (5, 111, '0', '0', '0', '0', 'واذكر نعمتي عليك، إذ ألهمتُ، وألقيتُ في قلوب جماعة من خلصائك أن يصدقوا بوحدانية الله تعالى ونبوتك، فقالوا: صدَّقنا يا ربنا، واشهد بأننا خاضعون لك منقادون لأمرك.');
INSERT INTO Quran__Arabic_Tafseer_Muyassar (QuranChapterID, VerseNumber, Rukuh, Sajdah, Manzil, HizbQuarter, VerseText) VALUES (5, 112, '0', '0', '0', '0', 'واذكر إذ قال الحواريون: يا عيسى ابن مريم هل يستطيع ربك إن سألته أن ينزل علينا مائدة طعام من السماء؟ فكان جوابه أن أمرهم بأن يتقوا عذاب الله تعالى، إن كانوا مؤمنين حقَّ الإيمان.');
INSERT INTO Quran__Arabic_Tafseer_Muyassar (QuranChapterID, VerseNumber, Rukuh, Sajdah, Manzil, HizbQuarter, VerseText) VALUES (5, 113, '0', '0', '0', '0', 'قال الحواريون: نريد أن نأكل من المائدة وتسكن قلوبنا لرؤيتها، ونعلم يقينا صدقك في نبوتك، وأن نكون من الشاهدين على هذه الآية أن الله أنزلها حجة له علينا في توحيده وقدرته على ما يشاء، وحجة لك على صدقك في نبوتك.');
INSERT INTO Quran__Arabic_Tafseer_Muyassar (QuranChapterID, VerseNumber, Rukuh, Sajdah, Manzil, HizbQuarter, VerseText) VALUES (5, 114, '0', '0', '0', '0', 'أجاب عيسى ابن مريم طلب الحواريين فدعا ربه جل وعلا قائلا ربنا أنزل علينا مائدة طعام من السماء، نتخذ يوم نزولها عيدًا لنا، نعظمه نحن ومَن بعدنا، وتكون المائدة علامة وحجة منك يا ألله على وحدانيتك وعلى صدق نبوتي، وامنحنا من عطائك الجزيل، وأنت خير الرازقين.');
INSERT INTO Quran__Arabic_Tafseer_Muyassar (QuranChapterID, VerseNumber, Rukuh, Sajdah, Manzil, HizbQuarter, VerseText) VALUES (5, 115, '1', '0', '0', '0', 'قال الله تعالى: إني منزل مائدة الطعام عليكم، فمن يجحد منكم وحدانيتي ونبوة عيسى عليه السلام بعد نزول المائدة فإني أعذبه عذابًا شديدًا، لا أعذبه أحدًا من العالمين. وقد نزلت المائدة كما وعد الله.');
INSERT INTO Quran__Arabic_Tafseer_Muyassar (QuranChapterID, VerseNumber, Rukuh, Sajdah, Manzil, HizbQuarter, VerseText) VALUES (5, 116, '0', '0', '0', '0', 'واذكر إذ قال الله تعالى يوم القيامة: يا عيسى ابن مريم أأنت قلت للناس اجعلوني وأمي معبودين من دون الله؟ فأجاب عيسى -منزِّهًا الله تعالى-: ما ينبغي لي أن أقول للناس غير الحق. إن كنتُ قلتُ هذا فقد علمتَه؛ لأنه لا يخفى عليك شيء، تعلم ما تضمره نفسي، ولا أعلم أنا ما في نفسك. إنك أنت عالمٌ بكل شيء مما ظهر أو خفي.');
INSERT INTO Quran__Arabic_Tafseer_Muyassar (QuranChapterID, VerseNumber, Rukuh, Sajdah, Manzil, HizbQuarter, VerseText) VALUES (5, 117, '0', '0', '0', '0', 'قال عيسى عليه السلام: يا ربِّ ما قلتُ لهم إلا ما أوحيته إليَّ، وأمرتني بتبليغه من إفرادك بالتوحيد والعبادة، وكنتُ على ما يفعلونه -وأنا بين أظهرهم- شاهدًا عليهم وعلى أفعالهم وأقوالهم، فلما وفيتني أجلي على الأرض، ورفعتني إلى السماء حيًّا، كنت أنت المطَّلِع على سرائرهم، وأنت على كل شيء شهيد، لا تخفى عليك خافية في الأرض ولا في السماء.');
INSERT INTO Quran__Arabic_Tafseer_Muyassar (QuranChapterID, VerseNumber, Rukuh, Sajdah, Manzil, HizbQuarter, VerseText) VALUES (5, 118, '0', '0', '0', '0', 'إنك يا ألله إن تعذبهم فإنهم عبادك -وأنت أعلم بأحوالهم-، تفعل بهم ما تشاء بعدلك، وإن تغفر برحمتك لمن أتى منهم بأسباب المغفرة، فإنك أنت العزيز الذي لا يغالَبُ، الحكيم في تدبيره وأمره. وهذه الآية ثناء على الله -تعالى- بحكمته وعدله، وكمال علمه.');
INSERT INTO Quran__Arabic_Tafseer_Muyassar (QuranChapterID, VerseNumber, Rukuh, Sajdah, Manzil, HizbQuarter, VerseText) VALUES (5, 119, '0', '0', '0', '0', 'قال الله تعالى لعيسى عليه السلام يوم القيامة: هذا يوم الجزاء الذي ينفع الموحدين توحيدهم ربهم، وانقيادهم لشرعه، وصدقهم في نياتهم وأقوالهم وأعمالهم، لهم جنات تجري من تحت قصورها الأنهار، ماكثين فيها أبدًا، رضي الله عنهم فقبل حسناتهم، ورضوا عنه بما أعطاهم من جزيل ثوابه. ذلك الجزاء والرضا منه عليهم هو الفوز العظيم.');
INSERT INTO Quran__Arabic_Tafseer_Muyassar (QuranChapterID, VerseNumber, Rukuh, Sajdah, Manzil, HizbQuarter, VerseText) VALUES (5, 120, '1', '0', '0', '0', 'لله وحده لا شريك له ملك السموات والأرض وما فيهن، وهو -سبحانه- على كل شيء قدير لا يعجزه شيء.');
INSERT INTO Quran__Arabic_Tafseer_Muyassar (QuranChapterID, VerseNumber, Rukuh, Sajdah, Manzil, HizbQuarter, VerseText) VALUES (6, 1, '0', '0', '0', '0', 'الثناء على الله بصفاته التي كلّها أوصاف كمال، وبنعمه الظاهرة والباطنة، الدينية والدنيوية، الذي أنشأ السموات والأرض وما فيهن، وخلق الظلمات والنور، وذلك بتعاقب الليل والنهار. وفي هذا دلالة على عظمة الله تعالى، واستحقاقه وحده العبادة، فلا يجوز لأحد أن يشرك به غيره. ومع هذا الوضوح فإن الكافرين يسوون بالله غيره، ويشركون به.');
INSERT INTO Quran__Arabic_Tafseer_Muyassar (QuranChapterID, VerseNumber, Rukuh, Sajdah, Manzil, HizbQuarter, VerseText) VALUES (6, 2, '0', '0', '0', '0', 'هو الذي خلق أباكم آدم من طين وأنتم سلالة منه، ثم كتب مدة بقائكم في هذه الحياة الدنيا، وكتب أجلا آخر محدَّدًا لا يعلمه إلا هو جل وعلا وهو يوم القيامة، ثم أنتم بعد هذا تشكُّون في قدرة الله تعالى على البعث بعد الموت.');
INSERT INTO Quran__Arabic_Tafseer_Muyassar (QuranChapterID, VerseNumber, Rukuh, Sajdah, Manzil, HizbQuarter, VerseText) VALUES (6, 3, '0', '0', '0', '0', 'والله سبحانه هو الإله المعبود في السموات والأرض. ومن دلائل ألوهيته أنه يعلم جميع ما تخفونه -أيها الناس- وما تعلنونه، ويعلم جميع أعمالكم من خير أو شر؛ ولهذا فإنه -جلَّ وعلا- وحده هو الإله المستحق للعبادة.');
INSERT INTO Quran__Arabic_Tafseer_Muyassar (QuranChapterID, VerseNumber, Rukuh, Sajdah, Manzil, HizbQuarter, VerseText) VALUES (6, 4, '0', '0', '0', '0', 'هؤلاء الكفار الذين يشركون مع الله تعالى غيره قد جاءتهم الحجج الواضحة والدلالات البينة على وحدانية الله -جل وعلا- وصِدْقِ محمد صلى الله عليه وسلم في نبوته، وما جاء به، ولكن ما إن جاءتهم حتى أعرضوا عن قبولها، ولم يؤمنوا بها.');
INSERT INTO Quran__Arabic_Tafseer_Muyassar (QuranChapterID, VerseNumber, Rukuh, Sajdah, Manzil, HizbQuarter, VerseText) VALUES (6, 5, '0', '0', '0', '0', 'لقد جحد هؤلاء الكفار الحقَّ الذي جاءهم به محمد صلى الله عليه وسلم وسخروا من دعائه؛ جهلا منهم بالله واغترارًا بإمهاله إياهم، فسوف يرون ما استهزءوا به أنه الحق والصدق، ويبين الله للمكذبين كذبهم وافتراءهم، ويجازيهم عليه.');
INSERT INTO Quran__Arabic_Tafseer_Muyassar (QuranChapterID, VerseNumber, Rukuh, Sajdah, Manzil, HizbQuarter, VerseText) VALUES (6, 6, '0', '0', '0', '0', 'ألم يعلم هؤلاء الذين يجحدون وحدانية الله تعالى واستحقاقه وحده العبادة، ويكذبون رسوله محمدًا صلى الله عليه وسلم ما حلَّ بالأمم المكذبة قبلهم من هلاك وتدمير، وقد مكنَّاهم في الأرض ما لم نمكن لكم أيها الكافرون، وأنعمنا عليهم بإنزال الأمطار وجريان الأنهار من تحت مساكنهم؛ استدراجًا وإملاءً لهم، فكفروا بنعم الله وكذبوا الرسل، فأهلكناهم بسبب ذنونهم، وأنشأنا من بعدهم أممًا أخرى خلفوهم في عمارة الأرض؟');
INSERT INTO Quran__Arabic_Tafseer_Muyassar (QuranChapterID, VerseNumber, Rukuh, Sajdah, Manzil, HizbQuarter, VerseText) VALUES (6, 7, '0', '0', '0', '0', 'ولو نزَّلنا عليك -أيها الرسول- كتابًا من السماء في أوراق فلمسه هؤلاء المشركون بأيديهم لقالوا: إنَّ ما جئت به -أيها الرسول- سحر واضح بيِّن.');
INSERT INTO Quran__Arabic_Tafseer_Muyassar (QuranChapterID, VerseNumber, Rukuh, Sajdah, Manzil, HizbQuarter, VerseText) VALUES (6, 8, '0', '0', '0', '0', 'وقال هؤلاء المشركون: هلا أنزل الله تعالى على محمد مَلَكًا من السماء؛ ليصدقه فيما جاء به من النبوة، ولوأنزلنا مَلَكًّا من السماء إجابة لطلبهم لقضي الأمر بإهلاكهم، ثم لا يمهلون لتوبة، فقد سبق في علم الله أنهم لا يؤمنون.');
INSERT INTO Quran__Arabic_Tafseer_Muyassar (QuranChapterID, VerseNumber, Rukuh, Sajdah, Manzil, HizbQuarter, VerseText) VALUES (6, 9, '0', '0', '0', '0', 'ولو جعلنا الرسول المرسل إليهم مَلَكًا إذ لم يقتنعوا بمحمد صلى الله عليه وسلم، لجعلنا ذلك الملك في صورة البشر، حتى يستطيعوا السماع منه ومخاطبته؛ إذ ليس بإمكانهم رؤية الملك على صورته الملائكية، ولو جاءهم الملك بصورة رجل لاشتبه الأمر عليهم، كما اشتبه عليهم أمر محمد صلى الله عليه وسلم.');
INSERT INTO Quran__Arabic_Tafseer_Muyassar (QuranChapterID, VerseNumber, Rukuh, Sajdah, Manzil, HizbQuarter, VerseText) VALUES (6, 10, '1', '0', '0', '0', 'ولمَّا كان طلبهم إنزال الملك على سبيل الاستهزاء بمحمد صلى الله عليه وسلم بيَّن الله تعالى له أن الاستهزاء بالرسل عليهم السلام ليس أمرا حادثا، بل قد وقع من الكفار السابقين مع أنبيائهم، فأحاط بهم العذاب الذي كانوا يهزؤون به وينكرون وقوعه.');
INSERT INTO Quran__Arabic_Tafseer_Muyassar (QuranChapterID, VerseNumber, Rukuh, Sajdah, Manzil, HizbQuarter, VerseText) VALUES (6, 11, '0', '0', '0', '0', 'قل لهم -أيها الرسول-: سيروا في الأرض ثم انظروا كيف أعقب الله المكذبين الهلاك والخزي؟ فاحذروا مثل مصارعهم، وخافوا أن يحلَّ بكم مثل الذي حل بهم.');
INSERT INTO Quran__Arabic_Tafseer_Muyassar (QuranChapterID, VerseNumber, Rukuh, Sajdah, Manzil, HizbQuarter, VerseText) VALUES (6, 12, '0', '0', '0', '0', 'قل -أيها الرسول- لهؤلاء المشركين: لمن مُلكُ السموات والأرض وما فيهن؟ قل: هو لله كما تقرون بذلك وتعلمونه، فاعبدوه وحده. كتب الله على نفسه الرحمة فلا يعجل على عباده بالعقوبة. ليجمعنكم إلى يوم القيامة الذي لا شك فيه للحساب والجزاء. الذين أشركوا بالله أهلكوا أنفسهم، فهم لا يوحدون الله، ولا يصدقون بوعده ووعيده، ولا يقرون بنبوة محمد صلى الله عليه وسلم.');
INSERT INTO Quran__Arabic_Tafseer_Muyassar (QuranChapterID, VerseNumber, Rukuh, Sajdah, Manzil, HizbQuarter, VerseText) VALUES (6, 13, '0', '0', '0', '1', 'ولله ملك كل شيء في السموات والأرض، سكن أو تحرك، خفي أو ظهر، الجميع عبيده وخلقه، وتحت قهره وتصرفه وتدبيره، وهو السميع لأقوال عباده، الحليم بحركاتهم وسرائرهم.');
INSERT INTO Quran__Arabic_Tafseer_Muyassar (QuranChapterID, VerseNumber, Rukuh, Sajdah, Manzil, HizbQuarter, VerseText) VALUES (6, 14, '0', '0', '0', '0', 'قل -أيها الرسول- لهؤلاء المشركين مع الله تعالى غيره: أغير الله تعالى أتخذ وليًّا ونصيرًا، وهو خالق السموات والأرض وما فيهن، وهو الذي يرزق خلقه ولا يرزقه أحد؟ قل -أيها الرسول-: إني أُمِرْتُ أن أكون أول مَن خضع وانقاد له بالعبودية من هذه الأمة، ونهيت أن أكون من المشركين معه غيره.');
INSERT INTO Quran__Arabic_Tafseer_Muyassar (QuranChapterID, VerseNumber, Rukuh, Sajdah, Manzil, HizbQuarter, VerseText) VALUES (6, 15, '0', '0', '0', '0', 'قل -أيها الرسول- لهؤلاء المشركين مع الله غيره: إني أخاف إن عصيت ربي، فخالفت أمره، وأشركت معه غيره في عبادته، أن ينزل بي عذاب عظيم يوم القيامة.');
INSERT INTO Quran__Arabic_Tafseer_Muyassar (QuranChapterID, VerseNumber, Rukuh, Sajdah, Manzil, HizbQuarter, VerseText) VALUES (6, 16, '0', '0', '0', '0', 'من يصرف الله عنه ذلك العذاب الشديد فقد رحمه، وذلك الصرف هو الظفر البين بالنجاة من العذاب العظيم.');
INSERT INTO Quran__Arabic_Tafseer_Muyassar (QuranChapterID, VerseNumber, Rukuh, Sajdah, Manzil, HizbQuarter, VerseText) VALUES (6, 17, '0', '0', '0', '0', 'وإن يصبك الله تعالى -أيها الإنسان- بشيء يضرك كالفقر والمرض فلا كاشف له إلا هو، وإن يصبك بخير كالغنى والصحة فلا راد لفضله ولا مانع لقضائه، فهو -جل وعلا- القادر على كل شيء.');
INSERT INTO Quran__Arabic_Tafseer_Muyassar (QuranChapterID, VerseNumber, Rukuh, Sajdah, Manzil, HizbQuarter, VerseText) VALUES (6, 18, '0', '0', '0', '0', 'والله سبحانه هو الغالب القاهر فوق عباده؛ خضعت له الرقاب وذَلَّتْ له الجبابرة، وهو الحكيم الذي يضع الأشياء مواضعها وَفْق حكمت، الخبير الذي لا يخفى عليه شيء. ومن اتصف بهذه الصفات يجب ألا يشرك به. وفي هذه الآية إثبات الفوقية لله -تعالى- على جميع خلقه، فوقية مطلقة تليق بجلاله سبحانه.');
INSERT INTO Quran__Arabic_Tafseer_Muyassar (QuranChapterID, VerseNumber, Rukuh, Sajdah, Manzil, HizbQuarter, VerseText) VALUES (6, 19, '0', '0', '0', '0', 'قل -أيها الرسول لهؤلاء المشركين-: أيُّ شيء أعظم شهادة في إثبات صدقي فيما أخبرتكم به أني رسول الله؟ قل: الله شهيد بيني وبينكم أي: هو العالم بما جئتكم به وما أنتم قائلونه لي، وأوحى الله إليَّ هذا القرآن مِن أجل أن أنذركم به عذابه أن يحلَّ بكم، وأنذر به مَن وصل إليه من الأمم. إنكم لتقرون أن مع الله معبودات أخرى تشركونها به. قل لهم -أيها الرسول-: إني لا أشهد على ما أقررتم به، إنما الله إله واحد لا شريك له، وإنني بريء من كل شريك تعبدونه معه.');
INSERT INTO Quran__Arabic_Tafseer_Muyassar (QuranChapterID, VerseNumber, Rukuh, Sajdah, Manzil, HizbQuarter, VerseText) VALUES (6, 20, '1', '0', '0', '0', 'الذين آتيناهم التوراة والإنجيل، يعرفون محمدًا صلى الله عليه وسلم بصفاته المكتوبة عندهم كمعرفتهم أبناءهم، فكما أن أبناءهم لا يشتبهون أمامهم بغيرهم، فكذلك محمد صلى الله عليه وسلم لا يشتبه بغيره لدقة وصفه في كتبهم، ولكنهم اتبعوا أهواءهم، فخسروا أنفسهم حين كفروا بمحمد صلى الله عليه وسلم وبما جاء به.');
INSERT INTO Quran__Arabic_Tafseer_Muyassar (QuranChapterID, VerseNumber, Rukuh, Sajdah, Manzil, HizbQuarter, VerseText) VALUES (6, 21, '0', '0', '0', '0', 'لا أحد أشد ظلمًا ممَّن تَقَوَّلَ الكذب على الله تعالى، فزعم أن له شركاء في العبادة، أو ادَّعى أن له ولدًا أو صاحبة، أو كذب ببراهينه وأدلته التي أيَّد بها رسله عليهم السلام. إنه لا يفلح الظالمون الذين افتروا الكذب على الله، ولا يظفرون بمطالبهم في الدنيا ولا في الآخرة.');
INSERT INTO Quran__Arabic_Tafseer_Muyassar (QuranChapterID, VerseNumber, Rukuh, Sajdah, Manzil, HizbQuarter, VerseText) VALUES (6, 22, '0', '0', '0', '0', 'وليحذر هؤلاء المشركون المكذبون بآيات الله تعالى يوم نحشرهم ثم نقول لهم: أين آلهتكم التي كنتم تدَّعون أنهم شركاء مع الله تعالى ليشفعوا لكم؟');
INSERT INTO Quran__Arabic_Tafseer_Muyassar (QuranChapterID, VerseNumber, Rukuh, Sajdah, Manzil, HizbQuarter, VerseText) VALUES (6, 23, '0', '0', '0', '0', 'ثم لم تكن إجابتهم حين فتنوا واختبروا بالسؤال عن شركائهم إلا أن تبرؤوا منهم، وأقسموا بالله ربهم أنهم لم يكونوا مشركين مع الله غيره.');
INSERT INTO Quran__Arabic_Tafseer_Muyassar (QuranChapterID, VerseNumber, Rukuh, Sajdah, Manzil, HizbQuarter, VerseText) VALUES (6, 24, '0', '0', '0', '0', 'تأمل -أيها الرسول- كيف كذب هؤلاء المشركون على أنفسهم وهم في الآخرة قد تبرؤوا من الشرك؟ وذهب وغاب عنهم ما كانوا يظنونه من شفاعة آلهتهم.');
INSERT INTO Quran__Arabic_Tafseer_Muyassar (QuranChapterID, VerseNumber, Rukuh, Sajdah, Manzil, HizbQuarter, VerseText) VALUES (6, 25, '0', '0', '0', '0', 'ومن هؤلاء المشركين من يستمع إليك القرآن -أيها الرسول-، فلا يصل إلى قلوبهم؛ لأنهم بسبب اتباعهم أهواءهم جعلنا على قلوبهم أغطية؛ لئلا يفقهوا القرآن، وجعلنا في آذانهم ثقلا وصممًا فلا تسمع ولا تعي شيئًا، وإن يروا الآيات الكثيرة الدالة على صدق محمد صلى الله عليه وسلم لا يصدقوا بها، حتى إذا جاؤوك -أيها الرسول- بعد معاينة الآيات الدالة على صدقك يخاصمونك: يقول الذين جحدوا آيات الله: ما هذا الذي نسمع إلا ما تناقله الأولون من حكايات لا حقيقة لها.');
INSERT INTO Quran__Arabic_Tafseer_Muyassar (QuranChapterID, VerseNumber, Rukuh, Sajdah, Manzil, HizbQuarter, VerseText) VALUES (6, 26, '0', '0', '0', '0', 'وهؤلاء المشركون ينهون الناس عن اتباع محمد صلى الله عليه وسلم والاستماع إليه، ويبتعدون بأنفسهم عنه، وما يهلكون -بصدهم عن سبيل الله- إلا أنفسهم، وما يحسون أنهم يعملون لهلاكها.');
INSERT INTO Quran__Arabic_Tafseer_Muyassar (QuranChapterID, VerseNumber, Rukuh, Sajdah, Manzil, HizbQuarter, VerseText) VALUES (6, 27, '0', '0', '0', '0', 'ولو ترى -أيها الرسول- هؤلاء المشركين يوم القيامة لرأيت أمرًا عظيمًا، وذلك حين يُحْبَسون على النار، ويشاهدون ما فيها من السلاسل والأغلال، ورأوا بأعينهم تلك الأمور العظام والأهوال، فعند ذلك قالوا: ياليتنا نُعاد إلى الحياة الدنيا، فنصدق بآيات الله ونعمل بها، ونكون من المؤمنين.');
INSERT INTO Quran__Arabic_Tafseer_Muyassar (QuranChapterID, VerseNumber, Rukuh, Sajdah, Manzil, HizbQuarter, VerseText) VALUES (6, 28, '0', '0', '0', '0', 'ليس الأمر كذلك، بل ظهر لهم يوم القيامة ما كانوا يعلمونه من أنفسهم من صدق ما جاءت به الرسل في الدنيا، وإن كانوا يظهرون لأتباعه خلافه. ولو فرض أن أعيدوا إلى الدنيا فأمهلوا لرجعوا إلى العناد بالكفر والتكذيب. وإنهم لكاذبون في قولهم: لو رددنا إلى الدنيا لم نكذب بآيات ربنا، وكنا من المؤمنين.');
INSERT INTO Quran__Arabic_Tafseer_Muyassar (QuranChapterID, VerseNumber, Rukuh, Sajdah, Manzil, HizbQuarter, VerseText) VALUES (6, 29, '0', '0', '0', '0', 'وقال هؤلاء المشركون المنكرون للبعث: ما الحياة إلا هذه الحياة التي نحن فيها، وما نحن بمبعوثين بعد موتنا.');
INSERT INTO Quran__Arabic_Tafseer_Muyassar (QuranChapterID, VerseNumber, Rukuh, Sajdah, Manzil, HizbQuarter, VerseText) VALUES (6, 30, '1', '0', '0', '0', 'ولو ترى -أيها الرسول- منكري البعث إذ حُبسوا بين يدي الله تعالى لقضائه فيهم يوم القيامة، لرأيت أسوأ حال، إذ يقول الله جل وعلا أليس هذا بالحق، أي: أليس هذا البعث الذي كنتم تنكرونه في الدنيا حقًّا؟ قالوا: بلى وربنا إنه لحق، قال الله تعالى: فذوقوا العذاب بما كنتم تكفرون أي: العذاب الذي كنتم تكذبون به في الدنيا بسبب جحودكم بالله تعالى ورسوله محمد صلى الله عليه وسلم.');
INSERT INTO Quran__Arabic_Tafseer_Muyassar (QuranChapterID, VerseNumber, Rukuh, Sajdah, Manzil, HizbQuarter, VerseText) VALUES (6, 31, '0', '0', '0', '0', 'قد خسر الكفار الذين أنكروا البعث بعد الموت، حتى إذا قامت القيامة، وفوجئوا بسوء المصير، نادَوا على أنفسهم بالحسرة على ما ضيَّعوه في حياتهم الدنيا، وهم يحملون آثامهم على ظهورهم، فما أسوأ الأحمال الثقيلة السيئة التي يحملونها!!');
INSERT INTO Quran__Arabic_Tafseer_Muyassar (QuranChapterID, VerseNumber, Rukuh, Sajdah, Manzil, HizbQuarter, VerseText) VALUES (6, 32, '0', '0', '0', '0', 'وما الحياة الدنيا في غالب أحوالها إلا غرور وباطل، والعمل الصالح للدار الآخرة خير للذين يخشون الله، فيتقون عذابه بطاعته واجتناب معاصيه. أفلا تعقلون -أيها المشركون المغترون بزينة الحياة الدنيا- فتقدِّموا ما يبقى على ما يفنى؟');
INSERT INTO Quran__Arabic_Tafseer_Muyassar (QuranChapterID, VerseNumber, Rukuh, Sajdah, Manzil, HizbQuarter, VerseText) VALUES (6, 33, '0', '0', '0', '0', 'إنا نعلم إنه ليُدْخل الحزنَ إلى قلبك تكذيبُ قومك لك في الظاهر، فاصبر واطمئن؛ فإنهم لا يكذبونك في قرارة أنفسهم، بل يعتقدون صدقك، ولكنهم لظلمهم وعدوانهم يجحدون البراهين الواضحة على صدقك، فيكذبونك فيما جئت به.');
INSERT INTO Quran__Arabic_Tafseer_Muyassar (QuranChapterID, VerseNumber, Rukuh, Sajdah, Manzil, HizbQuarter, VerseText) VALUES (6, 34, '0', '0', '0', '0', 'ولقد كذَّب الكفارُ رسلا من قبلك أرسلهم الله تعالى إلى أممهم وأوذوا في سبيله، فصبروا على ذلك ومضوا في دعوتهم وجهادهم حتى أتاهم نصر الله. ولا مبدل لكلمات الله، وهي ما أنزل على نبيه محمد صلى الله عليه وسلم مِن وعده إياه بالنصر على مَن عاداه. ولقد جاءك -أيها الرسول- مِن خبر مَن كان قبلك من الرسل، وما تحقق لهم من نصر الله، وما جرى على مكذبيهم من نقمة الله منهم وغضبه عليهم، فلك فيمن تقدم من الرسل أسوة وقدوة. وفي هذا تسلية للرسول صلى الله عليه وسلم.');
INSERT INTO Quran__Arabic_Tafseer_Muyassar (QuranChapterID, VerseNumber, Rukuh, Sajdah, Manzil, HizbQuarter, VerseText) VALUES (6, 35, '0', '0', '0', '0', 'وإن كان عَظُمَ عليك -أيها الرسول- صدود هؤلاء المشركين وانصرافهم عن الاستجابة لدعوتك، فإن استطعت أن تتخذ نفقًا في الأرض، أو مصعدًا تصعد فيه إلى السماء، فتأتيهم بعلامة وبرهان على صحة قولك غير الذي جئناهم به فافعل. ولو شاء الله لَجَمعهم على الهدى الذي أنتم عليه ووفَّقهم للإيمان، ولكن لم يشأ ذلك لحكمة يعلمها سبحانه، فلا تكونن -أيها الرسول- من الجاهلين الذين اشتد حزنهم، وتحسَّروا حتى أوصلهم ذلك إلى الجزع الشديد.');
INSERT INTO Quran__Arabic_Tafseer_Muyassar (QuranChapterID, VerseNumber, Rukuh, Sajdah, Manzil, HizbQuarter, VerseText) VALUES (6, 36, '0', '0', '0', '1', 'إنما يجيبك -أيها الرسول- إلى ما دعوت إليه من الهدى الذين يسمعون الكلام سماع قبول. أما الكفار فهم في عداد الموتى؛ لأن الحياة الحقيقية إنما تكون بالإسلام. والموتى يخرجهم الله من قبورهم أحياء، ثم يعودون إليه يوم القيامة ليوفوا حسابهم وجزاءهم.');
INSERT INTO Quran__Arabic_Tafseer_Muyassar (QuranChapterID, VerseNumber, Rukuh, Sajdah, Manzil, HizbQuarter, VerseText) VALUES (6, 37, '0', '0', '0', '0', 'وقال المشركون -تعنتًا واستكبارًا-: هلا أنزل الله علامة تدل على صدق محمد صلى الله عليه وسلم من نوع العلامات الخارقة، قل لهم -أيها الرسول-: إن الله قادر على أن ينزل عليهم آية، ولكن أكثرهم لا يعلمون أن إنزال الآيات إنما يكون وَفْق حكمته تعالى.');
INSERT INTO Quran__Arabic_Tafseer_Muyassar (QuranChapterID, VerseNumber, Rukuh, Sajdah, Manzil, HizbQuarter, VerseText) VALUES (6, 38, '0', '0', '0', '0', 'ليس في الأرض حيوان يَدِبُّ على الأرض أو طائر يطير في السماء بجناحيه إلا جماعات متجانسة الخلق مثلكم. ما تركنا في اللوح المحفوظ شيئًا إلا أثبتناه، ثم إنهم إلى ربهم يحشرون يوم القيامة، فيحاسب الله كلا بما عمل.');
INSERT INTO Quran__Arabic_Tafseer_Muyassar (QuranChapterID, VerseNumber, Rukuh, Sajdah, Manzil, HizbQuarter, VerseText) VALUES (6, 39, '0', '0', '0', '0', 'والذين كذبوا بحجج الله تعالى صمٌّ لا يسمعون ما ينفعهم، بُكْمٌ لا يتكلمون بالحق، فهم حائرون في الظلمات، لم يختاروا طريقة الاستقامة. من يشأ الله إضلاله يضلله، ومن يشأ هدايته يجعله على صراط مستقيم.');
INSERT INTO Quran__Arabic_Tafseer_Muyassar (QuranChapterID, VerseNumber, Rukuh, Sajdah, Manzil, HizbQuarter, VerseText) VALUES (6, 40, '0', '0', '0', '0', 'قل -أيها الرسول- لهؤلاء المشركين: أخبروني إن جاءكم عذاب الله في الدنيا أو جاءتكم الساعة التي تبعثون فيها: أغير الله تدعون هناك لكشف ما نزل بكم من البلاء، إن كتم محقين في زعمكم أن آلهتكم التي تعبدونها من دون الله تنفع أو تضر؟');
INSERT INTO Quran__Arabic_Tafseer_Muyassar (QuranChapterID, VerseNumber, Rukuh, Sajdah, Manzil, HizbQuarter, VerseText) VALUES (6, 41, '1', '0', '0', '0', 'بل تدعون -هناك- ربكم الذي خلقكم لا غيره، وتستغيثون به، فيفرج عنكم البلاء العظيم النازل بكم إن شاء؛ لأنه القادر على كل شيء، وتتركون حينئذ أصنامكم وأوثانكم وأولياءكم.');
INSERT INTO Quran__Arabic_Tafseer_Muyassar (QuranChapterID, VerseNumber, Rukuh, Sajdah, Manzil, HizbQuarter, VerseText) VALUES (6, 42, '0', '0', '0', '0', 'ولقد بعثنا -أيها الرسول- إلى جماعات من الناس من قبلك رسلا يدعونهم إلى الله تعالى، فكذَّبوهم، فابتليناهم في أموالهم بشدة الفقر وضيق المعيشة، وابتليناهم في أجسامهم بالأمراض والآلام؛ رجاء أن يتذللوا لربهم، ويخضعوا له وحده بالعبادة.');
INSERT INTO Quran__Arabic_Tafseer_Muyassar (QuranChapterID, VerseNumber, Rukuh, Sajdah, Manzil, HizbQuarter, VerseText) VALUES (6, 43, '0', '0', '0', '0', 'فهلا إذ جاء هذه الأمم المكذبة بلاؤنا تذللوا لنا، ولكن قست قلوبهم، وزيَّن لهم الشيطان ما كانوا يعملون من المعاصي، ويأتون من الشرك.');
INSERT INTO Quran__Arabic_Tafseer_Muyassar (QuranChapterID, VerseNumber, Rukuh, Sajdah, Manzil, HizbQuarter, VerseText) VALUES (6, 44, '0', '0', '0', '0', 'فلما تركوا العمل بأوامر الله تعالى معرضين عنها، فتحنا عليهم أبواب كل شيء من الرزق فأبدلناهم بالبأساء رخاءً في العيش، وبالضراء صحة في الأجسام؛ استدراجا منا لهم، حتى إذا بطروا، وأعجبوا بما أعطيناهم من الخير والنعمة أخذناهم بالعذاب فجأة، فإذا هم آيسون منقطعون من كل خير.');
INSERT INTO Quran__Arabic_Tafseer_Muyassar (QuranChapterID, VerseNumber, Rukuh, Sajdah, Manzil, HizbQuarter, VerseText) VALUES (6, 45, '0', '0', '0', '0', 'فاستؤصل هؤلاء القوم وأُهلكوا إذ كفروا بالله وكذَّبوا رسله، فلم يبق منهم أحد. والشكر والثناء لله تعالى -خالق كل شيء ومالكه- على نصرة أوليائه وهلاك أعدائه.');
INSERT INTO Quran__Arabic_Tafseer_Muyassar (QuranChapterID, VerseNumber, Rukuh, Sajdah, Manzil, HizbQuarter, VerseText) VALUES (6, 46, '0', '0', '0', '0', 'قل -أيها الرسول- لهؤلاء المشركين: أخبروني إن أذهب الله سمعكم فأصمَّكم، وذهب بأبصاركم فأعماكم، وطبع على قلوبكم فأصبحتم لا تفقهون قولا أيُّ إله غير الله جل وعلا يقدر على ردِّ ذلك لكم؟! انظر -أيها الرسول- كيف ننوِّع لهم الحجج، ثم هم بعد ذلك يعرضون عن التذكر والاعتبار؟');
INSERT INTO Quran__Arabic_Tafseer_Muyassar (QuranChapterID, VerseNumber, Rukuh, Sajdah, Manzil, HizbQuarter, VerseText) VALUES (6, 47, '0', '0', '0', '0', 'قل -أيها الرسول- لهؤلاء المشركين: أخبروني إن نزل بكم عقاب الله فجأة وأنتم لا تشعرون به، أو ظاهرًا عِيانًا وأنتم تنظرون إليه: هل يُهلك إلا القوم الظالمون الذين تجاوزوا الحد، بصرفهم العبادة لغير الله تعالى وبتكذيبهم رسله؟');
INSERT INTO Quran__Arabic_Tafseer_Muyassar (QuranChapterID, VerseNumber, Rukuh, Sajdah, Manzil, HizbQuarter, VerseText) VALUES (6, 48, '0', '0', '0', '0', 'وما نرسل رسلنا إلا مبشرين أهل طاعتنا بالنعيم المقيم، ومنذرين أهل المعصية بالعذاب الأليم، فمن آمن وصدَّق الرسل وعمل صالحًا فأولئك لا يخافون عند لقاء ربهم، ولا يحزنون على شيء فاتهم من حظوظ الدنيا.');
INSERT INTO Quran__Arabic_Tafseer_Muyassar (QuranChapterID, VerseNumber, Rukuh, Sajdah, Manzil, HizbQuarter, VerseText) VALUES (6, 49, '0', '0', '0', '0', 'والذين كذَّبوا بآياتنا من القرآن والمعجزات فأولئك يصيبهم العذاب يوم القيامة، بسبب كفرهم وخروجهم عن طاعة الله تعالى.');
INSERT INTO Quran__Arabic_Tafseer_Muyassar (QuranChapterID, VerseNumber, Rukuh, Sajdah, Manzil, HizbQuarter, VerseText) VALUES (6, 50, '1', '0', '0', '0', 'قل -أيها الرسول- لهؤلاء المشركين: إني لا أدَّعي أني أملك خزائن السموات والأرض، فأتصرف فيها، ولا أدَّعي أني أعلم الغيب، ولا أدَّعي أني ملك، وإنما أنا رسول من عند الله، أتبع ما يوحى إليَّ، وأبلِّغ وحيه إلى الناس، قل -أيها الرسول- لهؤلاء المشركين: هل يستوي الكافر الذي عَمِي عن آيات الله تعالى فلم يؤمن بها والمؤمن الذي أبصر آيات الله فآمن بها؟ أفلا تتفكرون في آيات الله؛ لتبصروا الحق فتؤمنوا به؟');
INSERT INTO Quran__Arabic_Tafseer_Muyassar (QuranChapterID, VerseNumber, Rukuh, Sajdah, Manzil, HizbQuarter, VerseText) VALUES (6, 51, '0', '0', '0', '0', 'وخوِّف -أيها النبي- بالقرآن الذين يعلمون أنهم يُحشرون إلى ربهم، فهم مصدِّقون بوعد الله ووعيده، ليس لهم غير الله وليّ ينصرهم، ولا شفيع يشفع لهم عنده تعالى، فيخلصهم من عذابه؛ لعلهم يتقون الله تعالى بفعل الأوامر واجتناب النواهي.');
INSERT INTO Quran__Arabic_Tafseer_Muyassar (QuranChapterID, VerseNumber, Rukuh, Sajdah, Manzil, HizbQuarter, VerseText) VALUES (6, 52, '0', '0', '0', '0', 'ولا تُبْعد -أيها النبي- عن مجالستك ضعفاء المسلمين الذين يعبدون ربهم أول النهار وآخره، يريدون بأعمالهم الصالحة وجه الله، ما عليك من حساب هؤلاء الفقراء من شيء، إنما حسابهم على الله، وليس عليهم شيء من حسابك، فإن أبعدتهم فإنك تكون من المتجاوزين حدود الله، الذين يضعون الشيء في غير موضعه.');
INSERT INTO Quran__Arabic_Tafseer_Muyassar (QuranChapterID, VerseNumber, Rukuh, Sajdah, Manzil, HizbQuarter, VerseText) VALUES (6, 53, '0', '0', '0', '0', 'وكذالك ابتلى الله تعالى بعض عباده ببعض بتباين حظوظهم من الأرزاق والأخلاق، فجعل بعضهم غنيًّا وبعضهم فقيرًا، وبعضهم قويًّا وبعضهم ضعيفًا، فأحوج بعضهم إلى بعض اختبارًا منه لهم بذلك؛ ليقول الكافرون الأغنياء: أهؤلاء الضعفاء مَنَّ الله عليهم بالهداية إلى الإسلام مِن بيننا؟ أليس الله تعالى بأعلم بمن يشكرون نعمته، فيوفقهم إلى الهداية لدينه؟');
INSERT INTO Quran__Arabic_Tafseer_Muyassar (QuranChapterID, VerseNumber, Rukuh, Sajdah, Manzil, HizbQuarter, VerseText) VALUES (6, 54, '0', '0', '0', '0', 'وإذا جاءك -أيها النبي- الذين صَدَّقوا بآيات الله الشاهدة على صدقك من القرآن وغيره مستفتين عن التوبة من ذنوبهم السابقة، فأكرِمْهم بردِّ السلام عليهم، وبَشِّرهم برحمة الله الواسعة؛ فإنه جلَّ وعلا قد كتب على نفسه الرحمة بعباده تفضلا أنه من اقترف ذنبًا بجهالة منه لعاقبتها وإيجابها لسخط الله -فكل عاص لله مخطئًا أو متعمدًا فهو جاهل بهذا الاعتبار وإن كان عالمًا بالتحريم- ثم تاب من بعده وداوم على العمل الصالح، فإنه تعالى يغفر ذنبه، فهو غفور لعباده التائبين، رحيم بهم.');
INSERT INTO Quran__Arabic_Tafseer_Muyassar (QuranChapterID, VerseNumber, Rukuh, Sajdah, Manzil, HizbQuarter, VerseText) VALUES (6, 55, '1', '0', '0', '0', 'ومثل هذا البيان الذي بيَّنَّاه لك -أيها الرسول- نبيِّن الحجج الواضحة على كل حق ينكره أهل الباطل؛ ليتبين الحق، وليظهر طريق أهل الباطل المخالفين للرسل.');
INSERT INTO Quran__Arabic_Tafseer_Muyassar (QuranChapterID, VerseNumber, Rukuh, Sajdah, Manzil, HizbQuarter, VerseText) VALUES (6, 56, '0', '0', '0', '0', 'قل -أيها الرسول- لهؤلاء المشركين: إن الله عز وجل نهاني أن أعبد الأوثان التي تعبدونها من دونه، وقل لهم: لا أتبع أهواءكم قد ضللت عن الصراط المستقيم إن اتبعت أهواءَكم، وما أنا من المهتدين.');
INSERT INTO Quran__Arabic_Tafseer_Muyassar (QuranChapterID, VerseNumber, Rukuh, Sajdah, Manzil, HizbQuarter, VerseText) VALUES (6, 57, '0', '0', '0', '0', 'قل -أيها الرسول لهؤلاء المشركين-: إني على بصيرة واضحة من شريعة الله التي أوحاها إليَّ، وذلك بإفراده وحده بالعبادة، وقد كذَّبتم بهذا، وليس في قدرتي إنزال العذاب الذي تستعجلون به، وما الحكم في تأخر ذلك إلا إلى الله تعالى، يقصُّ الحقَّ، وهو خير مَن يفصل بين الحق والباطل بقضائه وحكمه.');
INSERT INTO Quran__Arabic_Tafseer_Muyassar (QuranChapterID, VerseNumber, Rukuh, Sajdah, Manzil, HizbQuarter, VerseText) VALUES (6, 58, '0', '0', '0', '0', 'قل -أيها الرسول-: لو أنني أملك إنزال العذاب الذي تستحجلونه لأنزلته بكم، وقضي الأمر بيني وبينكم، ولكن ذلك إلى الله تعالى، وهو أعلم بالظالمين الذين تجاوزوا حدَّهم فأشركوا معه غيره.');
INSERT INTO Quran__Arabic_Tafseer_Muyassar (QuranChapterID, VerseNumber, Rukuh, Sajdah, Manzil, HizbQuarter, VerseText) VALUES (6, 59, '0', '0', '0', '1', 'وعند الله -جل وعلا- مفاتح الغيب أي: خزائن الغيب، لا يعلمها إلا هو، ومنها: علم الساعة، ونزول الغيث، وما في الأرحام، والكسب في المستقبل، ومكان موت الإنسان، ويعلم كل ما في البر والبحر، وما تسقط من ورقة من نبتة إلا يعلمها، فكل حبة في خفايا الأرض، وكل رطب ويابس، مثبت في كتاب واضح لا لَبْس فيه، وهو اللوح المحفوظ.');
INSERT INTO Quran__Arabic_Tafseer_Muyassar (QuranChapterID, VerseNumber, Rukuh, Sajdah, Manzil, HizbQuarter, VerseText) VALUES (6, 60, '1', '0', '0', '0', 'وهو سبحانه الذي يقبض أرواحكم بالليل بما يشبه قبضها عند الموت، ويعلم ما اكتسبتم في النهار من الأعمال، ثم يعيد أرواحكم إلى أجسامكم باليقظة من النوم نهارًا بما يشبه الأحياء بعد الموت؛ لتُقضى آجالكم المحددة في الدنيا، ثم إلى الله تعالى معادكم بعد بعثكم من قبوركم أحياءً، ثم يخبركم بما كنتم تعملون في حياتكم الدنيا، ثم يجازيكم بذلك.');
INSERT INTO Quran__Arabic_Tafseer_Muyassar (QuranChapterID, VerseNumber, Rukuh, Sajdah, Manzil, HizbQuarter, VerseText) VALUES (6, 61, '0', '0', '0', '0', 'والله تعالى هو القاهر فوق عباده، فوقية مطلقة من كل وجه، تليق بجلاله سبحانه وتعالى. كل شيء خاضع لجلاله وعظمته، ويرسل على عباده ملائكة، يحفظون أعمالهم ويُحْصونها، حتى إذا نزل الموت بأحدهم قبض روحَه مَلكُ الموت وأعوانه، وهم لا يضيعون ما أُمروا به.');
INSERT INTO Quran__Arabic_Tafseer_Muyassar (QuranChapterID, VerseNumber, Rukuh, Sajdah, Manzil, HizbQuarter, VerseText) VALUES (6, 62, '0', '0', '0', '0', 'ثم أعيد هؤلاء المتوفون إلى الله تعالى مولاهم الحق. ألا له القضاء والفصل يوم القيامة بين عباده وهو أسرع الحاسبين.');
INSERT INTO Quran__Arabic_Tafseer_Muyassar (QuranChapterID, VerseNumber, Rukuh, Sajdah, Manzil, HizbQuarter, VerseText) VALUES (6, 63, '0', '0', '0', '0', 'قل -أيها الرسول- لهؤلاء المشركين: من ينقذكم من مخاوف ظلمات البر والبحر؟ أليس هو الله تعالى الذي تدعونه في الشدائد متذللين جهرًا وسرًّا؟ تقولون: لئن أنجانا ربنا من هذه المخاوف لنكونن من الشاكرين بعبادته عز وجل وحده لا شريك له.');
INSERT INTO Quran__Arabic_Tafseer_Muyassar (QuranChapterID, VerseNumber, Rukuh, Sajdah, Manzil, HizbQuarter, VerseText) VALUES (6, 64, '0', '0', '0', '0', 'قل لهم -أيها الرسول-: الله وحده هو الذي ينقذكم من هذه المخاوف ومن كل شدة، ثم أنتم بعد ذلك تشركون معه في العبادة غيره.');
INSERT INTO Quran__Arabic_Tafseer_Muyassar (QuranChapterID, VerseNumber, Rukuh, Sajdah, Manzil, HizbQuarter, VerseText) VALUES (6, 65, '0', '0', '0', '0', 'قل -أيها الرسول-: الله عز وجل هو القادر وحده على أن يرسل عليكم عذابًا مِن فوقكم كالرَّجْم أو الطوفان، وما أشبه ذلك، أو من تحت أرجلكم كالزلازل والخسف، أو يخلط أمركم عليكم فتكونوا فرقًا متناحرة يقتل بعضكم بعضًا. انظر -أيها الرسول- كيف نُنوِّع حججنا الواضحات لهؤلاء المشركين لعلهم يفهمون فيعتبروا؟');
INSERT INTO Quran__Arabic_Tafseer_Muyassar (QuranChapterID, VerseNumber, Rukuh, Sajdah, Manzil, HizbQuarter, VerseText) VALUES (6, 66, '0', '0', '0', '0', 'وكذَّب بهذا القرآن الكفارُ مِن قومك أيها الرسول، وهو الكتاب الصادق في كل ما جاء به. قل لهم: لست عليكم بحفيظ ولا رقيب، وإنما أنا رسول الله أبلغكم ما أرسلت به.');
INSERT INTO Quran__Arabic_Tafseer_Muyassar (QuranChapterID, VerseNumber, Rukuh, Sajdah, Manzil, HizbQuarter, VerseText) VALUES (6, 67, '0', '0', '0', '0', 'لكل خبر قرار يستقر عنده، ونهاية ينتهي إليها، فيتبيَّن الحق من الباطل، وسوف تعلمون -أيها الكفار- عاقبة أمركم عند حلول عذاب الله بكم.');
INSERT INTO Quran__Arabic_Tafseer_Muyassar (QuranChapterID, VerseNumber, Rukuh, Sajdah, Manzil, HizbQuarter, VerseText) VALUES (6, 68, '0', '0', '0', '0', 'وإذا رأيت -أيها الرسول- المشركين الذين يتكلمون في آيات القرآن بالباطل والاستهزاء، فابتعد عنهم حتى يأخذوا في حديث آخر، وإن أنساك الشيطان هذا الأمر فلا تقعد بعد تذكرك مع القوم المعتدين، الذين تكلموا في آيات الله بالباطل.');
INSERT INTO Quran__Arabic_Tafseer_Muyassar (QuranChapterID, VerseNumber, Rukuh, Sajdah, Manzil, HizbQuarter, VerseText) VALUES (6, 69, '0', '0', '0', '0', 'وما على المؤمنين الذين يخافون الله تعالى، فيطيعون أوامره، ويجتنبون نواهيه من حساب الله للخائضين المستهزئين بآيات الله من شيء، ولكن عليهم أن يعظوهم ليمسكوا عن ذلك الكلام الباطل، لعلهم يتقون الله تعالى.');
INSERT INTO Quran__Arabic_Tafseer_Muyassar (QuranChapterID, VerseNumber, Rukuh, Sajdah, Manzil, HizbQuarter, VerseText) VALUES (6, 70, '1', '0', '0', '0', 'واترك -أيها الرسول- هؤلاء المشركين الذين جعلوا دين الإسلام لعبًا ولهوًا؛ مستهزئين بآيات الله تعالى، وغرَّتهم الحياة الدنيا بزينتها، وذكّر بالقرآن هؤلاء المشركين وغيرهم؛ كي لا ترتهن نفس بذنوبها وكفرها بربها، ليس لها غير الله ناصر ينصرها، فينقذها من عذابه، ولا شافع يشفع لها عنده، وإن تَفْتَدِ بأي فداء لا يُقْبَل منها. أولئك الذين ارتُهِنوا بذنوبهم، لهم في النار شراب شديد الحرارة وعذاب موجع؛ بسبب كفرهم بالله تعالى ورسوله محمَّد صلى الله عليه وسلم وبدين الإسلام.');
INSERT INTO Quran__Arabic_Tafseer_Muyassar (QuranChapterID, VerseNumber, Rukuh, Sajdah, Manzil, HizbQuarter, VerseText) VALUES (6, 71, '0', '0', '0', '0', 'قل -أيها الرسول- لهؤلاء المشركين: أنعبد من دون الله تعالى أوثانًا لا تنفع ولا تضر؟ ونرجع إلى الكفر بعد هداية الله تعالى لنا إلى الإسلام، فنشبه -في رجوعنا إلى الكفر- مَن فسد عقله باستهواء الشياطين له، فَضَلَّ في الأرض، وله رفقة عقلاء مؤمنون يدعونه إلى الطريق الصحيح الذي هم عليه فيأبى. قل -أيها الرسول- لهؤلاء المشركين: إنَّ هدى الله الذي بعثني به هو الهدى الحق، وأُمِرنا جميعًا لنسلم لله تعالى رب العالمين بعبادته وحده لا شريك له، فهو رب كل شيء ومالكه.');
INSERT INTO Quran__Arabic_Tafseer_Muyassar (QuranChapterID, VerseNumber, Rukuh, Sajdah, Manzil, HizbQuarter, VerseText) VALUES (6, 72, '0', '0', '0', '0', 'وكذلك أُمرنا بأن نقيم الصلاة كاملة، وأن نخشاه بفعل أوامره واجتناب نواهيه. وهو -جل وعلا- الذي إليه تُحْشَرُ جميع الخلائق يوم القيامة.');
INSERT INTO Quran__Arabic_Tafseer_Muyassar (QuranChapterID, VerseNumber, Rukuh, Sajdah, Manzil, HizbQuarter, VerseText) VALUES (6, 73, '0', '0', '0', '0', 'والله سبحانه هو الذي خلق السموات والأرض بالحق، واذكر -أيها الرسول- يوم القيامة إذ يقول الله: "كن"، فيكون عن أمره كلمح البصر أو هو أقرب، قوله هو الحق الكامل، وله الملك سبحانه وحده، يوم ينفخ المَلَك في "القرن" النفخة الثانية التي تكون بها عودة الأرواح إلى الأجسام. وهو سبحانه الذي يعلم ما غاب عن حواسكم -أيها الناس - وما تشاهدونه، وهو الحكيم الذي يضع الأمور في مواضعها، الخبير بأمور خلقه. والله تعالى هو الذي يختص بهذه الأمور وغيرها بدءًا ونهاية، نشأة ومصيرًا، وهو وحده الذي يجب على العباد الانقياد لشرعه، والتسليم لحكمه، والتطلع لرضوانه ومغفرته.');
INSERT INTO Quran__Arabic_Tafseer_Muyassar (QuranChapterID, VerseNumber, Rukuh, Sajdah, Manzil, HizbQuarter, VerseText) VALUES (6, 74, '0', '0', '0', '1', 'واذكر -أيها الرسول- مُحاجَّة إبراهيم عليه السلام لأبيه آزر، إذ قال له: أتجعل من الأصنام آلهة تعبدها من دون الله تعالى؟ إني أراك وقومك في ضلال بيِّن عن طريق الحق.');
INSERT INTO Quran__Arabic_Tafseer_Muyassar (QuranChapterID, VerseNumber, Rukuh, Sajdah, Manzil, HizbQuarter, VerseText) VALUES (6, 75, '0', '0', '0', '0', 'وكما هدينا إبراهيم عليه السلام إلى الحق في أمر العبادة نُريه ما تحتوي عليه السموات والأرض من ملك عظيم، وقدرة باهرة، ليكون من الراسخين في الإيمان.');
INSERT INTO Quran__Arabic_Tafseer_Muyassar (QuranChapterID, VerseNumber, Rukuh, Sajdah, Manzil, HizbQuarter, VerseText) VALUES (6, 76, '0', '0', '0', '0', 'فلما أظلم على إبراهيم عليه السلام الليل وغطَّاه ناظر قومه؛ ليثبت لهم أن دينهم باطل، وكانوا يعبدون النجوم. رأى إبراهيم عليه السلام كوكبًا، فقال -مستدرجا قومه لإلزامهم بالتوحيد-: هذا ربي، فلما غاب الكوكب، قال: لا أحب الآلهة التي تغيب.');
INSERT INTO Quran__Arabic_Tafseer_Muyassar (QuranChapterID, VerseNumber, Rukuh, Sajdah, Manzil, HizbQuarter, VerseText) VALUES (6, 77, '0', '0', '0', '0', 'فلما رأى إبراهيم القمر طالعًا قال لقومه -على سبيل استدراج الخصم-: هذا ربي، فلما غاب، قال -مفتقرا إلى هداية ربه-: لئن لم يوفقني ربي إلى الصواب في توحيده، لأكونن من القوم الضالين عن سواء السبيل بعبادة غير الله تعالى.');
INSERT INTO Quran__Arabic_Tafseer_Muyassar (QuranChapterID, VerseNumber, Rukuh, Sajdah, Manzil, HizbQuarter, VerseText) VALUES (6, 78, '0', '0', '0', '0', 'فلما رأى الشمس طالعة قال لقومه: هذا ربي، هذا أكبر من الكوكب والقمر، فلما غابت، قال لقومه: إني بريء مما تشركون من عبادة الأوثان والنجوم والأصنام التي تعبدونها من دون الله تعالى.');
INSERT INTO Quran__Arabic_Tafseer_Muyassar (QuranChapterID, VerseNumber, Rukuh, Sajdah, Manzil, HizbQuarter, VerseText) VALUES (6, 79, '0', '0', '0', '0', 'إني توجَّهت بوجهي في العبادة لله عز وجل وحده، فهو الذي خلق السموات والأرض، مائلا عن الشرك إلى التوحيد، وما أنا من المشركين مع الله غيره.');
INSERT INTO Quran__Arabic_Tafseer_Muyassar (QuranChapterID, VerseNumber, Rukuh, Sajdah, Manzil, HizbQuarter, VerseText) VALUES (6, 80, '0', '0', '0', '0', 'وجادله قومه في توحيد الله تعالى قال: أتجادلونني في توحيدي لله بالعبادة، وقد وفقني إلى معرفة وحدانيته، فإن كنتم تخوفونني بآلهتكم أن توقع بي ضررًا فإنني لا أرهبها فلن تضرني، إلا أن يشاء ربي شيئًا. وسع ربي كل شيء علمًا. أفلا تتذكرون فتعلموا أنه وحده المعبود المستحق للعبودية؟');
INSERT INTO Quran__Arabic_Tafseer_Muyassar (QuranChapterID, VerseNumber, Rukuh, Sajdah, Manzil, HizbQuarter, VerseText) VALUES (6, 81, '0', '0', '0', '0', 'وكيف أخاف أوثانكم وأنتم لا تخافون ربي الذي خلقكم، وخلق أوثانكم التي أشركتموها معه في العبادة، من غير حجة لكم على ذلك؟ فأي الفريقين: فريق المشركين وفريق الموحدين أحق بالطمأنينة والسلامة والأمن من عذاب الله؟ إن كنتم تعلمون صدق ما أقول فأخبروني.');
INSERT INTO Quran__Arabic_Tafseer_Muyassar (QuranChapterID, VerseNumber, Rukuh, Sajdah, Manzil, HizbQuarter, VerseText) VALUES (6, 82, '1', '0', '0', '0', 'الذين صدَّقوا الله ورسوله وعملوا بشرعه ولم يخلطوا إيمانهم بشرك، أولئك لهم الطمأنينة والسلامة، وهم الموفقون إلى طريق الحق.');
INSERT INTO Quran__Arabic_Tafseer_Muyassar (QuranChapterID, VerseNumber, Rukuh, Sajdah, Manzil, HizbQuarter, VerseText) VALUES (6, 83, '0', '0', '0', '0', 'وتلك الحجة التي حاجَّ بها إبراهيم عليه السلام قومه هي حجتنا التي وفقناه إليها حتى انقطعت حجتهم. نرفع مَن نشاء من عبادنا مراتب في الدنيا والآخرة. إن ربك حكيم في تدبير خلقه، عليم بهم.');
INSERT INTO Quran__Arabic_Tafseer_Muyassar (QuranChapterID, VerseNumber, Rukuh, Sajdah, Manzil, HizbQuarter, VerseText) VALUES (6, 84, '0', '0', '0', '0', 'ومننَّا على إبراهيم عليه السلام بأن رزقناه إسحاق ابنًا ويعقوب حفيدًا، ووفَّقنا كلا منهما لسبيل الرشاد، وكذلك وفَّقنا للحق نوحًا -من قبل إبراهيم وإسحاق ويعقوب- وكذلك وفَّقنا للحق من ذرية نوح داود وسليمان وأيوب ويوسف وموسى وهارون عليهم السلام، وكما جزينا هؤلاء الأنبياء لإحسانهم نجزي كل محسن.');
INSERT INTO Quran__Arabic_Tafseer_Muyassar (QuranChapterID, VerseNumber, Rukuh, Sajdah, Manzil, HizbQuarter, VerseText) VALUES (6, 85, '0', '0', '0', '0', 'وكذلك هدينا زكريا ويحيى وعيسى وإلياس، وكل هؤلاء الأنبياء عليهم السلام من الصالحين.');
INSERT INTO Quran__Arabic_Tafseer_Muyassar (QuranChapterID, VerseNumber, Rukuh, Sajdah, Manzil, HizbQuarter, VerseText) VALUES (6, 86, '0', '0', '0', '0', 'وهدينا كذلك إسماعيل واليسع ويونس ولوطا، وكل هؤلاء الرسل فضَّلناهم على أهل زمانهم.');
INSERT INTO Quran__Arabic_Tafseer_Muyassar (QuranChapterID, VerseNumber, Rukuh, Sajdah, Manzil, HizbQuarter, VerseText) VALUES (6, 87, '0', '0', '0', '0', 'وكذلك وفَّقنا للحق من شئنا هدايته من آباء هؤلاء وذرياتهم وإخوانهم، واخترناهم لديننا وإبلاغ رسالتنا إلى مَن أرسلناهم إليهم، وأرشدناهم إلى طريق صحيح، لا عوج فيه، وهو توحيد الله تعالى وتنزيهه عن الشرك.');
INSERT INTO Quran__Arabic_Tafseer_Muyassar (QuranChapterID, VerseNumber, Rukuh, Sajdah, Manzil, HizbQuarter, VerseText) VALUES (6, 88, '0', '0', '0', '0', 'ذلك الهدى هو توفيق الله، الذي يوفق به من يشاء من عباده. ولو أن هؤلاء الأنبياء أشركوا بالله -على سبيل الفرض والتقدير- لبطل عملهم؛ لأن الله تعالى لا يقبل مع الشرك عملا.');
INSERT INTO Quran__Arabic_Tafseer_Muyassar (QuranChapterID, VerseNumber, Rukuh, Sajdah, Manzil, HizbQuarter, VerseText) VALUES (6, 89, '0', '0', '0', '0', 'أولئك الأنبياء الذين أنعمنا عليهم بالهداية والنبوة هم الذين آتيناهم الكتاب كصحف إبراهيم وتوراة موسى وزبور داود وإنجيل عيسى، وآتيناهم فَهْمَ هذه الكتب، واخترناهم لإبلاغ وحينا، فإن يجحد -أيها الرسول- بآيات هذا القرآن الكفارُ من قومك، فقد وكلنا بها قومًا آخرين -أي: المهاجرين والأنصار وأتباعهم إلى يوم القيامة- ليسوا بها بكافرين، بل مؤمنون بها، عاملون بما تدل عليه.');
INSERT INTO Quran__Arabic_Tafseer_Muyassar (QuranChapterID, VerseNumber, Rukuh, Sajdah, Manzil, HizbQuarter, VerseText) VALUES (6, 90, '1', '0', '0', '0', 'أولئك الأنبياء المذكورون هم الذين وفقهم الله تعالى لدينه الحق، فاتبع هداهم -أيها الرسول- واسلك سبيلهم. قل للمشركين: لا أطلب منكم على تبليغ الإسلام عوضًا من الدنيا، إنْ أجري إلا على الله، وما الإسلام إلا دعوة جميع الناس إلى الطريق المستقيم وتذكير لكم ولكل مَن كان مثلكم، ممن هو مقيم على باطل، لعلكم تتذكرون به ما ينفعكم.');
INSERT INTO Quran__Arabic_Tafseer_Muyassar (QuranChapterID, VerseNumber, Rukuh, Sajdah, Manzil, HizbQuarter, VerseText) VALUES (6, 91, '0', '0', '0', '0', 'وما عَظَّم هؤلاء المشركون الله حق تعظيمه؛ إذ أنكروا أن يكون الله تعالى قد أنزل على أحد من البشر شيئًا من وحيه. قل لهم -أيها الرسول-: إذا كان الأمر كما تزعمون، فمن الذي أنزل الكتاب الذي جاء به موسى إلى قومه نورًا للناس وهداية لهم؟ ثم توجه الخطاب إلى اليهود زَجْرًا لهم بقوله: تجعلون هذا الكتاب في قراطيس متفرقة، تظهرون بعضها، وتكتمون كثيرًا منها، ومما كتموه الإخبار عن صفة محمد صلى الله عليه وسلم ونبوته، وعلَّمكم الله معشر العرب بالقرآنِ -الذي أنزله عليكم، فيه خبر مَن قبلكم ومَن بعدكم، وما يكون بعد موتكم- ما لم تعلموه أنتم ولا آباؤكم، قل: الله هو الذي أنزله، ثم دع هؤلاء في حديثهم الباطل يخوضون ويلعبون.');
INSERT INTO Quran__Arabic_Tafseer_Muyassar (QuranChapterID, VerseNumber, Rukuh, Sajdah, Manzil, HizbQuarter, VerseText) VALUES (6, 92, '0', '0', '0', '0', 'وهذا القرآن كتاب أنزلناه إليك -أيها الرسول- عظيم النفع، مصدق لما تقدمه من الكتب السماوية، أنزلناه لنخوِّف به من عذاب الله وبأسه أهل "مكة" ومن حولها من أهل أقطار الأرض كلها. والذين يصدقون بالحياة الآخرة، يصدقون بأن القرآن كلام الله، ويحافظون على إقام الصلاة في أوقاتها.');
INSERT INTO Quran__Arabic_Tafseer_Muyassar (QuranChapterID, VerseNumber, Rukuh, Sajdah, Manzil, HizbQuarter, VerseText) VALUES (6, 93, '0', '0', '0', '0', 'ومَن أشدُّ ظلمَّا ممَّن اختلق على الله تعالى قولا كذبًا، فادعى أنه لم يبعث رسولا من البشر، أو ادعى كذبًا أن الله أوحى إليه ولم يُوحِ إليه شيئًا، أو ادَّعى أنه قادر على أن يُنْزل مثل ما أنزل الله من القرآن؟ ولو أنك أبصرت -أيها الرسول- هؤلاء المتجاوزين الحدَّ وهم في أهوال الموت لرأيت أمرًا هائلا والملائكة الذين يقبضون أرواحهم باسطو أيديهم بالعذاب قائلين لهم: أخرجوا أنفسكم، اليوم تهانون غاية الإهانة، كما كنتم تكذبون على الله، وتستكبرون عن اتباع آياته والانقياد لرسله.');
INSERT INTO Quran__Arabic_Tafseer_Muyassar (QuranChapterID, VerseNumber, Rukuh, Sajdah, Manzil, HizbQuarter, VerseText) VALUES (6, 94, '1', '0', '0', '0', 'ولقد جئتمونا للحساب والجزاء فرادى كما أوجدناكم في الدنيا أول مرة حفاة عراة، وتركتم وراء ظهوركم ما مكنَّاكم فيه مما تتباهون به من أموال في الدنيا، وما نرى معكم في الآخرة أوثانكم التي كنتم تعتقدون أنها تشفع لكم، وتَدَّعون أنها شركاء مع الله في العبادة، لقد زال تَواصُلُكم الذي كان بينكم في الدنيا، وذهب عنكم ما كنتم تَدَّعون من أن آلهتكم شركاء لله في العبادة، وظهر أنكم الخاسرون لأنفسكم وأهليكم وأموالكم.');
INSERT INTO Quran__Arabic_Tafseer_Muyassar (QuranChapterID, VerseNumber, Rukuh, Sajdah, Manzil, HizbQuarter, VerseText) VALUES (6, 95, '0', '0', '0', '1', 'إن الله تعالى يشق الحب، فيخرج منه الزرع، ويشق النوى، فيخرج منه الشجر، يخرج الحي من الميت كالإنسان والحيوان مثلا من النطفة، ويخرج الميت من الحي كالنطفة من الإنسان والحيوان، ذلكم الله أي: فاعل هذا هو الله وحده لا شريك له المستحق للعبادة، فكيف تُصْرَفون عن الحق إلى الباطل فتعبدون معه غيره؟');
INSERT INTO Quran__Arabic_Tafseer_Muyassar (QuranChapterID, VerseNumber, Rukuh, Sajdah, Manzil, HizbQuarter, VerseText) VALUES (6, 96, '0', '0', '0', '0', 'والله سبحانه وتعالى هو الذي شق ضياء الصباح من ظلام الليل، وجعل الليل مستقرًا، يسكن فيه كل متحرك ويهدأ، وجعل الشمس والقمر يجريان في فلكيهما بحساب متقن مقدَّر، لا يتغير ولا يضطرب، ذلك تقدير العزيز الذي عزَّ سلطانه، العليم بمصالح خلقه وتدبير شئونهم. والعزيز والعليم من أسماء الله الحسنى يدلان على كمال العز والعلم.');
INSERT INTO Quran__Arabic_Tafseer_Muyassar (QuranChapterID, VerseNumber, Rukuh, Sajdah, Manzil, HizbQuarter, VerseText) VALUES (6, 97, '0', '0', '0', '0', 'والله سبحانه هو الذي جعل لكم أيها الناس النجوم علامات، تعرفون بها الطرق ليلا إذا ضللتم بسبب الظلمة الشديدة في البر والبحر، قد بيَّنَّا البراهين الواضحة؛ ليتدبرها منكم أولو العلم بالله وشرعه.');
INSERT INTO Quran__Arabic_Tafseer_Muyassar (QuranChapterID, VerseNumber, Rukuh, Sajdah, Manzil, HizbQuarter, VerseText) VALUES (6, 98, '0', '0', '0', '0', 'والله سبحانه هو الذي ابتدأ خلقكم أيها الناس من آدم عليه السلام؛ إذ خلقه من طين، ثم كنتم سلالة ونسلا منه، فجعل لكم مستقَرًا تستقرون فيه، وهو أرحام النساء، ومُستودعًا تُحفَظُون فيه، وهو أصلاب الرجال، قد بينا الحجج وميزنا الأدلة، وأحكمناها لقوم يفهمون مواقع الحجج ومواضع العبر.');
INSERT INTO Quran__Arabic_Tafseer_Muyassar (QuranChapterID, VerseNumber, Rukuh, Sajdah, Manzil, HizbQuarter, VerseText) VALUES (6, 99, '0', '0', '0', '0', 'والله سبحانه هو الذي أنزل من السحاب مطرًا فأخرج به نبات كل شيء، فأخرج من النبات زرعًا وشجرًا أخضر، ثم أخرج من الزرع حَبًّا يركب بعضه بعضًا، كسنابل القمح والشعير والأرز، وأخرج من طلع النخل -وهو ما تنشأ فيه عذوق الرطب- عذوقًا قريبة التناول، وأخرج سبحانه بساتين من أعناب، وأخرج شجر الزيتون والرمان الذي يتشابه في ورقه ويختلف في ثمره شكلا وطعمًا وطبعًا. انظروا أيها الناس إلى ثمر هذا النبات إذا أثمر، وإلى نضجه وبلوغه حين يبلغ. إن في ذلكم - أيها الناس - لدلالات على كمال قدرة خالق هذه الأشياء وحكمته ورحمته لقوم يصدقون به تعالى ويعملون بشرعه.');
INSERT INTO Quran__Arabic_Tafseer_Muyassar (QuranChapterID, VerseNumber, Rukuh, Sajdah, Manzil, HizbQuarter, VerseText) VALUES (6, 100, '1', '0', '0', '0', 'وجعل هؤلاء المشركون الجن شركاء لله تعالى في العبادة؛ اعتقادًا منهم أنهم ينفعون أو يضرون، وقد خلقهم الله تعالى وما يعبدون من العدم، فهو المستقل بالخلق وحده، فيجب أن يستقل بالعبادة وحده لا شريك له. ولقد كذب هؤلاء المشركون على الله تعالى حين نسبوا إليه البنين والبنات؛ جهلا منهم بما يجب له من صفات الكمال، تنزَّه وعلا عما نسبه إليه المشركون من ذلك الكذب والافتراء.');
INSERT INTO Quran__Arabic_Tafseer_Muyassar (QuranChapterID, VerseNumber, Rukuh, Sajdah, Manzil, HizbQuarter, VerseText) VALUES (6, 101, '0', '0', '0', '0', 'والله تعالى هو الذي أوجد السموات والأرض وما فيهن على غير مثال سابق. كيف يكون له ولد ولم تكن له صاحبة؟ تعالى الله عما يقول المشركون علوًّا كبيرًا، وهو الذي خلق كل شيء من العدم، ولا يخفى عليه شيء من أمور الخلق.');
INSERT INTO Quran__Arabic_Tafseer_Muyassar (QuranChapterID, VerseNumber, Rukuh, Sajdah, Manzil, HizbQuarter, VerseText) VALUES (6, 102, '0', '0', '0', '0', 'ذلكم -أيها المشركون- هو ربكم جل وعلا لا معبود بحق سواه، خالق كل شيء فانقادوا واخضعوا له بالطاعة والعبادة. وهو سبحانه على كل شيء وكيل وحفيظ، يدبر أمور خلقه.');
INSERT INTO Quran__Arabic_Tafseer_Muyassar (QuranChapterID, VerseNumber, Rukuh, Sajdah, Manzil, HizbQuarter, VerseText) VALUES (6, 103, '0', '0', '0', '0', 'لا ترى اللهَ الأبصارُ في الدنيا، أما في الدار الآخرة فإن المؤمنين يرون ربهم بغير إحاطة، وهو سبحانه يدرك الأبصار ويحيط بها، ويعلمها على ما هي عليه، وهو اللطيف بأوليائه الذي يعلم دقائق الأشياء، الخبير الذي يعلم بواطنها.');
INSERT INTO Quran__Arabic_Tafseer_Muyassar (QuranChapterID, VerseNumber, Rukuh, Sajdah, Manzil, HizbQuarter, VerseText) VALUES (6, 104, '0', '0', '0', '0', 'قل -أيها الرسول- لهؤلاء المشركين: قد جاءتكم براهين ظاهرة تبصرون بها الهدى من الضلال، مما اشتمل عليها القرآن، وجاء بها الرسول عليه الصلاة والسلام، فمَن تبيَّن هذه البراهين وآمن بمدلولها فنَفْعُ ذلك لنفسه، ومَن لم يبصر الهدى بعد ظهور الحجة عليه فعلى نفسه جنى، وما أنا عليكم بحافظ أحصي أعمالكم، وإنما أنا مبلغ، والله يهدي مَن يشاء ويضل مَن يشاء وَفْق علمه وحكمته.');
INSERT INTO Quran__Arabic_Tafseer_Muyassar (QuranChapterID, VerseNumber, Rukuh, Sajdah, Manzil, HizbQuarter, VerseText) VALUES (6, 105, '0', '0', '0', '0', 'وكما بيَّنَّا في هذا القرآن للمشركين البراهين الظاهرة في أمر التوحيد والنبوة والمعاد نبيِّن لهم البراهين في كل ما جهلوه فيقولون عند ذلك كذبًا: تعلمت من أهل الكتاب، ولنبين -بتصريفنا الآيات- الحقَّ لقوم يعلمونه، فيقبلونه ويتبعونه، وهم المؤمنون برسول الله محمد صلى الله عليه وسلم وما أنزل عليه.');
INSERT INTO Quran__Arabic_Tafseer_Muyassar (QuranChapterID, VerseNumber, Rukuh, Sajdah, Manzil, HizbQuarter, VerseText) VALUES (6, 106, '0', '0', '0', '0', 'اتبع -أيها الرسول- ما أوحيناه إليك من الأوامر والنواهي التي أعظمُها توحيد الله سبحانه والدعوة إليه، ولا تُبال بعناد المشركين، وادعائهم الباطل.');
INSERT INTO Quran__Arabic_Tafseer_Muyassar (QuranChapterID, VerseNumber, Rukuh, Sajdah, Manzil, HizbQuarter, VerseText) VALUES (6, 107, '0', '0', '0', '0', 'ولو شاء الله تعالى أن لا يشرك هؤلاء المشركون لما أشركوا، لكنه تعالى عليم بما سيكون من سوء اختيارهم واتباعهم أهواءهم المنحرفة. وما جعلناك -أيها الرسول- عليهم رقيبًا تحفظ عليهم أعمالهم، وما أنت بقَيِّمٍ عليهم تدبر مصالحهم.');
INSERT INTO Quran__Arabic_Tafseer_Muyassar (QuranChapterID, VerseNumber, Rukuh, Sajdah, Manzil, HizbQuarter, VerseText) VALUES (6, 108, '0', '0', '0', '0', 'ولا تسبوا -أيها المسلمون- الأوثان التي يعبدها المشركون -سدًّا للذريعة- حتى لا يتسبب ذلك في سبهم الله جهلا واعتداءً: بغير علم. وكما حسَّنَّا لهؤلاء عملهم السيئ عقوبة لهم على سوء اختيارهم، حسَّنَّا لكل أمة أعمالها، ثم إلى ربهم معادهم جميعًا فيخبرهم بأعمالهم التي كانوا يعملونها في الدنيا، ثم يجازيهم بها.');
INSERT INTO Quran__Arabic_Tafseer_Muyassar (QuranChapterID, VerseNumber, Rukuh, Sajdah, Manzil, HizbQuarter, VerseText) VALUES (6, 109, '0', '0', '0', '0', 'وأقسم هؤلاء المشركون بأيمان مؤكَّدة: لئن جاءنا محمد بعلامة خارقة لنصدقنَّ بما جاء به، قل -أيها الرسول-: إنما مجيء المعجزات الخارقة من عند الله تعالى، هو القادر على المجيء بها إذا شاء، وما يدريكم أيها المؤمنون: لعل هذه المعجزات إذا جاءت لا يصدِّق بها هؤلاء المشركون.');
INSERT INTO Quran__Arabic_Tafseer_Muyassar (QuranChapterID, VerseNumber, Rukuh, Sajdah, Manzil, HizbQuarter, VerseText) VALUES (6, 110, '1', '0', '0', '0', 'ونقلب أفئدتهم وأبصارهم، فنحول بينها وبين الانتفاع بآيات الله، فلا يؤمنون بها كما لم يؤمنوا بآيات القرآن عند نزولها أول مرة، ونتركهم في تمرُّدهم على الله متحيِّرين، لا يهتدون إلى الحق والصواب.');
INSERT INTO Quran__Arabic_Tafseer_Muyassar (QuranChapterID, VerseNumber, Rukuh, Sajdah, Manzil, HizbQuarter, VerseText) VALUES (6, 111, '0', '0', '0', '1', 'ولو أننا أجبنا طلب هؤلاء، فنزَّلنا إليهم الملائكة من السماء، وأحيينا لهم الموتى، فكلموهم، وجمعنا لهم كل شيء طلبوه فعاينوه مواجهة، لم يصدِّقوا بما دعوتهم إليه -أيها الرسول- ولم يعملوا به، إلا من شاء الله له الهداية، ولكن أكثر هؤلاء الكفار يجهلون الحق الذي جئت به من عند الله تعالى.');
INSERT INTO Quran__Arabic_Tafseer_Muyassar (QuranChapterID, VerseNumber, Rukuh, Sajdah, Manzil, HizbQuarter, VerseText) VALUES (6, 112, '0', '0', '0', '0', 'وكما ابتليناك -أيها الرسول- بأعدائك من المشركين ابتلينا جميع الأنبياء -عليهم السلام- بأعداء مِن مردة قومهم وأعداء من مردة الجن، يُلقي بعضهم إلى بعض القول الذي زيَّنوه بالباطل؛ ليغتر به سامعه، فيضل عن سبيل الله. ولو أراد ربك -جلَّ وعلا- لحال بينهم وبين تلك العداوة، ولكنه الابتلاء من الله، فدعهم وما يختلقون مِن كذب وزور.');
INSERT INTO Quran__Arabic_Tafseer_Muyassar (QuranChapterID, VerseNumber, Rukuh, Sajdah, Manzil, HizbQuarter, VerseText) VALUES (6, 113, '0', '0', '0', '0', 'ولِتميل إليه قلوب الكفار الذين لا يصدقون بالحياة الآخرة ولا يعملون لها، ولتحبَّه أنفسهم، وليكتسبوا من الأعمال السيئة ما هم مكتسبون. وفي هذا تهديد عظيم لهم.');
INSERT INTO Quran__Arabic_Tafseer_Muyassar (QuranChapterID, VerseNumber, Rukuh, Sajdah, Manzil, HizbQuarter, VerseText) VALUES (6, 114, '0', '0', '0', '0', 'قل -أيها الرسول- لهؤلاء المشركين: أغير الله إلهي وإلهكم أطلب حَكَمًا بيني وبينكم، وهو سبحانه الذي أنزل إليكم القرآن مبينًا فيه الحكم فيما تختصمون فيه من أمري وأمركم؟ وبنو إسرائيل الذين آتاهم الله التوراة والإنجيل يعلمون علمًا يقينًا أن هذا القرآن منزل عليك -أيها الرسول- من ربك بالحق، فلا تكونن من الشاكِّين في شيء مما أوحينا إليك.');
INSERT INTO Quran__Arabic_Tafseer_Muyassar (QuranChapterID, VerseNumber, Rukuh, Sajdah, Manzil, HizbQuarter, VerseText) VALUES (6, 115, '0', '0', '0', '0', 'وتمت كلمة ربك -وهي القرآن- صدقًا في الأخبار والأقوال، وعدلا في الأحكام، فلا يستطيع أحد أن يبدِّل كلماته الكاملة. والله تعالى هو السميع لما يقول عباده، الحليم بظواهر أمورهم وبواطنها.');
INSERT INTO Quran__Arabic_Tafseer_Muyassar (QuranChapterID, VerseNumber, Rukuh, Sajdah, Manzil, HizbQuarter, VerseText) VALUES (6, 116, '0', '0', '0', '0', 'ولو فُرض -أيها الرسول- أنك أطعت أكثر أهل الأرض لأضلُّوك عن دين الله، ما يسيرون إلا على ما ظنوه حقًّا بتقليدهم أسلافهم، وما هم إلا يظنون ويكذبون.');
INSERT INTO Quran__Arabic_Tafseer_Muyassar (QuranChapterID, VerseNumber, Rukuh, Sajdah, Manzil, HizbQuarter, VerseText) VALUES (6, 117, '0', '0', '0', '0', 'إن ربك هو أعلم بالضالين عن سبيل الرشاد، وهو أعلم منكم ومنهم بمن كان على استقامة وسداد، لا يخفى عليه منهم أحد.');
INSERT INTO Quran__Arabic_Tafseer_Muyassar (QuranChapterID, VerseNumber, Rukuh, Sajdah, Manzil, HizbQuarter, VerseText) VALUES (6, 118, '0', '0', '0', '0', 'فكلوا من الذبائح التي ذُكِرَ اسم الله عليها، إن كنتم ببراهين الله تعالى الواضحة مصدقين.');
INSERT INTO Quran__Arabic_Tafseer_Muyassar (QuranChapterID, VerseNumber, Rukuh, Sajdah, Manzil, HizbQuarter, VerseText) VALUES (6, 119, '0', '0', '0', '0', 'وأيُّ شيء يمنعكم أيها المسلمون من أن تأكلوا مما ذكر اسم الله عليه، وقد بيَّن الله سبحانه لكم جميع ما حرَّم عليكم؟ لكن ما دعت إليه الضرورة بسبب المجاعة، مما هو محرم عليكم كالميتة، فإنه مباح لكم. وإنَّ كثيرًا من الضالين ليضلون عن سبيل الله أشياعهم في تحليل الحرام وتحريم الحلال بأهوائهم؛ جهلا منهم. إن ربك -أيها الرسول- هو أعلم بمن تجاوز حده في ذلك، وهو الذي يتولى حسابه وجزاءه.');
INSERT INTO Quran__Arabic_Tafseer_Muyassar (QuranChapterID, VerseNumber, Rukuh, Sajdah, Manzil, HizbQuarter, VerseText) VALUES (6, 120, '0', '0', '0', '0', 'واتركوا -أيها الناس- جميع المعاصي، ما كان منها علانية وما كان سرًّا. إن الذين يفعلون المعاصي سيعاقبهم ربهم؛ بسبب ما كانوا يعملونه من السيئات.');
INSERT INTO Quran__Arabic_Tafseer_Muyassar (QuranChapterID, VerseNumber, Rukuh, Sajdah, Manzil, HizbQuarter, VerseText) VALUES (6, 121, '1', '0', '0', '0', 'ولا تأكلوا -أيها المسلمون- من الذبائح التي لم يذكر اسم الله عليها عند الذبح، كالميتة وما ذبح للأوثان والجن، وغير ذلك، وإن الأكل من تلك الذبائح لخروج عن طاعة الله تعالى. وإن مردة الجن لَيُلْقون إلى أوليائهم من شياطين الإنس بالشبهات حول تحريم أكل الميتة، فيأمرونهم أن يقولوا للمسلمين في جدالهم معهم: إنكم بعدم أكلكم الميتة لا تأكلون ما قتله الله، بينما تأكلون مما تذبحونه، وإن أطعتموهم -أيها المسلمون في تحليل الميتة- فأنتم وهم في الشرك سواء.');
INSERT INTO Quran__Arabic_Tafseer_Muyassar (QuranChapterID, VerseNumber, Rukuh, Sajdah, Manzil, HizbQuarter, VerseText) VALUES (6, 122, '0', '0', '0', '0', 'أوَمن كان ميتًا في الضلالة هالكا حائرا، فأحيينا قلبه بالإيمان، وهديناه له، ووفقناه لاتباع رسله، فأصبح يعيش في أنوار الهداية، كمن مثله في الجهالات والأهواء والضلالات المتفرقة، لا يهتدي إلى منفذ ولا مخلص له مما هو فيه؟ لا يستويان، وكما خذلتُ هذا الكافر الذي يجادلكم -أيها المؤمنون- فزيَّنْتُ له سوء عمله، فرآه حسنًا، زيَّنْتُ للجاحدين أعمالهم السيئة؛ ليستوجبوا بذلك العذاب.');
INSERT INTO Quran__Arabic_Tafseer_Muyassar (QuranChapterID, VerseNumber, Rukuh, Sajdah, Manzil, HizbQuarter, VerseText) VALUES (6, 123, '0', '0', '0', '0', 'ومثل هذا الذي حصل مِن زعماء الكفار في "مكة" من الصدِّ عن دين الله تعالى، جعلنا في كل قرية مجرمين يتزعمهم أكابرهم؛ ليمكروا فيها بالصد عن دين الله، وما يكيدون إلا أنفسهم، وما يُحِسُّون بذلك.');
INSERT INTO Quran__Arabic_Tafseer_Muyassar (QuranChapterID, VerseNumber, Rukuh, Sajdah, Manzil, HizbQuarter, VerseText) VALUES (6, 124, '0', '0', '0', '0', 'وإذا جاءت هؤلاء المشركين من أهل "مكة" حجة ظاهرة على نبوة محمد صلى الله عليه وسلم، قال بعض كبرائهم: لن نصدِّق بنبوته حتى يعطينا الله من النبوة والمعجزات مثل ما أعطى رسله السابقين. فردَّ الله تعالى عليهم بقوله: الله أعلم حيث يجعل رسالته أي: بالذين هم أهل لحمل رسالته وتبليغها إلى الناس. سينال هؤلاء الطغاة الذل، ولهم عذاب موجع في نار جهنم؛ بسبب كيدهم للإسلام وأهله.');
INSERT INTO Quran__Arabic_Tafseer_Muyassar (QuranChapterID, VerseNumber, Rukuh, Sajdah, Manzil, HizbQuarter, VerseText) VALUES (6, 125, '0', '0', '0', '0', 'فمن يشأ الله أن يوفقه لقَبول الحق يشرح صدره للتوحيد والإيمان، ومن يشأ أن يضله يجعل صدره في حال شديدة من الانقباض عن قَبول الهدى، كحال مَن يصعد في طبقات الجو العليا، فيصاب بضيق شديد في التنفس. وكما يجعل الله صدور الكافرين شديدة الضيق والانقباض، كذلك يجعل العذاب على الذين لا يؤمنون به.');
INSERT INTO Quran__Arabic_Tafseer_Muyassar (QuranChapterID, VerseNumber, Rukuh, Sajdah, Manzil, HizbQuarter, VerseText) VALUES (6, 126, '0', '0', '0', '0', 'وهذا الذي بيَّنَّاه لك -أيها الرسول- هو الطريق الموصل إلى رضا ربك وجنته. قد بينَّا البراهين لمن يتذكر من أهل العقول الراجحة.');
INSERT INTO Quran__Arabic_Tafseer_Muyassar (QuranChapterID, VerseNumber, Rukuh, Sajdah, Manzil, HizbQuarter, VerseText) VALUES (6, 127, '0', '0', '0', '1', 'للمتذكرين عند ربهم جل وعلا يوم القيامة دار السلامة والأمان من كل مكروه وهي الجنة، وهو سبحانه ناصرهم وحافظهم جزاءً لهم؛ بسبب أعمالهم الصالحة.');
INSERT INTO Quran__Arabic_Tafseer_Muyassar (QuranChapterID, VerseNumber, Rukuh, Sajdah, Manzil, HizbQuarter, VerseText) VALUES (6, 128, '0', '0', '0', '0', 'واذكر -أيها الرسول- يوم يحشر الله تعالى الكفار وأولياءهم من شياطين الجن فيقول: يا معشر الجن قد أضللتم كثيرًا من الإنس، وقال أولياؤهم من كفار الإنس: ربنا قد انتفع بعضنا من بعض، وبلغنا الأجل الذي أجَّلْتَه لنا بانقضاء حياتنا الدنيا، قال الله تعالى لهم: النار مثواكم، أي: مكان إقامتكم خالدين فيها، إلا مَن شاء الله عدم خلوده فيها من عصاة الموحدين. إن ربك حكيم في تدبيره وصنعه، عليم بجميع أمور عباده.');
INSERT INTO Quran__Arabic_Tafseer_Muyassar (QuranChapterID, VerseNumber, Rukuh, Sajdah, Manzil, HizbQuarter, VerseText) VALUES (6, 129, '1', '0', '0', '0', 'وكما سلَّطْنا شياطين الجن على كفار الإنس، فكانوا أولياء لهم، نسلِّط الظالمين من الإنس بعضهم على بعض في الدنيا؛ بسبب ما يعملونه من المعاصي.');
INSERT INTO Quran__Arabic_Tafseer_Muyassar (QuranChapterID, VerseNumber, Rukuh, Sajdah, Manzil, HizbQuarter, VerseText) VALUES (6, 130, '0', '0', '0', '0', 'أيها المشركون من الجن والإنس، ألم يأتكم رسل من جملتكم -وظاهر النصوص يدلُّ على أنَّ الرسل من الإنس فقط-، يخبرونكم بآياتي الواضحة المشتملة على الأمر والنهي وبيان الخير والشر، ويحذرونكم لقاء عذابي في يوم القيامة؟ قال هؤلاء المشركون من الإنس والجن: شَهِدْنا على أنفسنا بأن رسلك قد بلغونا آياتك، وأنذرونا لقاء يومنا هذا، فكذبناهم، وخدعت هؤلاء المشركين زينةُ الحياة الدنيا، وشهدوا على أنفسهم أنهم كانوا جاحدين وحدانية الله تعالى ومكذبين لرسله عليهم السلام.');
INSERT INTO Quran__Arabic_Tafseer_Muyassar (QuranChapterID, VerseNumber, Rukuh, Sajdah, Manzil, HizbQuarter, VerseText) VALUES (6, 131, '0', '0', '0', '0', 'إنما أعذرنا إلى الثقلين بإرسال الرسل وإنزال الكتب، لئلا يؤاخَذَ أحد بظلمه، وهو لم تبلغه دعوة، ولكن أعذرنا إلى الأمم، وما عذَّبنا أحدًا إلا بعد إرسال الرسل إليهم.');
INSERT INTO Quran__Arabic_Tafseer_Muyassar (QuranChapterID, VerseNumber, Rukuh, Sajdah, Manzil, HizbQuarter, VerseText) VALUES (6, 132, '0', '0', '0', '0', 'ولكل عامل في طاعة الله تعالى أو معصيته مراتب من عمله، يبلِّغه الله إياها، ويجازيه عليها. وما ربك -أيها الرسول- بغافل عما يعمل عباده.');
INSERT INTO Quran__Arabic_Tafseer_Muyassar (QuranChapterID, VerseNumber, Rukuh, Sajdah, Manzil, HizbQuarter, VerseText) VALUES (6, 133, '0', '0', '0', '0', 'وربك -أيها الرسول- الذي أمر الناس بعبادته، هو الغني وحده، وكل خلقه محتاجون إليه، وهو سبحانه ذو الرحمة الواسعة، لو أراد لأهلككم، وأوجد قومًا غيركم يخلفونكم من بعد فنائكم، ويعملون بطاعته تعالى، كما أوجدكم من نسل قوم آخرين كانوا قبلكم.');
INSERT INTO Quran__Arabic_Tafseer_Muyassar (QuranChapterID, VerseNumber, Rukuh, Sajdah, Manzil, HizbQuarter, VerseText) VALUES (6, 134, '0', '0', '0', '0', 'إن الذي يوعدكم به ربكم - أيها المشركون - من العقاب على كفركم واقع بكم، ولن تُعجِزوا ربكم هربًا، فهو قادر على إعادتكم، وإن صرتم ترابًا وعظامًا.');
INSERT INTO Quran__Arabic_Tafseer_Muyassar (QuranChapterID, VerseNumber, Rukuh, Sajdah, Manzil, HizbQuarter, VerseText) VALUES (6, 135, '0', '0', '0', '0', 'قل -أيها الرسول-: يا قوم اعملوا على طريقتكم فإني عامل على طريقتي التي شرعها لي ربي جل وعلا فسوف تعلمون -عند حلول النقمة بكم- مَنِ الذي تكون له العاقبة الحسنة؟ إنه لا يفوز برضوان الله تعالى والجنة مَن تجاوز حده وظلم، فأشرك مع الله غيره.');
INSERT INTO Quran__Arabic_Tafseer_Muyassar (QuranChapterID, VerseNumber, Rukuh, Sajdah, Manzil, HizbQuarter, VerseText) VALUES (6, 136, '0', '0', '0', '0', 'وجعل المشركون لله -جلَّ وعلا- جزءًا مما خلق من الزروع والثمار والأنعام يقدمونه للضيوف والمساكين، وجعلوا قسمًا آخر من هذه الأشياء لشركائهم من الأوثان والأنصاب، فما كان مخصصًا لشركائهم فإنه يصل إليها وحدها، ولا يصل إلى الله، وما كان مخصصا لله تعالى فإنه يصل إلى شركائهم. بئس حكم القوم وقسمتهم.');
INSERT INTO Quran__Arabic_Tafseer_Muyassar (QuranChapterID, VerseNumber, Rukuh, Sajdah, Manzil, HizbQuarter, VerseText) VALUES (6, 137, '0', '0', '0', '0', 'وكما زيَّن الشيطان للمشركين أن يجعلوا لله تعالى من الحرث والأنعام نصيبًا، ولشركائهم نصيبًا، زيَّنت الشياطين لكثير من المشركين قَتْلَ أولادهم خشية الفقر؛ ليوقعوا هؤلاء الآباء في الهلاك بقتل النفس التي حرم الله قتلها إلا بالحق، وليخلطوا عليهم دينهم فيلتبس، فيضلوا ويهلكوا، ولو شاء الله ألا يفعلوا ذلك ما فعلوه، ولكنه قدَّر ذلك لعلمه بسوء حالهم ومآلهم، فاتركهم -أيها الرسول- وشأنهم فيما يفترون من كذب، فسيحكم الله بينك وبينهم.');
INSERT INTO Quran__Arabic_Tafseer_Muyassar (QuranChapterID, VerseNumber, Rukuh, Sajdah, Manzil, HizbQuarter, VerseText) VALUES (6, 138, '0', '0', '0', '0', 'وقال المشركون: هذه إبل وزرع حرام، لا يأكلها إلا مَن يأذنون له -حسب ادعائهم- مِن سدنة الأوثان وغيرهم. وهذه إبل حُرِّمت ظهورها، فلا يحل ركوبها والحملُ عليها بحال من الأحوال. وهذه إبل لا يَذكرون اسم الله تعالى عليها في أي شأن من شئونها. فعلوا ذلك كذبًا منهم على الله، سيجزيهم الله بسبب ما كانوا يفترون من كذبٍ عليه سبحانه.');
INSERT INTO Quran__Arabic_Tafseer_Muyassar (QuranChapterID, VerseNumber, Rukuh, Sajdah, Manzil, HizbQuarter, VerseText) VALUES (6, 139, '0', '0', '0', '0', 'وقال المشركون: ما في بطون الأنعام من أجنَّة مباح لرجالنا، ومحرم على نسائنا، إذا ولد حيًّا، ويشتركون فيه إذا ولد ميتًا. سيعاقبهم الله إذ شرَّعوا لأنفسهم من التحليل والتحريم ما لم يأذن به الله. إنه تعالى حكيم في تدبير أمور خلقه، عليم بهم.');
INSERT INTO Quran__Arabic_Tafseer_Muyassar (QuranChapterID, VerseNumber, Rukuh, Sajdah, Manzil, HizbQuarter, VerseText) VALUES (6, 140, '1', '0', '0', '0', 'قد خسر وهلك الذين قتلوا أولادهم لضعف عقولهم وجهلهم، وحرموا ما رزقهم الله كذبًا على الله. قد بَعُدوا عن الحق، وما كانوا من أهل الهدى والرشاد. فالتحليل والتحريم من خصائص الألوهية في التشريع، والحلال ما أحله الله، والحرام ما حرَّمه الله، وليس لأحد من خَلْقه فردًا كان أو جماعة أن يشرع لعباده ما لم يأذن به الله.');
INSERT INTO Quran__Arabic_Tafseer_Muyassar (QuranChapterID, VerseNumber, Rukuh, Sajdah, Manzil, HizbQuarter, VerseText) VALUES (6, 141, '0', '0', '0', '1', 'والله سبحانه وتعالى هو الذي أوجد لكم بساتين: منها ما هو مرفوع عن الأرض كالأعناب، ومنها ما هو غير مرفوع، ولكنه قائم على سوقه كالنخل والزرع، متنوعًا طعمه، والزيتون والرمان متشابهًا منظره، ومختلفًا ثمره وطعمه. كلوا -أيها الناس- مِن ثمره إذا أثمر، وأعطوا زكاته المفروضة عليكم يوم حصاده وقطافه، ولا تتجاوزوا حدود الاعتدال في إخراج المال وأكل الطعام وغير ذلك. إنه تعالى لا يحب المتجاوزين حدوده بإنفاق المال في غير وجهه.');
INSERT INTO Quran__Arabic_Tafseer_Muyassar (QuranChapterID, VerseNumber, Rukuh, Sajdah, Manzil, HizbQuarter, VerseText) VALUES (6, 142, '0', '0', '0', '0', 'وأوجد من الأنعام ما هو مهيَّأ للحمل عليه لكبره وارتفاعه كالإبل، ومنها ما هو مهيَّأ لغير الحمل لصغره وقربه من الأرض كالبقر والغنم، كلوا مما أباحه الله لكم وأعطاكموه من هذه الأنعام، ولا تحرموا ما أحلَّ الله منها اتباعًا لطرق الشيطان، كما فعل المشركون. إن الشيطان لكم عدو ظاهر العداوة.');
INSERT INTO Quran__Arabic_Tafseer_Muyassar (QuranChapterID, VerseNumber, Rukuh, Sajdah, Manzil, HizbQuarter, VerseText) VALUES (6, 143, '0', '0', '0', '0', 'هذه الأنعام التي رزقها الله عباده من الإبل والبقر والغنم ثمانية أصناف: أربعة منها من الغنم، وهي الضأن ذكورًا وإناثًا، والمعز ذكورًا وإناثًا. قل -أيها الرسول- لأولئك المشركين: هل حَرَّم الله الذكرين من الغنم؟ فإن قالوا: نعم، فقد كذبوا في ذلك؛ لأنهم لا يحرمون كل ذكر من الضأن والمعز، وقل لهم: هل حَرَّم الله الأنثيين من الغنم؟ فإن قالوا: نعم، فقد كذبوا أيضًا؛ لأنهم لا يحرمون كل أنثى من ولد الضأن والمعز، وقل لهم: هل حَرَّم الله ما اشتملت عليه أرحام الأنثيين من الضأن والمعز من الحمل؟ فإن قالوا: نعم، فقد كذبوا أيضًا؛ لأنهم لا يحرمون كل حَمْل مِن ذلك، خبِّروني بعلم يدل على صحة ما ذهبتم إليه، إن كنتم صادقين فيما تنسبونه إلى ربكم.');
INSERT INTO Quran__Arabic_Tafseer_Muyassar (QuranChapterID, VerseNumber, Rukuh, Sajdah, Manzil, HizbQuarter, VerseText) VALUES (6, 144, '1', '0', '0', '0', 'والأصناف الأربعة الأخرى: هي اثنان من الإبل ذكورًا وإناثًا، واثنان من البقر ذكورًا وإناثًا. قل -أيها الرسول- لأولئك المشركين: أحَرَّم الله الذكرين أم الأنثيين؟ أم حرَّم ما اشتملت عليه أرحام الأنثيين ذكورًا وإناثًا؟ أم كنتم أيها المشركون حاضرين، إذ وصاكم الله بهذا التحريم للأنعام، فلا أحد أشد ظلمًا ممن اختلق على الله الكذب؛ ليصرف الناس بجهله عن طريق الهدى. إن الله تعالى لا يوفق للرشد مَن تجاوز حدَّه، فكذب على ربه، وأضلَّ الناس.');
INSERT INTO Quran__Arabic_Tafseer_Muyassar (QuranChapterID, VerseNumber, Rukuh, Sajdah, Manzil, HizbQuarter, VerseText) VALUES (6, 145, '0', '0', '0', '0', 'قل -أيها الرسول-: إني لا أجد فيما أوحى الله إليَّ شيئًا محرمًا على من يأكله مما تذكرون أنه حُرِّم من الأنعام، إلا أن يكون قد مات بغير تذكية، أو يكون دمًا مراقًا، أو يكون لحم خنزير فإنه نجس، أو الذي كانت ذكاته خروجًا عن طاعة الله تعالى؛ كما إذا كان المذبوح قد ذكر عليه اسم غير الله عند الذبح. فمن اضطر إلى الأكل من هذه المحرمات بسبب الجوع الشديد غير طالب بأكله منها تلذذًا، ولا متجاوز حد الضرورة، فإن الله تعالى غفور له، رحيم به. وقد ثبت - فيما بعد - بالسنة تحريم كل ذي ناب من السباع، ومخلب من الطير، والحمر الأهلية، والكلاب.');
INSERT INTO Quran__Arabic_Tafseer_Muyassar (QuranChapterID, VerseNumber, Rukuh, Sajdah, Manzil, HizbQuarter, VerseText) VALUES (6, 146, '0', '0', '0', '0', 'واذكر -أيها الرسول- لهؤلاء المشركين ما حرمَّنا على اليهود من البهائم والطير: وهو كل ما لم يكن مشقوق الأصابع كالإبل والنَّعام، وشحوم البقر والغنم، إلا ما عَلِق من الشحم بظهورها أو أمعائها، أو اختلط بعظم الألْية والجنب ونحو ذلك. ذلك التحرم المذكور على اليهود عقوبة مِنَّا لهم بسبب أعمالهم السيئة، وإنَّا لصادقون فيما أخبرنا به عنهم.');
INSERT INTO Quran__Arabic_Tafseer_Muyassar (QuranChapterID, VerseNumber, Rukuh, Sajdah, Manzil, HizbQuarter, VerseText) VALUES (6, 147, '0', '0', '0', '0', 'فإن كذبك -أيها الرسول- مخالفوك من المشركين واليهود، وغيرهم، فقل لهم: ربكم جل وعلا ذو رحمة واسعة، ولا يُدْفع عقابه عن القوم الذين أجرموا، فاكتسبوا الذنوب، واجترحوا السيئات. وفي هذا تهديد لهم لمخالفتهم الرسول صلى الله عليه وسلم.');
INSERT INTO Quran__Arabic_Tafseer_Muyassar (QuranChapterID, VerseNumber, Rukuh, Sajdah, Manzil, HizbQuarter, VerseText) VALUES (6, 148, '0', '0', '0', '0', 'سيقول الذين أشركوا: لو أراد الله أن لا نشرك -نحن وآباؤنا- وأن لا نحرم شيئًا مِن دونه ما فعلنا ذلك، وردَّ الله عليهم ببيان أن هذه الشبهة قد أثارها الكفار مِن قبلهم، وكذَّبوا بها دعوة رسلهم، واستمَرُّوا على ذلك، حتى نزل بهم عذاب الله. قل لهم -أيها الرسول-: هل عندكم -فيما حرَّمتم من الأنعام والحرث، وفيما زعمتم من أن الله قد شاء لكم الكفر، ورضيه منكم وأحبه لكم- من علم صحيح فتظهروه لنا؟ إن تتبعون في أمور هذا الدين إلا مجرد الظن، وإن أنتم إلا تكذبون.');
INSERT INTO Quran__Arabic_Tafseer_Muyassar (QuranChapterID, VerseNumber, Rukuh, Sajdah, Manzil, HizbQuarter, VerseText) VALUES (6, 149, '0', '0', '0', '0', 'قل -أيها الرسول- لهم: فلله جل وعلا الحجة القاطعة التي يقطع بها ظنونكم، فلو شاء لوفَّقكم جميعًا إلى طريق الاستقامة.');
INSERT INTO Quran__Arabic_Tafseer_Muyassar (QuranChapterID, VerseNumber, Rukuh, Sajdah, Manzil, HizbQuarter, VerseText) VALUES (6, 150, '1', '0', '0', '0', 'قل -أيها الرسول- لهؤلاء المشركين: هاتوا شهداءكم الذين يشهدون أن الله تعالى هو الذي حرَّم ما حرَّمتم من الحرث والأنعام، فإن شهدوا -كذبًا وزورًا- فلا تصدقهم، ولا توافق الذين حكَّموا أهواءهم، فكذبوا بآيات الله فيما ذهبوا إليه من تحريم ما أحل الله، وتحليل ما حرم الله، ولا تتبع الذين لا يصدقون بالحياة الآخرة ولا يعملون لها، والذين هم بربهم يشركون فيعبدون معه غيره.');
INSERT INTO Quran__Arabic_Tafseer_Muyassar (QuranChapterID, VerseNumber, Rukuh, Sajdah, Manzil, HizbQuarter, VerseText) VALUES (6, 151, '0', '0', '0', '1', 'قل -أيها الرسول- لهم: تعالوا أتل ما حرم ربكم عليكم: أن لا تشركوا معه شيئًا من مخلوقاته في عبادته، بل اصرفوا جميع أنواع العبادة له وحده، كالخوف والرجاء والدعاء، وغير ذلك، وأن تحسنوا إلى الوالدين بالبر والدعاء ونحو ذلك من الإحسان، ولا تقتلوا أولادكم مِن أجل فقر نزل بكم؛ فإن الله يرزقكم وإياهم، ولا تقربوا ما كان ظاهرًا من كبير الآثام، وما كان خفيًّا، ولا تقتلوا النفس التي حرم الله قتلها إلا بالحق، وذلك في حال القصاص من القاتل أو الزنى بعد الإحصان أو الردة عن الإسلام، ذلكم المذكور مما نهاكم الله عنه، وعهد إليكم باجتنابه، ومما أمركم به، وصَّاكم به ربكم؛ لعلكم تعقلون أوامره ونواهيه.');
INSERT INTO Quran__Arabic_Tafseer_Muyassar (QuranChapterID, VerseNumber, Rukuh, Sajdah, Manzil, HizbQuarter, VerseText) VALUES (6, 152, '0', '0', '0', '0', 'ولا تقربوا أيها الأوصياء مال اليتيم إلا بالحال التي تصلح بها أمواله ويَنْتَفِع بها، حتى يصل إلى سن البلوغ ويكون راشدًا، فإذا بلغ ذلك فسلموا إليه ماله، وأوفوا الكيل والوزن بالعدل الذي يكون به تمام الوفاء. وإذا بذلتم جهدكم فلا حرج عليكم فيما قد يكون من نقص، لا نكلف نفسًا إلا وسعها. وإذا قلتم فتحرَّوا في قولكم العدل دون ميل عن الحق في خبر أو شهادة أو حكم أو شفاعة، ولو كان الذي تعلق به القول ذا قرابة منكم، فلا تميلوا معه بغير حق، وأوفوا بما عهد الله به إليكم من الالتزام بشريعته. ذلكم المتلوُّ عليكم من الأحكام، وصَّاكم به ربكم؛ رجاء أن تتذكروا عاقبة أمركم.');
INSERT INTO Quran__Arabic_Tafseer_Muyassar (QuranChapterID, VerseNumber, Rukuh, Sajdah, Manzil, HizbQuarter, VerseText) VALUES (6, 153, '0', '0', '0', '0', 'ومما وصاكم الله به أن هذا الإسلام هو طريق الله تعالى المستقيم فاسلكوه، ولا تسلكوا سبل الضلال، فتفرقكم، وتبعدكم عن سبيل الله المستقيم. ذلكم التوجه نحو الطريق المستقيم هو الذي وصَّاكم الله به؛ لتتقوا عذابه بفعل أوامره، واجتناب نواهيه.');
INSERT INTO Quran__Arabic_Tafseer_Muyassar (QuranChapterID, VerseNumber, Rukuh, Sajdah, Manzil, HizbQuarter, VerseText) VALUES (6, 154, '1', '0', '0', '0', 'ثم قل -أيها الرسول- لهؤلاء المشركين: إن الله تعالى هو الذي أتى موسى التوراة تمامًا لنعمته على المحسنين من أهل ملته، وتفصيلا لكل شيء من أمور دينهم، وهدى ودلالة على الطريق المستقيم ورحمة لهم؛ رجاء أن يصدِّقوا بالبعث بعد الموت والحساب والجزاء، ويعملوا لذلك.');
INSERT INTO Quran__Arabic_Tafseer_Muyassar (QuranChapterID, VerseNumber, Rukuh, Sajdah, Manzil, HizbQuarter, VerseText) VALUES (6, 155, '0', '0', '0', '0', 'وهذا القرآن كتاب أنزلناه على نبينا محمد صلى الله عليه وسلم، خيره كثير فاتبعوه فيما يأمر به وينهى عنه، واتقوا الله أن تخالفوا له أمرًا؛ رجاء أن ترحموا فتنجوا من عذابه، وتظفروا بثوابه.');
INSERT INTO Quran__Arabic_Tafseer_Muyassar (QuranChapterID, VerseNumber, Rukuh, Sajdah, Manzil, HizbQuarter, VerseText) VALUES (6, 156, '0', '0', '0', '0', 'وأنزلنا هذا القرآن؛ لئلا تقولوا -يا كفار العرب-: إنما أُنزل الكتاب من السماء على اليهود والنصارى، وقد كنا عن قراءة كتبهم في شغل، ونحن ليس لنا بها علم ولا معرفة.');
INSERT INTO Quran__Arabic_Tafseer_Muyassar (QuranChapterID, VerseNumber, Rukuh, Sajdah, Manzil, HizbQuarter, VerseText) VALUES (6, 157, '0', '0', '0', '0', 'ولئلا تقولوا -أيها المشركون-: لو أنَّا أُنزل علينا كتاب من السماء، كما أُنزل على اليهود والنصارى، لكنَّا أشدَّ استقامة على طريق الحق منهم، فقد جاءكم كتاب بلسانكم عربي مبين، وذلك حجة واضحة مِن ربكم وإرشاد إلى طريق الحق، ورحمةٌ لهذه الأمة. فلا أحد أشد ظلمًا وعدوانًا ممن كذَّب بحجج الله تعالى وأعرض عنها!! فهؤلاء المعرضون سنعاقبهم عقابًا شديدًا في نار جهنم؛ بسبب إعراضهم عن آياتنا، وصدِّهم عن سبيلنا.');
INSERT INTO Quran__Arabic_Tafseer_Muyassar (QuranChapterID, VerseNumber, Rukuh, Sajdah, Manzil, HizbQuarter, VerseText) VALUES (6, 158, '0', '0', '0', '0', 'هل ينتظر الذين أعرضوا وصدوا عن سبيل الله إلا أن يأتيهم ملك الموت وأعوانه لقبض أرواحهم، أو يأتي ربك -أيها الرسول- للفصل بين عباده يوم القيامة، أو يأتي بعض أشراط الساعة وعلاماتها الدالة على مجيئها، وهي طلوع الشمس من مغربها؟ فحين يكون ذلك لا ينفع نفسا إيمانها، إن لم تكن آمنت من قبل، ولا يُقبل منها إن كانت مؤمنة كسب عمل صالح إن لم تكن عاملة به قبل ذلك. قل لهم -أيها الرسول-: انتظروا مجيء ذلك؛ لتعلموا المحق من المبطل، والمسيء من المحسن، إنا منتظرون ذلك.');
INSERT INTO Quran__Arabic_Tafseer_Muyassar (QuranChapterID, VerseNumber, Rukuh, Sajdah, Manzil, HizbQuarter, VerseText) VALUES (6, 159, '0', '0', '0', '0', 'إن الذين فرقوا دينهم بعد ما كانوا مجتمعين على توحيد الله والعمل بشرعه، فأصبحوا فرقا وأحزابا، إنك -أيها الرسول- بريء منهم، إنما حكمهم إلى الله تعالى، ثم يخبرهم بأعمالهم، فيجازي من تاب منهم وأحسن بإحسانه، ويعاقب المسيء بإساءته.');
INSERT INTO Quran__Arabic_Tafseer_Muyassar (QuranChapterID, VerseNumber, Rukuh, Sajdah, Manzil, HizbQuarter, VerseText) VALUES (6, 160, '0', '0', '0', '0', 'من لقي ربه يوم القيامة بحسنة من الأعمال الصالحة فله عشر حسنات أمثالها، ومن لقي ربه بسيئة فلا يعاقب إلا بمثلها، وهم لا يظلمون مثقال ذرة.');
INSERT INTO Quran__Arabic_Tafseer_Muyassar (QuranChapterID, VerseNumber, Rukuh, Sajdah, Manzil, HizbQuarter, VerseText) VALUES (6, 161, '0', '0', '0', '0', 'قل -أيها الرسول- لهؤلاء المشركين: إنني أرشدني ربي إلى الطريق القويم الموصل إلى جنته، وهو دين الإسلام القائم بأمر الدنيا والآخرة، وهو دين التوحيد دين إبراهيم عليه السلام، وما كان إبراهيم عليه السلام من المشركين مع الله غيره.');
INSERT INTO Quran__Arabic_Tafseer_Muyassar (QuranChapterID, VerseNumber, Rukuh, Sajdah, Manzil, HizbQuarter, VerseText) VALUES (6, 162, '0', '0', '0', '0', 'قل -أيها الرسول- لهؤلاء المشركين: إن صلاتي، ونسكي، أي: ذبحي لله وحده، لا للأصنام، ولا للأموات، ولا للجن، ولا لغير ذلك مما تذبحونه لغير الله، وعلى غير اسمه كما تفعلون، وحياتي وموتي لله تعالى رب العالمين.');
INSERT INTO Quran__Arabic_Tafseer_Muyassar (QuranChapterID, VerseNumber, Rukuh, Sajdah, Manzil, HizbQuarter, VerseText) VALUES (6, 163, '0', '0', '0', '0', 'لا شريك له في ألوهيته ولا في ربوبيته ولا في صفاته وأسمائه، وبذلك التوحيد الخالص أمرني ربي جل وعلا وأنا أول من أقر وانقاد لله من هذه الأمة.');
INSERT INTO Quran__Arabic_Tafseer_Muyassar (QuranChapterID, VerseNumber, Rukuh, Sajdah, Manzil, HizbQuarter, VerseText) VALUES (6, 164, '0', '0', '0', '0', 'قل -أيها الرسول-: أغير الله أطلب إلها، وهو خالق كل شيء ومالكه ومدبره؟ ولا يعمل أي إنسان عملا سيئا إلا كان إثمه عليه، ولا تحمل نفس آثمة إثم نفس أخرى، ثم إلى ربكم معادكم يوم القيامة، فيخبركم بما كنتم تختلفون فيه من أمر الدين.');
INSERT INTO Quran__Arabic_Tafseer_Muyassar (QuranChapterID, VerseNumber, Rukuh, Sajdah, Manzil, HizbQuarter, VerseText) VALUES (6, 165, '1', '0', '0', '0', 'والله سبحانه هو الذي جعلكم تخلفون من سبقكم في الأرض بعد أن أهلكهم الله، واستخلفكم فيها؛ لتعمروها بعدهم بطاعة ربكم، ورفع بعضكم في الرزق والقوة فوق بعض درجات، ليبلوكم فيما أعطاكم من نعمه، فيظهر للناس الشاكر من غيره. إن ربك سريع العقاب لمن كفر به وعصاه، وإنه لغفور لمن آمن به وعمل صالحا وتاب من الموبقات، رحيم به، والغفور والرحيم اسمان كريمان من أسماء الله الحسنى.');
INSERT INTO Quran__Arabic_Tafseer_Muyassar (QuranChapterID, VerseNumber, Rukuh, Sajdah, Manzil, HizbQuarter, VerseText) VALUES (7, 1, '0', '0', '0', '1', 'سبق الكلام على الحروف المقطَّعة في أول سورة البقرة.');
INSERT INTO Quran__Arabic_Tafseer_Muyassar (QuranChapterID, VerseNumber, Rukuh, Sajdah, Manzil, HizbQuarter, VerseText) VALUES (7, 2, '0', '0', '0', '0', 'هذا القرآن كتاب عظيم أنزله الله عليك -أيها الرسول- فلا يكن في صدرك شك منه في أنه أنزل من عند الله، ولا تتحرج في إبلاغه والإنذار به، أنزلناه إليك؛ لتخوف به الكافرين وتذكر المؤمنين.');
INSERT INTO Quran__Arabic_Tafseer_Muyassar (QuranChapterID, VerseNumber, Rukuh, Sajdah, Manzil, HizbQuarter, VerseText) VALUES (7, 3, '0', '0', '0', '0', 'اتبعوا -أيها الناس- ما أُنزل إليكم من ربكم من الكتاب والسنة بامتثال الأوامر واجتناب النواهي، ولا تتبعوا من دون الله أولياء كالشياطين والأحبار والرهبان. إنكم قليلا ما تتعظون، وتعتبرون، فترجعون إلى الحق.');
INSERT INTO Quran__Arabic_Tafseer_Muyassar (QuranChapterID, VerseNumber, Rukuh, Sajdah, Manzil, HizbQuarter, VerseText) VALUES (7, 4, '0', '0', '0', '0', 'وكثير من القرى أهلكنا أهلها بسبب مخالفة رسلنا وتكذيبهم، فأعقبهم ذلك خزي الدنيا موصولا بذلِّ الآخرة، فجاءهم عذابنا مرة وهم نائمون ليلا ومرة وهم نائمون نهارًا. وخَصَّ الله هذين الوقتين؛ لأنهما وقتان للسكون والاستراحة، فمجيء العذاب فيهما أفظع وأشد.');
INSERT INTO Quran__Arabic_Tafseer_Muyassar (QuranChapterID, VerseNumber, Rukuh, Sajdah, Manzil, HizbQuarter, VerseText) VALUES (7, 5, '0', '0', '0', '0', 'فما كان قولهم عند مجيء العذاب إلا الإقرار بالذنوب والإساءة، وأنهم حقيقون بالعذاب الذي نزل بهم.');
INSERT INTO Quran__Arabic_Tafseer_Muyassar (QuranChapterID, VerseNumber, Rukuh, Sajdah, Manzil, HizbQuarter, VerseText) VALUES (7, 6, '0', '0', '0', '0', 'فلنسألن الأمم الذي أرسل إليهم المرسلون: ماذا أجبتم رسلنا إليكم؟ ولنسْألَنَّ المرسلين عن تبليغهم لرسالات ربهم، وعمَّا أجابتهم به أممهم.');
INSERT INTO Quran__Arabic_Tafseer_Muyassar (QuranChapterID, VerseNumber, Rukuh, Sajdah, Manzil, HizbQuarter, VerseText) VALUES (7, 7, '0', '0', '0', '0', 'فلَنقُصَّنَّ على الخلق كلهم ما عملوا بعلم منا لأعمالهم في الدنيا فيما أمرناهم به، وما نهيناهم عنه، وما كنا غائبين عنهم في حال من الأحوال.');
INSERT INTO Quran__Arabic_Tafseer_Muyassar (QuranChapterID, VerseNumber, Rukuh, Sajdah, Manzil, HizbQuarter, VerseText) VALUES (7, 8, '0', '0', '0', '0', 'ووزن أعمال الناس يوم القيامة يكون بميزان حقيقي بالعدل والقسط الذي لا ظلم فيه، فمن ثقلت موازين أعماله -لكثرة حسناته- فأولئك هم الفائزون.');
INSERT INTO Quran__Arabic_Tafseer_Muyassar (QuranChapterID, VerseNumber, Rukuh, Sajdah, Manzil, HizbQuarter, VerseText) VALUES (7, 9, '0', '0', '0', '0', 'ومن خَفَّتْ موازين أعماله -لكثرة سيئاته- فأولئك هم الذين أضاعوا حظَّهم من رضوان الله تعالى، بسبب تجاوزهم الحد بجحد آيات الله تعالى وعدم الانقياد لها.');
INSERT INTO Quran__Arabic_Tafseer_Muyassar (QuranChapterID, VerseNumber, Rukuh, Sajdah, Manzil, HizbQuarter, VerseText) VALUES (7, 10, '1', '0', '0', '0', 'ولقد مكَّنَّا لكم -أيها الناس- في الأرض، وجعلناها قرارًا لكم، وجعلنا لكم فيها ما تعيشون به من مطاعم ومشارب، ومع ذلك فشكركم لنعم الله قليل.');
INSERT INTO Quran__Arabic_Tafseer_Muyassar (QuranChapterID, VerseNumber, Rukuh, Sajdah, Manzil, HizbQuarter, VerseText) VALUES (7, 11, '0', '0', '0', '0', 'ولقد أنعمنا عليكم بخلق أصلكم -وهو أبوكم آدم من العدم- ثم صوَّرناه على هيئته المفضلة على كثير من الخلق، ثم أمرنا ملائكتنا عليهم السلام بالسجود له -إكرامًا واحترامًا وإظهارًا لفضل آدم- فسجدوا جميعًا، لكنَّ إبليس الذي كان معهم لم يكن من الساجدين لآدم؛ حسدًا له على هذا التكريم العظيم.');
INSERT INTO Quran__Arabic_Tafseer_Muyassar (QuranChapterID, VerseNumber, Rukuh, Sajdah, Manzil, HizbQuarter, VerseText) VALUES (7, 12, '0', '0', '0', '0', 'قال تعالى منكرًا على إبليس تَرْكَ السجود: ما منعك ألا تسجد إذ أمرتك؟ فقال إبليس: أنا أفضل منه خلقًا؛ لأني مخلوق من نار، وهو مخلوق من طين. فرأى أن النار أشرف من الطين.');
INSERT INTO Quran__Arabic_Tafseer_Muyassar (QuranChapterID, VerseNumber, Rukuh, Sajdah, Manzil, HizbQuarter, VerseText) VALUES (7, 13, '0', '0', '0', '0', 'قال الله لإبليس: فاهبط من الجنة، فما يصح لك أن تتكبر فيها، فاخرج من الجنة، إنك من الذليلين الحقيرين.');
INSERT INTO Quran__Arabic_Tafseer_Muyassar (QuranChapterID, VerseNumber, Rukuh, Sajdah, Manzil, HizbQuarter, VerseText) VALUES (7, 14, '0', '0', '0', '0', 'قال إبليس لله -جل وعلا- حينما يئس من رحمته: أمهلني إلى يوم البعث؛ وذلك لأتمكن من إغواء مَن أقدر عليه من بني آدم.');
INSERT INTO Quran__Arabic_Tafseer_Muyassar (QuranChapterID, VerseNumber, Rukuh, Sajdah, Manzil, HizbQuarter, VerseText) VALUES (7, 15, '0', '0', '0', '0', 'قال الله تعالى: إنك ممن كتبتُ عليهم تأخير الأجل إلى النفخة الأولى في القرن، إذ يموت الخلق كلهم.');
INSERT INTO Quran__Arabic_Tafseer_Muyassar (QuranChapterID, VerseNumber, Rukuh, Sajdah, Manzil, HizbQuarter, VerseText) VALUES (7, 16, '0', '0', '0', '0', 'قال إبليس لعنه الله: فبسبب ما أضللتني لأجتهدنَّ في إغواء بني آدم عن طريقك القويم، ولأصدَّنَّهم عن الإسلام الذي فطرتهم عليه.');
INSERT INTO Quran__Arabic_Tafseer_Muyassar (QuranChapterID, VerseNumber, Rukuh, Sajdah, Manzil, HizbQuarter, VerseText) VALUES (7, 17, '0', '0', '0', '0', 'ثم لآتينَّهم من جميع الجهات والجوانب، فأصدهم عن الحق، وأُحسِّن لهم الباطل، وأرغبهم في الدنيا، وأشككهم في الآخرة، ولا تجد أكثر بني آدم شاكرين لك نعمتك.');
INSERT INTO Quran__Arabic_Tafseer_Muyassar (QuranChapterID, VerseNumber, Rukuh, Sajdah, Manzil, HizbQuarter, VerseText) VALUES (7, 18, '0', '0', '0', '0', 'قال الله تعالى لإبليس: اخرج من الجنة ممقوتًا مطرودًا، لأملأنَّ جهنم منك وممن تبعك من بني آدم أجمعين.');
INSERT INTO Quran__Arabic_Tafseer_Muyassar (QuranChapterID, VerseNumber, Rukuh, Sajdah, Manzil, HizbQuarter, VerseText) VALUES (7, 19, '0', '0', '0', '0', 'ويا آدم اسكن أنت وزوجك حواء الجنة، فكُلا من ثمارها حيث شئتما، ولا تأكلا من ثمرة شجرة (عَيَّنها لهما)، فإن فعلتما ذلك كنتما من الظالمين المتجاوزين حدود الله.');
INSERT INTO Quran__Arabic_Tafseer_Muyassar (QuranChapterID, VerseNumber, Rukuh, Sajdah, Manzil, HizbQuarter, VerseText) VALUES (7, 20, '0', '0', '0', '0', 'فألقى الشيطان لآدم وحواء وسوسة لإيقاعهما في معصية الله تعالى بالأكل من تلك الشجرة التي نهاهما الله عنها؛ لتكون عاقبتهما انكشاف ما سُتر من عوراتهما، وقال لهما في محاولة المكر بهما: إنما نهاكما ربكما عن الأكل مِن ثمر هذه الشجرة مِن أجل أن لا تكونا ملَكين، ومِن أجل أن لا تكونا من الخالدين في الحياة.');
INSERT INTO Quran__Arabic_Tafseer_Muyassar (QuranChapterID, VerseNumber, Rukuh, Sajdah, Manzil, HizbQuarter, VerseText) VALUES (7, 21, '0', '0', '0', '0', 'وأقسم الشيطان لآدم وحواء بالله إنه ممن ينصح لهما في مشورته عليهما بالأكل من الشجرة، وهو كاذب في ذلك.');
INSERT INTO Quran__Arabic_Tafseer_Muyassar (QuranChapterID, VerseNumber, Rukuh, Sajdah, Manzil, HizbQuarter, VerseText) VALUES (7, 22, '0', '0', '0', '0', 'فجرَّأهما وغرَّهما، فأكلا من الشجرة التي نهاهما الله عن الاقتراب منها، فلما أكلا منها انكشفت لهما عوراتهما، وزال ما سترهما الله به قبل المخالفة، فأخذا يلزقان بعض ورق الجنة على عوراتهما، وناداهما ربهما جل وعلا ألم أنهكما عن الأكل من تلك الشجرة، وأقل لكما: إن الشيطان لكما عدو ظاهر العداوة؟ وفي هذه الآية دليل على أن كشف العورة من عظائم الأمور، وأنه كان ولم يزل مستهجَنًا في الطباع، مستقبَحًا في العقول.');
INSERT INTO Quran__Arabic_Tafseer_Muyassar (QuranChapterID, VerseNumber, Rukuh, Sajdah, Manzil, HizbQuarter, VerseText) VALUES (7, 23, '0', '0', '0', '0', 'قال آدم وحواء: ربنا ظلمنا أنفسنا بالأكل من الشجرة، وإن لم تغفر لنا وترحمنا لنكونن ممن أضاعوا حظَّهم في دنياهم وأخراهم. (وهذه الكلمات هي التي تلقاها آدم من ربه، فدعا بها فتاب الله عليه).');
INSERT INTO Quran__Arabic_Tafseer_Muyassar (QuranChapterID, VerseNumber, Rukuh, Sajdah, Manzil, HizbQuarter, VerseText) VALUES (7, 24, '0', '0', '0', '0', 'قال تعالى مخاطبًا آدم وحواء لإبليس: اهبطوا من السماء إلى الأرض، وسيكون بعضكم لبعض عدوًا، ولكم في الأرض مكان تستقرون فيه، وتتمتعون إلى انقضاء آجالكم.');
INSERT INTO Quran__Arabic_Tafseer_Muyassar (QuranChapterID, VerseNumber, Rukuh, Sajdah, Manzil, HizbQuarter, VerseText) VALUES (7, 25, '1', '0', '0', '0', 'قال الله تعالى لآدم وحوَّاء وذريتهما: فيها تحيون، أي: في الأرض تقضون أيام حياتكم الدنيا، وفيها تكون وفاتكم، ومنها يخرجكم ربكم، ويحشركم أحياء يوم البعث.');
INSERT INTO Quran__Arabic_Tafseer_Muyassar (QuranChapterID, VerseNumber, Rukuh, Sajdah, Manzil, HizbQuarter, VerseText) VALUES (7, 26, '0', '0', '0', '0', 'يا بني آدم قد جعلنا لكم لباسًا يستر عوراتكم، وهو لباس الضرورة، ولباسًا للزينة والتجمل، وهو من الكمال والتنعم. ولباسُ تقوى الله تعالى بفعل الأوامر واجتناب النواهي هو خير لباس للمؤمن. ذلك الذي مَنَّ الله به عليكم من الدلائل على ربوبية الله تعالى ووحدانيته وفضله ورحمته بعباده؛ لكي تتذكروا هذه النعم، فتشكروا لله عليها. وفي ذلك امتنان من الله تعالى على خَلْقه بهذه النعم.');
INSERT INTO Quran__Arabic_Tafseer_Muyassar (QuranChapterID, VerseNumber, Rukuh, Sajdah, Manzil, HizbQuarter, VerseText) VALUES (7, 27, '0', '0', '0', '0', 'يا بني آدم لا يخدعنَّكم الشيطان، فيزين لكم المعصية، كما زيَّنها لأبويكم آدم وحواء، فأخرجهما بسببها من الجنة، ينزع عنهما لباسهما الذي سترهما الله به؛ لتنكشف لهما عوراتهما. إن الشيطان يراكم هو وذريته وجنسه وأنتم لا ترونهم فاحذروهم. إنَّا جعلنا الشياطين أولياء للكفار الذين لا يوحدون الله، ولا يصدقون رسله، ولا يعملون بهديه.');
INSERT INTO Quran__Arabic_Tafseer_Muyassar (QuranChapterID, VerseNumber, Rukuh, Sajdah, Manzil, HizbQuarter, VerseText) VALUES (7, 28, '0', '0', '0', '0', 'وإذا أتى الكفار قبيحًا من الفعل اعتذروا عن فعله بأنه مما ورثوه عن آبائهم، وأنه مما أمر الله به. قل لهم -أيها الرسول-: إن الله تعالى لا يأمر عباده بقبائح الأفعال ومساوئها، أتقولون على الله -أيها المشركون- ما لا تعلمون كذبًا وافتراءً؟');
INSERT INTO Quran__Arabic_Tafseer_Muyassar (QuranChapterID, VerseNumber, Rukuh, Sajdah, Manzil, HizbQuarter, VerseText) VALUES (7, 29, '0', '0', '0', '0', 'قل -أيها الرسول- لهؤلاء المشركين: أمر ربي بالعدل، وأمركم بأن تخلصوا له العبادة في كل موضع من مواضعها، وبخاصة في المساجد، وأن تدعوه مخلصين له الطاعة والعبادة، وأن تؤمنوا بالبعث بعد الموت. وكما أن الله أوجدكم من العدم فإنه قادر على إعادة الحياة إليكم مرة أخرى.');
INSERT INTO Quran__Arabic_Tafseer_Muyassar (QuranChapterID, VerseNumber, Rukuh, Sajdah, Manzil, HizbQuarter, VerseText) VALUES (7, 30, '0', '0', '0', '0', 'جعل الله عباده فريقين: فريقًا وفَّقهم للهداية إلى الصراط المستقيم، وفريقًا وجبت عليهم الضلالة عن الطريق المستقيم، إنهم اتخذوا الشياطين أولياء من دون الله، فأطاعوهم جهلا منهم وظنًا بأنهم قد سلكوا سبيل الهداية.');
INSERT INTO Quran__Arabic_Tafseer_Muyassar (QuranChapterID, VerseNumber, Rukuh, Sajdah, Manzil, HizbQuarter, VerseText) VALUES (7, 31, '1', '0', '0', '1', 'يا بني آدم كونوا عند أداء كل صلاة على حالة من الزينة المشروعة من ثياب ساترة لعوراتكم ونظافة وطهارة ونحو ذلك، وكلوا واشربوا من طيبات ما رزقكم الله، ولا تتجاوزوا حدود الاعتدال في ذلك. إن الله لا يحب المتجاوزين المسرفين في الطعام والشراب وغير ذلك.');
INSERT INTO Quran__Arabic_Tafseer_Muyassar (QuranChapterID, VerseNumber, Rukuh, Sajdah, Manzil, HizbQuarter, VerseText) VALUES (7, 32, '0', '0', '0', '0', 'قل -أيها الرسول- لهؤلاء الجهلة من المشركين: مَن الذي حرم عليكم اللباس الحسن الذي جعله الله تعالى زينة لكم؟ ومَن الذي حرَّم عليكم التمتع بالحلال الطيب من رزق الله تعالى؟ قل -أيها الرسول- لهؤلاء المشركين: إنَّ ما أحله الله من الملابس والطيبات من المطاعم والمشارب حق للذين آمنوا في الحياة الدنيا يشاركهم فيها غيرهم، خالصة لهم يوم القيامة. مثل ذلك التفصيل يفصِّل الله الآيات لقوم يعلمون ما يبيِّن لهم، ويفقهون ما يميز لهم.');
INSERT INTO Quran__Arabic_Tafseer_Muyassar (QuranChapterID, VerseNumber, Rukuh, Sajdah, Manzil, HizbQuarter, VerseText) VALUES (7, 33, '0', '0', '0', '0', 'قل -أيها الرسول- لهؤلاء المشركين: إنما حَرَّم الله القبائح من الأعمال، ما كان منها ظاهرًا، وما كان خفيًّا، وحَرَّم المعاصي كلها، ومِن أعظمها الاعتداء على الناس، فإن ذلك مجانب للحق، وحرَّم أن تعبدوا مع الله تعالى غيره مما لم يُنَزِّل به دليلا وبرهانًا، فإنه لا حجة لفاعل ذلك، وحرَّم أن تنسبوا إلى الله تعالى ما لم يشرعه افتراءً وكذبًا، كدعوى أن لله ولدًا، وتحريم بعض الحلال من الملابس والمآكل.');
INSERT INTO Quran__Arabic_Tafseer_Muyassar (QuranChapterID, VerseNumber, Rukuh, Sajdah, Manzil, HizbQuarter, VerseText) VALUES (7, 34, '0', '0', '0', '0', 'ولكل جماعة اجتمعت على الكفر بالله تعالى وتكذيب رسله -عليهم الصلاة والسلام- وقت لحلول العقوبة بهم، فإذا جاء الوقت الذي وقَّته الله لإهلاكهم لا يتأخرون عنه لحظة، ولا يتقدمون عليه.');
INSERT INTO Quran__Arabic_Tafseer_Muyassar (QuranChapterID, VerseNumber, Rukuh, Sajdah, Manzil, HizbQuarter, VerseText) VALUES (7, 35, '0', '0', '0', '0', 'يا بني آدم إذا جاءكم رسلي من أقوامكم، يتلون عليكم آيات كتابي، ويبينون لكم البراهين على صدق ما جاؤوكم به فأطيعوهم، فإنه من اتقى سخطي وأصلح عمله فلا خوف عليهم يوم القيامة من عقاب الله تعالى، ولا ههم يحزنون على ما فاتهم من حظوظ الدنيا.');
INSERT INTO Quran__Arabic_Tafseer_Muyassar (QuranChapterID, VerseNumber, Rukuh, Sajdah, Manzil, HizbQuarter, VerseText) VALUES (7, 36, '0', '0', '0', '0', 'والكفار الذين كذَّبوا بالدلائل على توحيد الله، واستعلَوا عن اتباعها، أولئك أصحاب النار ماكثين فيها، لا يخرجون منها أبدًا.');
INSERT INTO Quran__Arabic_Tafseer_Muyassar (QuranChapterID, VerseNumber, Rukuh, Sajdah, Manzil, HizbQuarter, VerseText) VALUES (7, 37, '0', '0', '0', '0', 'لا أحد أشد ظلمًا ممن اختلق على الله تعالى الكذب، أو كذَّب بآياته المنزلة، أولئك يصل إليهم حظُّهم من العذاب مما كتب لهم في اللوح المحفوظ، حتى إذا جاءهم ملك الموت وأعوانه يقبضون أرواحهم قالوا لهم: أين الذين كنتم تعبدونهم من دون الله من الشركاء والأولياء والأوثان ليخلِّصوكم مما أنتم فيه؟ قالوا: ذهبوا عنا، واعترفوا على أنفسهم حينئذ أنهم كانوا في الدنيا جاحدين مكذبين وحدانية الله تعالى.');
INSERT INTO Quran__Arabic_Tafseer_Muyassar (QuranChapterID, VerseNumber, Rukuh, Sajdah, Manzil, HizbQuarter, VerseText) VALUES (7, 38, '0', '0', '0', '0', 'قال الله تعالى -لهؤلاء المشركين المفترين-: ادخلوا النار في جملة جماعات من أمثالكم في الكفر، قد سلفت من قبلكم من الجن والإنس، كلما دخلت النارَ جماعةٌ من أهل ملة لعنت نظيرتها التي ضلَّتْ بالاقتداء بها، حتى إذا تلاحق في النار الأولون من أهل الملل الكافرة والآخرون منهم جميعًا، قال الآخرون المتبعون في الدنيا لقادتهم: ربنا هؤلاء هم الذين أضلونا عن الحق، فآتهم عذابًا مضاعفا من النار، قال الله تعالى: لكل ضعف، أي: لكل منكم ومنهم عذاب مضاعف من النار، ولكن لا تدركون أيها الأتباع ما لكل فريق منكم من العذاب والآلام.');
INSERT INTO Quran__Arabic_Tafseer_Muyassar (QuranChapterID, VerseNumber, Rukuh, Sajdah, Manzil, HizbQuarter, VerseText) VALUES (7, 39, '1', '0', '0', '0', 'وقال المتبوعون من الرؤساء وغيرهم لأتباعهم: نحن وأنتم متساوون في الغيِّ والضلال، وفي فِعْلِ أسباب العذاب فلا فَضْلَ لكم علينا، قال الله تعالى لهم جميعًا: فذوقوا العذاب أي عذاب جهنم؛ بسبب ما كسبتم من المعاصي.');
INSERT INTO Quran__Arabic_Tafseer_Muyassar (QuranChapterID, VerseNumber, Rukuh, Sajdah, Manzil, HizbQuarter, VerseText) VALUES (7, 40, '0', '0', '0', '0', 'إن الكفار الذين لم يصدِّقوا بحججنا وآياتنا الدالة على وحدانيتنا، ولم يعملوا بشرعنا تكبرًا واستعلاءً، لا تُفتَّح لأعمالهم في الحياة ولا لأرواحهم عند الممات أبواب السماء، ولا يمكن أن يدخل هؤلاء الكفار الجنة إلا إذا دخل الجمل في ثقب الإبرة، وهذا مستحيل. ومثل ذلك الجزاء نجزي الذين كثر إجرامهم، واشتدَّ طغيانهم.');
INSERT INTO Quran__Arabic_Tafseer_Muyassar (QuranChapterID, VerseNumber, Rukuh, Sajdah, Manzil, HizbQuarter, VerseText) VALUES (7, 41, '0', '0', '0', '0', 'هؤلاء الكفار مخلدون في النار، لهم مِن جهنم فراش مِن تحتهم، ومن فوقهم أغطية تغشاهم. وبمثل هذا العقاب الشديد يعاقب الله تعالى الظالمين الذين تجاوزوا حدوده فكفروا به وعصَوْه.');
INSERT INTO Quran__Arabic_Tafseer_Muyassar (QuranChapterID, VerseNumber, Rukuh, Sajdah, Manzil, HizbQuarter, VerseText) VALUES (7, 42, '0', '0', '0', '0', 'والذين آمنوا بالله وعملوا الأعمال الصالحة في حدود طاقاتهم -لا يكلف الله نفسًا من الأعمال إلا ما تطيق- أولئك أهل الجنة، هم فيها ماكثون أبدًا لا يخرجون منها.');
INSERT INTO Quran__Arabic_Tafseer_Muyassar (QuranChapterID, VerseNumber, Rukuh, Sajdah, Manzil, HizbQuarter, VerseText) VALUES (7, 43, '0', '0', '0', '0', 'وأذهب الله تعالى ما في صدور أهل الجنة من حقد وضغائن، ومن كمال نعيمهم أن الأنهار تجري في الجنة من تحتهم. وقال أهل الجنة حينما دخلوها: الحمد لله الذي وفَّقنا للعمل الصالح الذي أكسبنا ما نحن فيه من النعيم، وما كنا لنوفَّق إلى سلوك الطريق المستقيم لولا أَنْ هدانا الله سبحانه لسلوك هذا الطريق، ووفَّقنا للثبات عليه، لقد جاءت رسل ربنا بالحق من الإخبار بوعد أهل طاعته ووعيد أهل معصيته، ونُودوا تهنئة لهم وإكرامًا: أن تلكم الجنة أورثكم الله إياها برحمته، وبما قدَّمتموه من الإيمان والعمل الصالح.');
INSERT INTO Quran__Arabic_Tafseer_Muyassar (QuranChapterID, VerseNumber, Rukuh, Sajdah, Manzil, HizbQuarter, VerseText) VALUES (7, 44, '0', '0', '0', '0', 'ونادى أصحاب الجنة -بعد دخولهم فيها- أهلَ النار قائلين لهم: إنا قد وجدنا ما وعدنا ربنا على ألسنة رسله حقًا من إثابة أهل طاعته، فهل وجدتم ما وعدكم ربكم على ألسنة رسله حقًا من عقاب أهل معصيته؟ فأجابهم أهل النار قائلين: نعم قد وجدنا ما وعدنا ربنا حقًا. فأذَّن مؤذن بين أهل الجنة وأهل النار: أنْ لعنة الله على الظالمين الذين تجاوزوا حدود الله، وكفروا بالله ورسله.');
INSERT INTO Quran__Arabic_Tafseer_Muyassar (QuranChapterID, VerseNumber, Rukuh, Sajdah, Manzil, HizbQuarter, VerseText) VALUES (7, 45, '0', '0', '0', '0', 'هؤلاء الكافرون هم الذين كانوا يُعْرِضون عن طريق الله المستقيم، ويمنعون الناس من سلوكه، ويطلبون أن تكون السبيل معوجة حتى لا يتبينها أحد، وهم بالآخرة -وما فيها- جاحدون.');
INSERT INTO Quran__Arabic_Tafseer_Muyassar (QuranChapterID, VerseNumber, Rukuh, Sajdah, Manzil, HizbQuarter, VerseText) VALUES (7, 46, '0', '0', '0', '0', 'وبين أصحاب الجنة وأصحاب النار حاجز عظيم يقال له الأعراف، وعلى هذا الحاجز رجال يعرفون أهل الجنة وأهل النار بعلاماتهم، كبياض وجوه أهل الجنة، وسواد وجوه أهل النار، وهؤلاء الرجال قوم استوت حسناتهم وسيئاتهم يرجون رحمة الله تعالى. ونادى رجال الأعراف أهل الجنة بالتحية قائلين لهم: سلام عليكم، وأهل الأعراف لم يدخلوا الجنة بعد، وهم يرجون دخولها.');
INSERT INTO Quran__Arabic_Tafseer_Muyassar (QuranChapterID, VerseNumber, Rukuh, Sajdah, Manzil, HizbQuarter, VerseText) VALUES (7, 47, '1', '0', '0', '1', 'وإذا حُوِّلَتْ أبصار رجال الأعراف جهة أهل النار قالوا: ربنا لا تُصيِّرنا مع القوم الظالمين بشركهم وكفرهم.');
INSERT INTO Quran__Arabic_Tafseer_Muyassar (QuranChapterID, VerseNumber, Rukuh, Sajdah, Manzil, HizbQuarter, VerseText) VALUES (7, 48, '0', '0', '0', '0', 'ونادى أهل الأعراف رجالا من قادة الكفار الذين في النار، يعرفونهم بعلامات خاصة تميزهم، قالوا لهم: ما نفعكم ما كنتم تجمعون من الأموال والرجال في الدنيا، وما نفعكم استعلاؤكم عن الإيمان بالله وقَبول الحق.');
INSERT INTO Quran__Arabic_Tafseer_Muyassar (QuranChapterID, VerseNumber, Rukuh, Sajdah, Manzil, HizbQuarter, VerseText) VALUES (7, 49, '0', '0', '0', '0', 'أهؤلاء الضعفاء والفقراء من أهل الجنة الذين أقسمتم في الدنيا أن الله لا يشملهم يوم القيامة برحمة، ولن يدخلهم الجنة؟ ادخلوا الجنة يا أصحاب الأعراف فقد غُفِرَ لكم، لا خوف عليكم من عذاب الله، ولا أنتم تحزنون على ما فاتكم من حظوظ الدنيا.');
INSERT INTO Quran__Arabic_Tafseer_Muyassar (QuranChapterID, VerseNumber, Rukuh, Sajdah, Manzil, HizbQuarter, VerseText) VALUES (7, 50, '0', '0', '0', '0', 'واستغاث أهل النار بأهل الجنة طالبين منهم أن يُفيضوا عليهم من الماء، أو مما رزقهم الله من الطعام، فأجابوهم بأن الله تعالى قد حَرَّم الشراب والطعام على الذين جحدوا توحيده، وكذَّبوا رسله.');
INSERT INTO Quran__Arabic_Tafseer_Muyassar (QuranChapterID, VerseNumber, Rukuh, Sajdah, Manzil, HizbQuarter, VerseText) VALUES (7, 51, '0', '0', '0', '0', 'الذين حَرَمهم الله تعالى من نعيم الآخرة هم الذين جعلوا الدين الذي أمرهم الله باتباعه باطلا ولهوًا، وخدعتهم الحياة الدنيا وشغلوا بزخارفها عن العمل للآخرة، فيوم القيامة ينساهم الله تعالى ويتركهم في العذاب الموجع، كما تركوا العمل للقاء يومهم هذا، ولكونهم بأدلة الله وبراهينه ينكرون مع علمهم بأنها حق.');
INSERT INTO Quran__Arabic_Tafseer_Muyassar (QuranChapterID, VerseNumber, Rukuh, Sajdah, Manzil, HizbQuarter, VerseText) VALUES (7, 52, '0', '0', '0', '0', 'ولقد جئنا الكفار بقرآن أنزلناه عليك -أيها الرسول- بيَّنَّاه مشتملا على علم عظيم، هاديًا من الضلالة إلى الرشد ورحمة لقوم يؤمنون بالله ويعملون بشرعه. وخصَّهم دون غيرهم؛ لأنهم هم المنتفعون به.');
INSERT INTO Quran__Arabic_Tafseer_Muyassar (QuranChapterID, VerseNumber, Rukuh, Sajdah, Manzil, HizbQuarter, VerseText) VALUES (7, 53, '1', '0', '0', '0', 'هل ينتظر الكفار إلا ما وُعِدوا به في القرآن من العقاب الذي يؤول إليه أمرهم؟ يوم يأتي ما يئول إليه الأمر من الحساب والثواب والعقاب يوم القيامة يقول الكفار الذين تركوا القرآن، وكفروا به في الحياة الدنيا: قد تبيَّن لنا الآن أنَّ رسل ربنا قد جاؤوا بالحق، ونصحوا لنا، فهل لنا من أصدقاء وشفعاء، فيشفعوا لنا عند ربنا، أو نعاد إلى الدنيا مرة أخرى فنعمل فيها بما يرضي الله عنا؟ قد خسروا أنفسهم بدخولهم النار وخلودهم فيها، وذهب عنهم ما كانوا يعبدونه من دون الله، ويفترونه في الدنيا مما يَعِدُهم به الشيطان.');
INSERT INTO Quran__Arabic_Tafseer_Muyassar (QuranChapterID, VerseNumber, Rukuh, Sajdah, Manzil, HizbQuarter, VerseText) VALUES (7, 54, '0', '0', '0', '0', 'إن ربكم -أيها الناس- هو الله الذي أوجد السموات والأرض من العدم في ستة أيام، ثم استوى -سبحانه- على العرش -أي علا وارتفع- استواءً يليق بجلاله وعظمته، يُدخل سبحانه الليل على النهار، فيلبسه إياه حتى يذهب نوره، ويُدخل النهار على الليل فيذهب ظلامه، وكل واحد منهما يطلب الآخر سريعًا دائمًا، وهو -سبحانه- الذي خلق الشمس والقمر والنجوم مذللات له يسخرهن -سبحانه- كما يشاء، وهنَّ من آيات الله العظيمة. ألا له سبحانه وتعالى الخلق كله وله الأمر كله، تعالى الله وتعاظم وتنزَّه عن كل نقص، رب الخلق أجمعين.');
INSERT INTO Quran__Arabic_Tafseer_Muyassar (QuranChapterID, VerseNumber, Rukuh, Sajdah, Manzil, HizbQuarter, VerseText) VALUES (7, 55, '0', '0', '0', '0', 'ادعوا -أيها المؤمنون- ربكم متذللين له خفية وسرًّا، وليكن الدعاء بخشوع وبُعْدٍ عن الرياء. إن الله تعالى لا يحب المتجاوزين حدود شرعه، وأعظم التجاوز الشرك بالله، كدعاء غير الله من الأموات والأوثان، ونحو ذلك.');
INSERT INTO Quran__Arabic_Tafseer_Muyassar (QuranChapterID, VerseNumber, Rukuh, Sajdah, Manzil, HizbQuarter, VerseText) VALUES (7, 56, '0', '0', '0', '0', 'ولا تُفْسدوا في الأرض بأيِّ نوع من أنواع الفساد، بعد إصلاح الله إياها ببعثة الرسل -عليهم السلام- وعُمْرانها بطاعة الله، وادعوه -سبحانه- مخلصين له الدعاء؛ خوفًا من عقابه ورجاء لثوابه. إن رحمة الله قريب من المحسنين.');
INSERT INTO Quran__Arabic_Tafseer_Muyassar (QuranChapterID, VerseNumber, Rukuh, Sajdah, Manzil, HizbQuarter, VerseText) VALUES (7, 57, '0', '0', '0', '0', 'والله تعالى هو الذي يرسل الرياح الطيبة اللينة مبشرات بالغيث الذي تثيره بإذن الله، فيستبشر الخلق برحمة الله، حتى إذا حملت الريح السحاب المحمل بالمطر ساقه الله بها لإحياء بلد، قد أجدبت أرضه، ويَبِست أشجاره وزرعه، فأنزل الله به المطر، فأخرج به الكلأ والأشجار والزروع، فعادت أشجاره محملة بأنواع الثمرات. كما نحيي هذا البلد الميت بالمطر نخرج الموتى من قبورهم أحياءً بعد فنائهم؛ لتتعظوا، فتستدلوا على توحيد الله وقدرته على البعث.');
INSERT INTO Quran__Arabic_Tafseer_Muyassar (QuranChapterID, VerseNumber, Rukuh, Sajdah, Manzil, HizbQuarter, VerseText) VALUES (7, 58, '1', '0', '0', '0', 'والأرض النقية إذا نزل عليها المطر تُخْرج نباتًا -بإذن الله ومشيئته- طيبًا ميسرًا، وكذلك المؤمن إذا نزلت عليه آيات الله انتفع بها، وأثمرت فيه حياة صالحة، أما الأرض السَّبِخة الرديئة فإنها لا تُخرج النبات إلا عسرًا رديئا لا نفع فيه، ولا تُخرج نباتًا طيبًا، وكذلك الكافر لا ينتفع بآيات الله. مثل ذلك التنويع البديع في البيان نُنوِّع الحجج والبراهين لإثبات الحق لأناس يشكرون نعم الله، ويطيعونه.');
INSERT INTO Quran__Arabic_Tafseer_Muyassar (QuranChapterID, VerseNumber, Rukuh, Sajdah, Manzil, HizbQuarter, VerseText) VALUES (7, 59, '0', '0', '0', '0', 'لقد بعثنا نوحًا إلى قومه؛ ليدعوهم إلى توحيد الله سبحانه وإخلاص العبادة له، فقال: يا قوم اعبدوا الله وحده، ليس لكم من إله يستحق العبادة غيره جل وعلا فأخلصوا له العبادة فإن لم تفعلوا وبقيتم على عبادة أوثانكم، فإنني أخاف أن يحلَّ عليكم عذاب يوم يعظم فيه بلاؤكم، وهو يوم القيامة.');
INSERT INTO Quran__Arabic_Tafseer_Muyassar (QuranChapterID, VerseNumber, Rukuh, Sajdah, Manzil, HizbQuarter, VerseText) VALUES (7, 60, '0', '0', '0', '0', 'قال له سادتهم وكبراؤهم: إنا لنعتقد -يا نوح- أنك في ضلال بيِّن عن طريق الصواب.');
INSERT INTO Quran__Arabic_Tafseer_Muyassar (QuranChapterID, VerseNumber, Rukuh, Sajdah, Manzil, HizbQuarter, VerseText) VALUES (7, 61, '0', '0', '0', '0', 'قال نوح: يا قوم لست ضالا في مسألة من المسائل بوجه من الوجوه، ولكني رسول من رب العالمين ربي وربكم ورب جميع الخلق.');
INSERT INTO Quran__Arabic_Tafseer_Muyassar (QuranChapterID, VerseNumber, Rukuh, Sajdah, Manzil, HizbQuarter, VerseText) VALUES (7, 62, '0', '0', '0', '0', 'أُبلِّغكم ما أُرسلت به من ربي، وأنصح لكم محذرًا لكم من عذاب الله ومبشرًا بثوابه، وأعلم من شريعته ما لا تعلمون.');
INSERT INTO Quran__Arabic_Tafseer_Muyassar (QuranChapterID, VerseNumber, Rukuh, Sajdah, Manzil, HizbQuarter, VerseText) VALUES (7, 63, '0', '0', '0', '0', 'وهل أثار عجبكم أن أنزل الله تعالى إليكم ما يذكركم بما فيه الخير لكم، على لسان رجل منكم، تعرفون نسبه وصدقه؛ ليخوِّفكم بأس الله تعالى وعقابه، ولتتقوا سخطه بالإيمان به، ورجاء أن تظفروا برحمته وجزيل ثوابه؟');
INSERT INTO Quran__Arabic_Tafseer_Muyassar (QuranChapterID, VerseNumber, Rukuh, Sajdah, Manzil, HizbQuarter, VerseText) VALUES (7, 64, '1', '0', '0', '0', 'فكذبوا نوحًا فأنجيناه ومَن آمن معه في السفينة، وأغرقنا الكفار الذين كذبوا بحججنا الواضحة. إنهم كانوا عُمْيَ القلوب عن رؤية الحق.');
INSERT INTO Quran__Arabic_Tafseer_Muyassar (QuranChapterID, VerseNumber, Rukuh, Sajdah, Manzil, HizbQuarter, VerseText) VALUES (7, 65, '0', '0', '0', '1', 'ولقد أرسلنا إلى قبيلة عاد أخاهم هودا حين عبدوا الأوثان من دون الله، فقال لهم: اعبدوا الله وحده، ليس لكم من إله يستحق العبادة غيره جل وعلا فأخلصوا له العبادة أفلا تتقون عذاب الله وسخطه عليكم؟');
INSERT INTO Quran__Arabic_Tafseer_Muyassar (QuranChapterID, VerseNumber, Rukuh, Sajdah, Manzil, HizbQuarter, VerseText) VALUES (7, 66, '0', '0', '0', '0', 'قال الكبراء الذين كفروا من قوم هود: إنا لنعلم أنك بدعوتك إيانا إلى ترك عبادة آلهتنا وعبادة الله وحده ناقص العقل، وإنا لنعتقد أنك من الكاذبين على الله فيما تقول.');
INSERT INTO Quran__Arabic_Tafseer_Muyassar (QuranChapterID, VerseNumber, Rukuh, Sajdah, Manzil, HizbQuarter, VerseText) VALUES (7, 67, '0', '0', '0', '0', 'قال هود: يا قوم ليس بي نقص في عقلي، ولكني رسول إليكم من رب الخلق أجمعين.');
INSERT INTO Quran__Arabic_Tafseer_Muyassar (QuranChapterID, VerseNumber, Rukuh, Sajdah, Manzil, HizbQuarter, VerseText) VALUES (7, 68, '0', '0', '0', '0', 'أُبلِّغكم ما أرسلني به ربي إليكم، وأنا لكم - فيما دعوتكم إليه من توحيد الله والعمل بشريعته - ناصح، أمين على وحي الله تعالى.');
INSERT INTO Quran__Arabic_Tafseer_Muyassar (QuranChapterID, VerseNumber, Rukuh, Sajdah, Manzil, HizbQuarter, VerseText) VALUES (7, 69, '0', '0', '0', '0', 'وهل أثار عجبكم أن أنزل الله تعالى إليكم ما يذكركم بما فيه الخير لكم، على لسان رجل منكم، تعرفون نسبه وصدقه؛ ليخوِّفكم بأس الله وعقابه؟ واذكروا نعمة الله عليكم إذ جعلكم تخلفون في الأرض مَن قبلكم من بعد ما أهلك قوم نوح، وزاد في أجسامكم قوة وضخامة، فاذكروا نِعَمَ الله الكثيرة عليكم؛ رجاء أن تفوزوا الفوز العظيم في الدنيا والآخرة.');
INSERT INTO Quran__Arabic_Tafseer_Muyassar (QuranChapterID, VerseNumber, Rukuh, Sajdah, Manzil, HizbQuarter, VerseText) VALUES (7, 70, '0', '0', '0', '0', 'قالت عاد لهود عليه السلام: أدعوتنا لعبادة الله وحده وهَجْر عبادة الأصنام التي ورثنا عبادتها عن آبائنا؟ فأتنا بالعذاب الذي تخوفنا به إن كنت من أهل الصدق فيما تقول.');
INSERT INTO Quran__Arabic_Tafseer_Muyassar (QuranChapterID, VerseNumber, Rukuh, Sajdah, Manzil, HizbQuarter, VerseText) VALUES (7, 71, '0', '0', '0', '0', 'قال هود لقومه: قد حلَّ بكم عذاب وغضب من ربكم جل وعلا أتجادلونني في هذه الأصنام التي سميتموها آلهة أنتم وآباؤكم؟ ما نزَّل الله بها من حجة ولا برهان؛ لأنها مخلوقة لا تضر ولا تنفع، وإنما المعبود وحده هو الخالق سبحانه، فانتظروا نزول العذاب عليكم فإني منتظر معكم نزوله، وهذا غاية في التهديد والوعيد.');
INSERT INTO Quran__Arabic_Tafseer_Muyassar (QuranChapterID, VerseNumber, Rukuh, Sajdah, Manzil, HizbQuarter, VerseText) VALUES (7, 72, '1', '0', '0', '0', 'فوقع عذاب الله بإرسال الريح الشديدة عليهم، فأنجى الله هودًا والذين آمنوا معه برحمة عظيمة منه تعالى، وأهلك الكفار من قومه جميعا ودمَّرهم عن آخرهم، وما كانوا مؤمنين لجمعهم بين التكذيب بآيات الله وترك العمل الصالح.');
INSERT INTO Quran__Arabic_Tafseer_Muyassar (QuranChapterID, VerseNumber, Rukuh, Sajdah, Manzil, HizbQuarter, VerseText) VALUES (7, 73, '0', '0', '0', '0', 'ولقد أرسلنا إلى قبيلة ثمود أخاهم صالحًا لـمَّا عبدوا الأوثان من دون الله تعالى. فقال صالح لهم: يا قوم اعبدوا الله وحده؛ ليس لكم من إله يستحق العبادة غيره جل وعلا، فأخلصوا له العبادة، قد جئتكم بالبرهان على صدق ما أدعوكم إليه، إذ دعوتُ الله أمامكم، فأخرج لكم من الصخرة ناقة عظيمة كما سألتم، فاتركوها تأكل في أرض الله من المراعي، ولا تتعرضوا لها بأي أذى، فيصيبكم بسبب ذلك عذاب موجع.');
INSERT INTO Quran__Arabic_Tafseer_Muyassar (QuranChapterID, VerseNumber, Rukuh, Sajdah, Manzil, HizbQuarter, VerseText) VALUES (7, 74, '0', '0', '0', '0', 'واذكروا نعمة الله عليكم، إذ جعلكم تَخْلُفون في الأرض مَن قبلكم، من بعد قبيلة عاد، ومكَّن لكم في الأرض الطيبة تنزلونها، فتبنون في سهولها البيوت العظيمة، وتنحتون من جبالها بيوتًا أخرى، فاذكروا نِعَمَ الله عليكم، ولا تَسْعَوا في الأرض بالإفساد.');
INSERT INTO Quran__Arabic_Tafseer_Muyassar (QuranChapterID, VerseNumber, Rukuh, Sajdah, Manzil, HizbQuarter, VerseText) VALUES (7, 75, '0', '0', '0', '0', 'قال السادة والكبراء من الذين استعلَوا -من قوم صالح- للمؤمنين الذين استضعفوهم، واستهانوا بهم: أتعلمون حقيقة أن صالحًا قد أرسله الله إلينا؟ قال الذين آمنوا: إنا مصدقون بما أرسله الله به، متَّبعون لشرعه.');
INSERT INTO Quran__Arabic_Tafseer_Muyassar (QuranChapterID, VerseNumber, Rukuh, Sajdah, Manzil, HizbQuarter, VerseText) VALUES (7, 76, '0', '0', '0', '0', 'قال الذين استعلَوْا: إنَّا بالذي صدَّقتم به واتبعتموه من نبوة صالح جاحدون.');
INSERT INTO Quran__Arabic_Tafseer_Muyassar (QuranChapterID, VerseNumber, Rukuh, Sajdah, Manzil, HizbQuarter, VerseText) VALUES (7, 77, '0', '0', '0', '0', 'فنحروا الناقة استخفافا منهم بوعيد صالح، واستكبروا عن امتثال أمر ربهم، وقالوا على سبيل الاستهزاء واستبعاد العذاب: يا صالح ائتنا بما تتوعَّدنا به من العذاب، إن كنت مِن رسل الله.');
INSERT INTO Quran__Arabic_Tafseer_Muyassar (QuranChapterID, VerseNumber, Rukuh, Sajdah, Manzil, HizbQuarter, VerseText) VALUES (7, 78, '0', '0', '0', '0', 'فأخذَت الذين كفروا الزلزلةُ الشديدة التي خلعت قلوبهم، فأصبحوا في بلدهم هالكين، لاصقين بالأرض على رُكَبهم ووجوههم، لم يُفْلِت منهم أحد.');
INSERT INTO Quran__Arabic_Tafseer_Muyassar (QuranChapterID, VerseNumber, Rukuh, Sajdah, Manzil, HizbQuarter, VerseText) VALUES (7, 79, '0', '0', '0', '0', 'فأعرض صالح عليه السلام عن قومه -حين عقروا الناقة وحل بهم الهلاك- وقال لهم: يا قوم لقد أبلغتكم ما أمرني ربي بإبلاغه من أمره ونهيه، وبَذَلْت لكم وسعي في الترغيب والترهيب والنصح، ولكنكم لا تحبون الناصحين، فرددتم قولهم، وأطعتم كل شيطان رجيم.');
INSERT INTO Quran__Arabic_Tafseer_Muyassar (QuranChapterID, VerseNumber, Rukuh, Sajdah, Manzil, HizbQuarter, VerseText) VALUES (7, 80, '0', '0', '0', '0', 'واذكر -أيها الرسول- لوطًا عليه السلام حين قال لقومه: أتفعلون الفعلة المنكرة التي بلغت نهاية القبح؟ ما فعلها مِن أحد قبلكم من المخلوقين.');
INSERT INTO Quran__Arabic_Tafseer_Muyassar (QuranChapterID, VerseNumber, Rukuh, Sajdah, Manzil, HizbQuarter, VerseText) VALUES (7, 81, '0', '0', '0', '0', 'إنكم لتأتون الذكور في أدبارهم، شهوة منكم لذلك، غير مبالين بقبحها، تاركين الذي أحلَّه الله لكم من نسائكم، بل أنتم قوم متجاوزون لحدود الله في الإسراف. إن إتيان الذكور دون الإناث من الفواحش التي ابتدعها قوم لوط، ولم يسبقهم بها أحد من الخلق.');
INSERT INTO Quran__Arabic_Tafseer_Muyassar (QuranChapterID, VerseNumber, Rukuh, Sajdah, Manzil, HizbQuarter, VerseText) VALUES (7, 82, '0', '0', '0', '0', 'وما كان جواب قوم لوط حين أنكر عليهم فعلهم الشنيع إلا أن قال بعضهم لبعض: أخرجوا لوطًا وأهله من بلادكم، إنه ومن تبعه أناس يتنزهون عن إتيان أدبار الرجال والنساء.');
INSERT INTO Quran__Arabic_Tafseer_Muyassar (QuranChapterID, VerseNumber, Rukuh, Sajdah, Manzil, HizbQuarter, VerseText) VALUES (7, 83, '0', '0', '0', '0', 'فأنجى الله لوطًا وأهله من العذاب حيث أمره بمغادرة ذلك البلد، إلا امرأته، فإنها كانت من الهالكين الباقين في عذاب الله.');
INSERT INTO Quran__Arabic_Tafseer_Muyassar (QuranChapterID, VerseNumber, Rukuh, Sajdah, Manzil, HizbQuarter, VerseText) VALUES (7, 84, '1', '0', '0', '0', 'وعذَّب الله الكفار من قوم لوط بأن أنزل عليهم مطرًا من الحجارة، وقلب بلادهم، فجعل عاليها سافلها، فانظر -أيها الرسول- كيف صارت عاقبة الذين اجترؤوا على معاصي الله وكذبوا رسله.');
INSERT INTO Quran__Arabic_Tafseer_Muyassar (QuranChapterID, VerseNumber, Rukuh, Sajdah, Manzil, HizbQuarter, VerseText) VALUES (7, 85, '0', '0', '0', '0', 'ولقد أرسلنا إلى قبيلة "مدين" أخاهم شعيبًا عليه السلام، فقال لهم: يا قوم اعبدوا الله وحده لا شريك له؛ ليس لكم مِن إله يستحق العبادة غيره جل وعلا فأخلصوا له العبادة، قد جاءكم برهان من ربكم على صِدْق ما أدعوكم إليه، فأدوا للناس حقوقهم بإيفاء الكيل والميزان، ولا تنقصوهم حقوقهم فتظلموهم، ولا تفسدوا في الأرض -بالكفر والظلم- بعد إصلاحها بشرائع الأنبياء السابقين عليهم السلام. ذلك الذي دعوتكم إليه خير لكم في دنياكم وأخراكم، إن كنتم مصدقيَّ فيما دعوتكم إليه، عاملين بشرع الله.');
INSERT INTO Quran__Arabic_Tafseer_Muyassar (QuranChapterID, VerseNumber, Rukuh, Sajdah, Manzil, HizbQuarter, VerseText) VALUES (7, 86, '0', '0', '0', '0', 'ولا تقعدوا بكل طريق تتوعدون الناس بالقتل، إن لم يعطوكم أموالهم، وتصدُّون عن سبيل الله القويم من صدَّق به عز وجل، وعمل صالحًا، وتبغون سبيل الله أن تكون معوجة، وتميلونها اتباعًا لأهوائكم، وتنفِّرون الناس عن اتباعها. واذكروا نعمة الله تعالى عليكم إذ كان عددكم قليلا فكثَّركم، فأصبحتم أقوياء عزيزين، وانظروا كيف كان عاقبة المفسدين في الأرض، وما حلَّ بهم من الهلاك والدمار؟');
INSERT INTO Quran__Arabic_Tafseer_Muyassar (QuranChapterID, VerseNumber, Rukuh, Sajdah, Manzil, HizbQuarter, VerseText) VALUES (7, 87, '0', '0', '0', '0', 'وإن كان جماعة منكم صدَّقوا بالذي أرسلني الله به، وجماعة لم يصدِّقوا بذلك، فانتظروا أيها المكذبون قضاء الله الفاصل بيننا وبينكم حين يحلُّ عليكم عذابه الذي أنذرتكم به. والله -جلَّ وعلا- هو خير الحاكمين بين عباده.');
INSERT INTO Quran__Arabic_Tafseer_Muyassar (QuranChapterID, VerseNumber, Rukuh, Sajdah, Manzil, HizbQuarter, VerseText) VALUES (7, 88, '0', '0', '0', '1', 'قال السادة والكبراء من قوم شعيب الذين تكبروا عن الإيمان بالله واتباع رسوله شعيب عليه السلام: لنخرجنك يا شعيب ومَن معك من المؤمنين من ديارنا، إلا إذا صرتم إلى ديننا، قال شُعيب منكرًا ومتعجبًا من قولهم: أنتابعكم على دينكم ومِلَّتكم الباطلة، ولو كنا كارهين لها لعِلْمِنا ببطلانها؟');
INSERT INTO Quran__Arabic_Tafseer_Muyassar (QuranChapterID, VerseNumber, Rukuh, Sajdah, Manzil, HizbQuarter, VerseText) VALUES (7, 89, '0', '0', '0', '0', 'وقال شعيب لقومه مستدركًا: قد اختلقنا على الله الكذب إن عُدْنا إلى دينكم بعد أن أنقذنا الله منه، وليس لنا أن نتحول إلى غير دين ربنا إلا أن يشاء الله ربنا، وقد وسع ربنا كل شيء علمًا، فيعلم ما يصلح للعباد، على الله وحده اعتمادنا هداية ونصرة، ربنا احكم بيننا وبين قومنا بالحق، وأنت خير الحاكمين.');
INSERT INTO Quran__Arabic_Tafseer_Muyassar (QuranChapterID, VerseNumber, Rukuh, Sajdah, Manzil, HizbQuarter, VerseText) VALUES (7, 90, '0', '0', '0', '0', 'وقال السادة والكبراء المكذبون الرافضون لدعوة التوحيد إمعانًا في العتوِّ والتمرد، محذرين من اتباع شعيب: لئن اتبعتم شعيبًا إنكم إذًا لهالكون.');
INSERT INTO Quran__Arabic_Tafseer_Muyassar (QuranChapterID, VerseNumber, Rukuh, Sajdah, Manzil, HizbQuarter, VerseText) VALUES (7, 91, '0', '0', '0', '0', 'فأخذَتْ قومَ شعيب الزلزلةُ الشديدة، فأصبحوا في دارهم صرعى ميتين.');
INSERT INTO Quran__Arabic_Tafseer_Muyassar (QuranChapterID, VerseNumber, Rukuh, Sajdah, Manzil, HizbQuarter, VerseText) VALUES (7, 92, '0', '0', '0', '0', 'الذين كذَّبوا شعيبًا كأنهم لم يقيموا في ديارهم، ولم يتمتعوا فيها، حيث استؤصلوا، فلم يبق لهم أثر، وأصابهم الخسران والهلاك في الدنيا والآخرة.');
INSERT INTO Quran__Arabic_Tafseer_Muyassar (QuranChapterID, VerseNumber, Rukuh, Sajdah, Manzil, HizbQuarter, VerseText) VALUES (7, 93, '1', '0', '0', '0', 'فأعرض شعيب عنهم حينما أيقن بحلول العذاب بهم، وقال: يا قوم لقد أبلغتكم رسالات ربي، ونصحت لكم بالدخول في دين الله والإقلاع عما أنتم عليه، فلم تسمعوا ولم تطيعوا، فكيف أحزن على قوم جحدوا وحدانية الله وكذبوا رسله؟');
INSERT INTO Quran__Arabic_Tafseer_Muyassar (QuranChapterID, VerseNumber, Rukuh, Sajdah, Manzil, HizbQuarter, VerseText) VALUES (7, 94, '0', '0', '0', '0', 'وما أرسلنا في قرية من نبي يدعوهم إلى عبادة الله، وينهاهم عمَّا هم فيه من الشرك، فكذَّبه قومه، إلا ابتليناهم بالبأساء والضراء، فأصبناهم في أبدانهم بالأمراض والأسقام، وفي أموالهم بالفقر والحاجة؛ رجاء أن يستكينوا، وينيبوا إلى الله، ويرجعوا إلى الحق.');
INSERT INTO Quran__Arabic_Tafseer_Muyassar (QuranChapterID, VerseNumber, Rukuh, Sajdah, Manzil, HizbQuarter, VerseText) VALUES (7, 95, '0', '0', '0', '0', 'ثم بدَّلنا الحالة الطيبة الأولى مكان الحالة السيئة، فأصبحوا في عافية في أبدانهم، وسَعَة ورخاء في أموالهم؛ إمهالا لهم، ولعلهم يشكرون، فلم يُفِد معهم كل ذلك، ولم يعتبروا ولم ينتهوا عمَّا هم فيه، وقالوا: هذه عادة الدهر في أهله، يوم خير ويوم شر، وهو ما جرى لآبائنا من قبل، فأخذناهم بالعذاب فجأة وهم آمنون، لا يخطر لهم الهلاك على بال.');
INSERT INTO Quran__Arabic_Tafseer_Muyassar (QuranChapterID, VerseNumber, Rukuh, Sajdah, Manzil, HizbQuarter, VerseText) VALUES (7, 96, '0', '0', '0', '0', 'ولو أنَّ أهل القرى صدَّقوا رسلهم واتبعوهم واجتنبوا ما نهاهم الله عنه، لفتح الله لهم أبواب الخير من كلِّ وجه، ولكنهم كذَّبوا، فعاقبهم الله بالعذاب المهلك بسبب كفرهم ومعاصيهم.');
INSERT INTO Quran__Arabic_Tafseer_Muyassar (QuranChapterID, VerseNumber, Rukuh, Sajdah, Manzil, HizbQuarter, VerseText) VALUES (7, 97, '0', '0', '0', '0', 'أيظن أهل القرى أنهم في منجاة ومأمن من عذاب الله، أن يأتيهم ليلا وهم نائمون؟');
INSERT INTO Quran__Arabic_Tafseer_Muyassar (QuranChapterID, VerseNumber, Rukuh, Sajdah, Manzil, HizbQuarter, VerseText) VALUES (7, 98, '0', '0', '0', '0', 'أوَأمن أهل القرى أن يأتيهم عذاب الله وقت الضحى، وهم غافلون متشاغلون بأمور دنياهم؟ وخصَّ الله هذين الوقتين بالذكر، لأن الإنسان يكون أغْفَل ما يكون فيهما، فمجيء العذاب فيهما أفظع وأشد.');
INSERT INTO Quran__Arabic_Tafseer_Muyassar (QuranChapterID, VerseNumber, Rukuh, Sajdah, Manzil, HizbQuarter, VerseText) VALUES (7, 99, '1', '0', '0', '0', 'أفأمن أهل القرى المكذبة مَكْرَ الله وإمهاله لهم؛ استدراجًا لهم بما أنعم عليهم في دنياهم عقوبة لمكرهم؟ فلا يأمن مكر الله إلا القوم الهالكون.');
INSERT INTO Quran__Arabic_Tafseer_Muyassar (QuranChapterID, VerseNumber, Rukuh, Sajdah, Manzil, HizbQuarter, VerseText) VALUES (7, 100, '0', '0', '0', '0', 'أوَلم يتبين للذين سكنوا الأرض من بعد إهلاك أهلها السابقين بسبب معاصيهم، فساروا سيرتهم، أن لو نشاء أصبناهم بسبب ذنوبهم كما فعلنا بأسلافهم، ونختم على قلوبهم، فلا يدخلها الحق، ولا يسمعون موعظة ولا تذكيرًا؟');
INSERT INTO Quran__Arabic_Tafseer_Muyassar (QuranChapterID, VerseNumber, Rukuh, Sajdah, Manzil, HizbQuarter, VerseText) VALUES (7, 101, '0', '0', '0', '0', 'تلك القرى التي تَقَدَّم ذِكْرُها، وهي قرى قوم نوح وهود وصالح ولوط وشعيب، نقصُّ عليك -أيها الرسول- من أخبارها، وما كان من أَمْر رسل الله التي أرسلت إليهم، ما يحصل به عبرة للمعتبرين وازدجار للظالمين. ولقد جاءت أهلَ القرى رسلنا بالحجج البينات على صدقهم، فما كانوا ليؤمنوا بما جاءتهم به الرسل؛ بسبب طغيانهم وتكذيبهم بالحق، ومثل خَتْمِ الله على قلوب هؤلاء الكافرين المذكورين يختم الله على قلوب الكافرين بمحمد صلى الله عليه وسلم.');
INSERT INTO Quran__Arabic_Tafseer_Muyassar (QuranChapterID, VerseNumber, Rukuh, Sajdah, Manzil, HizbQuarter, VerseText) VALUES (7, 102, '0', '0', '0', '0', 'وما وَجَدْنا لأكثر الأم الماضية من أمانة ولا وفاء بالعهد، وما وجدنا أكثرهم إلا فسقة عن طاعة الله وامتثال أمره.');
INSERT INTO Quran__Arabic_Tafseer_Muyassar (QuranChapterID, VerseNumber, Rukuh, Sajdah, Manzil, HizbQuarter, VerseText) VALUES (7, 103, '0', '0', '0', '0', 'ثم بعثنا من بعد الرسل المتقدم ذِكْرهم موسى بن عمران بمعجزاتنا البينة إلى فرعون وقومه، فجحدوا وكفروا بها ظلمًا منهم وعنادًا، فانظر -أيها الرسول- متبصرًا كيف فعلنا بهم وأغرقناهم عن آخرهم بمرأى من موسى وقومه؟ وتلك نهاية المفسدين.');
INSERT INTO Quran__Arabic_Tafseer_Muyassar (QuranChapterID, VerseNumber, Rukuh, Sajdah, Manzil, HizbQuarter, VerseText) VALUES (7, 104, '0', '0', '0', '0', 'وقال موسى لفرعون محاورًا مبلِّغًا: إني رسولٌ من الله خالق الخلق أجمعين، ومدبِّر أحوالهم ومآلهم.');
INSERT INTO Quran__Arabic_Tafseer_Muyassar (QuranChapterID, VerseNumber, Rukuh, Sajdah, Manzil, HizbQuarter, VerseText) VALUES (7, 105, '0', '0', '0', '0', 'جدير بأن لا أقول على الله إلا الحق، وحريٌّ بي أن ألتزمه، قد جئتكم ببرهان وحجة باهرة من ربكم على صِدْق ما أذكره لكم، فأطلق -يا فرعون- معي بني إسرائيل مِن أَسْرك وقَهْرك، وخلِّ سبيلهم لعبادة الله.');
INSERT INTO Quran__Arabic_Tafseer_Muyassar (QuranChapterID, VerseNumber, Rukuh, Sajdah, Manzil, HizbQuarter, VerseText) VALUES (7, 106, '0', '0', '0', '0', 'قال فرعرن لموسى: إن كنتَ جئتَ بآية حسب زعمك فأتني بها، وأحضرها عندي؛ لتصحَّ دعواك ويثبت صدقك، إن كنت صادقًا فيما ادَّعيت أنك رسول رب العالمين.');
INSERT INTO Quran__Arabic_Tafseer_Muyassar (QuranChapterID, VerseNumber, Rukuh, Sajdah, Manzil, HizbQuarter, VerseText) VALUES (7, 107, '0', '0', '0', '0', 'فألقى موسى عصاه، فتحولت حيَّة عظيمة ظاهرة للعيان.');
INSERT INTO Quran__Arabic_Tafseer_Muyassar (QuranChapterID, VerseNumber, Rukuh, Sajdah, Manzil, HizbQuarter, VerseText) VALUES (7, 108, '1', '0', '0', '0', 'وجذب يده من جيبه أو من جناحه فإذا هي بيضاء كاللبن من غير برص آية لفرعون، فإذا ردَّها عادت إلى لونها الأول، كسائر بدنه.');
INSERT INTO Quran__Arabic_Tafseer_Muyassar (QuranChapterID, VerseNumber, Rukuh, Sajdah, Manzil, HizbQuarter, VerseText) VALUES (7, 109, '0', '0', '0', '0', 'قال الأشراف من قوم فرعون: إن موسى لساحر يأخذ بأعين الناس بخداعه إياهم، حتى يخيل إليهم أن العصا حية، والشيء بخلاف ما هو عليه، وهو واسع العلم بالسحر ماهر به.');
INSERT INTO Quran__Arabic_Tafseer_Muyassar (QuranChapterID, VerseNumber, Rukuh, Sajdah, Manzil, HizbQuarter, VerseText) VALUES (7, 110, '0', '0', '0', '0', 'يريد أن يخرجكم جميعًا من أرضكم، قال فرعون: فبماذا تشيرون عليَّ أيها الملأ في أمر موسى؟');
INSERT INTO Quran__Arabic_Tafseer_Muyassar (QuranChapterID, VerseNumber, Rukuh, Sajdah, Manzil, HizbQuarter, VerseText) VALUES (7, 111, '0', '0', '0', '0', 'قال مَن حضر مناظرة موسى مِن سادة قوم فرعون وكبرائهم: أَخِّر موسى وأخاه هارون، وابعث في مدائن "مصر" وأقاليمها الشُّرَط.');
INSERT INTO Quran__Arabic_Tafseer_Muyassar (QuranChapterID, VerseNumber, Rukuh, Sajdah, Manzil, HizbQuarter, VerseText) VALUES (7, 112, '0', '0', '0', '0', 'ليجمعوا لك كل ساحر واسع العلم بالسحر.');
INSERT INTO Quran__Arabic_Tafseer_Muyassar (QuranChapterID, VerseNumber, Rukuh, Sajdah, Manzil, HizbQuarter, VerseText) VALUES (7, 113, '0', '0', '0', '0', 'وجاء السحرة فرعون قالوا: أئنَّ لنا لجائزة ومالا إن غَلَبْنا موسى؟');
INSERT INTO Quran__Arabic_Tafseer_Muyassar (QuranChapterID, VerseNumber, Rukuh, Sajdah, Manzil, HizbQuarter, VerseText) VALUES (7, 114, '0', '0', '0', '0', 'قال فرعون: نعم لكم الأجر والقرب مني إن غَلَبْتُموه.');
INSERT INTO Quran__Arabic_Tafseer_Muyassar (QuranChapterID, VerseNumber, Rukuh, Sajdah, Manzil, HizbQuarter, VerseText) VALUES (7, 115, '0', '0', '0', '0', 'قال سحرة فرعون لموسى على سبيل التكبر وعدم المبالاة: يا موسى اختر أن تُلقي عصاك أولا أو نُلقي نحن أولا.');
INSERT INTO Quran__Arabic_Tafseer_Muyassar (QuranChapterID, VerseNumber, Rukuh, Sajdah, Manzil, HizbQuarter, VerseText) VALUES (7, 116, '0', '0', '0', '0', 'قال موسى للسحرة: ألقوا أنتم، فلما ألقَوا الحبال والعصيَّ سحروا أعين الناس، فخُيِّل إلى الأبصار أن ما فعلوه حقيقة، ولم يكن إلا مجرد صنعة وخيال، وأرهبوا الناس إرهابًا شديدًا، وجاؤوا بسحر قوي كثير.');
INSERT INTO Quran__Arabic_Tafseer_Muyassar (QuranChapterID, VerseNumber, Rukuh, Sajdah, Manzil, HizbQuarter, VerseText) VALUES (7, 117, '0', '0', '0', '1', 'وأوحى الله إلى عبده ورسوله موسى عليه السلام في ذلك الموقف العظيم الذي فرَّق الله فيه بين الحق والباطل، يأمره بأن يُلقي ما في يمينه وهي عصاه، فألقاها فإذا هي تبلع ما يلقونه، ويوهمون الناس أنه حق وهو باطل.');
INSERT INTO Quran__Arabic_Tafseer_Muyassar (QuranChapterID, VerseNumber, Rukuh, Sajdah, Manzil, HizbQuarter, VerseText) VALUES (7, 118, '0', '0', '0', '0', 'فظهر الحق واستبان لمن شهده وحضره في أمر موسى عليه السلام، وأنه رسول الله يدعو إلى الحق، وبطل الكذب الذي كانوا يعملونه.');
INSERT INTO Quran__Arabic_Tafseer_Muyassar (QuranChapterID, VerseNumber, Rukuh, Sajdah, Manzil, HizbQuarter, VerseText) VALUES (7, 119, '0', '0', '0', '0', 'فغُلِبَ جميع السحرة في مكان اجتماعهم، وانصرف فرعون وقومه أذلاء مقهورين مغلوبين.');
INSERT INTO Quran__Arabic_Tafseer_Muyassar (QuranChapterID, VerseNumber, Rukuh, Sajdah, Manzil, HizbQuarter, VerseText) VALUES (7, 120, '0', '0', '0', '0', 'وخَرَّ السحرة سُجَّدًا على وجوههم لله رب العالمين لِمَا عاينوا من عظيم قدرة الله.');
INSERT INTO Quran__Arabic_Tafseer_Muyassar (QuranChapterID, VerseNumber, Rukuh, Sajdah, Manzil, HizbQuarter, VerseText) VALUES (7, 121, '0', '0', '0', '0', 'قالوا: آمنا برب العالمين.');
INSERT INTO Quran__Arabic_Tafseer_Muyassar (QuranChapterID, VerseNumber, Rukuh, Sajdah, Manzil, HizbQuarter, VerseText) VALUES (7, 122, '0', '0', '0', '0', 'وهو رب موسى وهارون، وهو الذي يجب أن تصرف له العبادة وحده دون مَن سواه.');
INSERT INTO Quran__Arabic_Tafseer_Muyassar (QuranChapterID, VerseNumber, Rukuh, Sajdah, Manzil, HizbQuarter, VerseText) VALUES (7, 123, '0', '0', '0', '0', 'قال فرعون للسحرة: آمنتم بالله قبل أن آذن لكم بالإيمان به؟ إن إيمانكم بالله وتصديقكم لموسى وإقراركم بنبوته لحيلة احتلتموها أنتم وموسى؛ لتخرجوا أهل مدينتكم منها، وتكونوا المستأثرين بخيراتها، فسوف تعلمون -أيها السحرة- ما يحلُّ بكم من العذاب والنكال.');
INSERT INTO Quran__Arabic_Tafseer_Muyassar (QuranChapterID, VerseNumber, Rukuh, Sajdah, Manzil, HizbQuarter, VerseText) VALUES (7, 124, '0', '0', '0', '0', 'لأقطعنَّ أيديكم وأرجلكم -أيها السحرة- من خلاف: بقطع اليد اليمنى والرجل اليسرى، أو اليد اليسرى والرجل اليمنى، ثم لأعلقنَّكم جميعًا على جذوع النخل؛ تنكيلا بكم وإرهابًا للناس.');
INSERT INTO Quran__Arabic_Tafseer_Muyassar (QuranChapterID, VerseNumber, Rukuh, Sajdah, Manzil, HizbQuarter, VerseText) VALUES (7, 125, '0', '0', '0', '0', 'قال السحرة لفرعون: قد تحققنا أنَّا إلى الله راجعون، وأن عذابه أشد من عذابك، فلنصبرنَّ اليوم على عذابك؛ لِننجو من عذاب الله يوم القيامة.');
INSERT INTO Quran__Arabic_Tafseer_Muyassar (QuranChapterID, VerseNumber, Rukuh, Sajdah, Manzil, HizbQuarter, VerseText) VALUES (7, 126, '1', '0', '0', '0', 'ولستَ تعيب منا وتنكر -يا فرعون- إلا إيماننا وتصديقنا بحجج ربنا وأدلته التي جاء بها موسى ولا تقدر على مثلها أنت ولا أحد آخر سوى الله الذي له ملك السموات والأرض، ربنا أَفِضْ علينا صبرًا عظيمًا وثباتا عليه، وتوفَّنا منقادين لأمرك متبعين رسولك.');
INSERT INTO Quran__Arabic_Tafseer_Muyassar (QuranChapterID, VerseNumber, Rukuh, Sajdah, Manzil, HizbQuarter, VerseText) VALUES (7, 127, '0', '0', '0', '0', 'وقال السادة والكبراء من قوم فرعون لفرعون: أَتَدَعُ موسى وقومه من بني إسرائيل ليفسدوا الناس في أرض "مصر" بتغيير دينهم بعبادة الله وحده لا شريك له، وترك عبادتك وعبادة آلهتك؟ قال فرعون: سنُقَتِّل أبناء بني إسرائيل ونستبقي نساءهم أحياء للخدمة، وإنَّا عالون عليهم بقهر المُلْكِ والسلطان.');
INSERT INTO Quran__Arabic_Tafseer_Muyassar (QuranChapterID, VerseNumber, Rukuh, Sajdah, Manzil, HizbQuarter, VerseText) VALUES (7, 128, '0', '0', '0', '0', 'قال موسى لقومه -من بني إسرائيل-: استعينوا بالله على فرعون وقومه، واصبروا على ما نالكم من فرعون من المكاره في أنفسكم وأبنائكم. إن الأرض كلها لله يورثها من يشاء من عباده، والعاقبة المحمودة لمن اتقى الله ففعل أوامره واجتنب نواهيه.');
INSERT INTO Quran__Arabic_Tafseer_Muyassar (QuranChapterID, VerseNumber, Rukuh, Sajdah, Manzil, HizbQuarter, VerseText) VALUES (7, 129, '1', '0', '0', '0', 'قال قوم موسى -من بني إسرائيل- لنبيهم موسى: ابتُلينا وأُوذينا بذبح أبنائنا واستحياء نسائنا على يد فرعون وقومه، من قبل أن تأتينا، ومن بعد ما جئتنا، قال موسى لهم: لعل ربكم أن يهلك عدوكم فرعون وقومه، ويستخلفكم في أرضهم بعد هلاكهم، فينظر كيف تعملون، هل تشكرون أو تكفرون؟');
INSERT INTO Quran__Arabic_Tafseer_Muyassar (QuranChapterID, VerseNumber, Rukuh, Sajdah, Manzil, HizbQuarter, VerseText) VALUES (7, 130, '0', '0', '0', '0', 'ولقد ابتلينا فرعون وقومه بالقحط والجدب، ونَقْص ثمارهم وغَلاتهم؛ ليتذكروا، وينزجروا عن ضلالاتهم، ويفزعوا إلى ربهم بالتوبة.');
INSERT INTO Quran__Arabic_Tafseer_Muyassar (QuranChapterID, VerseNumber, Rukuh, Sajdah, Manzil, HizbQuarter, VerseText) VALUES (7, 131, '0', '0', '0', '0', 'فإذا جاء فرعونَ وقومَه الخِصْبُ والرزقُ قالوا: هذا لنا بما نستحقه، وإن يُصِبْهم جدب وقحط يتشاءموا، ويقولوا: هذا بسبب موسى ومَن معه. ألا إنَّ ما يصيبهم من الجدب والقحط إنما هو بقضاء الله وقدره، وبسبب ذنوبهم وكفرهم، ولكن أكثر قوم فرعون لا يعلمون ذلك؛ لانغمارهم في الجهل والضلال.');
INSERT INTO Quran__Arabic_Tafseer_Muyassar (QuranChapterID, VerseNumber, Rukuh, Sajdah, Manzil, HizbQuarter, VerseText) VALUES (7, 132, '0', '0', '0', '0', 'وقال قوم فرعون لموسى: أي آية تأتِنا بها، ودلالة وحجة أقمتها لتصرفنا عما نحن عليه من دين فرعون، فما نحن لك بمصدِّقين.');
INSERT INTO Quran__Arabic_Tafseer_Muyassar (QuranChapterID, VerseNumber, Rukuh, Sajdah, Manzil, HizbQuarter, VerseText) VALUES (7, 133, '0', '0', '0', '0', 'فأرسلنا عليهم سيلا جارفًا أغرق الزروع والثمار، وأرسلنا الجراد، فأكل زروعهم وثمارهم وأبوابهم وسقوفهم وثيابهم، وأرسلنا القُمَّل الذي يفسد الثمار ويقضي على الحيوان والنبات، وأرسلنا الضفادع فملأت آنيتهم وأطعمتهم ومضاجعهم، وأرسلنا أيضًا الدم فصارت أنهارهم وآبارهم دمًا، ولم يجدوا ماء صالحًا للشرب، هذه آيات من آيات الله لا يقدر عليها غيره، مفرقات بعضها عن بعض، ومع كل هذا ترفَّع قوم فرعون، فاستكبروا عن الإيمان بالله، وكانوا قومًا يعملون بما ينهى الله عنه من المعاصي والفسق عتوًّا وتمردًا.');
INSERT INTO Quran__Arabic_Tafseer_Muyassar (QuranChapterID, VerseNumber, Rukuh, Sajdah, Manzil, HizbQuarter, VerseText) VALUES (7, 134, '0', '0', '0', '0', 'ولما نزل العذاب على فرعون وقومه فزعوا إلى موسى وقالوا: يا موسى ادع لنا ربك بما أوحى به إليك مِن رَفْع العذاب بالتوبة، لئن رفعت عنا العذاب الذي نحن فيه لنصدِّقنَّ بما جئت به، ونتبع ما دعوت إليه، ولنطلقنَّ معك بني إسرائيل، فلا نمنعهم من أن يذهبوا حيث شاؤوا.');
INSERT INTO Quran__Arabic_Tafseer_Muyassar (QuranChapterID, VerseNumber, Rukuh, Sajdah, Manzil, HizbQuarter, VerseText) VALUES (7, 135, '0', '0', '0', '0', 'فلما رفع الله عنهم العذاب الذى أنزله بهم إلى أجلٍ هم بالغوه لا محالة فيعذبون فيه، لا ينفعهم ما تقدَّم لهم من الإمهال وكَشْفِ العذاب إلى حلوله، إذا هم ينقضون عهودهم التي عاهدوا عليها ربهم وموسى، ويقيمون على كفرهم وضلالهم.');
INSERT INTO Quran__Arabic_Tafseer_Muyassar (QuranChapterID, VerseNumber, Rukuh, Sajdah, Manzil, HizbQuarter, VerseText) VALUES (7, 136, '0', '0', '0', '0', 'فانتقمنا منهم حين جاء الأجل المحدد لإهلاكهم، وذلك بإحلال نقمتنا عليهم، وهي إغراقهم في البحر؛ بسبب تكذيبهم بالمعجزات التي ظهرت على يد موسى، وكانوا عن هذه المعجزات غافلين، وتلك الغفلة هي سبب التكذيب.');
INSERT INTO Quran__Arabic_Tafseer_Muyassar (QuranChapterID, VerseNumber, Rukuh, Sajdah, Manzil, HizbQuarter, VerseText) VALUES (7, 137, '0', '0', '0', '0', 'وأررثنا بني إسرائيل الذين كانوا يُستَذَلُّون للخدمة، مشارق الأرض ومغاربها (وهي بلاد "الشام") التي باركنا فيها، بإخراج الزروع والثمار والأنهار، وتمت كلمة ربك -أيها الرسول- الحسنى على بني إسرائيل بالتمكين لهم في الأرض؛ بسبب صبرهم على أذى فرعون وقومه، ودمَّرنا ما كان يصنع فرعون وقومه من العمارات والمزارع، وما كانوا يبنون من الأبنية والقصور وغير ذلك.');
INSERT INTO Quran__Arabic_Tafseer_Muyassar (QuranChapterID, VerseNumber, Rukuh, Sajdah, Manzil, HizbQuarter, VerseText) VALUES (7, 138, '0', '0', '0', '0', 'وقطعنا ببني إسرائيل البحر، فمرُّوا على قوم يقيمون ويواظبون على عبادة أصنام لهم، قال بنو إسرائيل: اجعل لنا يا موسى صنمًا نعبده ونتخذه إلهًا، كما لهؤلاء القوم أصنام يعبدونها، قال موسى لهم: إنكم أيها القوم تجهلون عظمة الله، ولا تعلمون أن العبادة لا تنبغي إلا لله الواحد القهار.');
INSERT INTO Quran__Arabic_Tafseer_Muyassar (QuranChapterID, VerseNumber, Rukuh, Sajdah, Manzil, HizbQuarter, VerseText) VALUES (7, 139, '0', '0', '0', '0', 'إن هؤلاء المقيمين على هذه الأصنام مُهْلَك ما هم فيه من الشرك، ومدمَّر وباطل ما كانوا يعملون من عبادتهم لتلك الأصنام، التي لا تدفع عنهم عذاب الله إذا نزل بهم.');
INSERT INTO Quran__Arabic_Tafseer_Muyassar (QuranChapterID, VerseNumber, Rukuh, Sajdah, Manzil, HizbQuarter, VerseText) VALUES (7, 140, '0', '0', '0', '0', 'قال موسى لقومه: أغير الله أطلب لكم معبودًا تعبدونه من دونه، والله هو الذي خلقكم، وفضَّلكم على عالمي زمانكم بكثرة الأنبياء فيكم، وإهلاك عدوكم وما خصَّكم به من الآيات؟');
INSERT INTO Quran__Arabic_Tafseer_Muyassar (QuranChapterID, VerseNumber, Rukuh, Sajdah, Manzil, HizbQuarter, VerseText) VALUES (7, 141, '1', '0', '0', '0', 'واذكروا - يا بني إسرائيل - نِعَمنا عليكم إذ أنقذناكم من أَسْر فرعون وآله، وما كنتم فيه من الهوان والذلة من تذبيح أبنائكم واستبقاء نسائكم للخدمة، وفي حَمْلِكم على أقبح العذاب وأسوئه، ثم إنجائكم، اختبار من الله لكم ونعمة عظيمة.');
INSERT INTO Quran__Arabic_Tafseer_Muyassar (QuranChapterID, VerseNumber, Rukuh, Sajdah, Manzil, HizbQuarter, VerseText) VALUES (7, 142, '0', '0', '0', '1', 'وواعد الله سبحانه وتعالى موسى لمناجاة ربه ثلاثين ليلة، ثم زاده في الأجل بعد ذلك عشر ليال، فتمَّ ما وَقَّتَه الله لموسى لتكليمه أربعين ليلة. وقال موسى لأخيه هارون -حين أراد المضيَّ لمناجاة ربه-: كن خليفتي في قومي حتى أرجع، وأحمِلَهم على طاعة الله وعبادته، ولا تسلكْ طريق الذين يفسدون في الأرض.');
INSERT INTO Quran__Arabic_Tafseer_Muyassar (QuranChapterID, VerseNumber, Rukuh, Sajdah, Manzil, HizbQuarter, VerseText) VALUES (7, 143, '0', '0', '0', '0', 'ولما جاء موسى في الوقت المحدد وهو تمام أربعين ليلة، وكلَّمه ربه بما كلَّمه من وحيه وأمره ونهيه، طمع في رؤية الله فطلب النظر إليه، قال الله له: لن تراني، أي لن تقدر على رؤيتي في الدنيا، ولكن انظر إلى الجبل، فإن استقر مكانه إذا تجلَّيتُ له فسوف تراني، فلما تجلَّى ربه للجبل جعله دكًّا مستويًا بالأرض، وسقط موسى مغشيًّا عليه، فلما أفاق من غشيته قال: تنزيهًا لك يا رب عما لا يليق بجلالك، إني تبت إليك من مسألتي إياك الرؤية في هذه الحياة الدنيا، وأنا أول المؤمنين بك من قومي.');
INSERT INTO Quran__Arabic_Tafseer_Muyassar (QuranChapterID, VerseNumber, Rukuh, Sajdah, Manzil, HizbQuarter, VerseText) VALUES (7, 144, '0', '0', '0', '0', 'قال الله يا موسى: إني اخترتك على الناس برسالاتي إلى خلقي الذين أرسلتك إليهم وبكلامي إياك مِن غير وساطة، فخذ ما أعطيتك مِن أمري ونهيي، وتمسَّك به، واعمل به، وكن من الشاكرين لله تعالى على ما آتاك من رسالته، وخصَّك بكلامه.');
INSERT INTO Quran__Arabic_Tafseer_Muyassar (QuranChapterID, VerseNumber, Rukuh, Sajdah, Manzil, HizbQuarter, VerseText) VALUES (7, 145, '0', '0', '0', '0', 'وكتبنا لموسى في التوراة من كل ما يحتاج إليه في دينه من الأحكام، موعظة للازدجار والاعتبار وتفصيلا لتكاليف الحلال والحرام والأمر والنهي والقصص والعقائد والأخبار والمغيبات، قال الله له: فخذها بقوة، أي: خذ التوراة بجد واجتهاد، وأمر قومك يعملوا بما شرع الله فيها؛ فإن مَن أشرك منهم ومِن غيرهم فإني سأريه في الآخرة دار الفاسقين، وهي نار الله التي أعدَّها لأعدائه الخارجين عن طاعته.');
INSERT INTO Quran__Arabic_Tafseer_Muyassar (QuranChapterID, VerseNumber, Rukuh, Sajdah, Manzil, HizbQuarter, VerseText) VALUES (7, 146, '0', '0', '0', '0', 'سأصرف عن فَهْم الحجج والأدلة الدالة على عظمتي وشريعتي وأحكامي قلوب المتكبرين عن طاعتي، والمتكبرين على الناس بغير الحق، فلا يتبعون نبيًا ولا يصغون إليه لتكبرهم، وإنْ يَرَ هؤلاء المتكبرون عن الإيمان كل آية لا يؤمنوا بها لإعراضهم ومحادَّتهم لله ورسوله، وإن يروا طريق الصلاح لا يتخذوه طريقًا، وإن يروا طريق الضلال، أي الكفر يتخذوه طريقًا ودينًا؛ وذلك بسبب تكذيبهم بآيات الله وغفلتهم عن النظر فيها والتفكر في دلالاتها.');
INSERT INTO Quran__Arabic_Tafseer_Muyassar (QuranChapterID, VerseNumber, Rukuh, Sajdah, Manzil, HizbQuarter, VerseText) VALUES (7, 147, '1', '0', '0', '0', 'والذين كذَّبوا بآيات الله وحججه وبلقاء الله في الآخرة حبطت أعمالهم؛ بسبب فَقْدِ شرطها، وهو الإيمان بالله والتصديق بجزائه، ما يجزون في الآخرة إلا جزاء ما كانوا يعملونه في الدنيا من الكفر والمعاصي، وهو الخلود في النار.');
INSERT INTO Quran__Arabic_Tafseer_Muyassar (QuranChapterID, VerseNumber, Rukuh, Sajdah, Manzil, HizbQuarter, VerseText) VALUES (7, 148, '0', '0', '0', '0', 'واتخذ قوم موسى من بعد ما فارقهم ماضيًا لمناجاة ربه معبودًا مِن ذهبهم عِجلا جسدًا بلا روح، له صوت، ألم يعلموا أنه لا يكلمهم، ولا يرشدهم إلى خير؟ أَقْدَمُوا على ما أقدموا عليه من هذا الأمر الشنيع، وكانوا ظالمين لأنفسهم واضعين الشيء في غير موضعه.');
INSERT INTO Quran__Arabic_Tafseer_Muyassar (QuranChapterID, VerseNumber, Rukuh, Sajdah, Manzil, HizbQuarter, VerseText) VALUES (7, 149, '0', '0', '0', '0', 'ولما ندم الذين عبدوا العجل مِن دون الله عند رجوع موسى إليهم، ورأوا أنهم قد ضلُّوا عن قصد السبيل، وذهبوا عن دين الله، أخذوا في الإقرار بالعبودية والاستغفار، فقالوا: لئن لم يرحمنا ربنا بقَبول توبتنا، ويستر بها ذنوبنا، لنكونن من الهالكين الذين ذهبت أعمالهم.');
INSERT INTO Quran__Arabic_Tafseer_Muyassar (QuranChapterID, VerseNumber, Rukuh, Sajdah, Manzil, HizbQuarter, VerseText) VALUES (7, 150, '0', '0', '0', '0', 'ولما رجع موسى إلى قومه مِن بني إسرائيل غضبان حزينًا؛ لأن الله قد أخبره أنه قد فُتِن قومه، وأن السامريَّ قد أضلَّهم، قال موسى: بئس الخلافة التي خلفتموني مِن بعدي، أعجلتم أَمْر ربكم؟ أي: أستعجلتم مجيئي إليكم وهو مقدَّر من الله تعالى؟ وألقى موسى ألواح التوراة غضبا على قومه الذين عبدوا العجل، وغضبًا على أخيه هارون، وأمسك برأس أخيه يجره إليه، قال هارون مستعطفًا: يا ابن أمي: إن القوم استذلوني وعدُّوني ضعيفًا وقاربوا أن يقتلوني، فلا تَسرَّ الأعداء بما تفعل بي، ولا تجعلني في غضبك مع القوم الذين خالفوا أمرك وعبدوا العجل.');
INSERT INTO Quran__Arabic_Tafseer_Muyassar (QuranChapterID, VerseNumber, Rukuh, Sajdah, Manzil, HizbQuarter, VerseText) VALUES (7, 151, '1', '0', '0', '0', 'قال موسى لما تبين له عذر أخيه، وعلم أنه لم يُفَرِّط فيما كان عليه من أمر الله: ربِّ اغفر لي غضبي، واغفر لأخي ما سبق بينه وبين بني إسرائيل، وأدخلنا في رحمتك الواسعة، فإنك أرحم بنا من كل راحم.');
INSERT INTO Quran__Arabic_Tafseer_Muyassar (QuranChapterID, VerseNumber, Rukuh, Sajdah, Manzil, HizbQuarter, VerseText) VALUES (7, 152, '0', '0', '0', '0', 'إن الذين اتخذوا العجل إلهًا سينالهم غضب شديد مِن ربهم وهوان في الحياة الدنيا؛ بسبب كفرهم بربهم، وكما فعلنا بهؤلاء نفعل بالمفترين المبتدعين في دين الله، فكل صاحب بدعة ذليل.');
INSERT INTO Quran__Arabic_Tafseer_Muyassar (QuranChapterID, VerseNumber, Rukuh, Sajdah, Manzil, HizbQuarter, VerseText) VALUES (7, 153, '0', '0', '0', '0', 'والذين عملوا السيئات من الكفر والمعاصي، ثم رجعوا مِن بعد فعلها إلى الإيمان والعمل الصالح، إن ربك من بعد التوبة النصوح لغفور لأعمالهم غير فاضحهم بها، رحيم بهم وبكل مَن كان مثلهم من التائبين.');
INSERT INTO Quran__Arabic_Tafseer_Muyassar (QuranChapterID, VerseNumber, Rukuh, Sajdah, Manzil, HizbQuarter, VerseText) VALUES (7, 154, '0', '0', '0', '0', 'ولما سكن عن موسى غضبه أخذ الألواح بعد أن ألقاها على الأرض، وفيها بيان للحق ورحمة للذين يخافون الله، ويخشون عقابه.');
INSERT INTO Quran__Arabic_Tafseer_Muyassar (QuranChapterID, VerseNumber, Rukuh, Sajdah, Manzil, HizbQuarter, VerseText) VALUES (7, 155, '0', '0', '0', '0', 'واختار موسى من قومه سبعين رجلا مِن خيارهم، وخرج بهم إلى طور "سيناء" للوقت والأجل الذي واعده الله أن يلقاه فيه بهم للتوبة مما كان من سفهاء بني إسرائيل من عبادة العجل، فلما أتوا ذلك المكان قالوا: لن نؤمن لك -يا موسى- حتى نرى الله جهرة فإنك قد كلَّمته فأرِنَاهُ، فأخذتهم الزلزلة الشديدة فماتوا، فقام موسى يتضرع إلى الله ويقول: رب ماذا أقول لبني إسرائيل إذا أتيتُهم، وقد أهلكتَ خيارهم؟ لو شئت أهلكتهم جميعًا من قبل هذا الحال وأنا معهم، فإن ذلك أخف عليَّ، أتهلكنا بما فعله سفهاء الأحلام منا؟ ما هذه الفعلة التي فعلها قومي من عبادتهم العجل إلا ابتلاءٌ واختبارٌ، تضلُّ بها مَن تشاء مِن خلقك، وتهدي بها من تشاء هدايته، أنت وليُّنا وناصرنا، فاغفر ذنوبنا، وارحمنا برحمتك، وأنت خير مَن صفح عن جُرْم، وستر عن ذنب.');
INSERT INTO Quran__Arabic_Tafseer_Muyassar (QuranChapterID, VerseNumber, Rukuh, Sajdah, Manzil, HizbQuarter, VerseText) VALUES (7, 156, '0', '0', '0', '1', 'واجعلنا ممن كتبتَ له الصالحات من الأعمال في الدنيا وفي الآخرة، إنا رجعنا تائبين إليك، قال الله تعالى لموسى: عذابي أصيب به مَن أشاء مِن خلقي، كما أصبتُ هؤلاء الذين أصبتهم من قومك، ورحمتي وسعت خلقي كلَّهم، فسأكتبها للذين يخافون الله، ويخشون عقابه، فيؤدون فرائضه، ويجتنبون معاصيه، والذين هم بدلائل التوحيد وبراهينه يصدقون.');
INSERT INTO Quran__Arabic_Tafseer_Muyassar (QuranChapterID, VerseNumber, Rukuh, Sajdah, Manzil, HizbQuarter, VerseText) VALUES (7, 157, '1', '0', '0', '0', 'هذه الرحمة سأكتبها للذين يخافون الله ويجتنبون معاصيه، ويتبعون الرسول النبي الأمي الذي لا يقرأ ولا يكتب، وهو محمد صلى الله عليه وسلم، الذي يجدون صفته وأمره مكتوبَيْن عندهم في التوراة والإنجيل، يأمرهم بالتوحيد والطاعة وكل ما عرف حُسْنه، وينهاهم عن الشرك والمعصية وكل ما عرف قُبْحه، ويُحِلُّ لهم الطيبات من المطاعم والمشارب والمناكح، ويُحرِّم عليهم الخبائث منها كلحم الخنزير، وما كانوا يستحلُّونه من المطاعم والمشارب التي حرَّمها الله، ويذهب عنهم ما كُلِّفوه من الأمور الشاقة كقطع موضع النجاسة من الجلد والثوب، وإحراق الغنائم، والقصاص حتمًا من القاتل عمدًا كان القتل أم خطأ، فالذين صدَّقوا بالنبي الأمي محمد صلى الله عليه وسلم وأقروا بنبوته، ووقروه وعظَّموه ونصروه، واتبعوا القرآن المنزل عليه، وعملوا بسنته، أولئك هم الفائزون بما وعد الله به عباده المؤمنين.');
INSERT INTO Quran__Arabic_Tafseer_Muyassar (QuranChapterID, VerseNumber, Rukuh, Sajdah, Manzil, HizbQuarter, VerseText) VALUES (7, 158, '0', '0', '0', '0', 'قل -أيها الرسول- للناس كلهم: إني رسول الله إليكم جميعًا لا إلى بعضكم دون بعض، الذي له ملك السموات والأرض وما فيهما، لا ينبغي أن تكون الألوهية والعبادة إلا له جل ثناؤه، القادر على إيجاد الخلق وإفنائه وبعثه، فصدَّقوا بالله وأقرُّوا بوحدانيته، وصدَّقوا برسوله محمد صلى الله عليه وسلم النبي الأميِّ الذي يؤمن بالله وما أنزل إليه من ربه وما أنزل على النبيين من قبله، واتبعوا هذا الرسول، والتزموا العمل بما أمركم به من طاعة الله، رجاء أن توفقوا إلى الطريق المستقيم.');
INSERT INTO Quran__Arabic_Tafseer_Muyassar (QuranChapterID, VerseNumber, Rukuh, Sajdah, Manzil, HizbQuarter, VerseText) VALUES (7, 159, '0', '0', '0', '0', 'ومِن بني إسرائيل من قوم موسى جماعة يستقيمون على الحق، يهدون الناس به، ويعدلون به في الحكم في قضاياهم.');
INSERT INTO Quran__Arabic_Tafseer_Muyassar (QuranChapterID, VerseNumber, Rukuh, Sajdah, Manzil, HizbQuarter, VerseText) VALUES (7, 160, '0', '0', '0', '0', 'وفرَّقنا قوم موسى مِن بني إسرائيل اثنتي عشرة قبيلة بعدد الأسباط -وهم أبناء يعقوب- كل قبيلة معروفة من جهة نقيبها. وأوحينا إلى موسى إذ طلب منه قومه السقيا حين عطشوا في التِّيْه: أن اضرب بعصاك الحجر، فضربه، فانفجرت منه اثنتا عشرة عينًا من الماء، قد علمت كل قبيلة من القبائل الاثنتي عشرة مشربهم، لا تدخل قبيلة على غيرها في شربها، وظلَّلنا عليهم السحاب، وأنزلنا عليهم المنَّ -وهو شيء يشبه الصَّمغ، طعمه كالعسل - والسلوى، وهو طائر يشبه السُّمَانَى، وقلنا لهم: كلوا من طيبات ما رزقناكم، فكرهوا ذلك وملُّوه من طول المداومة عليه، وقالوا: لن نصبر على طعام واحد، وطلبوا استبدال الذي هو أدنى بالذي هو خير. وما ظلمونا حين لم يشكروا لله، ولم يقوموا بما أوجب الله عليهم، ولكن كانوا أنفسهم يظلمون؛ إذ فوَّتوا عليها كل خير، وعرَّضوها للشر والنقمة.');
INSERT INTO Quran__Arabic_Tafseer_Muyassar (QuranChapterID, VerseNumber, Rukuh, Sajdah, Manzil, HizbQuarter, VerseText) VALUES (7, 161, '0', '0', '0', '0', 'واذكر -أيها الرسول- عصيان بني إسرائيل لربهم سبحانه وتعالى ولنبيهم موسى عليه السلام، وتبديلهم القول الذي أمروا أن يقولوه حين قال الله لهم: اسكنوا قرية "بيت المقدس"، وكلوا من ثمارها وحبوبها ونباتها أين شئتم ومتى شئتم، وقولوا: حُطَّ عنا ذنوبنا، وادخلوا الباب خاضعين لله، نغفر لكم خطاياكم، فلا نؤاخذكم عليها، وسنزيد المحسنين مِن خَيْرَيِ الدنيا والآخرة.');
INSERT INTO Quran__Arabic_Tafseer_Muyassar (QuranChapterID, VerseNumber, Rukuh, Sajdah, Manzil, HizbQuarter, VerseText) VALUES (7, 162, '1', '0', '0', '0', 'فغيَّر الذين كفروا بالله منهم ما أمرهم الله به من القول، ودخلوا الباب يزحفون على أستاههم، وقالوا: حبة في شعرة، فأرسلنا عليهم عذابًا من السماء، أهلكناهم به؛ بسبب ظلمهم وعصيانهم.');
INSERT INTO Quran__Arabic_Tafseer_Muyassar (QuranChapterID, VerseNumber, Rukuh, Sajdah, Manzil, HizbQuarter, VerseText) VALUES (7, 163, '0', '0', '0', '0', 'واسأل -أيها الرسول- هؤلاء اليهود عن خبر أهل القرية التي كانت بقرب البحر، إذ يعتدي أهلها في يوم السبت على حرمات الله، حيث أمرهم أن يعظموا يوم السبت ولا يصيدوا فيه سمكًا، فابتلاهم الله وامتحنهم؛ فكانت حيتانهم تأتيهم يوم السبت كثيرة طافية على وجه البحر، وإذا ذهب يوم السبت تذهب الحيتان في البحر، ولا يرون منها شيئًا، فكانوا يحتالون على حبسها في يوم السبت في حفائر، ويصطادونها بعده. وكما وصفنا لكم من الاختبار والابتلاء، لإظهار السمك على ظهر الماء في اليوم المحرم عليهم صيده فيه، وإخفائه عليهم في اليوم المحلل لهم فيه صيده، كذلك نختبرهم بسبب فسقهم عن طاعة الله وخروجهم عنها.');
INSERT INTO Quran__Arabic_Tafseer_Muyassar (QuranChapterID, VerseNumber, Rukuh, Sajdah, Manzil, HizbQuarter, VerseText) VALUES (7, 164, '0', '0', '0', '0', 'واذكر -أيها الرسول- إذ قالت جماعة منهم لجماعة أخرى كانت تعظ المعتدين في يوم السبت، وتنهاهم عن معصية الله فيه: لِمَ تعظون قومًا الله مهلكهم في الدنيا بمعصيتهم إياه أو معذبهم عذابا شديدًا في الآخرة؟ قال الذين كانوا ينهَوْنهم عن معصية الله: نَعِظهم وننهاهم لِنُعْذَر فيهم، ونؤدي فرض الله علينا في الأمر بالمعروف والنهي عن المنكر، ورجاء أن يتقوا الله، فيخافوه، ويتوبوا من معصيتهم ربهم وتعذِّيهم على ما حرَّم عليهم.');
INSERT INTO Quran__Arabic_Tafseer_Muyassar (QuranChapterID, VerseNumber, Rukuh, Sajdah, Manzil, HizbQuarter, VerseText) VALUES (7, 165, '0', '0', '0', '0', 'فلما تركت الطائفة التي اعتدت في يوم السبت ما ذُكِّرت به، واستمرت على غيِّها واعتدائها فيه، ولم تستجب لما وَعَظَتْها به الطائفة الواعظة، أنجى الله الذين ينهون عن معصيته، وأخذ الذين اعتدَوْا في يوم السبت بعذاب أليم شديد؛ بسبب مخالفتهم أمر الله وخروجهم عن طاعته.');
INSERT INTO Quran__Arabic_Tafseer_Muyassar (QuranChapterID, VerseNumber, Rukuh, Sajdah, Manzil, HizbQuarter, VerseText) VALUES (7, 166, '0', '0', '0', '0', 'فلما تمردت تلك الطائفة، وتجاوزت ما نهاها الله عنه من عدم الصيد في يوم السبت، قال لهم الله: كونوا قردة خاسئين مبعدين من كل خير، فكانوا كذلك.');
INSERT INTO Quran__Arabic_Tafseer_Muyassar (QuranChapterID, VerseNumber, Rukuh, Sajdah, Manzil, HizbQuarter, VerseText) VALUES (7, 167, '0', '0', '0', '0', 'واذكر -أيها الرسول- إذ علم ذلك إعلامًا صريحًا ليبعثن على اليهود مَن يذيقهم سوء العذاب والإذلال إلى يوم القيامة. إن ربك -أيها الرسول- لسريع العقاب لِمَن استحقه بسبب كفره ومعصيته، وإنه لغفور عن ذنوب التائبين، رحيم بهم.');
INSERT INTO Quran__Arabic_Tafseer_Muyassar (QuranChapterID, VerseNumber, Rukuh, Sajdah, Manzil, HizbQuarter, VerseText) VALUES (7, 168, '0', '0', '0', '0', 'وفرَّقنا بني إسرائيل في الأرض جماعات، منهم القائمون بحقوق الله وحقوق عباده، ومنهم المقصِّرون الظالمون لأنفسهم، واختبرنا هؤلاء بالرخاء في العيش والسَّعَة في الرزق، واختبرناهم أيضًا بالشدة في العيش والمصائب والرزايا؛ رجاء أن يرجعوا إلى طاعة ربهم ويتوبوا من معاصيه.');
INSERT INTO Quran__Arabic_Tafseer_Muyassar (QuranChapterID, VerseNumber, Rukuh, Sajdah, Manzil, HizbQuarter, VerseText) VALUES (7, 169, '0', '0', '0', '0', 'فجاء من بعد هؤلاء الذين وصفناهم بَدَلُ سوء أخذوا الكتاب من أسلافهم، فقرءوه وعلموه، وخالفوا حكمه، يأخذون ما يعرض لهم من متاع الدنيا من دنيء المكاسب كالرشوة وغيرها؛ وذلك لشدة حرصهم ونَهَمهم، ويقولون مع ذلك: إن الله سيغفر لنا ذنوبنا تمنيًا على الله الأباطيل، وإن يأت هؤلاء اليهودَ متاعٌ زائلٌ من أنواع الحرام يأخذوه ويستحلوه، مصرِّين على ذنوبهم وتناولهم الحرام، ألَمْ يؤخذ على هؤلاء العهود بإقامة التوراة والعمل بما فيها، وألا يقولوا على الله إلا الحق وألا يكذبوا عليه، وعلموا ما في الكتاب فضيعوه، وتركوا العمل به، وخالفوا عهد الله إليهم في ذلك؟ والدار الآخرة خير للذين يتقون الله، فيمتثلون أوامره، ويجتنبون نواهيه، أفلا يعقل هؤلاء الذين يأخذون دنيء المكاسب أن ما عند الله خير وأبقى للمتقين؟');
INSERT INTO Quran__Arabic_Tafseer_Muyassar (QuranChapterID, VerseNumber, Rukuh, Sajdah, Manzil, HizbQuarter, VerseText) VALUES (7, 170, '0', '0', '0', '0', 'والذين يتمسَّكون بالكتاب، ويعملون بما فيه من العقائد والأحكام، ويحافظون على الصلاة بحدودها، ولا يضيعون أوقاتها، فإن الله يثيبهم على أعمالهم الصالحة، ولا يضيعها.');
INSERT INTO Quran__Arabic_Tafseer_Muyassar (QuranChapterID, VerseNumber, Rukuh, Sajdah, Manzil, HizbQuarter, VerseText) VALUES (7, 171, '1', '0', '0', '1', 'واذكر -أيها الرسول- إذ رفعنا الجبل فوق بني إسرائيل كأنه سحابة تظلهم، وأيقنوا أنه واقع بهم إن لم يقبلوا أحكام التوراة، وقلنا لهم: خذوا ما آتيناكم بقوة، أي اعملوا بما أعطيناكم باجتهاد منكم، واذكروا ما في كتابنا من العهود والمواثيق التي أخذناها عليكم بالعمل بما فيه؛ كي تتقوا ربكم فتنجوا من عقابه.');
INSERT INTO Quran__Arabic_Tafseer_Muyassar (QuranChapterID, VerseNumber, Rukuh, Sajdah, Manzil, HizbQuarter, VerseText) VALUES (7, 172, '0', '0', '0', '0', 'واذكر -أيها النبي- إذ استخرج ربك أولاد آدم مِن أصلاب آبائهم، وقررهم بتوحيده بما أودعه في فطرهم من أنه ربهم وخالقهم ومليكهم، فأقروا له بذلك، خشية أن ينكروا يوم القيامة، فلا يقروا بشيء فيه، ويزعموا أن حجة الله ما قامت عليهم، ولا عندهم علم بها، بل كانوا عنها غافلين.');
INSERT INTO Quran__Arabic_Tafseer_Muyassar (QuranChapterID, VerseNumber, Rukuh, Sajdah, Manzil, HizbQuarter, VerseText) VALUES (7, 173, '0', '0', '0', '0', 'أو لئلا تقولوا: إنما أشرك آباؤنا من قبلنا ونقضوا العهد، فاقتدينا بهم من بعدهم، أفتعذبنا بما فعل الذين أبطلوا أعمالهم بجعلهم مع الله شريكا في العبادة؟');
INSERT INTO Quran__Arabic_Tafseer_Muyassar (QuranChapterID, VerseNumber, Rukuh, Sajdah, Manzil, HizbQuarter, VerseText) VALUES (7, 174, '0', '0', '0', '0', 'وكما فَصَّلْنا الآيات، وبيَّنَّا فيها ما فعلناه بالأمم السابقة، كذلك نفصِّل الآيات ونبيِّنها لقومك أيها الرسول؛ رجاء أن يرجعوا عن شركهم، وينيبوا إلى ربهم.');
INSERT INTO Quran__Arabic_Tafseer_Muyassar (QuranChapterID, VerseNumber, Rukuh, Sajdah, Manzil, HizbQuarter, VerseText) VALUES (7, 175, '0', '0', '0', '0', 'واقصص -أيها الرسول- على أمتك خبر رجل من بني إسرائيل أعطيناه حججنا وأدلتنا، فتعلَّمها، ثم كفر بها، ونبذها وراء ظهره، فاستحوذ عليه الشيطان، فصار من الضالين الهالكين؛ بسبب مخالفته أمر ربه وطاعته الشيطان.');
INSERT INTO Quran__Arabic_Tafseer_Muyassar (QuranChapterID, VerseNumber, Rukuh, Sajdah, Manzil, HizbQuarter, VerseText) VALUES (7, 176, '0', '0', '0', '0', 'ولو شئنا أن نرفع قدره بما آتيناه من الآيات لفعلنا، ولكنه رَكَنَ إلى الدنيا واتبع هواه، وآثر لَذَّاته وشهواته على الآخرة، وامتنع عن طاعة الله وخالف أمره. فَمَثَلُ هذا الرجل مثل الكلب، إن تطرده أو تتركه يُخْرج لسانه في الحالين لاهثًا، فكذلك الذي انسلخ من آيات الله يظل على كفره إن اجتهدْتَ في دعوتك له أو أهملته، هذا الوصف -أيها الرسول- وصف هؤلاء القوم الذين كانوا ضالين قبل أن تأتيهم بالهدى والرسالة، فاقصص -أيها الرسول- أخبار الأمم الماضية، ففي إخبارك بذلك أعظم معجزة، لعل قومك يتدبرون فيما جئتهم به فيؤمنوا لك.');
INSERT INTO Quran__Arabic_Tafseer_Muyassar (QuranChapterID, VerseNumber, Rukuh, Sajdah, Manzil, HizbQuarter, VerseText) VALUES (7, 177, '0', '0', '0', '0', 'قَبُحَ مثلا مثلُ القوم الذين كذَّبوا بحجج الله وأدلته، فجحدوها، وأنفسهم كانوا يظلمونها؛ بسبب تكذيبهم بهذه الحجج والأدلة.');
INSERT INTO Quran__Arabic_Tafseer_Muyassar (QuranChapterID, VerseNumber, Rukuh, Sajdah, Manzil, HizbQuarter, VerseText) VALUES (7, 178, '0', '0', '0', '0', 'من يوفقه الله للإيمان به وطاعته فهو الموفَّق، ومن يخذله فلم يوفقه فهو الخاسر الهالك، فالهداية والإضلال من الله وحده.');
INSERT INTO Quran__Arabic_Tafseer_Muyassar (QuranChapterID, VerseNumber, Rukuh, Sajdah, Manzil, HizbQuarter, VerseText) VALUES (7, 179, '0', '0', '0', '0', 'ولقد خلقنا للنار -التي يعذِّب الله فيها مَن يستحق العذاب في الآخرة - كثيرًا من الجن والإنس، لهم قلوب لا يعقلون بها، فلا يرجون ثوابًا ولا يخافون عقابًا، ولهم أعين لا ينظرون بها إلى آيات الله وأدلته، ولهم آذان لا يسمعون بها آيات كتاب الله فيتفكروا فيها، هؤلاء كالبهائم التي لا تَفْقَهُ ما يقال لها، ولا تفهم ما تبصره، ولا تعقل بقلوبها الخير والشر فتميز بينهما، بل هم أضل منها؛ لأن البهائم تبصر منافعها ومضارها وتتبع راعيها، وهم بخلاف ذلك، أولئك هم الغافلون عن الإيمان بالله وطاعته.');
INSERT INTO Quran__Arabic_Tafseer_Muyassar (QuranChapterID, VerseNumber, Rukuh, Sajdah, Manzil, HizbQuarter, VerseText) VALUES (7, 180, '0', '0', '0', '0', 'ولله سبحانه وتعالى الأسماء الحسنى، الدالة على كمال عظمته، وكل أسمائه حسن، فاطلبوا منه بأسمائه ما تريدون، واتركوا الذين يُغيِّرون في أسمائه بالزيادة أو النقصان أو التحريف، كأن يُسمَّى بها من لا يستحقها، كتسمية المشركين بها آلهتهم، أو أن يجعل لها معنى لم يُردْه الله ولا رسوله، فسوف يجزون جزاء أعمالهم السيئة التي كانوا يعملونها في الدنيا من الكفر بالله، والإلحاد في أسمائه وتكذيب رسوله.');
INSERT INTO Quran__Arabic_Tafseer_Muyassar (QuranChapterID, VerseNumber, Rukuh, Sajdah, Manzil, HizbQuarter, VerseText) VALUES (7, 181, '1', '0', '0', '0', 'ومن الذين خَلَقْنا جماعة فاضلة يهتدون بالحق ويَدْعون إليه، وبه يقضون وينصفون الناس، وهم أئمة الهدى ممن أنعم الله عليهم بالإيمان والعمل الصالح.');
INSERT INTO Quran__Arabic_Tafseer_Muyassar (QuranChapterID, VerseNumber, Rukuh, Sajdah, Manzil, HizbQuarter, VerseText) VALUES (7, 182, '0', '0', '0', '0', 'والذين كذَّبوا بآياتنا، فجحدوها، ولم يتذكروا بها، سنفتح لهم أبواب الرزق ووجوه المعاش في الدنيا، استدراجًا لهم حتى يغتروا بما هم فيه ويعتقدوا أنهم على شيء، ثم نعاقبهم على غِرَّة من حيث لا يعلمون. وهذه عقوبة من الله على التكذيب بحجج الله وآياته.');
INSERT INTO Quran__Arabic_Tafseer_Muyassar (QuranChapterID, VerseNumber, Rukuh, Sajdah, Manzil, HizbQuarter, VerseText) VALUES (7, 183, '0', '0', '0', '0', 'وأمهل هؤلاء الذين كذبوا بآياتنا حتى يظنوا أنهم لا يعاقبون، فيزدادوا كفرًا وطغيانًا، وبذلك يتضاعف لهم العذاب. إن كيدي متين، أي: قوي شديد لا يُدْفع بقوة ولا بحيلة.');
INSERT INTO Quran__Arabic_Tafseer_Muyassar (QuranChapterID, VerseNumber, Rukuh, Sajdah, Manzil, HizbQuarter, VerseText) VALUES (7, 184, '0', '0', '0', '0', 'أولم يتفكر هؤلاء الذين كذبوا بآياتنا فيتدبروا بعقولهم، ويعلموا أنه ليس بمحمد جنون؟ ما هو إلا نذير لهم من عقاب الله على كفرهم به إن لم يؤمنوا، ناصح مبين.');
INSERT INTO Quran__Arabic_Tafseer_Muyassar (QuranChapterID, VerseNumber, Rukuh, Sajdah, Manzil, HizbQuarter, VerseText) VALUES (7, 185, '0', '0', '0', '0', 'أولم ينظر هؤلاء المكذبون بآيات الله في ملك الله العظيم وسلطانه القاهر في السموات والأرض، وما خلق الله -جلَّ ثناؤه- من شيء فيهما، فيتدبروا ذلك ويعتبروا به، وينظروا في آجالهم التي عست أن تكون قَرُبَتْ فيهلكوا على كفرهم، ويصيروا إلى عذاب الله وأليم عقابه؟ فبأي تخويف وتحذير بعد تحذير القرآن يصدقون ويعملون؟');
INSERT INTO Quran__Arabic_Tafseer_Muyassar (QuranChapterID, VerseNumber, Rukuh, Sajdah, Manzil, HizbQuarter, VerseText) VALUES (7, 186, '0', '0', '0', '0', 'مَن يضلله الله عن طريق الرشاد فلا هادي له، ويتركُهم في كفرهم يتحيرون ويترددون.');
INSERT INTO Quran__Arabic_Tafseer_Muyassar (QuranChapterID, VerseNumber, Rukuh, Sajdah, Manzil, HizbQuarter, VerseText) VALUES (7, 187, '0', '0', '0', '0', 'يسألك -أيها الرسول- كفار "مكة" عن الساعة متى قيامها؟ قل لهم: عِلْمُ قيامها عند الله لا يظهرها إلا هو، ثَقُلَ علمها، وخفي على أهل السموات والأرض، فلا يعلم وقت قيامها ملَك مقرَّب ولا نبي مرسل، لا تجيء الساعة إلا فجأة، يسألك هؤلاء القوم عنها كأنك حريص على العلم بها، مستقص بالسؤال عنها، قل لهم: إنما علمها عند الله الذي يعلم غيب السموات والأرض، ولكنَّ أكثر الناس لا يعلمون أن ذلك لا يعلمه إلا الله.');
INSERT INTO Quran__Arabic_Tafseer_Muyassar (QuranChapterID, VerseNumber, Rukuh, Sajdah, Manzil, HizbQuarter, VerseText) VALUES (7, 188, '1', '0', '0', '0', 'قل -أيها الرسول-: لا أقدرُ على جَلْبِ خير لنفسي ولا دفع شر يحل بها إلا ما شاء الله، ولو كنت أعلم الغيب لفعلت الأسباب التي أعلم أنها تكثِّر لي المصالح والمنافع، ولاتَّقيتُ ما يكون من الشر قبل أن يقع، ما أنا إلا رسول الله أرسلني إليكم، أخوِّف من عقابه، وأبشر بثوابه قومًا يصدقون بأني رسول الله، ويعملون بشرعه.');
INSERT INTO Quran__Arabic_Tafseer_Muyassar (QuranChapterID, VerseNumber, Rukuh, Sajdah, Manzil, HizbQuarter, VerseText) VALUES (7, 189, '0', '0', '0', '1', 'هو الذي خلقكم -أيها الناس- من نفس واحدة، وهي آدم عليه السلام وخَلَق منها زوجها، وهي حواء؛ ليأنس بها ويطمئن، فلما جامعها -والمراد جنس الزوجين من ذرية آدم- حملت ماءً خفيفًا، فقامت به وقعدت وأتمت الحمل، فلما قَرُبت ولادتها وأثقلت دعا الزوجان ربهما: لئن أعطيتنا بشرًا سويًا صالحًا لنكونن ممن يشكرك على ما وهبت لنا من الولد الصالح.');
INSERT INTO Quran__Arabic_Tafseer_Muyassar (QuranChapterID, VerseNumber, Rukuh, Sajdah, Manzil, HizbQuarter, VerseText) VALUES (7, 190, '0', '0', '0', '0', 'فلما رزق الله الزوجين ولدًا صالحًا، جعلا لله شركاء في ذلك الولد الذي انفرد الله بخلقه فعبَّداه لغير الله، فتعالى الله وتنزه عن كل شرك.');
INSERT INTO Quran__Arabic_Tafseer_Muyassar (QuranChapterID, VerseNumber, Rukuh, Sajdah, Manzil, HizbQuarter, VerseText) VALUES (7, 191, '0', '0', '0', '0', 'أيشرك هؤلاء المشركون في عبادة الله مخلوقاته، وهي لا تقدر على خَلْق شيء، بل هي مخلوقة؟');
INSERT INTO Quran__Arabic_Tafseer_Muyassar (QuranChapterID, VerseNumber, Rukuh, Sajdah, Manzil, HizbQuarter, VerseText) VALUES (7, 192, '0', '0', '0', '0', 'ولا تستطيع أن تنصر عابديها أو تدفع عن نفسها سوءًا، فإذا كانت لا تخلق شيئًا، بل هي مخلوقة، ولا تستطيع أن تدفع المكروه عمن يعبدها، ولا عن نفسها، فكيف تُتَّخذ مع الله آلهة؟ إنْ هذا إلا أظلم الظلم وأسفه السَّفَه.');
INSERT INTO Quran__Arabic_Tafseer_Muyassar (QuranChapterID, VerseNumber, Rukuh, Sajdah, Manzil, HizbQuarter, VerseText) VALUES (7, 193, '0', '0', '0', '0', 'وإن تدعوا -أيها المشركون- هذه الأصنام التي عبدتموها من دون الله إلى الهدى، لا تسمع دعاءكم ولا تتبعكم؛ يستوي دعاؤكم لها وسكوتكم عنها؛ لأنها لا تسمع ولا تبصر ولا تَهدِي ولا تُهدى.');
INSERT INTO Quran__Arabic_Tafseer_Muyassar (QuranChapterID, VerseNumber, Rukuh, Sajdah, Manzil, HizbQuarter, VerseText) VALUES (7, 194, '0', '0', '0', '0', 'إن الذين تعبدون من غير الله -أيها المشركون- هم مملوكون لربهم كما أنكم مملوكون لربكم، فإن كنتم كما تزعمون صادقين في أنها تستحق من العبادة شيئًا فادعوهم فليستجيبوا لكم، فإن استجابوا لكم وحصَّلوا مطلوبكم، وإلا تبين أنكم كاذبون مفترون على الله أعظم الفرية.');
INSERT INTO Quran__Arabic_Tafseer_Muyassar (QuranChapterID, VerseNumber, Rukuh, Sajdah, Manzil, HizbQuarter, VerseText) VALUES (7, 195, '0', '0', '0', '0', 'ألهذه الآلهة والأصنام أرجل يسعَوْن بها معكم في حوائجكم؟ أم لهم أيدٍ يدفعون بها عنكم وينصرونكم على من يريد بكم شرًا ومكروهًا؟ أم لهم أعين ينظرون بها فيعرِّفونكم ما عاينوا وأبصروا مما يغيب عنكم فلا ترونه؟ أم لهم آذان يسمعون بها فيخبرونكم بما لم تسمعوه؟ فإذا كانت آلهتكم التي تعبدونها ليس فيها شيء من هذه الآلات، فما وجه عبادتكم إياها، وهي خالية من هذه الأشياء التي بها يتوصل إلى جلب النفع أو دفع الضر؟ قل -أيها الرسول- لهؤلاء المشركين من عبدة الأوثان: ادعوا آلهتكم الذين جعلتموهم لله شركاء في العبادة، ثم اجتمعوا على إيقاع السوء والمكروه بي، فلا تؤخروني وعجِّلوا بذلك، فإني لا أبالي بآلهتكم؛ لاعتمادي على حفظ الله وحده.');
INSERT INTO Quran__Arabic_Tafseer_Muyassar (QuranChapterID, VerseNumber, Rukuh, Sajdah, Manzil, HizbQuarter, VerseText) VALUES (7, 196, '0', '0', '0', '0', 'إن وليِّيَ الله، الذي يتولى حفظي ونصري، هو الذي نزَّل عليَّ القرآن بالحق، وهو يتولى الصالحين مِن عباده، وينصرهم على أعدائهم ولا يخذلهم.');
INSERT INTO Quran__Arabic_Tafseer_Muyassar (QuranChapterID, VerseNumber, Rukuh, Sajdah, Manzil, HizbQuarter, VerseText) VALUES (7, 197, '0', '0', '0', '0', 'والذين تدعون -أنتم أيها المشركون- مِن غير الله من الآلهة لا يستطيعون نصركم، ولا يقدرون على نصرة أنفسهم.');
INSERT INTO Quran__Arabic_Tafseer_Muyassar (QuranChapterID, VerseNumber, Rukuh, Sajdah, Manzil, HizbQuarter, VerseText) VALUES (7, 198, '0', '0', '0', '0', 'وإن تدعوا -أيها المشركون- آلهتكم إلى الاستقامة والسداد لا يسمعوا دعاءكم، وترى -أيها الرسول- آلهة هؤلاء المشركين مِن عبدة الأوثان يقابلونك كالناظر إليك وهم لا يبصرون؛ لأنهم لا أبصار لهم ولا بصائر.');
INSERT INTO Quran__Arabic_Tafseer_Muyassar (QuranChapterID, VerseNumber, Rukuh, Sajdah, Manzil, HizbQuarter, VerseText) VALUES (7, 199, '0', '0', '0', '0', 'اقْبَلْ -أيها النبي أنت وأمتك- الفضل من أخلاق الناس وأعمالهم، ولا تطلب منهم ما يشق عليهم حتى لا ينفروا، وأْمر بكل قول حسن وفِعْلٍ جميل، وأعرض عن منازعة السفهاء ومساواة الجهلة الأغبياء.');
INSERT INTO Quran__Arabic_Tafseer_Muyassar (QuranChapterID, VerseNumber, Rukuh, Sajdah, Manzil, HizbQuarter, VerseText) VALUES (7, 200, '0', '0', '0', '0', 'وإما يصيبنَّك -أيها النبي- من الشيطان غضب أو تُحِس منه بوسوسة وتثبيط عن الخير أو حث على الشرِّ، فالجأ إلى الله مستعيذًا به، إنه سميع لكل قول، عليم بكل فعل.');
INSERT INTO Quran__Arabic_Tafseer_Muyassar (QuranChapterID, VerseNumber, Rukuh, Sajdah, Manzil, HizbQuarter, VerseText) VALUES (7, 201, '0', '0', '0', '0', 'إن الذين اتقوا الله مِن خلقه، فخافوا عقابه بأداء فرائضه واجتناب نواهيه، إذا أصابهم عارض من وسوسة الشيطان تذكَّروا ما أوجب الله عليهم من طاعته، والتوبة إليه، فإذا هم منتهون عن معصية الله على بصيرة، آخذون بأمر الله، عاصون للشيطان.');
INSERT INTO Quran__Arabic_Tafseer_Muyassar (QuranChapterID, VerseNumber, Rukuh, Sajdah, Manzil, HizbQuarter, VerseText) VALUES (7, 202, '0', '0', '0', '0', 'وإخوان الشياطين، وهم الفجَّار من ضلال الإنس تمدهم الشياطين من الجن في الضلالة والغَواية، ولا تدَّخر شياطين الجن وُسْعًا في مدِّهم شياطين الإنس في الغيِّ، ولا تدَّخر شياطين الإنس وُسْعًا في عمل ما توحي به شياطين الجن.');
INSERT INTO Quran__Arabic_Tafseer_Muyassar (QuranChapterID, VerseNumber, Rukuh, Sajdah, Manzil, HizbQuarter, VerseText) VALUES (7, 203, '0', '0', '0', '0', 'وإذا لم تجئ -أيها الرسول- هؤلاء المشركين بآية قالوا: هلا أحدَثْتها واختلقتها من عند نفسك، قل لهم -أيها الرسول-: إن هذا ليس لي، ولا يجوز لي فِعْله؛ لأن الله إنما أمرني باتباع ما يوحى إليَّ من عنده، وهو هذا القرآن الذي أتلوه عليكم حججًا وبراهين من ربكم، وبيانًا يهدي المؤمنين إلى الطريق المستقيم، ورحمة يرحم الله بها عباده المؤمنين.');
INSERT INTO Quran__Arabic_Tafseer_Muyassar (QuranChapterID, VerseNumber, Rukuh, Sajdah, Manzil, HizbQuarter, VerseText) VALUES (7, 204, '0', '0', '0', '0', 'وإذا قرئ القرآن فاستمعوا له أيها الناس وأنصتوا، لتعقلوه رجاء أن يرحمكم الله به.');
INSERT INTO Quran__Arabic_Tafseer_Muyassar (QuranChapterID, VerseNumber, Rukuh, Sajdah, Manzil, HizbQuarter, VerseText) VALUES (7, 205, '0', '0', '0', '0', 'واذكر -أيها الرسول- ربك في نفسك تخشعًا وتواضعًا لله خائفًا وجل القلب منه، وادعه متوسطًا بين الجهر والمخافتة في أول النهار وآخره، ولا تكن من الذين يَغْفُلون عن ذكر الله، ويلهون عنه في سائر أوقاتهم.');
INSERT INTO Quran__Arabic_Tafseer_Muyassar (QuranChapterID, VerseNumber, Rukuh, Sajdah, Manzil, HizbQuarter, VerseText) VALUES (7, 206, '1', '1', '0', '0', 'إن الذين عند ربك من الملائكة لا يستكبرون عن عبادة الله، بل ينقادون لأوامره، ويسبحونه بالليل والنهار، وينزهونه عما لا يليق به، وله وحده لا شريك له يسجدون.');
INSERT INTO Quran__Arabic_Tafseer_Muyassar (QuranChapterID, VerseNumber, Rukuh, Sajdah, Manzil, HizbQuarter, VerseText) VALUES (8, 1, '0', '0', '0', '1', 'يسألك أصحابك -أيها النبي- عن الغنائم يوم "بدر" كيف تقسمها بينهم؟ قل لهم: إنَّ أمرها إلى الله ورسوله، فالرسول يتولى قسمتها بأمر ربه، فاتقوا عقاب الله ولا تُقَدموا على معصيته، واتركوا المنازعة والمخاصمة بسبب هذه الأموال، وأصلحوا الحال بينكم، والتزموا طاعة الله ورسوله إن كنتم مؤمنين؛ فإن الإيمان يدعو إلى طاعة الله ورسوله.');
INSERT INTO Quran__Arabic_Tafseer_Muyassar (QuranChapterID, VerseNumber, Rukuh, Sajdah, Manzil, HizbQuarter, VerseText) VALUES (8, 2, '0', '0', '0', '0', 'إنما المؤمنون بالله حقًا هم الذين إذا ذُكِر الله فزعت قلوبهم، وإذا تليت عليهم آيات القرآن زادتهم إيمانًا مع إيمانهم، لتدبرهم لمعانيه وعلى الله تعالى يتوكلون، فلا يرجون غيره، ولا يرهبون سواه.');
INSERT INTO Quran__Arabic_Tafseer_Muyassar (QuranChapterID, VerseNumber, Rukuh, Sajdah, Manzil, HizbQuarter, VerseText) VALUES (8, 3, '0', '0', '0', '0', 'الذين يداومون على أداء الصلوات المفروضة في أوقاتها، ومما رزقناهم من الأموال ينفقون فيما أمرناهم به.');
INSERT INTO Quran__Arabic_Tafseer_Muyassar (QuranChapterID, VerseNumber, Rukuh, Sajdah, Manzil, HizbQuarter, VerseText) VALUES (8, 4, '0', '0', '0', '0', 'هؤلاء الذين يفعلون هذه الأفعال هم المؤمنون حقًا ظاهرًا وباطنًا بما أنزل الله عليهم، لهم منازل عالية عند الله، وعفو عن ذنوبهم، ورزق كريم، وهو الجنة.');
INSERT INTO Quran__Arabic_Tafseer_Muyassar (QuranChapterID, VerseNumber, Rukuh, Sajdah, Manzil, HizbQuarter, VerseText) VALUES (8, 5, '0', '0', '0', '0', 'كما أنكم لما اختلفتم في المغانم فانتزعها الله منكم، وجعلها إلى قَسْمه وقَسْم رسوله صلى الله عليه وسلم، كذلك أمرك ربك -أيها النبي- بالخروج من "المدينة" للقاء عِيْر قريش، وذلك بالوحي الذي أتاك به جبريل مع كراهة فريق من المؤمنين للخروج.');
INSERT INTO Quran__Arabic_Tafseer_Muyassar (QuranChapterID, VerseNumber, Rukuh, Sajdah, Manzil, HizbQuarter, VerseText) VALUES (8, 6, '0', '0', '0', '0', 'يجادلك -أيها النبي- فريق من المؤمنين في القتال مِن بعد ما تبيَّن لهم أن ذلك واقع، كأنهم يساقون إلى الموت، وهم ينظرون إليه عِيانًا.');
INSERT INTO Quran__Arabic_Tafseer_Muyassar (QuranChapterID, VerseNumber, Rukuh, Sajdah, Manzil, HizbQuarter, VerseText) VALUES (8, 7, '0', '0', '0', '0', 'واذكروا -أيها المجادلون- وَعْدَ الله لكم بالظَّفْر بإحدى الطائفتين: العير وما تحمله مِن أرزاق، أو النفير، وهو قتال الأعداء والانتصار عليهم، وأنتم تحبون الظَّفْر بالعير دون القتال، ويريد الله أن يحق الإسلام، ويُعْليه بأمره إياكم بقتال الكفار، ويستأصل الكافرين بالهلاك.');
INSERT INTO Quran__Arabic_Tafseer_Muyassar (QuranChapterID, VerseNumber, Rukuh, Sajdah, Manzil, HizbQuarter, VerseText) VALUES (8, 8, '0', '0', '0', '0', 'ليعزَّ الله الإسلام وأهله، ويذهب الشرك وأهله، ولو كره المشركون ذلك.');
INSERT INTO Quran__Arabic_Tafseer_Muyassar (QuranChapterID, VerseNumber, Rukuh, Sajdah, Manzil, HizbQuarter, VerseText) VALUES (8, 9, '0', '0', '0', '0', 'اذكروا نعمة الله عليكم يوم "بدر" إذ تطلبون النصر على عدوكم، فاستجاب الله لدعائكم قائلا إني ممدُّكم بألف من الملائكة من السماء، يتبع بعضهم بعضًا.');
INSERT INTO Quran__Arabic_Tafseer_Muyassar (QuranChapterID, VerseNumber, Rukuh, Sajdah, Manzil, HizbQuarter, VerseText) VALUES (8, 10, '1', '0', '0', '0', 'وما جعل الله ذلك الإمداد إلا بشارة لكم بالنصر، ولتسكن به قلوبكم، وتوقنوا بنصر الله لكم، وما النصر إلا من عند الله، لا بشدة بأسكم وقواكم. إن الله عزيز في ملكه، حكيم في تدبيره وشرعه.');
INSERT INTO Quran__Arabic_Tafseer_Muyassar (QuranChapterID, VerseNumber, Rukuh, Sajdah, Manzil, HizbQuarter, VerseText) VALUES (8, 11, '0', '0', '0', '0', 'إذ يُلْقي الله عليكم النعاس أمانًا منه لكم من خوف عدوكم أن يغلبكم، وينزل عليكم من السحاب ماء طهورًا، ليطهركم به من الأحداث الظاهرة، ويزيل عنكم في الباطن وساوس الشيطان وخواطره، وليشدَّ على قلوبكم بالصبر عند القتال، ويثبت به أقدام المؤمنين بتلبيد الأرض الرملية بالمطر حتى لا تنزلق فيها الأقدام.');
INSERT INTO Quran__Arabic_Tafseer_Muyassar (QuranChapterID, VerseNumber, Rukuh, Sajdah, Manzil, HizbQuarter, VerseText) VALUES (8, 12, '0', '0', '0', '0', 'إذ يوحي ربك -أيها النبي- إلى الملائكة الذين أمدَّ الله بهم المسلمين في غزوة "بدر" أني معكم أُعينكم وأنصركم، فقوُّوا عزائم الذين آمنوا، سألقي في قلوب الذين كفروا الخوف الشديد والذلة والصَّغَار، فاضربوا -أيها المؤمنون- رؤوس الكفار، واضربوا منهم كل طرف ومِفْصل.');
INSERT INTO Quran__Arabic_Tafseer_Muyassar (QuranChapterID, VerseNumber, Rukuh, Sajdah, Manzil, HizbQuarter, VerseText) VALUES (8, 13, '0', '0', '0', '0', 'ذلك الذي حدث للكفار من ضَرْب رؤوسهم وأعناقهم وأطرافهم؛ بسبب مخالفتهم لأمر الله ورسوله، ومَن يخالف أمر الله ورسوله، فإن الله شديد العقاب له في الدنيا والآخرة.');
INSERT INTO Quran__Arabic_Tafseer_Muyassar (QuranChapterID, VerseNumber, Rukuh, Sajdah, Manzil, HizbQuarter, VerseText) VALUES (8, 14, '0', '0', '0', '0', 'ذلكم العذاب الذي عجَّلته لكم -أيها الكافرون المخالفون لأوامر الله ورسوله في الدنيا- فذوقوه في الحياة الدنيا، ولكم في الآخرة عذاب النار.');
INSERT INTO Quran__Arabic_Tafseer_Muyassar (QuranChapterID, VerseNumber, Rukuh, Sajdah, Manzil, HizbQuarter, VerseText) VALUES (8, 15, '0', '0', '0', '0', 'يا أيها الذين صَدَّقوا الله ورسوله وعملوا بشرعه، إذا قابلتم الذين كفروا في القتال متقاربين منكم فلا تُوَلُّوهم ظهوركم، فتنهزموا عنهم، ولكن اثبتوا لهم، فإن الله معكم وناصركم عليهم.');
INSERT INTO Quran__Arabic_Tafseer_Muyassar (QuranChapterID, VerseNumber, Rukuh, Sajdah, Manzil, HizbQuarter, VerseText) VALUES (8, 16, '0', '0', '0', '0', 'ومن يُوَلِّهم منكم ظهره وقت الزحف إلا منعطفًا لمكيدة الكفار أو منحازًا إلى جماعة المسلمين حاضري الحرب حيث كانوا، فقد استحق الغضب من الله، ومقامه جهنم، وبئس المصير والمنقلب.');
INSERT INTO Quran__Arabic_Tafseer_Muyassar (QuranChapterID, VerseNumber, Rukuh, Sajdah, Manzil, HizbQuarter, VerseText) VALUES (8, 17, '0', '0', '0', '0', 'فلم تقتلوا -أيها المؤمنون- المشركين يوم "بدر"، ولكن الله قتلهم، حيث أعانكم على ذلك، وما رميت حين رميت -أيها النبي- ولكن الله رمى، حيث أوصل الرمية التي رميتها إلى وجوه المشركين؛ وليختبر المؤمنين بالله ورسوله ويوصلهم بالجهاد إلى أعلى الدرجات، ويعرِّفهم نعمته عليهم، فيشكروا له سبحانه على ذلك. إن الله سميع لدعائكم وأقوالكم ما أسررتم به وما أعلنتم، عليم بما فيه صلاح عباده.');
INSERT INTO Quran__Arabic_Tafseer_Muyassar (QuranChapterID, VerseNumber, Rukuh, Sajdah, Manzil, HizbQuarter, VerseText) VALUES (8, 18, '0', '0', '0', '0', 'هذا الفعل مِن قتل المشركين ورميهم حين انهزموا، والبلاء الحسن بنصر المؤمنين على أعدائهم، هو من الله للمؤمنين، وأن الله -فيما يُسْتقبل- مُضعِف ومُبطِل مكر الكافرين حتى يَذِلُّوا وينقادوا للحق أو يهلكوا.');
INSERT INTO Quran__Arabic_Tafseer_Muyassar (QuranChapterID, VerseNumber, Rukuh, Sajdah, Manzil, HizbQuarter, VerseText) VALUES (8, 19, '1', '0', '0', '0', 'إن تطلبوا -أيها الكفار- من الله أن يوقع بأسه وعذابه على المعتدين الظالمين فقد أجاب الله طلبكم، حين أوقع بكم مِن عقابه ما كان نكالا لكم وعبرة للمتقين، فإن تنتهوا -أيها الكفار- عن الكفر بالله ورسوله وقتال نبيه محمد صلى الله عليه وسلم، فهو خير لكم في دنياكم وأخراكم، وإن تعودوا إلى الحرب وقتال محمد صلى الله عليه وسلم وقتال أتباعه المؤمنين نَعُدْ بهزيمتكم كما هُزمتم يوم "بدر"، ولن تغني عنكم جماعتكم شيئًا، كما لم تغن عنكم يوم "بدر" مع كثرة عددكم وعتادكم وقلة عدد المؤمنين وعدتهم، وأن الله مع المؤمنين بتأييده ونصره.');
INSERT INTO Quran__Arabic_Tafseer_Muyassar (QuranChapterID, VerseNumber, Rukuh, Sajdah, Manzil, HizbQuarter, VerseText) VALUES (8, 20, '0', '0', '0', '0', 'يا أيها الذين صدَّقوا الله ورسوله أطيعوا الله ورسوله فيما أمركم به ونهاكم عنه، ولا تتركوا طاعة الله وطاعة رسوله، وأنتم تسمعون ما يتلى عليكم في القرآن من الحجج والبراهين.');
INSERT INTO Quran__Arabic_Tafseer_Muyassar (QuranChapterID, VerseNumber, Rukuh, Sajdah, Manzil, HizbQuarter, VerseText) VALUES (8, 21, '0', '0', '0', '0', 'ولا تكونوا أيها المؤمنون في مخالفة الله ورسوله محمد صلى الله عليه وسلم كالمشركين والمنافقين الذين إذا سمعوا كتاب الله يتلى عليهم قالوا: سمعنا بآذاننا، وهم في الحقيقة لا يتدبرون ما سمعوا، ولا يفكرون فيه.');
INSERT INTO Quran__Arabic_Tafseer_Muyassar (QuranChapterID, VerseNumber, Rukuh, Sajdah, Manzil, HizbQuarter, VerseText) VALUES (8, 22, '0', '0', '0', '1', 'إنَّ شر ما دبَّ على الأرض -مِنْ خَلْق الله- عند الله الصمُّ الذين انسدَّت آذانهم عن سماع الحق فلا يسمعون، البكم الذين خرست ألسنتهم عن النطق به فلا ينطقون، هؤلاء هم الذين لا يعقلون عن الله أمره ونهيه.');
INSERT INTO Quran__Arabic_Tafseer_Muyassar (QuranChapterID, VerseNumber, Rukuh, Sajdah, Manzil, HizbQuarter, VerseText) VALUES (8, 23, '0', '0', '0', '0', 'ولو علم الله في هؤلاء خيرًا لأسمعهم مواعظ القرآن وعبره حتى يعقلوا عن الله عز وجل حججه وبراهينه، ولكنه علم أنه لا خير فيهم وأنهم لا يؤمنون، ولو أسمعهم -على الفرض والتقدير- لتولَّوا عن الإيمان قصدًا وعنادًا بعد فهمهم له، وهم معرضون عنه، لا التفات لهم إلى الحق بوجه من الوجوه.');
INSERT INTO Quran__Arabic_Tafseer_Muyassar (QuranChapterID, VerseNumber, Rukuh, Sajdah, Manzil, HizbQuarter, VerseText) VALUES (8, 24, '0', '0', '0', '0', 'يا أيها الذين صدِّقوا بالله ربًا وبمحمد نبيًا ورسولا استجيبوا لله وللرسول بالطاعة إذا دعاكم لما يحييكم من الحق، ففي الاستجابة إصلاح حياتكم في الدنيا والآخرة، واعلموا -أيها المؤمنون- أن الله تعالى هو المتصرف في جميع الأشياء، والقادر على أن يحول بين الإنسان وما يشتهيه قلبه، فهو سبحانه الذي ينبغي أن يستجاب له إذا دعاكم؛ إذ بيده ملكوت كل شيء، واعلموا أنكم تُجمعون ليوم لا ريب فيه، فيجازي كلا بما يستحق.');
INSERT INTO Quran__Arabic_Tafseer_Muyassar (QuranChapterID, VerseNumber, Rukuh, Sajdah, Manzil, HizbQuarter, VerseText) VALUES (8, 25, '0', '0', '0', '0', 'واحذروا -أيها المؤمنون- اختبارًا ومحنة يُعَمُّ بها المسيء وغيره لا يُخَص بها أهل المعاصي ولا مَن باشر الذنب، بل تصيب الصالحين معهم إذا قدروا على إنكار الظلم ولم ينكروه، واعلموا أن الله شديد العقاب لمن خالف أمره ونهيه.');
INSERT INTO Quran__Arabic_Tafseer_Muyassar (QuranChapterID, VerseNumber, Rukuh, Sajdah, Manzil, HizbQuarter, VerseText) VALUES (8, 26, '0', '0', '0', '0', 'واذكروا أيها المؤمنون نِعَم الله عليكم إذ أنتم بـ"مكة" قليلو العدد مقهورون، تخافون أن يأخذكم الكفار بسرعة، فجعل لكم مأوى تأوون إليه وهو "المدينة"، وقوَّاكم بنصره عليهم يوم "بدر"، وأطعمكم من الطيبات -التي من جملتها الغنائم-؛ لكي تشكروا له على ما رزقكم وأنعم به عليكم.');
INSERT INTO Quran__Arabic_Tafseer_Muyassar (QuranChapterID, VerseNumber, Rukuh, Sajdah, Manzil, HizbQuarter, VerseText) VALUES (8, 27, '0', '0', '0', '0', 'يا أيها الذين صدَّقوا الله ورسوله وعملوا بشرعه لا تخونوا الله ورسوله بترك ما أوجبه الله عليكم وفِعْل ما نهاكم عنه، ولا تفرطوا فيما ائتمنكم الله عليه، وأنتم تعلمون أنه أمانة يجب الوفاء بها.');
INSERT INTO Quran__Arabic_Tafseer_Muyassar (QuranChapterID, VerseNumber, Rukuh, Sajdah, Manzil, HizbQuarter, VerseText) VALUES (8, 28, '1', '0', '0', '0', 'واعلموا -أيها المؤمنون- أن أموالكم التي استخلفكم الله فيها، وأولادكم الذين وهبهم الله لكم اختبار من الله وابتلاء لعباده؛ ليعلم أيشكرونه عليها ويطيعونه فيها، أو ينشغلون بها عنه؟ واعلموا أن الله عنده خير وثواب عظيم لمن اتقاه وأطاعه.');
INSERT INTO Quran__Arabic_Tafseer_Muyassar (QuranChapterID, VerseNumber, Rukuh, Sajdah, Manzil, HizbQuarter, VerseText) VALUES (8, 29, '0', '0', '0', '0', 'يا أيها الذين صدَّقوا الله ورسوله وعملوا بشرعه إن تتقوا الله بفعل أوامره واجتناب نواهيه يجعل لكم فصلا بين الحق والباطل، ويَمحُ عنكم ما سلف من ذنوبكم ويسترها عليكم، فلا يؤاخذكم بها. والله ذو الفضل العظيم.');
INSERT INTO Quran__Arabic_Tafseer_Muyassar (QuranChapterID, VerseNumber, Rukuh, Sajdah, Manzil, HizbQuarter, VerseText) VALUES (8, 30, '0', '0', '0', '0', 'واذكر -أيها الرسول- حين يكيد لك مشركو قومك بـ"مكَّة"؛ ليحبسوك أو يقتلوك أو ينفوك من بلدك. ويكيدون لك، وردَّ الله مكرهم عليهم جزاء لهم، ويمكر الله، والله خير الماكرين.');
INSERT INTO Quran__Arabic_Tafseer_Muyassar (QuranChapterID, VerseNumber, Rukuh, Sajdah, Manzil, HizbQuarter, VerseText) VALUES (8, 31, '0', '0', '0', '0', 'وإذا تتلى على هؤلاء الذين كفروا بالله آيات القرآن العزيز قالوا جهلا منهم وعنادًا للحق: قد سمعنا هذا من قبل، لو نشاء لقلنا مثل هذا القرآن، ما هذا القرآن الذي تتلوه علينا -يا محمد- إلا أكاذيب الأولين.');
INSERT INTO Quran__Arabic_Tafseer_Muyassar (QuranChapterID, VerseNumber, Rukuh, Sajdah, Manzil, HizbQuarter, VerseText) VALUES (8, 32, '0', '0', '0', '0', 'واذكر -أيها الرسول- قول المشركين من قومك داعين الله: إن كان ما جاء به محمد هو الحق مِن عندك فأمطر علينا حجارة من السماء، أو ائتنا بعذاب شديد موجع.');
INSERT INTO Quran__Arabic_Tafseer_Muyassar (QuranChapterID, VerseNumber, Rukuh, Sajdah, Manzil, HizbQuarter, VerseText) VALUES (8, 33, '0', '0', '0', '0', 'وما كان الله سبحانه وتعالى ليعذِّب هؤلاء المشركين، وأنت -أيها الرسول- بين ظهرانَيْهم، وما كان الله معذِّبهم، وهم يستغفرون من ذنوبهم.');
INSERT INTO Quran__Arabic_Tafseer_Muyassar (QuranChapterID, VerseNumber, Rukuh, Sajdah, Manzil, HizbQuarter, VerseText) VALUES (8, 34, '0', '0', '0', '0', 'وكيف لا يستحقُّون عذاب الله، وهم يصدون أولياءه المؤمنين عن الطواف بالكعبة والصلاة في المسجد الحرام؟ وما كانوا أولياء الله، إنْ أولياء الله إلا الذين يتقونه بأداء فرائضه واجتناب معاصيه، ولكن أكثر الكفار لا يعلمون؛ فلذلك ادَّعوا لأنفسهم أمرًا، غيرهم أولى به.');
INSERT INTO Quran__Arabic_Tafseer_Muyassar (QuranChapterID, VerseNumber, Rukuh, Sajdah, Manzil, HizbQuarter, VerseText) VALUES (8, 35, '0', '0', '0', '0', 'وما كان صلاتهم عند المسجد الحرام إلا صفيرًا وتصفيقًا. فذوقوا عذاب القتل والأسر يوم "بدر"؛ بسبب جحودكم وأفعالكم التي لا يُقْدم عليها إلا الكفرة، الجاحدون توحيد ربهم ورسالة نبيهم.');
INSERT INTO Quran__Arabic_Tafseer_Muyassar (QuranChapterID, VerseNumber, Rukuh, Sajdah, Manzil, HizbQuarter, VerseText) VALUES (8, 36, '0', '0', '0', '0', 'إن الذين جحدوا وحدانية الله وعصوا رسوله ينفقون أموالهم فيعطونها أمثالهم من المشركين وأهل الضلال، ليصدوا عن سبيل الله ويمنعوا المؤمنين عن الإيمان بالله ورسوله، فينفقون أموالهم في ذلك، ثم تكون عاقبة نفقتهم تلك ندامة وحسرة عليهم؛ لأن أموالهم تذهب، ولا يظفرون بما يأمُلون مِن إطفاء نور الله والصد عن سبيله، ثم يهزمهم المؤمنون آخر الأمر. والذين كفروا إلى جهنم يحشرون فيعذبون فيها.');
INSERT INTO Quran__Arabic_Tafseer_Muyassar (QuranChapterID, VerseNumber, Rukuh, Sajdah, Manzil, HizbQuarter, VerseText) VALUES (8, 37, '1', '0', '0', '0', 'يحشر الله ويخزي هؤلاء الذين كفروا بربهم، وأنفقوا أموالهم لمنع الناس عن الإيمان بالله والصد عن سبيله؛ ليميز الله تعالى الخبيث من الطيب، ويجعل الله المال الحرام الذي أُنفق للصدِّ عن دين الله بعضه فوق بعض متراكمًا متراكبًا، فيجعله في نار جهنم، هؤلاء الكفار هم الخاسرون في الدنيا والآخرة.');
INSERT INTO Quran__Arabic_Tafseer_Muyassar (QuranChapterID, VerseNumber, Rukuh, Sajdah, Manzil, HizbQuarter, VerseText) VALUES (8, 38, '0', '0', '0', '0', 'قل -أيها الرسول- للذين جحدوا وحدانية الله مِن مشركي قومك: إن ينزجروا عن الكفر وعداوة النبي صلى الله عليه وسلم، ويرجعوا إلى الإيمان بالله وحده وعدم قتال الرسول والمؤمنين، يغفر الله لهم ما سبق من الذنوب، فالإسلام يجُبُّ ما قبله. وإن يَعُدْ هؤلاء المشركون لقتالك -أيها الرسول- بعد الوقعة التي أوقعتها بهم يوم "بدر" فقد سبقت طريقة الأولين، وهي أنهم إذا كذبوا واستمروا على عنادهم أننا نعاجلهم بالعذاب والعقوبة.');
INSERT INTO Quran__Arabic_Tafseer_Muyassar (QuranChapterID, VerseNumber, Rukuh, Sajdah, Manzil, HizbQuarter, VerseText) VALUES (8, 39, '0', '0', '0', '0', 'وقاتلوا -أيها المؤمنون- المشركين حتى لا يكون شِرْكٌ وصدٌّ عن سبيل الله؛ ولا يُعْبَدَ إلا الله وحده لا شريك له، فيرتفع البلاء عن عباد الله في الأرض، وحتى يكون الدين والطاعة والعبادة كلها لله خالصة دون غيره، فإن انزجروا عن فتنة المؤمنين وعن الشرك بالله وصاروا إلى الدين الحق معكم، فإن الله لا يخفى عليه ما يعملون مِن ترك الكفر والدخول في الإسلام.');
INSERT INTO Quran__Arabic_Tafseer_Muyassar (QuranChapterID, VerseNumber, Rukuh, Sajdah, Manzil, HizbQuarter, VerseText) VALUES (8, 40, '0', '0', '0', '0', 'وإن أعرض هؤلاء المشركون عمَّا دعوتموهم إليه -أيها المؤمنون- من الإيمان بالله ورسوله وترك قتالكم، وأبَوْا إلا الإصرار على الكفر وقتالكم، فأيقِنوا أن الله معينكم وناصركم عليهم. نِعْمَ المعين والناصر لكم ولأوليائه على أعدائكم.');
INSERT INTO Quran__Arabic_Tafseer_Muyassar (QuranChapterID, VerseNumber, Rukuh, Sajdah, Manzil, HizbQuarter, VerseText) VALUES (8, 41, '0', '0', '0', '1', 'واعلموا -أيها المؤمنون- أن ما ظَفِرتم به مِن عدوكم بالجهاد في سبيل الله فأربعة أخماسه للمقاتلين الذين حضروا المعركة، والخمس الباقي يجزَّأُ خمسة أقسام: الأول لله وللرسول، فيجعل في مصالح المسلمين العامة، والثاني لذوي قرابة رسول الله صلى الله عليه وسلم، وهم بنو هاشم وبنو المطلب، جُعِل لهم الخمس مكان الصدقة فإنها لا تحلُّ لهم، والثالث لليتامى، والرابع للمساكين، والخامس للمسافر الذي انقطعت به النفقة، إن كنتم مقرِّين بتوحيد الله مطيعين له، مؤمنين بما أنزل على عبده محمد صلى الله عليه وسلم من الآيات والمدد والنصر يوم فَرَق بين الحق والباطل بـ"بدر"، يوم التقى جَمْعُ المؤمنين وجَمْعُ المشركين. والله على كل شيء قدير لا يعجزه شيء.');
INSERT INTO Quran__Arabic_Tafseer_Muyassar (QuranChapterID, VerseNumber, Rukuh, Sajdah, Manzil, HizbQuarter, VerseText) VALUES (8, 42, '0', '0', '0', '0', 'واذكروا حينما كنتم على جانب الوادي الأقرب إلى "المدينة"، وعدوكم نازل بجانب الوادي الأقصى، وعِير التجارة في مكان أسفل منكم إلى ساحل "البحر الأحمر"، ولو حاولتم أن تضعوا موعدًا لهذا اللقاء لاختلفتم، ولكنَّ الله جمعكم على غير ميعاد؛ ليقضي أمرًا كان مفعولا بنصر أوليائه، وخِذْلان أعدائه بالقتل والأسر؛ وذلك ليهلك من هلك منهم عن حجة لله ثبتت له فعاينها وقطعت عذره، وليحيا مَن حيَّ عن حجة لله قد ثبتت وظهرت له. وإن الله لسميع لأقوال الفريقين، لا يخفى عليه شيء، عليم بنيَّاتهم.');
INSERT INTO Quran__Arabic_Tafseer_Muyassar (QuranChapterID, VerseNumber, Rukuh, Sajdah, Manzil, HizbQuarter, VerseText) VALUES (8, 43, '0', '0', '0', '0', 'واذكر -أيها النبي- حينما أراك الله قلة عدد عدوك في منامك، فأخبرت المؤمنين بذلك، فقوِيت قلوبهم، واجترؤوا على حربهم، ولو أراك ربك كثرة عددهم لتردد أصحابك في ملاقاتهم، وجَبُنتم واختلفتم في أمر القتال، ولكن الله سلَّم من الفشل، ونجَّى من عاقبة ذلك. إنه عليم بخفايا القلوب وطبائع النفوس.');
INSERT INTO Quran__Arabic_Tafseer_Muyassar (QuranChapterID, VerseNumber, Rukuh, Sajdah, Manzil, HizbQuarter, VerseText) VALUES (8, 44, '1', '0', '0', '0', 'واذكر أيضًا حينما برز الأعداء إلى أرض المعركة فرأيتموهم قليلا فاجترأتم عليهم، وقلَّلكم في أعينهم، ليتركوا الاستعداد لحربكم؛ ليقضي الله أمرًا كان مفعولا فيتحقق وَعْدُ الله لكم بالنصر والغلبة، فكانت كلمة الله هي العليا وكلمة الذين كفروا السفلى. وإلى الله مصير الأمور كلها، فيجازي كلا بما يستحق.');
INSERT INTO Quran__Arabic_Tafseer_Muyassar (QuranChapterID, VerseNumber, Rukuh, Sajdah, Manzil, HizbQuarter, VerseText) VALUES (8, 45, '0', '0', '0', '0', 'يا أيها الذين صدَّقوا الله ورسوله وعملوا بشرعه، إذا لقيتم جماعة من أهل الكفر قد استعدوا لقتالكم، فاثبتوا ولا تنهزموا عنهم، واذكروا الله كثيرًا داعين مبتهلين لإنزال النصر عليكم والظَّفَر بعدوكم؛ لكي تفوزوا.');
INSERT INTO Quran__Arabic_Tafseer_Muyassar (QuranChapterID, VerseNumber, Rukuh, Sajdah, Manzil, HizbQuarter, VerseText) VALUES (8, 46, '0', '0', '0', '0', 'والتزموا طاعة الله وطاعة رسوله في كل أحوالكم، ولا تختلفوا فتتفرق كلمتكم وتختلف قلوبكم، فتضعفوا وتذهب قوتكم ونصركم، واصبروا عند لقاه العدو. إن الله مع الصابرين بالعون والنصر والتأييد، ولن يخذلهم.');
INSERT INTO Quran__Arabic_Tafseer_Muyassar (QuranChapterID, VerseNumber, Rukuh, Sajdah, Manzil, HizbQuarter, VerseText) VALUES (8, 47, '0', '0', '0', '0', 'ولا تكونوا مثل المشركين الذين خرجوا من بلدهم كبرًا ورياءً؛ ليمنعوا الناس عن الدخول في دين الله. والله بما يعملون محيط لا يغيب عنه شيء.');
INSERT INTO Quran__Arabic_Tafseer_Muyassar (QuranChapterID, VerseNumber, Rukuh, Sajdah, Manzil, HizbQuarter, VerseText) VALUES (8, 48, '1', '0', '0', '0', 'واذكروا حين حسَّن الشيطان للمشركين ما جاؤوا له وما همُّوا به، وقال لهم: لن يغلبكم أحد اليوم، فإني ناصركم، فلما تقابل الفريقان: المشركون ومعهم الشيطان، والمسلمون ومعهم الملائكة، رجع الشيطان مُدْبرًا، وقال للمشركين: إني بريء منكم، إني أرى ما لا ترون من الملائكة الذين جاؤوا مددًا للمسلمين، إني أخاف الله، فخذلهم وتبرأ منهم. والله شديد العقاب لمن عصاه ولم يتب توبة نصوحًا.');
INSERT INTO Quran__Arabic_Tafseer_Muyassar (QuranChapterID, VerseNumber, Rukuh, Sajdah, Manzil, HizbQuarter, VerseText) VALUES (8, 49, '0', '0', '0', '0', 'واذكروا حين يقول أهل الشرك والنفاق ومرضى القلوب، وهم يرون قلة المسلمين وكثرة عدوهم: غرَّ هؤلاء المسلمين دينُهم، فأوردهم هذه الموارد، ولم يدرك هؤلاء المنافقون أنه من يتوكل على الله ويثق بوعده فإن الله لن يخذله، فإن الله عزيز لا يعجزه شيء، حكيم في تدبيره وصنعه.');
INSERT INTO Quran__Arabic_Tafseer_Muyassar (QuranChapterID, VerseNumber, Rukuh, Sajdah, Manzil, HizbQuarter, VerseText) VALUES (8, 50, '0', '0', '0', '0', 'ولو تعاين -أيها الرسول- حال قبض الملائكة أرواح الكفار وانتزاعها، وهم يضربون وجوههم في حال إقبالهم، ويضربون ظهورهم في حال فرارهم، ويقولون لهم: ذوقوا العذاب المحرق، لرأيت أمرًا عظيمًا، وهذا السياق وإن كان سببه وقعة "بدر"، ولكنه عام في حق كلِّ كافر.');
INSERT INTO Quran__Arabic_Tafseer_Muyassar (QuranChapterID, VerseNumber, Rukuh, Sajdah, Manzil, HizbQuarter, VerseText) VALUES (8, 51, '0', '0', '0', '0', 'ذلك الجزاء الذي أصاب المشركين فبسبب أعمالهم السيئة في حياتهم الدنيا، ولا يظلم الله أحدًا من خَلْقه مثقال ذرة، بل هو الحَكَمُ العدل الذي لا يجور.');
INSERT INTO Quran__Arabic_Tafseer_Muyassar (QuranChapterID, VerseNumber, Rukuh, Sajdah, Manzil, HizbQuarter, VerseText) VALUES (8, 52, '0', '0', '0', '0', 'إنَّ ما نزل بالمشركين يومئذ سُنَّة الله في عقاب الطغاة من الأمم السابقة من أمثال فرعون والسابقين له، عندما كذَّبوا رسل الله وجحدوا آياته، فإن الله أنزل بهم عقابه بسبب ذنوبهم. إن الله قوي لا يُقْهر، شديد العقاب لمن عصاه ولم يتب من ذنبه.');
INSERT INTO Quran__Arabic_Tafseer_Muyassar (QuranChapterID, VerseNumber, Rukuh, Sajdah, Manzil, HizbQuarter, VerseText) VALUES (8, 53, '0', '0', '0', '0', 'ذلك الجزاء السيِّئ بأن الله إذا أنعم على قوم نعمة لم يسلبها منهم حتى يغيِّروا حالهم الطيبة إلى حال سيئة، وأن الله سميع لأقوال خلقه، عليم بأحوالهم، فيجري عليهم ما اقتضاه علمه ومشيئته.');
INSERT INTO Quran__Arabic_Tafseer_Muyassar (QuranChapterID, VerseNumber, Rukuh, Sajdah, Manzil, HizbQuarter, VerseText) VALUES (8, 54, '0', '0', '0', '0', 'شأن هؤلاء الكافرين في ذلك كشأن آل فرعون الذين كذبوا موسى، وشأن الذين كذبوا رسلهم من الأمم السابقة فأهلكهم الله بسبب ذنوبهم، وأغرق آل فرعون في البحر، وكل منهم كان فاعلا ما لم يكن له فِعْلُه من تكذيبهم رسل الله وجحودهم آياته، وإشراكهم في العبادة غيره.');
INSERT INTO Quran__Arabic_Tafseer_Muyassar (QuranChapterID, VerseNumber, Rukuh, Sajdah, Manzil, HizbQuarter, VerseText) VALUES (8, 55, '0', '0', '0', '0', 'إن شر ما دبَّ على الأرض عند الله الكفار المصرُّون على الكفر، فهم لا يصدقون رسل الله، ولا يُقرون بوحدانيته، ولا يتبعون شرعه.');
INSERT INTO Quran__Arabic_Tafseer_Muyassar (QuranChapterID, VerseNumber, Rukuh, Sajdah, Manzil, HizbQuarter, VerseText) VALUES (8, 56, '0', '0', '0', '0', 'مِن أولئك الأشرار اليهود الذين دخلوا معك في المعاهدات بأن لا يحاربوك ولا يظاهروا عليك أحدًا، ثم ينقضون عهدهم المرة تلو المرة، وهم لا يخافون الله.');
INSERT INTO Quran__Arabic_Tafseer_Muyassar (QuranChapterID, VerseNumber, Rukuh, Sajdah, Manzil, HizbQuarter, VerseText) VALUES (8, 57, '0', '0', '0', '0', 'فإن واجهت هؤلاء الناقضين للعهود والمواثيق في المعركة، فأنزِلْ بهم من العذاب ما يُدْخل الرعب في قلوب الآخرين، ويشتت جموعهم؛ لعلهم يذّكرون، فلا يجترئون على مثل الذي أقدم عليه السابقون.');
INSERT INTO Quran__Arabic_Tafseer_Muyassar (QuranChapterID, VerseNumber, Rukuh, Sajdah, Manzil, HizbQuarter, VerseText) VALUES (8, 58, '1', '0', '0', '0', 'وإن خفت -أيها الرسول- من قومٍ خيانة ظهرت بوادرها فألق إليهم عهدهم، كي يكون الطرفان مستويين في العلم بأنه لا عهد بعد اليوم. إن الله لا يحب الخائنين في عهودهم الناقضين للعهد والميثاق.');
INSERT INTO Quran__Arabic_Tafseer_Muyassar (QuranChapterID, VerseNumber, Rukuh, Sajdah, Manzil, HizbQuarter, VerseText) VALUES (8, 59, '0', '0', '0', '0', 'ولا يظنن الذين جحدوا آيات الله أنهم فاتوا ونجَوْا، وأن الله لا يقدر عليهم، إنهم لن يُفْلِتوا من عذاب الله.');
INSERT INTO Quran__Arabic_Tafseer_Muyassar (QuranChapterID, VerseNumber, Rukuh, Sajdah, Manzil, HizbQuarter, VerseText) VALUES (8, 60, '0', '0', '0', '0', 'وأعدُّوا - يا معشر المسلمين - لمواجهة أعدائكم كل ما تقدرون عليه مِن عدد وعدة، لتُدْخلوا بذلك الرهبة في قلوب أعداء الله وأعدائكم المتربصين بكم، وتخيفوا آخرين لا تظهر لكم عداوتهم الآن، لكن الله يعلمهم ويعلم ما يضمرونه. وما تبذلوا من مال وغيره في سبيل الله قليلا أو كثيرًا يخلفه الله عليكم في الدنيا، ويدخر لكم ثوابه إلى يوم القيامة، وأنتم لا تُنْقصون من أجر ذلك شيئًا.');
INSERT INTO Quran__Arabic_Tafseer_Muyassar (QuranChapterID, VerseNumber, Rukuh, Sajdah, Manzil, HizbQuarter, VerseText) VALUES (8, 61, '0', '0', '0', '1', 'وإن مالوا إلى ترك الحرب ورغبوا في مسالمتكم فمِلْ إلى ذلك -أيها النبي- وفَوِّضْ أمرك إلى الله، وثق به. إنه هو السميع لأقوالهم، العليم بنيَّاتهم.');
INSERT INTO Quran__Arabic_Tafseer_Muyassar (QuranChapterID, VerseNumber, Rukuh, Sajdah, Manzil, HizbQuarter, VerseText) VALUES (8, 62, '0', '0', '0', '0', 'وإن أراد الذين عاهدوك المكر بك فإن الله سيكفيك خداعهم؛ إنه هو الذي أنزل عليك نصره وقوَّاك بالمؤمنين من المهاجرين والأنصار، وجَمَع بين قلوبهم بعد التفرق، لو أنفقت مال الدنيا على جمع قلوبهم ما استطعت إلى ذلك سبيلا ولكن الله جمع بينها على الإيمان فأصبحوا إخوانًا متحابين، إنه عزيز في مُلْكه، حكيم في أمره وتدبيره.');
INSERT INTO Quran__Arabic_Tafseer_Muyassar (QuranChapterID, VerseNumber, Rukuh, Sajdah, Manzil, HizbQuarter, VerseText) VALUES (8, 63, '0', '0', '0', '0', 'وإن أراد الذين عاهدوك المكر بك فإن الله سيكفيك خداعهم؛ إنه هو الذي أنزل عليك نصره وقوَّاك بالمؤمنين من المهاجرين والأنصار، وجَمَع بين قلوبهم بعد التفرق، لو أنفقت مال الدنيا على جمع قلوبهم ما استطعت إلى ذلك سبيلا ولكن الله جمع بينها على الإيمان فأصبحوا إخوانًا متحابين، إنه عزيز في مُلْكه، حكيم في أمره وتدبيره.');
INSERT INTO Quran__Arabic_Tafseer_Muyassar (QuranChapterID, VerseNumber, Rukuh, Sajdah, Manzil, HizbQuarter, VerseText) VALUES (8, 64, '1', '0', '0', '0', 'يا أيها النبي إن الله كافيك، وكافي الذين معك من المؤمنين شرَّ أعدائكم.');
INSERT INTO Quran__Arabic_Tafseer_Muyassar (QuranChapterID, VerseNumber, Rukuh, Sajdah, Manzil, HizbQuarter, VerseText) VALUES (8, 65, '0', '0', '0', '0', 'يا أيها النبي حُثَّ المؤمنين بك على القتال، إن يكن منكم عشرون صابرون عند لقاء العدو يغلبوا مائتين منهم، فإن يكن منكم مائة مجاهدة صابرة يغلبوا ألفًا من الكفار؛ لأنهم قوم لا عِلْم ولا فهم عندهم لما أعدَّ الله للمجاهدين في سبيله، فهم يقاتلون من أجل العلو في الأرض والفساد فيها.');
INSERT INTO Quran__Arabic_Tafseer_Muyassar (QuranChapterID, VerseNumber, Rukuh, Sajdah, Manzil, HizbQuarter, VerseText) VALUES (8, 66, '0', '0', '0', '0', 'الآن خفف الله عنكم أيها المؤمنون لما فيكم من الضعف، فإن يكن منكم مائة صابرة يغلبوا مائتين من الكافرين، وإن يكن منكم ألف يغلبوا ألفين منهم بإذن الله تعالى. والله مع الصابرين بتأييده ونصره.');
INSERT INTO Quran__Arabic_Tafseer_Muyassar (QuranChapterID, VerseNumber, Rukuh, Sajdah, Manzil, HizbQuarter, VerseText) VALUES (8, 67, '0', '0', '0', '0', 'لا ينبغي لنبي أن يكون له أسرى مِن أعدائه حتى يبالغ في القتل؛ لإدخال الرعب في قلوبهم ويوطد دعائم الدين، تريدون -يا معشر المسلمين- بأخذكم الفداء من أسرى "بدر" متاع الدنيا، والله يريد إظهار دينه الذي به تدرك الآخرة. والله عزيز لا يُقْهر، حكيم في شرعه.');
INSERT INTO Quran__Arabic_Tafseer_Muyassar (QuranChapterID, VerseNumber, Rukuh, Sajdah, Manzil, HizbQuarter, VerseText) VALUES (8, 68, '0', '0', '0', '0', 'لولا كتاب من الله سبق به القضاء والقدر بإباحة الغنيمة وفداء الأسرى لهذه الأمة، لنالكم عذاب عظيم بسبب أخْذكم الغنيمة والفداء قبل أن ينزل بشأنهما تشريع.');
INSERT INTO Quran__Arabic_Tafseer_Muyassar (QuranChapterID, VerseNumber, Rukuh, Sajdah, Manzil, HizbQuarter, VerseText) VALUES (8, 69, '1', '0', '0', '0', 'فكلوا من الغنائم وفداء الأسرى فهو حلال طيب، وحافظوا على أحكام دين الله وتشريعاته. إن الله غفور لعباده، رحيم بهم.');
INSERT INTO Quran__Arabic_Tafseer_Muyassar (QuranChapterID, VerseNumber, Rukuh, Sajdah, Manzil, HizbQuarter, VerseText) VALUES (8, 70, '0', '0', '0', '0', 'يا أيها النبي قل لمن أسرتموهم في "بدر": لا تأسوا على الفداء الذي أخذ منكم، إن يعلم الله تعالى في قلوبكم خيرًا يؤتكم خيرًا مما أُخذ منكم من المال بأن يُيَسِّر لكم من فضله خيرًا كثيرًا -وقد أنجز الله وعده للعباس رضي الله عنه وغيره-، ويغفر لكم ذنوبكم. والله سبحانه غفور لذنوب عباده إذا تابوا، رحيم بهم.');
INSERT INTO Quran__Arabic_Tafseer_Muyassar (QuranChapterID, VerseNumber, Rukuh, Sajdah, Manzil, HizbQuarter, VerseText) VALUES (8, 71, '0', '0', '0', '0', 'وإن يرد الذين أَطْلَقْتَ سراحهم -أيها النبي- من الأسرى الغدر بك مرة أخرى فلا تَيْئسْ، فقد خانوا الله من قبل وحاربوك، فنصرك الله عليهم. والله عليم بما تنطوي عليه الصدور، حكيم في تدبير شؤون عباده.');
INSERT INTO Quran__Arabic_Tafseer_Muyassar (QuranChapterID, VerseNumber, Rukuh, Sajdah, Manzil, HizbQuarter, VerseText) VALUES (8, 72, '0', '0', '0', '0', 'إن الذين صدَّقوا الله، ورسوله وعملوا بشرعه، وهاجروا إلى دار الإسلام، أو بلد يتمكنون فيه من عبادة ربهم، وجاهدوا في سبيل الله بالمال والنفس، والذين أنزلوا المهاجرين في دورهم، وواسوهم بأموالهم، ونصروا دين الله، أولئك بعضهم نصراء بعض. أما الذين آمنوا ولم يهاجروا من دار الكفر فلستم مكلفين بحمايتهم ونصرتهم حتى يهاجروا، وإن وقع عليهم ظلم من الكفار فطلبوا نصرتكم فاستجيبوا لهم، إلا على قوم بينكم وبينهم عهد مؤكد لم ينقضوه. والله بصير بأعمالكم، بجزي كلا على قدر نيته وعمله.');
INSERT INTO Quran__Arabic_Tafseer_Muyassar (QuranChapterID, VerseNumber, Rukuh, Sajdah, Manzil, HizbQuarter, VerseText) VALUES (8, 73, '0', '0', '0', '0', 'والذين كفروا بعضهم نصراء بعض، وإن لم تكونوا -أيها المؤمنون- نصراء بعض تكن في الأرض فتنة للمؤمنين عن دين الله، وفساد عريض بالصد عن سبيل الله وتقوية دعائم الكفر.');
INSERT INTO Quran__Arabic_Tafseer_Muyassar (QuranChapterID, VerseNumber, Rukuh, Sajdah, Manzil, HizbQuarter, VerseText) VALUES (8, 74, '0', '0', '0', '0', 'والذين آمنوا بالله ورسوله، وتركوا ديارهم قاصدين دار الإسلام أو بلدًا يتمكنون فيه من عبادة ربهم، وجاهدوا لإعلاء كلمة الله، والذين نصروا إخوانهم المهاجرين وآووهم وواسوهم بالمال والتأييد، أولئك هم المؤمنون الصادقون حقًا، لهم مغفرة لذنوبهم، ورزق كريم واسع في جنات النعيم.');
INSERT INTO Quran__Arabic_Tafseer_Muyassar (QuranChapterID, VerseNumber, Rukuh, Sajdah, Manzil, HizbQuarter, VerseText) VALUES (8, 75, '1', '0', '0', '0', 'والذين آمنوا مِن بعد هؤلاء المهاجرين والأنصار، وهاجروا وجاهدوا معكم في سبيل الله، فأولئك منكم -أيها المؤمنون- لهم ما لكم وعليهم ما عليكم، وأولو القرابة بعضهم أولى ببعض في التوارث في حكم الله من عامة المسلمين. إن الله بكل شيء عليم يعلم ما يصلح عباده مِن توريث بعضهم من بعض في القرابة والنسب دون التوارث بالحِلْف، وغير ذلك مما كان في أول الإسلام.');
INSERT INTO Quran__Arabic_Tafseer_Muyassar (QuranChapterID, VerseNumber, Rukuh, Sajdah, Manzil, HizbQuarter, VerseText) VALUES (9, 1, '0', '0', '0', '1', 'هذه براءة من الله ورسوله، وإعلان بالتخلي عن العهود التي كانت بين المسلمين والمشركين.');
INSERT INTO Quran__Arabic_Tafseer_Muyassar (QuranChapterID, VerseNumber, Rukuh, Sajdah, Manzil, HizbQuarter, VerseText) VALUES (9, 2, '0', '0', '0', '0', 'فسيروا -أيها المشركون- في الأرض مدَّة أربعة أشهر، تذهبون حيث شئتم آمنين من المؤمنين، واعلموا أنكم لن تُفْلِتوا من العقوبة، وأن الله مذل الكافرين ومورثهم العار في الدنيا، والنار في الآخرة. وهذه الآية لذوي العهود المطلقة غير المؤقتة، أو من له عهد دون أربعة أشهر، فيكمَّل له أربعة أشهر، أو مَن كان له عهد فنقضه.');
INSERT INTO Quran__Arabic_Tafseer_Muyassar (QuranChapterID, VerseNumber, Rukuh, Sajdah, Manzil, HizbQuarter, VerseText) VALUES (9, 3, '0', '0', '0', '0', 'وإعلام من الله ورسوله وإنذار إلى الناس يوم النحر أن الله بريء من المشركين، ورسوله بريء منهم كذلك. فإن رجعتم -أيها المشركون- إلى الحق وتركتم شرككم فهو خير لكم، وإن أعرضتم عن قَبول الحق وأبيتم الدخول في دين الله فاعلموا أنكم لن تُفْلِتوا من عذاب الله. وأنذر -أيها الرسول- هؤلاء المعرضين عن الإسلام عذاب الله الموجع.');
INSERT INTO Quran__Arabic_Tafseer_Muyassar (QuranChapterID, VerseNumber, Rukuh, Sajdah, Manzil, HizbQuarter, VerseText) VALUES (9, 4, '0', '0', '0', '0', 'ويُستثنى من الحكم السابق المشركون الذين دخلوا معكم في عهد محدد بمدة، ولم يخونوا العهد، ولم يعاونوا عليكم أحدا من الأعداء، فأكملوا لهم عهدهم إلى نهايته المحدودة. إن الله يحب المتقين الذين أدَّوا ما أمروا به، واتقوا الشرك والخيانة، وغير ذلك من المعاصي.');
INSERT INTO Quran__Arabic_Tafseer_Muyassar (QuranChapterID, VerseNumber, Rukuh, Sajdah, Manzil, HizbQuarter, VerseText) VALUES (9, 5, '0', '0', '0', '0', 'فإذا انقضت الأشهر الأربعة التي أمَّنتم فيها المشركين، فأعلنوا الحرب على أعداء الله حيث كانوا، واقصدوهم بالحصار في معاقلهم، وترصدوا لهم في طرقهم، فإن رجعوا عن كفرهم ودخلوا الإسلام والتزموا شرائعه من إقام الصلاة وإخراج الزكاة، فاتركوهم، فقد أصبحوا إخوانكم في الإسلام، إن الله غفور لمن تاب وأناب، رحيم بهم.');
INSERT INTO Quran__Arabic_Tafseer_Muyassar (QuranChapterID, VerseNumber, Rukuh, Sajdah, Manzil, HizbQuarter, VerseText) VALUES (9, 6, '1', '0', '0', '0', 'وإذا طلب أحد من المشركين الذين استبيحت دماؤهم وأموالهم الدخول في جوارك -أيها الرسول- ورغب في الأمان، فأجبه إلى طلبه حتى يسمع القرآن الكريم ويطَّلع على هدايته، ثم أَعِدْه من حيث أتى آمنًا؛ وذلك لإقامة الحجة عليه؛ ذلك بسبب أن الكفار قوم جاهلون بحقائق الإسلام، فربما اختاروه إذا زال الجهل عنهم.');
INSERT INTO Quran__Arabic_Tafseer_Muyassar (QuranChapterID, VerseNumber, Rukuh, Sajdah, Manzil, HizbQuarter, VerseText) VALUES (9, 7, '0', '0', '0', '0', 'لا ينبغي أن يكون للمشركين عهد عند الله وعند رسوله، إلا الذين عاهدتم عند المسجد الحرام في صلح (الحديبية) فما أقاموا على الوفاء بعهدكم فأقيموا لهم على مثل ذلك. إن الله يحب المتقين الموفِّين بعهودهم.');
INSERT INTO Quran__Arabic_Tafseer_Muyassar (QuranChapterID, VerseNumber, Rukuh, Sajdah, Manzil, HizbQuarter, VerseText) VALUES (9, 8, '0', '0', '0', '0', 'إن شأن المشركين أن يلتزموا بالعهود ما دامت الغلبة لغيرهم، أما إذا شعروا بالقوة على المؤمنين فإنهم لا يراعون القرابة ولا العهد، فلا يغرنكم منهم ما يعاملونكم به وقت الخوف منكم، فإنهم يقولون لكم كلامًا بألسنتهم؛ لترضوا عنهم، ولكن قلوبهم تأبى ذلك، وأكثرهم متمردون على الإسلام ناقضون للعهد.');
INSERT INTO Quran__Arabic_Tafseer_Muyassar (QuranChapterID, VerseNumber, Rukuh, Sajdah, Manzil, HizbQuarter, VerseText) VALUES (9, 9, '0', '0', '0', '0', 'استبدلوا بآيات الله عرض الدنيا التافه، فأعرضوا عن الحق ومنعوا الراغبين في الإسلام عن الدخول فيه، لقد قَبُح فعلهم، وساء صنيعهم.');
INSERT INTO Quran__Arabic_Tafseer_Muyassar (QuranChapterID, VerseNumber, Rukuh, Sajdah, Manzil, HizbQuarter, VerseText) VALUES (9, 10, '0', '0', '0', '0', 'إن هؤلاء المشركين حرب على الإيمان وأهله، فلا يقيمون وزنًا لقرابة المؤمن ولا لعهده، وشأنهم العدوان والظلم.');
INSERT INTO Quran__Arabic_Tafseer_Muyassar (QuranChapterID, VerseNumber, Rukuh, Sajdah, Manzil, HizbQuarter, VerseText) VALUES (9, 11, '0', '0', '0', '0', 'فإن أقلعوا عن عبادة غير الله، ونطقوا بكلمة التوحيد، والتزموا شرائع الإسلام من إقام الصلاة وإيتاء الزكاة، فإنهم إخوانكم في الإسلام. ونبين الآيات، ونوضحها لقوم ينتفعون بها.');
INSERT INTO Quran__Arabic_Tafseer_Muyassar (QuranChapterID, VerseNumber, Rukuh, Sajdah, Manzil, HizbQuarter, VerseText) VALUES (9, 12, '0', '0', '0', '0', 'وإنْ نَقَضَ هؤلاء المشركون العهود التي أبرمتموها معهم، وأظهروا الطعن في دين الإسلام، فقاتلوهم فإنهم رؤساء الضلال، لا عهد لهم ولا ذمة، حتى ينتهوا عن كفرهم وعداوتهم للإسلام.');
INSERT INTO Quran__Arabic_Tafseer_Muyassar (QuranChapterID, VerseNumber, Rukuh, Sajdah, Manzil, HizbQuarter, VerseText) VALUES (9, 13, '0', '0', '0', '0', 'لا تترددوا في قتال هؤلاء القوم الذين نقضوا عهودهم، وعملوا على إخراج الرسول من (مكة)، وهم الذين بدؤوا بإيذائكم أول الأمر، أتخافونهم أو تخافون ملاقاتهم في الحرب؟ فالله أحق أن تخافوه إن كنتم مؤمنين حقًا.');
INSERT INTO Quran__Arabic_Tafseer_Muyassar (QuranChapterID, VerseNumber, Rukuh, Sajdah, Manzil, HizbQuarter, VerseText) VALUES (9, 14, '0', '0', '0', '0', 'يا معشر المؤمنين قاتلوا أعداء الله يعذبهم عز وجل بأيديكم، ويذلهم بالهزيمة والخزي، وينصركم عليهم، ويُعْلِ كلمته، ويشف بهزيمتهم صدوركم التي طالما لحق بها الحزن والغم من كيد هؤلاء المشركين، ويُذْهِب عن قلوب المؤمنين الغيظ. ومن تاب من هؤلاء المعاندين فإن الله يتوب على من يشاء. والله عليم بصدق توبة التائب، حكيم في تدبيره وصنعه ووَضْع تشريعاته لعباده.');
INSERT INTO Quran__Arabic_Tafseer_Muyassar (QuranChapterID, VerseNumber, Rukuh, Sajdah, Manzil, HizbQuarter, VerseText) VALUES (9, 15, '0', '0', '0', '0', 'يا معشر المؤمنين قاتلوا أعداء الله يعذبهم عز وجل بأيديكم، ويذلهم بالهزيمة والخزي، وينصركم عليهم، ويُعْلِ كلمته، ويشف بهزيمتهم صدوركم التي طالما لحق بها الحزن والغم من كيد هؤلاء المشركين، ويُذْهِب عن قلوب المؤمنين الغيظ. ومن تاب من هؤلاء المعاندين فإن الله يتوب على من يشاء. والله عليم بصدق توبة التائب، حكيم في تدبيره وصنعه ووَضْع تشريعاته لعباده.');
INSERT INTO Quran__Arabic_Tafseer_Muyassar (QuranChapterID, VerseNumber, Rukuh, Sajdah, Manzil, HizbQuarter, VerseText) VALUES (9, 16, '1', '0', '0', '0', 'مِن سنة الله الابتلاء، فلا تظنوا يا معشر المؤمنين أن يترككم الله دون اختبار؛ ليعلم الله علمًا ظاهرًا للخلق الذين أخلصوا في جهادهم، ولم يتخذوا غير الله ورسوله والمؤمنين بطانة وأولياء. والله خبير بجميع أعمالكم ومجازيكم بها.');
INSERT INTO Quran__Arabic_Tafseer_Muyassar (QuranChapterID, VerseNumber, Rukuh, Sajdah, Manzil, HizbQuarter, VerseText) VALUES (9, 17, '0', '0', '0', '0', 'ليس من شأن المشركين إعمار بيوت الله، وهم يعلنون كفرهم بالله ويجعلون له شركاء. هؤلاء المشركون بطلت أعمالهم يوم القيامة، ومصيرهم الخلود في النار.');
INSERT INTO Quran__Arabic_Tafseer_Muyassar (QuranChapterID, VerseNumber, Rukuh, Sajdah, Manzil, HizbQuarter, VerseText) VALUES (9, 18, '0', '0', '0', '0', 'لا يعتني ببيوت الله ويعمرها إلا الذين يؤمنون بالله واليوم الآخر، ويقيمون الصلاة ويؤتون الزكاة، ولا يخافون في الله لومة لائم، هؤلاء العُمَّار هم المهتدون إلى الحق.');
INSERT INTO Quran__Arabic_Tafseer_Muyassar (QuranChapterID, VerseNumber, Rukuh, Sajdah, Manzil, HizbQuarter, VerseText) VALUES (9, 19, '0', '0', '0', '1', 'أجعلتم -أيها القوم- ما تقومون به من سقي الحجيج وعِمارة المسجد الحرام كإيمان من آمن بالله واليوم الآخر وجاهد في سبيل الله؟ لا تتساوى حال المؤمنين وحال الكافرين عند الله، لأن الله لا يقبل عملا بغير الإيمان. والله سبحانه لا يوفق لأعمال الخير القوم الظالمين لأنفسهم بالكفر.');
INSERT INTO Quran__Arabic_Tafseer_Muyassar (QuranChapterID, VerseNumber, Rukuh, Sajdah, Manzil, HizbQuarter, VerseText) VALUES (9, 20, '0', '0', '0', '0', 'الذين آمنوا بالله وتركوا دار الكفر قاصدين دار الإسلام، وبذلوا أموالهم وأنفسهم في الجهاد لإعلاء كلمة الله، هؤلاء أعظم درجه عند الله، وأولئك هم الفائزون برضوانه.');
INSERT INTO Quran__Arabic_Tafseer_Muyassar (QuranChapterID, VerseNumber, Rukuh, Sajdah, Manzil, HizbQuarter, VerseText) VALUES (9, 21, '0', '0', '0', '0', 'إن هؤلاء المؤمنين المهاجرين لهم البشرى من ربهم بالرحمة الواسعة والرضوان الذي لا سخط بعده، ومصيرهم إلى جنات الخلد والنعيم الدائم.');
INSERT INTO Quran__Arabic_Tafseer_Muyassar (QuranChapterID, VerseNumber, Rukuh, Sajdah, Manzil, HizbQuarter, VerseText) VALUES (9, 22, '0', '0', '0', '0', 'ماكثين في تلك الجنان لا نهاية لإقامتهم وتنعمهم، وذلك ثواب ما قدَّموه من الطاعات والعمل الصالح في حياتهم الدنيا. إن الله تعالى عنده أجر عظيم لمن آمن وعمل صالحا بامتثال أوامره واجتناب نواهيه.');
INSERT INTO Quran__Arabic_Tafseer_Muyassar (QuranChapterID, VerseNumber, Rukuh, Sajdah, Manzil, HizbQuarter, VerseText) VALUES (9, 23, '0', '0', '0', '0', 'يا أيها الذين صدَّقوا الله ورسوله وعملوا بشرعه لا تتخذوا أقرباءكم -من الآباء والإخوان وغيرهم- أولياء، تفشون إليهم أسرار المسلمين، وتستشيرونهم في أموركم، ما داموا على الكفر معادين للإسلام. ومن يتخذهم أولياء ويُلْقِ إليهم المودة فقد عصى الله تعالى، وظلم نفسه ظلمًا عظيمًا.');
INSERT INTO Quran__Arabic_Tafseer_Muyassar (QuranChapterID, VerseNumber, Rukuh, Sajdah, Manzil, HizbQuarter, VerseText) VALUES (9, 24, '1', '0', '0', '0', 'قل -يا أيها الرسول- للمؤمنين: إن فَضَّلتم الآباء والأبناء والإخوان والزوجات والقرابات والأموال التي جمعتموها والتجارة التي تخافون عدم رواجها والبيوت الفارهة التي أقمتم فيها، إن فَضَّلتم ذلك على حب الله ورسوله والجهاد في سبيله فانتظروا عقاب الله ونكاله بكم. والله لا يوفق الخارجين عن طاعته.');
INSERT INTO Quran__Arabic_Tafseer_Muyassar (QuranChapterID, VerseNumber, Rukuh, Sajdah, Manzil, HizbQuarter, VerseText) VALUES (9, 25, '0', '0', '0', '0', 'لقد أنزل الله نَصْرَه عليكم في مواقع كثيرة عندما أخذتم بالأسباب وتوكلتم على الله. ويوم غزوة (حنين) قلتم: لن نُغْلَبَ اليوم0 من قلة، فغرَّتكم الكثرة فلم تنفعكم، وظهر عليكم العدو فلم تجدوا ملجأً في الأرض الواسعة ففررتم منهزمين.');
INSERT INTO Quran__Arabic_Tafseer_Muyassar (QuranChapterID, VerseNumber, Rukuh, Sajdah, Manzil, HizbQuarter, VerseText) VALUES (9, 26, '0', '0', '0', '0', 'ثم أنزل الله الطمأنينة على رسوله وعلى المؤمنين فثبتوا، وأمدَّهم بجنود من الملائكة لم يروها، فنصرهم على عدوهم، وعذَّب الذين كفروا. وتلك عقوبة الله للصادِّين عن دينه، المكذِّبين لرسوله.');
INSERT INTO Quran__Arabic_Tafseer_Muyassar (QuranChapterID, VerseNumber, Rukuh, Sajdah, Manzil, HizbQuarter, VerseText) VALUES (9, 27, '0', '0', '0', '0', 'ومن رجع عن كفره بعد ذلك ودخل الإسلام فإن الله يقبل توبة مَن يشاء منهم، فيغفر ذنبه. والله غفور رحيم.');
INSERT INTO Quran__Arabic_Tafseer_Muyassar (QuranChapterID, VerseNumber, Rukuh, Sajdah, Manzil, HizbQuarter, VerseText) VALUES (9, 28, '0', '0', '0', '0', 'يا معشر المؤمنين إنما المشركون رِجْس وخَبَث فلا تمكنوهم من الاقتراب من الحرم بعد هذا العام التاسع من الهجرة، وإن خفتم فقرًا لانقطاع تجارتهم عنكم، فإن الله سيعوضكم عنها، ويكفيكم من فضله إن شاء، إن الله عليم بحالكم، حكيم في تدبير شؤونكم.');
INSERT INTO Quran__Arabic_Tafseer_Muyassar (QuranChapterID, VerseNumber, Rukuh, Sajdah, Manzil, HizbQuarter, VerseText) VALUES (9, 29, '1', '0', '0', '0', 'أيها المسلمون قاتلوا الكفار الذين لا يؤمنون بالله، ولا يؤمنون بالبعث والجزاء، ولا يجتنبون ما نهى الله عنه ورسوله، ولا يلتزمون أحكام شريعة الإسلام من اليهود والنصارى، حتى يدفعوا الجزية التي تفرضونها عليهم بأيديهم خاضعين أذلاء.');
INSERT INTO Quran__Arabic_Tafseer_Muyassar (QuranChapterID, VerseNumber, Rukuh, Sajdah, Manzil, HizbQuarter, VerseText) VALUES (9, 30, '0', '0', '0', '0', 'لقد أشرك اليهود بالله عندما زعموا أن عزيرًا ابن الله. وأشرك النصارى بالله عندما ادَّعوا أن المسيح ابن الله. وهذا القول اختلقوه من عند أنفسهم، وهم بذلك لا يشابهون قول المشركين من قبلهم. قَاتَلَ الله المشركين جميعًا كيف يعدلون عن الحق إلى الباطل؟');
INSERT INTO Quran__Arabic_Tafseer_Muyassar (QuranChapterID, VerseNumber, Rukuh, Sajdah, Manzil, HizbQuarter, VerseText) VALUES (9, 31, '0', '0', '0', '0', 'اتخذ اليهودُ والنصارى العلماءَ والعُبَّادَ أربابًا يُشَرِّعون لهم الأحكام، فيلتزمون بها ويتركون شرائع الله، واتخذوا المسيح عيسى ابن مريم إلهًا فعبدوه، وقد أمرهم الله بعبادته وحده دون غيره، فهو الإله الحق لا إله إلا هو. تنزَّه وتقدَّس عما يفتريه أهل الشرك والضلال.');
INSERT INTO Quran__Arabic_Tafseer_Muyassar (QuranChapterID, VerseNumber, Rukuh, Sajdah, Manzil, HizbQuarter, VerseText) VALUES (9, 32, '0', '0', '0', '0', 'يريد الكفار بتكذيبهم أن يبطلوا دين الإسلام، ويبطلوا حجج الله وبراهينه على توحيده الذي جاء به محمد صلى الله عليه وسلم، ويأبى الله إلا أن يتم دينه ويظهره، ويعلي كلمته، ولو كره ذلك الجاحدون.');
INSERT INTO Quran__Arabic_Tafseer_Muyassar (QuranChapterID, VerseNumber, Rukuh, Sajdah, Manzil, HizbQuarter, VerseText) VALUES (9, 33, '0', '0', '0', '0', 'هو الذي أرسل رسوله محمدًا صلى الله عليه وسلم بالقرآن ودين الإسلام؛ ليعليه على الأديان كلها، ولو كره المشركون دين الحق -الإسلام- وظهوره على الأديان.');
INSERT INTO Quran__Arabic_Tafseer_Muyassar (QuranChapterID, VerseNumber, Rukuh, Sajdah, Manzil, HizbQuarter, VerseText) VALUES (9, 34, '0', '0', '0', '1', 'يا أيها الذين صَدَّقوا الله ورسوله وعملوا بشرعه، إن كثيرًا من علماء أهل الكتاب وعُبَّادهم ليأخذون أموال الناس بغير حق كالرشوة وغيرها، ويمنعون الناس من الدخول في الإسلام، ويصدون عن سبيل الله. والذين يمسكون الأموال، ولا يؤدون زكاتها، ولا يُخْرجون منها الحقوق الواجبة، فبشِّرهم بعذاب موجع.');
INSERT INTO Quran__Arabic_Tafseer_Muyassar (QuranChapterID, VerseNumber, Rukuh, Sajdah, Manzil, HizbQuarter, VerseText) VALUES (9, 35, '0', '0', '0', '0', 'يوم القيامة توضع قطع الذهب والفضة في النار، فإذا اشتدت حرارتها أُحرقت بها جباه أصحابها وجنوبهم وظهورهم. وقيل لهم توبيخًا: هذا مالكم الذي أمسكتموه ومنعتم منه حقوق الله، فذوقوا العذاب الموجع؛ بسبب كنزكم وإمساككم.');
INSERT INTO Quran__Arabic_Tafseer_Muyassar (QuranChapterID, VerseNumber, Rukuh, Sajdah, Manzil, HizbQuarter, VerseText) VALUES (9, 36, '0', '0', '0', '0', 'إنّ عدة الشهور في حكم الله وفيما كُتب في اللوح المحفوظ اثنا عشر شهرًا، يوم خلق السموات والأرض، منها أربعة حُرُم؛ حرَّم الله فيهنَّ القتال (هي: ذو القعدة وذو الحجة والمحرم ورجب) ذلك هو الدين المستقيم، فلا تظلموا فيهن أنفسكم؛ لزيادة تحريمها، وكون الظلم فيها أشد منه في غيرها، لا أنَّ الظلم في غيرها جائز. وقاتلوا المشركين جميعًا كما يقاتلونكم جميعًا، واعلموا أن الله مع أهل التقوى بتأييده ونصره.');
INSERT INTO Quran__Arabic_Tafseer_Muyassar (QuranChapterID, VerseNumber, Rukuh, Sajdah, Manzil, HizbQuarter, VerseText) VALUES (9, 37, '1', '0', '0', '0', 'إن الذي كانت تفعله العرب في الجاهلية من تحريم أربعة أشهر من السنة عددًا لا تحديدًا بأسماء الأشهر التي حرَّمها الله، فيؤخرون بعضها أو يقدِّمونه ويجعلون مكانه من أشهر الحل ما أرادوا حسب حاجتهم للقتال، إن ذلك زيادة في الكفر، يضل الشيطان به الذين كفروا، يحلون الذي أخروا تحريمه من الأشهر الأربعة عامًا، ويحرمونه عاما؛ ليوافقوا عدد الشهور الأربعة، فيحلوا ما حرَّم الله منها. زَيَّن لهم الشيطان الأعمال السيئة. والله لا يوفق القوم الكافرين إلى الحق والصواب.');
INSERT INTO Quran__Arabic_Tafseer_Muyassar (QuranChapterID, VerseNumber, Rukuh, Sajdah, Manzil, HizbQuarter, VerseText) VALUES (9, 38, '0', '0', '0', '0', 'يا أيها الذين صدَّقوا الله ورسوله وعملوا بشرعه، ما بالكم إذا قيل لكم: اخرجوا إلى الجهاد في سبيل الله لقتال أعدائكم تكاسلتم ولزمتم مساكنكم؟ هل آثرتم حظوظكم الدنيوية على نعيم الآخرة؟ فما تستمتعون به في الدنيا قليل زائل، أما نعيم الآخرة الذي أعده الله للمؤمنين المجاهدين فكثير دائم.');
INSERT INTO Quran__Arabic_Tafseer_Muyassar (QuranChapterID, VerseNumber, Rukuh, Sajdah, Manzil, HizbQuarter, VerseText) VALUES (9, 39, '0', '0', '0', '0', 'إن لا تنفروا أيها المؤمنون إلى قتال عدوكم ينزلِ الله عقوبته بكم، ويأت بقوم آخرين ينفرون إذ ا استُنْفروا، ويطيعون الله ورسوله، ولن تضروا الله شيئًا بتولِّيكم عن الجهاد، فهو الغني عنكم وأنتم الفقراء إليه. وما يريده الله يكون لا محالة. والله على كل شيء قدير من نصر دينه ونبيه دونكم.');
INSERT INTO Quran__Arabic_Tafseer_Muyassar (QuranChapterID, VerseNumber, Rukuh, Sajdah, Manzil, HizbQuarter, VerseText) VALUES (9, 40, '0', '0', '0', '0', 'يا معشر أصحاب رسول الله صلى الله عليه وسلم إن لا تنفروا معه أيها المؤمنون إذا استَنْفَركم، وإن لا تنصروه؛ فقد أيده الله ونصره يوم أخرجه الكفار من قريش من بلده (مكة)، وهو ثاني اثنين (هو وأبو بكر الصديق رضي الله عنه) وألجؤوهما إلى نقب في جبل ثور "بمكة"، فمكثا فيه ثلاث ليال، إذ يقول لصاحبه (أبي بكر) لما رأى منه الخوف عليه: لا تحزن إن الله معنا بنصره وتأييده، فأنزل الله الطمأنينة في قلب رسول الله صلى الله عليه وسلم، وأعانه بجنود لم يرها أحد من البشر وهم الملائكة، فأنجاه الله من عدوه وأذل الله أعداءه، وجعل كلمة الذين كفروا السفلى. وكلمةُ الله هي العليا،، ذلك بإعلاء شأن الإسلام. والله عزيز في ملكه، حكيم في تدبير شؤون عباده. وفي هذه الآية منقبة عظيمة لأبي بكر الصديق رضي الله عنه.');
INSERT INTO Quran__Arabic_Tafseer_Muyassar (QuranChapterID, VerseNumber, Rukuh, Sajdah, Manzil, HizbQuarter, VerseText) VALUES (9, 41, '0', '0', '0', '0', 'اخرجوا -أيها المؤمنون- للجهاد في سبيل الله شبابًا وشيوخًا في العسر واليسر، على أي حال كنتم، وأنفقوا أموالكم في سبيل الله، وقاتلوا بأيديكم لإعلاء كلمة الله، ذلك الخروج والبذل خير لكم في حالكم ومآلكم فافعلوا ذلك وانفروا واستجيبوا لله ورسوله.');
INSERT INTO Quran__Arabic_Tafseer_Muyassar (QuranChapterID, VerseNumber, Rukuh, Sajdah, Manzil, HizbQuarter, VerseText) VALUES (9, 42, '1', '0', '0', '0', 'وبَّخ الله جلَّ جلاله جماعة من المنافقين استأذنوا رسول الله صلى الله عليه وسلم في التخلف عن غزوة (تبوك) مبينًا أنه لو كان خروجهم إلى غنيمة قريبة سهلة المنال لاتبعوك، ولكن لما دعوا إلى قتال الروم في أطراف بلاد (الشام) في وقت الحر تخاذلوا، وتخلفوا، وسيعتذرون لتخلفهم عن الخروج حالفين بأنهم لا يستطيعون ذلك، يهلكون أنفسهم بالكذب والنفاق، والله يعلم إنهم لكاذبون فيما يبدون لك من الأعذار.');
INSERT INTO Quran__Arabic_Tafseer_Muyassar (QuranChapterID, VerseNumber, Rukuh, Sajdah, Manzil, HizbQuarter, VerseText) VALUES (9, 43, '0', '0', '0', '0', 'عفا الله عنك -أيها النبي- عمَّا وقع منك مِن تَرْك الأولى والأكمل، وهو إذنك للمنافقين في القعود عن الجهاد، لأي سبب أَذِنْتَ لهؤلاء بالتخلف عن الغزوة، حتى يظهر لك الذين صدقوا في اعتذارهم وتعلم الكاذبين منهم في ذلك؟');
INSERT INTO Quran__Arabic_Tafseer_Muyassar (QuranChapterID, VerseNumber, Rukuh, Sajdah, Manzil, HizbQuarter, VerseText) VALUES (9, 44, '0', '0', '0', '0', 'ليس من شأن المؤمنين بالله ورسوله واليوم الآخر أن يستأذنوك -أيها النبي- في التخلف عن الجهاد في سبيل الله بالنفس والمال، وإنما هذا من شأن المنافقين. والله عليم بمن خافه فاتقاه بأداء فرائضه واجتناب نواهيه.');
INSERT INTO Quran__Arabic_Tafseer_Muyassar (QuranChapterID, VerseNumber, Rukuh, Sajdah, Manzil, HizbQuarter, VerseText) VALUES (9, 45, '0', '0', '0', '0', 'إنما يطلب الإذن للتخلف عن الجهاد الذين لا يصدِّقون بالله ولا باليوم الآخر، ولا يعملون صالحًا، وشكَّتْ قلوبهم في صحة ما جئت به -أيها النبي- من الإسلام وشرائعه، فهم في شكهم يتحيَّرون.');
INSERT INTO Quran__Arabic_Tafseer_Muyassar (QuranChapterID, VerseNumber, Rukuh, Sajdah, Manzil, HizbQuarter, VerseText) VALUES (9, 46, '0', '0', '0', '1', 'ولو أراد المنافقون الخروج معك -أيها النبي- إلى الجهاد لتأهَّبوا له بالزاد والراحلة، ولكن الله كره خروجهم فثَقُلَ عليهم الخروج قضاء وقدرًا، وإن كان أمرهم به شرعا، وقيل لهم: تخلفوا مع القاعدين من المرضى والضعفاء والنساء والصبيان.');
INSERT INTO Quran__Arabic_Tafseer_Muyassar (QuranChapterID, VerseNumber, Rukuh, Sajdah, Manzil, HizbQuarter, VerseText) VALUES (9, 47, '0', '0', '0', '0', 'لو خرج المنافقون معكم -أيها المؤمنون- للجهاد لنشروا الاضطراب في الصفوف والشر والفساد، ولأسرعوا السير بينكم بالنميمة والبغضاء، يبغون فتنتكم بتثبيطكم عن الجهاد في سبيل الله، وفيكم -أيها المؤمنون- عيون لهم يسمعون أخباركم، وينقلونها إليهم. والله عليم بهؤلاء المنافقين الظالمين، وسيجازيهم على ذلك.');
INSERT INTO Quran__Arabic_Tafseer_Muyassar (QuranChapterID, VerseNumber, Rukuh, Sajdah, Manzil, HizbQuarter, VerseText) VALUES (9, 48, '0', '0', '0', '0', 'لقد ابتغى المنافقون فتنة المؤمنين عن دينهم وصدهم عن سبيل الله من قبل غزوة (تبوك)، وكشف أمرهم، وصرَّفوا لك -أيها النبي- الأمور في إبطال ما جئت به، كما فعلوا يوم (أحد) ويوم (الخندق)، ودبَّروا لك الكيد حتى جاء النصر من عند الله، وأعز جنده ونصر دينه، وهم كارهون له.');
INSERT INTO Quran__Arabic_Tafseer_Muyassar (QuranChapterID, VerseNumber, Rukuh, Sajdah, Manzil, HizbQuarter, VerseText) VALUES (9, 49, '0', '0', '0', '0', 'ومِن هؤلاء المنافقين من يطلب الإذن للقعود عن الجهاد ويقول: لا توقعْني في الابتلاء بما يعرض لي في حالة الخروج من فتنة النساء. لقد سقط هؤلاء المنافقون في فتنة النفاق الكبرى. فإن جهنم لمحيطة بالكافرين بالله واليوم الآخر، فلا يُفْلِت منهم أحد.');
INSERT INTO Quran__Arabic_Tafseer_Muyassar (QuranChapterID, VerseNumber, Rukuh, Sajdah, Manzil, HizbQuarter, VerseText) VALUES (9, 50, '0', '0', '0', '0', 'إن يصبك -أيها النبي- سرور وغنيمة يحزن المنافقون، وإن يلحق بك مكروه من هزيمة أو شدة يقولوا: نحن أصحاب رأي وتدبير قد احتطنا لأنفسنا بتخلفنا عن محمد، وينصرفوا وهم مسرورون بما صنعوا وبما أصابك من السوء.');
INSERT INTO Quran__Arabic_Tafseer_Muyassar (QuranChapterID, VerseNumber, Rukuh, Sajdah, Manzil, HizbQuarter, VerseText) VALUES (9, 51, '0', '0', '0', '0', 'قل -أيها النبي- لهؤلاء المتخاذلين زجرًا لهم وتوبيخًا: لن يصيبنا إلا ما قدَّره الله علينا وكتبه في اللوح المحفوظ، هو ناصرنا على أعدائنا، وعلى الله، وحده فليعتمد المؤمنون به.');
INSERT INTO Quran__Arabic_Tafseer_Muyassar (QuranChapterID, VerseNumber, Rukuh, Sajdah, Manzil, HizbQuarter, VerseText) VALUES (9, 52, '0', '0', '0', '0', 'قل لهم -أيها النبي-: هل تنتظرون بنا إلا شهادة أو ظفرًا بكم؟ ونحن ننتظر بكم أن يصيبكم الله بعقوبة مِن عنده عاجلة تهلككم أو بأيدينا فنقتلكم، فانتظروا إنا معكم منتظرون ما الله فاعل بكل فريق منا ومنكم.');
INSERT INTO Quran__Arabic_Tafseer_Muyassar (QuranChapterID, VerseNumber, Rukuh, Sajdah, Manzil, HizbQuarter, VerseText) VALUES (9, 53, '0', '0', '0', '0', 'قل -أيها النبي- للمنافقين: أنفقوا أموالكم كيف شئتم، وعلى أي حال شئتم طائعين أو كارهين، لن يقبل الله منكم نفقاتكم؛ لأنكم قوم خارجون عن دين الله وطاعته.');
INSERT INTO Quran__Arabic_Tafseer_Muyassar (QuranChapterID, VerseNumber, Rukuh, Sajdah, Manzil, HizbQuarter, VerseText) VALUES (9, 54, '0', '0', '0', '0', 'وسبب عدم قَبول نفقاتهم أنهم أضمروا الكفر بالله عز وجل وتكذيب رسوله محمد صلى الله عليه وسلم، ولا يأتون الصلاة إلا وهم متثاقلون، ولا ينفقون الأموال إلا وهم كارهون، فهم لا يرجون ثواب هذه الفرائض، ولا يخشون على تركها عقابًا بسبب كفرهم.');
INSERT INTO Quran__Arabic_Tafseer_Muyassar (QuranChapterID, VerseNumber, Rukuh, Sajdah, Manzil, HizbQuarter, VerseText) VALUES (9, 55, '0', '0', '0', '0', 'فلا تعجبك -أيها النبي- أموال هؤلاء المنافقين ولا أولادهم، إنما يريد الله أن يعذبهم بها في الحياة الدنيا بالتعب في تحصيلها وبالمصائب التي تقع فيها، حيث لا يحتسبون ذلك عند الله، وتخرج أنفسهم، فيموتوا على كفرهم بالله ورسوله.');
INSERT INTO Quran__Arabic_Tafseer_Muyassar (QuranChapterID, VerseNumber, Rukuh, Sajdah, Manzil, HizbQuarter, VerseText) VALUES (9, 56, '0', '0', '0', '0', 'ويحلف هؤلاء المنافقون بالله لكم أيها المؤمنون كذبًا وباطلا إنهم لمنكم، وليسوا منكم، ولكنهم قوم يخافون فيحلفون تَقِيَّة لكم.');
INSERT INTO Quran__Arabic_Tafseer_Muyassar (QuranChapterID, VerseNumber, Rukuh, Sajdah, Manzil, HizbQuarter, VerseText) VALUES (9, 57, '0', '0', '0', '0', 'لو يجد هؤلاء المنافقون مأمنًا وحصنًا يحفظهم، أو كهفًا في جبل يؤويهم، أو نفقًا في الأرض ينجيهم منكم، لانصرفوا إليه وهم يسرعون.');
INSERT INTO Quran__Arabic_Tafseer_Muyassar (QuranChapterID, VerseNumber, Rukuh, Sajdah, Manzil, HizbQuarter, VerseText) VALUES (9, 58, '0', '0', '0', '0', 'ومن المنافقين مَن يعيبك في قسمة الصدقات، فإن نالهم نصيب منها رضوا وسكتوا، وإن لم يصبهم حظ منها سخطوا عليك وعابوك.');
INSERT INTO Quran__Arabic_Tafseer_Muyassar (QuranChapterID, VerseNumber, Rukuh, Sajdah, Manzil, HizbQuarter, VerseText) VALUES (9, 59, '1', '0', '0', '0', 'ولو أن هؤلاء الذين يعيبونك في قسمة الصدقات رضوا بما قسم الله ورسوله لهم، وقالوا: حسبنا الله، سيؤتينا الله من فضله، ويعطينا رسوله مما آتاه الله، إنا نرغب أن يوسع الله علينا، فيغنينا عن الصدقة وعن صدقات الناس. لو فعلوا ذلك لكان خيرًا لهم وأجدى.');
INSERT INTO Quran__Arabic_Tafseer_Muyassar (QuranChapterID, VerseNumber, Rukuh, Sajdah, Manzil, HizbQuarter, VerseText) VALUES (9, 60, '0', '0', '0', '1', 'إنما تعطى الزكوات الواجبة للمحتاجين الذين لا يملكون شيئًا، وللمساكين الذين لا يملكون كفايتهم، وللسعاة الذين يجمعونها، وللذين تؤلِّفون قلوبهم بها ممن يُرْجَى إسلامه أو قوة إيمانه أو نفعه للمسلمين، أو تدفعون بها شرَّ أحد عن المسلمين، وتعطى في عتق رقاب الأرقاء والمكاتبين، وتعطى للغارمين لإصلاح ذات البين، ولمن أثقلَتْهم الديون في غير فساد ولا تبذير فأعسروا، وللغزاة في سبيل الله، وللمسافر الذي انقطعت به النفقة، هذه القسمة فريضة فرضها الله وقدَّرها. والله عليم بمصالح عباده، حكيم في تدبيره وشرعه.');
INSERT INTO Quran__Arabic_Tafseer_Muyassar (QuranChapterID, VerseNumber, Rukuh, Sajdah, Manzil, HizbQuarter, VerseText) VALUES (9, 61, '0', '0', '0', '0', 'ومن المنافقين قوم يؤذون رسول الله صلى الله عليه وسلم بالكلام، ويقولون: إنه يستمع لكل ما يقال له فيصدقه، قل لهم -أيها النبي-: إن محمدًا هو أذن تستمع لكل خير، يؤمن بالله ويصدق المؤمنين فيما يخبرونه، وهو رحمة لمن اتبعه واهتدى بهداه. والذين يؤذون رسول الله محمدًا صلى الله عليه وسلم بأي نوع من أنواع الإيذاء، لهم عذاب مؤلم موجع.');
INSERT INTO Quran__Arabic_Tafseer_Muyassar (QuranChapterID, VerseNumber, Rukuh, Sajdah, Manzil, HizbQuarter, VerseText) VALUES (9, 62, '0', '0', '0', '0', 'يحلف المنافقون الأيمان الكاذبة، ويقدمون الأعذار الملفقة؛ ليُرضُوا المؤمنين، والله ورسوله أحق وأولى أن يُرضُوهما بالإيمان بهما وطاعتهما، إن كانوا مؤمنين حقًا.');
INSERT INTO Quran__Arabic_Tafseer_Muyassar (QuranChapterID, VerseNumber, Rukuh, Sajdah, Manzil, HizbQuarter, VerseText) VALUES (9, 63, '0', '0', '0', '0', 'ألم يعلم هؤلاء المنافقون أن مصير الذين يحاربون الله ورسوله نارُ جهنم لهم العذاب الدائم فيها؟ ذلك المصير هو الهوان والذل العظيم، ومن المحاربة أذِيَّة رسول الله صلى الله عليه وسلم بسبه والقدح فيه، عياذًا بالله من ذلك.');
INSERT INTO Quran__Arabic_Tafseer_Muyassar (QuranChapterID, VerseNumber, Rukuh, Sajdah, Manzil, HizbQuarter, VerseText) VALUES (9, 64, '0', '0', '0', '0', 'يخاف المنافقون أن تنزل في شأنهم سورة تخبرهم بما يضمرونه في قلوبهم من الكفر، قل لهم -أيها النبي-: استمروا على ما أنتم عليه من الاستهزاء والسخرية، إن الله مخرج حقيقة ما تحذرون.');
INSERT INTO Quran__Arabic_Tafseer_Muyassar (QuranChapterID, VerseNumber, Rukuh, Sajdah, Manzil, HizbQuarter, VerseText) VALUES (9, 65, '0', '0', '0', '0', 'ولئن سألتهم -أيها النبي- عما قالوا من القَدْح في حقك وحق أصحابك لَيَقولُنَّ: إنما كنا نتحدث بكلام لا قصد لنا به، قل لهم -أيها النبي-: أبالله عز وجل وآياته ورسوله كنتم تستهزئون؟');
INSERT INTO Quran__Arabic_Tafseer_Muyassar (QuranChapterID, VerseNumber, Rukuh, Sajdah, Manzil, HizbQuarter, VerseText) VALUES (9, 66, '1', '0', '0', '0', 'لا تعتذروا -معشر المنافقين- فلا جدوى مِن اعتذاركم، قد كفرتم بهذا المقال الذي استهزأتم به، إن نعف عن جماعة منكم طلبت العفو وأخلصت في توبتها، نعذب جماعة أخرى بسبب إجرامهم بهذه المقالة الفاجرة الخاطئة.');
INSERT INTO Quran__Arabic_Tafseer_Muyassar (QuranChapterID, VerseNumber, Rukuh, Sajdah, Manzil, HizbQuarter, VerseText) VALUES (9, 67, '0', '0', '0', '0', 'المنافقون والمنافقات صنف واحد في إعلانهم الإيمان واستبطانهم الكفر، يأمرون بالكفر بالله ومعصية رسوله وينهون عن الإيمان والطاعة، ويمسكون أيديهم عن النفقة في سبيل الله، نسوا الله فلا يذكرونه، فنسيهم من رحمته، فلم يوفقهم إلى خير. إن المنافقين هم الخارجون عن الإيمان بالله ورسوله.');
INSERT INTO Quran__Arabic_Tafseer_Muyassar (QuranChapterID, VerseNumber, Rukuh, Sajdah, Manzil, HizbQuarter, VerseText) VALUES (9, 68, '0', '0', '0', '0', 'وعد الله المنافقين والمنافقات والكفار بأن مصيرهم إلى نار جهنم خالدين فيها أبدًا، هي كافيتهم؛ عقابًا على كفرهم بالله، وطردهم الله مِن رحمته، ولهم عذاب دائم.');
INSERT INTO Quran__Arabic_Tafseer_Muyassar (QuranChapterID, VerseNumber, Rukuh, Sajdah, Manzil, HizbQuarter, VerseText) VALUES (9, 69, '0', '0', '0', '0', 'إن أفعالكم -معشر المنافقين- من الاستهزاء والكفر كأفعال الأمم السابقة التي كانت على جانب من القوة والمال والأولاد أشد منكم، فاطْمَأنوا إلى الحياة الدنيا، وتَمتَّعوا بما فيها من الحظوظ والملذات، فاستمعتم أيها المنافقون بنصيبكم من الشهوات الفانية كاستمتاع الذين من قبلكم بحظوظهم الفانية، وخضتم بالكذب على الله كخوض تلك الأمم قبلكم، أولئك الموصوفون بهذه الأخلاق هم الذين ذهبت حسناتهم في الدنيا والآخرة، وأولئك هم الخاسرون ببيعهم نعيم الآخرة بحظوظهم من الدنيا.');
INSERT INTO Quran__Arabic_Tafseer_Muyassar (QuranChapterID, VerseNumber, Rukuh, Sajdah, Manzil, HizbQuarter, VerseText) VALUES (9, 70, '0', '0', '0', '0', 'ألم يأت هؤلاء المنافقين خبرُ الذين مضوا مِن قوم نوح وقبيلة عاد وقبيلة ثمود وقوم إبراهيم وأصحاب (مدين) وقوم لوط عندما جاءهم المرسلون بالوحي وبآيات الله فكذَّبوهم؟ فأنزل الله بهؤلاء جميعًا عذابه؛ انتقامًا منهم لسوء عملهم، فما كان الله ليظلمهم، ولكن كانوا هم الظالمين لأنفسهم بالتكذيب والمخالفة.');
INSERT INTO Quran__Arabic_Tafseer_Muyassar (QuranChapterID, VerseNumber, Rukuh, Sajdah, Manzil, HizbQuarter, VerseText) VALUES (9, 71, '0', '0', '0', '0', 'والمؤمنون والمؤمنات بالله ورسوله بعضهم أنصار بعض، يأمرون الناس بالإيمان والعمل الصالح، وينهونهم عن الكفر والمعاصي، ويؤدون الصلاة، ويعطون الزكاة، ويطيعون الله ورسوله، وينتهون عما نُهوا عنه، أولئك سيرحمهم الله فينقذهم من عذابه ويدخلهم جنته. إن الله عزيز في ملكه، حكيم في تشريعاته وأحكامه.');
INSERT INTO Quran__Arabic_Tafseer_Muyassar (QuranChapterID, VerseNumber, Rukuh, Sajdah, Manzil, HizbQuarter, VerseText) VALUES (9, 72, '1', '0', '0', '0', 'وعد الله المؤمنين والمؤمنات بالله ورسوله جنات تجري من تحتها الأنهار ماكثين فيها أبدًا، لا يزول عنهم نعيمها، ومساكن حسنة البناء طيبة القرار في جنات إقامة، ورضوان من الله أكبر وأعظم مما هم فيه من النعيم. ذلك الوعد بثواب الآخرة هو الفلاح العظيم.');
INSERT INTO Quran__Arabic_Tafseer_Muyassar (QuranChapterID, VerseNumber, Rukuh, Sajdah, Manzil, HizbQuarter, VerseText) VALUES (9, 73, '0', '0', '0', '0', 'يا أيها النبي جاهد الكفار بالسيف والمنافقين باللسان والحجة، واشدد على كلا الفريقين، ومقرُّهم جهنم، وبئس المصير مصيرهم.');
INSERT INTO Quran__Arabic_Tafseer_Muyassar (QuranChapterID, VerseNumber, Rukuh, Sajdah, Manzil, HizbQuarter, VerseText) VALUES (9, 74, '0', '0', '0', '0', 'يحلف المنافقون بالله أنهم ما قالوا شيئًا يسيء إلى الرسول وإلى المسلمين، إنهم لكاذبون؛ فلقد قالوا كلمة الكفر وارتدوا بها عن الإسلام وحاولوا الإضرار برسول الله محمد صلى الله عليه وسلم، فلم يمكنهم الله من ذلك، وما وجد المنافقون شيئًا يعيبونه، وينتقدونه، إلا أن الله -تعالى- تفضل عليهم، فأغناهم بما فتح على نبيه صلى الله عليه وسلم من الخير والبركة، فإن يرجع هؤلاء الكفار إلى الإيمان والتوبة فهو خير لهم، وإن يعرضوا، أو يستمروا على حالهم، يعذبهم الله العذاب الموجع في الدنيا على أيدي المؤمنين، وفي الآخرة بنار جهنم، وليس لهم منقذ ينقذهم ولا ناصر يدفع عنهم سوء العذاب.');
INSERT INTO Quran__Arabic_Tafseer_Muyassar (QuranChapterID, VerseNumber, Rukuh, Sajdah, Manzil, HizbQuarter, VerseText) VALUES (9, 75, '0', '0', '0', '1', 'ومن فقراء المنافقين مَن يقطع العهد على نفسه: لئن أعطاه الله المال ليصدَّقنَّ منه، وليعمَلنَّ ما يعمل الصالحون في أموالهم، وليسيرَنَّ في طريق الصلاح.');
INSERT INTO Quran__Arabic_Tafseer_Muyassar (QuranChapterID, VerseNumber, Rukuh, Sajdah, Manzil, HizbQuarter, VerseText) VALUES (9, 76, '0', '0', '0', '0', 'فلما أعطاهم الله من فضله بخلوا بإعطاء الصدقة وبإنفاق المال في الخير، وتولَّوا وهم معرضون عن الإسلام.');
INSERT INTO Quran__Arabic_Tafseer_Muyassar (QuranChapterID, VerseNumber, Rukuh, Sajdah, Manzil, HizbQuarter, VerseText) VALUES (9, 77, '0', '0', '0', '0', 'فكان جزاء صنيعهم وعاقبتهم أَنْ زادهم نفاقًا على نفاقهم، لا يستطيعون التخلص منه إلى يوم الحساب؛ وذلك بسبب إخلافهم الوعد الذي قطعوه على أنفسهم، وبسبب نفاقهم وكذبهم.');
INSERT INTO Quran__Arabic_Tafseer_Muyassar (QuranChapterID, VerseNumber, Rukuh, Sajdah, Manzil, HizbQuarter, VerseText) VALUES (9, 78, '0', '0', '0', '0', 'ألم يعلم هؤلاء المنافقون أن الله يعلم ما يخفونه في أنفسهم وما يتحدثون به في مجالسهم من الكيد والمكر، وأن الله علام الغيوب؟ فسيجازيهم على أعمالهم التي أحصاها عليهم.');
INSERT INTO Quran__Arabic_Tafseer_Muyassar (QuranChapterID, VerseNumber, Rukuh, Sajdah, Manzil, HizbQuarter, VerseText) VALUES (9, 79, '0', '0', '0', '0', 'ومع بخل المنافقين لا يَسْلَم المتصدقون من أذاهم؛ فإذا تصدق الأغنياء بالمال الكثير عابوهم واتهموهم بالرياء، وإذا تصدق الفقراء بما في طاقتهم استهزؤوا بهم، وقالوا سخرية منهم: ماذا تجدي صدقتهم هذه؟ سخر الله من هؤلاء المنافقين، ولهم عذاب مؤلم موجع.');
INSERT INTO Quran__Arabic_Tafseer_Muyassar (QuranChapterID, VerseNumber, Rukuh, Sajdah, Manzil, HizbQuarter, VerseText) VALUES (9, 80, '1', '0', '0', '0', 'استغفر -أيها الرسول- للمنافقين أو لا تستغفر لهم، فلن يغفر الله لهم، مهما كثر استغفارك لهم وتكرر؛ لأنهم كفروا بالله ورسوله. والله سبحانه وتعالى لا يوفق للهدى الخارجين عن طاعته.');
INSERT INTO Quran__Arabic_Tafseer_Muyassar (QuranChapterID, VerseNumber, Rukuh, Sajdah, Manzil, HizbQuarter, VerseText) VALUES (9, 81, '0', '0', '0', '0', 'فرح المخلفون الذين تخلفوا عن رسول الله صلى الله عليه وسلم بقعودهم في (المدينة) مخالفين لرسول الله صلى الله عليه وسلم، وكرهوا أن يجاهدوا معه بأموالهم وأنفسهم في سبيل الله، وقال بعضهم لبعض: لا تنفروا في الحرِّ، وكانت غزوة (تبوك) في وقت شدة الحرِّ. قل لهم -أيها الرسول-: نار جهنم أشد حرًا، لو كانوا يعلمون ذلك.');
INSERT INTO Quran__Arabic_Tafseer_Muyassar (QuranChapterID, VerseNumber, Rukuh, Sajdah, Manzil, HizbQuarter, VerseText) VALUES (9, 82, '0', '0', '0', '0', 'فليضحك هؤلاء المنافقون الذين تخلفوا عن رسول الله في غزوة (تبوك) قليلا في حياتهم الدنيا الفانية، وليبكوا كثيرًا في نار جهنم؛ جزاءً بما كانوا يكسبون في الدنيا من النفاق والكفر.');
INSERT INTO Quran__Arabic_Tafseer_Muyassar (QuranChapterID, VerseNumber, Rukuh, Sajdah, Manzil, HizbQuarter, VerseText) VALUES (9, 83, '0', '0', '0', '0', 'فإنْ رَدَّك الله -أيها الرسول- مِن غزوتك إلى جماعة من المنافقين الثابتين على النفاق، فاستأذنوك للخروج معك إلى غزوة أخرى بعد غزوة (تبوك) فقل لهم: لن تخرجوا معي أبدًا في غزوة من الغزوات، ولن تقاتلوا معي عدوًا من الأعداء؛ إنكم رضيتم بالقعود أول مرة، فاقعدوا مع الذين تخلفوا عن الجهاد مع رسول الله صلى الله عليه وسلم.');
INSERT INTO Quran__Arabic_Tafseer_Muyassar (QuranChapterID, VerseNumber, Rukuh, Sajdah, Manzil, HizbQuarter, VerseText) VALUES (9, 84, '0', '0', '0', '0', 'ولا تصلِّ -أيها الرسول- أبدًا على أحد مات من المنافقين، ولا تقم على قبره لتدعو له؛ لأنهم كفروا بالله تعالى وبرسوله صلى الله عليه وسلم وماتوا وهم فاسقون. وهذا حكم عام في كل من عُلِمَ نفاقه.');
INSERT INTO Quran__Arabic_Tafseer_Muyassar (QuranChapterID, VerseNumber, Rukuh, Sajdah, Manzil, HizbQuarter, VerseText) VALUES (9, 85, '0', '0', '0', '0', 'ولا تعجبك -أيها الرسول- أموال هؤلاء المنافقين وأولادهم، إنما يريد الله أن يعذبهم بها في الدنيا بمكابدتهم الشدائد في شأنها، وبموتهم على كفرهم بالله ورسوله.');
INSERT INTO Quran__Arabic_Tafseer_Muyassar (QuranChapterID, VerseNumber, Rukuh, Sajdah, Manzil, HizbQuarter, VerseText) VALUES (9, 86, '0', '0', '0', '0', 'وإذا أنزلت سورة على محمد صلى الله عليه ولم تأمر بالإيمان بالله والإخلاص له والجهاد مع رسول الله، طلب الإذن منك -أيها الرسول- أولو اليسار من المنافقين، وقالوا: اتركنا مع القاعدين العاجزين عن الخروج.');
INSERT INTO Quran__Arabic_Tafseer_Muyassar (QuranChapterID, VerseNumber, Rukuh, Sajdah, Manzil, HizbQuarter, VerseText) VALUES (9, 87, '0', '0', '0', '0', 'رضي هؤلاء المنافقون لأنفسهم بالعار، وهو أن يقعدوا في البيوت مع النساء والصبيان وأصحاب الأعذار، وختم الله على قلوبهم؛ بسبب نفاقهم وتخلفهم عن الجهاد والخروج مع رسول الله صلى الله عليه وسلم في سبيل الله، فهم لا يفقهون ما فيه صلاحهم ورشادهم.');
INSERT INTO Quran__Arabic_Tafseer_Muyassar (QuranChapterID, VerseNumber, Rukuh, Sajdah, Manzil, HizbQuarter, VerseText) VALUES (9, 88, '0', '0', '0', '0', 'إنْ تخلَّف هؤلاء المنافقون عن الغزو، فقد جاهد رسول الله صلى الله عليه وسلم والمؤمنون معه بأموالهم وأنفسهم، وأولئك لهم النصر والغنيمة في الدنيا، والجنة والكرامة في الآخرة، وأولئك هم الفائزون.');
INSERT INTO Quran__Arabic_Tafseer_Muyassar (QuranChapterID, VerseNumber, Rukuh, Sajdah, Manzil, HizbQuarter, VerseText) VALUES (9, 89, '1', '0', '0', '0', 'أعدَّ الله لهم يوم القيامة جنات تجري مِن تحت أشجارها الأنهار ماكثين فيها أبدًا. وذلك هو الفلاح العظيم.');
INSERT INTO Quran__Arabic_Tafseer_Muyassar (QuranChapterID, VerseNumber, Rukuh, Sajdah, Manzil, HizbQuarter, VerseText) VALUES (9, 90, '0', '0', '0', '0', 'وجاء جماعة من أحياء العرب حول (المدينة) يعتذرون إلى رسول الله صلى الله عليه وسلم، ويبينون له ما هم فيه من الضعف وعدم القدرة على الخروج للغزو، وقعد قوم بغير عذر أظهروه جرأة على رسول الله صلى الله عليه وسلم. سيصيب الذين كفروا من هؤلاء عذاب أليم في الدنيا بالقتل وغيره، وفي الآخرة بالنار.');
INSERT INTO Quran__Arabic_Tafseer_Muyassar (QuranChapterID, VerseNumber, Rukuh, Sajdah, Manzil, HizbQuarter, VerseText) VALUES (9, 91, '0', '0', '0', '0', 'ليس على أهل الأعذار مِن الضعفاء والمرضى والفقراء الذين لا يملكون من المال ما يتجهزون به للخروج إثم في القعود إذا أخلصوا لله ورسوله، وعملوا بشرعه، ما على مَن أحسن ممن منعه العذر عن الجهاد مع رسول الله صلى الله عليه وسلم، وهو ناصح لله ولرسوله من طريق يعاقب مِن قِبَلِه ويؤاخذ عليه. والله غفور للمحسنين، رحيم بهم.');
INSERT INTO Quran__Arabic_Tafseer_Muyassar (QuranChapterID, VerseNumber, Rukuh, Sajdah, Manzil, HizbQuarter, VerseText) VALUES (9, 92, '0', '0', '0', '0', 'وكذلك لا إثم على الذين إذا ما جاؤوك يطلبون أن تعينهم بحملهم إلى الجهاد قلت لهم: لا أجد ما أحملكم عليه من الدوابِّ، فانصرفوا عنك، وقد فاضت أعينهم دَمعًا أسفًا على ما فاتهم من شرف الجهاد وثوابه؛ لأنهم لم يجدوا ما ينفقون، وما يحملهم لو خرجوا للجهاد في سبيل الله.');
INSERT INTO Quran__Arabic_Tafseer_Muyassar (QuranChapterID, VerseNumber, Rukuh, Sajdah, Manzil, HizbQuarter, VerseText) VALUES (9, 93, '0', '0', '0', '1', 'إنما الإثم واللوم على الأغنياء الذين جاءوك -أيها الرسول- يطلبون الإذن بالتخلف، وهم المنافقون الأغنياء اختاروا لأنفسهم القعود مع النساء وأهل الأعذار، وختم الله على قلوبهم بالنفاق، فلا يدخلها إيمان، فهم لا يعلمون سوء عاقبتهم بتخلفهم عنك وتركهم الجهاد معك.');
INSERT INTO Quran__Arabic_Tafseer_Muyassar (QuranChapterID, VerseNumber, Rukuh, Sajdah, Manzil, HizbQuarter, VerseText) VALUES (9, 94, '0', '0', '0', '0', 'يعتذر إليكم -أيها المؤمنون- هؤلاء المتخلفون عن جهاد المشركين بالأكاذيب عندما تعودون مِن جهادكم من غزوة (تبوك)، قل لهم -أيها الرسول-: لا تعتذروا لن نصدقكم فيما تقولون، قد نبأنا الله من أمركم ما حقق لدينا كذبكم، وسيرى الله عملكم ورسوله، إن كنتم تتوبون من نفاقكم، أو تقيمون عليه، وسيُظهر للناس أعمالكم في الدنيا، ثم ترجعون بعد مماتكم إلى الذي لا تخفى عليه بواطن أموركم وظواهرها، فيخبركم بأعمالكم كلها، ويجازيكم عليها.');
INSERT INTO Quran__Arabic_Tafseer_Muyassar (QuranChapterID, VerseNumber, Rukuh, Sajdah, Manzil, HizbQuarter, VerseText) VALUES (9, 95, '0', '0', '0', '0', 'سيحلف لكم المنافقون بالله -كاذبين معتذرين- إذا رجعتم إليهم من الغزو؛ لتتركوهم دون مساءلة، فاجتنبوهم وأعرضوا عنهم احتقارًا لهم، إنهم خبثاء البواطن، ومكانهم الذي يأوون إليه في الآخرة نار جهنم؛ جزاء بما كانوا يكسبون من الآثام والخطايا.');
INSERT INTO Quran__Arabic_Tafseer_Muyassar (QuranChapterID, VerseNumber, Rukuh, Sajdah, Manzil, HizbQuarter, VerseText) VALUES (9, 96, '0', '0', '0', '0', 'يحلف لكم -أيها المؤمنون- هؤلاء المنافقون كذبًا؛ لتَرضَوا عنهم، فإن رضيتم عنهم -لأنكم لا تعلمون كذبهم- فإن الله لا يرضى عن هؤلاء وغيرهم ممن استمروا على الفسوق والخروج عن طاعة الله ورسوله.');
INSERT INTO Quran__Arabic_Tafseer_Muyassar (QuranChapterID, VerseNumber, Rukuh, Sajdah, Manzil, HizbQuarter, VerseText) VALUES (9, 97, '0', '0', '0', '0', 'الأعراب سكان البادية أشد كفرًا ونفاقًا من أهل الحاضرة، وذلك لجفائهم وقسوة قلوبهم وبُعدهم عن العلم والعلماء، ومجالس الوعظ والذكر، فهم لذلك أحق بأن لا يعلموا حدود الدين، وما أنزل الله من الشرائع والأحكام. والله عليم بحال هؤلاء جميعًا، حكيم في تدبيره لأمور عباده.');
INSERT INTO Quran__Arabic_Tafseer_Muyassar (QuranChapterID, VerseNumber, Rukuh, Sajdah, Manzil, HizbQuarter, VerseText) VALUES (9, 98, '0', '0', '0', '0', 'ومن الأعراب مَن يحتسب ما ينفق في سبيل الله غرامة وخسارة لا يرجو له ثوابًا، ولا يدفع عن نفسه عقابًا، وينتظر بكم الحوادث والآفات، ولكن السوء دائر عليهم لا بالمسلمين. والله سميع لما يقولون عليم بنياتهم الفاسدة.');
INSERT INTO Quran__Arabic_Tafseer_Muyassar (QuranChapterID, VerseNumber, Rukuh, Sajdah, Manzil, HizbQuarter, VerseText) VALUES (9, 99, '1', '0', '0', '0', 'ومن الأعراب مَن يؤمن بالله ويقرُّ بوحدانيته وبالبعث بعد الموت، والثواب والعقاب، ويحتسب ما ينفق من نفقة في جهاد المشركين قاصدًا بها رضا الله ومحبته، ويجعلها وسيلة إلى دعاء الرسول صلى الله عليه وسلم له، ألا إن هذه الأعمال تقربهم إلى الله تعالى، سيدخلهم الله في جنته. إن الله غفور لما فعلوا من السيئات، رحيم بهم.');
INSERT INTO Quran__Arabic_Tafseer_Muyassar (QuranChapterID, VerseNumber, Rukuh, Sajdah, Manzil, HizbQuarter, VerseText) VALUES (9, 100, '0', '0', '0', '0', 'والذين سبقوا الناس أولا إلى الإيمان بالله ورسوله من المهاجرين الذين هجروا قومهم وعشيرتهم وانتقلوا إلى دار الإسلام، والأنصار الذين نصروا رسول الله صلى الله عليه وسلم على أعدائه الكفار، والذين اتبعوهم بإحسان في الاعتقاد والأقوال والأعمال طلبًا لمرضاة الله سبحانه وتعالى، أولئك الذين رضي الله عنهم لطاعتهم الله ورسوله، ورضوا عنه لما أجزل لهم من الثواب على طاعتهم وإيمانهم، وأعدَّ لهم جنات تجري تحتها الأنهار خالدين فيها أبدًا، ذلك هو الفلاح العظيم. وفي هذه الآية تزكية للصحابة -رضي الله عنهم- وتعديل لهم، وثناء عليهم؛ ولهذا فإن توقيرهم من أصول الإيمان.');
INSERT INTO Quran__Arabic_Tafseer_Muyassar (QuranChapterID, VerseNumber, Rukuh, Sajdah, Manzil, HizbQuarter, VerseText) VALUES (9, 101, '0', '0', '0', '0', 'ومن القوم الذين حول (المدينة) أعراب منافقون، ومن أهل (المدينة) منافقون أقاموا على النفاق، وازدادوا فيه طغيانًا، بحيث يخفى عليك -أيها الرسول- أمرهم، نحن نعلمهم، سنعذبهم مرتين: بالقتل والسبي والفضيحة في الدنيا، وبعذاب القبر بعد الموت، ثم يُرَدُّون يوم القيامة إلى عذاب عظيم في نار جهنم.');
INSERT INTO Quran__Arabic_Tafseer_Muyassar (QuranChapterID, VerseNumber, Rukuh, Sajdah, Manzil, HizbQuarter, VerseText) VALUES (9, 102, '0', '0', '0', '0', 'وآخرون من أهل (المدينة) وممن حولها، اعترفوا بذنوبهم وندموا عليها وتابوا منها، خلطوا العمل الصالح -وهو التوبة والندم والاعتراف بالذنب وغير ذلك من الأعمال الصالحة- بآخر سيِّئ- وهو التخلف عن رسول الله صلى الله عليه وسلم وغيره من الأعمال السيئة -عسى الله أن يوفقهم للتوبة ويقبلها منهم. إن الله غفور لعباده، رحيم بهم.');
INSERT INTO Quran__Arabic_Tafseer_Muyassar (QuranChapterID, VerseNumber, Rukuh, Sajdah, Manzil, HizbQuarter, VerseText) VALUES (9, 103, '0', '0', '0', '0', 'خذ -أيها النبي- من أموال هؤلاء التائبين الذين خلطوا عملا صالحا وآخر سيئا صدقة تطهرهم مِن دنس ذنوبهم، وترفعهم عن منازل المنافقين إلى منازل المخلصين، وادع لهم بالمغفرة لذنوبهم واستغفر لهم منها، إن دعاءك واستغفارك رحمة وطمأنينة لهم. والله سميع لكل دعاء وقول، عليم بأحوال العباد ونياتهم، وسيجازي كلَّ عامل بعمله.');
INSERT INTO Quran__Arabic_Tafseer_Muyassar (QuranChapterID, VerseNumber, Rukuh, Sajdah, Manzil, HizbQuarter, VerseText) VALUES (9, 104, '0', '0', '0', '0', 'ألم يعلم هؤلاء المتخلفون عن الجهاد وغيرهم أن الله وحده هو الذي يقبل توبة عباده، ويأخذ الصدقات ويثيب عليها، وأن الله هو التواب لعباده إذا رجعوا إلى طاعته، الرحيم بهم إذا أنابوا إلى رضاه؟');
INSERT INTO Quran__Arabic_Tafseer_Muyassar (QuranChapterID, VerseNumber, Rukuh, Sajdah, Manzil, HizbQuarter, VerseText) VALUES (9, 105, '0', '0', '0', '0', 'وقل -أيها النبي- لهؤلاء المتخلِّفين عن الجهاد: اعملوا لله بما يرضيه من طاعته، وأداء فرائضه، واجتناب المعاصي، فسيرى الله عملكم ورسوله والمؤمنون، وسيتبين أمركم، وسترجعون يوم القيامة إلى مَن يعلم سركم وجهركم، فيخبركم بما كنتم تعملون. وفي هذا تهديد ووعيد لمن استمر على باطله وطغيانه.');
INSERT INTO Quran__Arabic_Tafseer_Muyassar (QuranChapterID, VerseNumber, Rukuh, Sajdah, Manzil, HizbQuarter, VerseText) VALUES (9, 106, '0', '0', '0', '0', 'ومن هؤلاء المتخلفين عنكم -أيها المؤمنون- في غزوة (تبوك) آخرون مؤخرون؛ ليقضي الله فيهم ما هو قاض. وهؤلاء هم الذين ندموا على ما فعلوا، وهم: مُرارة بن الربيع، وكعب بن مالك، وهلال بن أُميَّة، إما يعذبهم الله، وإما يعفو عنهم. والله عليم بمن يستحق العقوبة أو العفو، حكيم في كل أقواله وأفعاله.');
INSERT INTO Quran__Arabic_Tafseer_Muyassar (QuranChapterID, VerseNumber, Rukuh, Sajdah, Manzil, HizbQuarter, VerseText) VALUES (9, 107, '0', '0', '0', '0', 'والمنافقون الذين بنوا مسجدًا؛ مضارة للمؤمنين وكفرًا بالله وتفريقًا بين المؤمنين، ليصلي فيه بعضهم ويترك مسجد (قباء) الذي يصلي فيه المسلمون، فيختلف المسلمون ويتفرقوا بسبب ذلك، وانتظارا لمن حارب الله ورسوله من قبل -وهو أبو عامر الراهب الفاسق- ليكون مكانًا للكيد للمسلمين، وليحلفنَّ هؤلاء المنافقون أنهم ما أرادوا ببنائه إلا الخير والرفق بالمسلمين والتوسعة على الضعفاء العاجزين عن السير إلى مسجد (قباء)، والله يشهد إنهم لكاذبون فيما يحلفون عليه. وقد هُدِم المسجد وأُحرِق.');
INSERT INTO Quran__Arabic_Tafseer_Muyassar (QuranChapterID, VerseNumber, Rukuh, Sajdah, Manzil, HizbQuarter, VerseText) VALUES (9, 108, '0', '0', '0', '0', 'لا تقم -أيها النبي- للصلاة في ذلك المسجد أبدًا؛ فإن المسجد الذي أُسِّسَ على التقوى من أول يوم -وهو مسجد (قباء)- أولى أن تقوم فيه للصلاة، ففي هذا المسجد رجال يحبون أن يتطهروا بالماء من النجاسات والأقذار، كما يتطهرون بالتورع والاستغفار من الذنوب والمعاصي. والله يحب المتطهرين. وإذا كان مسجد (قباء) قد أُسِّسَ على التقوى من أول يوم، فمسجد رسول الله، صلى الله عليه وسلم، كذلك بطريق الأولى والأحرى.');
INSERT INTO Quran__Arabic_Tafseer_Muyassar (QuranChapterID, VerseNumber, Rukuh, Sajdah, Manzil, HizbQuarter, VerseText) VALUES (9, 109, '0', '0', '0', '0', 'لا يستوي مَن أسَّس بنيانه على تقوى الله وطاعته ومرضاته، ومن أسَّس بنيانه على طرف حفرة متداعية للسقوط، فبنى مسجدًا ضرارًا وكفرًا وتفريقًا بين المسلمين، فأدَّى به ذلك إلى السقوط في نار جهنم. والله لا يهدي القوم الظالمين المتجاوزين حدوده.');
INSERT INTO Quran__Arabic_Tafseer_Muyassar (QuranChapterID, VerseNumber, Rukuh, Sajdah, Manzil, HizbQuarter, VerseText) VALUES (9, 110, '1', '0', '0', '0', 'لا يزال بنيان المنافقين الذي بنوه مضارَّة لمسجد (قباء) شكًا ونفاقًا ماكثًا في قلوبهم، إلى أن تتقطع قلوبهم بقتلهم أو موتهم، أو بندمهم غاية الندم، وتوبتهم إلى ربهم، وخوفهم منه غاية الخوف. والله عليم بما عليه هؤلاء المنافقون من الشك وما قصدوا في بنائهم، حكيم في تدبير أمور خلقه.');
INSERT INTO Quran__Arabic_Tafseer_Muyassar (QuranChapterID, VerseNumber, Rukuh, Sajdah, Manzil, HizbQuarter, VerseText) VALUES (9, 111, '0', '0', '0', '1', 'إن الله اشترى من المؤمنين أنفسهم بأن لهم في مقابل ذلك الجنة، وما أعد الله فيها من النعيم لبذلهم نفوسهم وأموالهم في جهاد أعدائه لإعلاء كلمته وإظهار دينه، فيَقْتلون ويُقتَلون، وعدًا عليه حقًا في التوراة المنزلة على موسى عليه السلام، والإنجيل المنزل على عيسى عليه السلام، والقرآن المنزل على محمد صلى الله عليه وسلم. ولا أحد أوفى بعهده من الله لمن وفَّى بما عاهد الله عليه، فأظهِروا السرور-أيها المؤمنون- ببيعكم الذي بايعتم الله به، وبما وعدكم به من الجنة والرضوان، وذلك البيع هو الفلاح العظيم.');
INSERT INTO Quran__Arabic_Tafseer_Muyassar (QuranChapterID, VerseNumber, Rukuh, Sajdah, Manzil, HizbQuarter, VerseText) VALUES (9, 112, '0', '0', '0', '0', 'ومن صفات هؤلاء المؤمنين الذين لهم البشارة بدخول الجنة أنهم التائبون الراجعون عما كرهه الله إلى ما يحبه ويرضاه، الذين أخلصوا العبادة لله وحده وجدوا في طاعته، الذين يحمدون الله على كل ما امتحنهم به من خير أو شر، الصائمون، الراكعون في صلاتهم، الساجدون فيها، الذين يأمرون الناس بكل ما أمر الله ورسوله به، وينهونهم عن كل ما نهى الله عنه ورسوله، المؤدون فرائض الله المنتهون إلى أمره ونهيه، القائمون على طاعته، الواقفون عند حدوده. وبشِّر -أيها النبي- هؤلاء المؤمنين المتصفين بهذه الصفات برضوان الله وجنته.');
INSERT INTO Quran__Arabic_Tafseer_Muyassar (QuranChapterID, VerseNumber, Rukuh, Sajdah, Manzil, HizbQuarter, VerseText) VALUES (9, 113, '0', '0', '0', '0', 'ما كان ينبغي للنبي محمد صلى الله عليه وسلم والذين آمنوا أن يدعوا بالمغفرة للمشركين، ولو كانوا ذوي قرابة لهم مِن بعد ما ماتوا على شركهم بالله وعبادة الأوثان، وتبين لهم أنهم أصحاب الجحيم لموتهم على الشرك، والله لا يغفر للمشركين، كما قال تعالى: {إِنَّ اللَّهَ لا يَغْفِرُ أَنْ يُشْرَكَ بِهِ (4:48)} وكما قال سبحانه: {إِنَّهُ مَنْ يُشْرِكْ بِاللَّهِ فَقَدْ حَرَّمَ اللَّهُ عَلَيْهِ الْجَنَّةَ (5:72)}.');
INSERT INTO Quran__Arabic_Tafseer_Muyassar (QuranChapterID, VerseNumber, Rukuh, Sajdah, Manzil, HizbQuarter, VerseText) VALUES (9, 114, '0', '0', '0', '0', 'وما كان استغفار إبراهيم عليه السلام لأبيه المشرك، إلا عن موعدة وعدها إياه، وهي قوله: {سَأَسْتَغْفِرُ لَكَ رَبِّي إِنَّهُ كَانَ بِي حَفِيًّا (19:47)}. فلما تبيَّن لإبراهيم أن أباه عدو لله ولم ينفع فيه الوعظ والتذكير، وأنه سيموت كافرًا، تركه وترك الاستغفار له، وتبرأ منه. إن إبراهيم عليه السلام عظيم التضرع لله، كثير الصفح عما يصدر مِن قومه من الزلات.');
INSERT INTO Quran__Arabic_Tafseer_Muyassar (QuranChapterID, VerseNumber, Rukuh, Sajdah, Manzil, HizbQuarter, VerseText) VALUES (9, 115, '0', '0', '0', '0', 'وما كان الله ليضلَّ قومًا بعد أن مَنَّ عليهم بالهداية والتوفيق حتى يبيِّن لهم ما يتقونه به، وما يحتاجون إليه في أصول الدين وفروعه. إن الله بكل شيء عليم، فقد علَّمكم ما لم تكونوا تعلمون، وبيَّن لكم ما به تنتفعون، وأقام الحجة عليكم بإبلاغكم رسالته.');
INSERT INTO Quran__Arabic_Tafseer_Muyassar (QuranChapterID, VerseNumber, Rukuh, Sajdah, Manzil, HizbQuarter, VerseText) VALUES (9, 116, '0', '0', '0', '0', 'إن لله مالك السموات والأرض وما فيهن لا شريك له في الخلق والتدبير والعبادة والتشريع، يحيي مَن يشاء ويميت مَن يشاء، وما لكم مِن أحد غير الله يتولى أموركم، ولا نصير ينصركم على عدوكم.');
INSERT INTO Quran__Arabic_Tafseer_Muyassar (QuranChapterID, VerseNumber, Rukuh, Sajdah, Manzil, HizbQuarter, VerseText) VALUES (9, 117, '0', '0', '0', '0', 'لقد وفَّق الله نبيه محمدا صلى الله عليه وسلم إلى الإنابة إليه وطاعته، وتاب الله على المهاجرين الذين هجروا ديارهم وعشيرتهم إلى دار الإسلام، وتاب على أنصار رسول الله صلى الله عليه وسلم الذين خرجوا معه لقتال الأعداء في غزوة (تبوك) في حرٍّ شديد، وضيق من الزاد والظَّهْر، لقد تاب الله عليهم من بعد ما كاد يَميل قلوب بعضهم عن الحق، فيميلون إلى الدَّعة والسكون، لكن الله ثبتهم وقوَّاهم وتاب عليهم، إنه بهم رؤوف رحيم. ومن رحمته بهم أنْ مَنَّ عليهم بالتوبة، وقَبِلَها منهم، وثبَّتهم عليها.');
INSERT INTO Quran__Arabic_Tafseer_Muyassar (QuranChapterID, VerseNumber, Rukuh, Sajdah, Manzil, HizbQuarter, VerseText) VALUES (9, 118, '1', '0', '0', '0', 'وكذلك تاب الله على الثلاثة الذين خُلِّفوا من الأنصار -وهم كعب بن مالك وهلال بن أُميَّة ومُرَارة بن الربيع- تخلَّفوا عن رسول الله صلى الله عليه وسلم، وحزنوا حزنًا شديدًا، حتى إذا ضاقت عليهم الأرض بسَعَتها غمًّا وندمًا بسبب تخلفهم، وضاقت عليهم أنفسهم لِمَا أصابهم من الهم، وأيقنوا أن لا ملجأ من الله إلا إليه، وفَّقهم الله سبحانه وتعالى إلى الطاعة والرجوع إلى ما يرضيه سبحانه. إن الله هو التواب على عباده، الرحيم بهم.');
INSERT INTO Quran__Arabic_Tafseer_Muyassar (QuranChapterID, VerseNumber, Rukuh, Sajdah, Manzil, HizbQuarter, VerseText) VALUES (9, 119, '0', '0', '0', '0', 'يا أيها الذين صدَّقوا الله ورسوله وعملوا بشرعه، امتثلوا أوامر الله واجتنبوا نواهيه في كل ما تفعلون وتتركون، وكونوا مع الصادقين في أَيمانهم وعهودهم، وفي كل شأن من شؤونهم.');
INSERT INTO Quran__Arabic_Tafseer_Muyassar (QuranChapterID, VerseNumber, Rukuh, Sajdah, Manzil, HizbQuarter, VerseText) VALUES (9, 120, '0', '0', '0', '0', 'ما كان ينبغي لأهل مدينة رسول الله صلى الله عليه وسلم ومَن حولهم من سكان البادية أن يتخلَّفوا في أهلهم ودورهم عن رسول الله صلى الله عليه وسلم، ولا يرضوا لأنفسهم بالراحة والرسول صلى الله عليه وسلم في تعب ومشقة؛ ذلك بأنهم لا يصيبهم في سفرهم وجهادهم عطش ولا تعب ولا مجاعة في سبيل الله، ولا يطؤون أرضًا يُغضِبُ الكفارَ وطؤهم إياها، ولا يصيبون مِن عدو الله وعدوهم قتلا أو هزيمةً إلا كُتِب لهم بذلك كله ثواب عمل صالح. إن الله لا يضيع أجر المحسنين.');
INSERT INTO Quran__Arabic_Tafseer_Muyassar (QuranChapterID, VerseNumber, Rukuh, Sajdah, Manzil, HizbQuarter, VerseText) VALUES (9, 121, '0', '0', '0', '0', 'ولا ينفقون نفقة صغيرة ولا كبيرة في سبيل الله، ولا يقطعون واديًا في سيرهم مع رسول الله صلى الله عليه وسلم في جهاده، إلا كُتِب لهم أجر عملهم؛ ليجزيهم الله أحسن ما يُجْزَون به على أعمالهم الصالحة.');
INSERT INTO Quran__Arabic_Tafseer_Muyassar (QuranChapterID, VerseNumber, Rukuh, Sajdah, Manzil, HizbQuarter, VerseText) VALUES (9, 122, '1', '0', '0', '1', 'وما كان ينبغي للمؤمنين أن يخرجوا جميعًا لقتال عدوِّهم، كما لا يستقيم لهم أن يقعدوا جميعًا، فهلا خرج من كل فرقة جماعة تحصل بهم الكفاية والمقصود؛ وذلك ليتفقه النافرون في دين الله وما أنزل على رسوله، وينذروا قومهم بما تعلموه عند رجوعهم إليهم، لعلهم يحذرون عذاب الله بامتثال أوامره واجتناب نواهيه.');
INSERT INTO Quran__Arabic_Tafseer_Muyassar (QuranChapterID, VerseNumber, Rukuh, Sajdah, Manzil, HizbQuarter, VerseText) VALUES (9, 123, '0', '0', '0', '0', 'يا أيها الذين صدَّقوا الله ورسوله وعملوا بشرعه، ابدؤوا بقتال الأقرب فالأقرب إلى دار الإسلام من الكفار، وليجد الكفار فيكم غِلْظة وشدة، واعلموا أن الله مع المتقين بتأييده ونصره.');
INSERT INTO Quran__Arabic_Tafseer_Muyassar (QuranChapterID, VerseNumber, Rukuh, Sajdah, Manzil, HizbQuarter, VerseText) VALUES (9, 124, '0', '0', '0', '0', 'وإذا ما أنزل الله سورة من سور القرآن على رسوله، فمِن هؤلاء المنافقين من يقول: -إنكارًا واستهزاءً- أيُّكم زادته هذه السورة تصديقًا بالله وآياته؟ فأما الذين آمنوا بالله ورسوله فزادهم نزول السورة إيمانًا بالعلم بها وتدبرها واعتقادها والعمل بها، وهم يفرحون بما أعطاهم الله من الإيمان واليقين.');
INSERT INTO Quran__Arabic_Tafseer_Muyassar (QuranChapterID, VerseNumber, Rukuh, Sajdah, Manzil, HizbQuarter, VerseText) VALUES (9, 125, '0', '0', '0', '0', 'وأما الذين في قلوبهم نفاق وشك في دين الله، فإن نزول السورة يزيدهم نفاقًا وشكًا إلى ما هم عليه من قبلُ من النفاق والشك، وهلك هؤلاء وهم جاحدون بالله وآياته.');
INSERT INTO Quran__Arabic_Tafseer_Muyassar (QuranChapterID, VerseNumber, Rukuh, Sajdah, Manzil, HizbQuarter, VerseText) VALUES (9, 126, '0', '0', '0', '0', 'أولا يرى المنافقون أن الله يبتليهم بالقحط والشدة، وبإظهار ما يبطنون من النفاق مرة أو مرتين في كل عام؟ ثم هم مع ذلك لا يتوبون مِن كفرهم ونفاقهم، ولا هم يتعظون ولا يتذكرون بما يعاينون من آيات الله.');
INSERT INTO Quran__Arabic_Tafseer_Muyassar (QuranChapterID, VerseNumber, Rukuh, Sajdah, Manzil, HizbQuarter, VerseText) VALUES (9, 127, '0', '0', '0', '0', 'وإذا ما أُنزلت سورة تغَامَزَ المنافقون بالعيون إنكارًا لنزولها وسخرية وغيظًا؛ لِمَا نزل فيها مِن ذِكْر عيوبهم وأفعالهم، ثم يقولون: هل يراكم من أحد إن قمتم من عند الرسول؟ فإن لم يرهم أحد قاموا وانصرفوا من عنده عليه الصلاة والسلام مخافة الفضيحة. صرف الله قلوبهم عن الإيمان؛ بسبب أنهم لا يفهمون ولا يتدبرون.');
INSERT INTO Quran__Arabic_Tafseer_Muyassar (QuranChapterID, VerseNumber, Rukuh, Sajdah, Manzil, HizbQuarter, VerseText) VALUES (9, 128, '0', '0', '0', '0', 'لقد جاءكم أيها المؤمنون رسول من قومكم، يشق عليه ما تلقون من المكروه والعنت، حريص على إيمانكم وصلاح شأنكم، وهو بالمؤمنين كثير الرأفة والرحمة.');
INSERT INTO Quran__Arabic_Tafseer_Muyassar (QuranChapterID, VerseNumber, Rukuh, Sajdah, Manzil, HizbQuarter, VerseText) VALUES (9, 129, '1', '0', '0', '0', 'فإن أعرض المشركون والمنافقون عن الإيمان بك -أيها الرسول- فقل لهم: حسبي الله، يكفيني جميع ما أهمَّني، لا معبود بحق إلا هو، عليه اعتمدت، وإليه فَوَّضْتُ جميع أموري؛ فإنه ناصري ومعيني، وهو رب العرش العظيم، الذي هو أعظم المخلوقات.');
INSERT INTO Quran__Arabic_Tafseer_Muyassar (QuranChapterID, VerseNumber, Rukuh, Sajdah, Manzil, HizbQuarter, VerseText) VALUES (10, 1, '0', '0', '1', '0', '(الر) سبق الكلام على الحروف المقطَّعة في أول سورة البقرة.');
INSERT INTO Quran__Arabic_Tafseer_Muyassar (QuranChapterID, VerseNumber, Rukuh, Sajdah, Manzil, HizbQuarter, VerseText) VALUES (10, 2, '0', '0', '0', '0', 'أكان أمرًا عجبًا للناس إنزالنا الوحي بالقرآن على رجل منهم ينذرهم عقاب الله، ويبشِّر الذين آمنوا بالله ورسله أن لهم أجرًا حسنًا بما قدَّموا من صالح الأعمال؟ فلما أتاهم رسول الله صلى الله عليه وسلم بوحي الله وتلاه عليهم، قال المنكرون: إنَّ محمدًا ساحر، وما جاء به سحر ظاهر البطلان.');
INSERT INTO Quran__Arabic_Tafseer_Muyassar (QuranChapterID, VerseNumber, Rukuh, Sajdah, Manzil, HizbQuarter, VerseText) VALUES (10, 3, '0', '0', '0', '0', 'إن ربكم الله الذي أوجد السموات والأرض في ستة أيام، ثم استوى -أي علا وارتفع- على العرش استواء يليق بجلاله وعظمته، يدبر أمور خلقه، لا يضادُّه في قضائه أحد، ولا يشفع عنده شافع يوم القيامة إلا من بعد أن يأذن له بالشفاعة، فاعبدوا الله ربكم المتصف بهذه الصفات، وأخلصوا له العبادة. أفلا تتعظون وتعتبرون بهذه الآيات والحجج؟');
INSERT INTO Quran__Arabic_Tafseer_Muyassar (QuranChapterID, VerseNumber, Rukuh, Sajdah, Manzil, HizbQuarter, VerseText) VALUES (10, 4, '0', '0', '0', '0', 'إلى ربكم معادكم يوم القيامة جميعًا، وهذا وعد الله الحق، هو الذي يبدأ إيجاد الخلق ثم يعيده بعد الموت، فيوجده حيًا كهيئته الأولى، ليجزي مَن صَدَّق الله ورسوله، وعمل الأعمال الحسنة أحسن الجزاء بالعدل. والذين جحدوا وحدانية الله ورسالة رسوله لهم شراب من ماء شديد الحرارة يشوي الوجوه ويقطِّع الأمعاء، ولهم عذاب موجع بسبب كفرهم وضلالهم.');
INSERT INTO Quran__Arabic_Tafseer_Muyassar (QuranChapterID, VerseNumber, Rukuh, Sajdah, Manzil, HizbQuarter, VerseText) VALUES (10, 5, '0', '0', '0', '0', 'الله هو الذي جعل الشمس ضياء، وجعل القمر نورًا، وقدَّر القمر منازل، فبالشمس تعرف الأيام، وبالقمر تعرف الشهور والأعوام، ما خلق الله تعالى الشمس والقمر إلا لحكمة عظيمة، ودلالة على كمال قدرة الله وعلمه، يبيِّن الحجج والأدلة لقوم يعلمون الحكمة في إبداع الخلق.');
INSERT INTO Quran__Arabic_Tafseer_Muyassar (QuranChapterID, VerseNumber, Rukuh, Sajdah, Manzil, HizbQuarter, VerseText) VALUES (10, 6, '0', '0', '0', '0', 'إن في تعاقب الليل والنهار وما خلق الله في السموات والأرض من عجائب الخلق وما فيهما من إبداع ونظام، لأدلة وحججًا واضحة لقوم يخشون عقاب الله وسخطه وعذابه.');
INSERT INTO Quran__Arabic_Tafseer_Muyassar (QuranChapterID, VerseNumber, Rukuh, Sajdah, Manzil, HizbQuarter, VerseText) VALUES (10, 7, '0', '0', '0', '0', 'إن الذين لا يطمعون في لقائنا في الآخرة للحساب، وما يتلوه من الجزاء على الأعمال لإنكارهم البعث، ورضوا بالحياة الدنيا عوضًا عن الآخرة، وركنوا إليها، والذين هم عن آياتنا الكونية والشرعية ساهون.');
INSERT INTO Quran__Arabic_Tafseer_Muyassar (QuranChapterID, VerseNumber, Rukuh, Sajdah, Manzil, HizbQuarter, VerseText) VALUES (10, 8, '0', '0', '0', '0', 'أولئك مقرُّهم نار جهنم في الآخرة؛ جزاء بما كانوا يكسبون في دنياهم من الآثام والخطايا.');
INSERT INTO Quran__Arabic_Tafseer_Muyassar (QuranChapterID, VerseNumber, Rukuh, Sajdah, Manzil, HizbQuarter, VerseText) VALUES (10, 9, '0', '0', '0', '0', 'إن الذين آمنوا بالله ورسوله وعملوا الصالحات يدلهم ربهم إلى طريق الجنة ويوفقهم إلى العمل الموصل إليه؛ بسبب إيمانهم، ثم يثيبهم بدخول الجنة وإحلال رضوانه عليهم، تجري من تحتهم الأنهار في جنات النعيم.');
INSERT INTO Quran__Arabic_Tafseer_Muyassar (QuranChapterID, VerseNumber, Rukuh, Sajdah, Manzil, HizbQuarter, VerseText) VALUES (10, 10, '1', '0', '0', '0', 'دعاؤهم في الجنة التسبيح (سبحانك اللهم)، وتحية الله وملائكته لهم، وتحية بعضهم بعضًا في الجنة (سلام)، وآخر دعائهم قولهم: "الحمد لله رب العالمين" أي: الشكر والثناء لله خالق المخلوقات ومربِّيها بنعمه.');
INSERT INTO Quran__Arabic_Tafseer_Muyassar (QuranChapterID, VerseNumber, Rukuh, Sajdah, Manzil, HizbQuarter, VerseText) VALUES (10, 11, '0', '0', '0', '1', 'ولو يعجِّل الله للناس إجابة دعائهم في الشر كاستعجاله لهم في الخير بالإجابة لهلكوا، فنترك الذين لا يخافون عقابنا، ولا يوقنون بالبعث والنشور في تمرُّدهم وعتوِّهم، يترددون حائرين.');
INSERT INTO Quran__Arabic_Tafseer_Muyassar (QuranChapterID, VerseNumber, Rukuh, Sajdah, Manzil, HizbQuarter, VerseText) VALUES (10, 12, '0', '0', '0', '0', 'وإذا أصاب الإنسانَ الشدةُ استغاث بنا في كشف ذلك عنه مضطجعًا لجنبه أو قاعدًا أو قائمًا، على حسب الحال التي يكون بها عند نزول ذلك الضرِّ به. فلما كشفنا عنه الشدة التي أصابته استمرَّ على طريقته الأولى قبل أن يصيبه الضر، ونسي ما كان فيه من الشدة والبلاء، وترك الشكر لربه الذي فرَّج عنه ما كان قد نزل به من البلاء، كما زُيِّن لهذا الإنسان استمراره على جحوده وعناده بعد كشف الله عنه ما كان فيه من الضر، زُيِّن للذين أسرفوا في الكذب على الله وعلى أنبيائه ما كانوا يعملون من معاصي الله والشرك به.');
INSERT INTO Quran__Arabic_Tafseer_Muyassar (QuranChapterID, VerseNumber, Rukuh, Sajdah, Manzil, HizbQuarter, VerseText) VALUES (10, 13, '0', '0', '0', '0', 'ولقد أهلكنا الأمم التي كذَّبت رسل الله من قبلكم -أيها المشركون بربهم- لـمَّا أشركوا، وجاءتهم رسلهم من عند الله بالمعجزات الواضحات والحجج التي تبين صدق مَن جاء بها، فلم تكن هذه الأمم التي أهلكناها لتصدق رسلها وتنقاد لها، فاستحقوا الهلاك، ومثل ذلك الإهلاك نجزي كل مجرم متجاوز حدود الله.');
INSERT INTO Quran__Arabic_Tafseer_Muyassar (QuranChapterID, VerseNumber, Rukuh, Sajdah, Manzil, HizbQuarter, VerseText) VALUES (10, 14, '0', '0', '0', '0', 'ثم جعلناكم -أيها الناس- خَلَفًا في الأرض من بعد القرون الـمُهْلَكة، لننظر كيف تعملون: أخيرًا أم شرًا، فنجازيكم بذلك حسب عملكم.');
INSERT INTO Quran__Arabic_Tafseer_Muyassar (QuranChapterID, VerseNumber, Rukuh, Sajdah, Manzil, HizbQuarter, VerseText) VALUES (10, 15, '0', '0', '0', '0', 'وإذا تتلى على المشركين آيات الله التي أنزلناها إليك -أيها الرسول- واضحات، قال الذين لا يخافون الحساب، ولا يرجون الثواب، ولا يؤمنون بيوم البعث والنشور: ائت بقرآن غير هذا، أو بدِّل هذا القرآن: بأن تجعل الحلال حرامًا، والحرام حلالا والوعد وعيدًا، والوعيد وعدًا، وأن تُسْقط ما فيه مِن عيب آلهتنا وتسفيه أحلامنا، قل لهم -أيها الرسول-: إن ذلك ليس إليَّ، وإنما أتبع في كل ما آمركم به وأنهاكم عنه ما ينزله عليَّ ربي ويأمرني به، إني أخشى من الله -إن خالفت أمره- عذاب يوم عظيم وهو يوم القيامة.');
INSERT INTO Quran__Arabic_Tafseer_Muyassar (QuranChapterID, VerseNumber, Rukuh, Sajdah, Manzil, HizbQuarter, VerseText) VALUES (10, 16, '0', '0', '0', '0', 'قل لهم -أيها الرسول-: لو شاء الله ما تلوت هذا القرآن عليكم، ولا أعلمكم الله به، فاعلموا أنه الحق من الله، فإنكم تعلمون أنني مكثت فيكم زمنًا طويلا من قبل أن يوحيه إليَّ ربي، ومن قبل أن أتلوه عليكم، أفلا تستعملون عقولكم بالتدبر والتفكر؟');
INSERT INTO Quran__Arabic_Tafseer_Muyassar (QuranChapterID, VerseNumber, Rukuh, Sajdah, Manzil, HizbQuarter, VerseText) VALUES (10, 17, '0', '0', '0', '0', 'لا أحد أشد ظلمًا ممن اختلق على الله الكذب أو كذَّب بآياته إنه لا ينجح مَن كذَّب أنبياء الله ورسله، ولا ينالون الفلاح.');
INSERT INTO Quran__Arabic_Tafseer_Muyassar (QuranChapterID, VerseNumber, Rukuh, Sajdah, Manzil, HizbQuarter, VerseText) VALUES (10, 18, '0', '0', '0', '0', 'ويعبد هؤلاء المشركون من دون الله ما لا يضرهم شيئًا، ولا ينفعهم في الدنيا والآخرة، ويقولون: إنما نعبدهم ليشفعوا لنا عند الله، قل لهم -أيها الرسول-: أتخبرون الله تعالى بشيء لا يعلمه مِن أمر هؤلاء الشفعاء في السموات أو في الأرض؟ فإنه لو كان فيهما شفعاء يشفعون لكم عنده لكان أعلم بهم منكم، فالله تعالى منزَّه عما يفعله هؤلاء المشركون من إشراكهم في عبادته ما لا يضر ولا ينفع.');
INSERT INTO Quran__Arabic_Tafseer_Muyassar (QuranChapterID, VerseNumber, Rukuh, Sajdah, Manzil, HizbQuarter, VerseText) VALUES (10, 19, '0', '0', '0', '0', 'كان الناس على دين واحد وهو الإسلام، ثم اختلفوا بعد ذلك، فكفر بعضهم، وثبت بعضهم على الحق. ولولا كلمة سبقت من الله بإمهال العاصين وعدم معاجلتهم بذنوبهم لقُضِيَ بينهم: بأن يُهْلك أهل الباطل منهم، وينجي أهل الحق.');
INSERT INTO Quran__Arabic_Tafseer_Muyassar (QuranChapterID, VerseNumber, Rukuh, Sajdah, Manzil, HizbQuarter, VerseText) VALUES (10, 20, '1', '0', '0', '0', 'ويقول هؤلاء الكفرة المعاندون: هلاَّ أُنزل على محمد علم ودليل، وآية حسية من ربه نعلم بها أنه على حق فيما يقول، فقل لهم -أيها الرسول-: لا يعلم الغيب أحد إلا الله، فإن شاء فعل وإن شاء لم يفعل، فانتظروا -أيها القوم- قضاء الله بيننا وبينكم بتعجيل عقوبته للمبطل منا، ونصرة صاحب الحق، إني منتظر ذلك.');
INSERT INTO Quran__Arabic_Tafseer_Muyassar (QuranChapterID, VerseNumber, Rukuh, Sajdah, Manzil, HizbQuarter, VerseText) VALUES (10, 21, '0', '0', '0', '0', 'وإذا أذقنا المشركين يسرًا وفرجًا ورخاءً بعد عسر وشدة وكرب أصابهم، إذا هم يكذِّبون، ويستهزئون بآيات الله، قل -أيها الرسول- لهؤلاء المشركين المستهزئين: الله أسرع مكرًا واستدراجًا وعقوبة لكم. إن حَفَظَتَنا الذين نرسلهم إليكم يكتبون عليكم ما تمكرون في آياتنا، ثم نحاسبكم على ذلك.');
INSERT INTO Quran__Arabic_Tafseer_Muyassar (QuranChapterID, VerseNumber, Rukuh, Sajdah, Manzil, HizbQuarter, VerseText) VALUES (10, 22, '0', '0', '0', '0', 'هو الذي يسيِّركم -أيها الناس- في البر على الدواب وغيرها، وفي البحر في السُّفُن، حتى إذا كنتم فيها وجرت بريح طيبة، وفرح ركاب السفن بالريح الطيبة، جاءت هذه السفنَ ريحٌ شديدة، وجاء الركابَ الموجُ (وهو ما ارتفع من الماء) من كل مكان، وأيقنوا أن الهلاك قد أحاط بهم، أخلصوا الدعاء لله وحده، وتركوا ما كانوا يعبدون، وقالوا: لئن أنجيتنا من هذه الشدة التي نحن فيها لنكونن من الشاكرين لك على نِعَمك.');
INSERT INTO Quran__Arabic_Tafseer_Muyassar (QuranChapterID, VerseNumber, Rukuh, Sajdah, Manzil, HizbQuarter, VerseText) VALUES (10, 23, '0', '0', '0', '0', 'فلما أنجاهم الله من الشدائد والأهوال إذا هم يعملون في الأرض بالفساد وبالمعاصي. يا أيها الناس إنما وَبالُ بغيكم راجع على أنفسكم، لكم متاع في الحياة الدنيا الزائلة، ثم إلينا مصيركم ومرجعكم، فنخبركم بجميع أعمالكم، ونحاسبكم عليها.');
INSERT INTO Quran__Arabic_Tafseer_Muyassar (QuranChapterID, VerseNumber, Rukuh, Sajdah, Manzil, HizbQuarter, VerseText) VALUES (10, 24, '0', '0', '0', '0', 'إنما مثل الحياة الدنيا وما تتفاخرون به فيها من زينة وأموال، كمثل مطر أنزلناه من السماء إلى الأرض، فنبتت به أنواع من النبات مختلط بعضها ببعض مما يقتات به الناس من الثمار، وما تأكله الحيوانات من النبات، حتى إذا ظهر حُسْنُ هذه الأرض وبهاؤها، وظن أهل هذه الأرض أنهم قادرون على حصادها والانتفاع بها، جاءها أمرنا وقضاؤنا بهلاك ما عليها من النبات، والزينة إما ليلا وإما نهارًا، فجعلنا هذه النباتات والأشجار محصودة مقطوعة لا شيء فيها، كأن لم تكن تلك الزروع والنباتات قائمة قبل ذلك على وجه الأرض، فكذلك يأتي الفناء على ما تتباهَون به من دنياكم وزخارفها فيفنيها الله ويهلكها. وكما بيَّنا لكم -أيها الناس- مَثَلَ هذه الدنيا وعرَّفناكم بحقيقتها، نبيِّن حججنا وأدلتنا لقوم يتفكرون في آيات الله، ويتدبرون ما ينفعهم في الدنيا والآخرة.');
INSERT INTO Quran__Arabic_Tafseer_Muyassar (QuranChapterID, VerseNumber, Rukuh, Sajdah, Manzil, HizbQuarter, VerseText) VALUES (10, 25, '0', '0', '0', '0', 'والله يدعوكم إلى جناته التي أعدها لأوليائه، ويهدي مَن يشاء مِن خَلْقه، فيوفقه لإصابة الطريق المستقيم، وهو الإسلام.');
INSERT INTO Quran__Arabic_Tafseer_Muyassar (QuranChapterID, VerseNumber, Rukuh, Sajdah, Manzil, HizbQuarter, VerseText) VALUES (10, 26, '0', '0', '0', '1', 'للمؤمنين الذين أحسنوا عبادة الله فأطاعوه فيما أمر ونهى، الجنةُ، وزيادة عليها، وهي النظر إلى وجه الله تعالى في الجنة، والمغفرةُ والرضوان، ولا يغشى وجوههم غبار ولا ذلة، كما يلحق أهل النار. هؤلاء المتصفون بهذه الصفات هم أصحاب الجنة ماكثون فيها أبدًا.');
INSERT INTO Quran__Arabic_Tafseer_Muyassar (QuranChapterID, VerseNumber, Rukuh, Sajdah, Manzil, HizbQuarter, VerseText) VALUES (10, 27, '0', '0', '0', '0', 'والذين عملوا السيئات في الدنيا فكفروا وعصَوا الله لهم جزاء أعمالهم السيئة التي عملوها بمثلها من عقاب الله في الآخرة، وتغشاهم ذلَّة وهوان، وليس لهم مِن عذاب الله مِن مانع يمنعهم إذا عاقبهم، كأنما أُلبست وجوههم طائفة من سواد الليل المظلم. هؤلاء هم أهل النار ماكثون فيها أبدًا.');
INSERT INTO Quran__Arabic_Tafseer_Muyassar (QuranChapterID, VerseNumber, Rukuh, Sajdah, Manzil, HizbQuarter, VerseText) VALUES (10, 28, '0', '0', '0', '0', 'واذكر -أيها الرسول- يوم نحشر الخلق جميعا للحساب والجزاء، ثم نقول للذين أشركوا بالله: الزموا مكانكم أنتم وشركاؤكم الذين كنتم تعبدونهم من دون الله حتى تنظروا ما يُفْعل بكم، فَفَرَّقْنا بين المشركين ومعبوديهم، وتبرَّأ مَن عُبِدُوا مِن دون الله ممن كانوا يعبدونهم، وقالوا للمشركين: ما كنتم إيانا تعبدون في الدنيا.');
INSERT INTO Quran__Arabic_Tafseer_Muyassar (QuranChapterID, VerseNumber, Rukuh, Sajdah, Manzil, HizbQuarter, VerseText) VALUES (10, 29, '0', '0', '0', '0', 'فكفى بالله شهيدًا بيننا وبينكم، إننا لم نكن نعلم ما كنتم تقولون وتفعلون، ولقد كنَّا عن عبادتكم إيانا غافلين، لا نشعر بها.');
INSERT INTO Quran__Arabic_Tafseer_Muyassar (QuranChapterID, VerseNumber, Rukuh, Sajdah, Manzil, HizbQuarter, VerseText) VALUES (10, 30, '1', '0', '0', '0', 'في ذلك الموقف للحساب تتفقد كل نفس أحوالها وأعمالها التي سلفت وتعاينها، وتجازى بحسبها: إن خيرًا فخير، وإن شرًا فشر، ورُدَّ الجميع إلى الله الحكم العدل، فأُدخِلَ أهل الجنةِ الجنةَ وأهل النار النار، وذهب عن المشركين ما كانوا يعبدون من دون الله افتراء عليه.');
INSERT INTO Quran__Arabic_Tafseer_Muyassar (QuranChapterID, VerseNumber, Rukuh, Sajdah, Manzil, HizbQuarter, VerseText) VALUES (10, 31, '0', '0', '0', '0', 'قل -أيها الرسول- لهؤلاء المشركين: مَن يرزقكم من السماء، بما يُنزله من المطر، ومن الأرض بما ينبته فيها من أنواع النبات والشجر تأكلون منه أنتم وأنعامكم؟ ومَن يملك ما تتمتعون به أنتم وغيركم من حواسِّ السمع والأبصار؟ ومن ذا الذي يملك الحياة والموت في الكون كلِّه، فيخرج الأحياء والأموات بعضها من بعض فيما تعرفون من المخلوقات، وفيما لا تعرفون؟ ومَن يدبِّر أمر السماء والأرض وما فيهن، وأمركم وأمر الخليقة جميعًا؟ فسوف يجيبونك بأن الذي يفعل ذلك كله هو الله، فقل لهم: أفلا تخافون عقاب الله إن عبدتم معه غيره؟');
INSERT INTO Quran__Arabic_Tafseer_Muyassar (QuranChapterID, VerseNumber, Rukuh, Sajdah, Manzil, HizbQuarter, VerseText) VALUES (10, 32, '0', '0', '0', '0', 'فذلكم الله ربكم هو الحق الذي لا ريب فيه، المستَحِق للعبادة وحده لا شريك له، فأي شيء سوى الحق إلا الضلال؟، فكيف تُصْرَفون عن عبادته إلى عبادة ما سواه؟');
INSERT INTO Quran__Arabic_Tafseer_Muyassar (QuranChapterID, VerseNumber, Rukuh, Sajdah, Manzil, HizbQuarter, VerseText) VALUES (10, 33, '0', '0', '0', '0', 'كما كفر هؤلاء المشركون واستمرُّوا على شركهم، حقت كلمة ربك وحكمه وقضاؤه على الذين خرجوا عن طاعة ربهم إلى معصيته وكفروا به أنَّهم لا يصدقون بوحدانية الله، ولا بنبوة نبيِّه محمد صلى الله عليه وسلم، ولا يعملون بهديه.');
INSERT INTO Quran__Arabic_Tafseer_Muyassar (QuranChapterID, VerseNumber, Rukuh, Sajdah, Manzil, HizbQuarter, VerseText) VALUES (10, 34, '0', '0', '0', '0', 'قل لهم -أيها الرسول-: هل من آلهتكم ومعبوداتكم مَن يبدأ خَلْق أي شيء من غير أصل، ثم يفنيه بعد إنشائه، ثم يعيده كهيئته قبل أن يفنيه؟ فإنهم لا يقدرون على دعوى ذلك، قل -أيها الرسول-: الله تعالى وحده هو الذي ينشئ الخلق ثم يفنيه ثم يعيده، فكيف تنصرفون عن طريق الحق إلى الباطل، وهو عبادة غير الله؟');
INSERT INTO Quran__Arabic_Tafseer_Muyassar (QuranChapterID, VerseNumber, Rukuh, Sajdah, Manzil, HizbQuarter, VerseText) VALUES (10, 35, '0', '0', '0', '0', 'قل -أيها الرسول- لهؤلاء المشركين: هل مِن شركائكم مَن يرشد إلى الطريق المستقيم؟ فإنهم لا يقدرون على ذلك، قل لهم: الله وحده يهدي الضال عن الهدى إلى الحق. أيهما أحق بالاتباع: مَن يهدي وحده للحق أم من لا يهتدي لعدم علمه ولضلاله، وهي شركاؤكم التي لا تَهدي ولا تَهتدي إلا أن تُهدَى؟ فما بالكم كيف سوَّيتم بين الله وخلقه؟ وهذا حكم باطل.');
INSERT INTO Quran__Arabic_Tafseer_Muyassar (QuranChapterID, VerseNumber, Rukuh, Sajdah, Manzil, HizbQuarter, VerseText) VALUES (10, 36, '0', '0', '0', '0', 'وما يتبع أكثر هؤلاء المشركين في جعلهم الأصنام آلهة واعتقادهم بأنها تقرِّب إلى الله إلا تخرصًا وظنًا، وهو لا يغني من اليقين شيئًا. إن الله عليم بما يفعل هؤلاء المشركون من الكفر والتكذيب.');
INSERT INTO Quran__Arabic_Tafseer_Muyassar (QuranChapterID, VerseNumber, Rukuh, Sajdah, Manzil, HizbQuarter, VerseText) VALUES (10, 37, '0', '0', '0', '0', 'وما كان يتهيَّأ لأحد أن يأتي بهذا القرآن مِن عند غير الله، لأنه لا يقدر على ذلك أحد من الخلق، ولكن الله أنزله مصدِّقا للكتب التي أنزلها على أنبيائه؛ لأن دين الله واحد، وفي هذا القرآن بيان وتفصيل لما شرعه الله لأمة محمد صلى الله عليه وسلم، لا شك في أن هذا القرآن موحىً من رب العالمين.');
INSERT INTO Quran__Arabic_Tafseer_Muyassar (QuranChapterID, VerseNumber, Rukuh, Sajdah, Manzil, HizbQuarter, VerseText) VALUES (10, 38, '0', '0', '0', '0', 'بل أيقولون: إن هذا القرآن افتراه محمد من عند نفسه؟ فإنهم يعلمون أنه بشر مثلهم!! قل لهم -أيها الرسول-: فأتوا أنتم بسورة واحدة من جنس هذا القرآن في نظمه وهدايته، واستعينوا على ذلك بكل مَن قَدَرْتم عليه من دون الله من إنس وجن، إن كنتم صادقين في دعواكم.');
INSERT INTO Quran__Arabic_Tafseer_Muyassar (QuranChapterID, VerseNumber, Rukuh, Sajdah, Manzil, HizbQuarter, VerseText) VALUES (10, 39, '0', '0', '0', '0', 'بل سارَعوا إلى التكذيب بالقرآن أول ما سمعوه، قبل أن يتدبروا آياته، وكفروا بما لم يحيطوا بعلمه من ذكر البعث والجزاء والجنة والنار وغير ذلك، ولم يأتهم بعدُ حقيقة ما وُعِدوا به في الكتاب. وكما كذَّب المشركون بوعيد الله كذَّبت الأمم التي خلت قبلهم، فانظر -أيها الرسول- كيف كانت عاقبة الظالمين؟ فقد أهلك الله بعضهم بالخسف، وبعضهم بالغرق، وبعضهم بغير ذلك.');
INSERT INTO Quran__Arabic_Tafseer_Muyassar (QuranChapterID, VerseNumber, Rukuh, Sajdah, Manzil, HizbQuarter, VerseText) VALUES (10, 40, '1', '0', '0', '0', 'ومِن قومك -أيها الرسول- مَن يصدِّق بالقرآن، ومنهم من لا يصدِّق به حتى يموت على ذلك ويبعث عليه، وربك أعلم بالمفسدين الذين لا يؤمنون به على وجه الظلم والعناد والفساد، فيجازيهم على فسادهم بأشد العذاب.');
INSERT INTO Quran__Arabic_Tafseer_Muyassar (QuranChapterID, VerseNumber, Rukuh, Sajdah, Manzil, HizbQuarter, VerseText) VALUES (10, 41, '0', '0', '0', '0', 'وإن كذَّبك -أيها الرسول- هؤلاء المشركون فقل لهم: لي ديني وعملي، ولكم دينكم وعملكم، فأنتم لا تؤاخَذون بعملي، وأنا لا أؤاخَذ بعملكم.');
INSERT INTO Quran__Arabic_Tafseer_Muyassar (QuranChapterID, VerseNumber, Rukuh, Sajdah, Manzil, HizbQuarter, VerseText) VALUES (10, 42, '0', '0', '0', '0', 'ومِنَ الكفار مَن يسمعون كلامك الحق، وتلاوتك القرآن، ولكنهم لا يهتدون. أفأنت تَقْدر على إسماع الصم؟ فكذلك لا تقدر على هداية هؤلاء إلا أن يشاء الله هدايتهم؛ لأنهم صمٌّ عن سماع الحق، لا يعقلونه.');
INSERT INTO Quran__Arabic_Tafseer_Muyassar (QuranChapterID, VerseNumber, Rukuh, Sajdah, Manzil, HizbQuarter, VerseText) VALUES (10, 43, '0', '0', '0', '0', 'ومِنَ الكفار مَن ينظر إليك وإلى أدلة نبوتك الصادقة، ولكنه لا يبصر ما آتاك الله من نور الإيمان، أفأنت -أيها الرسول- تقدر على أن تخلق للعمي أبصارًا يهتدون بها؟ فكذلك لا تقدر على هدايتهم إذا كانوا فاقدي البصيرة، وإنما ذلك كلُّه لله وحده.');
INSERT INTO Quran__Arabic_Tafseer_Muyassar (QuranChapterID, VerseNumber, Rukuh, Sajdah, Manzil, HizbQuarter, VerseText) VALUES (10, 44, '0', '0', '0', '0', 'إن الله لا يظلم الناس شيئًا بزيادة في سيئاتهم أو نقص من حسناتهم، ولكن الناس هم الذين يظلمون أنفسهم بالكفر والمعصية ومخالفة أمر الله ونهيه.');
INSERT INTO Quran__Arabic_Tafseer_Muyassar (QuranChapterID, VerseNumber, Rukuh, Sajdah, Manzil, HizbQuarter, VerseText) VALUES (10, 45, '0', '0', '0', '0', 'ويوم يَحشر الله هؤلاء المشركين يوم البعث والحساب، كأنهم قبل ذلك لم يمكثوا في الحياة الدنيا إلا قدر ساعة من النهار، يعرف بعضهم بعضًا كحالهم في الدنيا، ثم انقطعت تلك المعرفة وانقضت تلك الساعة. قد خسر الذين كفروا وكذَّبوا بلقاء الله وثوابه وعقابه، وما كانوا موفَّقين لإصابة الرشد فيما فعلوا.');
INSERT INTO Quran__Arabic_Tafseer_Muyassar (QuranChapterID, VerseNumber, Rukuh, Sajdah, Manzil, HizbQuarter, VerseText) VALUES (10, 46, '0', '0', '0', '0', 'وإمَّا نرينَّك -أيها الرسول- في حياتك بعض الذي نَعِدُهم من العقاب في الدنيا، أو نتوفينك قبل أن نريك ذلك فيهم، فإلينا وحدنا يرجع أمرهم في الحالتين، ثم الله شهيد على أفعالهم التي كانوا يفعلونها في الدنيا، لا يخفى عليه شيء منها، فيجازيهم بها جزاءهم الذي يستحقونه.');
INSERT INTO Quran__Arabic_Tafseer_Muyassar (QuranChapterID, VerseNumber, Rukuh, Sajdah, Manzil, HizbQuarter, VerseText) VALUES (10, 47, '0', '0', '0', '0', 'ولكل أمة خَلَتْ قبلكم -أيها الناس- رسول أرسلتُه إليهم، كما أرسلت محمدًا إليكم يدعو إلى دين الله وطاعته، فإذا جاء رسولهم في الآخرة قُضِيَ حينئذ بينهم بالعدل، وهم لا يُظلمون مِن جزاء أعمالهم شيئًا.');
INSERT INTO Quran__Arabic_Tafseer_Muyassar (QuranChapterID, VerseNumber, Rukuh, Sajdah, Manzil, HizbQuarter, VerseText) VALUES (10, 48, '0', '0', '0', '0', 'ويقول المشركون من قومك -أيها الرسول-: متى قيام الساعة إن كنت أنت ومَن تبعك من الصادقين فيما تَعِدوننا به؟');
INSERT INTO Quran__Arabic_Tafseer_Muyassar (QuranChapterID, VerseNumber, Rukuh, Sajdah, Manzil, HizbQuarter, VerseText) VALUES (10, 49, '0', '0', '0', '0', 'قل لهم -أيها الرسول-: لا أستطيع أن أدفع عن نفسي ضرًا، ولا أجلب لها نفعًا، إلا ما شاء الله أن يدفع عني مِن ضرٍّ أو يجلب لي من نفع. لكل قوم وقت لانقضاء مدتهم وأجلهم، إذا جاء وقت انقضاء أجلهم وفناء أعمارهم، فلا يستأخرون عنه ساعة فيُمْهلون، ولا يتقدم أجلهم عن الوقت المعلوم.');
INSERT INTO Quran__Arabic_Tafseer_Muyassar (QuranChapterID, VerseNumber, Rukuh, Sajdah, Manzil, HizbQuarter, VerseText) VALUES (10, 50, '0', '0', '0', '0', 'قل -أيها الرسول- لهؤلاء المشركين: أخبروني إن أتاكم عذاب الله ليلا أو نهارًا، فأي شيء تستعجلون أيها المجرمون بنزول العذاب؟');
INSERT INTO Quran__Arabic_Tafseer_Muyassar (QuranChapterID, VerseNumber, Rukuh, Sajdah, Manzil, HizbQuarter, VerseText) VALUES (10, 51, '0', '0', '0', '0', 'أبعدما وقع عذاب الله بكم -أيها المشركون- آمنتم في وقت لا ينفعكم فيه الإيمان؟ وقيل لكم حينئذ: آلآن تؤمنون به، وقد كنتم من قبل تستعجلون به؟');
INSERT INTO Quran__Arabic_Tafseer_Muyassar (QuranChapterID, VerseNumber, Rukuh, Sajdah, Manzil, HizbQuarter, VerseText) VALUES (10, 52, '0', '0', '0', '0', 'ثم قيل للذين ظلموا أنفسهم بكفرهم بالله: تجرَّعوا عذاب الله الدائم لكم أبدًا، فهل تُعاقَبون إلا بما كنتم تعملون في حياتكم من معاصي الله؟');
INSERT INTO Quran__Arabic_Tafseer_Muyassar (QuranChapterID, VerseNumber, Rukuh, Sajdah, Manzil, HizbQuarter, VerseText) VALUES (10, 53, '1', '0', '0', '1', 'ويستخبرك هؤلاء المشركون من قومك -أيها الرسول- عن العذاب يوم القيامة، أحقٌّ هو؟ قل لهم -أيها الرسول-: نعم وربي إنه لحق لا شك فيه، وما أنتم بمعجزين الله أن يبعثكم ويجازيكم، فأنتم في قبضته وسلطانه.');
INSERT INTO Quran__Arabic_Tafseer_Muyassar (QuranChapterID, VerseNumber, Rukuh, Sajdah, Manzil, HizbQuarter, VerseText) VALUES (10, 54, '0', '0', '0', '0', 'ولو أن لكل نفس أشركت وكفرت بالله جميع ما في الأرض، وأمكنها أن تجعله فداء لها من ذلك العذاب لافتدت به، وأخفى الذين ظلموا حسرتهم حين أبصروا عذاب الله واقعا بهم جميعًا، وقضى الله عز وجل بينهم بالعدل، وهم لا يُظلَمون؛ لأن الله تعالى لا يعاقب أحدا إلا بذنبه.');
INSERT INTO Quran__Arabic_Tafseer_Muyassar (QuranChapterID, VerseNumber, Rukuh, Sajdah, Manzil, HizbQuarter, VerseText) VALUES (10, 55, '0', '0', '0', '0', 'ألا إن كل ما في السموات وما في الأرض ملك لله تعالى، لا شيء من ذلك لأحد سواه. ألا إن لقاء الله تعالى وعذابه للمشركين كائن، ولكن أكثرهم لا يعلمون حقيقة ذلك.');
INSERT INTO Quran__Arabic_Tafseer_Muyassar (QuranChapterID, VerseNumber, Rukuh, Sajdah, Manzil, HizbQuarter, VerseText) VALUES (10, 56, '0', '0', '0', '0', 'إن الله هو المحيي والمميت لا يتعذَّر عليه إحياء الناس بعد موتهم، كما لا تعجزه إماتتهم إذا أراد ذلك، وهم إليه راجعون بعد موتهم.');
INSERT INTO Quran__Arabic_Tafseer_Muyassar (QuranChapterID, VerseNumber, Rukuh, Sajdah, Manzil, HizbQuarter, VerseText) VALUES (10, 57, '0', '0', '0', '0', 'يا أيها الناس قد جاءتكم موعظة من ربكم تذكِّركم عقاب الله وتخوفكم وعيده، وهي القرآن وما اشتمل عليه من الآيات والعظات لإصلاح أخلاقكم وأعمالكم، وفيه دواء لما في القلوب من الجهل والشرك وسائر الأمراض، ورشد لمن اتبعه من الخلق فينجيه من الهلاك، جعله سبحانه وتعالى نعمة ورحمة للمؤمنين، وخصَّهم بذلك؛ لأنهم المنتفعون بالإيمان، وأما الكافرون فهو عليهم عَمَى.');
INSERT INTO Quran__Arabic_Tafseer_Muyassar (QuranChapterID, VerseNumber, Rukuh, Sajdah, Manzil, HizbQuarter, VerseText) VALUES (10, 58, '0', '0', '0', '0', 'قل -أيها الرسول- لجميع الناس: بفضل الله وبرحمته، وهو ما جاءهم من الله من الهدى ودين الحق وهو الإسلام، فبذلك فليفرحوا؛ فإن الإسلام الذي دعاهم الله إليه، والقرآن الذي أنزله على محمد صلى الله عليه وسلم، خير مما يجمعون من حطام الدنيا وما فيها من الزهرة الفانية الذاهبة.');
INSERT INTO Quran__Arabic_Tafseer_Muyassar (QuranChapterID, VerseNumber, Rukuh, Sajdah, Manzil, HizbQuarter, VerseText) VALUES (10, 59, '0', '0', '0', '0', 'قل -أيها الرسول- لهؤلاء الجاحدين للوحي: أخبروني عن هذا الرزق الذي خلقه الله لكم من الحيوان والنبات والخيرات فحلَّلتم بعض ذلك لأنفسكم وحرَّمتم بعضه، قل لهم: آلله أذن لكم بذلك، أم تقولون على الله الباطل وتكذبون؟ وإنهم ليقولون على الله الباطل ويكذبون.');
INSERT INTO Quran__Arabic_Tafseer_Muyassar (QuranChapterID, VerseNumber, Rukuh, Sajdah, Manzil, HizbQuarter, VerseText) VALUES (10, 60, '1', '0', '0', '0', 'وما ظنُّ هؤلاء الذين يتخرصون على الله الكذب يوم الحساب، فيضيفون إليه تحريم ما لم يحرمه عليهم من الأرزاق والأقوات، أن الله فاعل بهم يوم القيامة بكذبهم وفِرْيَتِهم عليه؟ أيحسبون أنه يصفح عنهم ويغفر لهم؟ إن الله لذو فضل على خلقه؛ بتركه معاجلة مَن افترى عليه الكذب بالعقوبة في الدنيا وإمهاله إياه، ولكن أكثر الناس لا يشكرون الله على تفضله عليهم بذلك.');
INSERT INTO Quran__Arabic_Tafseer_Muyassar (QuranChapterID, VerseNumber, Rukuh, Sajdah, Manzil, HizbQuarter, VerseText) VALUES (10, 61, '0', '0', '0', '0', 'وما تكون -أيها الرسول- في أمر مِن أمورك وما تتلو من كتاب الله من آيات، وما يعمل أحد من هذه الأمة عملا من خير أو شر إلا كنا عليكم شهودًا مُطَّلِعين عليه، إذ تأخذون في ذلك، وتعملونه، فنحفظه عليكم ونجزيكم به، وما يغيب عن علم ربك -أيها الرسول- من زنة نملة صغيرة في الأرض ولا في السماء، ولا أصغر الأشياء ولا أكبرها، إلا في كتاب عند الله واضح جلي، أحاط به علمه وجرى به قلمه.');
INSERT INTO Quran__Arabic_Tafseer_Muyassar (QuranChapterID, VerseNumber, Rukuh, Sajdah, Manzil, HizbQuarter, VerseText) VALUES (10, 62, '0', '0', '0', '0', 'ألا إن أولياء الله لا خوف عليهم في الآخرة من عقاب الله، ولا هم يحزنون على ما فاتهم من حظوظ الدنيا.');
INSERT INTO Quran__Arabic_Tafseer_Muyassar (QuranChapterID, VerseNumber, Rukuh, Sajdah, Manzil, HizbQuarter, VerseText) VALUES (10, 63, '0', '0', '0', '0', 'وصفات هؤلاء الأولياء، أنهم الذين صدَّقوا الله واتبعوا رسوله وما جاء به من عند الله، وكانوا يتقون الله بامتثال أوامره، واجتناب معاصيه.');
INSERT INTO Quran__Arabic_Tafseer_Muyassar (QuranChapterID, VerseNumber, Rukuh, Sajdah, Manzil, HizbQuarter, VerseText) VALUES (10, 64, '0', '0', '0', '0', 'لهؤلاء الأولياء البشارة من الله في الحياة الدنيا بما يسرُّهم، وفي الآخرة بالجنة، لا يخلف الله وعده ولا يغيِّره، ذلك هو الفوز العظيم؛ لأنه اشتمل على النجاة مِن كل محذور، والظَّفَر بكل مطلوب محبوب.');
INSERT INTO Quran__Arabic_Tafseer_Muyassar (QuranChapterID, VerseNumber, Rukuh, Sajdah, Manzil, HizbQuarter, VerseText) VALUES (10, 65, '0', '0', '0', '0', 'ولا يحزنك -أيها الرسول- قول المشركين في ربهم وافتراؤهم عليه وإشراكهم معه الأوثان والأصنام؛ فإن الله تعالى هو المتفرد بالقوة الكاملة والقدرة التامة في الدنيا والآخرة، وهو السميع لأقوالهم، العليم بأفعالهم ونياتهم.');
INSERT INTO Quran__Arabic_Tafseer_Muyassar (QuranChapterID, VerseNumber, Rukuh, Sajdah, Manzil, HizbQuarter, VerseText) VALUES (10, 66, '0', '0', '0', '0', 'ألا إن لله كل مَن في السموات ومن في الأرض من الملائكة، والإنس، والجن وغير ذلك. وأي شيء يتَّبع مَن يدعو غير الله من الشركاء؟ ما يتَّبعون إلا الشك، وإن هم إلا يكذبون فيما ينسبونه إلى الله.');
INSERT INTO Quran__Arabic_Tafseer_Muyassar (QuranChapterID, VerseNumber, Rukuh, Sajdah, Manzil, HizbQuarter, VerseText) VALUES (10, 67, '0', '0', '0', '0', 'هو الذي جعل لكم -أيها الناس- الليل لتسكنوا فيه وتهدؤوا من عناء الحركة في طلب المعاش، وجعل لكم النهار؛ لتبصروا فيه، ولتسعَوْا لطلب رزقكم. إن في اختلاف الليل والنهار وحال أهلهما فيهما لَدلالةً وحججًا على أن الله وحده هو المستحق للعبادة، لقوم يسمعون هذه الحجج، ويتفكرون فيها.');
INSERT INTO Quran__Arabic_Tafseer_Muyassar (QuranChapterID, VerseNumber, Rukuh, Sajdah, Manzil, HizbQuarter, VerseText) VALUES (10, 68, '0', '0', '0', '0', 'قال المشركون: اتخذ الله ولدًا، كقولهم: الملائكة بنات الله، أو المسيح ابن الله. تقدَّس الله عن ذلك كله وتنزَّه، هو الغني عن كل ما سواه، له كل ما في السموات والأرض، فكيف يكون له ولد ممن خلق وكل شيء مملوك له؟ وليس لديكم دليل على ما تفترونه من الكذب، أتقولون على الله ما لا تعلمون حقيقته وصحته؟');
INSERT INTO Quran__Arabic_Tafseer_Muyassar (QuranChapterID, VerseNumber, Rukuh, Sajdah, Manzil, HizbQuarter, VerseText) VALUES (10, 69, '0', '0', '0', '0', 'قل: إن الذين يفترون على الله الكذب باتخاذ الولد وإضافة الشريك إليه، لا ينالون مطلوبهم في الدنيا ولا في الآخرة.');
INSERT INTO Quran__Arabic_Tafseer_Muyassar (QuranChapterID, VerseNumber, Rukuh, Sajdah, Manzil, HizbQuarter, VerseText) VALUES (10, 70, '1', '0', '0', '0', 'إنما يتمتعون في الدنيا بكفرهم وكذبهم متاعًا قصيرًا، ثم إذا انقضى أجلهم فإلينا مصيرهم، ثم نذيقهم عذاب جهنم بسبب كفرهم بالله وتكذيبهم رسل الله، وجحدهم آياته.');
INSERT INTO Quran__Arabic_Tafseer_Muyassar (QuranChapterID, VerseNumber, Rukuh, Sajdah, Manzil, HizbQuarter, VerseText) VALUES (10, 71, '0', '0', '0', '1', 'واقصص -أيها الرسول- على كفار "مكة" خبر نوح -عليه السلام- مع قومه حين قال لهم: إن كان عَظُمَ عليكم مقامي فيكم وتذكيري إياكم بحجج الله وبراهينه فعلى الله اعتمادي وبه ثقتي، فأعدُّوا أمركم، وادعوا شركاءكم، ثم لا تجعلوا أمركم عليكم مستترًا بل ظاهرًا منكشفًا، ثم اقضوا عليَّ بالعقوبة والسوء الذي في إمكانكم، ولا تمهلوني ساعة من نهار.');
INSERT INTO Quran__Arabic_Tafseer_Muyassar (QuranChapterID, VerseNumber, Rukuh, Sajdah, Manzil, HizbQuarter, VerseText) VALUES (10, 72, '0', '0', '0', '0', 'فإن أعرضتم عن دعوتي فإنني لم أسألكم أجرًا؛ لأن ثوابي عند ربي وأجري عليه سبحانه، وحده لا شريك له، وأمرت أن أكون من المنقادين لحكمه.');
INSERT INTO Quran__Arabic_Tafseer_Muyassar (QuranChapterID, VerseNumber, Rukuh, Sajdah, Manzil, HizbQuarter, VerseText) VALUES (10, 73, '0', '0', '0', '0', 'فكذب نوحًا قومُه فيما أخبرهم به عن الله، فنجَّيناه هو ومن معه في السفينة، وجعلناهم يَخْلُفون المكذبين في الأرض، وأغرقنا الذين جحدوا حججنا، فتأمل -أيها الرسول- كيف كان عاقبة القوم الذين أنذرهم رسولهم عذاب الله وبأسه؟');
INSERT INTO Quran__Arabic_Tafseer_Muyassar (QuranChapterID, VerseNumber, Rukuh, Sajdah, Manzil, HizbQuarter, VerseText) VALUES (10, 74, '0', '0', '0', '0', 'ثم بعثنا من بعد نوح رسلا إلى أقوامهم (هودًا وصالحًا وإبراهيم ولوطًا وشعيبًا وغيرَهم) فجاء كل رسول قومه بالمعجزات الدالة على رسالته، وعلى صحة ما دعاهم إليه، فما كانوا ليصدِّقوا ويعملوا بما كذَّب به قوم نوح ومَن سبقهم من الأمم الخالية. وكما ختم الله على قلوب هؤلاء الأقوام فلم يؤمنوا، كذلك يختم على قلوب مَن شابههم ممن بعدهم من الذين تجاوزوا حدود الله، وخالفوا ما دعاهم إليه رسلهم من طاعته عقوبة لهم على معاصيهم.');
INSERT INTO Quran__Arabic_Tafseer_Muyassar (QuranChapterID, VerseNumber, Rukuh, Sajdah, Manzil, HizbQuarter, VerseText) VALUES (10, 75, '0', '0', '0', '0', 'ثم بعثنا مِن بعد أولئك الرسل موسى وهارون عليهما السلام إلى فرعون وأشراف قومه بالمعجزات الدالة على صدقهما، فاستكبروا عن قَبول الحق، وكانوا قومًا مشركين مجرمين مكذبين.');
INSERT INTO Quran__Arabic_Tafseer_Muyassar (QuranChapterID, VerseNumber, Rukuh, Sajdah, Manzil, HizbQuarter, VerseText) VALUES (10, 76, '0', '0', '0', '0', 'فلما أتى فرعونَ وقومَه الحقُّ الذي جاء به موسى قالوا: إن الذي جاء به موسى من الآيات إنما هو سحر ظاهر.');
INSERT INTO Quran__Arabic_Tafseer_Muyassar (QuranChapterID, VerseNumber, Rukuh, Sajdah, Manzil, HizbQuarter, VerseText) VALUES (10, 77, '0', '0', '0', '0', 'قال لهم موسى متعجبًا مِن قولهم: أتقولون للحق لما جاءكم: إنه سحر مبين؟ انظروا وَصْفَ ما جاءكم وما اشتمل عليه تجدوه الحق. ولا يفلح الساحرون، ولا يفوزون في الدنيا ولا في الآخرة.');
INSERT INTO Quran__Arabic_Tafseer_Muyassar (QuranChapterID, VerseNumber, Rukuh, Sajdah, Manzil, HizbQuarter, VerseText) VALUES (10, 78, '0', '0', '0', '0', 'قال فرعون وملؤه لموسى: أجئتنا لتصرفنا عما وجدنا عليه آباءنا من عبادة غير الله، وتكون لكما أنت وهارون العظمة والسلطان في أرض "مصر"؟ وما نحن لكما بمقرِّين بأنكما رسولان أُرسلتما إلينا؛ لنعبد الله وحده لا شريك له.');
INSERT INTO Quran__Arabic_Tafseer_Muyassar (QuranChapterID, VerseNumber, Rukuh, Sajdah, Manzil, HizbQuarter, VerseText) VALUES (10, 79, '0', '0', '0', '0', 'وقال فرعون: جيئوني بكل ساحر متقن للسحر.');
INSERT INTO Quran__Arabic_Tafseer_Muyassar (QuranChapterID, VerseNumber, Rukuh, Sajdah, Manzil, HizbQuarter, VerseText) VALUES (10, 80, '0', '0', '0', '0', 'فلما جاء السحرة فرعون قال لهم موسى: ألقوا على الأرض ما معكم من حبالكم وعصيِّكم.');
INSERT INTO Quran__Arabic_Tafseer_Muyassar (QuranChapterID, VerseNumber, Rukuh, Sajdah, Manzil, HizbQuarter, VerseText) VALUES (10, 81, '0', '0', '0', '0', 'فلما ألقَوا حبالهم وعصيَّهم قال لهم موسى: إنَّ الذي جئتم به وألقيتموه هو السحر، إن الله سيُذْهب ما جئتم به وسيُبطله، إن الله لا يصلح عمل مَن سعى في أرض الله بما يكرهه، وأفسد فيها بمعصيته.');
INSERT INTO Quran__Arabic_Tafseer_Muyassar (QuranChapterID, VerseNumber, Rukuh, Sajdah, Manzil, HizbQuarter, VerseText) VALUES (10, 82, '1', '0', '0', '0', 'ويثبِّت الله الحق الذي جئتكم به من عنده فيُعليه على باطلكم بكلماته وأمره، ولو كره المجرمون أصحاب المعاصي مِن آل فرعون.');
INSERT INTO Quran__Arabic_Tafseer_Muyassar (QuranChapterID, VerseNumber, Rukuh, Sajdah, Manzil, HizbQuarter, VerseText) VALUES (10, 83, '0', '0', '0', '0', 'فما آمن لموسى عليه السلام مع ما أتاهم به من الحجج والأدلة إلا ذرية من قومه من بني إسرائيل، وهم خائفون من فرعون وملئه أن يفتنوهم بالعذاب، فيصدُّوهم عن دينهم، وإن فرعون لَجبار مستكبر في الأرض، وإنه لمن المتجاوزين الحد في الكفر والفساد.');
INSERT INTO Quran__Arabic_Tafseer_Muyassar (QuranChapterID, VerseNumber, Rukuh, Sajdah, Manzil, HizbQuarter, VerseText) VALUES (10, 84, '0', '0', '0', '0', 'وقال موسى: يا قومي إن صدقتم بالله -جلَّ وعلا- وامتثلتم شرعه فثقوا به، وسلِّموا لأمره، وعلى الله توكلوا إن كنتم مذعنين له بالطاعة.');
INSERT INTO Quran__Arabic_Tafseer_Muyassar (QuranChapterID, VerseNumber, Rukuh, Sajdah, Manzil, HizbQuarter, VerseText) VALUES (10, 85, '0', '0', '0', '0', 'فقال قوم موسى له: على الله وحده لا شريك له اعتمدنا، وإليه فوَّضنا أمرنا، ربنا لا تنصرهم علينا فيكون ذلك فتنة لنا عن الدين، أو يُفتن الكفارُ بنصرهم، فيقولوا: لو كانوا على حق لما غُلبوا.');
INSERT INTO Quran__Arabic_Tafseer_Muyassar (QuranChapterID, VerseNumber, Rukuh, Sajdah, Manzil, HizbQuarter, VerseText) VALUES (10, 86, '0', '0', '0', '0', 'ونجِّنا برحمتك من القوم الكافرين فرعون وملئه؛ لأنهم كانوا يأخذونهم بالأعمال الشاقة.');
INSERT INTO Quran__Arabic_Tafseer_Muyassar (QuranChapterID, VerseNumber, Rukuh, Sajdah, Manzil, HizbQuarter, VerseText) VALUES (10, 87, '0', '0', '0', '0', 'وأوحينا إلى موسى وأخيه هارون أن اتخذا لقومكما بيوتًا في "مصر" تكون مساكن وملاجئ تعتصمون بها، واجعلوا بيوتكم أماكن تصلُّون فيها عند الخوف، وأدُّوا الصلاة المفروضة في أوقاتها. وبشِّر المؤمنين المطيعين لله بالنصر المؤزر، والثواب الجزيل منه سبحانه وتعالى.');
INSERT INTO Quran__Arabic_Tafseer_Muyassar (QuranChapterID, VerseNumber, Rukuh, Sajdah, Manzil, HizbQuarter, VerseText) VALUES (10, 88, '0', '0', '0', '0', 'وقال موسى: ربنا إنك أعطيت فرعون وأشراف قومه زينة من متاع الدنيا؛ فلم يشكروا لك، وإنما استعانوا بها على الإضلال عن سبيلك، ربنا اطمس على أموالهم، فلا ينتفعوا بها، واختم على قلوبهم حتى لا تنشرح للإيمان، فلا يؤمنوا حتى يروا العذاب الشديد الموجع.');
INSERT INTO Quran__Arabic_Tafseer_Muyassar (QuranChapterID, VerseNumber, Rukuh, Sajdah, Manzil, HizbQuarter, VerseText) VALUES (10, 89, '0', '0', '0', '0', 'قال الله تعالى لهما: قد أجيبت دعوتكما في فرعون وملئه وأموالهم -وكان موسى يدعو، وهارون يؤمِّن على دعائه، فمن هنا نسبت الدعوة إلى الاثنين- فاستقيما على دينكما، واستمرَّا على دعوتكما فرعون وقومه إلى توحيد الله وطاعته، ولا تسلكا طريق مَن لا يعلم حقيقة وعدي ووعيدي.');
INSERT INTO Quran__Arabic_Tafseer_Muyassar (QuranChapterID, VerseNumber, Rukuh, Sajdah, Manzil, HizbQuarter, VerseText) VALUES (10, 90, '0', '0', '0', '1', 'وقطَعْنا ببني إسرائيل البحر حتى جاوزوه، فأتبعهم فرعون وجنوده ظلمًا وعدوانًا، فسلكوا البحر وراءهم، حتى إذا أحاط بفرعون الغرق قال: آمنتُ أنه لا إله إلا الذي آمنتْ به بنو إسرائيل، وأنا من الموحدين المستسلمين بالانقياد والطاعة.');
INSERT INTO Quran__Arabic_Tafseer_Muyassar (QuranChapterID, VerseNumber, Rukuh, Sajdah, Manzil, HizbQuarter, VerseText) VALUES (10, 91, '0', '0', '0', '0', 'آلآن يا فرعون، وقد نزل بك الموت تقرُّ لله بالعبودية، وقد عصيته قبل نزول عذابه بك، وكنت من المفسدين الصادين عن سبيله!! فلا تنفعك التوبة ساعة الاحتضار ومشاهدة الموت والعذاب.');
INSERT INTO Quran__Arabic_Tafseer_Muyassar (QuranChapterID, VerseNumber, Rukuh, Sajdah, Manzil, HizbQuarter, VerseText) VALUES (10, 92, '1', '0', '0', '0', 'فاليوم نجعلك على مرتفع من الأرض ببدنك، ينظر إليك من كذَّب بهلاكك؛ لتكون لمن بعدك من الناس عبرة يعتبرون بك. فإن كثيرًا من الناس عن حججنا وأدلتنا لَغافلون، لا يتفكرون فيها ولا يعتبرون.');
INSERT INTO Quran__Arabic_Tafseer_Muyassar (QuranChapterID, VerseNumber, Rukuh, Sajdah, Manzil, HizbQuarter, VerseText) VALUES (10, 93, '0', '0', '0', '0', 'ولقد أنزلنا بني إسرائيل منزلا صالحًا مختارًا في بلاد "الشام" و"مصر"، ورزقناهم الرزق الحلال الطيب من خيرات الأرض المباركة، فما اختلفوا في أمر دينهم إلا مِن بعد ما جاءهم العلم الموجب لاجتماعهم وائتلافهم، ومن ذلك ما اشتملت عليه التوراة من الإخبار بنبوة محمد صلى الله عليه وسلم. إن ربك -أيها الرسول- يقضي بينهم يوم القيامة، ويَفْصِل فيما كانوا يختلفون فيه من أمرك، فيدخل المكذبين النار والمؤمنين الجنة.');
INSERT INTO Quran__Arabic_Tafseer_Muyassar (QuranChapterID, VerseNumber, Rukuh, Sajdah, Manzil, HizbQuarter, VerseText) VALUES (10, 94, '0', '0', '0', '0', 'فإن كنت -أيها الرسول- في ريب من حقيقة ما أخبرناك به فاسأل الذين يقرؤون الكتاب من قبلك من أهل التوراة والإنجيل، فإن ذلك ثابت في كتبهم، لقد جاءك الحق اليقين من ربك بأنك رسول الله، وأن هؤلاء اليهود والنصارى يعلمون صحة ذلك، ويجدون صفتك في كتبهم، ولكنهم ينكرون ذلك مع علمهم به، فلا تكوننَّ من الشاكِّين في صحة ذلك وحقيقته.');
INSERT INTO Quran__Arabic_Tafseer_Muyassar (QuranChapterID, VerseNumber, Rukuh, Sajdah, Manzil, HizbQuarter, VerseText) VALUES (10, 95, '0', '0', '0', '0', 'ولا تكونن -أيها الرسول- من الذين كذَّبوا بحجج الله وأدلته فتكون من الخاسرين الذين سخِطَ الله عليهم ونالوا عقابه.');
INSERT INTO Quran__Arabic_Tafseer_Muyassar (QuranChapterID, VerseNumber, Rukuh, Sajdah, Manzil, HizbQuarter, VerseText) VALUES (10, 96, '0', '0', '0', '0', 'إن الذين حقَّت عليهم كلمة ربك -أيها الرسول- بطردهم من رحمته وعذابه لهم، لا يؤمنون بحجج الله، ولا يقرُّون بوحدانيته، ولا يعملون بشرعه.');
INSERT INTO Quran__Arabic_Tafseer_Muyassar (QuranChapterID, VerseNumber, Rukuh, Sajdah, Manzil, HizbQuarter, VerseText) VALUES (10, 97, '0', '0', '0', '0', 'ولو جاءتهم كل موعظة وعبرة حتى يعاينوا العذاب الموجع، فحينئذ يؤمنون، ولا ينفعهم إيمانهم.');
INSERT INTO Quran__Arabic_Tafseer_Muyassar (QuranChapterID, VerseNumber, Rukuh, Sajdah, Manzil, HizbQuarter, VerseText) VALUES (10, 98, '0', '0', '0', '0', 'لم ينفع الإيمان أهل قرية آمنوا عند معاينة العذاب إلا أهل قرية يونس بن مَتَّى، فإنهم لـمَّا أيقنوا أن العذاب نازل بهم تابوا إلى الله تعالى توبة نصوحا، فلمَّا تبيَّن منهم الصدق في توبتهم كشف الله عنهم عذاب الخزي بعد أن اقترب منهم، وتركهم في الدنيا يستمتعون إلى وقت إنهاء آجالهم.');
INSERT INTO Quran__Arabic_Tafseer_Muyassar (QuranChapterID, VerseNumber, Rukuh, Sajdah, Manzil, HizbQuarter, VerseText) VALUES (10, 99, '0', '0', '0', '0', 'ولو شاء ربك -أيها الرسول- الإيمان لأهل الأرض كلهم لآمنوا جميعًا بما جئتهم به، ولكن له حكمة في ذلك؛ فإنه يهدي من يشاء ويضل من يشاء وَفْق حكمته، وليس في استطاعتك أن تُكْره الناس على الإيمان.');
INSERT INTO Quran__Arabic_Tafseer_Muyassar (QuranChapterID, VerseNumber, Rukuh, Sajdah, Manzil, HizbQuarter, VerseText) VALUES (10, 100, '0', '0', '0', '0', 'وما كان لنفس أن تؤمن بالله إلا بإذنه وتوفيقه، فلا تُجهد نفسك في ذلك، فإن أمرهم إلى الله. ويجعل الله العذاب والخزي على الذين لا يعقلون أمره ونهيه.');
INSERT INTO Quran__Arabic_Tafseer_Muyassar (QuranChapterID, VerseNumber, Rukuh, Sajdah, Manzil, HizbQuarter, VerseText) VALUES (10, 101, '0', '0', '0', '0', 'قل -أيها الرسول- لقومك: تفكروا واعتبروا بما في السموات والأرض من آيات الله البينات، ولكن الآيات والعبر والرسل المنذرة عباد الله عقابه، لا تنفع قومًا لا يؤمنون بشيء من ذلك؛ لإعراضهم وعنادهم.');
INSERT INTO Quran__Arabic_Tafseer_Muyassar (QuranChapterID, VerseNumber, Rukuh, Sajdah, Manzil, HizbQuarter, VerseText) VALUES (10, 102, '0', '0', '0', '0', 'فهل ينتظر هؤلاء إلا يومًا يعاينون فيه عذاب الله مثل أيام أسلافهم المكذبين الذين مَضَوا قبلهم؟ قل لهم -أيها الرسول-: فانتظروا عقاب الله إني معكم من المنتظرين عقابكم.');
INSERT INTO Quran__Arabic_Tafseer_Muyassar (QuranChapterID, VerseNumber, Rukuh, Sajdah, Manzil, HizbQuarter, VerseText) VALUES (10, 103, '1', '0', '0', '0', 'ثم ننجِّي رسلنا والذين آمنوا معهم، وكما نجينا أولئك ننجِّيك -أيها الرسول- ومن آمن بك تفضلا منَّا ورحمة.');
INSERT INTO Quran__Arabic_Tafseer_Muyassar (QuranChapterID, VerseNumber, Rukuh, Sajdah, Manzil, HizbQuarter, VerseText) VALUES (10, 104, '0', '0', '0', '0', 'قل -أيها الرسول- لهؤلاء الناس: إن كنتم في شك من صحة ديني الذي دعوتكم إليه، وهو الإسلام ومن ثباتي واستقامتي عليه، وترجون تحويلي عنه، فإني لا أعبد في حال من الأحوال أحدًا من الذين تعبدونهم مما اتخذتم من الأصنام والأوثان، ولكن أعبد الله وحده الذي يميتكم ويقبض أرواحكم، وأُمِرْت أن أكون من المصدِّقين به العاملين بشرعه.');
INSERT INTO Quran__Arabic_Tafseer_Muyassar (QuranChapterID, VerseNumber, Rukuh, Sajdah, Manzil, HizbQuarter, VerseText) VALUES (10, 105, '0', '0', '0', '0', 'وأن أقم -أيها الرسول- نفسك على دين الإسلام مستقيمًا عليه غير مائل عنه إلى يهودية ولا نصرانية ولا عبادة غيره، ولا تكونن ممن يشرك في عبادة ربه الآلهة والأنداد، فتكون من الهالكين. وهذا وإن كان خطابًا للرسول صلى الله عليه وسلَّم فإنه موجَّه لعموم الأمة.');
INSERT INTO Quran__Arabic_Tafseer_Muyassar (QuranChapterID, VerseNumber, Rukuh, Sajdah, Manzil, HizbQuarter, VerseText) VALUES (10, 106, '0', '0', '0', '0', 'ولا تَدْعُ -أيها الرسول- من دون الله شيئًا من الأوثان والأصنام؛ لأنها لا تنفع ولا تضرُّ، فإن فعَلْت ذلك ودعوتها من دون الله فإنك إذًا من المشركين بالله، الظالمين لأنفسهم بالشرك والمعصية. وهذا وإن كان خطابًا للرسول صلى الله عليه وسلَّم فإنه موجَّه لعموم الأمة.');
INSERT INTO Quran__Arabic_Tafseer_Muyassar (QuranChapterID, VerseNumber, Rukuh, Sajdah, Manzil, HizbQuarter, VerseText) VALUES (10, 107, '0', '0', '0', '0', 'وإن يصبك الله -أيها الرسول- بشدة أو بلاء فلا كاشف لذلك إلا هو جلَّ وعلا وإن يُرِدْك برخاء أو نعمة لا يمنعه عنك أحد، يصيب الله عز وجل بالسراء والضراء من يشاء من عباده، وهو الغفور لذنوب مَن تاب، الرحيم بمن آمن به وأطاعه.');
INSERT INTO Quran__Arabic_Tafseer_Muyassar (QuranChapterID, VerseNumber, Rukuh, Sajdah, Manzil, HizbQuarter, VerseText) VALUES (10, 108, '0', '0', '0', '0', 'قل -أيها الرسول- لهؤلاء الناس: قد جاءكم رسول الله بالقرآن الذي فيه بيان هدايتكم، فمن اهتدى بهدي الله فإنما ثمرة عمله راجعة إليه، ومن انحرف عن الحق وأصرَّ على الضلال فإنما ضلاله وضرره على نفسه، وما أنا موكَّل بكم حتى تكونوا مؤمنين، إنما أنا رسول مبلِّغ أبلِّغكم ما أُرْسِلْت به.');
INSERT INTO Quran__Arabic_Tafseer_Muyassar (QuranChapterID, VerseNumber, Rukuh, Sajdah, Manzil, HizbQuarter, VerseText) VALUES (10, 109, '1', '0', '0', '0', 'واتبع -أيها الرسول- وحي الله الذي يوحيه إليك فاعمل به، واصبر على طاعة الله تعالى، وعن معصيته، وعلى أذى من آذاك في تبليغ رسالته، حتى يقضي الله فيهم وفيك أمره، وهو -عزَّ وجل- خير الحاكمين؛ فإن حكمه مشتمل على العدل التام.');
INSERT INTO Quran__Arabic_Tafseer_Muyassar (QuranChapterID, VerseNumber, Rukuh, Sajdah, Manzil, HizbQuarter, VerseText) VALUES (11, 1, '0', '0', '0', '0', '(الر) سبق الكلام على الحروف المقطَّعة في أول سورة البقرة.');
INSERT INTO Quran__Arabic_Tafseer_Muyassar (QuranChapterID, VerseNumber, Rukuh, Sajdah, Manzil, HizbQuarter, VerseText) VALUES (11, 2, '0', '0', '0', '0', 'وإنزال القرآن وبيان أحكامه وتفصيلها وإحكامها؛ لأجل أن لا تعبدوا إلا الله وحده لا شريك له. إنني لكم -أيها الناس- من عند الله نذير ينذركم عقابه، وبشير يبشِّركم بثوابه.');
INSERT INTO Quran__Arabic_Tafseer_Muyassar (QuranChapterID, VerseNumber, Rukuh, Sajdah, Manzil, HizbQuarter, VerseText) VALUES (11, 3, '0', '0', '0', '0', 'واسألوه أن يغفر لكم ذنوبكم، ثم ارجعوا إليه نادمين يمتعْكم في دنياكم متاعًا حسنًا بالحياة الطيبة فيها، إلى أن يحين أجلكم، ويُعطِ كل ذي فضل من علم وعمل جزاء فضله كاملا لا نقص فيه، وإن تعرضوا عمَّا أدعوكم إليه فإني أخشى عليكم عذاب يوم شديد، وهو يوم القيامة. وهذا تهديد شديد لمن تولَّى عن أوامر الله تعالى وكذَّب رسله.');
INSERT INTO Quran__Arabic_Tafseer_Muyassar (QuranChapterID, VerseNumber, Rukuh, Sajdah, Manzil, HizbQuarter, VerseText) VALUES (11, 4, '0', '0', '0', '0', 'إلى الله رجوعكم بعد موتكم جميعًا فاحذروا عقابه، وهو سبحانه قادر على بعثكم وحشركم وجزائكم.');
INSERT INTO Quran__Arabic_Tafseer_Muyassar (QuranChapterID, VerseNumber, Rukuh, Sajdah, Manzil, HizbQuarter, VerseText) VALUES (11, 5, '0', '0', '0', '0', 'إن هؤلاء المشركين يضمرون في صدورهم الكفر؛ ظنًا منهم أنه يخفى على الله ما تضمره نفوسهم، ألا يعلمون حين يغطُّون أجسادهم بثيابهم أن الله لا يخفى عليه سِرُّهم وعلانيتهم؟ إنه عليم بكل ما تُكِنُّه صدورهم من النيات والضمائر والسرائر.');
INSERT INTO Quran__Arabic_Tafseer_Muyassar (QuranChapterID, VerseNumber, Rukuh, Sajdah, Manzil, HizbQuarter, VerseText) VALUES (11, 6, '0', '0', '0', '1', 'لقد تكفَّل الله برزق جميع ما دبَّ على وجه الأرض، تفضلا منه، ويعلم مكان استقراره في حياته وبعد موته، ويعلم الموضع الذي يموت فيه، كل ذلك مكتوب في كتاب عند الله مبين عن جميع ذلك.');
INSERT INTO Quran__Arabic_Tafseer_Muyassar (QuranChapterID, VerseNumber, Rukuh, Sajdah, Manzil, HizbQuarter, VerseText) VALUES (11, 7, '0', '0', '0', '0', 'وهو الذي خلق السماوات والأرض وما فيهن في ستة أيام، وكان عرشه على الماء قبل ذلك؛ ليختبركم أيكم أحسن له طاعةً وعملا وهو ما كان خالصًا لله موافقًا لما كان عليه رسول الله صلى الله عليه وسلم. ولئن قلت -أيها الرسول- لهؤلاء المشركين من قومك: إنكم مبعوثون أحياءً بعد موتكم، لسارعوا إلى التكذيب وقالوا: ما هذا القرآن الذي تتلوه علينا إلا سحر بيِّن.');
INSERT INTO Quran__Arabic_Tafseer_Muyassar (QuranChapterID, VerseNumber, Rukuh, Sajdah, Manzil, HizbQuarter, VerseText) VALUES (11, 8, '1', '0', '0', '0', 'ولئن أخَّرنا عن هؤلاء المشركين العذاب إلى أجل معلوم فاستبطؤوه، ليقولُنَّ استهزاء وتكذيبًا: أي شيء يمنع هذا العذاب من الوقوع إن كان حقًا؟ ألا يوم يأتيهم ذلك العذاب لا يستطيع أن يصرفه عنهم صارف، ولا يدفعه دافع، وأحاط بهم من كل جانب عذاب ما كانوا يستهزئون به قبل وقوعه بهم.');
INSERT INTO Quran__Arabic_Tafseer_Muyassar (QuranChapterID, VerseNumber, Rukuh, Sajdah, Manzil, HizbQuarter, VerseText) VALUES (11, 9, '0', '0', '0', '0', 'ولئن أعطينا الإنسان مِنَّا نعمة من صحة وأمن وغيرهما، ثم سلبناها منه، إنه لَشديد اليأس من رحمة الله، جَحود بالنعم التي أنعم الله بها عليه.');
INSERT INTO Quran__Arabic_Tafseer_Muyassar (QuranChapterID, VerseNumber, Rukuh, Sajdah, Manzil, HizbQuarter, VerseText) VALUES (11, 10, '0', '0', '0', '0', 'ولئن بسطنا للإنسان في دنياه ووسَّعنا عليه في رزقه بعد ضيق من العيش، ليقولَنَّ عند ذلك: ذهب الضيق عني وزالت الشدائد، إنه لبَطِر بالنعم، مبالغ في الفخر والتعالي على الناس.');
INSERT INTO Quran__Arabic_Tafseer_Muyassar (QuranChapterID, VerseNumber, Rukuh, Sajdah, Manzil, HizbQuarter, VerseText) VALUES (11, 11, '0', '0', '0', '0', 'لكن الذين صبروا على ما أصابهم من الضراء إيمانًا بالله واحتسابًا للأجر عنده، وعملوا الصالحات شكرا لله على نعمه، هؤلاء لهم مغفرة لذنوبهم وأجر كبير في الآخرة.');
INSERT INTO Quran__Arabic_Tafseer_Muyassar (QuranChapterID, VerseNumber, Rukuh, Sajdah, Manzil, HizbQuarter, VerseText) VALUES (11, 12, '0', '0', '0', '0', 'فلعلك -أيها الرسول لعظم ما تراه منهم من الكفر والتكذيب- تارك بعض ما يوحى إليك مما أنزله الله عليك وأمرك بتبليغه، وضائق به صدرك؛ خشية أن يطلبوا منك بعض المطالب على وجه التعنت، كأن يقولوا: لولا أُنزل عليه مال كثير، أو جاء معه ملك يصدقه في رسالته، فبلغهم ما أوحيته إليك؛ فإنه ليس عليك إلا الإنذار بما أُوحي إليك. والله على كل شيء حفيظ يدَبِّر جميع شؤون خلقه.');
INSERT INTO Quran__Arabic_Tafseer_Muyassar (QuranChapterID, VerseNumber, Rukuh, Sajdah, Manzil, HizbQuarter, VerseText) VALUES (11, 13, '0', '0', '0', '0', 'بل أيقول هؤلاء المشركون من أهل "مكة": إن محمدًا قد افترى هذا القرآن؟ قل لهم: إن كان الأمر كما تزعمون فأتوا بعشر سور مثله مفتريات، وادعوا من استطعتم من جميع خلق الله ليساعدوكم على الإتيان بهذه السور العشر، إن كنتم صادقين في دعواكم.');
INSERT INTO Quran__Arabic_Tafseer_Muyassar (QuranChapterID, VerseNumber, Rukuh, Sajdah, Manzil, HizbQuarter, VerseText) VALUES (11, 14, '0', '0', '0', '0', 'فإن لم يستجب هؤلاء المشركون لكم -أيها الرسول ومَن آمن معك- لما تدعونهم إليه؛ لِعَجْز الجميع عن ذلك، فاعلموا أن هذا القرآن إنما أنزله الله على رسوله بعلمه وليس من قول البشر، واعلموا أن لا إله يُعبد بحق إلا الله، فهل أنتم -بعد قيام هذه الحجة عليكم- مسلمون منقادون لله ورسوله؟');
INSERT INTO Quran__Arabic_Tafseer_Muyassar (QuranChapterID, VerseNumber, Rukuh, Sajdah, Manzil, HizbQuarter, VerseText) VALUES (11, 15, '0', '0', '0', '0', 'من كان يريد بعمله الحياة الدنيا ومُتَعها نعطهم ما قُسِم لهم من ثواب أعمالهم في الحياة الدنيا كاملا غير منقوص.');
INSERT INTO Quran__Arabic_Tafseer_Muyassar (QuranChapterID, VerseNumber, Rukuh, Sajdah, Manzil, HizbQuarter, VerseText) VALUES (11, 16, '0', '0', '0', '0', 'أولئك ليس لهم في الآخرة إلا نار جهنم يقاسون حرَّها، وذهب عنهم نَفْع ما عملوه، وكان عملهم باطلا لأنه لم يكن لوجه الله.');
INSERT INTO Quran__Arabic_Tafseer_Muyassar (QuranChapterID, VerseNumber, Rukuh, Sajdah, Manzil, HizbQuarter, VerseText) VALUES (11, 17, '0', '0', '0', '0', 'أفمَن كان على حجة وبصيرة من ربه فيما يؤمن به، ويدعو إليه بالوحي الذي أنزل الله فيه هذه البينة، ويتلوها برهان آخر شاهد منه، وهو جبريل أو محمد عليهما السلام، ويؤيد ذلك برهان ثالث من قبل القرآن، وهو التوراة -الكتاب الذي أنزل على موسى إمامًا ورحمة لمن آمن به-، كمن كان همه الحياة الفانية بزينتها؟ أولئك يصدِّقون بهذا القرآن ويعملون بأحكامه، ومن يكفر بهذا القرآن من الذين تحزَّبوا على رسول الله صلى الله عليه وسلم فجزاؤه النار، يَرِدُها لا محالة، فلا تك -أيها الرسول- في شك من أمر القرآن وكونه من عند الله تعالى بعد ما شهدت بذلك الأدلة والحجج، واعلم أن هذا الدين هو الحق من ربك، ولكن أكثر الناس لا يصدِّقون ولا يعملون بما أُمروا به. وهذا توجيه عام لأمة محمد صلى الله عليه وسلم.');
INSERT INTO Quran__Arabic_Tafseer_Muyassar (QuranChapterID, VerseNumber, Rukuh, Sajdah, Manzil, HizbQuarter, VerseText) VALUES (11, 18, '0', '0', '0', '0', 'ولا أحد أظلم ممن اختلق على الله كذبًا، أولئك سيعرضون على ربهم يوم القيامة؛ ليحاسبهم على أعمالهم، ويقول الأشهاد من الملائكة والنبيين وغيرهم: هؤلاء الذين كذبوا على ربهم في الدنيا قد سخط الله عليهم، ولعنهم لعنة لا تنقطع؛ لأن ظلمهم صار وصفًا ملازمًا لهم.');
INSERT INTO Quran__Arabic_Tafseer_Muyassar (QuranChapterID, VerseNumber, Rukuh, Sajdah, Manzil, HizbQuarter, VerseText) VALUES (11, 19, '0', '0', '0', '0', 'هؤلاء الظالمون الذين يمنعون الناس عن سبيل الله الموصلة إلى عبادته، ويريدون أن تكون هذه السبيل عوجاء بموافقتها لأهوائهم، وهم كافرون بالآخرة لا يؤمنون ببعث ولا جزاء.');
INSERT INTO Quran__Arabic_Tafseer_Muyassar (QuranChapterID, VerseNumber, Rukuh, Sajdah, Manzil, HizbQuarter, VerseText) VALUES (11, 20, '0', '0', '0', '0', 'أولئك الكافرون لم يكونوا ليفوتوا الله في الدنيا هربًا، وما كان لهم مِن أنصار يمنعونهم من عقابه. يضاعَفُ لهم العذاب في جهنم؛ لأنهم كانوا لا يستطيعون أن يسمعوا القرآن سماع منتفع، أو يبصروا آيات الله في هذا الكون إبصار مهتد؛ لاشتغالهم بالكفر الذي كانوا عليه مقيمين.');
INSERT INTO Quran__Arabic_Tafseer_Muyassar (QuranChapterID, VerseNumber, Rukuh, Sajdah, Manzil, HizbQuarter, VerseText) VALUES (11, 21, '0', '0', '0', '0', 'أولئك الذين خسروا أنفسهم بافترائهم على الله، وذهب عنهم ما كانوا يفترون من الآلهة التي يدَّعون أنها تشفع لهم.');
INSERT INTO Quran__Arabic_Tafseer_Muyassar (QuranChapterID, VerseNumber, Rukuh, Sajdah, Manzil, HizbQuarter, VerseText) VALUES (11, 22, '0', '0', '0', '0', 'حقًا أنهم في الآخرة أخسر الناس صفقة؛ لأنهم استبدلوا الدركات بالدرجات، فكانوا في جهنم، وذلك هو الخسران المبين.');
INSERT INTO Quran__Arabic_Tafseer_Muyassar (QuranChapterID, VerseNumber, Rukuh, Sajdah, Manzil, HizbQuarter, VerseText) VALUES (11, 23, '0', '0', '0', '0', 'إن الذين صدَّقوا الله ورسوله وعملوا الأعمال الصالحة، وخضعوا لله في كل ما أُمروا به ونُهوا عنه، أولئك هم أهل الجنة، لا يموتون فيها، ولا يَخْرجون منها أبدًا.');
INSERT INTO Quran__Arabic_Tafseer_Muyassar (QuranChapterID, VerseNumber, Rukuh, Sajdah, Manzil, HizbQuarter, VerseText) VALUES (11, 24, '1', '0', '0', '1', 'مثل فريقَي الكفر والإيمان كمثل الأعمى الذي لا يرى والأصم الذي لا يسمع والبصير والسميع: ففريق الكفر لا يبصر الحق فيتبعه، ولا يسمع داعي الله فيهتدي به، أما فريق الإيمان فقد أبصر حجج الله وسمع داعي الله فأجابه، هل يستوي هذان الفريقان؟ أفلا تعتبرون وتتفكرون؟');
INSERT INTO Quran__Arabic_Tafseer_Muyassar (QuranChapterID, VerseNumber, Rukuh, Sajdah, Manzil, HizbQuarter, VerseText) VALUES (11, 25, '0', '0', '0', '0', 'ولقد أرسلنا نوحًا إلى قومه فقال لهم: إني نذير لكم من عذاب الله، مبيِّن لكم ما أُرسلت به إليكم من أمر الله ونهيه.');
INSERT INTO Quran__Arabic_Tafseer_Muyassar (QuranChapterID, VerseNumber, Rukuh, Sajdah, Manzil, HizbQuarter, VerseText) VALUES (11, 26, '0', '0', '0', '0', 'آمركم ألا تعبدوا إلا الله، إني أخاف عليكم -إن لم تفردوا الله وحده بالعبادة- عذاب يوم موجع.');
INSERT INTO Quran__Arabic_Tafseer_Muyassar (QuranChapterID, VerseNumber, Rukuh, Sajdah, Manzil, HizbQuarter, VerseText) VALUES (11, 27, '0', '0', '0', '0', 'فقال رؤساء الكفر من قومه: إنك لست بمَلَك ولكنك بشر، فكيف أُوحي إليك مِن دوننا؟ وما نراك اتبعك إلا الذين هم أسافلنا وإنما اتبعوك من غير تفكر ولا رويَّة، وما نرى لكم علينا من فضل في رزق ولا مال لـمَّا دخلتم في دينكم هذا، بل نعتقد أنكم كاذبون فيما تدَّعون.');
INSERT INTO Quran__Arabic_Tafseer_Muyassar (QuranChapterID, VerseNumber, Rukuh, Sajdah, Manzil, HizbQuarter, VerseText) VALUES (11, 28, '0', '0', '0', '0', 'قال نوح: يا قومي أرأيتم إن كنتُ على حجة ظاهرة من ربي فيما جئتكم به تبيِّن لكم أنني على الحق من عنده، وآتاني رحمة من عنده، وهي النبوة والرسالة فأخفاها عليكم بسبب جهلكم وغروركم، فهل يصح أن نُلْزمكم إياها بالإكراه وأنتم جاحدون بها؟ لا نفعل ذلك، ولكن نَكِل أمركم إلى الله حتى يقضي في أمركم ما يشاء.');
INSERT INTO Quran__Arabic_Tafseer_Muyassar (QuranChapterID, VerseNumber, Rukuh, Sajdah, Manzil, HizbQuarter, VerseText) VALUES (11, 29, '0', '0', '0', '0', 'قال نوح عليه السلام لقومه: يا قوم لا أسألكم على دعوتكم لتوحيد الله وإخلاص العبادة له مالاً تؤدونه إليَّ بعد إيمانكم، ولكن ثواب نصحي لكم على الله وحده، وليس من شأني أن أطرد المؤمنين، فإنهم ملاقو ربهم يوم القيامة، ولكني أراكم قومًا تجهلون؛ إذ تأمرونني بطرد أولياء الله وإبعادهم عني.');
INSERT INTO Quran__Arabic_Tafseer_Muyassar (QuranChapterID, VerseNumber, Rukuh, Sajdah, Manzil, HizbQuarter, VerseText) VALUES (11, 30, '0', '0', '0', '0', 'ويا قوم مَن يمنعني من الله إن عاقبني على طردي المؤمنين؟ أفلا تتدبرون الأمور فتعلموا ما هو الأنفع لكم والأصلح؟');
INSERT INTO Quran__Arabic_Tafseer_Muyassar (QuranChapterID, VerseNumber, Rukuh, Sajdah, Manzil, HizbQuarter, VerseText) VALUES (11, 31, '0', '0', '0', '0', 'ولا أقول لكم: إني أملك التصرف في خزائن الله، ولا أعلم الغيب، ولست بمَلَك من الملائكة، ولا أقول لهؤلاء الذين تحتقرون من ضعفاء المؤمنين: لن يؤتيكم الله ثوابًا على أعمالكم، فالله وحده أعلم بما في صدورهم وقلوبهم، ولئن فعلتُ ذلك إني إذًا لمن الظالمين لأنفسهم ولغيرهم.');
INSERT INTO Quran__Arabic_Tafseer_Muyassar (QuranChapterID, VerseNumber, Rukuh, Sajdah, Manzil, HizbQuarter, VerseText) VALUES (11, 32, '0', '0', '0', '0', 'قالوا: يا نوح قد حاججتنا فأكثرت جدالنا، فأتنا بما تعدنا من العذاب إن كنت من الصادقين في دعواك.');
INSERT INTO Quran__Arabic_Tafseer_Muyassar (QuranChapterID, VerseNumber, Rukuh, Sajdah, Manzil, HizbQuarter, VerseText) VALUES (11, 33, '0', '0', '0', '0', 'قال نوح لقومه: إن الله وحده هو الذي يأتيكم بالعذاب إذا شاء، ولستم بفائتيه إذا أراد أن يعذبكم؛ لأنه سبحانه لا يعجزه شيء في الأرض ولا في السماء.');
INSERT INTO Quran__Arabic_Tafseer_Muyassar (QuranChapterID, VerseNumber, Rukuh, Sajdah, Manzil, HizbQuarter, VerseText) VALUES (11, 34, '0', '0', '0', '0', 'ولا ينفعكم نصحي واجتهادي في دعوتكم للإيمان، إن كان الله يريد أن يضلَّكم ويهلككم، هو سبحانه مالككم، وإليه تُرجَعون في الآخرة للحساب والجزاء.');
INSERT INTO Quran__Arabic_Tafseer_Muyassar (QuranChapterID, VerseNumber, Rukuh, Sajdah, Manzil, HizbQuarter, VerseText) VALUES (11, 35, '1', '0', '0', '0', 'بل أيقول هؤلاء المشركون من قوم نوح: افترى نوح هذا القول؟ قل لهم: إن كنتُ قد افتريتُ ذلك على الله فعليَّ وحدي إثم ذلك، وإذا كنتُ صادقًا فأنتم المجرمون الآثمون، وأنا بريء مِن كفركم وتكذيبكم وإجرامكم.');
INSERT INTO Quran__Arabic_Tafseer_Muyassar (QuranChapterID, VerseNumber, Rukuh, Sajdah, Manzil, HizbQuarter, VerseText) VALUES (11, 36, '0', '0', '0', '0', 'وأوحى الله سبحانه وتعالى إلى نوح -عليه السلام- لـمَّا حق على قومه العذاب، أنه لن يؤمن بالله إلا مَن قد آمن مِن قبل، فلا تحزن يا نوح على ما كانوا يفعلون.');
INSERT INTO Quran__Arabic_Tafseer_Muyassar (QuranChapterID, VerseNumber, Rukuh, Sajdah, Manzil, HizbQuarter, VerseText) VALUES (11, 37, '0', '0', '0', '0', 'واصنع السفينة بمرأى منَّا وبأمرنا لك ومعونتنا، وأنت في حفظنا وكلاءتنا، ولا تطلب مني إمهال هؤلاء الذين ظلموا أنفسهم من قومك بكفرهم، فإنهم مغرقون بالطوفان. وفي الآية إثبات صفة العين لله تعالى على ما يليق به سبحانه.');
INSERT INTO Quran__Arabic_Tafseer_Muyassar (QuranChapterID, VerseNumber, Rukuh, Sajdah, Manzil, HizbQuarter, VerseText) VALUES (11, 38, '0', '0', '0', '0', 'ويصنع نوح السفينة، وكلَّما مر عليه جماعة من كبراء قومه سخروا منه، قال لهم نوح: إن تسخروا منا اليوم لجهلكم بصدق وعد الله، فإنا نسخر منكم غدًا عند الغرق كما تسخرون منا.');
INSERT INTO Quran__Arabic_Tafseer_Muyassar (QuranChapterID, VerseNumber, Rukuh, Sajdah, Manzil, HizbQuarter, VerseText) VALUES (11, 39, '0', '0', '0', '0', 'فسوف تعلمون إذا جاء أمر الله بذلك: من الذي يأتيه في الدنيا عذاب الله الذي يُهينه، وينزل به في الآخرة عذاب دائم لا انقطاع له؟');
INSERT INTO Quran__Arabic_Tafseer_Muyassar (QuranChapterID, VerseNumber, Rukuh, Sajdah, Manzil, HizbQuarter, VerseText) VALUES (11, 40, '0', '0', '0', '0', 'حتى إذا جاء أمرنا بإهلاكهم كما وَعدْنا نوحًا بذلك، ونبع الماء بقوة من التنور -وهو المكان الذي يخبز فيه- علامة على مجيء العذاب، قلنا لنوح: احمل في السفينة من كل نوع من أنواع الحيوانات ذكرًا وأنثى، واحمل فيها أهل بيتك، إلا مَن سبق عليهم القول ممن لم يؤمن بالله كابنه وامرأته، واحمل فيها من آمن معك من قومك، وما آمن معه إلا قليل مع طول المدة والمقام فيهم.');
INSERT INTO Quran__Arabic_Tafseer_Muyassar (QuranChapterID, VerseNumber, Rukuh, Sajdah, Manzil, HizbQuarter, VerseText) VALUES (11, 41, '0', '0', '0', '1', 'وقال نوح لمن آمن معه: اركبوا في السفينة، باسم الله يكون جريها على وجه الماء، وباسم الله يكون منتهى سيرها ورُسوُّها. إن ربي لَغفور ذنوب من تاب وأناب إليه من عباده، رحيم بهم أن يعذبهم بعد التوبة.');
INSERT INTO Quran__Arabic_Tafseer_Muyassar (QuranChapterID, VerseNumber, Rukuh, Sajdah, Manzil, HizbQuarter, VerseText) VALUES (11, 42, '0', '0', '0', '0', 'وهي تجري بهم في موج يعلو ويرتفع حتى يصير كالجبال في علوها، ونادى نوح ابنه -وكان في مكانٍ عَزَل فيه نفسه عن المؤمنين- فقال له: يا بني اركب معنا في السفينة، ولا تكن مع الكافرين بالله فتغرق.');
INSERT INTO Quran__Arabic_Tafseer_Muyassar (QuranChapterID, VerseNumber, Rukuh, Sajdah, Manzil, HizbQuarter, VerseText) VALUES (11, 43, '0', '0', '0', '0', 'قال ابن نوح: سألجأ إلى جبل أتحصَّن به من الماء، فيمنعني من الغرق، فأجابه نوح: لا مانع اليوم من أمر الله وقضائه الذي قد نزل بالخلق من الغرق والهلاك إلا مَن رحمه الله تعالى، فآمِنْ واركب في السفينة معنا، وحال الموج المرتفع بين نوح وابنه، فكان من المغرقين الهالكين.');
INSERT INTO Quran__Arabic_Tafseer_Muyassar (QuranChapterID, VerseNumber, Rukuh, Sajdah, Manzil, HizbQuarter, VerseText) VALUES (11, 44, '0', '0', '0', '0', 'وقال الله للأرض -بعد هلاك قوم نوح -: يا أرض اشربي ماءك، ويا سماء أمسكي عن المطر، ونقص الماء ونضب، وقُضي أمر الله بهلاك قوم نوح، ورست السفينة على جبل الجوديِّ، وقيل: هلاكًا وبعدًا للقوم الظالمين الذين تجاوزوا حدود الله، ولم يؤمنوا به.');
INSERT INTO Quran__Arabic_Tafseer_Muyassar (QuranChapterID, VerseNumber, Rukuh, Sajdah, Manzil, HizbQuarter, VerseText) VALUES (11, 45, '0', '0', '0', '0', 'ونادى نوح ربه فقال: رب إنك وعَدْتني أن تنجيني وأهلي من الغرق والهلاك، وإن ابني هذا من أهلي، وإن وعدك الحق الذي لا خُلْف فيه، وأنت أحكم الحاكمين وأعدلهم.');
INSERT INTO Quran__Arabic_Tafseer_Muyassar (QuranChapterID, VerseNumber, Rukuh, Sajdah, Manzil, HizbQuarter, VerseText) VALUES (11, 46, '0', '0', '0', '0', 'قال الله: يا نوح إن ابنك الذي هلك ليس من أهلك الذين وعدتك أن أنجيهم؛ وذلك بسبب كفره، وعمله عملا غير صالح، وإني أنهاك أن تسألني أمرًا لا علم لك به، إني أعظك لئلا تكون من الجاهلين في مسألتك إياي عن ذلك.');
INSERT INTO Quran__Arabic_Tafseer_Muyassar (QuranChapterID, VerseNumber, Rukuh, Sajdah, Manzil, HizbQuarter, VerseText) VALUES (11, 47, '0', '0', '0', '0', 'قال نوح: يا رب إني أعتصم وأستجير بك أن أسألك ما ليس لي به علم، وإن لم تغفر لي ذنبي، وترحمني برحمتك، أكن من الذين غَبَنوا أنفسهم حظوظها وهلكوا.');
INSERT INTO Quran__Arabic_Tafseer_Muyassar (QuranChapterID, VerseNumber, Rukuh, Sajdah, Manzil, HizbQuarter, VerseText) VALUES (11, 48, '0', '0', '0', '0', 'قال الله: يا نوح اهبط من السفينة إلى الأرض بأمن وسلامة منَّا وبركات عليك وعلى أمم ممن معك. وهناك أمم وجماعات من أهل الشقاء سنمتعهم في الحياة الدنيا، إلى أن يبلغوا آجالهم، ثم ينالهم منا العذاب الموجع يوم القيامة.');
INSERT INTO Quran__Arabic_Tafseer_Muyassar (QuranChapterID, VerseNumber, Rukuh, Sajdah, Manzil, HizbQuarter, VerseText) VALUES (11, 49, '1', '0', '0', '0', 'تلك القصة التي قصصناها عليك -أيها الرسول- عن نوح وقومه هي من أخبار الغيب السالفة، نوحيها إليك، ما كنت تعلمها أنت ولا قومك مِن قبل هذا البيان، فاصبر على تكذيب قومك وإيذائهم لك، كما صبر الأنبياء من قبل، إن العاقبة الطيبة في الدنيا والآخرة للمتقين الذين يخشون الله.');
INSERT INTO Quran__Arabic_Tafseer_Muyassar (QuranChapterID, VerseNumber, Rukuh, Sajdah, Manzil, HizbQuarter, VerseText) VALUES (11, 50, '0', '0', '0', '0', 'وأرسلنا إلى عاد أخاهم هودًا، قال لهم: يا قوم اعبدوا الله وحده، ليس لكم من إله يستحق العبادة غيره جل وعلا، فأخلصوا له العبادة، فما أنتم إلا كاذبون في إشراككم بالله.');
INSERT INTO Quran__Arabic_Tafseer_Muyassar (QuranChapterID, VerseNumber, Rukuh, Sajdah, Manzil, HizbQuarter, VerseText) VALUES (11, 51, '0', '0', '0', '0', 'يا قوم لا أسألكم على ما أدعوكم إليه من إخلاص العبادة لله وترك عبادة الأوثان أجرًا، ما أجري على دعوتي لكم إلا على الله الذي خلقني، أفلا تعقلون فتميِّزوا بين الحق والباطل؟');
INSERT INTO Quran__Arabic_Tafseer_Muyassar (QuranChapterID, VerseNumber, Rukuh, Sajdah, Manzil, HizbQuarter, VerseText) VALUES (11, 52, '0', '0', '0', '0', 'ويا قوم اطلبوا مغفرة الله والإيمان به، ثم توبوا إليه من ذنوبكم، فإنكم إن فعلتم ذلك يرسل المطر عليكم متتابعًا كثيرًا، فتكثر خيراتكم، ويزدكم قوة إلى قوتكم بكثرة ذرياتكم وتتابع النِّعم عليكم، ولا تُعرضوا عما دعوتكم إليه مصرِّين على إجرامكم.');
INSERT INTO Quran__Arabic_Tafseer_Muyassar (QuranChapterID, VerseNumber, Rukuh, Sajdah, Manzil, HizbQuarter, VerseText) VALUES (11, 53, '0', '0', '0', '0', 'قالوا: يا هود ما جئتنا بحجة واضحة على صحة ما تدعونا إليه، وما نحن بتاركي آلهتنا التي نعبدها من أجل قولك، وما نحن بمصدِّقين لك فيما تدَّعيه.');
INSERT INTO Quran__Arabic_Tafseer_Muyassar (QuranChapterID, VerseNumber, Rukuh, Sajdah, Manzil, HizbQuarter, VerseText) VALUES (11, 54, '0', '0', '0', '0', 'ما نقول إلا أن بعض آلهتنا أصابك بجنون بسبب نهيك عن عبادتها. قال لهم: إني أُشهد الله على ما أقول، وأُشهدكم على أنني بريء مما تشركون، مِن دون الله من الأنداد والأصنام، فانظروا واجتهدوا أنتم ومَن زعمتم من آلهتكم في إلحاق الضرر بي، ثم لا تؤخروا ذلك طرفة عين؛ ذلك أن هودًا واثق كل الوثوق أنه لا يصيبه منهم ولا من آلهتهم أذى.');
INSERT INTO Quran__Arabic_Tafseer_Muyassar (QuranChapterID, VerseNumber, Rukuh, Sajdah, Manzil, HizbQuarter, VerseText) VALUES (11, 55, '0', '0', '0', '0', 'ما نقول إلا أن بعض آلهتنا أصابك بجنون بسبب نهيك عن عبادتها. قال لهم: إني أُشهد الله على ما أقول، وأُشهدكم على أنني بريء مما تشركون، مِن دون الله من الأنداد والأصنام، فانظروا واجتهدوا أنتم ومَن زعمتم من آلهتكم في إلحاق الضرر بي، ثم لا تؤخروا ذلك طرفة عين؛ ذلك أن هودًا واثق كل الوثوق أنه لا يصيبه منهم ولا من آلهتهم أذى.');
INSERT INTO Quran__Arabic_Tafseer_Muyassar (QuranChapterID, VerseNumber, Rukuh, Sajdah, Manzil, HizbQuarter, VerseText) VALUES (11, 56, '0', '0', '0', '0', 'إني توكلت على الله ربي وربكم مالك كل شيء والمتصرف فيه، فلا يصيبني شيء إلا بأمره، وهو القادر على كل شيء، فليس من شيء يدِبُّ على هذه الأرض إلا والله مالكه، وهو في سلطانه وتصرفه. إن ربي على صراط مستقيم، أي عدل في قضائه وشرعه وأمره. يجازي المحسن بإحسانه والمسيء بإساءته.');
INSERT INTO Quran__Arabic_Tafseer_Muyassar (QuranChapterID, VerseNumber, Rukuh, Sajdah, Manzil, HizbQuarter, VerseText) VALUES (11, 57, '0', '0', '0', '0', 'فإن تُعرضوا عما أدعوكم إليه من توحيد الله وإخلاص العبادة له فقد أبلغتكم رسالة ربي إليكم، وقامت عليكم الحجة، وحيث لم تؤمنوا بالله فسيهلككم ويأتي بقوم آخرين يخلفونكم في دياركم وأموالكم، ويخلصون لله العبادة، ولا تضرونه شيئًا، إن ربي على كل شيء حفيظ، فهو الذي يحفظني من أن تنالوني بسوء.');
INSERT INTO Quran__Arabic_Tafseer_Muyassar (QuranChapterID, VerseNumber, Rukuh, Sajdah, Manzil, HizbQuarter, VerseText) VALUES (11, 58, '0', '0', '0', '0', 'ولما جاء أمرنا بعذاب قوم هود نجَّينا منه هودًا والمؤمنين بفضل منَّا عليهم ورحمة، ونجَّيناهم من عذاب شديد أحله الله بعادٍ فأصبحوا لا يُرى إلا مساكنُهم.');
INSERT INTO Quran__Arabic_Tafseer_Muyassar (QuranChapterID, VerseNumber, Rukuh, Sajdah, Manzil, HizbQuarter, VerseText) VALUES (11, 59, '0', '0', '0', '0', 'وتلك عاد كفروا بآيات الله وعصَوا رسله، وأطاعوا أمر كل مستكبر على الله لا يقبل الحق ولا يُذْعن له.');
INSERT INTO Quran__Arabic_Tafseer_Muyassar (QuranChapterID, VerseNumber, Rukuh, Sajdah, Manzil, HizbQuarter, VerseText) VALUES (11, 60, '1', '0', '0', '0', 'وأُتبعوا في هذه الدنيا لعنة من الله وسخطًا منه يوم القيامة. ألا إن عادًا جحدوا ربهم وكذَّبوا رسله. ألا بُعْدًا وهلاكًا لعاد قوم هود؛ بسبب شركهم وكفرهم نعمة ربهم.');
INSERT INTO Quran__Arabic_Tafseer_Muyassar (QuranChapterID, VerseNumber, Rukuh, Sajdah, Manzil, HizbQuarter, VerseText) VALUES (11, 61, '0', '0', '0', '1', 'وأرسلنا إلى ثمود أخاهم صالحًا، فقال لهم: يا قوم اعبدوا الله وحده ليس لكم من إله يستحق العبادة غيره جل وعلا، فأخلصوا له العبادة، هو الذي بدأ خَلْقكم من الأرض بخلق أبيكم آدم منها، وجعلكم عُمَّارا لها، فاسألوه أن يغفر لكم ذنوبكم، وارجعوا إليه بالتوبة النصوح. إن ربي قريب لمن أخلص له العبادة، ورغب إليه في التوبة، مجيب له إذا دعاه.');
INSERT INTO Quran__Arabic_Tafseer_Muyassar (QuranChapterID, VerseNumber, Rukuh, Sajdah, Manzil, HizbQuarter, VerseText) VALUES (11, 62, '0', '0', '0', '0', 'قالت ثمود لنبيِّهم صالح: لقد كنا نرجو أن تكون فينا سيدًا مطاعًا قبل هذا القول الذي قلته لنا، أتنهانا أن نعبد الآلهة التي كان يعبدها آباؤنا؟ وإننا لفي شكٍّ مريب مِن دعوتك لنا إلى عبادة الله وحده.');
INSERT INTO Quran__Arabic_Tafseer_Muyassar (QuranChapterID, VerseNumber, Rukuh, Sajdah, Manzil, HizbQuarter, VerseText) VALUES (11, 63, '0', '0', '0', '0', 'قال صالح لقومه: يا قوم أخبروني إن كنت على برهان من الله وآتاني منه النبوة والحكمة، فمن الذي يدفع عني عقاب الله تعالى إن عصيته فلم أبلِّغ الرسالة وأنصحْ لكم؟ فما تزيدونني غير تضليل وإبعاد عن الخير.');
INSERT INTO Quran__Arabic_Tafseer_Muyassar (QuranChapterID, VerseNumber, Rukuh, Sajdah, Manzil, HizbQuarter, VerseText) VALUES (11, 64, '0', '0', '0', '0', 'ويا قوم هذه ناقة الله جعلها لكم حجة وعلامة تدلُّ على صدقي فيما أدعوكم إليه، فاتركوها تأكل في أرض الله فليس عليكم رزقها، ولا تمسُّوها بعَقْر، فإنكم إن فعلتم ذلك يأخذكم من الله عذاب قريب من عَقْرها.');
INSERT INTO Quran__Arabic_Tafseer_Muyassar (QuranChapterID, VerseNumber, Rukuh, Sajdah, Manzil, HizbQuarter, VerseText) VALUES (11, 65, '0', '0', '0', '0', 'فكذَّبوه ونحروا الناقة، فقال لهم صالح: استمتعوا بحياتكم في بلدكم ثلاثة أيام، فإن العذاب نازل بكم بعدها، وذلك وَعْدٌ من الله غير مكذوب، لا بد من وقوعه.');
INSERT INTO Quran__Arabic_Tafseer_Muyassar (QuranChapterID, VerseNumber, Rukuh, Sajdah, Manzil, HizbQuarter, VerseText) VALUES (11, 66, '0', '0', '0', '0', 'فلما جاء أمرنا بهلاك ثمود نجينا صالحًا والذين آمنوا معه من الهلاك برحمة منا، ونجيناهم من هوان ذلك اليوم وذلَّته. إن ربك -أيها الرسول- هو القوي العزيز، ومِن قوته وعزته أن أهلك الأمم الطاغية، ونجَّى الرسل وأتباعهم.');
INSERT INTO Quran__Arabic_Tafseer_Muyassar (QuranChapterID, VerseNumber, Rukuh, Sajdah, Manzil, HizbQuarter, VerseText) VALUES (11, 67, '0', '0', '0', '0', 'وأخذت الصيحة القوية ثمود الظالمين، فأصبحوا في ديارهم موتى هامدين ساقطين على وجوههم لا حِرَاك لهم.');
INSERT INTO Quran__Arabic_Tafseer_Muyassar (QuranChapterID, VerseNumber, Rukuh, Sajdah, Manzil, HizbQuarter, VerseText) VALUES (11, 68, '1', '0', '0', '0', 'كأنهم في سرعة زوالهم وفنائهم لم يعيشوا فيها. ألا إن ثمود جحدوا بآيات ربهم وحججه. ألا بُعْدًا لثمود وطردًا لهم من رحمة الله، فما أشقاهم وأذلَّهم!!');
INSERT INTO Quran__Arabic_Tafseer_Muyassar (QuranChapterID, VerseNumber, Rukuh, Sajdah, Manzil, HizbQuarter, VerseText) VALUES (11, 69, '0', '0', '0', '0', 'ولقد جاءت الملائكة إبراهيم يبشرونه هو وزوجته بإسحاق، ويعقوبَ بعده، فقالوا: سلامًا، قال ردًّا على تحيتهم: سلام، فذهب سريعًا وجاءهم بعجل سمين مشويٍّ ليأكلوا منه.');
INSERT INTO Quran__Arabic_Tafseer_Muyassar (QuranChapterID, VerseNumber, Rukuh, Sajdah, Manzil, HizbQuarter, VerseText) VALUES (11, 70, '0', '0', '0', '0', 'فلما رأى إبراهيم أيديهم لا تَصِل إلى العجل الذي أتاهم به ولا يأكلون منه، أنكر ذلك منهم، وأحس في نفسه خيفة وأضمرها، قالت الملائكة -لما رأت ما بإبراهيم من الخوف-: لا تَخَفْ إنا ملائكة ربك أُرسلنا إلى قوم لوط لإهلاكهم.');
INSERT INTO Quran__Arabic_Tafseer_Muyassar (QuranChapterID, VerseNumber, Rukuh, Sajdah, Manzil, HizbQuarter, VerseText) VALUES (11, 71, '0', '0', '0', '0', 'وامرأة إبراهيم -سارة- كانت قائمة من وراء الستر تسمع الكلام، فضحكت تعجبًا مما سمعت، فبشرناها على ألسنة الملائكة بأنها ستلد مِن زوجها إبراهيم ولدًا يسمى إسحاق، وسيعيش ولدها، وسيكون لها بعد إسحاق حفيد منه، وهو يعقوب.');
INSERT INTO Quran__Arabic_Tafseer_Muyassar (QuranChapterID, VerseNumber, Rukuh, Sajdah, Manzil, HizbQuarter, VerseText) VALUES (11, 72, '0', '0', '0', '0', 'قالت سارة لما بُشِّرت بإسحاق متعجبة: يا ويلتا كيف يكون لي ولد وأنا عجوز، وهذا زوجي في حال الشيخوخة والكبر؟ إن إنجاب الولد مِن مثلي ومثل زوجي مع كبر السن لَشيء عجيب.');
INSERT INTO Quran__Arabic_Tafseer_Muyassar (QuranChapterID, VerseNumber, Rukuh, Sajdah, Manzil, HizbQuarter, VerseText) VALUES (11, 73, '0', '0', '0', '0', 'قالت الرسل لها: أتعجبين من أمر الله وقضائه؟ رحمة الله وبركاته عليكم معشر أهل بيت النبوة. إنه سبحانه وتعالى حميد الصفات والأفعال، ذو مَجْد وعظمة فيها.');
INSERT INTO Quran__Arabic_Tafseer_Muyassar (QuranChapterID, VerseNumber, Rukuh, Sajdah, Manzil, HizbQuarter, VerseText) VALUES (11, 74, '0', '0', '0', '0', 'فلما ذهب عن إبراهيم الخوف الذي انتابه لعدم أكل الضيوف الطعام، وجاءته البشرى بإسحاق ويعقوب، ظلَّ يجادل رسلنا فيما أرسلناهم به من عقاب قوم لوط وإهلاكهم.');
INSERT INTO Quran__Arabic_Tafseer_Muyassar (QuranChapterID, VerseNumber, Rukuh, Sajdah, Manzil, HizbQuarter, VerseText) VALUES (11, 75, '0', '0', '0', '0', 'إن إبراهيم كثير الحلم لا يحب المعاجلة بالعقاب، كثير التضرع إلى الله والدعاء له، تائب يرجع إلى الله في أموره كلها.');
INSERT INTO Quran__Arabic_Tafseer_Muyassar (QuranChapterID, VerseNumber, Rukuh, Sajdah, Manzil, HizbQuarter, VerseText) VALUES (11, 76, '0', '0', '0', '0', 'قالت رسل الله: يا إبراهيم أعرض عن هذا الجدال في أمر قوم لوط والتماس الرحمة لهم؛ فإنه قد حق عليهم العذاب، وجاء أمر ربك الذي قدَّره عليهم بهلاكهم، وإنهم نازل بهم عذاب من الله غير مصروف عنهم ولا مدفوع.');
INSERT INTO Quran__Arabic_Tafseer_Muyassar (QuranChapterID, VerseNumber, Rukuh, Sajdah, Manzil, HizbQuarter, VerseText) VALUES (11, 77, '0', '0', '0', '0', 'ولما جاءت ملائكتنا لوطًا ساءه مجيئهم واغتمَّ لذلك؛ وذلك لأنه لم يكن يعلم أنهم رسل الله، فخاف عليهم من قومه، وقال: هذا يوم بلاء وشدة.');
INSERT INTO Quran__Arabic_Tafseer_Muyassar (QuranChapterID, VerseNumber, Rukuh, Sajdah, Manzil, HizbQuarter, VerseText) VALUES (11, 78, '0', '0', '0', '0', 'وجاء قومُ لوط يسرعون المشي إليه لطلب الفاحشة، وكانوا مِن قبل مجيئهم يأتون الرجال شهوة دون النساء، فقال لوط لقومه: هؤلاء بناتي تَزَوَّجوهن فهنَّ أطهر لكم مما تريدون، وسماهن بناته؛ لأن نبي الأمة بمنزلة الأب لهم، فاخشوا الله واحذروا عقابه، ولا تفضحوني بالاعتداء على ضيفي، أليس منكم رجل ذو رشد، ينهى من أراد ركوب الفاحشة، فيحول بينهم وبين ذلك؟');
INSERT INTO Quran__Arabic_Tafseer_Muyassar (QuranChapterID, VerseNumber, Rukuh, Sajdah, Manzil, HizbQuarter, VerseText) VALUES (11, 79, '0', '0', '0', '0', 'قال قوم لوط له: لقد علمتَ من قبلُ أنه ليس لنا في النساء من حاجة أو رغبة، وإنك لتعلم ما نريد، أي لا نريد إلا الرجال ولا رغبة لنا في نكاح النساء.');
INSERT INTO Quran__Arabic_Tafseer_Muyassar (QuranChapterID, VerseNumber, Rukuh, Sajdah, Manzil, HizbQuarter, VerseText) VALUES (11, 80, '0', '0', '0', '0', 'قال لهم حين أبوا إلا فعل الفاحشة: لو أن لي بكم قوة وأنصارًا معي، أو أركَن إلى عشيرة تمنعني منكم، لَحُلْتُ بينكم وبين ما تريدون.');
INSERT INTO Quran__Arabic_Tafseer_Muyassar (QuranChapterID, VerseNumber, Rukuh, Sajdah, Manzil, HizbQuarter, VerseText) VALUES (11, 81, '0', '0', '0', '0', 'قالت الملائكة: يا لوط إنَّا رسل ربك أَرْسَلَنا لإهلاك قومك، وإنهم لن يصلوا إليك، فاخرج من هذه القرية أنت وأهلك ببقية من الليل، ولا يلتفت منكم أحد وراءه؛ لئلا يرى العذاب فيصيبه، لكنَّ امرأتك التي خانتك بالكفر والنفاق سيصيبها ما أصاب قومك من الهلاك، إن موعد هلاكهم الصبح، وهو موعد قريب الحلول.');
INSERT INTO Quran__Arabic_Tafseer_Muyassar (QuranChapterID, VerseNumber, Rukuh, Sajdah, Manzil, HizbQuarter, VerseText) VALUES (11, 82, '0', '0', '0', '0', 'فلما جاء أمرنا بنزول العذاب بهم جعلنا عالي قريتهم التي كانوا يعيشون فيها سافلها فقلبناها، وأمطرنا عليهم حجارة من طين متصلِّب متين، قد صُفَّ بعضها إلى بعض متتابعة، معلَّمة عند الله بعلامة معروفة لا تشاكِل حجارة الأرض، وما هذه الحجارة التي أمطرها الله على قوم لوط من كفار قريش ببعيد أن يُمْطَروا بمثلها. وفي هذا تهديد لكل عاص متمرِّد على الله.');
INSERT INTO Quran__Arabic_Tafseer_Muyassar (QuranChapterID, VerseNumber, Rukuh, Sajdah, Manzil, HizbQuarter, VerseText) VALUES (11, 83, '1', '0', '0', '0', 'فلما جاء أمرنا بنزول العذاب بهم جعلنا عالي قريتهم التي كانوا يعيشون فيها سافلها فقلبناها، وأمطرنا عليهم حجارة من طين متصلِّب متين، قد صُفَّ بعضها إلى بعض متتابعة، معلَّمة عند الله بعلامة معروفة لا تشاكِل حجارة الأرض، وما هذه الحجارة التي أمطرها الله على قوم لوط من كفار قريش ببعيد أن يُمْطَروا بمثلها. وفي هذا تهديد لكل عاص متمرِّد على الله.');
INSERT INTO Quran__Arabic_Tafseer_Muyassar (QuranChapterID, VerseNumber, Rukuh, Sajdah, Manzil, HizbQuarter, VerseText) VALUES (11, 84, '0', '0', '0', '1', 'وأرسلنا إلى "مدين" أخاهم شعيبًا، فقال: يا قوم اعبدوا الله وحده، ليس لكم مِن إله يستحق العبادة غيره جل وعلا، فأخلصوا له العبادة، ولا تنقصوا الناس حقوقهم في مكاييلهم وموازينهم، إني أراكم في سَعَة عيش، وإني أخاف عليكم -بسبب إنقاص المكيال والميزان- عذاب يوم يحيط بكم.');
INSERT INTO Quran__Arabic_Tafseer_Muyassar (QuranChapterID, VerseNumber, Rukuh, Sajdah, Manzil, HizbQuarter, VerseText) VALUES (11, 85, '0', '0', '0', '0', 'ويا قوم أتمُّوا المكيال والميزان بالعدل، ولا تُنْقِصوا الناس حقهم في عموم أشيائهم، ولا تسيروا في الأرض تعملون فيها بمعاصي الله ونشر الفساد.');
INSERT INTO Quran__Arabic_Tafseer_Muyassar (QuranChapterID, VerseNumber, Rukuh, Sajdah, Manzil, HizbQuarter, VerseText) VALUES (11, 86, '0', '0', '0', '0', 'إن ما يبقى لكم بعد إيفاء الكيل والميزان من الربح الحلال خير لكم ممَّا تأخذونه بالتطفيف ونحوه من الكسب الحرام، إن كنتم تؤمنون بالله حقا، فامتثلوا أمره، وما أنا عليكم برقيب أحصي عليكم أعمالكم.');
INSERT INTO Quran__Arabic_Tafseer_Muyassar (QuranChapterID, VerseNumber, Rukuh, Sajdah, Manzil, HizbQuarter, VerseText) VALUES (11, 87, '0', '0', '0', '0', 'قالوا: يا شعيب أهذه الصلاة التي تداوم عليها تأمرك بأن نترك ما يعبده آباؤنا من الأصنام والأوثان، أو أن نمتنع عن التصرف في كسب أموالنا بما نستطيع من احتيال ومكر؟ وقالوا -استهزاءً به-: إنك لأنت الحليم الرشيد.');
INSERT INTO Quran__Arabic_Tafseer_Muyassar (QuranChapterID, VerseNumber, Rukuh, Sajdah, Manzil, HizbQuarter, VerseText) VALUES (11, 88, '0', '0', '0', '0', 'قال شعيب: يا قوم أرأيتم إن كنت على طريق واضح من ربي فيما أدعوكم إليه من إخلاص العبادة له، وفيما أنهاكم عنه من إفساد المال، ورزقني منه رزقًا واسعًا حلالا طيبًا؟ وما أريد أن أخالفكم فأرتكب أمرًا نهيتكم عنه، وما أريد فيما آمركم به وأنهاكم عنه إلا إصلاحكم قَدْر طاقتي واستطاعتي، وما توفيقي -في إصابة الحق ومحاولة إصلاحكم- إلا بالله، على الله وحده توكلت وإليه أرجع بالتوبة والإنابة.');
INSERT INTO Quran__Arabic_Tafseer_Muyassar (QuranChapterID, VerseNumber, Rukuh, Sajdah, Manzil, HizbQuarter, VerseText) VALUES (11, 89, '0', '0', '0', '0', 'ويا قوم لا تحملنَّكم عداوتي وبغضي وفراق الدين الذي أنا عليه على العناد والإصرار على ما أنتم عليه من الكفر بالله، فيصيبكم مثلُ ما أصاب قوم نوح أو قوم هود أو قوم صالح من الهلاك، وما قوم لوط وما حلَّ بهم من العذاب ببعيدين عنكم لا في الدار ولا في الزمان.');
INSERT INTO Quran__Arabic_Tafseer_Muyassar (QuranChapterID, VerseNumber, Rukuh, Sajdah, Manzil, HizbQuarter, VerseText) VALUES (11, 90, '0', '0', '0', '0', 'واطلبوا من ربِّكم المغفرة لذنوبكم، ثم ارجعوا إلى طاعته واستمروا عليها. إن ربِّي رحيم كثير المودة والمحبة لمن تاب إليه وأناب، يرحمه ويقبل توبته. وفي الآية إثبات صفة الرحمة والمودة لله تعالى، كما يليق به سبحانه.');
INSERT INTO Quran__Arabic_Tafseer_Muyassar (QuranChapterID, VerseNumber, Rukuh, Sajdah, Manzil, HizbQuarter, VerseText) VALUES (11, 91, '0', '0', '0', '0', 'قالوا: يا شعيب ما نفقه كثيرًا مما تقول، وإننا لَنراك فينا ضعيفًا لست من الكبراء ولا من الرؤساء، ولولا مراعاة عشيرتك لقتلناك رَجْما بالحجارة -وكان رهطه من أهل ملتهم-، وليس لك قَدْر واحترام في نفوسنا.');
INSERT INTO Quran__Arabic_Tafseer_Muyassar (QuranChapterID, VerseNumber, Rukuh, Sajdah, Manzil, HizbQuarter, VerseText) VALUES (11, 92, '0', '0', '0', '0', 'قال: يا قوم أعشيرتي أعزُّ وأكرم عليكم من الله؟ ونبذتم أمر ربكم فجعلتموه خلف ظهوركم، لا تأتمرون به ولا تنتهون بنهيه، إن ربي بما تعملون محيط، لا يخفى عليه من أعمالكم مثقال ذرة، وسيجازيكم عليها عاجلا وآجلا.');
INSERT INTO Quran__Arabic_Tafseer_Muyassar (QuranChapterID, VerseNumber, Rukuh, Sajdah, Manzil, HizbQuarter, VerseText) VALUES (11, 93, '0', '0', '0', '0', 'ويا قوم اعملوا كل ما تستطيعون على طريقتكم وحالتكم، إني عامل مثابر على طريقتي وما وهبني ربي مِن دعوتكم إلى التوحيد، سوف تعلمون مَن منا يأتيه عذاب يذلُّه، ومَن منا كاذب في قوله، أنا أم أنتم؟ وانتظروا ما سيحل بكم إني معكم من المنتظرين. وهذا تهديد شديد لهم.');
INSERT INTO Quran__Arabic_Tafseer_Muyassar (QuranChapterID, VerseNumber, Rukuh, Sajdah, Manzil, HizbQuarter, VerseText) VALUES (11, 94, '0', '0', '0', '0', 'ولما جاء أمرنا بإهلاك قوم شعيب نجَّينا رسولنا شعيبًا والذين آمنوا معه برحمة منا، وأخذت الذين ظلموا الصيحة من السماء، فأهلكتهم، فأصبحوا في ديارهم باركين على رُكَبهم ميتين لا حِرَاك بهم.');
INSERT INTO Quran__Arabic_Tafseer_Muyassar (QuranChapterID, VerseNumber, Rukuh, Sajdah, Manzil, HizbQuarter, VerseText) VALUES (11, 95, '1', '0', '0', '0', 'كأن لم يقيموا في ديارهم وقتًا من الأوقات. ألا بُعدًا لـ "مدين" -إذ أهلكها الله وأخزاها- كما بَعِدت ثمود، فقد اشتركت هاتان القبيلتان في البعد والهلاك.');
INSERT INTO Quran__Arabic_Tafseer_Muyassar (QuranChapterID, VerseNumber, Rukuh, Sajdah, Manzil, HizbQuarter, VerseText) VALUES (11, 96, '0', '0', '0', '0', 'ولقد أرسلنا موسى بأدلتنا على توحيدنا وحجة تبين لمن عاينها وتأملها -بقلب صحيح- أنها تدل على وحدانية الله، وكَذِبِ كلِّ من ادَّعى الربوبية دونه سبحانه وتعالى.');
INSERT INTO Quran__Arabic_Tafseer_Muyassar (QuranChapterID, VerseNumber, Rukuh, Sajdah, Manzil, HizbQuarter, VerseText) VALUES (11, 97, '0', '0', '0', '0', 'أرسلنا موسى إلى فرعون وأكابر أتباعه وأشراف قومه، فكفر فرعون وأمر قومه أن يتبعوه، فأطاعوه، وخالفوا أمر موسى، وليس في أمر فرعون رشد ولا هدى، وإنما هو جهل وضلال وكفر وعناد.');
INSERT INTO Quran__Arabic_Tafseer_Muyassar (QuranChapterID, VerseNumber, Rukuh, Sajdah, Manzil, HizbQuarter, VerseText) VALUES (11, 98, '0', '0', '0', '0', 'يَقْدُم فرعون قومه يوم القيامة حتى يدخلهم النار، وقبُح المدخل الذي يدخلونه.');
INSERT INTO Quran__Arabic_Tafseer_Muyassar (QuranChapterID, VerseNumber, Rukuh, Sajdah, Manzil, HizbQuarter, VerseText) VALUES (11, 99, '0', '0', '0', '0', 'وأتبعهم الله في هذه الدنيا مع العذاب الذي عجَّله لهم فيها من الغرق في البحر لعنةً، ويوم القيامة كذلك لعنة أخرى بإدخالهم النار، وبئس ما اجتمع لهم وترادَف عليهم من عذاب الله، ولعنة الدنيا والآخرة.');
INSERT INTO Quran__Arabic_Tafseer_Muyassar (QuranChapterID, VerseNumber, Rukuh, Sajdah, Manzil, HizbQuarter, VerseText) VALUES (11, 100, '0', '0', '0', '0', 'ذلك الذي ذكرناه لك -أيها الرسول- من أخبار القرى التي أهلكنا أهلها نخبرك به، ومن تلك القرى ما له آثار باقية، ومنها ما قد مُحِيَتْ آثاره، فلم يَبْق منه شيء.');
INSERT INTO Quran__Arabic_Tafseer_Muyassar (QuranChapterID, VerseNumber, Rukuh, Sajdah, Manzil, HizbQuarter, VerseText) VALUES (11, 101, '0', '0', '0', '0', 'وما كان إهلاكهم بغير سبب وذنب يستحقونه، ولكن ظلموا أنفسهم بشركهم وإفسادهم في الأرض، فما نفعتهم آلهتهم التي كانوا يدعُونها ويطلبون منها أن تدفع عنهم الضر لـمَّا جاء أمر ربك بعذابهم، وما زادتهم آلهتهم غير تدمير وإهلاك وخسران.');
INSERT INTO Quran__Arabic_Tafseer_Muyassar (QuranChapterID, VerseNumber, Rukuh, Sajdah, Manzil, HizbQuarter, VerseText) VALUES (11, 102, '0', '0', '0', '0', 'وكما أخذتُ أهل القرى الظالمة بالعذاب لمخالفتهم أمري وتكذيبهم برسلي، آخذ غيرهم مِن أهل القرى إذا ظلموا أنفسهم بكفرهم بالله ومعصيتهم له وتكذيبهم لرسله. إنَّ أَخْذه بالعقوبة لأليم موجع شديد.');
INSERT INTO Quran__Arabic_Tafseer_Muyassar (QuranChapterID, VerseNumber, Rukuh, Sajdah, Manzil, HizbQuarter, VerseText) VALUES (11, 103, '0', '0', '0', '0', 'إن في أخذنا لأهل القرى السابقة الظالمة لعبرةً وعظة لمن خاف عقاب الله وعذابه في الآخرة، ذلك اليوم الذي يُجمع له الناس جميعًا للمحاسبة والجزاء، ويشهده الخلائق كلهم.');
INSERT INTO Quran__Arabic_Tafseer_Muyassar (QuranChapterID, VerseNumber, Rukuh, Sajdah, Manzil, HizbQuarter, VerseText) VALUES (11, 104, '0', '0', '0', '0', 'وما نؤخر يوم القيامة عنكم إلا لانتهاء مدة معدودة في علمنا، لا تزيد ولا تنقص عن تقديرنا لها بحكمتنا.');
INSERT INTO Quran__Arabic_Tafseer_Muyassar (QuranChapterID, VerseNumber, Rukuh, Sajdah, Manzil, HizbQuarter, VerseText) VALUES (11, 105, '0', '0', '0', '0', 'يوم يأتي يوم القيامة، لا تتكلم نفس إلا بإذن ربها، فمنهم شقي مستحق للعذاب، وسعيد متفضَّل عليه بالنعيم.');
INSERT INTO Quran__Arabic_Tafseer_Muyassar (QuranChapterID, VerseNumber, Rukuh, Sajdah, Manzil, HizbQuarter, VerseText) VALUES (11, 106, '0', '0', '0', '0', 'فأما الذين شَقُوا في الدنيا لفساد عقيدتهم وسوء أعمالهم، فالنار مستقرهم، لهم فيها من شدة ما هم فيه من العذاب زفير وشهيق، وهما أشنع الأصوات وأقبحها، ماكثين في النار أبدًا ما دامت السموات والأرض، فلا ينقطع عذابهم ولا ينتهي، بل هو دائم مؤكَّد، إلا ما شاء ربك من إخراج عصاة الموحدين بعد مدَّة من مكثهم في النار. إن ربك -أيها الرسول- فعَّال لما يريد.');
INSERT INTO Quran__Arabic_Tafseer_Muyassar (QuranChapterID, VerseNumber, Rukuh, Sajdah, Manzil, HizbQuarter, VerseText) VALUES (11, 107, '0', '0', '0', '0', 'فأما الذين شَقُوا في الدنيا لفساد عقيدتهم وسوء أعمالهم، فالنار مستقرهم، لهم فيها من شدة ما هم فيه من العذاب زفير وشهيق، وهما أشنع الأصوات وأقبحها، ماكثين في النار أبدًا ما دامت السموات والأرض، فلا ينقطع عذابهم ولا ينتهي، بل هو دائم مؤكَّد، إلا ما شاء ربك من إخراج عصاة الموحدين بعد مدَّة من مكثهم في النار. إن ربك -أيها الرسول- فعَّال لما يريد.');
INSERT INTO Quran__Arabic_Tafseer_Muyassar (QuranChapterID, VerseNumber, Rukuh, Sajdah, Manzil, HizbQuarter, VerseText) VALUES (11, 108, '0', '0', '0', '1', 'وأما الذين رزقهم الله السعادة فيدخلون الجنة خالدين فيها ما دامت السموات والأرض، إلا الفريق الذي شاء الله تأخيره، وهم عصاة الموحدين، فإنهم يبقون في النار فترة من الزمن، ثم يخرجون منها إلى الجنة بمشيئة الله ورحمته، ويعطي ربك هؤلاء السعداء في الجنة عطاء غير مقطوع عنهم.');
INSERT INTO Quran__Arabic_Tafseer_Muyassar (QuranChapterID, VerseNumber, Rukuh, Sajdah, Manzil, HizbQuarter, VerseText) VALUES (11, 109, '1', '0', '0', '0', 'فلا تكن -أيها الرسول- في شك من بطلان ما يعبد هؤلاء المشركون من قومك، ما يعبدون من الأوثان إلا مثل ما يعبد آباؤهم من قبل، وإنا لموفوهم ما وعدناهم تاما غير منقوص. وهذا توجيه لجميع الأمة، وإن كان لفظه موجهًا إلى الرسول صلى الله عليه وسلَّم.');
INSERT INTO Quran__Arabic_Tafseer_Muyassar (QuranChapterID, VerseNumber, Rukuh, Sajdah, Manzil, HizbQuarter, VerseText) VALUES (11, 110, '0', '0', '0', '0', 'ولقد آتينا موسى الكتاب وهو التوراة، فاختلف فيه قومه، فآمن به جماعة وكفر به آخرون كما فعل قومك بالقرآن. ولولا كلمة سبقت من ربك بأنه لا يعجل لخلقه العذاب، لحلَّ بهم في دنياهم قضاء الله بإهلاك المكذِّبين ونجاة المؤمنين. وإن الكفار من اليهود والمشركين -أيها الرسول- لفي شك -من هذا القرآن- مريب.');
INSERT INTO Quran__Arabic_Tafseer_Muyassar (QuranChapterID, VerseNumber, Rukuh, Sajdah, Manzil, HizbQuarter, VerseText) VALUES (11, 111, '0', '0', '0', '0', 'وإن كل أولئك الأقوام المختلفين الذين ذكرنا لك -أيها الرسول- أخبارهم ليوفينهم ربك جزاء أعمالهم يوم القيامة، إن خيرًا فخير، وإن شرًا فشر، إن ربك بما يعمل هؤلاء المشركون خبير، لا يخفى عليه شيء من عملهم. وفي هذا تهديد ووعيد لهم.');
INSERT INTO Quran__Arabic_Tafseer_Muyassar (QuranChapterID, VerseNumber, Rukuh, Sajdah, Manzil, HizbQuarter, VerseText) VALUES (11, 112, '0', '0', '0', '0', 'فاستقم -أيها النبي- كما أمرك ربك أنت ومن تاب معك، ولا تتجاوزوا ما حدَّه الله لكم، إن ربَّكم بما تعملون من الأعمال كلها بصير، لا يخفى عليه شيء منها، وسيجازيكم عليها.');
INSERT INTO Quran__Arabic_Tafseer_Muyassar (QuranChapterID, VerseNumber, Rukuh, Sajdah, Manzil, HizbQuarter, VerseText) VALUES (11, 113, '0', '0', '0', '0', 'ولا تميلوا إلى هؤلاء الكفار الظلمة، فتصيبكم النار، وما لكم من دون الله من ناصر ينصركم، ويتولى أموركم.');
INSERT INTO Quran__Arabic_Tafseer_Muyassar (QuranChapterID, VerseNumber, Rukuh, Sajdah, Manzil, HizbQuarter, VerseText) VALUES (11, 114, '0', '0', '0', '0', 'وأدِّ الصلاة -أيها النبي- على أتمِّ وجه طَرَفَي النهار في الصباح والمساء، وفي ساعات من الليل. إنَّ فِعْلَ الخيرات يكفِّر الذنوب السالفة ويمحو آثارها، والأمر بإقامة الصلاة وبيان أن الحسنات يذهبن السيئات، موعظة لمن اتعظ بها وتذكر.');
INSERT INTO Quran__Arabic_Tafseer_Muyassar (QuranChapterID, VerseNumber, Rukuh, Sajdah, Manzil, HizbQuarter, VerseText) VALUES (11, 115, '0', '0', '0', '0', 'واصبر -أيها النبي- على الصلاة، وعلى ما تَلْقى من الأذى من مشركي قومك؛ فإن الله لا يضيع ثواب المحسنين في أعمالهم.');
INSERT INTO Quran__Arabic_Tafseer_Muyassar (QuranChapterID, VerseNumber, Rukuh, Sajdah, Manzil, HizbQuarter, VerseText) VALUES (11, 116, '0', '0', '0', '0', 'فهلاَّ وُجد من القرون الماضية بقايا من أهل الخير والصلاح، ينهون أهل الكفر عن كفرهم، وعن الفساد في الأرض، لم يوجد من أولئك الأقوام إلا قليل ممن آمن، فنجَّاهم الله بسبب ذلك مِن عذابه حين أخذ الظالمين. واتَّبع عامتهم من الذين ظلموا أنفسهم ما مُتِّعوا فيه من لذات الدنيا ونعيمها، وكانوا مجرمين ظالمين باتباعهم ما تنعموا فيه، فحقَّ عليهم العذاب.');
INSERT INTO Quran__Arabic_Tafseer_Muyassar (QuranChapterID, VerseNumber, Rukuh, Sajdah, Manzil, HizbQuarter, VerseText) VALUES (11, 117, '0', '0', '0', '0', 'وما كان ربك -أيها الرسول- ليهلك قرية من القرى وأهلها مصلحون في الأرض، مجتنبون للفساد والظلم، وإنما يهلكهم بسبب ظلمهم وفسادهم.');
INSERT INTO Quran__Arabic_Tafseer_Muyassar (QuranChapterID, VerseNumber, Rukuh, Sajdah, Manzil, HizbQuarter, VerseText) VALUES (11, 118, '0', '0', '0', '0', 'ولو شاء ربك لجعل الناس كلهم جماعة واحدة على دين واحد وهو دين الإسلام، ولكنه سبحانه لم يشأ ذلك، فلا يزال الناس مختلفين في أديانهم؛ وذلك مقتضى حكمته.');
INSERT INTO Quran__Arabic_Tafseer_Muyassar (QuranChapterID, VerseNumber, Rukuh, Sajdah, Manzil, HizbQuarter, VerseText) VALUES (11, 119, '0', '0', '0', '0', 'إلا مَن رحم ربك فآمنوا به واتبعوا رسله، فإنهم لا يختلفون في توحيد الله وما جاءت به الرسل من عند الله، وقد اقتضت حكمته سبحانه وتعالى أنه خَلَقهم مختلفين: فريق شقيٌّ وفريق سعيد، وكل ميسر لما خُلِق له. وبهذا يتحقق وعد ربك في قضائه وقدره: أنه سبحانه سيملأ جهنم من الجن والإنس الذين اتبعوا إبليس وجنده ولم يهتدوا للإيمان.');
INSERT INTO Quran__Arabic_Tafseer_Muyassar (QuranChapterID, VerseNumber, Rukuh, Sajdah, Manzil, HizbQuarter, VerseText) VALUES (11, 120, '0', '0', '0', '0', 'ونقصُّ عليك -أيها النبي- من أخبار الرسل الذين كانوا قبلك، كل ما تحتاج إليه مما يقوِّي قلبك للقيام بأعباء الرسالة، وقد جاءك في هذه السورة وما اشتملت عليه من أخبار، بيان الحق الذي أنت عليه، وجاءك فيها موعظة يرتدع بها الكافرون، وذكرى يتذكر بها المؤمنون بالله ورسله.');
INSERT INTO Quran__Arabic_Tafseer_Muyassar (QuranChapterID, VerseNumber, Rukuh, Sajdah, Manzil, HizbQuarter, VerseText) VALUES (11, 121, '0', '0', '0', '0', 'وقل -أيها الرسول- للكافرين الذين لا يقرُّون بوحدانية الله: اعملوا ما أنتم عاملون على حالتكم وطريقتكم في مقاومة الدعوة وإيذاء الرسول والمستجيبين له، فإنَّا عاملون على مكانتنا وطريقتنا من الثبات على ديننا وتنفيذ أمر الله. وانتظروا عاقبة أمرنا، فإنَّا منتظرون عاقبة أمركم. وفي هذا تهديد ووعيد لهم.');
INSERT INTO Quran__Arabic_Tafseer_Muyassar (QuranChapterID, VerseNumber, Rukuh, Sajdah, Manzil, HizbQuarter, VerseText) VALUES (11, 122, '0', '0', '0', '0', 'وقل -أيها الرسول- للكافرين الذين لا يقرُّون بوحدانية الله: اعملوا ما أنتم عاملون على حالتكم وطريقتكم في مقاومة الدعوة وإيذاء الرسول والمستجيبين له، فإنَّا عاملون على مكانتنا وطريقتنا من الثبات على ديننا وتنفيذ أمر الله. وانتظروا عاقبة أمرنا، فإنَّا منتظرون عاقبة أمركم. وفي هذا تهديد ووعيد لهم.');
INSERT INTO Quran__Arabic_Tafseer_Muyassar (QuranChapterID, VerseNumber, Rukuh, Sajdah, Manzil, HizbQuarter, VerseText) VALUES (11, 123, '1', '0', '0', '0', 'ولله سبحانه وتعالى علم كل ما غاب في السموات والأرض، وإليه يُرْجَع الأمر كله يوم القيامة، فاعبده -أيها النبي- وفوِّض أمرك إليه، وما ربك بغافل عما تعملون من الخير والشر، وسيجازي كلاًّ بعمله.');
INSERT INTO Quran__Arabic_Tafseer_Muyassar (QuranChapterID, VerseNumber, Rukuh, Sajdah, Manzil, HizbQuarter, VerseText) VALUES (12, 1, '0', '0', '0', '0', '(الر) سبق الكلام على الحروف المقطَّعة في أول سورة البقرة.');
INSERT INTO Quran__Arabic_Tafseer_Muyassar (QuranChapterID, VerseNumber, Rukuh, Sajdah, Manzil, HizbQuarter, VerseText) VALUES (12, 2, '0', '0', '0', '0', 'إنا أنزلنا هذا القرآن بلغة العرب، لعلكم -أيها العرب- تعقلون معانيه وتفهمونها، وتعملون بهديه.');
INSERT INTO Quran__Arabic_Tafseer_Muyassar (QuranChapterID, VerseNumber, Rukuh, Sajdah, Manzil, HizbQuarter, VerseText) VALUES (12, 3, '0', '0', '0', '0', 'نحن نقصُّ عليك -أيها الرسول- أحسن القصص بوحينا إليك هذا القرآن، وإن كنت قبل إنزاله عليك لمن الغافلين عن هذه الأخبار، لا تدري عنها شيئًا.');
INSERT INTO Quran__Arabic_Tafseer_Muyassar (QuranChapterID, VerseNumber, Rukuh, Sajdah, Manzil, HizbQuarter, VerseText) VALUES (12, 4, '0', '0', '0', '0', 'اذكر -أيها الرسول- لقومك قول يوسف لأبيه: إني رأيت في المنام أحد عشر كوكبًا، والشمس والقمر رأيتهم لي ساجدين. فكانت هذه الرؤيا بشرى لِمَا وصل إليه يوسف عليه السلام من علوِّ المنزلة في الدنيا والآخرة.');
INSERT INTO Quran__Arabic_Tafseer_Muyassar (QuranChapterID, VerseNumber, Rukuh, Sajdah, Manzil, HizbQuarter, VerseText) VALUES (12, 5, '0', '0', '0', '0', 'قال يعقوب لابنه يوسف: يا بني لا تذكر لإخوتك هذه الرؤيا فيحسدوك، ويعادوك، ويحتالوا في إهلاكك، إن الشيطان للإنسان عدو ظاهر العداوة.');
INSERT INTO Quran__Arabic_Tafseer_Muyassar (QuranChapterID, VerseNumber, Rukuh, Sajdah, Manzil, HizbQuarter, VerseText) VALUES (12, 6, '1', '0', '0', '0', 'وكما أراك ربك هذه الرؤيا فكذلك يصطفيك ويعلمك تفسير ما يراه الناس في منامهم من الرؤى مما تؤول إليه واقعًا، ويتم نعمته عليك وعلى آل يعقوب بالنبوة والرسالة، كما أتمها من قبل على أبويك إبراهيم وإسحاق بالنبوة والرسالة. إن ربك عليم بمن يصطفيه من عباده، حكيم في تدبير أمور خلقه.');
INSERT INTO Quran__Arabic_Tafseer_Muyassar (QuranChapterID, VerseNumber, Rukuh, Sajdah, Manzil, HizbQuarter, VerseText) VALUES (12, 7, '0', '0', '0', '1', 'لقد كان في قصة يوسف وإخوته عبر وأدلة تدل على قدرة الله وحكمته لمن يسأل عن أخبارهم، ويرغب في معرفتها.');
INSERT INTO Quran__Arabic_Tafseer_Muyassar (QuranChapterID, VerseNumber, Rukuh, Sajdah, Manzil, HizbQuarter, VerseText) VALUES (12, 8, '0', '0', '0', '0', 'إذ قال إخوة يوسف من أبيه فيما بينهم: إن يوسف وأخاه الشقيق أحب إلى أبينا منا، يفضِّلهما علينا، ونحن جماعة ذوو عدد، إن أبانا لفي خطأ بيِّن حيث فضَّلهما علينا من غير موجب نراه.');
INSERT INTO Quran__Arabic_Tafseer_Muyassar (QuranChapterID, VerseNumber, Rukuh, Sajdah, Manzil, HizbQuarter, VerseText) VALUES (12, 9, '0', '0', '0', '0', 'اقتلوا يوسف أو ألقوا به في أرض مجهولة بعيدة عن العُمران يخلُص لكم حب أبيكم وإقباله عليكم، ولا يلتفت عنكم إلى غيركم، وتكونوا مِنْ بعد قَتْل يوسف أو إبعاده تائبين إلى الله، مستغفرين له من بعد ذنبكم.');
INSERT INTO Quran__Arabic_Tafseer_Muyassar (QuranChapterID, VerseNumber, Rukuh, Sajdah, Manzil, HizbQuarter, VerseText) VALUES (12, 10, '0', '0', '0', '0', 'قال قائل من إخوة يوسف: لا تقتلوا يوسف وألقوه في جوف البئر يلتقطه بعض المارَّة من المسافرين فتستريحوا منه، ولا حاجة إلى قتله، إن كنتم عازمين على فعل ما تقولون.');
INSERT INTO Quran__Arabic_Tafseer_Muyassar (QuranChapterID, VerseNumber, Rukuh, Sajdah, Manzil, HizbQuarter, VerseText) VALUES (12, 11, '0', '0', '0', '0', 'قال إخوة يوسف -بعد اتفاقهم على إبعاده-: يا أبانا ما لك لا تجعلنا أمناء على يوسف مع أنه أخونا، ونحن نريد له الخير ونشفق عليه ونرعاه، ونخصه بخالص النصح؟');
INSERT INTO Quran__Arabic_Tafseer_Muyassar (QuranChapterID, VerseNumber, Rukuh, Sajdah, Manzil, HizbQuarter, VerseText) VALUES (12, 12, '0', '0', '0', '0', 'أرسله معنا غدًا عندما نخرج إلى مراعينا يَسْعَ وينشط ويفرح، ويلعب بالاستباق ونحوه من اللعب المباح، وإنا لحافظون له من كل ما تخاف عليه.');
INSERT INTO Quran__Arabic_Tafseer_Muyassar (QuranChapterID, VerseNumber, Rukuh, Sajdah, Manzil, HizbQuarter, VerseText) VALUES (12, 13, '0', '0', '0', '0', 'قال يعقوب: إني لَيؤلم نفسي مفارقته لي إذا ذهبتم به إلى المراعي، وأخشى أن يأكله الذئب، وأنتم عنه غافلون منشغلون.');
INSERT INTO Quran__Arabic_Tafseer_Muyassar (QuranChapterID, VerseNumber, Rukuh, Sajdah, Manzil, HizbQuarter, VerseText) VALUES (12, 14, '0', '0', '0', '0', 'قال إخوة يوسف لوالدهم: لئن أكله الذئب، ونحن جماعة قوية إنا إذًا لخاسرون، لا خير فينا، ولا نفع يُرْجَى منا.');
INSERT INTO Quran__Arabic_Tafseer_Muyassar (QuranChapterID, VerseNumber, Rukuh, Sajdah, Manzil, HizbQuarter, VerseText) VALUES (12, 15, '0', '0', '0', '0', 'فأرْسَلَهُ معهم. فلما ذهبوا به وأجمعوا على إلقائه في جوف البئر، وأوحينا إلى يوسف لتخبرنَّ إخوتك مستقبلا بفعلهم هذا الذي فعلوه بك، وهم لا يُحِسُّون بذلك الأمر ولا يشعرون به.');
INSERT INTO Quran__Arabic_Tafseer_Muyassar (QuranChapterID, VerseNumber, Rukuh, Sajdah, Manzil, HizbQuarter, VerseText) VALUES (12, 16, '0', '0', '0', '0', 'وجاء إخوة يوسف إلى أبيهم في وقت العِشاء من أول الليل، يبكون ويظهرون الأسف والجزع.');
INSERT INTO Quran__Arabic_Tafseer_Muyassar (QuranChapterID, VerseNumber, Rukuh, Sajdah, Manzil, HizbQuarter, VerseText) VALUES (12, 17, '0', '0', '0', '0', 'قالوا: يا أبانا إنا ذهبنا نتسابق في الجَرْي والرمي بالسهام، وتركنا يوسف عند زادنا وثيابنا، فلم نقصِّر في حفظه، بل تركناه في مأمننا، وما فارقناه إلا وقتًا يسيرًا، فأكله الذئب، وما أنت بمصدِّق لنا ولو كنا موصوفين بالصدق؛ لشدة حبك ليوسف.');
INSERT INTO Quran__Arabic_Tafseer_Muyassar (QuranChapterID, VerseNumber, Rukuh, Sajdah, Manzil, HizbQuarter, VerseText) VALUES (12, 18, '0', '0', '0', '0', 'وجاؤوا بقميصه ملطخًا بدم غير دم يوسف؛ ليشهد على صدقهم، فكان دليلا على كذبهم؛ لأن القميص لم يُمَزَّقْ. فقال لهم أبوهم يعقوب عليه السلام: ما الأمر كما تقولون، بل زيَّنت لكم أنفسكم الأمَّارة بالسوء أمرًا قبيحًا في يوسف، فرأيتموه حسنًا وفعلتموه، فصبري صبر جميل لا شكوى معه لأحد من الخلق، وأستعين بالله على احتمال ما تصفون من الكذب، لا على حولي وقوتي.');
INSERT INTO Quran__Arabic_Tafseer_Muyassar (QuranChapterID, VerseNumber, Rukuh, Sajdah, Manzil, HizbQuarter, VerseText) VALUES (12, 19, '0', '0', '0', '0', 'وجاءت جماعة من المسافرين، فأرسلوا مَن يطلب لهم الماء، فلما أرسل دلوه في البئر تعلَّق بها يوسف، فقال واردهم: يا بشراي هذا غلام نفيس، وأخفى الواردُ وأصحابه يوسفَ من بقية المسافرين فلم يظهروه لهم، وقالوا: إن هذه بضاعة استبضعناها، والله عليم بما يعملونه بيوسف.');
INSERT INTO Quran__Arabic_Tafseer_Muyassar (QuranChapterID, VerseNumber, Rukuh, Sajdah, Manzil, HizbQuarter, VerseText) VALUES (12, 20, '1', '0', '0', '0', 'وباعه إخوته للواردين من المسافرين بثمن قليل من الدراهم، وكانوا زاهدين فيه راغبين في التخلص منه؛ وذلك أنهم لا يعلمون منزلته عند الله.');
INSERT INTO Quran__Arabic_Tafseer_Muyassar (QuranChapterID, VerseNumber, Rukuh, Sajdah, Manzil, HizbQuarter, VerseText) VALUES (12, 21, '0', '0', '0', '0', 'ولما ذهب المسافرون بيوسف إلى "مصر" اشتراه منهم عزيزها، وهو الوزير، وقال لامرأته: أحسني معاملته، واجعلي مقامه عندنا كريمًا، لعلنا نستفيد من خدمته، أو نقيمه عندنا مقام الولد، وكما أنجينا يوسف وجعلنا عزيز "مصر" يَعْطِف عليه، فكذلك مكنَّا له في أرض "مصر"، وجعلناه على خزائنها، ولنعلِّمه تفسير الرؤى فيعرف منها ما سيقع مستقبلا. والله غالب على أمره، فحكمه نافذ لا يبطله مبطل، ولكن أكثر الناس لا يعلمون أن الأمر كله بيد الله.');
INSERT INTO Quran__Arabic_Tafseer_Muyassar (QuranChapterID, VerseNumber, Rukuh, Sajdah, Manzil, HizbQuarter, VerseText) VALUES (12, 22, '0', '0', '0', '0', 'ولما بلغ يوسف منتهى قوته في شبابه أعطيناه فهمًا وعلمًا، ومثل هذا الجزاء الذي جزينا به يوسف على إحسانه نجزي المحسنين على إحسانهم. وفي هذا تسلية للرسول صلى الله عليه وسلم.');
INSERT INTO Quran__Arabic_Tafseer_Muyassar (QuranChapterID, VerseNumber, Rukuh, Sajdah, Manzil, HizbQuarter, VerseText) VALUES (12, 23, '0', '0', '0', '0', 'ودعت امرأة العزيز -برفق ولين- يوسف الذي هو في بيتها إلى نفسها؛ لحبها الشديد له وحسن بهائه، وغلَّقت الأبواب عليها وعلى يوسف، وقالت: هلمَّ إليَّ، فقال: معاذ الله أعتصم به، وأستجير مِن الذي تدعينني إليه، من خيانة سيدي الذي أحسن منزلتي وأكرمني فلا أخونه في أهله، إنه لا يفلح مَن ظَلَم فَفَعل ما ليس له فعله.');
INSERT INTO Quran__Arabic_Tafseer_Muyassar (QuranChapterID, VerseNumber, Rukuh, Sajdah, Manzil, HizbQuarter, VerseText) VALUES (12, 24, '0', '0', '0', '0', 'ولقد مالت نفسها لفعل الفاحشة، وحدَّثت يوسفَ نفسُه حديث خطرات للاستجابة، لولا أن رأى آية من آيات ربه تزجره عمَّا حدثته به نفسه، وإنما أريناه ذلك؛ لندفع عنه السوء والفاحشة في جميع أموره، إنه من عبادنا المطهرين المصطفَين للرسالة الذين أخلصوا في عبادتهم لله وتوحيده.');
INSERT INTO Quran__Arabic_Tafseer_Muyassar (QuranChapterID, VerseNumber, Rukuh, Sajdah, Manzil, HizbQuarter, VerseText) VALUES (12, 25, '0', '0', '0', '0', 'وأسرع يوسف إلى الباب يريد الخروج، وأسرعت تحاول الإمساك به، وجذبت قميصه من خلفه؛ لتحول بينه وبين الخروج فشقَّته، ووجدا زوجها عند الباب فقالت: ما جزاء مَن أراد بامرأتك فاحشة إلا أن يسجن أو يعذب العذاب الموجع.');
INSERT INTO Quran__Arabic_Tafseer_Muyassar (QuranChapterID, VerseNumber, Rukuh, Sajdah, Manzil, HizbQuarter, VerseText) VALUES (12, 26, '0', '0', '0', '0', 'قال يوسف: هي التي طلبت مني ذلك، فشهد صبي في المهد مِن أهلها فقال: إن كان قميصه شُقَّ من الأمام فصدقت في اتِّهامها له، وهو من الكاذبين.');
INSERT INTO Quran__Arabic_Tafseer_Muyassar (QuranChapterID, VerseNumber, Rukuh, Sajdah, Manzil, HizbQuarter, VerseText) VALUES (12, 27, '0', '0', '0', '0', 'وإن كان قميصه شُقَّ من الخلف فكذبت في قولها، وهو من الصادقين.');
INSERT INTO Quran__Arabic_Tafseer_Muyassar (QuranChapterID, VerseNumber, Rukuh, Sajdah, Manzil, HizbQuarter, VerseText) VALUES (12, 28, '0', '0', '0', '0', 'فلما رأى الزوج قميص يوسف شُقَّ من خلفه علم براءة يوسف، وقال لزوجته: إن هذا الكذب الذي اتهمتِ به هذا الشاب هو مِن جملة مكركن -أيتها النساء-، إنَّ مكركن عظيم.');
INSERT INTO Quran__Arabic_Tafseer_Muyassar (QuranChapterID, VerseNumber, Rukuh, Sajdah, Manzil, HizbQuarter, VerseText) VALUES (12, 29, '1', '0', '0', '0', 'قال عزيز "مصر": يا يوسف اترك ذِكْر ما كان منها فلا تذكره لأحد، واطلبي -أيتها المرأة- المغفرة لذنبك؛ إنك كنتِ من الآثمين في مراودة يوسف عن نفسه، وفي افترائك عليه.');
INSERT INTO Quran__Arabic_Tafseer_Muyassar (QuranChapterID, VerseNumber, Rukuh, Sajdah, Manzil, HizbQuarter, VerseText) VALUES (12, 30, '0', '0', '0', '1', 'ووصل الخبر إلى نسوة في المدينة فتحدثن به، وقلن منكرات على امرأة العزيز: امرأة العزيز تحاول غلامها عن نفسه، وتدعوه إلى نفسها، وقد بلغ حبها له شَغَاف قلبها (وهو غلافه)، إنا لَنراها في هذا الفعل لفي ضلال واضح.');
INSERT INTO Quran__Arabic_Tafseer_Muyassar (QuranChapterID, VerseNumber, Rukuh, Sajdah, Manzil, HizbQuarter, VerseText) VALUES (12, 31, '0', '0', '0', '0', 'فلما سمعت امرأة العزيز بغِيْبتهن إياها واحتيالهن في ذمِّها، أرسلت إليهن تدعوهن لزيارتها، وهيَّأت لهن ما يتكئن عليه من الوسائد، وما يأكلنه من الطعام، وأعطت كل واحدة منهن سكينًا ليُقَطِّعن الطعام، ثم قالت ليوسف: اخرج عليهن، فلما رأينه أعظمنه وأجللنه، وأخَذَهن حسنه وجماله، فجرحن أيديهن وهن يُقَطِّعن الطعام من فرط الدهشة والذهول، وقلن متعجبات: معاذ الله، ما هذا من جنس البشر؛ لأن جماله غير معهود في البشر، ما هو إلا مَلَك كريم من الملائكة.');
INSERT INTO Quran__Arabic_Tafseer_Muyassar (QuranChapterID, VerseNumber, Rukuh, Sajdah, Manzil, HizbQuarter, VerseText) VALUES (12, 32, '0', '0', '0', '0', 'قالت امرأة العزيز للنسوة اللاتي قطَّعن أيديهن: فهذا الذي أصابكن في رؤيتكن إياه ما أصابكن هو الفتى الذي لُمتُنَّني في الافتتان به، ولقد طلبته وحاولت إغراءه؛ ليستجيب لي فامتنع وأبى، ولئن لم يفعل ما آمره به مستقبلا لَيعاقَبَنَّ بدخول السجن، ولَيكونن من الأذلاء.');
INSERT INTO Quran__Arabic_Tafseer_Muyassar (QuranChapterID, VerseNumber, Rukuh, Sajdah, Manzil, HizbQuarter, VerseText) VALUES (12, 33, '0', '0', '0', '0', 'قال يوسف مستعيذًا مِن شرهن ومكرهن: يا ربِّ السجنُ أحب إليَّ مما يدعونني إليه من عمل الفاحشة، وإن لم تدفع عني مكرهن أَمِلْ إليهن، وأكن من السفهاء الذين يرتكبون الإثم لجهلهم.');
INSERT INTO Quran__Arabic_Tafseer_Muyassar (QuranChapterID, VerseNumber, Rukuh, Sajdah, Manzil, HizbQuarter, VerseText) VALUES (12, 34, '0', '0', '0', '0', 'فاستجاب الله ليوسف دعاءه فصرف عنه ما أرادت منه امرأة العزيز وصواحباتها من معصية الله. إن الله هو السميع لدعاء يوسف، ودعاء كل داع مِن خلقه، العليم بمطلبه وحاجته وما يصلحه، وبحاجة جميع خلقه وما يصلحهم.');
INSERT INTO Quran__Arabic_Tafseer_Muyassar (QuranChapterID, VerseNumber, Rukuh, Sajdah, Manzil, HizbQuarter, VerseText) VALUES (12, 35, '1', '0', '0', '0', 'ثم ظهر للعزيز وأصحابه -من بعد ما رأوا الأدلة على براءة يوسف وعفته- أن يسجنوه إلى زمن يطول أو يقصر؛ منعًا للفضيحة.');
INSERT INTO Quran__Arabic_Tafseer_Muyassar (QuranChapterID, VerseNumber, Rukuh, Sajdah, Manzil, HizbQuarter, VerseText) VALUES (12, 36, '0', '0', '0', '0', 'ودخل السجن مع يوسف فَتَيان، قال أحدهما: إني رأيت في المنام أني أعصر عنبًا ليصير خمرًا، وقال الآخر: إني رأيت أني أحمل فوق رأسي خبزًا تأكل الطير منه، أخبرنا -يا يوسف -بتفسير ما رأينا، إنا نراك من الذين يحسنون في عبادتهم لله، ومعاملتهم لخلقه.');
INSERT INTO Quran__Arabic_Tafseer_Muyassar (QuranChapterID, VerseNumber, Rukuh, Sajdah, Manzil, HizbQuarter, VerseText) VALUES (12, 37, '0', '0', '0', '0', 'قال لهما يوسف: لا يأتيكما طعام ترزقانه في حال من الأحوال إلا أخبرتكما بتفسيره قبل أن يأتيكما، ذلكما التعبير الذي سأعبِّره لكما مما علَّمني ربي؛ إني آمنت به، وأخلصت له العبادة، وابتعدت عن دين قوم لا يؤمنون بالله، وهم بالبعث والحساب جاحدون.');
INSERT INTO Quran__Arabic_Tafseer_Muyassar (QuranChapterID, VerseNumber, Rukuh, Sajdah, Manzil, HizbQuarter, VerseText) VALUES (12, 38, '0', '0', '0', '0', 'واتبعت دين آبائي إبراهيم وإسحاق ويعقوب فعبدت الله وحده، ما كان لنا أن نجعل لله شريكًا في عبادته، ذلك التوحيد بإفراد الله بالعبادة، مما تفضل الله به علينا وعلى الناس، ولكن أكثر الناس لا يشكرون الله على نعمة التوحيد والإيمان.');
INSERT INTO Quran__Arabic_Tafseer_Muyassar (QuranChapterID, VerseNumber, Rukuh, Sajdah, Manzil, HizbQuarter, VerseText) VALUES (12, 39, '0', '0', '0', '0', 'وقال يوسف للفَتَيين اللذين معه في السجن: أعبادةُ آلهةٍ مخلوقة شتى خير أم عبادة الله الواحد القهار؟');
INSERT INTO Quran__Arabic_Tafseer_Muyassar (QuranChapterID, VerseNumber, Rukuh, Sajdah, Manzil, HizbQuarter, VerseText) VALUES (12, 40, '0', '0', '0', '0', 'ما تعبدون من دون الله إلا أسماءً لا معاني وراءها، جعلتموها أنتم وآباؤكم أربابًا جهلا منكم وضلالا، ما أنزل الله من حجة أو برهان على صحتها، ما الحكم الحق إلا لله تعالى وحده، لا شريك له، أمر ألا تنقادوا ولا تخضعوا لغيره، وأن تعبدوه وحده، وهذا هو الدين القيم الذي لا عوج فيه، ولكن أكثر الناس يجهلون ذلك، فلا يعلمون حقيقته.');
INSERT INTO Quran__Arabic_Tafseer_Muyassar (QuranChapterID, VerseNumber, Rukuh, Sajdah, Manzil, HizbQuarter, VerseText) VALUES (12, 41, '0', '0', '0', '0', 'يا صاحبيَّ في السجن، إليكما تفسيرَ رؤياكما: أما الذي رأى أنه يعصر العنب في رؤياه فإنه يخرج من السجن ويكون ساقي الخمر للملك، وأما الآخر الذي رأى أنه يحمل على رأسه خبزًا فإنه يُصْلب ويُتْرك، وتأكل الطير من رأسه، قُضي الأمر الذي فيه تستفتيان وفُرغ منه.');
INSERT INTO Quran__Arabic_Tafseer_Muyassar (QuranChapterID, VerseNumber, Rukuh, Sajdah, Manzil, HizbQuarter, VerseText) VALUES (12, 42, '1', '0', '0', '0', 'وقال يوسف للذي علم أنه ناجٍ من صاحبيه: اذكرني عند سيِّدك الملك وأخبره بأني مظلوم محبوس بلا ذنب، فأنسى الشيطان ذلك الرجل أن يذكر للملك حال يوسف، فمكث يوسف بعد ذلك في السجن عدة سنوات.');
INSERT INTO Quran__Arabic_Tafseer_Muyassar (QuranChapterID, VerseNumber, Rukuh, Sajdah, Manzil, HizbQuarter, VerseText) VALUES (12, 43, '0', '0', '0', '0', 'وقال الملك: إني رأيت في منامي سبع بقرات سمان، يأكلهن سبع بقرات نحيلات من الهُزال، ورأيت سبع سنبلات خضر، وسبع سنبلات يابسات، يا أيها السادة والكبراء أخبروني عن هذه الرؤيا، إن كنتم للرؤيا تُفَسِّرون.');
INSERT INTO Quran__Arabic_Tafseer_Muyassar (QuranChapterID, VerseNumber, Rukuh, Sajdah, Manzil, HizbQuarter, VerseText) VALUES (12, 44, '0', '0', '0', '0', 'قالوا: رؤياك هذه أخلاط أحلام لا تأويل لها، وما نحن بتفسير الأحلام بعالمين.');
INSERT INTO Quran__Arabic_Tafseer_Muyassar (QuranChapterID, VerseNumber, Rukuh, Sajdah, Manzil, HizbQuarter, VerseText) VALUES (12, 45, '0', '0', '0', '0', 'وقال الذي نجا من القتل من صاحبَي يوسف في السجن وتذكر بعد مدة ما نسي من أمر يوسف: أنا أخبركم بتأويل هذه الرؤيا، فابعثوني إلى يوسف لآتيكم بتفسيرها.');
INSERT INTO Quran__Arabic_Tafseer_Muyassar (QuranChapterID, VerseNumber, Rukuh, Sajdah, Manzil, HizbQuarter, VerseText) VALUES (12, 46, '0', '0', '0', '0', 'وعندما وصل الرجل إلى يوسف قال له: يوسف أيها الصديق فسِّر لنا رؤيا مَن رأى سبع بقرات سمان يأكلهن سبع بقرات هزيلات، ورأى سبع سنبلات خضر وأخر يابسات؛ لعلي أرجع إلى الملك وأصحابه فأخبرهم؛ ليعلموا تأويل ما سألتك عنه، وليعلموا مكانتك وفضلك.');
INSERT INTO Quran__Arabic_Tafseer_Muyassar (QuranChapterID, VerseNumber, Rukuh, Sajdah, Manzil, HizbQuarter, VerseText) VALUES (12, 47, '0', '0', '0', '0', 'قال يوسف لسائله عن رؤيا الملك: تفسير هذه الرؤيا أنكم تزرعون سبع سنين متتابعة جادِّين ليَكْثُر العطاء، فما حصدتم منه في كل مرة فادَّخِروه، واتركوه في سنبله؛ ليتمَّ حفظه من التسوُّس، وليكون أبقى، إلا قليلا مما تأكلونه من الحبوب.');
INSERT INTO Quran__Arabic_Tafseer_Muyassar (QuranChapterID, VerseNumber, Rukuh, Sajdah, Manzil, HizbQuarter, VerseText) VALUES (12, 48, '0', '0', '0', '0', 'ثم يأتي بعد هذه السنين الخِصْبة سبع سنين شديدة الجَدْب، يأكل أهلها كل ما ادَّخرتم لهن من قبل، إلا قليلا مما تحفظونه وتدَّخرونه ليكون بذورًا للزراعة.');
INSERT INTO Quran__Arabic_Tafseer_Muyassar (QuranChapterID, VerseNumber, Rukuh, Sajdah, Manzil, HizbQuarter, VerseText) VALUES (12, 49, '1', '0', '0', '0', 'ثم يأتي من بعد هذه السنين المجدبة عام يغاث فيه الناس بالمطر، فيرفع الله تعالى عنهم الشدة، ويعصرون فيه الثمار من كثرة الخِصْب والنماء.');
INSERT INTO Quran__Arabic_Tafseer_Muyassar (QuranChapterID, VerseNumber, Rukuh, Sajdah, Manzil, HizbQuarter, VerseText) VALUES (12, 50, '0', '0', '0', '0', 'وقال الملك لأعوانه: أخرجوا الرجل المعبِّر للرؤيا من السجن وأحضروه لي، فلما جاءه رسول الملك يدعوه قال يوسف للرسول: ارجع إلى سيدك الملك، واطلب منه أن يسأل النسوة اللاتي جرحن أيديهن عن حقيقة أمرهن وشأنهن معي؛ لتظهر الحقيقة للجميع، وتتضح براءتي، إن ربي عليم بصنيعهن وأفعالهن لا يخفى عليه شيء من ذلك.');
INSERT INTO Quran__Arabic_Tafseer_Muyassar (QuranChapterID, VerseNumber, Rukuh, Sajdah, Manzil, HizbQuarter, VerseText) VALUES (12, 51, '0', '0', '0', '0', 'قال الملك للنسوة اللاتي جرحن أيديهن: ما شأنكن حين راودتنَّ يوسف عن نفسه يوم الضيافة؟ فهل رأيتن منه ما يريب؟ قلن: معاذ الله ما علمنا عليه أدنى شيء يَشينه، عند ذلك قالت امراة العزيز: الآن ظهر الحق بعد خفائه، فأنا التي حاولت فتنته بإغرائه فامتنع، وإنه لمن الصادقين في كل ما قاله.');
INSERT INTO Quran__Arabic_Tafseer_Muyassar (QuranChapterID, VerseNumber, Rukuh, Sajdah, Manzil, HizbQuarter, VerseText) VALUES (12, 52, '0', '0', '0', '0', 'ذلك القول الذي قلته في تنزيهه والإقرار على نفسي ليعلم زوجي أني لم أخنه بالكذب عليه، ولم تقع مني الفاحشة، وأنني راودته، واعترفت بذلك لإظهار براءتي وبراءته، وأن الله لا يوفق أهل الخيانة، ولا يرشدهم في خيانتهم.');
INSERT INTO Quran__Arabic_Tafseer_Muyassar (QuranChapterID, VerseNumber, Rukuh, Sajdah, Manzil, HizbQuarter, VerseText) VALUES (12, 53, '0', '0', '0', '1', 'قالت امرأة العزيز: وما أزكي نفسي ولا أبرئها، إن النفس لكثيرة الأمر لصاحبها بعمل المعاصي طلبا لملذاتها، إلا مَن عصمه الله. إن الله غفور لذنوب مَن تاب مِن عباده، رحيم بهم.');
INSERT INTO Quran__Arabic_Tafseer_Muyassar (QuranChapterID, VerseNumber, Rukuh, Sajdah, Manzil, HizbQuarter, VerseText) VALUES (12, 54, '0', '0', '0', '0', 'وقال الملك الحاكم لـ "مصر" حين بلغته براءة يوسف: جيئوني به أجعله من خلصائي وأهل مشورتي، فلما جاء يوسف وكلَّمه الملك، وعرف براءته، وعظيم أمانته، وحسن خلقه، قال له: إنك اليوم عندنا عظيم المكانة، ومؤتمن على كل شيء.');
INSERT INTO Quran__Arabic_Tafseer_Muyassar (QuranChapterID, VerseNumber, Rukuh, Sajdah, Manzil, HizbQuarter, VerseText) VALUES (12, 55, '0', '0', '0', '0', 'وأراد يوسف أن ينفع العباد، ويقيم العدل بينهم، فقال للملك: اجعلني واليًا على خزائن "مصر"، فإني خازن أمين، ذو علم وبصيرة بما أتولاه.');
INSERT INTO Quran__Arabic_Tafseer_Muyassar (QuranChapterID, VerseNumber, Rukuh, Sajdah, Manzil, HizbQuarter, VerseText) VALUES (12, 56, '0', '0', '0', '0', 'وكما أنعم الله على يوسف بالخلاص من السجن مكَّن له في أرض "مصر" ينزل منها أي منزل شاءه. يصيب الله برحمته من يشاء من عباده المتقين، ولا يضيع أجر مَن أحسن شيئًا مِن العمل الصالح.');
INSERT INTO Quran__Arabic_Tafseer_Muyassar (QuranChapterID, VerseNumber, Rukuh, Sajdah, Manzil, HizbQuarter, VerseText) VALUES (12, 57, '1', '0', '0', '0', 'ولَثواب الآخرة عند الله أعظم من ثواب الدنيا لأهل الإيمان والتقوى الذين يخافون عقاب الله، ويطيعونه في أمره ونهيه.');
INSERT INTO Quran__Arabic_Tafseer_Muyassar (QuranChapterID, VerseNumber, Rukuh, Sajdah, Manzil, HizbQuarter, VerseText) VALUES (12, 58, '0', '0', '0', '0', 'وقدِمَ إخوة يوسف إلى "مصر" -بعد أن حلَّ بهم الجدب في أرضهم-؛ ليجلبوا منها الطعام، فدخلوا عليه فعرفهم، ولم يعرفوه لطول المدة وتغيُّر هيئته.');
INSERT INTO Quran__Arabic_Tafseer_Muyassar (QuranChapterID, VerseNumber, Rukuh, Sajdah, Manzil, HizbQuarter, VerseText) VALUES (12, 59, '0', '0', '0', '0', 'وقد أمر يوسف بإكرامهم وحسن ضيافتهم، ثم أعطاهم من الطعام ما طلبوا، وكانوا قد أخبروه أن لهم أخًا من أبيهم لم يُحضروه معهم -يريدون شقيقه- فقال: ائتوني بأخيكم من أبيكم، ألم تروا أني أوفيتُ لكم الكيل وأكرمتكم في الضيافة، وأنا خير المضيفين لكم؟');
INSERT INTO Quran__Arabic_Tafseer_Muyassar (QuranChapterID, VerseNumber, Rukuh, Sajdah, Manzil, HizbQuarter, VerseText) VALUES (12, 60, '0', '0', '0', '0', 'فإن لم تأتوني به فليس لكم عندي طعام أكيله لكم، ولا تأتوا إليَّ.');
INSERT INTO Quran__Arabic_Tafseer_Muyassar (QuranChapterID, VerseNumber, Rukuh, Sajdah, Manzil, HizbQuarter, VerseText) VALUES (12, 61, '0', '0', '0', '0', 'قالوا: سنبذل جهدنا لإقناع أبيه أن يرسله معنا، ولن نقصِّر في ذلك.');
INSERT INTO Quran__Arabic_Tafseer_Muyassar (QuranChapterID, VerseNumber, Rukuh, Sajdah, Manzil, HizbQuarter, VerseText) VALUES (12, 62, '0', '0', '0', '0', 'وقال يوسف لغلمانه: اجعلوا ثمن ما أخذوه في أمتعتهم سرًا؛ رجاء أن يعرفوه إذا رجعوا إلى أهلهم، ويقدِّروا إكرامنا لهم؛ ليرجعوا طمعًا في عطائنا.');
INSERT INTO Quran__Arabic_Tafseer_Muyassar (QuranChapterID, VerseNumber, Rukuh, Sajdah, Manzil, HizbQuarter, VerseText) VALUES (12, 63, '0', '0', '0', '0', 'فلما رجعوا إلى أبيهم قصُّوا عليه ما كان من إكرام العزيز لهم، وقالوا: إنه لن يعطينا مستقبَلا إلا إذا كان معنا أخونا الذي أخبرناه به، فأرسلْه معنا نحضر الطعام وافيًا، ونتعهد لك بحفظه.');
INSERT INTO Quran__Arabic_Tafseer_Muyassar (QuranChapterID, VerseNumber, Rukuh, Sajdah, Manzil, HizbQuarter, VerseText) VALUES (12, 64, '0', '0', '0', '0', 'قال لهم أبوهم: كيف آمنكم على "بنيامين" وقد أمنتكم على أخيه يوسف من قبل، والتزمتم بحفظه فلم تفوا بذلك؟ فلا أثق بالتزامكم وحفظكم، ولكني أثق بحفظ الله، خير الحافظين وأرحم الراحمين، أرجو أن يرحمني فيحفظه ويرده عليَّ.');
INSERT INTO Quran__Arabic_Tafseer_Muyassar (QuranChapterID, VerseNumber, Rukuh, Sajdah, Manzil, HizbQuarter, VerseText) VALUES (12, 65, '0', '0', '0', '0', 'ولما فتحوا أوعيتهم وجدوا ثمن بضاعتهم الذي دفعوه قد رُدَّ إليهم قالوا: يا أبانا ماذا نطلب أكثر من هذا؟ هذا ثمن بضاعتنا ردَّه العزيز إلينا، فكن مطمئنًا على أخينا، وأرسله معنا؛ لنجلب طعامًا وفيرًا لأهلنا، ونحفظ أخانا، ونزداد حِمْلَ بعير له؛ فإن العزيز يكيل لكل واحد حِمْلَ بعير، وذلك كيل يسير عليه.');
INSERT INTO Quran__Arabic_Tafseer_Muyassar (QuranChapterID, VerseNumber, Rukuh, Sajdah, Manzil, HizbQuarter, VerseText) VALUES (12, 66, '0', '0', '0', '0', 'قال لهم يعقوب: لن أتركه يذهب معكم حتى تتعهدوا وتحلفوا لي بالله أن تردوه إليَّ، إلا أن تُغْلبوا عليه فلا تستطيعوا تخليصه، فلما أعطَوْه عهد الله على ما طلب، قال يعقوب: الله على ما نقول وكيل، أي تكفينا شهادته علينا وحفظه لنا.');
INSERT INTO Quran__Arabic_Tafseer_Muyassar (QuranChapterID, VerseNumber, Rukuh, Sajdah, Manzil, HizbQuarter, VerseText) VALUES (12, 67, '0', '0', '0', '0', 'وقال لهم أبوهم: يا أبنائي إذا دخلتم أرض "مصر" فلا تدخلوا مِن باب واحد، ولكن ادخلوها من أبواب متفرقة، حتى لا تصيبكم العين، وإني إذ أوصيكم بهذا لا أدفع عنكم شيئًا قضاه الله عليكم، فما الحكم إلا لله وحده، عليه اعتمدت ووثقت، وعليه وحده يعتمد المؤمنون.');
INSERT INTO Quran__Arabic_Tafseer_Muyassar (QuranChapterID, VerseNumber, Rukuh, Sajdah, Manzil, HizbQuarter, VerseText) VALUES (12, 68, '1', '0', '0', '0', 'ولما دخلوا من أبواب متفرقة كما أمرهم أبوهم، ما كان ذلك ليدفع قضاء الله عنهم، ولكن كان شفقة في نفس يعقوب عليهم أن تصيبهم العين، وإن يعقوب لصاحب علمٍ عظيم بأمر دينه علَّمه الله له وحْيًا، ولكن أكثر الناس لا يعلمون عواقب الأمور ودقائق الأشياء، وما يعلمه يعقوب -عليه السلام- مِن أمر دينه.');
INSERT INTO Quran__Arabic_Tafseer_Muyassar (QuranChapterID, VerseNumber, Rukuh, Sajdah, Manzil, HizbQuarter, VerseText) VALUES (12, 69, '0', '0', '0', '0', 'ولما دخل إخوة يوسف عليه في منزل ضيافته ومعهم شقيقه، ضم يوسف إليه شقيقه، وقال له سرًا: إني أنا أخوك فلا تحزن، ولا تغتمَّ بما صنعوه بي فيما مضى. وأمره بكتمان ذلك عنهم.');
INSERT INTO Quran__Arabic_Tafseer_Muyassar (QuranChapterID, VerseNumber, Rukuh, Sajdah, Manzil, HizbQuarter, VerseText) VALUES (12, 70, '0', '0', '0', '0', 'فلما جهزَّهم يوسف، وحمَّل إبلهم بالطعام، أمر عماله، فوضعوا الإناء الذي كان يكيل للناس به في متاع أخيه "بنيامين" من حيث لا يشعر أحد، ولما ركبوا ليسيروا نادى منادٍ قائلا يا أصحاب هذه العير المحمَّلة بالطعام، إنكم لسارقون.');
INSERT INTO Quran__Arabic_Tafseer_Muyassar (QuranChapterID, VerseNumber, Rukuh, Sajdah, Manzil, HizbQuarter, VerseText) VALUES (12, 71, '0', '0', '0', '0', 'قال أولاد يعقوب مقبلين على المنادي: ما الذي تفقدونه؟');
INSERT INTO Quran__Arabic_Tafseer_Muyassar (QuranChapterID, VerseNumber, Rukuh, Sajdah, Manzil, HizbQuarter, VerseText) VALUES (12, 72, '0', '0', '0', '0', 'قال المنادي ومَن بحضرته: نفقد المكيال الذي يكيل الملك به، ومكافأة من يحضره مقدار حِمْل بعير من الطعام، وقال المنادي: وأنا بحِمْل البعير من الطعام ضامن وكفيل.');
INSERT INTO Quran__Arabic_Tafseer_Muyassar (QuranChapterID, VerseNumber, Rukuh, Sajdah, Manzil, HizbQuarter, VerseText) VALUES (12, 73, '0', '0', '0', '0', 'قال إخوة يوسف: والله لقد تحققتم مما شاهدتموه منا أننا ما جئنا أرض "مصر" من أجل الإفساد فيها، وليس من صفاتنا أن نكون سارقين.');
INSERT INTO Quran__Arabic_Tafseer_Muyassar (QuranChapterID, VerseNumber, Rukuh, Sajdah, Manzil, HizbQuarter, VerseText) VALUES (12, 74, '0', '0', '0', '0', 'قال المكلَّفون بالبحث عن المكيال لإخوة يوسف: فما عقوبة السارق عندكم إن كنتم كاذبين في قولكم: لسنا بسارقين؟');
INSERT INTO Quran__Arabic_Tafseer_Muyassar (QuranChapterID, VerseNumber, Rukuh, Sajdah, Manzil, HizbQuarter, VerseText) VALUES (12, 75, '0', '0', '0', '0', 'قال إخوة يوسف: جزاء السارق مَن وُجِد المسروق في رحله فهو جزاؤه. أي يسلَّم بسرقته إلى مَن سرق منه حتى يكون عبدًا عنده، مثل هذا الجزاء -وهو الاسترقاق- نجزي الظالمين بالسرقة، وهذا ديننا وسنتنا في أهل السرقة.');
INSERT INTO Quran__Arabic_Tafseer_Muyassar (QuranChapterID, VerseNumber, Rukuh, Sajdah, Manzil, HizbQuarter, VerseText) VALUES (12, 76, '0', '0', '0', '0', 'ورجعوا بإخوة يوسف إليه، فقام بنفسه يفتش أمتعتهم، فبدأ بأمتعتهم قبل متاع شقيقه؛ إحكامًا لما دبَّره لاستبقاء أخيه معه، ثم انتهى بوعاء أخيه، فاستخرج الإناء منه، كذلك يسَّرنا ليوسف هذا التدبير الذي توصَّل به لأخذ أخيه، وما كان له أن يأخذ أخاه في حكم مَلِك "مصر"؛ لأنه ليس من دينه أن يتملك السارق، إلا أن مشيئة الله اقتضت هذا التدبير والاحتكام إلى شريعة إخوة يوسف القاضية برِقِّ السارق. نرفع منازل مَن نشاء في الدنيا على غيره كما رفعنا منزلة يوسف. وفوق كل ذي علمٍ من هو أعلم منه، حتى ينتهي العلم إلى الله تعالى عالم الغيب والشهادة.');
INSERT INTO Quran__Arabic_Tafseer_Muyassar (QuranChapterID, VerseNumber, Rukuh, Sajdah, Manzil, HizbQuarter, VerseText) VALUES (12, 77, '0', '0', '0', '1', 'قال إخوة يوسف: إنْ سرق هذا فقد سرق أخ شقيق له من قبل (يقصدون يوسف عليه السلام) فأخفى يوسف في نفسه ما سمعه، وحدَّث نفسه قائلا أنتم أسوأ منزلة ممن ذكرتم، حيث دبَّرتم لي ما كان منكم، والله أعلم بما تصفون من الكذب والافتراء.');
INSERT INTO Quran__Arabic_Tafseer_Muyassar (QuranChapterID, VerseNumber, Rukuh, Sajdah, Manzil, HizbQuarter, VerseText) VALUES (12, 78, '0', '0', '0', '0', 'قالوا مستعطفين ليوفوا بعهد أبيهم: يا أيها العزيز إن له والدًا كبيرًا في السن يحبه ولا يطيق بُعده، فخُذْ أحدنا بدلا من "بنيامين"، إنا نراك من المحسنين في معاملتك لنا ولغيرنا.');
INSERT INTO Quran__Arabic_Tafseer_Muyassar (QuranChapterID, VerseNumber, Rukuh, Sajdah, Manzil, HizbQuarter, VerseText) VALUES (12, 79, '1', '0', '0', '0', 'قال يوسف: نعتصم بالله ونستجير به أن نأخذ أحدًا غير الذي وجدنا المكيال عنده -كما حكمتم أنتم-، فإننا إن فعلنا ما تطلبون نكون في عداد الظالمين.');
INSERT INTO Quran__Arabic_Tafseer_Muyassar (QuranChapterID, VerseNumber, Rukuh, Sajdah, Manzil, HizbQuarter, VerseText) VALUES (12, 80, '0', '0', '0', '0', 'فلما يئسوا من إجابته إياهم لِمَا طلبوه انفردوا عن الناس، وأخذوا يتشاورون فيما بينهم، قال كبيرهم في السن: ألم تعلموا أن أباكم قد أخذ عليكم العهد المؤكد لتردُّنَّ أخاكم إلا أن تُغلبوا، ومن قبل هذا كان تقصيركم في يوسف وغدركم به؛ لذلك لن أفارق أرض "مصر" حتى يأذن لي أبي في مفارقتها، أو يقضي لي ربي بالخروج منها، وأتمكن مِن أَخْذِ أخي، والله خيرُ مَن حَكَمَ، وأعدل مَن فَصَلَ بين الناس.');
INSERT INTO Quran__Arabic_Tafseer_Muyassar (QuranChapterID, VerseNumber, Rukuh, Sajdah, Manzil, HizbQuarter, VerseText) VALUES (12, 81, '0', '0', '0', '0', 'ارجعوا أنتم إلى أبيكم، وأخبروه بما جرى، وقولوا له: إن ابنك "بنيامين" قد سرق، وما شهدنا بذلك إلا بعد أن تَيَقَّنَّا، فقد رأينا المكيال في رحله، وما كان عندنا علم الغيب أنه سيسرق حين عاهدناك على ردِّه.');
INSERT INTO Quran__Arabic_Tafseer_Muyassar (QuranChapterID, VerseNumber, Rukuh, Sajdah, Manzil, HizbQuarter, VerseText) VALUES (12, 82, '0', '0', '0', '0', 'واسأل -يا أبانا- أهل "مصر"، ومَن كان معنا في القافلة التي كنا فيها، وإننا صادقون فيما أخبرناك به.');
INSERT INTO Quran__Arabic_Tafseer_Muyassar (QuranChapterID, VerseNumber, Rukuh, Sajdah, Manzil, HizbQuarter, VerseText) VALUES (12, 83, '0', '0', '0', '0', 'ولما رجعوا وأخبروا أباهم قال لهم: بل زَيَّنَت لكم أنفسكم الأمَّارة بالسوء مكيدة دبَّرتموها كما فعلتم مِن قبل مع يوسف، فصبري صبر جميل لا جزع فيه ولا شكوى معه، عسى الله أن يردَّ إليَّ أبنائي الثلاثة -وهم يوسف وشقيقه وأخوهم الكبير المتخلف من أجل أخيه- إنه هو العليم بحالي، الحكيم في تدبيره.');
INSERT INTO Quran__Arabic_Tafseer_Muyassar (QuranChapterID, VerseNumber, Rukuh, Sajdah, Manzil, HizbQuarter, VerseText) VALUES (12, 84, '0', '0', '0', '0', 'وأعرض يعقوب عنهم، وقد ضاق صدره بما قالوه، وقال: يا حسرتا على يوسف وابيضَّتْ عيناه، بذهاب سوادهما مِن شدة الحزن فهو ممتلئ القلب حزنًا، ولكنه شديد الكتمان له.');
INSERT INTO Quran__Arabic_Tafseer_Muyassar (QuranChapterID, VerseNumber, Rukuh, Sajdah, Manzil, HizbQuarter, VerseText) VALUES (12, 85, '0', '0', '0', '0', 'قال بنوه: تالله ما تزال تتذكر يوسف، ويشتدُّ حزنك عليه حتى تُشْرِف على الهلاك أو تهلك فعلا فخفف عن نفسك.');
INSERT INTO Quran__Arabic_Tafseer_Muyassar (QuranChapterID, VerseNumber, Rukuh, Sajdah, Manzil, HizbQuarter, VerseText) VALUES (12, 86, '0', '0', '0', '0', 'قال يعقوب مجيبًا لهم: لا أظهر همِّي وحزني إلا لله وحده، فهو كاشف الضرِّ والبلاء، وأعلم من رحمة الله وفرجه ما لا تعلمونه.');
INSERT INTO Quran__Arabic_Tafseer_Muyassar (QuranChapterID, VerseNumber, Rukuh, Sajdah, Manzil, HizbQuarter, VerseText) VALUES (12, 87, '0', '0', '0', '0', 'قال يعقوب: يا أبنائي عودوا إلى "مصر" فاستقصوا أخبار يوسف وأخيه، ولا تقطعوا رجاءكم من رحمة الله، إنه لا يقطع الرجاء من رحمة الله إلا الجاحدون لقدرته، الكافرون به.');
INSERT INTO Quran__Arabic_Tafseer_Muyassar (QuranChapterID, VerseNumber, Rukuh, Sajdah, Manzil, HizbQuarter, VerseText) VALUES (12, 88, '0', '0', '0', '0', 'فذهبوا إلى "مصر"، فلما دخلوا على يوسف قالوا: يا أيها العزيز أصابنا وأهلنا القحط والجدب، وجئناك بثمن رديء قليل، فأعطنا به ما كنت تعطينا من قبل بالثمن الجيد، وتصدَّقْ علينا بقبض هذه الدراهم المزجاة وتجوَّز فيها، إن الله تعالى يثيب المتفضِّلين على أهل الحاجة بأموالهم.');
INSERT INTO Quran__Arabic_Tafseer_Muyassar (QuranChapterID, VerseNumber, Rukuh, Sajdah, Manzil, HizbQuarter, VerseText) VALUES (12, 89, '0', '0', '0', '0', 'فلما سمع مقالتهم رقَّ لهم، وعرَّفهم بنفسه وقال: هل تذكرون الذي فعلتموه بيوسف وأخيه من الأذى في حال جَهْلكم بعاقبة ما تفعلون؟');
INSERT INTO Quran__Arabic_Tafseer_Muyassar (QuranChapterID, VerseNumber, Rukuh, Sajdah, Manzil, HizbQuarter, VerseText) VALUES (12, 90, '0', '0', '0', '0', 'قالوا: أإنَّك لأنت يوسف؟ قال: نعم أنا يوسف، وهذا شقيقي، قد تفضَّل الله علينا، فجمع بيننا بعد الفرقة، إنه من يتق الله، ويصبر على المحن، فإن الله لا يذهب ثواب إحسانه، وإنما يجزيه أحسن الجزاء.');
INSERT INTO Quran__Arabic_Tafseer_Muyassar (QuranChapterID, VerseNumber, Rukuh, Sajdah, Manzil, HizbQuarter, VerseText) VALUES (12, 91, '0', '0', '0', '0', 'قالوا: تالله لقد فَضَّلك الله علينا وأعزَّك بالعلم والحلم والفضل، وإن كنا لخاطئين بما فعلناه عمدًا بك وبأخيك.');
INSERT INTO Quran__Arabic_Tafseer_Muyassar (QuranChapterID, VerseNumber, Rukuh, Sajdah, Manzil, HizbQuarter, VerseText) VALUES (12, 92, '0', '0', '0', '0', 'قال لهم يوسف: لا تأنيب عليكم اليوم، يغفر الله لكم، وهو أرحم الراحمين لمن تاب من ذنبه وأناب إلى طاعته.');
INSERT INTO Quran__Arabic_Tafseer_Muyassar (QuranChapterID, VerseNumber, Rukuh, Sajdah, Manzil, HizbQuarter, VerseText) VALUES (12, 93, '1', '0', '0', '0', 'ولما سألهم عن أبيه أخبروه بذهاب بصره من البكاء عليه، فقال لهم: عودوا إلى أبيكم ومعكم قميصي هذا فاطرحوه على وجه أبي يَعُدْ إليه بصره، ثم أحضروا إليَّ جميع أهلكم.');
INSERT INTO Quran__Arabic_Tafseer_Muyassar (QuranChapterID, VerseNumber, Rukuh, Sajdah, Manzil, HizbQuarter, VerseText) VALUES (12, 94, '0', '0', '0', '0', 'ولما خرجت القافلة من أرض "مصر"، ومعهم القميص قال يعقوب لمن حضره: إني لأجد ريح يوسف لولا أن تسفهوني وتسخروا مني، وتزعموا أن هذا الكلام صدر مني من غير شعور.');
INSERT INTO Quran__Arabic_Tafseer_Muyassar (QuranChapterID, VerseNumber, Rukuh, Sajdah, Manzil, HizbQuarter, VerseText) VALUES (12, 95, '0', '0', '0', '0', 'قال الحاضرون عنده: تالله إنك لا تزال في خطئك القديم مِن حب يوسف، وأنك لا تنساه.');
INSERT INTO Quran__Arabic_Tafseer_Muyassar (QuranChapterID, VerseNumber, Rukuh, Sajdah, Manzil, HizbQuarter, VerseText) VALUES (12, 96, '0', '0', '0', '0', 'فلما أن جاء من يُبشِّر يعقوب بأن يوسف حيٌّ، وطرح قميص يوسف على وجهه فعاد يعقوب مبصرًا، وعمَّه السرور فقال لمن عنده: ألـمْ أخبركم أني أعلم من الله ما لا تعلمونه من فضل الله ورحمته وكرمه؟');
INSERT INTO Quran__Arabic_Tafseer_Muyassar (QuranChapterID, VerseNumber, Rukuh, Sajdah, Manzil, HizbQuarter, VerseText) VALUES (12, 97, '0', '0', '0', '0', 'قال بنوه: يا أبانا سل لنا ربك أن يعفو عنا ويستر علينا ذنوبنا، إنا كنا خاطئين فيما فعلناه بيوسف وشقيقه.');
INSERT INTO Quran__Arabic_Tafseer_Muyassar (QuranChapterID, VerseNumber, Rukuh, Sajdah, Manzil, HizbQuarter, VerseText) VALUES (12, 98, '0', '0', '0', '0', 'قال يعقوب: سوف أسأل ربي أن يغفر لكم ذنوبكم، إنه هو الغفور لذنوب عباده التائبين، الرحيم بهم.');
INSERT INTO Quran__Arabic_Tafseer_Muyassar (QuranChapterID, VerseNumber, Rukuh, Sajdah, Manzil, HizbQuarter, VerseText) VALUES (12, 99, '0', '0', '0', '0', 'وخرج يعقوب وأهله إلى "مصر" قاصدين يوسف، فلما وصلوا إليه ضمَّ يوسف إليه أبويه، وقال لهم: ادخلوا "مصر" بمشيئة الله، وأنتم آمنون من الجهد والقحط، ومن كل مكروه.');
INSERT INTO Quran__Arabic_Tafseer_Muyassar (QuranChapterID, VerseNumber, Rukuh, Sajdah, Manzil, HizbQuarter, VerseText) VALUES (12, 100, '0', '0', '0', '0', 'وأجْلَسَ أباه وأمه على سرير ملكه بجانبه؛ إكرامًا لهما، وحيَّاه أبواه وإخوته الأحد عشر بالسجود له تحية وتكريمًا، لا عبادة وخضوعًا، وكان ذلك جائزًا في شريعتهم، وقد حَرُم في شريعتنا؛ سدًا لذريعة الشرك بالله. وقال يوسف لأبيه: هذا السجود هو تفسير رؤياي التي قصصتها عليك من قبل في صغري، قد جعلها ربي صدقًا، وقد تفضَّل عليَّ حين أخرجني من السجن، وجاء بكم إليَّ من البادية، من بعد أن أفسد الشيطان رابطة الأخوة بيني وبين إخوتي. إن ربي لطيف التدبير لما يشاء، إنه هو العليم بمصالح عباده، الحكيم في أقواله وأفعاله.');
INSERT INTO Quran__Arabic_Tafseer_Muyassar (QuranChapterID, VerseNumber, Rukuh, Sajdah, Manzil, HizbQuarter, VerseText) VALUES (12, 101, '0', '0', '0', '1', 'ثم دعا يوسف ربه قائلا ربِّ قد أعطيتني من ملك "مصر"، وعلَّمتني من تفسير الرؤى وغير ذلك من العلم، يا خالق السموات والأرض ومبدعهما، أنت متولي جميع شأني في الدنيا والآخرة، توفني إليك مسلمًا، وألحقني بعبادك الصالحين من الأنبياء الأبرار والأصفياء الأخيار.');
INSERT INTO Quran__Arabic_Tafseer_Muyassar (QuranChapterID, VerseNumber, Rukuh, Sajdah, Manzil, HizbQuarter, VerseText) VALUES (12, 102, '0', '0', '0', '0', 'ذلك المذكور من قصة يوسف هو من أخبار الغيب نخبرك به -أيها الرسول- وحيًا، وما كنت حاضرًا مع إخوة يوسف حين دبَّروا له الإلقاء في البئر، واحتالوا عليه وعلى أبيه. وهذا يدل على صدقك، وأن الله يُوحِي إليك.');
INSERT INTO Quran__Arabic_Tafseer_Muyassar (QuranChapterID, VerseNumber, Rukuh, Sajdah, Manzil, HizbQuarter, VerseText) VALUES (12, 103, '0', '0', '0', '0', 'وما أكثرُ المشركين من قومك -أيها الرسول- بمصدِّقيك ولا متبعيك، ولو حَرَصْتَ على إيمانهم، فلا تحزن على ذلك.');
INSERT INTO Quran__Arabic_Tafseer_Muyassar (QuranChapterID, VerseNumber, Rukuh, Sajdah, Manzil, HizbQuarter, VerseText) VALUES (12, 104, '1', '0', '0', '0', 'وما تطلب من قومك أجرة على إرشادهم للإيمان، إن الذي أُرسلتَ به من القرآن والهدى عظة للناس أجمعين يتذكرون به ويهتدون.');
INSERT INTO Quran__Arabic_Tafseer_Muyassar (QuranChapterID, VerseNumber, Rukuh, Sajdah, Manzil, HizbQuarter, VerseText) VALUES (12, 105, '0', '0', '0', '0', 'وكثير من الدلائل الدالة على وحدانية الله وقدرته منتشرة في السموات والأرض، كالشمس والقمر والجبال والأشجار، يشاهدونها وهم عنها معرضون، لا يفكرون فيها ولا يعتبرون.');
INSERT INTO Quran__Arabic_Tafseer_Muyassar (QuranChapterID, VerseNumber, Rukuh, Sajdah, Manzil, HizbQuarter, VerseText) VALUES (12, 106, '0', '0', '0', '0', 'وما يُقِرُّ هؤلاء المعرضون عن آيات الله بأن الله خالقهم ورازقهم وخالق كل شيء ومستحق للعبادة وحده إلا وهم مشركون في عبادتهم الأوثان والأصنام. تعالى الله عن ذلك علوًّا كبيرا.');
INSERT INTO Quran__Arabic_Tafseer_Muyassar (QuranChapterID, VerseNumber, Rukuh, Sajdah, Manzil, HizbQuarter, VerseText) VALUES (12, 107, '0', '0', '0', '0', 'فهل عندهم ما يجعلهم آمنين أن ينزل بهم عذاب من الله يعُمُّهم، أو أن تأتيهم القيامة فجأة، وهم لا يشعرون ولا يُحِسُّون بذلك.');
INSERT INTO Quran__Arabic_Tafseer_Muyassar (QuranChapterID, VerseNumber, Rukuh, Sajdah, Manzil, HizbQuarter, VerseText) VALUES (12, 108, '0', '0', '0', '0', 'قل لهم -أيها الرسول-: هذه طريقتي، أدعو إلى عبادة الله وحده، على حجة من الله ويقين، أنا ومن اقتدى بي، وأنزِّه الله سبحانه وتعالى عن الشركاء، ولستُ من المشركين مع الله غيره.');
INSERT INTO Quran__Arabic_Tafseer_Muyassar (QuranChapterID, VerseNumber, Rukuh, Sajdah, Manzil, HizbQuarter, VerseText) VALUES (12, 109, '0', '0', '0', '0', 'وما أرسلنا من قبلك -أيها الرسول- للناس إلا رجالا منهم ننزل عليهم وحينا، وهم من أهل الحاضرة، فهم أقدر على فهم الدعوة والرسالة، يصدقهم المهتدون للحق، ويكذبهم الضالون عنه، أفلم يمشوا في الأرض، فيعاينوا كيف كان مآل المكذبين السابقين وما حلَّ بهم من الهلاك؟ ولَثواب الدار الآخرة أفضل من الدنيا وما فيها للذين آمنوا وخافوا ربهم. أفلا تتفكرون فتعتبروا؟');
INSERT INTO Quran__Arabic_Tafseer_Muyassar (QuranChapterID, VerseNumber, Rukuh, Sajdah, Manzil, HizbQuarter, VerseText) VALUES (12, 110, '0', '0', '0', '0', 'ولا تستعجل -أيها الرسول- النصر على مكذبيك، فإن الرسل قبلك ما كان يأتيهم النصر عاجلا لحكمة نعلمها، حتى إذا يئس الرسل من قومهم، وأيقنوا أن قومهم قد كذبوهم ولا أمل في إيمانهم، جاءهم نصرنا عند شدة الكرب، فننجي من نشاء من الرسل وأتباعهم، ولا يُرَدُّ عذابنا عمَّن أجرم وتجرَّأ على الله. وفي هذا تسلية للنبي صلى الله عليه وسلم.');
INSERT INTO Quran__Arabic_Tafseer_Muyassar (QuranChapterID, VerseNumber, Rukuh, Sajdah, Manzil, HizbQuarter, VerseText) VALUES (12, 111, '1', '0', '0', '0', 'لقد كان في نبأ المرسلين الذي قصصناه عليك وما حلَّ بالمكذبين عظة لأهل العقول السليمة. ما كان هذا القرآن حديثًا مكذوبًا مختلَقًا، ولكن أنزلناه مصدقًا لما سبقه من الكتب السماوية، وبيانًا لكل ما يحتاج إليه العباد من تحليل وتحريم، ومحبوب ومكروه وغير ذلك، وإرشادًا من الضلال، ورحمة لأهل الإيمان تهتدي به قلوبهم، فيعملون بما فيه من الأوامر والنواهي.');
INSERT INTO Quran__Arabic_Tafseer_Muyassar (QuranChapterID, VerseNumber, Rukuh, Sajdah, Manzil, HizbQuarter, VerseText) VALUES (13, 1, '0', '0', '0', '0', '(المر) سبق الكلام على الحروف المقطَّعة في أول سورة البقرة.');
INSERT INTO Quran__Arabic_Tafseer_Muyassar (QuranChapterID, VerseNumber, Rukuh, Sajdah, Manzil, HizbQuarter, VerseText) VALUES (13, 2, '0', '0', '0', '0', 'الله تعالى هو الذي رفع السموات السبع بقدرته من غير عمد كما ترونها، ثم استوى -أي علا وارتفع- على العرش استواء يليق بجلاله وعظمته، وذلَّل الشمس والقمر لمنافع العباد، كلٌّ منهما يدور في فلكه إلى يوم القيامة. يدبِّر سبحانه أمور الدنيا والآخرة، يوضح لكم الآيات الدالة على قدرته وأنه لا إله إلا هو؛ لتوقنوا بالله والمعاد إليه، فتصدقوا بوعده ووعيده وتُخْلصوا العبادة له وحده.');
INSERT INTO Quran__Arabic_Tafseer_Muyassar (QuranChapterID, VerseNumber, Rukuh, Sajdah, Manzil, HizbQuarter, VerseText) VALUES (13, 3, '0', '0', '0', '0', 'وهو سبحانه الذي جعل الأرض متسعة ممتدة، وهيأها لمعاشكم، وجعل فيها جبالا تُثبِّتُها وأنهارًا لشربكم ومنافعكم، وجعل فيها من كل الثمرات صنفين اثنين، فكان منها الأبيض والأسود والحلو والحامض، وجعل الليل يغطي النهار بظلمته، إن في ذلك كله لَعظات لقوم يتفكرون فيها، فيتعظون.');
INSERT INTO Quran__Arabic_Tafseer_Muyassar (QuranChapterID, VerseNumber, Rukuh, Sajdah, Manzil, HizbQuarter, VerseText) VALUES (13, 4, '0', '0', '0', '0', 'وفي الأرض قطع يجاور بعضها بعضًا، منها ما هو طيِّب يُنبتُ ما ينفع الناس، ومنها سَبِخة مِلْحة لا تُنبت شيئًا، وفي الأرض الطيبة بساتين من أعناب، وجعل فيها زروعًا مختلفة ونخيلا مجتمعًا في منبت واحد، وغير مجتمع فيه، كل ذلك في تربة واحدة، ويشرب من ماء واحد، ولكنه يختلف في الثمار والحجم والطعم وغير ذلك، فهذا حلو وهذا حامض، وبعضها أفضل من بعض في الأكل، إن في ذلك لَعلامات لمن كان له قلب يعقل عن الله تعالى أمره ونهيه.');
INSERT INTO Quran__Arabic_Tafseer_Muyassar (QuranChapterID, VerseNumber, Rukuh, Sajdah, Manzil, HizbQuarter, VerseText) VALUES (13, 5, '0', '0', '0', '1', 'وإن تعجب -أيها الرسول- من عدم إيمانهم بعد هذه الأدلة فالعجب الأشدُّ من قول الكفار: أإذا متنا وكنا ترابا نُبعث من جديد؟ أولئك هم الجاحدون بربهم الذي أوجدهم من العدم، وأولئك تكون السلاسل من النار في أعناقهم يوم القيامة، وأولئك يدخلون النار، ولا يخرجون منها أبدًا.');
INSERT INTO Quran__Arabic_Tafseer_Muyassar (QuranChapterID, VerseNumber, Rukuh, Sajdah, Manzil, HizbQuarter, VerseText) VALUES (13, 6, '0', '0', '0', '0', 'ويستعجلك المكذِّبون بالعقوبة التي لم أعاجلهم بها قبل الإيمان الذي يرجى به الأمان والحسنات، وقد مضت عقوبات المكذبين مِن قبلهم، فكيف لا يعتبرون بهم؟ وإن ربك -أيها الرسول- لَذو مغفرة لذنوبِ مَن تاب مِن ذنوبه من الناس على ظلمهم، يفتح لهم باب المغفرة، ويدعوهم إليها، وهم يظلمون أنفسهم بعصيانهم ربهم، وإن ربك لشديد العقاب على مَن أصرَّ على الكفر والضلال ومعصية الله.');
INSERT INTO Quran__Arabic_Tafseer_Muyassar (QuranChapterID, VerseNumber, Rukuh, Sajdah, Manzil, HizbQuarter, VerseText) VALUES (13, 7, '1', '0', '0', '0', 'ويقول كفار "مكة": هلا جاءته معجزة محسوسة كعصا موسى وناقة صالح، وليس ذلك بيدك -أيها الرسول- فما أنت إلا مبلِّغ لهم، ومخوِّف مِن بأس الله. ولكل أمة رسول يرشدهم إلى الله تعالى.');
INSERT INTO Quran__Arabic_Tafseer_Muyassar (QuranChapterID, VerseNumber, Rukuh, Sajdah, Manzil, HizbQuarter, VerseText) VALUES (13, 8, '0', '0', '0', '0', 'الله تعالى يعلم ما تحمل كلُّ أنثى في بطنها، أذكر هو أم أنثى؟ وشقي هو أم سعيد؟ ويعلم ما تنقصه الأرحام، فيسقط أو يولد قبل تسعة أشهر، وما يزيد حمله عليها. وكل شيء مقدَّر عند الله بمقدار من النقصان أو الزيادة لا يتجاوزه.');
INSERT INTO Quran__Arabic_Tafseer_Muyassar (QuranChapterID, VerseNumber, Rukuh, Sajdah, Manzil, HizbQuarter, VerseText) VALUES (13, 9, '0', '0', '0', '0', 'الله عالم بما خفي عن الأبصار، وبما هو مشاهَد، الكبير في ذاته وأسمائه وصفاته، المتعال على جميع خلقه بذاته وقدرته وقهره.');
INSERT INTO Quran__Arabic_Tafseer_Muyassar (QuranChapterID, VerseNumber, Rukuh, Sajdah, Manzil, HizbQuarter, VerseText) VALUES (13, 10, '0', '0', '0', '0', 'يستوي في علمه تعالى مَن أخفى القول منكم ومَن جهر به، ويستوي عنده مَن استتر بأعماله في ظلمة الليل، ومن جهر بها في وضح النهار.');
INSERT INTO Quran__Arabic_Tafseer_Muyassar (QuranChapterID, VerseNumber, Rukuh, Sajdah, Manzil, HizbQuarter, VerseText) VALUES (13, 11, '0', '0', '0', '0', 'لله تعالى ملائكة يتعاقبون على الإنسان من بين يديه ومن خلفه، يحفظونه بأمر الله ويحصون ما يصدر عنه من خير أو شر. إن الله سبحانه وتعالى لا يغيِّر نعمة أنعمها على قوم إلا إذا غيَّروا ما أمرهم به فعصوه. وإذا أراد الله بجماعةٍ بلاءً فلا مفرَّ منه، وليس لهم مِن دون الله مِن وال يتولى أمورهم، فيجلب لهم المحبوب، ويدفع عنهم المكروه.');
INSERT INTO Quran__Arabic_Tafseer_Muyassar (QuranChapterID, VerseNumber, Rukuh, Sajdah, Manzil, HizbQuarter, VerseText) VALUES (13, 12, '0', '0', '0', '0', 'هو الذي يريكم من آياته البرق -وهو النور اللامع من خلال السحاب- فتخافون أن تنزل عليكم منه الصواعق المحرقة، وتطمعون أن ينزل معه المطر، وبقدرته سبحانه يوجد السحاب المحمَّل بالماء الكثير لمنافعكم.');
INSERT INTO Quran__Arabic_Tafseer_Muyassar (QuranChapterID, VerseNumber, Rukuh, Sajdah, Manzil, HizbQuarter, VerseText) VALUES (13, 13, '0', '0', '0', '0', 'ويسبِّح الرعد بحمد الله تسبيحًا يدل على خضوعه لربه، وتنزِّه الملائكة ربها مِن خوفها من الله، ويرسل الله الصواعق المهلكة فيهلك بها مَن يشاء من خلقه، والكفار يجادلون في وحدانية الله وقدرته على البعث، وهو شديد الحول والقوة والبطش بمن عصاه.');
INSERT INTO Quran__Arabic_Tafseer_Muyassar (QuranChapterID, VerseNumber, Rukuh, Sajdah, Manzil, HizbQuarter, VerseText) VALUES (13, 14, '0', '0', '0', '0', 'لله سبحانه وتعالى وحده دعوة التوحيد (لا إله إلا الله)، فلا يُعبد ولا يُدعى إلا هو، والآلهة التي يعبدونها من دون الله لا تجيب دعاء مَن دعاها، وحالهم معها كحال عطشان يمد يده إلى الماء من بعيد؛ ليصل إلى فمه فلا يصل إليه، وما سؤال الكافرين لها إلا غاية في البعد عن الصواب لإشراكهم بالله غيره.');
INSERT INTO Quran__Arabic_Tafseer_Muyassar (QuranChapterID, VerseNumber, Rukuh, Sajdah, Manzil, HizbQuarter, VerseText) VALUES (13, 15, '0', '1', '0', '0', 'ولله وحده يسجد خاضعًا منقادًا كُلُّ مَن في السموات والأرض، فيسجد ويخضع له المؤمنون طوعًا واختيارًا، ويخضع له الكافرون رغمًا عنهم؛ لأنهم يستكبرون عن عبادته، وحالهم وفطرتهم تكذِّبهم في ذلك، وتنقاد لعظمته ظلال المخلوقات، فتتحرك بإرادته أول النهار وآخره.');
INSERT INTO Quran__Arabic_Tafseer_Muyassar (QuranChapterID, VerseNumber, Rukuh, Sajdah, Manzil, HizbQuarter, VerseText) VALUES (13, 16, '0', '0', '0', '0', 'قل -أيها الرسول- للمشركين: مَن خالق السَّموات والأرض ومدبِّرهما؟ قل: الله هو الخالق المدبر لهما، وأنتم تقرون بذلك، ثم قل لهم ملزمًا بالحجة: أجعلتم غيره معبودين لكم، وهم لا يَقْدرون على نفع أنفسهم أو ضرها فضلا عن نفعكم أو ضركم، وتركتم عبادة مالكها؟ قل لهم -أيها الرسول-: هل يستوي عندكم الكافر -وهو كالأعمى- والمؤمن وهو كالبصير؟ أم هل يستوي عندكم الكفر -وهو كالظلمات- والإيمان -وهو كالنور؟ أم أن أولياءهم الذين جعلوهم شركاء لله يخلقون مثل خَلْقه، فتشابه عليهم خَلْق الشركاء بخلق الله، فاعتقدوا استحقاقهم للعبادة؟ قل لهم -أيها الرسول-: الله تعالى خالق كل كائن من العدم، وهو المستحق للعبادة وحده، وهو الواحد القهار الذي يستحق الألوهية والعبادة، لا الأصنام والأوثان التي لا تضرُّ ولا تنفع.');
INSERT INTO Quran__Arabic_Tafseer_Muyassar (QuranChapterID, VerseNumber, Rukuh, Sajdah, Manzil, HizbQuarter, VerseText) VALUES (13, 17, '0', '0', '0', '0', 'ثم ضرب الله سبحانه مثلا للحق والباطل بماء أنزله من السماء، فجَرَت به أودية الأرض بقدر صغرها وكبرها، فحمل السيل غثاء طافيًا فوقه لا نفع فيه. وضرب مثلا آخر: هو المعادن يوقِدون عليها النار لصهرها طلبًا للزينة كما في الذهب والفضة، أو طلبًا لمنافع ينتفعون بها كما في النحاس، فيخرج منها خبثها مما لا فائدة فيه كالذي كان مع الماء، بمثل هذا يضرب الله المثل للحق والباطل: فالباطل كغثاء الماء يتلاشى أو يُرْمى إذ لا فائدة منه، والحق كالماء الصافي، والمعادن النقية تبقى في الأرض للانتفاع بها، كما بيَّن لكم هذه الأمثال، كذلك يضربها للناس؛ ليتضح الحق من الباطل والهدى من الضلال.');
INSERT INTO Quran__Arabic_Tafseer_Muyassar (QuranChapterID, VerseNumber, Rukuh, Sajdah, Manzil, HizbQuarter, VerseText) VALUES (13, 18, '1', '0', '0', '0', 'للمؤمنين الذين أطاعوا الله ورسوله الجنة، والذين لم يطيعوا وكفروا به لهم النار، ولو كانوا يملكون كل ما في الأرض وضِعْفه معه لبذلوه فداء لأنفسهم من عذاب الله يوم القيامة، ولن يُتَقبل منهم، أولئك يحاسَبون على كل ما أسلفوه من عمل سيِّئ، ومسكنهم ومقامهم جهنم تكون لهم فراشًا، وبئس الفراش الذي مهدوه لأنفسهم.');
INSERT INTO Quran__Arabic_Tafseer_Muyassar (QuranChapterID, VerseNumber, Rukuh, Sajdah, Manzil, HizbQuarter, VerseText) VALUES (13, 19, '0', '0', '0', '1', 'هل الذي يعلم أن ما جاءك -أيها الرسول- من عند الله هو الحق فيؤمن به، كالأعمى عن الحق الذي لم يؤمن؟ إنما يتعظ أصحاب العقول السليمة الذين يوفون بعهد الله الذي أمرهم به، ولا ينكثون العهد المؤكد الذي عاهدوا الله عليه.');
INSERT INTO Quran__Arabic_Tafseer_Muyassar (QuranChapterID, VerseNumber, Rukuh, Sajdah, Manzil, HizbQuarter, VerseText) VALUES (13, 20, '0', '0', '0', '0', 'هل الذي يعلم أن ما جاءك -أيها الرسول- من عند الله هو الحق فيؤمن به، كالأعمى عن الحق الذي لم يؤمن؟ إنما يتعظ أصحاب العقول السليمة الذين يوفون بعهد الله الذي أمرهم به، ولا ينكثون العهد المؤكد الذي عاهدوا الله عليه.');
INSERT INTO Quran__Arabic_Tafseer_Muyassar (QuranChapterID, VerseNumber, Rukuh, Sajdah, Manzil, HizbQuarter, VerseText) VALUES (13, 21, '0', '0', '0', '0', 'وهم الذين يَصِلون ما أمرهم الله بوصله كالأرحام والمحتاجين، ويراقبون ربهم، ويخشون أن يحاسبهم على كل ذنوبهم، ولا يغفر لهم منها شيئًا.');
INSERT INTO Quran__Arabic_Tafseer_Muyassar (QuranChapterID, VerseNumber, Rukuh, Sajdah, Manzil, HizbQuarter, VerseText) VALUES (13, 22, '0', '0', '0', '0', 'وهم الذين صبروا على الأذى وعلى الطاعة، وعن المعصية طلبًا لرضا ربهم، وأدَّوا الصلاة على أتمِّ وجوهها، وأدَّوا من أموالهم زكاتهم المفروضة، والنفقات المستحبة في الخفاء والعلن، ويدفعون بالحسنة السيئة فتمحوها، أولئك الموصوفون بهذه الصفات لهم العاقبة المحمودة في الآخرة.');
INSERT INTO Quran__Arabic_Tafseer_Muyassar (QuranChapterID, VerseNumber, Rukuh, Sajdah, Manzil, HizbQuarter, VerseText) VALUES (13, 23, '0', '0', '0', '0', 'تلك العاقبة هي جنات عدن يقيمون فيها لا يزولون عنها، ومعهم الصالحون من الآباء والزوجات والذريات من الذكور والإنات، وتدخل الملائكة عليهم من كل باب؛ لتهنئتهم بدخول الجنة.');
INSERT INTO Quran__Arabic_Tafseer_Muyassar (QuranChapterID, VerseNumber, Rukuh, Sajdah, Manzil, HizbQuarter, VerseText) VALUES (13, 24, '0', '0', '0', '0', 'تقول الملائكة لهم: سَلِمْتم من كل سوء بسبب صبركم على طاعة الله، فنِعْمَ عاقبة الدار الجنة.');
INSERT INTO Quran__Arabic_Tafseer_Muyassar (QuranChapterID, VerseNumber, Rukuh, Sajdah, Manzil, HizbQuarter, VerseText) VALUES (13, 25, '0', '0', '0', '0', 'أما الأشقياء فقد وُصِفوا بضد صفات المؤمنين، فهم الذين لا يوفون بعهد الله بإفراده سبحانه بالعبادة بعد أن أكدوه على أنفسهم، وهم الذين يقطعون ما أمرهم الله بوصله مِن صلة الأرحام وغيرها، ويفسدون في الأرض بعمل المعاصي، أولئك الموصوفون بهذه الصفات القبيحة لهم الطرد من رحمة الله، ولهم ما يسوءهم من العذاب الشديد في الدار الآخرة.');
INSERT INTO Quran__Arabic_Tafseer_Muyassar (QuranChapterID, VerseNumber, Rukuh, Sajdah, Manzil, HizbQuarter, VerseText) VALUES (13, 26, '1', '0', '0', '0', 'الله وحده يوسِّع الرزق لمن يشاء من عباده، ويضيِّق على مَن يشاء منهم، وفرح الكفار بالسَّعة في الحياة الدنيا، وما هذه الحياة الدنيا بالنسبة للآخرة إلا شيء قليل يتمتع به، سُرعان ما يزول.');
INSERT INTO Quran__Arabic_Tafseer_Muyassar (QuranChapterID, VerseNumber, Rukuh, Sajdah, Manzil, HizbQuarter, VerseText) VALUES (13, 27, '0', '0', '0', '0', 'ويقول الكفار عنادًا: هلا أُنزل على محمد معجزة محسوسة كمعجزة موسى وعيسى. قل لهم: إن الله يضل مَن يشاء من المعاندين عن الهداية ولا تنفعه المعجزات، ويهدي إلى دينه الحق مَن رجع إليه وطلب رضوانه.');
INSERT INTO Quran__Arabic_Tafseer_Muyassar (QuranChapterID, VerseNumber, Rukuh, Sajdah, Manzil, HizbQuarter, VerseText) VALUES (13, 28, '0', '0', '0', '0', 'ويهدي الذين تسكن قلوبهم بتوحيد الله وذكره فتطمئن، ألا بطاعة الله وذكره وثوابه تسكن القلوب وتستأنس.');
INSERT INTO Quran__Arabic_Tafseer_Muyassar (QuranChapterID, VerseNumber, Rukuh, Sajdah, Manzil, HizbQuarter, VerseText) VALUES (13, 29, '0', '0', '0', '0', 'الذين صدَّقوا بالله ورسوله، وعملوا الأعمال الصالحات لهم فرح وقرة عين، وحال طيبة، ومرجع حسن إلى جنة الله ورضوانه.');
INSERT INTO Quran__Arabic_Tafseer_Muyassar (QuranChapterID, VerseNumber, Rukuh, Sajdah, Manzil, HizbQuarter, VerseText) VALUES (13, 30, '0', '0', '0', '0', 'كما أرسلنا المرسلين قبلك أرسلناك -أيها الرسول- في أمة قد مضت مِن قبلها أمم المرسلين؛ لتتلو على هذه الأمة القرآن المنزل عليك، وحال قومك الجحود بوحدانية الرحمن، قل لهم -أيها الرسول-: الرحمن الذي لم تتخذوه إلهًا واحدًا هو ربي وحده لا معبود بحق سواه، عليه اعتمدت ووثقت، وإليه مرجعي وإنابتي.');
INSERT INTO Quran__Arabic_Tafseer_Muyassar (QuranChapterID, VerseNumber, Rukuh, Sajdah, Manzil, HizbQuarter, VerseText) VALUES (13, 31, '1', '0', '0', '0', 'يردُّ الله -تعالى- على الكافرين الذين طلبوا إنزال معجزات محسوسة على النبي صلى الله عليه وسلم فيقول لهم: ولو أن ثمة قرآنًا يقرأ، فتزول به الجبال عن أماكنها، أو تتشقق به الأرض أنهارًا، أو يحيا به الموتى وتُكَلَّم -كما طلبوا منك- لكان هذا القرآن هو المتصف بذلك دون غيره، ولما آمنوا به. بل لله وحده الأمر كله في المعجزات وغيرها. أفلم يعلم المؤمنون أن الله لو يشاء لآمن أهل الأرض كلهم من غير معجزة؟ ولا يزال الكفار تنزل بهم مصيبة بسبب كفرهم كالقتل والأسر في غزوات المسلمين، أو تنزل تلك المصيبة قريبًا من دارهم، حتى يأتي وعد الله بالنصر عليهم، إن الله لا يخلف الميعاد.');
INSERT INTO Quran__Arabic_Tafseer_Muyassar (QuranChapterID, VerseNumber, Rukuh, Sajdah, Manzil, HizbQuarter, VerseText) VALUES (13, 32, '0', '0', '0', '0', 'وإذا كانوا قد سخروا من دعوتك -أيها الرسول- فلقد سَخِرَتْ أمم من قبلك برسلهم، فلا تحزن فقد أمهلتُ الذين كفروا، ثم أخذتُهم بعقابي، وكان عقابًا شديدًا.');
INSERT INTO Quran__Arabic_Tafseer_Muyassar (QuranChapterID, VerseNumber, Rukuh, Sajdah, Manzil, HizbQuarter, VerseText) VALUES (13, 33, '0', '0', '0', '0', 'أفمن هو قائم على كل نفس يُحصي عليها ما تعمل، أحق أن يعبد، أم هذه المخلوقات العاجزة؟ وهم -من جهلهم- جعلوا لله شركاء مِن خَلْقه يعبدونهم، قل لهم -أيها الرسول-: اذكروا أسماءهم وصفاتهم، ولن يجدوا من صفاتهم ما يجعلهم أهلا للعبادة، أم تخبرون الله بشركاء في أرضه لا يعلمهم، أم تسمونهم شركاء بظاهر من اللفظ من غير أن يكون لهم حقيقة. بل حسَّن الشيطان للكفار قولهم الباطل وصدَّهم عن سبيل الله. ومَن لم يوفِّقه الله لهدايته فليس له أحد يهديه، ويوفقه إلى الحق والرشاد.');
INSERT INTO Quran__Arabic_Tafseer_Muyassar (QuranChapterID, VerseNumber, Rukuh, Sajdah, Manzil, HizbQuarter, VerseText) VALUES (13, 34, '0', '0', '0', '0', 'لهؤلاء الكفار الصادين عن سبيل الله عذاب شاق في الحياة الدنيا بالقتل والأسر والخزي، ولَعذابهم في الآخرة أثقل وأشد، وليس لهم مانع يمنعهم من عذاب الله.');
INSERT INTO Quran__Arabic_Tafseer_Muyassar (QuranChapterID, VerseNumber, Rukuh, Sajdah, Manzil, HizbQuarter, VerseText) VALUES (13, 35, '0', '0', '0', '1', 'صفة الجنة التي وعد الله بها الذين يخشونه أنها تجري من تحت أشجارها وقصورها الأنهار، ثمرها لا ينقطع، وظلها لا يزول ولا ينقص، تلك المثوبة بالجنة عاقبة الذين خافوا الله، فاجتنبوا معاصيه وأدَّوا فرائضه، وعاقبة الكافرين بالله النار.');
INSERT INTO Quran__Arabic_Tafseer_Muyassar (QuranChapterID, VerseNumber, Rukuh, Sajdah, Manzil, HizbQuarter, VerseText) VALUES (13, 36, '0', '0', '0', '0', 'والذين أعطيناهم الكتاب من اليهود والنصارى مَن آمن منهم بك كعبد الله بن سلام والنجاشي، يستبشرون بالقرآن المنزل عليك لموافقته ما عندهم، ومن المتحزبين على الكفر ضدك، كالسَّيد والعاقب، أُسْقفَي "نجران"، وكعب بن الأشرف، مَن ينكر بعض المنزل عليك، قل لهم: إنما أمرني الله أن أعبده وحده، ولا أشرك به شيئًا، إلى عبادته أدعو الناس، وإليه مرجعي ومآبي.');
INSERT INTO Quran__Arabic_Tafseer_Muyassar (QuranChapterID, VerseNumber, Rukuh, Sajdah, Manzil, HizbQuarter, VerseText) VALUES (13, 37, '1', '0', '0', '0', 'وكما أنزلنا الكتب على الأنبياء بلسانهم أنزلنا إليك -أيها الرسول- القرآن بلغة العرب؛ لتحكم به، ولئن اتبعت أهواء المشركين في عبادة غير الله -بعد الحق الذي جاءك من الله- ليس لك ناصر ينصرك ويمنعك من عذابه.');
INSERT INTO Quran__Arabic_Tafseer_Muyassar (QuranChapterID, VerseNumber, Rukuh, Sajdah, Manzil, HizbQuarter, VerseText) VALUES (13, 38, '0', '0', '0', '0', 'وإذا قالوا: ما لك -أيها الرسول- تتزوج النساء؟ فلقد بعثنا قبلك رسلا من البشر وجعلنا لهم أزواجًا وذرية، وإذا قالوا: لو كان رسولا لأتى بما طلبنا من المعجزات، فليس في وُسْع رسولٍ أن يأتي بمعجزةٍ أرادها قومه إلا بإذن الله. لكل أمر قضاه الله كتاب وأجل قد كتبه الله عنده، لا يتقدم ولا يتأخر.');
INSERT INTO Quran__Arabic_Tafseer_Muyassar (QuranChapterID, VerseNumber, Rukuh, Sajdah, Manzil, HizbQuarter, VerseText) VALUES (13, 39, '0', '0', '0', '0', 'يمحو الله ما يشاء من الأحكام وغيرها، ويُبْقي ما يشاء منها لحكمة يعلمها، وعنده أمُّ الكتاب، وهو اللوح المحفوظ.');
INSERT INTO Quran__Arabic_Tafseer_Muyassar (QuranChapterID, VerseNumber, Rukuh, Sajdah, Manzil, HizbQuarter, VerseText) VALUES (13, 40, '0', '0', '0', '0', 'وإن أريناك -أيها الرسول- بعض العقاب الذي توعَّدْنا به أعداءك من الخزي والنَّكال في الدنيا فذلك المعجَّل لهم، وإن توفيناك قبل أن ترى ذلك، فما عليك إلا تبليغ الدعوة، وعلينا الحساب والجزاء.');
INSERT INTO Quran__Arabic_Tafseer_Muyassar (QuranChapterID, VerseNumber, Rukuh, Sajdah, Manzil, HizbQuarter, VerseText) VALUES (13, 41, '0', '0', '0', '0', 'أولم يبصر هؤلاء الكفار أنا نأتي الأرض ننقصها من أطرافها، وذلك بفتح المسلمين بلاد المشركين وإلحاقها ببلاد المسلمين؟ والله سبحانه يحكم لا معقِّب لحكمه وقضائه، وهو سريع الحساب، فلا يستعجلوا بالعذاب؛ فإن كل آت قريب.');
INSERT INTO Quran__Arabic_Tafseer_Muyassar (QuranChapterID, VerseNumber, Rukuh, Sajdah, Manzil, HizbQuarter, VerseText) VALUES (13, 42, '0', '0', '0', '0', 'ولقد دبَّر الذين من قبلهم المكايد لرسلهم، كما فعل هؤلاء معك، فلله المكر جميعًا، فيبطل مكرهم، ويعيده عليهم بالخيبة والندم، يعلم سبحانه ما تكسب كل نفس من خير أو شر فتجازى عليه. وسيعلم الكفار -إذا قدموا على ربهم- لمن تكون العاقبة المحمودة بعد هذه الدنيا؟ إنها لأتباع الرسل. وفي هذا تهديد ووعيد للكافرين.');
INSERT INTO Quran__Arabic_Tafseer_Muyassar (QuranChapterID, VerseNumber, Rukuh, Sajdah, Manzil, HizbQuarter, VerseText) VALUES (13, 43, '1', '0', '0', '0', 'ويقول الذين كفروا لنبي الله: -يا محمد- ما أرسلك الله، قل لهم: كفى بالله شهيدًا بصدقي وكذبكم، وكَفَتْ شهادة مَن عنده علم الكتاب من اليهود والنصارى ممن آمن برسالتي، وما جئتُ به من عند الله، واتبع الحق فصرَّح بتلك الشهادة، ولم يكتمها.');
INSERT INTO Quran__Arabic_Tafseer_Muyassar (QuranChapterID, VerseNumber, Rukuh, Sajdah, Manzil, HizbQuarter, VerseText) VALUES (14, 1, '0', '0', '0', '0', '(الر) سبق الكلام على الحروف المقطَّعة في أول سورة البقرة.');
INSERT INTO Quran__Arabic_Tafseer_Muyassar (QuranChapterID, VerseNumber, Rukuh, Sajdah, Manzil, HizbQuarter, VerseText) VALUES (14, 2, '0', '0', '0', '0', '(الر) سبق الكلام على الحروف المقطَّعة في أول سورة البقرة.');
INSERT INTO Quran__Arabic_Tafseer_Muyassar (QuranChapterID, VerseNumber, Rukuh, Sajdah, Manzil, HizbQuarter, VerseText) VALUES (14, 3, '0', '0', '0', '0', 'وهؤلاء الذين أعرضوا ولم يؤمنوا بالله ويتبعوا رسله هم الذين يختارون الحياة الدنيا الفانية، ويتركون الآخرة الباقية، ويمنعون الناس عن اتباع دين الله، ويريدونه طريقًا معوجًا ليوافق أهواءهم، أولئك الموصوفون بهذه الصفات في ضلال عن الحق بعيد عن كل أسباب الهداية.');
INSERT INTO Quran__Arabic_Tafseer_Muyassar (QuranChapterID, VerseNumber, Rukuh, Sajdah, Manzil, HizbQuarter, VerseText) VALUES (14, 4, '0', '0', '0', '0', 'وما أرسلنا مِن رسولٍ قبلك -أيها النبي- إلا بلُغة قومه؛ ليوضِّح لهم شريعة الله، فيضل الله من يشاء عن الهدى، ويهدي من يشاء إلى الحق، وهو العزيز في ملكه، الحكيم الذي يضع الأمور في مواضعها وَفْق الحكمة.');
INSERT INTO Quran__Arabic_Tafseer_Muyassar (QuranChapterID, VerseNumber, Rukuh, Sajdah, Manzil, HizbQuarter, VerseText) VALUES (14, 5, '0', '0', '0', '0', 'ولقد أرسلنا موسى إلى بني إسرائيل وأيدناه بالمعجزات الدالة على صدقه، وأمرناه بأن يدعوهم إلى الإيمان؛ ليخرجهم من الضلال إلى الهدى، ويذكِّرهم بنعم الله ونقمه في أيامه، إن في هذا التذكير بها لَدلالات لكل صبَّار على طاعة الله، وعن محارمه، وعلى أقداره، شكور قائم بحقوق الله، يشكر الله على نعمه، وخصَّهم بذلك؛ لأنهم هم الذين يعتبرون بها، ولا يَغْفُلون عنها.');
INSERT INTO Quran__Arabic_Tafseer_Muyassar (QuranChapterID, VerseNumber, Rukuh, Sajdah, Manzil, HizbQuarter, VerseText) VALUES (14, 6, '1', '0', '0', '0', 'واذكر -أيها الرسول- لقومك قصة موسى حين قال لبني إسرائيل: اذكروا نعمة الله عليكم حين أنجاكم من فرعون وأتباعه يذيقونكم أشد العذاب، ويذبِّحون أبناءكم الذكور، حتى لا يأتي منهم من يستولي على مُلْك فرعون، ويبقون الإناث على قيد الحياة ذليلات، وفي ذلكم البلاء والإنجاء اختبار لكم من ربكم عظيم.');
INSERT INTO Quran__Arabic_Tafseer_Muyassar (QuranChapterID, VerseNumber, Rukuh, Sajdah, Manzil, HizbQuarter, VerseText) VALUES (14, 7, '0', '0', '0', '0', 'وقال لهم موسى: واذكروا حين أعلم ربكم إعلامًا مؤكَّدًا: لئن شكرتموه على نعمه ليزيدنكم من فضله، ولئن جحدتم نعمة الله ليعذبنَّكم عذابًا شديدًا.');
INSERT INTO Quran__Arabic_Tafseer_Muyassar (QuranChapterID, VerseNumber, Rukuh, Sajdah, Manzil, HizbQuarter, VerseText) VALUES (14, 8, '0', '0', '0', '0', 'وقال لهم: إن تكفروا بالله أنتم وجميع أهل الأرض فلن تضروا الله شيئًا؛ فإن الله لغني عن خلقه، مستحق للحمد والثناء، محمود في كل حال.');
INSERT INTO Quran__Arabic_Tafseer_Muyassar (QuranChapterID, VerseNumber, Rukuh, Sajdah, Manzil, HizbQuarter, VerseText) VALUES (14, 9, '0', '0', '0', '0', 'ألم يأتكم -يا أمَّة محمد- خبر الأمم التي سبقتكم، قوم نوح وقوم هود وقوم صالح، والأمم التي بعدهم، لا يحصي عددهم إلا الله، جاءتهم رسلهم بالبراهين الواضحات، فعضُّوا أيديهم غيظًا واستنكافًا عن قَبول الإيمان، وقالوا لرسلهم: إنا لا نصدِّق بما جئتمونا به، وإنا لفي شكٍّ مما تدعوننا إليه من الإيمان والتوحيد موجب للريبة.');
INSERT INTO Quran__Arabic_Tafseer_Muyassar (QuranChapterID, VerseNumber, Rukuh, Sajdah, Manzil, HizbQuarter, VerseText) VALUES (14, 10, '0', '0', '0', '1', 'قالت لهم رسلهم: أفي الله وعبادته -وحده- ريب، وهو خالق السموات والأرض، ومنشئهما من العدم على غير مثال سابق، وهو يدعوكم إلى الإيمان؛ ليغفر لكم ذنوبكم، ويؤخر بقاءكم في الدنيا إلى أجل قدَّره، وهو نهاية آجالكم، فلا يعذبكم في الدنيا؟ فقالوا لرسلهم: ما نراكم إلا بشرًا صفاتكم كصفاتنا، لا فضل لكم علينا يؤهلكم أن تكونوا رسلا. تريدون أن تمنعونا من عبادة ما كان يعبده آباونا من الأصنام والأوثان، فأتونا بحجة ظاهرة تشهد على صحة ما تقولون.');
INSERT INTO Quran__Arabic_Tafseer_Muyassar (QuranChapterID, VerseNumber, Rukuh, Sajdah, Manzil, HizbQuarter, VerseText) VALUES (14, 11, '0', '0', '0', '0', 'ولما سمع الرسل ما قاله أقوامهم قالوا لهم: حقًا ما نحن إلا بشر مثلكم كما قلتم، ولكن الله يتفضل بإنعامه على مَن يشاء من عباده فيصطفيهم لرسالته، وما طلبتم من البرهان المبين، فلا يمكن لنا ولا نستطيع أن نأتيكم به إلا بإذن الله وتوفيقه، وعلى الله وحده يعتمد المؤمنون في كل أمورهم.');
INSERT INTO Quran__Arabic_Tafseer_Muyassar (QuranChapterID, VerseNumber, Rukuh, Sajdah, Manzil, HizbQuarter, VerseText) VALUES (14, 12, '1', '0', '0', '0', 'وكيف لا نعتمد على الله، وهو الذي أرشدنا إلى طريق النجاة من عذابه باتباع أحكام دينه؟ ولنصبرنَّ على إيذائكم لنا بالكلام السيئ وغيره، وعلى الله وحده يجب أن يعتمد المؤمنون في نصرهم، وهزيمة أعدائهم.');
INSERT INTO Quran__Arabic_Tafseer_Muyassar (QuranChapterID, VerseNumber, Rukuh, Sajdah, Manzil, HizbQuarter, VerseText) VALUES (14, 13, '0', '0', '0', '0', 'وضاقت صدور الكفار مما قاله الرسل فقالوا لهم: لنطردنكم من بلادنا حتى تعودوا إلى ديننا، فأوحى الله إلى رسله أنه سيهلك الجاحدين الذين كفروا به وبرسله.');
INSERT INTO Quran__Arabic_Tafseer_Muyassar (QuranChapterID, VerseNumber, Rukuh, Sajdah, Manzil, HizbQuarter, VerseText) VALUES (14, 14, '0', '0', '0', '0', 'ولنجعلن العاقبة الحسنة للرسل وأتباعهم بإسكانهم أرض الكافرين بعد إهلاكهم، ذلك الإهلاك للكفار، وإسكان المؤمنين أرضهم أمر مؤكد لمن خاف مقامه بين يديَّ يوم القيامة، وخشي وعيدي وعذابي.');
INSERT INTO Quran__Arabic_Tafseer_Muyassar (QuranChapterID, VerseNumber, Rukuh, Sajdah, Manzil, HizbQuarter, VerseText) VALUES (14, 15, '0', '0', '0', '0', 'ولجأ الرسل إلى ربهم وسألوه النصر على أعدائهم والحكم بينهم، فاستجاب لهم، وهلك كل متكبر لا يقبل الحق ولا يُذْعن له، ولا يقر بتوحيد الله وإخلاص العبادة له.');
INSERT INTO Quran__Arabic_Tafseer_Muyassar (QuranChapterID, VerseNumber, Rukuh, Sajdah, Manzil, HizbQuarter, VerseText) VALUES (14, 16, '0', '0', '0', '0', 'ومِن أمام هذا الكافر جهنم يَلْقى عذابها؛ ويُسقى فيها من القيح والدم الذي يَخْرج من أجسام أهل النار.');
INSERT INTO Quran__Arabic_Tafseer_Muyassar (QuranChapterID, VerseNumber, Rukuh, Sajdah, Manzil, HizbQuarter, VerseText) VALUES (14, 17, '0', '0', '0', '0', 'يحاول المتكبر ابتلاع القيح والدم وغير ذلك مما يسيل من أهل النار مرة بعد مرة، فلا يستطيع أن يبتلعه؛ لقذارته وحرارته، ومرارته، ويأتيه العذاب الشديد من كل نوع ومن كل عضو من جسده، وما هو بميت فيستريح، وله من بعد هذا العذاب عذاب آخر مؤلم.');
INSERT INTO Quran__Arabic_Tafseer_Muyassar (QuranChapterID, VerseNumber, Rukuh, Sajdah, Manzil, HizbQuarter, VerseText) VALUES (14, 18, '0', '0', '0', '0', 'صفة أعمال الكفار في الدنيا كالبر وصلة الأرحام كصفة رماد اشتدت به الريح في يوم ذي ريح شديدة، فلم تترك له أثرًا، فكذلك أعمالهم لا يجدون منها ما ينفعهم عند الله، فقد أذهبها الكفر كما أذهبت الريح الرماد، ذلك السعي والعمل على غير أساس، هو الضلال البعيد عن الطريق المستقيم.');
INSERT INTO Quran__Arabic_Tafseer_Muyassar (QuranChapterID, VerseNumber, Rukuh, Sajdah, Manzil, HizbQuarter, VerseText) VALUES (14, 19, '0', '0', '0', '0', 'ألم تعلم أيها المخاطب -والمراد عموم الناس- أن الله أوجد السموات والأرض على الوجه الصحيح الدال على حكمته، وأنه لم يخلقهما عبثًا، بل للاستدلال بهما على وحدانيته، وكمال قدرته، فيعبدوه وحده، ولا يشركوا به شيئًا؟ إن يشأ يذهبكم ويأت بقوم غيركم يطيعون الله.');
INSERT INTO Quran__Arabic_Tafseer_Muyassar (QuranChapterID, VerseNumber, Rukuh, Sajdah, Manzil, HizbQuarter, VerseText) VALUES (14, 20, '0', '0', '0', '0', 'وما إهلاككم والإتيان بغيركم بممتنع على الله، بل هو سهل يسير.');
INSERT INTO Quran__Arabic_Tafseer_Muyassar (QuranChapterID, VerseNumber, Rukuh, Sajdah, Manzil, HizbQuarter, VerseText) VALUES (14, 21, '1', '0', '0', '0', 'وخرجت الخلائق من قبورهم، وظهروا كلهم يوم القيامة لله الواحد القهار؛ ليحكم بينهم، فيقول الأتباع لقادتهم: إنَّا كنَّا لكم في الدنيا أتباعًا، نأتمر بأمركم، فهل أنتم -اليوم- دافعون عنا من عذاب الله شيئًا كما كنتم تَعِدوننا؟ فيقول الرؤساء: لو هدانا الله إلى الإيمان لأرشدناكم إليه، ولكنه لم يوفقنا، فضللنا وأضللناكم، يستوي علينا وعليكم الـجَزَع والصبر عليه، فليس لنا مهرب من العذاب ولا منجى.');
INSERT INTO Quran__Arabic_Tafseer_Muyassar (QuranChapterID, VerseNumber, Rukuh, Sajdah, Manzil, HizbQuarter, VerseText) VALUES (14, 22, '0', '0', '0', '0', 'وقال الشيطان -بعد أن قضى الله الأمر وحاسب خَلْقه، ودخل أهلُ الجنة الجنةَ وأهلُ النارِ النارَ-: إن الله وعدكم وعدًا حقًا بالبعث والجزاء، ووعدتكم وعدًا باطلا أنه لا بَعْثَ ولا جزاء، فأخلفتكم وعدي، وما كان لي عليكم من قوة أقهركم بها على اتباعي، ولا كانت معي حجة، ولكن دعوتكم إلى الكفر والضلال فاتبعتموني، فلا تلوموني ولوموا أنفسكم، فالذنب ذنبكم، ما أنا بمغيثكم ولا أنتم بمغيثيَّ من عذاب الله، إني تبرَّأت مِن جَعْلِكم لي شريكًا مع الله في طاعته في الدنيا. إن الظالمين -في إعراضهم عن الحق واتباعهم الباطل- لهم عذاب مؤلم موجع.');
INSERT INTO Quran__Arabic_Tafseer_Muyassar (QuranChapterID, VerseNumber, Rukuh, Sajdah, Manzil, HizbQuarter, VerseText) VALUES (14, 23, '0', '0', '0', '0', 'وأُدخل الذين صدَّقوا الله ورسوله وعملوا الصالحات جنات تجري من تحت أشجارها وقصورها الأنهار، لا يخرجون منها أبدًا -بإذن ربهم وحوله وقوته- يُحَيَّوْن فيها بسلام من الله وملائكته والمؤمنين.');
INSERT INTO Quran__Arabic_Tafseer_Muyassar (QuranChapterID, VerseNumber, Rukuh, Sajdah, Manzil, HizbQuarter, VerseText) VALUES (14, 24, '0', '0', '0', '0', 'ألم تعلم -أيها الرسول- كيف ضرب الله مثلا لكلمة التوحيد (لا إله إلا الله) بشجرة عظيمة، وهي النخلة، أصلها متمكن في الأرض، وأعلاها مرتفع علوًّا نحو السماء؟');
INSERT INTO Quran__Arabic_Tafseer_Muyassar (QuranChapterID, VerseNumber, Rukuh, Sajdah, Manzil, HizbQuarter, VerseText) VALUES (14, 25, '0', '0', '0', '0', 'تعطي ثمارها كل وقت بإذن ربها، وكذلك شجرة الإيمان أصلها ثابت في قلب المؤمن علمًا واعتقادًا، وفرعها من الأعمال الصالحة والأخلاق المرضية يُرفع إلى الله وينال ثوابه في كل وقت. ويضرب الله الأمثال للناس؛ ليتذكروا ويتعظوا، فيعتبروا.');
INSERT INTO Quran__Arabic_Tafseer_Muyassar (QuranChapterID, VerseNumber, Rukuh, Sajdah, Manzil, HizbQuarter, VerseText) VALUES (14, 26, '0', '0', '0', '0', 'ومثل كلمة خبيثة -وهي كلمة الكفر- كشجرة خبيثة المأكل والمطعم، وهي شجرة الحنظل، اقتلعت من أعلى الأرض؛ لأن عروقها قريبة من سطح الأرض ما لها أصل ثابت، ولا فرع صاعد، وكذلك الكافر لا ثبات له ولا خير فيه، ولا يُرْفَع له عمل صالح إلى الله.');
INSERT INTO Quran__Arabic_Tafseer_Muyassar (QuranChapterID, VerseNumber, Rukuh, Sajdah, Manzil, HizbQuarter, VerseText) VALUES (14, 27, '1', '0', '0', '0', 'يثبِّت الله الذين آمنوا بالقول الحق الراسخ، وهو شهادة أن لا إله إلا الله وأن محمدًا رسول الله، وما جاء به من الدين الحق يثبتهم الله به في الحياة الدنيا، وعند مماتهم بالخاتمة الحسنة، وفي القبر عند سؤال المَلَكين بهدايتهم إلى الجواب الصحيح، ويضل الله الظالمين عن الصواب في الدنيا والآخرة، ويفعل الله ما يشاء من توفيق أهل الإيمان وخِذْلان أهل الكفر والطغيان.');
INSERT INTO Quran__Arabic_Tafseer_Muyassar (QuranChapterID, VerseNumber, Rukuh, Sajdah, Manzil, HizbQuarter, VerseText) VALUES (14, 28, '0', '0', '0', '1', 'ألم تنظر أيها المخاطب -والمراد العموم- إلى حال المكذبين من كفار قريش الذين استبدلوا الكفر بالله بدلا عن شكره على نعمة الأمن بالحرم وبعثة النبي محمد صلى الله عليه وسلم فيهم؟ وقد أنـزلوا أتباعهم دار الهلاك حين تَسببوا بإخراجهم إلى "بدر" فقُتِلوا وصار مصيرهم دار البوار، وهي جهنم، يدخلونها ويقاسون حرها، وقَبُحَ المستقر مستقرهم.');
INSERT INTO Quran__Arabic_Tafseer_Muyassar (QuranChapterID, VerseNumber, Rukuh, Sajdah, Manzil, HizbQuarter, VerseText) VALUES (14, 29, '0', '0', '0', '0', 'ألم تنظر أيها المخاطب -والمراد العموم- إلى حال المكذبين من كفار قريش الذين استبدلوا الكفر بالله بدلا عن شكره على نعمة الأمن بالحرم وبعثة النبي محمد صلى الله عليه وسلم فيهم؟ وقد أنـزلوا أتباعهم دار الهلاك حين تَسببوا بإخراجهم إلى "بدر" فقُتِلوا وصار مصيرهم دار البوار، وهي جهنم، يدخلونها ويقاسون حرها، وقَبُحَ المستقر مستقرهم.');
INSERT INTO Quran__Arabic_Tafseer_Muyassar (QuranChapterID, VerseNumber, Rukuh, Sajdah, Manzil, HizbQuarter, VerseText) VALUES (14, 30, '0', '0', '0', '0', 'وجعل هؤلاء الكفار لله شركاء عبدوهم معه؛ ليُبْعدوا الناس عن دينه. قل لهم -أيها الرسول-: استمتعوا في الحياة الدنيا؛ فإنها سريعة الزوال، وإن مردَّكم ومرجعكم إلى عذاب جهنم.');
INSERT INTO Quran__Arabic_Tafseer_Muyassar (QuranChapterID, VerseNumber, Rukuh, Sajdah, Manzil, HizbQuarter, VerseText) VALUES (14, 31, '0', '0', '0', '0', 'قل -أيها الرسول- لعبادي الذين آمنوا: يؤدوا الصلاة بحدودها، ويخرجوا بعض ما أعطيناهم من المال في وجوه الخير الواجبة والمستحبة مسرِّين ذلك ومعلنين، من قبل أن يأتي يوم القيامة الذي لا ينفع فيه فداء ولا صداقة.');
INSERT INTO Quran__Arabic_Tafseer_Muyassar (QuranChapterID, VerseNumber, Rukuh, Sajdah, Manzil, HizbQuarter, VerseText) VALUES (14, 32, '0', '0', '0', '0', 'الله تعالى الذي خلق السموات والأرض وأوجدهما من العدم، وأنزل المطر من السحاب فأحيا به الأرض بعد موتها، وأخرج لكم منها أرزاقكم، وذلَّل لكم السفن؛ لتسير في البحر بأمره لمنافعكم، وذلَّل لكم الأنهار لسقياكم وسقيا دوابكم وزروعكم وسائر منافعكم.');
INSERT INTO Quran__Arabic_Tafseer_Muyassar (QuranChapterID, VerseNumber, Rukuh, Sajdah, Manzil, HizbQuarter, VerseText) VALUES (14, 33, '0', '0', '0', '0', 'وذلَّل الله لكم الشمس والقمر لا يَفْتُران عن حركتهما؛ لتتحقق المصالح بهما، وذلَّل لكم الليل؛ لتسكنوا فيه وتستريحوا، والنهار؛ لتبتغوا من فضله، وتدبِّروا معايشكم.');
INSERT INTO Quran__Arabic_Tafseer_Muyassar (QuranChapterID, VerseNumber, Rukuh, Sajdah, Manzil, HizbQuarter, VerseText) VALUES (14, 34, '1', '0', '0', '0', 'وأعطاكم من كل ما طلبتموه، وإن تعدُّوا نِعَم الله عليكم لا تطيقوا عدها ولا إحصاءها ولا القيام بشكرها؛ لكثرتها وتنوُّعها. إن الإنسان لَكثير الظلم لنفسه، كثير الجحود لنعم ربه.');
INSERT INTO Quran__Arabic_Tafseer_Muyassar (QuranChapterID, VerseNumber, Rukuh, Sajdah, Manzil, HizbQuarter, VerseText) VALUES (14, 35, '0', '0', '0', '0', 'واذكر -أيها الرسول- حين قال إبراهيم داعيًا ربه -بعد أن أسكن ابنه إسماعيل وأمه "هاجَر" وادي "مكة" -: رب اجعل "مكة" بلدَ أمنٍ يأمن كل مَن فيها، وأبعِدني وأبنائي عن عبادة الأصنام.');
INSERT INTO Quran__Arabic_Tafseer_Muyassar (QuranChapterID, VerseNumber, Rukuh, Sajdah, Manzil, HizbQuarter, VerseText) VALUES (14, 36, '0', '0', '0', '0', 'ربِّ إن الأصنام تسبَّبتْ في إبعاد كثير من الناس عن طريق الحق، فمن اقتدى بي في التوحيد فهو على ديني وسُنَّتي، ومَن خالفني فيما دون الشرك، فإنك غفور لذنوب المذنبين -بفضلك- رحيم بهم، تعفو عمن تشاء منهم.');
INSERT INTO Quran__Arabic_Tafseer_Muyassar (QuranChapterID, VerseNumber, Rukuh, Sajdah, Manzil, HizbQuarter, VerseText) VALUES (14, 37, '0', '0', '0', '0', 'ربنا إني أسكنت من ذريتي بوادٍ ليس فيه زرع ولا ماء بجوار بيتك المحرم، ربنا إنني فعلت ذلك بأمرك؛ لكي يؤدوا الصلاة بحدودها، فاجعل قلوب بعض خلقك تَنزع إليهم وتحنُّ، وارزقهم في هذا المكان من أنواع الثمار؛ لكي يشكروا لك على عظيم نعمك. فاستجاب الله دعاءه.');
INSERT INTO Quran__Arabic_Tafseer_Muyassar (QuranChapterID, VerseNumber, Rukuh, Sajdah, Manzil, HizbQuarter, VerseText) VALUES (14, 38, '0', '0', '0', '0', 'ربنا إنك تعلم كل ما نخفيه وما نظهره. وما يغيب عن علم الله شيء من الكائنات في الأرض ولا في السماء.');
INSERT INTO Quran__Arabic_Tafseer_Muyassar (QuranChapterID, VerseNumber, Rukuh, Sajdah, Manzil, HizbQuarter, VerseText) VALUES (14, 39, '0', '0', '0', '0', 'يُثْني إبراهيم على الله تعالى، فيقول: الحمد لله الذي رزقني على كِبَر سني ولديَّ إسماعيل وإسحاق بعد دعائي أن يهب لي من الصالحين، إن ربي لسميع الدعاء ممن دعاه، وقد دعوته ولم يخيِّب رجائي.');
INSERT INTO Quran__Arabic_Tafseer_Muyassar (QuranChapterID, VerseNumber, Rukuh, Sajdah, Manzil, HizbQuarter, VerseText) VALUES (14, 40, '0', '0', '0', '0', 'رب اجعلني مداومًا على أداء الصلاة على أتم وجوهها، واجعل من ذريتي مَن يحافظ عليها، ربنا واستجب دعائي وتقبَّل عبادتي.');
INSERT INTO Quran__Arabic_Tafseer_Muyassar (QuranChapterID, VerseNumber, Rukuh, Sajdah, Manzil, HizbQuarter, VerseText) VALUES (14, 41, '1', '0', '0', '0', 'ربنا اغفر لي ما وقع مني مما لا يسلم منه البشر واغفر لوالديَّ، (وهذا قبل أن يتبيَّن له أن والده عدو لله) واغفر للمؤمنين جميعًا يوم يقوم الناس للحساب والجزاء.');
INSERT INTO Quran__Arabic_Tafseer_Muyassar (QuranChapterID, VerseNumber, Rukuh, Sajdah, Manzil, HizbQuarter, VerseText) VALUES (14, 42, '0', '0', '0', '0', 'ولا تحسبن -أيها الرسول- أن الله غافل عما يعمله الظالمون: من التكذيب بك وبغيرك من الرسل، وإيذاء المؤمنين وغير ذلك من المعاصي، إنما يؤخِّرُ عقابهم ليوم شديد ترتفع فيه عيونهم ولا تَغْمَض؛ مِن هول ما تراه. وفي هذا تسلية لرسول الله محمد صلى الله عليه وسلم.');
INSERT INTO Quran__Arabic_Tafseer_Muyassar (QuranChapterID, VerseNumber, Rukuh, Sajdah, Manzil, HizbQuarter, VerseText) VALUES (14, 43, '0', '0', '0', '0', 'يوم يقوم الظالمون من قبورهم مسرعين لإجابة الداعي رافعي رؤوسهم لا يبصرون شيئًا لهول الموقف، وقلوبهم خالية ليس فيها شيء؛ لكثرة الخوف والوجل من هول ما ترى.');
INSERT INTO Quran__Arabic_Tafseer_Muyassar (QuranChapterID, VerseNumber, Rukuh, Sajdah, Manzil, HizbQuarter, VerseText) VALUES (14, 44, '0', '0', '0', '0', 'وأنذر -أيها الرسول- الناس الذين أرسلتُكَ إليهم عذاب الله يوم القيامة، وعند ذلك يقول الذين ظلموا أنفسهم بالكفر: ربنا أَمْهِلْنا إلى وقت قريب نؤمن بك ونصدق رسلك. فيقال لهم توبيخًا: ألم تقسموا في حياتكم أنه لا زوال لكم عن الحياة الدنيا إلى الآخرة، فلم تصدِّقوا بهذا البعث؟');
INSERT INTO Quran__Arabic_Tafseer_Muyassar (QuranChapterID, VerseNumber, Rukuh, Sajdah, Manzil, HizbQuarter, VerseText) VALUES (14, 45, '0', '0', '0', '0', 'وحللتم في مساكن الكافرين السابقين الذين ظلموا أنفسهم كقوم هود وصالح، وعلمتم -بما رأيتم وأُخبرتم- ما أنزلناه بهم من الهلاك، وضربنا لكم الأمثال في القرآن، فلم تعتبروا؟');
INSERT INTO Quran__Arabic_Tafseer_Muyassar (QuranChapterID, VerseNumber, Rukuh, Sajdah, Manzil, HizbQuarter, VerseText) VALUES (14, 46, '0', '0', '0', '0', 'وقد دبَّر المشركون الشرَّ للرسول صلى الله عليه وسلم بقتله، وعند الله مكرهم فهو محيط به، وقد عاد مكرهم عليهم، وما كان مكرهم لتزول منه الجبال ولا غيرها لضعفه ووَهَنه، ولم يضرُّوا الله شيئًا، وإنما ضرُّوا أنفسهم.');
INSERT INTO Quran__Arabic_Tafseer_Muyassar (QuranChapterID, VerseNumber, Rukuh, Sajdah, Manzil, HizbQuarter, VerseText) VALUES (14, 47, '0', '0', '0', '0', 'فلا تحسبن -أيها الرسول- أن الله يخلف رسله ما وعدهم به من النصر وإهلاك مكذبيهم. إن الله عزيز لا يمتنع عليه شيء، منتقم من أعدائه أشد انتقام. والخطاب وإن كان خاصًّا بالنبي صلى الله عليه وسلم، فهو موجَّه لعموم الأمة.');
INSERT INTO Quran__Arabic_Tafseer_Muyassar (QuranChapterID, VerseNumber, Rukuh, Sajdah, Manzil, HizbQuarter, VerseText) VALUES (14, 48, '0', '0', '0', '0', 'وانتقام الله تعالى مِن أعدائه في يوم القيامة يوم تُبَدَّل هذه الأرض بأرض أخرى بيضاء نقيَّة كالفضة، وكذلك تُبَدَّل السموات بغيرها، وتخرج الخلائق من قبورها أحياء ظاهرين للقاء الله الواحد القهار، المتفرد بعظمته وأسمائه وصفاته وأفعاله وقهره لكل شيء.');
INSERT INTO Quran__Arabic_Tafseer_Muyassar (QuranChapterID, VerseNumber, Rukuh, Sajdah, Manzil, HizbQuarter, VerseText) VALUES (14, 49, '0', '0', '0', '0', 'وتُبْصِرُ -أيها الرسول- المجرمين يوم القيامة مقيدين بالقيود، قد قُرِنت أيديهم وأرجلهم بالسلاسل، وهم في ذُلٍّ وهوان.');
INSERT INTO Quran__Arabic_Tafseer_Muyassar (QuranChapterID, VerseNumber, Rukuh, Sajdah, Manzil, HizbQuarter, VerseText) VALUES (14, 50, '0', '0', '0', '0', 'ثيابهم من القَطِران الشديد الاشتعال، وتلفح وجوههم النار فتحرقها.');
INSERT INTO Quran__Arabic_Tafseer_Muyassar (QuranChapterID, VerseNumber, Rukuh, Sajdah, Manzil, HizbQuarter, VerseText) VALUES (14, 51, '0', '0', '0', '0', 'فَعَل الله ذلك بهم؛ جزاء لهم بما كسبوا من الآثام في الدنيا، والله يجازي كل إنسان بما عمل مِن خير أو شر، إن الله سريع الحساب.');
INSERT INTO Quran__Arabic_Tafseer_Muyassar (QuranChapterID, VerseNumber, Rukuh, Sajdah, Manzil, HizbQuarter, VerseText) VALUES (14, 52, '1', '0', '0', '0', 'هذا القرآن الذي أنزلناه إليك -أيها الرسول- بلاغ وإعلام للناس؛ لنصحهم وتخويفهم، ولكي يوقنوا أن الله هو الإله الواحد، فيعبدوه وحده لا شريك له، وليتعظ به أصحاب العقول السليمة.');
INSERT INTO Quran__Arabic_Tafseer_Muyassar (QuranChapterID, VerseNumber, Rukuh, Sajdah, Manzil, HizbQuarter, VerseText) VALUES (15, 1, '0', '0', '0', '1', '(الر) سبق الكلام على الحروف المقطَّعة في أول سورة البقرة.');
INSERT INTO Quran__Arabic_Tafseer_Muyassar (QuranChapterID, VerseNumber, Rukuh, Sajdah, Manzil, HizbQuarter, VerseText) VALUES (15, 2, '0', '0', '0', '0', 'سيتمنى الكفار حين يرون خروج عصاة المؤمنين من النار أن لو كانوا موحدين؛ ليخرجوا كما خرجوا.');
INSERT INTO Quran__Arabic_Tafseer_Muyassar (QuranChapterID, VerseNumber, Rukuh, Sajdah, Manzil, HizbQuarter, VerseText) VALUES (15, 3, '0', '0', '0', '0', 'اترك -أيها الرسول- الكفار يأكلوا، ويستمتعوا بدنياهم، ويشغلهم الطمع فيها عن طاعة الله، فسوف يعلمون عاقبة أمرهم الخاسرة في الدنيا والآخرة.');
INSERT INTO Quran__Arabic_Tafseer_Muyassar (QuranChapterID, VerseNumber, Rukuh, Sajdah, Manzil, HizbQuarter, VerseText) VALUES (15, 4, '0', '0', '0', '0', 'وإذا طلبوا نزول العذاب بهم تكذيبًا لك -أيها الرسول- فإنا لا نُهْلك قرية إلا ولإهلاكها أجل مقدَّر، لا نُهْلكهم حتى يبلغوه، مثل مَن سبقهم.');
INSERT INTO Quran__Arabic_Tafseer_Muyassar (QuranChapterID, VerseNumber, Rukuh, Sajdah, Manzil, HizbQuarter, VerseText) VALUES (15, 5, '0', '0', '0', '0', 'لا تتجاوز أمة أجلها فتزيد عليه، ولا تتقدم عليه، فتنقص منه.');
INSERT INTO Quran__Arabic_Tafseer_Muyassar (QuranChapterID, VerseNumber, Rukuh, Sajdah, Manzil, HizbQuarter, VerseText) VALUES (15, 6, '0', '0', '0', '0', 'وقال المكذبون لمحمد صلى الله عليه وسلم استهزاءً: يا أيها الذي نُزِّل عليه القرآن إنك لذاهب العقل، هلا تأتينا بالملائكة -إن كنت صادقًا-؛ لتشهد أن الله أرسلك.');
INSERT INTO Quran__Arabic_Tafseer_Muyassar (QuranChapterID, VerseNumber, Rukuh, Sajdah, Manzil, HizbQuarter, VerseText) VALUES (15, 7, '0', '0', '0', '0', 'وقال المكذبون لمحمد صلى الله عليه وسلم استهزاءً: يا أيها الذي نُزِّل عليه القرآن إنك لذاهب العقل، هلا تأتينا بالملائكة -إن كنت صادقًا-؛ لتشهد أن الله أرسلك.');
INSERT INTO Quran__Arabic_Tafseer_Muyassar (QuranChapterID, VerseNumber, Rukuh, Sajdah, Manzil, HizbQuarter, VerseText) VALUES (15, 8, '0', '0', '0', '0', 'وردَّ الله عليهم: إننا لا ننزل الملائكة إلا بالعذاب الذي لا إمهال فيه لمن لم يؤمن، وما كانوا حين تنزل الملائكة بالعذاب بِمُمْهلين.');
INSERT INTO Quran__Arabic_Tafseer_Muyassar (QuranChapterID, VerseNumber, Rukuh, Sajdah, Manzil, HizbQuarter, VerseText) VALUES (15, 9, '0', '0', '0', '0', 'إنَّا نحن نزَّلنا القرآن على النبي محمد صلى الله عليه وسلم، وإنَّا نتعهد بحفظه مِن أن يُزاد فيه أو يُنْقَص منه، أو يضيع منه شيء.');
INSERT INTO Quran__Arabic_Tafseer_Muyassar (QuranChapterID, VerseNumber, Rukuh, Sajdah, Manzil, HizbQuarter, VerseText) VALUES (15, 10, '0', '0', '0', '0', 'ولقد أرسلنا من قبلك -أيها الرسول- رسلا في فِرَق الأولين، فما من رسولٍ جاءهم إلا كانوا منه يسخرون. وفي هذا تسلية للرسول صلى الله عليه وسلم. فكما فَعَل بك هؤلاء المشركون فكذلك فُعِلَ بمن قبلك من الرسل.');
INSERT INTO Quran__Arabic_Tafseer_Muyassar (QuranChapterID, VerseNumber, Rukuh, Sajdah, Manzil, HizbQuarter, VerseText) VALUES (15, 11, '0', '0', '0', '0', 'ولقد أرسلنا من قبلك -أيها الرسول- رسلا في فِرَق الأولين، فما من رسولٍ جاءهم إلا كانوا منه يسخرون. وفي هذا تسلية للرسول صلى الله عليه وسلم. فكما فَعَل بك هؤلاء المشركون فكذلك فُعِلَ بمن قبلك من الرسل.');
INSERT INTO Quran__Arabic_Tafseer_Muyassar (QuranChapterID, VerseNumber, Rukuh, Sajdah, Manzil, HizbQuarter, VerseText) VALUES (15, 12, '0', '0', '0', '0', 'كما أدخلنا الكفر في قلوب الأمم السابقة بالاستهزاء بالرسل وتكذيبهم، كذلك نفعل ذلك في قلوب مشركي قومك الذين أجرموا بالكفر بالله وتكذيب رسوله، لا يُصَدِّقون بالذكر الذي أُنزل إليك، وقد مضت سنَّة الأولين بإهلاك الكفار، وهؤلاء مِثْلهم، سَيُهْلك المستمرون منهم على الكفر والتكذيب.');
INSERT INTO Quran__Arabic_Tafseer_Muyassar (QuranChapterID, VerseNumber, Rukuh, Sajdah, Manzil, HizbQuarter, VerseText) VALUES (15, 13, '0', '0', '0', '0', 'كما أدخلنا الكفر في قلوب الأمم السابقة بالاستهزاء بالرسل وتكذيبهم، كذلك نفعل ذلك في قلوب مشركي قومك الذين أجرموا بالكفر بالله وتكذيب رسوله، لا يُصَدِّقون بالذكر الذي أُنزل إليك، وقد مضت سنَّة الأولين بإهلاك الكفار، وهؤلاء مِثْلهم، سَيُهْلك المستمرون منهم على الكفر والتكذيب.');
INSERT INTO Quran__Arabic_Tafseer_Muyassar (QuranChapterID, VerseNumber, Rukuh, Sajdah, Manzil, HizbQuarter, VerseText) VALUES (15, 14, '0', '0', '0', '0', 'ولو فتحنا على كفار "مكة" بابًا من السماء فاستمروا صاعدين فيه حتى يشاهدوا ما في السماء من عجائب ملكوت الله، لما صدَّقوا، ولقالوا: سُحِرَتْ أبصارنا، حتى رأينا ما لم نرَ، وما نحن إلا مسحورون في عقولنا من محمد.');
INSERT INTO Quran__Arabic_Tafseer_Muyassar (QuranChapterID, VerseNumber, Rukuh, Sajdah, Manzil, HizbQuarter, VerseText) VALUES (15, 15, '1', '0', '0', '0', 'ولو فتحنا على كفار "مكة" بابًا من السماء فاستمروا صاعدين فيه حتى يشاهدوا ما في السماء من عجائب ملكوت الله، لما صدَّقوا، ولقالوا: سُحِرَتْ أبصارنا، حتى رأينا ما لم نرَ، وما نحن إلا مسحورون في عقولنا من محمد.');
INSERT INTO Quran__Arabic_Tafseer_Muyassar (QuranChapterID, VerseNumber, Rukuh, Sajdah, Manzil, HizbQuarter, VerseText) VALUES (15, 16, '0', '0', '0', '0', 'ومن أدلة قدرتنا: أنا جعلنا في السماء الدنيا منازل للكواكب تنزل فيها، ويستدل بذلك على الطرقات والأوقات والخِصْب والجَدْب، وزَيَّنَّا هذه السماء بالنجوم لمن ينظرون إليها، ويتأملون فيعتبرون.');
INSERT INTO Quran__Arabic_Tafseer_Muyassar (QuranChapterID, VerseNumber, Rukuh, Sajdah, Manzil, HizbQuarter, VerseText) VALUES (15, 17, '0', '0', '0', '0', 'وحفظنا السماء من كل شيطان مرجوم مطرود من رحمة الله؛ كي لا يصل إليها.');
INSERT INTO Quran__Arabic_Tafseer_Muyassar (QuranChapterID, VerseNumber, Rukuh, Sajdah, Manzil, HizbQuarter, VerseText) VALUES (15, 18, '0', '0', '0', '0', 'إلا من اختلس السمع مِن كلام أهل الملأ الأعلى في بعض الأوقات، فأدركه ولحقه كوكب مضيء يحرقه. وقد يُلْقي الشيطان إلى وليه بعض ما استرقَه قبل أن يحرقه الشهاب.');
INSERT INTO Quran__Arabic_Tafseer_Muyassar (QuranChapterID, VerseNumber, Rukuh, Sajdah, Manzil, HizbQuarter, VerseText) VALUES (15, 19, '0', '0', '0', '0', 'والأرض مددناها متسعة، وألقينا فيها جبالا تثبتها، وأنبتنا فيها من كل أنواع النبات ما هو مقدَّر معلوم مما يحتاج إليه العباد.');
INSERT INTO Quran__Arabic_Tafseer_Muyassar (QuranChapterID, VerseNumber, Rukuh, Sajdah, Manzil, HizbQuarter, VerseText) VALUES (15, 20, '0', '0', '0', '0', 'وجعلنا لكم فيها ما به تعيشون من الحَرْث، ومن الماشية، ومن أنواع المكاسب وغيرها، وخلقنا لكم من الذرية والخدم والدوابِّ ما تنتفعون به، وليس رزقهم عليكم، وإنما هو على الله رب العالمين تفضلا منه وتكرمًا.');
INSERT INTO Quran__Arabic_Tafseer_Muyassar (QuranChapterID, VerseNumber, Rukuh, Sajdah, Manzil, HizbQuarter, VerseText) VALUES (15, 21, '0', '0', '0', '0', 'وما من شيء من منافع العباد إلا عندنا خزائنه من جميع الصنوف، وما ننزله إلا بمقدار محدد كما نشاء وكما نريد، فالخزائن بيد الله يعطي من يشاء ويمنع من يشاء، بحسب رحمته الواسعة، وحكمته البالغة.');
INSERT INTO Quran__Arabic_Tafseer_Muyassar (QuranChapterID, VerseNumber, Rukuh, Sajdah, Manzil, HizbQuarter, VerseText) VALUES (15, 22, '0', '0', '0', '0', 'وأرسلنا الرياح وسخرناها تُلَقِّح السحاب، وتحمل المطر والخير والنفع، فأنزلنا من السحاب ماء أعددناه لشرابكم وأرضكم ومواشيكم، وما أنتم بقادرين على خزنه وادِّخاره، ولكن نخزنه لكم رحمة بكم، وإحسانًا إليكم.');
INSERT INTO Quran__Arabic_Tafseer_Muyassar (QuranChapterID, VerseNumber, Rukuh, Sajdah, Manzil, HizbQuarter, VerseText) VALUES (15, 23, '0', '0', '0', '0', 'وإنَّا لنحن نحيي مَن كان ميتًا بخلقه من العدم، ونميت من كان حيًا بعد انقضاء أجله، ونحن الوارثون الأرض ومَن عليها.');
INSERT INTO Quran__Arabic_Tafseer_Muyassar (QuranChapterID, VerseNumber, Rukuh, Sajdah, Manzil, HizbQuarter, VerseText) VALUES (15, 24, '0', '0', '0', '0', 'ولقد علمنا مَن هلك منكم مِن لدن آدم، ومَن هو حيٌّ، ومَن سيأتي إلى يوم القيامة.');
INSERT INTO Quran__Arabic_Tafseer_Muyassar (QuranChapterID, VerseNumber, Rukuh, Sajdah, Manzil, HizbQuarter, VerseText) VALUES (15, 25, '1', '0', '0', '0', 'وإن ربك هو يحشرهم للحساب والجزاء، إنه حكيم في تدبيره، عليم لا يخفى عليه شيء.');
INSERT INTO Quran__Arabic_Tafseer_Muyassar (QuranChapterID, VerseNumber, Rukuh, Sajdah, Manzil, HizbQuarter, VerseText) VALUES (15, 26, '0', '0', '0', '0', 'ولقد خلقنا آدم مِن طين يابس إذا نُقِر عليه سُمع له صوت، وهذا الطين اليابس من طين أسود متغيِّر لونه وريحه؛ مِن طول مكثه.');
INSERT INTO Quran__Arabic_Tafseer_Muyassar (QuranChapterID, VerseNumber, Rukuh, Sajdah, Manzil, HizbQuarter, VerseText) VALUES (15, 27, '0', '0', '0', '0', 'وخلقنا أبا الجن، وهو إبليس مِن قَبْل خلق آدم من نار شديدة الحرارة لا دخان لها.');
INSERT INTO Quran__Arabic_Tafseer_Muyassar (QuranChapterID, VerseNumber, Rukuh, Sajdah, Manzil, HizbQuarter, VerseText) VALUES (15, 28, '0', '0', '0', '0', 'واذكر -أيها النبي- حين قال ربك للملائكة: إني خالق إنسانًا من طين يابس، وهذا الطين اليابس من طين أسود متغيِّر اللون.');
INSERT INTO Quran__Arabic_Tafseer_Muyassar (QuranChapterID, VerseNumber, Rukuh, Sajdah, Manzil, HizbQuarter, VerseText) VALUES (15, 29, '0', '0', '0', '0', 'فإذا سوَّيته وأكملت صورته ونفخت فيه الروح، فخُرُّوا له ساجدين سجود تحية وتكريم، لا سجود عبادة.');
INSERT INTO Quran__Arabic_Tafseer_Muyassar (QuranChapterID, VerseNumber, Rukuh, Sajdah, Manzil, HizbQuarter, VerseText) VALUES (15, 30, '0', '0', '0', '0', 'فسجد الملائكة كلهم أجمعون كما أمرهم ربهم لم يمتنع منهم أحد، لكن إبليس امتنع أن يسجد لآدم مع الملائكة الساجدين.');
INSERT INTO Quran__Arabic_Tafseer_Muyassar (QuranChapterID, VerseNumber, Rukuh, Sajdah, Manzil, HizbQuarter, VerseText) VALUES (15, 31, '0', '0', '0', '0', 'فسجد الملائكة كلهم أجمعون كما أمرهم ربهم لم يمتنع منهم أحد، لكن إبليس امتنع أن يسجد لآدم مع الملائكة الساجدين.');
INSERT INTO Quran__Arabic_Tafseer_Muyassar (QuranChapterID, VerseNumber, Rukuh, Sajdah, Manzil, HizbQuarter, VerseText) VALUES (15, 32, '0', '0', '0', '0', 'قال الله لإبليس: ما لك ألا تسجد مع الملائكة؟');
INSERT INTO Quran__Arabic_Tafseer_Muyassar (QuranChapterID, VerseNumber, Rukuh, Sajdah, Manzil, HizbQuarter, VerseText) VALUES (15, 33, '0', '0', '0', '0', 'قال إبليس مظهرًا كبره وحسده: لا يليق بي أن أسجد لإنسان أَوجدْتَهُ من طين يابس كان طينًا أسود متغيرًا.');
INSERT INTO Quran__Arabic_Tafseer_Muyassar (QuranChapterID, VerseNumber, Rukuh, Sajdah, Manzil, HizbQuarter, VerseText) VALUES (15, 34, '0', '0', '0', '0', 'قال الله تعالى له: فاخرج من الجنة، فإنك مطرود من كل خير، وإن عليك اللعنة والبعد من رحمتي إلى يوم يُبْعَث الناس للحساب والجزاء.');
INSERT INTO Quran__Arabic_Tafseer_Muyassar (QuranChapterID, VerseNumber, Rukuh, Sajdah, Manzil, HizbQuarter, VerseText) VALUES (15, 35, '0', '0', '0', '0', 'قال الله تعالى له: فاخرج من الجنة، فإنك مطرود من كل خير، وإن عليك اللعنة والبعد من رحمتي إلى يوم يُبْعَث الناس للحساب والجزاء.');
INSERT INTO Quran__Arabic_Tafseer_Muyassar (QuranChapterID, VerseNumber, Rukuh, Sajdah, Manzil, HizbQuarter, VerseText) VALUES (15, 36, '0', '0', '0', '0', 'قال إبليس: رب أخِّرني في الدنيا إلى اليوم الذي تَبْعَث فيه عبادك، وهو يوم القيامة.');
INSERT INTO Quran__Arabic_Tafseer_Muyassar (QuranChapterID, VerseNumber, Rukuh, Sajdah, Manzil, HizbQuarter, VerseText) VALUES (15, 37, '0', '0', '0', '0', 'قال الله له: فإنك ممن أخَّرْتُ هلاكهم إلى اليوم الذي يموت فيه كل الخلق بعد النفخة الأولى، لا إلى يوم البعث، وإنما أُجيبَ إلى ذلك استدراجًا له وإمهالا وفتنة للثقلين.');
INSERT INTO Quran__Arabic_Tafseer_Muyassar (QuranChapterID, VerseNumber, Rukuh, Sajdah, Manzil, HizbQuarter, VerseText) VALUES (15, 38, '0', '0', '0', '0', 'قال الله له: فإنك ممن أخَّرْتُ هلاكهم إلى اليوم الذي يموت فيه كل الخلق بعد النفخة الأولى، لا إلى يوم البعث، وإنما أُجيبَ إلى ذلك استدراجًا له وإمهالا وفتنة للثقلين.');
INSERT INTO Quran__Arabic_Tafseer_Muyassar (QuranChapterID, VerseNumber, Rukuh, Sajdah, Manzil, HizbQuarter, VerseText) VALUES (15, 39, '0', '0', '0', '0', 'قال إبليس: ربِّ بسبب ما أغويتني وأضللتني لأحسِّنَنَّ لذرية آدم معاصيك في الأرض، ولأضلنهم أجمعين عن طريق الهدى، إلا عبادك الذين هديتهم فأخلصوا لك العبادة وحدك دون سائر خلقك.');
INSERT INTO Quran__Arabic_Tafseer_Muyassar (QuranChapterID, VerseNumber, Rukuh, Sajdah, Manzil, HizbQuarter, VerseText) VALUES (15, 40, '0', '0', '0', '0', 'قال إبليس: ربِّ بسبب ما أغويتني وأضللتني لأحسِّنَنَّ لذرية آدم معاصيك في الأرض، ولأضلنهم أجمعين عن طريق الهدى، إلا عبادك الذين هديتهم فأخلصوا لك العبادة وحدك دون سائر خلقك.');
INSERT INTO Quran__Arabic_Tafseer_Muyassar (QuranChapterID, VerseNumber, Rukuh, Sajdah, Manzil, HizbQuarter, VerseText) VALUES (15, 41, '0', '0', '0', '0', 'قال الله: هذا طريق مستقيم معتدل موصل إليَّ وإلى دار كرامتي. إن عبادي الذين أخلصوا لي لا أجعل لك سلطانًا على قلوبهم تضلُّهم به عن الصراط المستقيم، لكن سلطانك على مَنِ اتبعك مِنَ الضالين المشركين الذين رضوا بولايتك وطاعتك بدلا من طاعتي.');
INSERT INTO Quran__Arabic_Tafseer_Muyassar (QuranChapterID, VerseNumber, Rukuh, Sajdah, Manzil, HizbQuarter, VerseText) VALUES (15, 42, '0', '0', '0', '0', 'قال الله: هذا طريق مستقيم معتدل موصل إليَّ وإلى دار كرامتي. إن عبادي الذين أخلصوا لي لا أجعل لك سلطانًا على قلوبهم تضلُّهم به عن الصراط المستقيم، لكن سلطانك على مَنِ اتبعك مِنَ الضالين المشركين الذين رضوا بولايتك وطاعتك بدلا من طاعتي.');
INSERT INTO Quran__Arabic_Tafseer_Muyassar (QuranChapterID, VerseNumber, Rukuh, Sajdah, Manzil, HizbQuarter, VerseText) VALUES (15, 43, '0', '0', '0', '0', 'وإن النار الشديدة لَموعدُ إبليس وأتباعه أجمعين، لها سبعة أبواب كل باب أسفل من الآخر، لكل بابٍ مِن أتباع إبليس قسم ونصيب بحسب أعمالهم.');
INSERT INTO Quran__Arabic_Tafseer_Muyassar (QuranChapterID, VerseNumber, Rukuh, Sajdah, Manzil, HizbQuarter, VerseText) VALUES (15, 44, '1', '0', '0', '0', 'وإن النار الشديدة لَموعدُ إبليس وأتباعه أجمعين، لها سبعة أبواب كل باب أسفل من الآخر، لكل بابٍ مِن أتباع إبليس قسم ونصيب بحسب أعمالهم.');
INSERT INTO Quran__Arabic_Tafseer_Muyassar (QuranChapterID, VerseNumber, Rukuh, Sajdah, Manzil, HizbQuarter, VerseText) VALUES (15, 45, '0', '0', '0', '0', 'إن الذين اتقوا الله بامتثال ما أمر واجتناب ما نهى في بساتين وأنهار جارية يقال لهم: ادخلوا هذه الجنات سالمين من كل سوء آمنين من كل عذاب. ونزعنا ما في قلوبهم من حقد وعداوة، يعيشون في الجنة إخوانًا متحابين، يجلسون على أسرَّة عظيمة، تتقابل وجوههم تواصلا وتحاببًا، لا يصيبهم فيها تعب ولا إعياء، وهم باقون فيها أبدًا.');
INSERT INTO Quran__Arabic_Tafseer_Muyassar (QuranChapterID, VerseNumber, Rukuh, Sajdah, Manzil, HizbQuarter, VerseText) VALUES (15, 46, '0', '0', '0', '0', 'إن الذين اتقوا الله بامتثال ما أمر واجتناب ما نهى في بساتين وأنهار جارية يقال لهم: ادخلوا هذه الجنات سالمين من كل سوء آمنين من كل عذاب. ونزعنا ما في قلوبهم من حقد وعداوة، يعيشون في الجنة إخوانًا متحابين، يجلسون على أسرَّة عظيمة، تتقابل وجوههم تواصلا وتحاببًا، لا يصيبهم فيها تعب ولا إعياء، وهم باقون فيها أبدًا.');
INSERT INTO Quran__Arabic_Tafseer_Muyassar (QuranChapterID, VerseNumber, Rukuh, Sajdah, Manzil, HizbQuarter, VerseText) VALUES (15, 47, '0', '0', '0', '0', 'إن الذين اتقوا الله بامتثال ما أمر واجتناب ما نهى في بساتين وأنهار جارية يقال لهم: ادخلوا هذه الجنات سالمين من كل سوء آمنين من كل عذاب. ونزعنا ما في قلوبهم من حقد وعداوة، يعيشون في الجنة إخوانًا متحابين، يجلسون على أسرَّة عظيمة، تتقابل وجوههم تواصلا وتحاببًا، لا يصيبهم فيها تعب ولا إعياء، وهم باقون فيها أبدًا.');
INSERT INTO Quran__Arabic_Tafseer_Muyassar (QuranChapterID, VerseNumber, Rukuh, Sajdah, Manzil, HizbQuarter, VerseText) VALUES (15, 48, '0', '0', '0', '0', 'إن الذين اتقوا الله بامتثال ما أمر واجتناب ما نهى في بساتين وأنهار جارية يقال لهم: ادخلوا هذه الجنات سالمين من كل سوء آمنين من كل عذاب. ونزعنا ما في قلوبهم من حقد وعداوة، يعيشون في الجنة إخوانًا متحابين، يجلسون على أسرَّة عظيمة، تتقابل وجوههم تواصلا وتحاببًا، لا يصيبهم فيها تعب ولا إعياء، وهم باقون فيها أبدًا.');
INSERT INTO Quran__Arabic_Tafseer_Muyassar (QuranChapterID, VerseNumber, Rukuh, Sajdah, Manzil, HizbQuarter, VerseText) VALUES (15, 49, '0', '0', '0', '0', 'أخبر -أيها الرسول- عبادي أني أنا الغفور للمؤمنين التائبين، الرحيم بهم، وأن عذابي هو العذاب المؤلم الموجع لغير التائبين. وأخبرهم -أيها الرسول- عن ضيوف إبراهيم من الملائكة الذين بشَّروه بالولد، وبهلاك قوم لوط.');
INSERT INTO Quran__Arabic_Tafseer_Muyassar (QuranChapterID, VerseNumber, Rukuh, Sajdah, Manzil, HizbQuarter, VerseText) VALUES (15, 50, '0', '0', '0', '1', 'أخبر -أيها الرسول- عبادي أني أنا الغفور للمؤمنين التائبين، الرحيم بهم، وأن عذابي هو العذاب المؤلم الموجع لغير التائبين. وأخبرهم -أيها الرسول- عن ضيوف إبراهيم من الملائكة الذين بشَّروه بالولد، وبهلاك قوم لوط.');
INSERT INTO Quran__Arabic_Tafseer_Muyassar (QuranChapterID, VerseNumber, Rukuh, Sajdah, Manzil, HizbQuarter, VerseText) VALUES (15, 51, '0', '0', '0', '0', 'أخبر -أيها الرسول- عبادي أني أنا الغفور للمؤمنين التائبين، الرحيم بهم، وأن عذابي هو العذاب المؤلم الموجع لغير التائبين. وأخبرهم -أيها الرسول- عن ضيوف إبراهيم من الملائكة الذين بشَّروه بالولد، وبهلاك قوم لوط.');
INSERT INTO Quran__Arabic_Tafseer_Muyassar (QuranChapterID, VerseNumber, Rukuh, Sajdah, Manzil, HizbQuarter, VerseText) VALUES (15, 52, '0', '0', '0', '0', 'حين دخلوا عليه فقالوا: سلامًا؛ فرد عليهم السلام، ثم قدَّم لهم الطعام فلم يأكلوا، قال: إنا منكم فزعون.');
INSERT INTO Quran__Arabic_Tafseer_Muyassar (QuranChapterID, VerseNumber, Rukuh, Sajdah, Manzil, HizbQuarter, VerseText) VALUES (15, 53, '0', '0', '0', '0', 'قالت الملائكة له: لا تفزع إنَّا جئنا نبشرك بولد كثير العلم بالدين، هو إسحاق.');
INSERT INTO Quran__Arabic_Tafseer_Muyassar (QuranChapterID, VerseNumber, Rukuh, Sajdah, Manzil, HizbQuarter, VerseText) VALUES (15, 54, '0', '0', '0', '0', 'قال إبراهيم متعجبًا: أبشَّرتموني بالولد، وأنا كبير وزوجتي كذلك، فبأي أعجوبة تبشِّرونني؟');
INSERT INTO Quran__Arabic_Tafseer_Muyassar (QuranChapterID, VerseNumber, Rukuh, Sajdah, Manzil, HizbQuarter, VerseText) VALUES (15, 55, '0', '0', '0', '0', 'قالوا: بشَّرناك بالحق الذي أعلمَنا به الله، فلا تكن من اليائسين أن يولد لك.');
INSERT INTO Quran__Arabic_Tafseer_Muyassar (QuranChapterID, VerseNumber, Rukuh, Sajdah, Manzil, HizbQuarter, VerseText) VALUES (15, 56, '0', '0', '0', '0', 'قال: لا ييئس من رحمة ربه إلا الخاطئون المنصرفون عن طريق الحق. قال: فما الأمر الخطير الذي جئتم من أجله -أيها المرسلون- من عند الله؟');
INSERT INTO Quran__Arabic_Tafseer_Muyassar (QuranChapterID, VerseNumber, Rukuh, Sajdah, Manzil, HizbQuarter, VerseText) VALUES (15, 57, '0', '0', '0', '0', 'قال: لا ييئس من رحمة ربه إلا الخاطئون المنصرفون عن طريق الحق. قال: فما الأمر الخطير الذي جئتم من أجله -أيها المرسلون- من عند الله؟');
INSERT INTO Quran__Arabic_Tafseer_Muyassar (QuranChapterID, VerseNumber, Rukuh, Sajdah, Manzil, HizbQuarter, VerseText) VALUES (15, 58, '0', '0', '0', '0', 'قالوا: إن الله أرسلنا لإهلاك قوم لوط المشركين الضالين إلا لوطًا وأهله المؤمنين به، فلن نهلكهم وسننجيهم أجمعين، لكن زوجته الكافرة قضينا بأمر الله بإهلاكها مع الباقين في العذاب.');
INSERT INTO Quran__Arabic_Tafseer_Muyassar (QuranChapterID, VerseNumber, Rukuh, Sajdah, Manzil, HizbQuarter, VerseText) VALUES (15, 59, '0', '0', '0', '0', 'قالوا: إن الله أرسلنا لإهلاك قوم لوط المشركين الضالين إلا لوطًا وأهله المؤمنين به، فلن نهلكهم وسننجيهم أجمعين، لكن زوجته الكافرة قضينا بأمر الله بإهلاكها مع الباقين في العذاب.');
INSERT INTO Quran__Arabic_Tafseer_Muyassar (QuranChapterID, VerseNumber, Rukuh, Sajdah, Manzil, HizbQuarter, VerseText) VALUES (15, 60, '1', '0', '0', '0', 'قالوا: إن الله أرسلنا لإهلاك قوم لوط المشركين الضالين إلا لوطًا وأهله المؤمنين به، فلن نهلكهم وسننجيهم أجمعين، لكن زوجته الكافرة قضينا بأمر الله بإهلاكها مع الباقين في العذاب.');
INSERT INTO Quran__Arabic_Tafseer_Muyassar (QuranChapterID, VerseNumber, Rukuh, Sajdah, Manzil, HizbQuarter, VerseText) VALUES (15, 61, '0', '0', '0', '0', 'فلما وصل الملائكة المرسلون إلى لوط، قال لهم: إنكم قوم غير معروفين لي.');
INSERT INTO Quran__Arabic_Tafseer_Muyassar (QuranChapterID, VerseNumber, Rukuh, Sajdah, Manzil, HizbQuarter, VerseText) VALUES (15, 62, '0', '0', '0', '0', 'فلما وصل الملائكة المرسلون إلى لوط، قال لهم: إنكم قوم غير معروفين لي.');
INSERT INTO Quran__Arabic_Tafseer_Muyassar (QuranChapterID, VerseNumber, Rukuh, Sajdah, Manzil, HizbQuarter, VerseText) VALUES (15, 63, '0', '0', '0', '0', 'قالوا: لا تَخَفْ، فإنَّا جئنا بالعذاب الذي كان يشك فيه قومك ولا يُصَدِّقون، وجئناك بالحق من عند الله، وإنا لصادقون، فاخرج مِن بينهم ومعك أهلك المؤمنون، بعد مرور جزء من الليل، وسر أنت وراءهم؛ لئلا يتخلف منهم أحد فيناله العذاب، واحذروا أن يلتفت منكم أحد، وأسرعوا إلى حيث أمركم الله؛ لتكونوا في مكان أمين.');
INSERT INTO Quran__Arabic_Tafseer_Muyassar (QuranChapterID, VerseNumber, Rukuh, Sajdah, Manzil, HizbQuarter, VerseText) VALUES (15, 64, '0', '0', '0', '0', 'قالوا: لا تَخَفْ، فإنَّا جئنا بالعذاب الذي كان يشك فيه قومك ولا يُصَدِّقون، وجئناك بالحق من عند الله، وإنا لصادقون، فاخرج مِن بينهم ومعك أهلك المؤمنون، بعد مرور جزء من الليل، وسر أنت وراءهم؛ لئلا يتخلف منهم أحد فيناله العذاب، واحذروا أن يلتفت منكم أحد، وأسرعوا إلى حيث أمركم الله؛ لتكونوا في مكان أمين.');
INSERT INTO Quran__Arabic_Tafseer_Muyassar (QuranChapterID, VerseNumber, Rukuh, Sajdah, Manzil, HizbQuarter, VerseText) VALUES (15, 65, '0', '0', '0', '0', 'قالوا: لا تَخَفْ، فإنَّا جئنا بالعذاب الذي كان يشك فيه قومك ولا يُصَدِّقون، وجئناك بالحق من عند الله، وإنا لصادقون، فاخرج مِن بينهم ومعك أهلك المؤمنون، بعد مرور جزء من الليل، وسر أنت وراءهم؛ لئلا يتخلف منهم أحد فيناله العذاب، واحذروا أن يلتفت منكم أحد، وأسرعوا إلى حيث أمركم الله؛ لتكونوا في مكان أمين.');
INSERT INTO Quran__Arabic_Tafseer_Muyassar (QuranChapterID, VerseNumber, Rukuh, Sajdah, Manzil, HizbQuarter, VerseText) VALUES (15, 66, '0', '0', '0', '0', 'وأوحينا إلى لوط أن قومك مستأصَلون بالهلاك عن آخرهم عند طلوع الصبح.');
INSERT INTO Quran__Arabic_Tafseer_Muyassar (QuranChapterID, VerseNumber, Rukuh, Sajdah, Manzil, HizbQuarter, VerseText) VALUES (15, 67, '0', '0', '0', '0', 'وجاء أهل مدينة لوط إلى لوط حين علموا بمن عنده من الضيوف، وهم فرحون يستبشرون بضيوفه؛ ليأخذوهم ويفعلوا بهم الفاحشة.');
INSERT INTO Quran__Arabic_Tafseer_Muyassar (QuranChapterID, VerseNumber, Rukuh, Sajdah, Manzil, HizbQuarter, VerseText) VALUES (15, 68, '0', '0', '0', '0', 'قال لهم لوط: إن هؤلاء ضيفي وهم في حمايتي فلا تفضحوني، وخافوا عقاب الله، ولا تتعرضوا لهم، فتوقعوني في الذل والهوان بإيذائكم لضيوفي.');
INSERT INTO Quran__Arabic_Tafseer_Muyassar (QuranChapterID, VerseNumber, Rukuh, Sajdah, Manzil, HizbQuarter, VerseText) VALUES (15, 69, '0', '0', '0', '0', 'قال لهم لوط: إن هؤلاء ضيفي وهم في حمايتي فلا تفضحوني، وخافوا عقاب الله، ولا تتعرضوا لهم، فتوقعوني في الذل والهوان بإيذائكم لضيوفي.');
INSERT INTO Quran__Arabic_Tafseer_Muyassar (QuranChapterID, VerseNumber, Rukuh, Sajdah, Manzil, HizbQuarter, VerseText) VALUES (15, 70, '0', '0', '0', '0', 'قال قومه: أولم نَنْهَكَ أن تضيِّف أحدا من العالمين؛ لأنَّا نريد منهم الفاحشة؟');
INSERT INTO Quran__Arabic_Tafseer_Muyassar (QuranChapterID, VerseNumber, Rukuh, Sajdah, Manzil, HizbQuarter, VerseText) VALUES (15, 71, '0', '0', '0', '0', 'قال لوط لهم: هؤلاء نساؤكم بناتي فتزوَّجوهن إن كنتم تريدون قضاء وطركم، وسماهن بناته؛ لأن نبي الأمة بمنزلة الأب لهم، ولا تفعلوا ما حرَّم الله عليكم من إتيان الرجال.');
INSERT INTO Quran__Arabic_Tafseer_Muyassar (QuranChapterID, VerseNumber, Rukuh, Sajdah, Manzil, HizbQuarter, VerseText) VALUES (15, 72, '0', '0', '0', '0', 'يقسم الخالق بمن يشاء وبما يشاء، أما المخلوق فلا يجوز له القسم إلا بالله، وقد أقسم الله تعالى بحياة محمد صلى الله عليه وسلم تشريفًا له. إن قوم لوط في غفلة شديدة يترددون ويتمادون، حتى حلَّتْ بهم صاعقة العذاب وقت شروق الشمس.');
INSERT INTO Quran__Arabic_Tafseer_Muyassar (QuranChapterID, VerseNumber, Rukuh, Sajdah, Manzil, HizbQuarter, VerseText) VALUES (15, 73, '0', '0', '0', '0', 'يقسم الخالق بمن يشاء وبما يشاء، أما المخلوق فلا يجوز له القسم إلا بالله، وقد أقسم الله تعالى بحياة محمد صلى الله عليه وسلم تشريفًا له. إن قوم لوط في غفلة شديدة يترددون ويتمادون، حتى حلَّتْ بهم صاعقة العذاب وقت شروق الشمس.');
INSERT INTO Quran__Arabic_Tafseer_Muyassar (QuranChapterID, VerseNumber, Rukuh, Sajdah, Manzil, HizbQuarter, VerseText) VALUES (15, 74, '0', '0', '0', '0', 'فقلبنا قُراهم فجعلنا عاليها سافلها، وأمطرنا عليهم حجارة من طين متصلب متين.');
INSERT INTO Quran__Arabic_Tafseer_Muyassar (QuranChapterID, VerseNumber, Rukuh, Sajdah, Manzil, HizbQuarter, VerseText) VALUES (15, 75, '0', '0', '0', '0', 'إن فيما أصابهم لَعظاتٍ للناظرين المعتبرين، وإن قراهم لفي طريق ثابت يراها المسافرون المارُّون بها. إن في إهلاكنا لهم لَدلالةً بيِّنةً للمصدقين العاملين بشرع الله.');
INSERT INTO Quran__Arabic_Tafseer_Muyassar (QuranChapterID, VerseNumber, Rukuh, Sajdah, Manzil, HizbQuarter, VerseText) VALUES (15, 76, '0', '0', '0', '0', 'إن فيما أصابهم لَعظاتٍ للناظرين المعتبرين، وإن قراهم لفي طريق ثابت يراها المسافرون المارُّون بها. إن في إهلاكنا لهم لَدلالةً بيِّنةً للمصدقين العاملين بشرع الله.');
INSERT INTO Quran__Arabic_Tafseer_Muyassar (QuranChapterID, VerseNumber, Rukuh, Sajdah, Manzil, HizbQuarter, VerseText) VALUES (15, 77, '0', '0', '0', '0', 'إن فيما أصابهم لَعظاتٍ للناظرين المعتبرين، وإن قراهم لفي طريق ثابت يراها المسافرون المارُّون بها. إن في إهلاكنا لهم لَدلالةً بيِّنةً للمصدقين العاملين بشرع الله.');
INSERT INTO Quran__Arabic_Tafseer_Muyassar (QuranChapterID, VerseNumber, Rukuh, Sajdah, Manzil, HizbQuarter, VerseText) VALUES (15, 78, '0', '0', '0', '0', 'وقد كان أصحاب المدينة الملتفة الشجر -وهم قوم شعيب- ظالمين لأنفسهم لكفرهم بالله ورسولهم الكريم، فانتقمنا منهم بالرجفة وعذاب يوم الظلة، وإن مساكن قوم لوط وشعيب لفي طريق واضح يمرُّ بهما الناس في سفرهم فيعتبرون.');
INSERT INTO Quran__Arabic_Tafseer_Muyassar (QuranChapterID, VerseNumber, Rukuh, Sajdah, Manzil, HizbQuarter, VerseText) VALUES (15, 79, '1', '0', '0', '0', 'وقد كان أصحاب المدينة الملتفة الشجر -وهم قوم شعيب- ظالمين لأنفسهم لكفرهم بالله ورسولهم الكريم، فانتقمنا منهم بالرجفة وعذاب يوم الظلة، وإن مساكن قوم لوط وشعيب لفي طريق واضح يمرُّ بهما الناس في سفرهم فيعتبرون.');
INSERT INTO Quran__Arabic_Tafseer_Muyassar (QuranChapterID, VerseNumber, Rukuh, Sajdah, Manzil, HizbQuarter, VerseText) VALUES (15, 80, '0', '0', '0', '0', 'ولقد كذَّب سكان "وادي الحِجْر" صالحًا عليه السلام، وهم ثمود فكانوا بذلك مكذبين لكل المرسلين؛ لأن من كذَّب نبيًا فقد كذَّب الأنبياء كلهم؛ لأنهم على دين واحد.');
INSERT INTO Quran__Arabic_Tafseer_Muyassar (QuranChapterID, VerseNumber, Rukuh, Sajdah, Manzil, HizbQuarter, VerseText) VALUES (15, 81, '0', '0', '0', '0', 'وآتينا قوم صالح آياتنا الدالة على صحة ما جاءهم به صالح من الحق، ومن جملتها الناقة، فلم يعتبروا بها، وكانوا عنها مبتعدين معرضين.');
INSERT INTO Quran__Arabic_Tafseer_Muyassar (QuranChapterID, VerseNumber, Rukuh, Sajdah, Manzil, HizbQuarter, VerseText) VALUES (15, 82, '0', '0', '0', '0', 'وكانوا ينحتون الجبال، فيتخذون منها بيوتًا، وهم آمنون من أن تسقط عليهم أو تخرب.');
INSERT INTO Quran__Arabic_Tafseer_Muyassar (QuranChapterID, VerseNumber, Rukuh, Sajdah, Manzil, HizbQuarter, VerseText) VALUES (15, 83, '0', '0', '0', '0', 'فأخذتهم صاعقة العذاب وقت الصباح مبكرين، فما دفع عنهم عذابَ الله الأموالُ والحصونُ في الجبال، ولا ما أُعطوه من قوة وجاه.');
INSERT INTO Quran__Arabic_Tafseer_Muyassar (QuranChapterID, VerseNumber, Rukuh, Sajdah, Manzil, HizbQuarter, VerseText) VALUES (15, 84, '0', '0', '0', '0', 'فأخذتهم صاعقة العذاب وقت الصباح مبكرين، فما دفع عنهم عذابَ الله الأموالُ والحصونُ في الجبال، ولا ما أُعطوه من قوة وجاه.');
INSERT INTO Quran__Arabic_Tafseer_Muyassar (QuranChapterID, VerseNumber, Rukuh, Sajdah, Manzil, HizbQuarter, VerseText) VALUES (15, 85, '0', '0', '0', '0', 'وما خلَقْنا السموات والأرض وما بينهما إلا بالحق دالتين على كمال خالقهما واقتداره، وأنه الذي لا تنبغي العبادة إلا له وحده لا شريك له. وإن الساعة التي تقوم فيها القيامة لآتية لا محالة؛ لتوفَّى كل نفس بما عملت، فاعف -أيها الرسول- عن المشركين، واصفح عنهم وتجاوز عما يفعلونه.');
INSERT INTO Quran__Arabic_Tafseer_Muyassar (QuranChapterID, VerseNumber, Rukuh, Sajdah, Manzil, HizbQuarter, VerseText) VALUES (15, 86, '0', '0', '0', '0', 'إنَّ ربك هو الخلاَّق لكل شيء، العليم به، فلا يعجزه شيء في الأرض ولا في السماء، ولا يخفى عليه.');
INSERT INTO Quran__Arabic_Tafseer_Muyassar (QuranChapterID, VerseNumber, Rukuh, Sajdah, Manzil, HizbQuarter, VerseText) VALUES (15, 87, '0', '0', '0', '0', 'ولقد آتيناك -أيها النبي- فاتحة القرآن، وهي سبع آيات تكرر في كل صلاة، وآتيناك القرآن العظيم.');
INSERT INTO Quran__Arabic_Tafseer_Muyassar (QuranChapterID, VerseNumber, Rukuh, Sajdah, Manzil, HizbQuarter, VerseText) VALUES (15, 88, '0', '0', '0', '0', 'لا تنظر بعينيك وتتمنَّ ما مَتَّعْنا به أصنافًا من الكفار مِن مُتَع الدنيا، ولا تحزن على كفرهم، وتواضَعْ للمؤمنين بالله ورسوله.');
INSERT INTO Quran__Arabic_Tafseer_Muyassar (QuranChapterID, VerseNumber, Rukuh, Sajdah, Manzil, HizbQuarter, VerseText) VALUES (15, 89, '0', '0', '0', '0', 'وقل: إني أنا المنذر الموضِّح لما يهتدي به الناس إلى الإيمان بالله رب العالمين، ومنذركم أن يصيبكم العذاب، كما أنزله الله على الذين قسَّموا القرآن، فآمنوا ببعضه، وكفروا ببعضه الآخر من اليهود والنصارى وغيرهم.');
INSERT INTO Quran__Arabic_Tafseer_Muyassar (QuranChapterID, VerseNumber, Rukuh, Sajdah, Manzil, HizbQuarter, VerseText) VALUES (15, 90, '0', '0', '0', '0', 'وقل: إني أنا المنذر الموضِّح لما يهتدي به الناس إلى الإيمان بالله رب العالمين، ومنذركم أن يصيبكم العذاب، كما أنزله الله على الذين قسَّموا القرآن، فآمنوا ببعضه، وكفروا ببعضه الآخر من اليهود والنصارى وغيرهم.');
INSERT INTO Quran__Arabic_Tafseer_Muyassar (QuranChapterID, VerseNumber, Rukuh, Sajdah, Manzil, HizbQuarter, VerseText) VALUES (15, 91, '0', '0', '0', '0', 'وهم الذين جعلوا القرآن أقسامًا وأجزاء، فمنهم من يقول: سحر، ومنهم من يقول كَهَانة، ومنهم من يقول غير ذلك، يصرِّفونه بحسب أهوائهم؛ ليصدوا الناس عن الهدى.');
INSERT INTO Quran__Arabic_Tafseer_Muyassar (QuranChapterID, VerseNumber, Rukuh, Sajdah, Manzil, HizbQuarter, VerseText) VALUES (15, 92, '0', '0', '0', '0', 'فوربك لنحاسبنَّهم يوم القيامة ولنجزينهم أجمعين، عن تقسيمهم للقرآن بافتراءاتهم، وتحريفه وتبديله، وغير ذلك مما كانوا يعملونه من عبادة الأوثان، ومن المعاصي والآثام. وفي هذا ترهيب وزجر لهم من الإقامة على هذه الأفعال القبيحة.');
INSERT INTO Quran__Arabic_Tafseer_Muyassar (QuranChapterID, VerseNumber, Rukuh, Sajdah, Manzil, HizbQuarter, VerseText) VALUES (15, 93, '0', '0', '0', '0', 'فوربك لنحاسبنَّهم يوم القيامة ولنجزينهم أجمعين، عن تقسيمهم للقرآن بافتراءاتهم، وتحريفه وتبديله، وغير ذلك مما كانوا يعملونه من عبادة الأوثان، ومن المعاصي والآثام. وفي هذا ترهيب وزجر لهم من الإقامة على هذه الأفعال القبيحة.');
INSERT INTO Quran__Arabic_Tafseer_Muyassar (QuranChapterID, VerseNumber, Rukuh, Sajdah, Manzil, HizbQuarter, VerseText) VALUES (15, 94, '0', '0', '0', '0', 'فاجهر بدعوة الحق التي أمرك الله بها، ولا تبال بالمشركين، فقد برَّأك الله ممَّا يقولون.');
INSERT INTO Quran__Arabic_Tafseer_Muyassar (QuranChapterID, VerseNumber, Rukuh, Sajdah, Manzil, HizbQuarter, VerseText) VALUES (15, 95, '0', '0', '0', '0', 'إنَّا كَفَيْناك المستهزئين الساخرين من زعماء قريش، الذين اتخذوا شريكًا مع الله من الأوثان وغيرها، فسوف يعلمون عاقبة عملهم في الدنيا والآخرة.');
INSERT INTO Quran__Arabic_Tafseer_Muyassar (QuranChapterID, VerseNumber, Rukuh, Sajdah, Manzil, HizbQuarter, VerseText) VALUES (15, 96, '0', '0', '0', '0', 'إنَّا كَفَيْناك المستهزئين الساخرين من زعماء قريش، الذين اتخذوا شريكًا مع الله من الأوثان وغيرها، فسوف يعلمون عاقبة عملهم في الدنيا والآخرة.');
INSERT INTO Quran__Arabic_Tafseer_Muyassar (QuranChapterID, VerseNumber, Rukuh, Sajdah, Manzil, HizbQuarter, VerseText) VALUES (15, 97, '0', '0', '0', '0', 'ولقد نعلم بانقباض صدرك -أيها الرسول-؛ بسبب ما يقوله المشركون فيك وفي دعوتك.');
INSERT INTO Quran__Arabic_Tafseer_Muyassar (QuranChapterID, VerseNumber, Rukuh, Sajdah, Manzil, HizbQuarter, VerseText) VALUES (15, 98, '0', '0', '0', '0', 'فافزع إلى ربك عند ضيق صدرك، وسَبِّح بحمده شاكرًا له مثنيا عليه، وكن من المصلِّين لله العابدين له، فإن ذلك يكفيك ما أهمَّك.');
INSERT INTO Quran__Arabic_Tafseer_Muyassar (QuranChapterID, VerseNumber, Rukuh, Sajdah, Manzil, HizbQuarter, VerseText) VALUES (15, 99, '1', '0', '0', '0', 'واستمِرَّ في عبادة ربك مدة حياتك حتى يأتيك اليقين، وهو الموت. وامتثل رسول الله صلى الله عليه وسلم أمر ربه، فلم يزل دائبًا في عبادة الله، حتى أتاه اليقين من ربه.');
INSERT INTO Quran__Arabic_Tafseer_Muyassar (QuranChapterID, VerseNumber, Rukuh, Sajdah, Manzil, HizbQuarter, VerseText) VALUES (16, 1, '0', '0', '0', '1', 'قَرُب قيام الساعة وقضاء الله بعذابكم -أيها الكفار- فلا تستعجلوا العذاب استهزاء بوعيد الرسول لكم. تنزَّه الله سبحانه وتعالى عن الشرك والشركاء.');
INSERT INTO Quran__Arabic_Tafseer_Muyassar (QuranChapterID, VerseNumber, Rukuh, Sajdah, Manzil, HizbQuarter, VerseText) VALUES (16, 2, '0', '0', '0', '0', 'ينزِّل الله الملائكة بالوحي مِن أمره على مَن يشاء من عباده المرسلين: بأن خوِّفوا الناس من الشرك، وأنه لا معبود بحق إلا أنا، فاتقون بأداء فرائضي وإفرادي بالعبادة والإخلاص.');
INSERT INTO Quran__Arabic_Tafseer_Muyassar (QuranChapterID, VerseNumber, Rukuh, Sajdah, Manzil, HizbQuarter, VerseText) VALUES (16, 3, '0', '0', '0', '0', 'خلق الله السموات والأرض بالحق؛ ليستدِل بهما العباد على عظمة خالقهما، وأنه وحده المستحق للعبادة، تنزَّه -سبحانه- وتعاظم عن شركهم.');
INSERT INTO Quran__Arabic_Tafseer_Muyassar (QuranChapterID, VerseNumber, Rukuh, Sajdah, Manzil, HizbQuarter, VerseText) VALUES (16, 4, '0', '0', '0', '0', 'خَلَق الإنسان من ماء مهين فإذا به يَقْوى ويغترُّ، فيصبح شديد الخصومة والجدال لربه في إنكار البعث، وغير ذلك، كقوله: "مَن يُحْيِ الْعِظَامَ وَهِيَ رَمِيمٌ"، ونسي الله الذي خلقه من العدم.');
INSERT INTO Quran__Arabic_Tafseer_Muyassar (QuranChapterID, VerseNumber, Rukuh, Sajdah, Manzil, HizbQuarter, VerseText) VALUES (16, 5, '0', '0', '0', '0', 'والأنعامَ من الإبل والبقر والغنم خلقها الله لكم -أيها الناس- وجعل في أصوافها وأوبارها الدفء، ومنافع أُخر في ألبانها وجلودها وركوبها، ومنها ما تأكلون.');
INSERT INTO Quran__Arabic_Tafseer_Muyassar (QuranChapterID, VerseNumber, Rukuh, Sajdah, Manzil, HizbQuarter, VerseText) VALUES (16, 6, '0', '0', '0', '0', 'ولكم فيها زينة تُدْخل السرور عليكم عندما تَرُدُّونها إلى منازلها في المساء، وعندما تُخْرجونها للمرعى في الصباح.');
INSERT INTO Quran__Arabic_Tafseer_Muyassar (QuranChapterID, VerseNumber, Rukuh, Sajdah, Manzil, HizbQuarter, VerseText) VALUES (16, 7, '0', '0', '0', '0', 'وتحمل هذه الأنعام ما ثَقُل من أمتعتكم إلى بلد بعيد، لم تكونوا مستطيعين الوصول إليه إلا بجهد شديد من أنفسكم ومشقة عظيمة، إن ربكم لَرؤوف رحيم بكم، حيث سخَّر لكم ما تحتاجون إليه، فله الحمد وله الشكر.');
INSERT INTO Quran__Arabic_Tafseer_Muyassar (QuranChapterID, VerseNumber, Rukuh, Sajdah, Manzil, HizbQuarter, VerseText) VALUES (16, 8, '0', '0', '0', '0', 'وخلق لكم الخيل والبغال والحمير؛ لكي تركبوها، ولتكون جمَالا لكم ومنظرًا حسنًا؛ ويخلق لكم من وسائل الركوب وغيرها ما لا عِلْمَ لكم به؛ لتزدادوا إيمانًا به وشكرا له.');
INSERT INTO Quran__Arabic_Tafseer_Muyassar (QuranChapterID, VerseNumber, Rukuh, Sajdah, Manzil, HizbQuarter, VerseText) VALUES (16, 9, '1', '0', '0', '0', 'وعلى الله بيان الطريق المستقيم لِهدايتكم، وهو الإسلام، ومن الطرق ما هو مائل لا يُوصل إلى الهداية، وهو كل ما خالف الإسلام من الملل والنحل. ولو شاء الله هدايتكم لهداكم جميعًا للإيمان.');
INSERT INTO Quran__Arabic_Tafseer_Muyassar (QuranChapterID, VerseNumber, Rukuh, Sajdah, Manzil, HizbQuarter, VerseText) VALUES (16, 10, '0', '0', '0', '0', 'هو الذي أنزل لكم من السحاب مطرًا، فجعل لكم منه ماءً تشربونه، وأخرج لكم به شجرًا تَرْعَوْن فيه دوابّكم، ويعود عليكم دَرُّها ونفْعُها.');
INSERT INTO Quran__Arabic_Tafseer_Muyassar (QuranChapterID, VerseNumber, Rukuh, Sajdah, Manzil, HizbQuarter, VerseText) VALUES (16, 11, '0', '0', '0', '0', 'يُخرج لكم من الأرض بهذا الماء الواحد الزروع المختلفة، ويُخرج به الزيتون والنخيل والأعناب، ويُخرج به كل أنواع الثمار والفواكه. إن في ذلك الإخراج لدلالةً واضحة لقوم يتأملون، فيعتبرون.');
INSERT INTO Quran__Arabic_Tafseer_Muyassar (QuranChapterID, VerseNumber, Rukuh, Sajdah, Manzil, HizbQuarter, VerseText) VALUES (16, 12, '0', '0', '0', '0', 'وسخَّر لكم الليل لراحتكم، والنهار لمعاشكم، وسخَّر لكم الشمس ضياء، والقمر نورًا ولمعرفة السنين والحساب، وغير ذلك من المنافع، والنجوم في السماء مذللات لكم بأمر الله لمعرفة الأوقات، ونضج الثمار والزروع، والاهتداء بها في الظلمات. إن في ذلك التسخير لَدلائلَ واضحةً لقوم سيعقلون عن الله حججه وبراهينه.');
INSERT INTO Quran__Arabic_Tafseer_Muyassar (QuranChapterID, VerseNumber, Rukuh, Sajdah, Manzil, HizbQuarter, VerseText) VALUES (16, 13, '0', '0', '0', '0', 'وسخَّر ما خلقه لكم في الأرض من الدوابِّ والثمار والمعادن، وغير ذلك مما تختلف ألوانه ومنافعه. إن في ذلك الخَلْق واختلاف الألوان والمنافع لَعبرةً لقوم يتعظون، ويعلمون أنَّ في تسخير هذه الأشياء علاماتٍ على وحدانية الله تعالى وإفراده بالعبادة.');
INSERT INTO Quran__Arabic_Tafseer_Muyassar (QuranChapterID, VerseNumber, Rukuh, Sajdah, Manzil, HizbQuarter, VerseText) VALUES (16, 14, '0', '0', '0', '0', 'وهو الذي سخَّر لكم البحر؛ لتأكلوا مما تصطادون من سمكه لحمًا طريًا، وتستخرجوا منه زينة تَلْبَسونها كاللؤلؤ والمرجان، وترى السفن العظيمة تشق وجه الماء تذهب وتجيء، وتركبونها؛ لتطلبوا رزق الله بالتجارة والربح فيها، ولعلكم تشكرون لله تعالى على عظيم إنعامه عليكم، فلا تعبدون غيره.');
INSERT INTO Quran__Arabic_Tafseer_Muyassar (QuranChapterID, VerseNumber, Rukuh, Sajdah, Manzil, HizbQuarter, VerseText) VALUES (16, 15, '0', '0', '0', '0', 'وأرسى في الأرض جبالا تثبتها حتى لا تميل بكم، وجعل فيها أنهارًا؛ لتشربوا منها، وجعل فيها طرقًا؛ لتهتدوا بها في الوصول إلى الأماكن التي تقصدونها.');
INSERT INTO Quran__Arabic_Tafseer_Muyassar (QuranChapterID, VerseNumber, Rukuh, Sajdah, Manzil, HizbQuarter, VerseText) VALUES (16, 16, '0', '0', '0', '0', 'وجعل في الأرض معالم تستدلُّون بها على الطرق نهارًا، كما جعل النجوم للاهتداء بها ليلا.');
INSERT INTO Quran__Arabic_Tafseer_Muyassar (QuranChapterID, VerseNumber, Rukuh, Sajdah, Manzil, HizbQuarter, VerseText) VALUES (16, 17, '0', '0', '0', '0', 'أتجعلون الله الذي يخلق كل هذه الأشياء وغيرها في استحقاق العبادة كالآلهة المزعومة التي لا تخلق شيئًا؟ أفلا تتذكرون عظمة الله، فتفردوه بالعبادة؟');
INSERT INTO Quran__Arabic_Tafseer_Muyassar (QuranChapterID, VerseNumber, Rukuh, Sajdah, Manzil, HizbQuarter, VerseText) VALUES (16, 18, '0', '0', '0', '0', 'وإن تحاولوا حَصْرَ نِعَم الله عليكم لا تَفُوا بحَصْرها؛ لكثرتها وتنوعها. إن الله لَغفور لكم رحيم بكم؛ إذ يتجاوز عن تقصيركم في أداء شكر النعم، ولا يقطعها عنكم لتفريطكم، ولا يعاجلكم بالعقوبة.');
INSERT INTO Quran__Arabic_Tafseer_Muyassar (QuranChapterID, VerseNumber, Rukuh, Sajdah, Manzil, HizbQuarter, VerseText) VALUES (16, 19, '0', '0', '0', '0', 'والله سبحانه يعلم كل أعمالكم، سواء ما تخفونه منها في نفوسكم وما تظهرونه لغيركم، وسيجازيكم عليها.');
INSERT INTO Quran__Arabic_Tafseer_Muyassar (QuranChapterID, VerseNumber, Rukuh, Sajdah, Manzil, HizbQuarter, VerseText) VALUES (16, 20, '0', '0', '0', '0', 'والآلهة التي يعبدها المشركون لا تخلق شيئًا وإن صَغُر، فهي مخلوقات صنعها الكفار بأيديهم، فكيف يعبدونها؟');
INSERT INTO Quran__Arabic_Tafseer_Muyassar (QuranChapterID, VerseNumber, Rukuh, Sajdah, Manzil, HizbQuarter, VerseText) VALUES (16, 21, '1', '0', '0', '0', 'هم جميعًا جمادات لا حياة فيها ولا تشعر بالوقت الذي يبعث الله فيه عابديها، وهي معهم ليُلقى بهم جميعًا في النار يوم القيامة.');
INSERT INTO Quran__Arabic_Tafseer_Muyassar (QuranChapterID, VerseNumber, Rukuh, Sajdah, Manzil, HizbQuarter, VerseText) VALUES (16, 22, '0', '0', '0', '0', 'إلهكم المستحق وحده للعبادة هو الله الإله الواحد، فالذين لا يؤمنون بالبعث قلوبهم جاحدة وحدانيته سبحانه؛ لعدم خوفهم من عقابه، فهم متكبرون عن قبول الحق، وعبادة الله وحده.');
INSERT INTO Quran__Arabic_Tafseer_Muyassar (QuranChapterID, VerseNumber, Rukuh, Sajdah, Manzil, HizbQuarter, VerseText) VALUES (16, 23, '0', '0', '0', '0', 'حقًا أنَّ الله يعلم ما يخفونه مِن عقائد وأقوال وأفعال، وما يظهرونه منها، وسيجازيهم على ذلك، إنه عز وجل لا يحب المستكبرين عن عبادته والانقياد له، وسيجازيهم على ذلك.');
INSERT INTO Quran__Arabic_Tafseer_Muyassar (QuranChapterID, VerseNumber, Rukuh, Sajdah, Manzil, HizbQuarter, VerseText) VALUES (16, 24, '0', '0', '0', '0', 'وإذا سُئِل هؤلاء المشركون عمَّا نزل على النبي محمد صلى الله عليه وسلم قالوا كذبًا وزورًا: ما أتى إلا بقصص السابقين وأباطيلهم.');
INSERT INTO Quran__Arabic_Tafseer_Muyassar (QuranChapterID, VerseNumber, Rukuh, Sajdah, Manzil, HizbQuarter, VerseText) VALUES (16, 25, '1', '0', '0', '0', 'ستكون عاقبتهم أن يحملوا آثامهم كاملة يوم القيامة -لا يُغْفَر لهم منها شيء - ويَحْملوا من آثام الذين كذبوا عليهم؛ ليبعدوهم عن الإسلام من غير نقص من آثامهم. ألا قَبُحَ ما يحملونه من آثام.');
INSERT INTO Quran__Arabic_Tafseer_Muyassar (QuranChapterID, VerseNumber, Rukuh, Sajdah, Manzil, HizbQuarter, VerseText) VALUES (16, 26, '0', '0', '0', '0', 'قد دبَّر الكفار من قَبْل هؤلاء المشركين المكايد لرسلهم، وما جاءوا به من دعوة الحق، فأتى الله بنيانهم من أساسه وقاعدته، فسقط عليهم السقف مِن فوقهم، وأتاهم الهلاك مِن مأمنهم، من حيث لا يحتسبون ولا يتوقعون أنه يأتيهم منه.');
INSERT INTO Quran__Arabic_Tafseer_Muyassar (QuranChapterID, VerseNumber, Rukuh, Sajdah, Manzil, HizbQuarter, VerseText) VALUES (16, 27, '0', '0', '0', '0', 'ثم يوم القيامة يفضحهم الله بالعذاب ويذلُّهم به، ويقول: أين شركائي من الآلهة التي عبدتموها من دوني؛ ليدفعوا عنكم العذاب، وقد كنتم تحاربون الأنبياء والمؤمنين وتعادونهم لأجلهم؟ قال العلماء الربانيون: إن الذل في هذا اليوم والعذاب على الكافرين بالله ورسله، الذين تقبض الملائكة أرواحهم في حال ظلمهم لأنفسهم بالكفر، فاستسْلَموا لأمر الله حين رأوا الموت، وأنكروا ما كانوا يعبدون من دون الله، وقالوا: ما كنا نعمل شيئًا من المعاصي، فيقال لهم: كَذَبْتم، قد كنتم تعملونها، إن الله عليم بأعمالكم كلها، وسيجازيكم عليها.');
INSERT INTO Quran__Arabic_Tafseer_Muyassar (QuranChapterID, VerseNumber, Rukuh, Sajdah, Manzil, HizbQuarter, VerseText) VALUES (16, 28, '0', '0', '0', '0', 'ثم يوم القيامة يفضحهم الله بالعذاب ويذلُّهم به، ويقول: أين شركائي من الآلهة التي عبدتموها من دوني؛ ليدفعوا عنكم العذاب، وقد كنتم تحاربون الأنبياء والمؤمنين وتعادونهم لأجلهم؟ قال العلماء الربانيون: إن الذل في هذا اليوم والعذاب على الكافرين بالله ورسله، الذين تقبض الملائكة أرواحهم في حال ظلمهم لأنفسهم بالكفر، فاستسْلَموا لأمر الله حين رأوا الموت، وأنكروا ما كانوا يعبدون من دون الله، وقالوا: ما كنا نعمل شيئًا من المعاصي، فيقال لهم: كَذَبْتم، قد كنتم تعملونها، إن الله عليم بأعمالكم كلها، وسيجازيكم عليها.');
INSERT INTO Quran__Arabic_Tafseer_Muyassar (QuranChapterID, VerseNumber, Rukuh, Sajdah, Manzil, HizbQuarter, VerseText) VALUES (16, 29, '0', '0', '0', '0', 'فادخلوا أبواب جهنم، لا تخرجون منها أبدًا، فلبئست مقرًا للذين تكبَّروا عن الإيمان بالله وعن عبادته وحده وطاعته.');
INSERT INTO Quran__Arabic_Tafseer_Muyassar (QuranChapterID, VerseNumber, Rukuh, Sajdah, Manzil, HizbQuarter, VerseText) VALUES (16, 30, '0', '0', '0', '1', 'وإذا قيل للمؤمنين الخائفين من الله: ما الذي أنزل الله على النبي محمد صلى الله عليه وسلم؟ قالوا: أنزل الله عليه الخير والهدى. للذين آمنوا بالله ورسوله في هذه الدنيا، ودَعَوْا عباد الله إلى الإيمان والعمل الصالح، مَكْرُمَة كبيرة من النصر لهم في الدنيا، وسَعَة الرزق، ولَدار الآخرة لهم خير وأعظم مما أُوتوه في الدنيا، ولَنِعْم دارُ المتقين الخائفين من الله الآخرةُ.');
INSERT INTO Quran__Arabic_Tafseer_Muyassar (QuranChapterID, VerseNumber, Rukuh, Sajdah, Manzil, HizbQuarter, VerseText) VALUES (16, 31, '0', '0', '0', '0', 'جنات إقامة لهم، يستقرون فيها، لا يخرجون منها أبدًا، تجري من تحت أشجارها وقصورها الأنهار، لهم فيها كل ما تشتهيه أنفسهم، بمثل هذا الجزاء الطيب يجزي الله أهل خشيته وتقواه الذين تقبض الملائكةُ أرواحَهم، وقلوبُهم طاهرة من الكفر، تقول الملائكة لهم: سلام عليكم، تحية خاصة لكم وسلامة من كل آفة، ادخلوا الجنة بما كنتم تعملون من الإيمان بالله والانقياد لأمره.');
INSERT INTO Quran__Arabic_Tafseer_Muyassar (QuranChapterID, VerseNumber, Rukuh, Sajdah, Manzil, HizbQuarter, VerseText) VALUES (16, 32, '0', '0', '0', '0', 'جنات إقامة لهم، يستقرون فيها، لا يخرجون منها أبدًا، تجري من تحت أشجارها وقصورها الأنهار، لهم فيها كل ما تشتهيه أنفسهم، بمثل هذا الجزاء الطيب يجزي الله أهل خشيته وتقواه الذين تقبض الملائكةُ أرواحَهم، وقلوبُهم طاهرة من الكفر، تقول الملائكة لهم: سلام عليكم، تحية خاصة لكم وسلامة من كل آفة، ادخلوا الجنة بما كنتم تعملون من الإيمان بالله والانقياد لأمره.');
INSERT INTO Quran__Arabic_Tafseer_Muyassar (QuranChapterID, VerseNumber, Rukuh, Sajdah, Manzil, HizbQuarter, VerseText) VALUES (16, 33, '0', '0', '0', '0', 'ما ينتظر المشركون إلا أن تأتيهم الملائكة؛ لتقبض أرواحهم وهم على الكفر، أو يأتي أمر الله بعذاب عاجل يهلكهم، كما كذَّب هؤلاء كذَّب الكفار مِن قبلهم، فأهلكهم الله، وما ظلمهم الله بإهلاكهم، وإنزال العذاب بهم، ولكنهم هم الذين كانوا يظلمون أنفسهم بما جعلهم أهلا للعذاب.');
INSERT INTO Quran__Arabic_Tafseer_Muyassar (QuranChapterID, VerseNumber, Rukuh, Sajdah, Manzil, HizbQuarter, VerseText) VALUES (16, 34, '1', '0', '0', '0', 'فنزلت بهم عقوبة ذنوبهم التي عملوها، وأحاط بهم العذاب الذي كانوا يسخرون منه.');
INSERT INTO Quran__Arabic_Tafseer_Muyassar (QuranChapterID, VerseNumber, Rukuh, Sajdah, Manzil, HizbQuarter, VerseText) VALUES (16, 35, '0', '0', '0', '0', 'وقال المشركون: لو شاء الله أن نعبده وحده ما عبدنا أحدًا غيره، لا نحن ولا آباؤنا مِن قبلنا، ولا حَرَّمَنا شيئًا لم يحرمه، بمثل هذا الاحتجاج الباطل احتج الكفار السابقون، وهم كاذبون؛ فإن الله أمرهم ونهاهم ومكَّنهم من القيام بما كلَّفهم به، وجعل لهم قوة ومشيئة تصدر عنها أفعالهم، فاحتجاجهم بالقضاء والقدر من أبطل الباطل من بعد إنذار الرسل لهم، فليس على الرسل المنذِرين لهم إلا التبليغ الواضح لما كُلِّفوا به.');
INSERT INTO Quran__Arabic_Tafseer_Muyassar (QuranChapterID, VerseNumber, Rukuh, Sajdah, Manzil, HizbQuarter, VerseText) VALUES (16, 36, '0', '0', '0', '0', 'ولقد بعثنا في كل أمة سبقَتْ رسولا آمرًا لهم بعبادة الله وطاعته وحده وتَرْكِ عبادة غيره من الشياطين والأوثان والأموات وغير ذلك مما يتخذ من دون الله وليًا، فكان منهم مَن هدى الله، فاتبع المرسلين، ومنهم المعاند الذي اتبع سبيل الغيِّ، فوجبت عليه الضلالة، فلم يوفقه الله. فامشوا في الأرض، وأبصروا بأعينكم كيف كان مآل هؤلاء المكذبين، وماذا حلَّ بهم مِن دمار؛ لتعتبروا؟');
INSERT INTO Quran__Arabic_Tafseer_Muyassar (QuranChapterID, VerseNumber, Rukuh, Sajdah, Manzil, HizbQuarter, VerseText) VALUES (16, 37, '0', '0', '0', '0', 'إن تبذل -أيها الرسول- أقصى جهدك لهداية هؤلاء المشركين فاعلم أن الله لا يهدي مَن يضلُّ، وليس لهم من دون الله أحد ينصرهم، ويمنع عنهم عذابه.');
INSERT INTO Quran__Arabic_Tafseer_Muyassar (QuranChapterID, VerseNumber, Rukuh, Sajdah, Manzil, HizbQuarter, VerseText) VALUES (16, 38, '0', '0', '0', '0', 'وحلف هؤلاء المشركون بالله أيمانًا مغلَّظة أن الله لا يبعث مَن يموت بعدما بَلِيَ وتفرَّق، بلى سيبعثهم الله حتمًا، وعدًا عليه حقًا، ولكن أكثر الناس لا يعلمون قدرة الله على البعث، فينكرونه.');
INSERT INTO Quran__Arabic_Tafseer_Muyassar (QuranChapterID, VerseNumber, Rukuh, Sajdah, Manzil, HizbQuarter, VerseText) VALUES (16, 39, '0', '0', '0', '0', 'يبعث الله جميع العباد؛ ليبين لهم حقيقة البعث الذي اختلفوا فيه، ويعلم الكفار المنكرون له أنهم على باطل، وأنهم كاذبون حين حلفوا أنْ لا بعث.');
INSERT INTO Quran__Arabic_Tafseer_Muyassar (QuranChapterID, VerseNumber, Rukuh, Sajdah, Manzil, HizbQuarter, VerseText) VALUES (16, 40, '1', '0', '0', '0', 'إنَّ أمر البعث يسير علينا، فإنَّا إذا أردنا شيئًا فإنما نقول له: "كن"، فإذا هو كائن موجود.');
INSERT INTO Quran__Arabic_Tafseer_Muyassar (QuranChapterID, VerseNumber, Rukuh, Sajdah, Manzil, HizbQuarter, VerseText) VALUES (16, 41, '0', '0', '0', '0', 'والذين تركوا ديارهم مِن أجل الله، فهاجروا بعدما وقع عليهم الظلم، لنسكننهم في الدنيا دارًا حسنة، ولأجر الآخرة أكبر؛ لأن ثوابهم فيها الجنة. لو كان المتخلفون عن الهجرة يعلمون علم يقين ما عند الله من الأجر والثواب للمهاجرين في سبيله، ما تخلَّف منهم أحد عن ذلك.');
INSERT INTO Quran__Arabic_Tafseer_Muyassar (QuranChapterID, VerseNumber, Rukuh, Sajdah, Manzil, HizbQuarter, VerseText) VALUES (16, 42, '0', '0', '0', '0', 'هؤلاء المهاجرون في سبيل الله هم الذين صبروا على أوامر الله وعن نواهيه وعلى أقداره المؤلمة، وعلى ربهم وحده يعتمدون، فاستحقوا هذه المنزلة العظيمة.');
INSERT INTO Quran__Arabic_Tafseer_Muyassar (QuranChapterID, VerseNumber, Rukuh, Sajdah, Manzil, HizbQuarter, VerseText) VALUES (16, 43, '0', '0', '0', '0', 'وما أرسلنا في السابقين قبلك -أيها الرسول- إلا رسلا من الرجال لا من الملائكة، نوحي إليهم، وإن كنتم -يا مشركي قريش- لا تصدقون بذلك فاسألوا أهل الكتب السابقة، يخبروكم أن الأنبياء كانوا بشرًا، إن كنتم لا تعلمون أنهم بشر. والآية عامة في كل مسألة من مسائل الدين، إذا لم يكن عند الإنسان علم منها أن يسأل من يعلمها من العلماء الراسخين في العلم.');
INSERT INTO Quran__Arabic_Tafseer_Muyassar (QuranChapterID, VerseNumber, Rukuh, Sajdah, Manzil, HizbQuarter, VerseText) VALUES (16, 44, '0', '0', '0', '0', 'وأَرْسَلْنا الرسل السابقين بالدلائل الواضحة وبالكتب السماوية، وأنزلنا إليك -أيها الرسول- القرآن؛ لتوضح للناس ما خفي مِن معانيه وأحكامه، ولكي يتدبروه ويهتدوا به.');
INSERT INTO Quran__Arabic_Tafseer_Muyassar (QuranChapterID, VerseNumber, Rukuh, Sajdah, Manzil, HizbQuarter, VerseText) VALUES (16, 45, '0', '0', '0', '0', 'أفأمن الكفار المدبِّرون للمكايد أن يخسف الله بهم الأرض كما فعل بقارون، أو يأتيهم العذاب من مكان لا يُحِسُّونه ولا يتوقعونه، أو يأخذهم العذاب، وهم يتقلبون في أسفارهم وتصرفهم؟ فما هم بسابقين الله ولا فائتيه ولا ناجين من عذابه؛ لأنه القوي الذي لا يعجزه شيء، أو يأخذهم الله بنقص من الأموال والأنفس والثمرات، أو في حال خوفهم من أخذه لهم، فإن ربكم لرؤوف بخلقه، رحيم بهم.');
INSERT INTO Quran__Arabic_Tafseer_Muyassar (QuranChapterID, VerseNumber, Rukuh, Sajdah, Manzil, HizbQuarter, VerseText) VALUES (16, 46, '0', '0', '0', '0', 'أفأمن الكفار المدبِّرون للمكايد أن يخسف الله بهم الأرض كما فعل بقارون، أو يأتيهم العذاب من مكان لا يُحِسُّونه ولا يتوقعونه، أو يأخذهم العذاب، وهم يتقلبون في أسفارهم وتصرفهم؟ فما هم بسابقين الله ولا فائتيه ولا ناجين من عذابه؛ لأنه القوي الذي لا يعجزه شيء، أو يأخذهم الله بنقص من الأموال والأنفس والثمرات، أو في حال خوفهم من أخذه لهم، فإن ربكم لرؤوف بخلقه، رحيم بهم.');
INSERT INTO Quran__Arabic_Tafseer_Muyassar (QuranChapterID, VerseNumber, Rukuh, Sajdah, Manzil, HizbQuarter, VerseText) VALUES (16, 47, '0', '0', '0', '0', 'أفأمن الكفار المدبِّرون للمكايد أن يخسف الله بهم الأرض كما فعل بقارون، أو يأتيهم العذاب من مكان لا يُحِسُّونه ولا يتوقعونه، أو يأخذهم العذاب، وهم يتقلبون في أسفارهم وتصرفهم؟ فما هم بسابقين الله ولا فائتيه ولا ناجين من عذابه؛ لأنه القوي الذي لا يعجزه شيء، أو يأخذهم الله بنقص من الأموال والأنفس والثمرات، أو في حال خوفهم من أخذه لهم، فإن ربكم لرؤوف بخلقه، رحيم بهم.');
INSERT INTO Quran__Arabic_Tafseer_Muyassar (QuranChapterID, VerseNumber, Rukuh, Sajdah, Manzil, HizbQuarter, VerseText) VALUES (16, 48, '0', '0', '0', '0', 'أَعَمِيَ هؤلاء الكفار، فلم ينظروا إلى ما خلق الله من شيء له ظل، كالجبال والأشجار، تميل ظلالها تارة يمينًا وتارة شمالا تبعًا لحركة الشمس نهارًا والقمر ليلا كلها خاضعة لعظمة ربها وجلاله، وهي تحت تسخيره وتدبيره وقهره؟');
INSERT INTO Quran__Arabic_Tafseer_Muyassar (QuranChapterID, VerseNumber, Rukuh, Sajdah, Manzil, HizbQuarter, VerseText) VALUES (16, 49, '0', '0', '0', '0', 'ولله وحده يسجد كل ما في السموات وما في الأرض مِن دابة، والملائكة يسجدون لله، وهم لا يستكبرون عن عبادته. وخصَّهم بالذكر بعد العموم لفَضْلهم وشرفهم وكثرة عبادتهم.');
INSERT INTO Quran__Arabic_Tafseer_Muyassar (QuranChapterID, VerseNumber, Rukuh, Sajdah, Manzil, HizbQuarter, VerseText) VALUES (16, 50, '1', '1', '0', '0', 'يخاف الملائكة ربهم الذي هو فوقهم بالذات والقهر وكمال الصفات، ويفعلون ما يُؤْمرون به من طاعة الله. وفي الآية: إثبات صفة العلو والفوقية لله على جميع خلقه، كما يليق بجلاله وكماله.');
INSERT INTO Quran__Arabic_Tafseer_Muyassar (QuranChapterID, VerseNumber, Rukuh, Sajdah, Manzil, HizbQuarter, VerseText) VALUES (16, 51, '0', '0', '0', '1', 'وقال الله لعباده: لا تعبدوا إلهين اثنين، إنما معبودكم إله واحد، فخافوني دون سواي.');
INSERT INTO Quran__Arabic_Tafseer_Muyassar (QuranChapterID, VerseNumber, Rukuh, Sajdah, Manzil, HizbQuarter, VerseText) VALUES (16, 52, '0', '0', '0', '0', 'ولله كل ما في السموات والأرض خلقًا وملكًا وعبيدًا، وله وحده العبادة والطاعة والإخلاص دائمًا، أيليق بكم أن تخافوا غير الله وتعبدوه؟');
INSERT INTO Quran__Arabic_Tafseer_Muyassar (QuranChapterID, VerseNumber, Rukuh, Sajdah, Manzil, HizbQuarter, VerseText) VALUES (16, 53, '0', '0', '0', '0', 'وما بكم مِن نعمةِ هدايةٍ، أو صحة جسم، وسَعَة رزقٍ وولد، وغير ذلك، فمِنَ الله وحده، فهو المُنْعِم بها عليكم، ثم إذا نزل بكم السقم والبلاء والقحط فإلى الله وحده تَضِجُّون بالدعاء.');
INSERT INTO Quran__Arabic_Tafseer_Muyassar (QuranChapterID, VerseNumber, Rukuh, Sajdah, Manzil, HizbQuarter, VerseText) VALUES (16, 54, '0', '0', '0', '0', 'ثم إذا كشف عنكم البلاء والسقم، إذا جماعة منكم بربهم المُنْعِم عليهم بالنجاة يتخذون معه الشركاء والأولياء.');
INSERT INTO Quran__Arabic_Tafseer_Muyassar (QuranChapterID, VerseNumber, Rukuh, Sajdah, Manzil, HizbQuarter, VerseText) VALUES (16, 55, '0', '0', '0', '0', 'ليجحدوا نعمنا عليهم، ومنها كَشْفُ البلاء عنهم، فاستمتعوا بدنياكم، ومصيرها إلى الزوال، فسوف تعلمون عاقبة كفركم وعصيانكم.');
INSERT INTO Quran__Arabic_Tafseer_Muyassar (QuranChapterID, VerseNumber, Rukuh, Sajdah, Manzil, HizbQuarter, VerseText) VALUES (16, 56, '0', '0', '0', '0', 'ومِن قبيح أعمالهم أنهم يجعلون للأصنام التي اتخذوها آلهة، وهي لا تعلم شيئًا ولا تنفع ولا تضر، جزءًا من أموالهم التي رزقهم الله بها تقربًا إليها. تالله لتسألُنَّ يوم القيامة عما كنتم تختلقونه من الكذب على الله.');
INSERT INTO Quran__Arabic_Tafseer_Muyassar (QuranChapterID, VerseNumber, Rukuh, Sajdah, Manzil, HizbQuarter, VerseText) VALUES (16, 57, '0', '0', '0', '0', 'ويجعل الكفار لله البنات، فيقولون: الملائكة بنات الله، تنزَّه الله عن قولهم، ويجعلون لأنفسهم ما يحبون من البنين.');
INSERT INTO Quran__Arabic_Tafseer_Muyassar (QuranChapterID, VerseNumber, Rukuh, Sajdah, Manzil, HizbQuarter, VerseText) VALUES (16, 58, '0', '0', '0', '0', 'وإذا جاء مَن يخبر أحدهم بولادة أنثى اسودَّ وجهه؛ كراهية لما سمع، وامتلأ غمًّا وحزنًا.');
INSERT INTO Quran__Arabic_Tafseer_Muyassar (QuranChapterID, VerseNumber, Rukuh, Sajdah, Manzil, HizbQuarter, VerseText) VALUES (16, 59, '0', '0', '0', '0', 'يستخفي مِن قومه كراهة أن يلقاهم متلبسًا بما ساءه من الحزن والعار؛ بسبب البنت التي وُلِدت له، ومتحيرًا في أمر هذه المولودة: أيبقيها حية على ذلٍّ وهوان، أم يدفنها حية في التراب؟ ألا بئس الحكم الذي حكموه مِن جَعْل البنات لله والذكور لهم.');
INSERT INTO Quran__Arabic_Tafseer_Muyassar (QuranChapterID, VerseNumber, Rukuh, Sajdah, Manzil, HizbQuarter, VerseText) VALUES (16, 60, '1', '0', '0', '0', 'للذين لا يؤمنون بالآخرة ولا يعملون لها، الصفة القبيحة من العجز والحاجة والجهل والكفر، ولله الصفات العليا من الكمال والاستغناء عن خلقه، وهو العزيز في ملكه، الحكيم في تدبيره.');
INSERT INTO Quran__Arabic_Tafseer_Muyassar (QuranChapterID, VerseNumber, Rukuh, Sajdah, Manzil, HizbQuarter, VerseText) VALUES (16, 61, '0', '0', '0', '0', 'ولو يؤاخذ الله الناس بكفرهم وافترائهم ما ترك على الأرض مَن يتحرَّك، ولكن يبقيهم إلى وقت محدد هو نهاية آجالهم، فإذا جاء أجلهم لا يتأخرون عنه وقتًا يسيرًا، ولا يتقدمون.');
INSERT INTO Quran__Arabic_Tafseer_Muyassar (QuranChapterID, VerseNumber, Rukuh, Sajdah, Manzil, HizbQuarter, VerseText) VALUES (16, 62, '0', '0', '0', '0', 'ومن قبائحهم: أنهم يجعلون لله ما يكرهونه لأنفسهم من البنات، وتقول ألسنتهم كذبًا: إن لهم حسن العاقبة، حقًا أن لهم النار، وأنهم فيها مَتْروكون مَنْسيون.');
INSERT INTO Quran__Arabic_Tafseer_Muyassar (QuranChapterID, VerseNumber, Rukuh, Sajdah, Manzil, HizbQuarter, VerseText) VALUES (16, 63, '0', '0', '0', '0', 'تالله لقد أرسلنا رسلا إلى أمم مِن قبلك -أيها الرسول- فحسَّن لهم الشيطان ما عملوه من الكفر والتكذيب وعبادة غير الله، فهو متولٍّ إغواءهم في الدنيا، ولهم في الآخرة عذاب أليم موجع.');
INSERT INTO Quran__Arabic_Tafseer_Muyassar (QuranChapterID, VerseNumber, Rukuh, Sajdah, Manzil, HizbQuarter, VerseText) VALUES (16, 64, '0', '0', '0', '0', 'وما أنزلنا عليك القرآن -أيها الرسول- إلا لتوضح للناس ما اختلفوا فيه من الدين والأحكام؛ لتقوم الحجة عليهم ببيانك ورشدًا ورحمة لقوم يؤمنون.');
INSERT INTO Quran__Arabic_Tafseer_Muyassar (QuranChapterID, VerseNumber, Rukuh, Sajdah, Manzil, HizbQuarter, VerseText) VALUES (16, 65, '1', '0', '0', '0', 'والله أنزل من السحاب مطرًا، فأخرج به النبات من الأرض بعد أن كانت قاحلة يابسة، إن في إنزال المطر وإنبات النبات لَدليلا على قدرة الله على البعث وعلى الوحدانية، لقوم يسمعون، ويتدبرون، ويطيعون الله، ويتقونه.');
INSERT INTO Quran__Arabic_Tafseer_Muyassar (QuranChapterID, VerseNumber, Rukuh, Sajdah, Manzil, HizbQuarter, VerseText) VALUES (16, 66, '0', '0', '0', '0', 'وإن لكم -أيها الناس- في الأنعام -وهي الإبل والبقر والغنم- لَعظة، فقد شاهدتم أننا نسقيكم من ضروعها لبنًا خارجًا من بين فَرْث -وهو ما في الكَرِش- وبين دم خالصًا من كل الشوائب، لذيذًا لا يَغَصُّ به مَن شَرِبَه.');
INSERT INTO Quran__Arabic_Tafseer_Muyassar (QuranChapterID, VerseNumber, Rukuh, Sajdah, Manzil, HizbQuarter, VerseText) VALUES (16, 67, '0', '0', '0', '0', 'ومِن نِعَمنا عليكم ما تأخذونه من ثمرات النخيل والأعناب، فتجعلونه خمرًا مُسْكِرًا -وهذا قبل تحريمها- وطعامًا طيبًا. إن فيما ذكر لَدليلا على قدرة الله لِقومٍ يعقلون البراهين فيعتبرون بها.');
INSERT INTO Quran__Arabic_Tafseer_Muyassar (QuranChapterID, VerseNumber, Rukuh, Sajdah, Manzil, HizbQuarter, VerseText) VALUES (16, 68, '0', '0', '0', '0', 'وألْهَمَ ربك -أيها النبي- النحل بأن اجعلي لك بيوتًا في الجبال، وفي الشجر، وفيما يبني الناس من البيوت والسُّقُف.');
INSERT INTO Quran__Arabic_Tafseer_Muyassar (QuranChapterID, VerseNumber, Rukuh, Sajdah, Manzil, HizbQuarter, VerseText) VALUES (16, 69, '0', '0', '0', '0', 'ثم كُلي مِن كل ثمرة تشتهينها، فاسلكي طرق ربك مذللة لك؛ لطلب الرزق في الجبال وخلال الشجر، وقد جعلها سهلة عليكِ، لا تضلي في العَوْد إليها وإن بَعُدَتْ. يخرج من بطون النحل عسل مختلف الألوان مِن بياض وصفرة وحمرة وغير ذلك، فيه شفاء للناس من الأمراض. إن فيما يصنعه النحل لَدلالة قوية على قدرة خالقها لقوم يتفكرون، فيعتبرون.');
INSERT INTO Quran__Arabic_Tafseer_Muyassar (QuranChapterID, VerseNumber, Rukuh, Sajdah, Manzil, HizbQuarter, VerseText) VALUES (16, 70, '1', '0', '0', '0', 'والله سجانه وتعالى خلقكم ثم يميتكم في نهاية أعماركم، ومنكم مَن يصير إلى أردأ العمر وهو الهرم، كما كان في طفولته لا يعلم شيئًا مما كان يعلمه، إن الله عليم قدير، أحاط علمه وقدرته بكل شيء، فالله الذي ردَّ الإنسان إلى هذه الحالة قادر على أن يميته، ثم يبعثه.');
INSERT INTO Quran__Arabic_Tafseer_Muyassar (QuranChapterID, VerseNumber, Rukuh, Sajdah, Manzil, HizbQuarter, VerseText) VALUES (16, 71, '0', '0', '0', '0', 'والله فَضَّل بعضكم على بعض فيما أعطاكم في الدنيا من الرزق، فمنكم غني ومنكم فقير، ومنكم مالك ومنكم مملوك، فلا يعطي المالكون مملوكيهم مما أعطاهم الله ما يصيرون به شركاء لهم متساوين معهم في المال، فإذا لم يرضوا بذلك لأنفسهم، فلماذا رضوا أن يجعلوا لله شركاء من عبيده؟ إن هذا لَمن أعظم الظلم والجحود لِنعم الله عز وجل.');
INSERT INTO Quran__Arabic_Tafseer_Muyassar (QuranChapterID, VerseNumber, Rukuh, Sajdah, Manzil, HizbQuarter, VerseText) VALUES (16, 72, '0', '0', '0', '0', 'والله سبحانه جعل مِن جنسكم أزواجا؛ لتستريح نفوسكم معهن، وجعل لكم منهن الأبناء ومِن نسلهنَّ الأحفاد، ورزقكم من الأطعمة الطيبة من الثمار والحبوب واللحوم وغير ذلك. أفبالباطل من ألوهية شركائهم يؤمنون، وبنعم الله التي لا تحصى يجحدون، ولا يشكرون له بإفراده جل وعلا بالعبادة؟');
INSERT INTO Quran__Arabic_Tafseer_Muyassar (QuranChapterID, VerseNumber, Rukuh, Sajdah, Manzil, HizbQuarter, VerseText) VALUES (16, 73, '0', '0', '0', '0', 'ويعبد المشركون أصنامًا لا تملك أن تعطيهم شيئًا من الرزق من السماء كالمطر، ولا من الأرض كالزرع، فهم لا يملكون شيئًا، ولا يتأتى منهم أن يملكوه؛ لأنهم لا يقدرون.');
INSERT INTO Quran__Arabic_Tafseer_Muyassar (QuranChapterID, VerseNumber, Rukuh, Sajdah, Manzil, HizbQuarter, VerseText) VALUES (16, 74, '0', '0', '0', '0', 'وإذا عَلِمتم أن الأصنام والأوثان لا تنفع، فلا تجعلوا -أيها الناس- لله أشباهًا مماثلين له مِن خَلْقه تشركونهم معه في العبادة. إن الله يعلم ما تفعلون، وأنتم غافلون لا تعلمون خطأكم وسوء عاقبتكم.');
INSERT INTO Quran__Arabic_Tafseer_Muyassar (QuranChapterID, VerseNumber, Rukuh, Sajdah, Manzil, HizbQuarter, VerseText) VALUES (16, 75, '0', '0', '0', '1', 'ضرب الله مثلا بيَّن فيه فساد عقيدة أهل الشرك: رجلا مملوكًا عاجزًا عن التصرف لا يملك شيئًا، ورجلا آخر حرًا، له مال حلال رزَقَه الله به، يملك التصرف فيه، ويعطي منه في الخفاء والعلن، فهل يقول عاقل بالتساوي بين الرجلين؟ فكذلك الله الخالق المالك المتصرف لا يستوي مع خلقه وعبيده، فكيف تُسَوُّون بينهما؟ الحمد لله وحده، فهو المستحق للحمد والثناء، بل أكثر المشركين لا يعلمون أن الحمد والنعمة لله، وأنه وحده المستحق للعبادة.');
INSERT INTO Quran__Arabic_Tafseer_Muyassar (QuranChapterID, VerseNumber, Rukuh, Sajdah, Manzil, HizbQuarter, VerseText) VALUES (16, 76, '1', '0', '0', '0', 'وضرب الله مثلا آخر لبطلان الشرك رجلين: أحدهما أخرس أصم لا يَفْهَم ولا يُفْهِم، لا يقدر على منفعة نفسه أو غيره، وهو عبء ثقيل على مَن يَلي أمره ويعوله، إذا أرسله لأمر يقضيه لا ينجح، ولا يعود عليه بخير، ورجل آخر سليم الحواس، ينفع نفسه وغيره، يأمر بالإنصاف، وهو على طريق واضح لا عوج فيه، فهل يستوي الرجلان في نظر العقلاء؟ فكيف تُسَوُّون بين الصنم الأبكم الأصمِّ وبين الله القادر المنعم بكل خير؟');
INSERT INTO Quran__Arabic_Tafseer_Muyassar (QuranChapterID, VerseNumber, Rukuh, Sajdah, Manzil, HizbQuarter, VerseText) VALUES (16, 77, '0', '0', '0', '0', 'ولله سبحانه وتعالى عِلْمُ ما غاب في السموات والأرض، وما شأن القيامة في سرعة مجيئها إلا كنظرة سريعة بالبصر، بل هو أسرع من ذلك. إن الله على كل شيء قدير.');
INSERT INTO Quran__Arabic_Tafseer_Muyassar (QuranChapterID, VerseNumber, Rukuh, Sajdah, Manzil, HizbQuarter, VerseText) VALUES (16, 78, '0', '0', '0', '0', 'والله سبحانه وتعالى أخرجكم مِن بطون أمهاتكم بعد مدة الحمل، لا تدركون شيئًا مما حولكم، وجعل لكم وسائل الإدراك من السمع والبصر والقلوب؛ لعلكم تشكرون لله تعالى على تلك النعم، وتفردونه عز وجل بالعبادة.');
INSERT INTO Quran__Arabic_Tafseer_Muyassar (QuranChapterID, VerseNumber, Rukuh, Sajdah, Manzil, HizbQuarter, VerseText) VALUES (16, 79, '0', '0', '0', '0', 'ألم ينظر المشركون إلى الطير مذللات للطيران في الهواء بين السماء والأرض بأمر الله؟ ما يمسكهن عن الوقوع إلا هو سبحانه بما خَلَقه لها، وأقدرها عليه. إن في ذلك التذليل والإمساك لَدلالات لقوم يؤمنون بما يرونه من الأدلة على قدرة الله.');
INSERT INTO Quran__Arabic_Tafseer_Muyassar (QuranChapterID, VerseNumber, Rukuh, Sajdah, Manzil, HizbQuarter, VerseText) VALUES (16, 80, '0', '0', '0', '0', 'والله سبحانه جعل لكم من بيوتكم راحة واستقرارًا مع أهلكم، وأنتم مقيمون في الحضر، وجعل لكم في سفركم خيامًا وقبابًا من جلود الأنعام، يَخِفُّ عليكم حِمْلها وقت تَرْحالكم، ويخف عليكم نَصْبها وقت إقامتكم بعد التَّرْحال، وجعل لكم من أصواف الغنم، وأوبار الإبل، وأشعار المعز أثاثًا لكم من أكسية وألبسة وأغطية وفرش وزينة، تتمتعون بها إلى أجل مسمَّى ووقت معلوم.');
INSERT INTO Quran__Arabic_Tafseer_Muyassar (QuranChapterID, VerseNumber, Rukuh, Sajdah, Manzil, HizbQuarter, VerseText) VALUES (16, 81, '0', '0', '0', '0', 'والله جعل لكم ما تستظلُّون به من الأشجار وغيرها، وجعل لكم في الجبال من المغارات والكهوف أماكن تلجؤون إليها عند الحاجة، وجعل لكم ثيابًا من القطن والصوف وغيرهما، تحفظكم من الحر والبرد، وجعل لكم من الحديد ما يردُّ عنكم الطعن والأذى في حروبكم، كما أنعم الله عليكم بهذه النعم يتمُّ نعمته عليكم ببيان الدين الحق؛ لتستسلموا لأمر الله وحده، ولا تشركوا به شيئًا في عبادته.');
INSERT INTO Quran__Arabic_Tafseer_Muyassar (QuranChapterID, VerseNumber, Rukuh, Sajdah, Manzil, HizbQuarter, VerseText) VALUES (16, 82, '0', '0', '0', '0', 'فإن أعرضوا عنك -أيها الرسول- بعدما رأوا من الآيات فلا تحزن، فما عليك إلا البلاغ الواضح لما أُرْسِلْتَ به، وأما الهداية فإلينا.');
INSERT INTO Quran__Arabic_Tafseer_Muyassar (QuranChapterID, VerseNumber, Rukuh, Sajdah, Manzil, HizbQuarter, VerseText) VALUES (16, 83, '1', '0', '0', '0', 'يعرف هؤلاء المشركون نعمة الله عليهم بإرسال محمد صلى الله عليه وسلم إليهم، ثم يجحدون نبوته، وأكثر قومه الجاحدون لنبوته، لا المقرون بها.');
INSERT INTO Quran__Arabic_Tafseer_Muyassar (QuranChapterID, VerseNumber, Rukuh, Sajdah, Manzil, HizbQuarter, VerseText) VALUES (16, 84, '0', '0', '0', '0', 'واذكر لهم -أيها الرسول- ما يكون يوم القيامة، حين نبعث من كل أمة رسولها شاهدًا على إيمان من آمن منها، وكُفْر مَن كَفَر، ثم لا يُؤذن للذين كفروا بالاعتذار عما وقع منهم، ولا يُطْلب منهم إرضاءُ ربهم بالتوبة والعمل الصالح، فقد مضى أوان ذلك.');
INSERT INTO Quran__Arabic_Tafseer_Muyassar (QuranChapterID, VerseNumber, Rukuh, Sajdah, Manzil, HizbQuarter, VerseText) VALUES (16, 85, '0', '0', '0', '0', 'وإذا شاهد الذين كفروا عذاب الله في الآخرة فلا يخفف عنهم منه شيء، ولا يُمْهلون، ولا يؤخر عذابهم.');
INSERT INTO Quran__Arabic_Tafseer_Muyassar (QuranChapterID, VerseNumber, Rukuh, Sajdah, Manzil, HizbQuarter, VerseText) VALUES (16, 86, '0', '0', '0', '0', 'وإذا أبصر المشركون يوم القيامة آلهتهم التي عبدوها مع الله، قالوا: ربنا هؤلاء شركاؤنا الذين كنا نعبدهم مِن دونك، فنطقَتِ الآلهة بتكذيب مَن عبدوها، وقالت: إنكم -أيها المشركون- لَكاذبون، حين جعلتمونا شركاء لله وعبدتمونا معه، فلم نأمركم بذلك، ولا زعمنا أننا مستحقون للألوهية، فاللوم عليكم.');
INSERT INTO Quran__Arabic_Tafseer_Muyassar (QuranChapterID, VerseNumber, Rukuh, Sajdah, Manzil, HizbQuarter, VerseText) VALUES (16, 87, '0', '0', '0', '0', 'وأظهر المشركون الاستسلام والخضوع لله يوم القيامة، وغاب عنهم ما كانوا يختلقونه من الأكاذيب، وأن آلهتهم تشفع لهم.');
INSERT INTO Quran__Arabic_Tafseer_Muyassar (QuranChapterID, VerseNumber, Rukuh, Sajdah, Manzil, HizbQuarter, VerseText) VALUES (16, 88, '0', '0', '0', '0', 'الذين جحدوا وحدانية الله ونبوتك -أيها الرسول- وكذَّبوك، ومنعوا غيرهم عن الإيمان بالله ورسوله، زدناهم عذابا على كفرهم وعذابًا على صدِّهم الناس عن اتباع الحق؛ وهذا بسبب تعمُّدهم الإفساد وإضلال العباد بالكفر والمعصية.');
INSERT INTO Quran__Arabic_Tafseer_Muyassar (QuranChapterID, VerseNumber, Rukuh, Sajdah, Manzil, HizbQuarter, VerseText) VALUES (16, 89, '1', '0', '0', '0', 'واذكر -أيها الرسول- حين نبعث يوم القيامة في كل أمة من الأمم شهيدًا عليهم، هو الرسول الذي بعثه الله إليهم من أنفسهم وبلسانهم، وجئنا بك -أيها الرسول- شهيدًا على أمتك، وقد نَزَّلْنا عليك القرآن توضيحًا لكل أمر يحتاج إلى بيان، كأحكام الحلال والحرام، والثواب والعقاب، وغير ذلك، وليكون هداية من الضلال، ورحمة لمن صدَّق وعمل به، وبشارة طيبة للمؤمنين بحسن مصيرهم.');
INSERT INTO Quran__Arabic_Tafseer_Muyassar (QuranChapterID, VerseNumber, Rukuh, Sajdah, Manzil, HizbQuarter, VerseText) VALUES (16, 90, '0', '0', '0', '1', 'إن الله سبحانه وتعالى يأمر عباده في هذا القرآن بالعدل والإنصاف في حقه بتوحيده وعدم الإشراك به، وفي حق عباده بإعطاء كل ذي حق حقه، ويأمر بالإحسان في حقه بعبادته وأداء فرائضه على الوجه المشروع، وإلى الخلق في الأقوال والأفعال، ويأمر بإعطاء ذوي القرابة ما به صلتهم وبرُّهم، وينهى عن كل ما قَبُحَ قولا أو عملا وعما ينكره الشرع ولا يرضاه من الكفر والمعاصي، وعن ظلم الناس والتعدي عليهم، والله -بهذا الأمر وهذا النهي- يَعِظكم ويذكِّركم العواقب؛ لكي تتذكروا أوامر الله وتنتفعوا بها.');
INSERT INTO Quran__Arabic_Tafseer_Muyassar (QuranChapterID, VerseNumber, Rukuh, Sajdah, Manzil, HizbQuarter, VerseText) VALUES (16, 91, '0', '0', '0', '0', 'والتزموا الوفاء بكل عهد أوجبتموه على أنفسكم بينكم وبين الله -تعالى- أو بينكم وبين الناس فيما لا يخالف كتاب الله وسنة نبيه، ولا ترجعوا في الأيمان بعد أن أكَّدْتموها، وقد جعلتم الله عليكم كفيلا وضامنًا حين عاهدتموه. إن الله يعلم ما تفعلونه، وسيجزيكم عليه.');
INSERT INTO Quran__Arabic_Tafseer_Muyassar (QuranChapterID, VerseNumber, Rukuh, Sajdah, Manzil, HizbQuarter, VerseText) VALUES (16, 92, '0', '0', '0', '0', 'ولا ترجعوا في عهودكم، فيكون مَثَلكم مثل امرأة غزلت غَزْلا وأحكمته، ثم نقضته، تجعلون أيمانكم التي حلفتموها عند التعاهد خديعة لمن عاهدتموه، وتنقضون عهدكم إذا وجدتم جماعة أكثر مالا ومنفعة من الذين عاهدتموهم، إنما يختبركم الله بما أمركم به من الوفاء بالعهود وما نهاكم عنه مِن نقضها، وليبيِّن لكم يوم القيامة ما كنتم فيه تختلفون في الدنيا من الإيمان بالله ونبوة محمد صلى الله عليه وسلم.');
INSERT INTO Quran__Arabic_Tafseer_Muyassar (QuranChapterID, VerseNumber, Rukuh, Sajdah, Manzil, HizbQuarter, VerseText) VALUES (16, 93, '0', '0', '0', '0', 'ولو شاء الله لوفَّقكم كلكم، فجعلكم على ملة واحدة، وهي الإسلام والإيمان، وألزمكم به، ولكنه سبحانه يُضلُّ مَن يشاء ممن علم منه إيثار الضلال، فلا يهديه عدلا منه، ويهدي مَن يشاء مِمَّن علم منه إيثار الحق، فيوفقه فضلا منه، وليسألنَّكم الله جميعًا يوم القيامة عما كنتم تعملون في الدنيا فيما أمركم به، ونهاكم عنه، وسيجازيكم على ذلك.');
INSERT INTO Quran__Arabic_Tafseer_Muyassar (QuranChapterID, VerseNumber, Rukuh, Sajdah, Manzil, HizbQuarter, VerseText) VALUES (16, 94, '0', '0', '0', '0', 'ولا تجعلوا من الأيمان التي تحلفونها خديعة لمن حلفتم لهم، فتهلكوا بعد أن كنتم آمنين، كمن زلقت قدمه بعد ثبوتها، وتذوقوا ما يسوؤكم من العذاب في الدنيا؛ بما تسببتم فيه مِن مَنْع غيركم عن هذا الدين لما رأوه منكم من الغدر، ولكم في الآخرة عذاب عظيم.');
INSERT INTO Quran__Arabic_Tafseer_Muyassar (QuranChapterID, VerseNumber, Rukuh, Sajdah, Manzil, HizbQuarter, VerseText) VALUES (16, 95, '0', '0', '0', '0', 'ولا تنقضوا عهد الله؛ لتستبدلوا مكانه عرضًا قليلا من متاع الدنيا، إن ما عند الله من الثواب على الوفاء أفضل لكم من هذا الثمن القليل، إن كنتم من أهل العلم، فتدبَّروا الفرق بين خيْرَي الدنيا والآخرة.');
INSERT INTO Quran__Arabic_Tafseer_Muyassar (QuranChapterID, VerseNumber, Rukuh, Sajdah, Manzil, HizbQuarter, VerseText) VALUES (16, 96, '0', '0', '0', '0', 'ما عندكم من حطام الدنيا يذهب، وما عند الله لكم من الرزق والثواب لا يزول. ولنُثِيبنَّ الذين تحمَّلوا مشاق التكاليف -ومنها الوفاء بالعهد- ثوابهم بأحسن أعمالهم، فنعطيهم على أدناها، كما نعطيهم على أعلاها تفضُّلا.');
INSERT INTO Quran__Arabic_Tafseer_Muyassar (QuranChapterID, VerseNumber, Rukuh, Sajdah, Manzil, HizbQuarter, VerseText) VALUES (16, 97, '0', '0', '0', '0', 'مَن عمل عملا صالحًا ذكرًا كان أم أنثى، وهو مؤمن بالله ورسوله، فلنحيينه في الدنيا حياة سعيدة مطمئنة، ولو كان قليل المال، ولنجزينَّهم في الآخرة ثوابهم بأحسن ما عملوا في الدنيا.');
INSERT INTO Quran__Arabic_Tafseer_Muyassar (QuranChapterID, VerseNumber, Rukuh, Sajdah, Manzil, HizbQuarter, VerseText) VALUES (16, 98, '0', '0', '0', '0', 'فإذا أردت -أيها المؤمن- أن تقرأ شيئًا من القرآن فاستعذ بالله مِن شرِّ الشيطان المطرود من رحمة الله قائلا أعوذ بالله من الشيطان الرجيم.');
INSERT INTO Quran__Arabic_Tafseer_Muyassar (QuranChapterID, VerseNumber, Rukuh, Sajdah, Manzil, HizbQuarter, VerseText) VALUES (16, 99, '0', '0', '0', '0', 'إن الشيطان ليس له تسلُّطٌ على المؤمنين بالله ورسوله، وعلى ربهم وحده يعتمدون. إنما تسلُّطه على الذين جعلوه مُعينًا لهم وأطاعوه، والذين هم -بسبب طاعته- مشركون بالله تعالى.');
INSERT INTO Quran__Arabic_Tafseer_Muyassar (QuranChapterID, VerseNumber, Rukuh, Sajdah, Manzil, HizbQuarter, VerseText) VALUES (16, 100, '1', '0', '0', '0', 'إن الشيطان ليس له تسلُّطٌ على المؤمنين بالله ورسوله، وعلى ربهم وحده يعتمدون. إنما تسلُّطه على الذين جعلوه مُعينًا لهم وأطاعوه، والذين هم -بسبب طاعته- مشركون بالله تعالى.');
INSERT INTO Quran__Arabic_Tafseer_Muyassar (QuranChapterID, VerseNumber, Rukuh, Sajdah, Manzil, HizbQuarter, VerseText) VALUES (16, 101, '0', '0', '0', '0', 'وإذا بدَّلنا آية بآية أخرى، والله الخالق أعلم بمصلحة خَلْقه بما ينزله من الأحكام في الأوقات المختلفة، قال الكفار: إنما أنت -يا محمد- كاذب مختَلِق على الله ما لم يَقُلْه. ومحمد صلى الله عليه وسلم ليس كما يزعمون. بل أكثرهم لا عِلْم لهم بربهم ولا بشرعه وأحكامه.');
INSERT INTO Quran__Arabic_Tafseer_Muyassar (QuranChapterID, VerseNumber, Rukuh, Sajdah, Manzil, HizbQuarter, VerseText) VALUES (16, 102, '0', '0', '0', '0', 'قل لهم -أيها الرسول-: ليس القرآن مختلَقًا مِن عندي، بل نَزَّله جبريل مِن ربك بالصدق والعدل؛ تثبيتًا للمؤمنين، وهداية من الضلال، وبشارة طيبة لمن أسلموا وخضعوا لله رب العالمين.');
INSERT INTO Quran__Arabic_Tafseer_Muyassar (QuranChapterID, VerseNumber, Rukuh, Sajdah, Manzil, HizbQuarter, VerseText) VALUES (16, 103, '0', '0', '0', '0', 'ولقد نعلم أن المشركين يقولون: إن النبي يتلقى القرآن مِن بشر مِن بني آدم. كذبوا؛ فإن لسان الذي نسبوا إليه تعليم النبي صلى الله عليه وسلم أعجمي لا يُفصح، والقرآن عربي غاية في الوضوح والبيان.');
INSERT INTO Quran__Arabic_Tafseer_Muyassar (QuranChapterID, VerseNumber, Rukuh, Sajdah, Manzil, HizbQuarter, VerseText) VALUES (16, 104, '0', '0', '0', '0', 'إن الكفار الذين لا يصدقون بالقرآن لا يوفقهم الله لإصابة الحق، ولهم في الآخرة عذاب مؤلم موجع.');
INSERT INTO Quran__Arabic_Tafseer_Muyassar (QuranChapterID, VerseNumber, Rukuh, Sajdah, Manzil, HizbQuarter, VerseText) VALUES (16, 105, '0', '0', '0', '0', 'إنما يختلق الكذبَ مَن لا يؤمن بالله وآياته، وأولئك هم الكاذبون في قولهم ذلك. أما محمد صلى الله عليه وسلم المؤمن بربه الخاضع له فمحال أن يكذب على الله، ويقول عليه ما لم يقله.');
INSERT INTO Quran__Arabic_Tafseer_Muyassar (QuranChapterID, VerseNumber, Rukuh, Sajdah, Manzil, HizbQuarter, VerseText) VALUES (16, 106, '0', '0', '0', '0', 'إنما يفتري الكذب مَن نطق بكلمة الكفر وارتدَّ بعد إيمانه، فعليهم غضب من الله، إلا مَن أُرغم على النطق بالكفر، فنطق به خوفًا من الهلاك وقلبه ثابت على الإيمان، فلا لوم عليه، لكن من نطق بالكفر واطمأن قلبه إليه، فعليهم غضب شديد من الله، ولهم عذاب عظيم؛ وذلك بسبب إيثارهم الدنيا وزينتها، وتفضيلهم إياها على الآخرة وثوابها، وأن الله لا يهدي الكافرين، ولا يوفقهم للحق والصواب.');
INSERT INTO Quran__Arabic_Tafseer_Muyassar (QuranChapterID, VerseNumber, Rukuh, Sajdah, Manzil, HizbQuarter, VerseText) VALUES (16, 107, '0', '0', '0', '0', 'إنما يفتري الكذب مَن نطق بكلمة الكفر وارتدَّ بعد إيمانه، فعليهم غضب من الله، إلا مَن أُرغم على النطق بالكفر، فنطق به خوفًا من الهلاك وقلبه ثابت على الإيمان، فلا لوم عليه، لكن من نطق بالكفر واطمأن قلبه إليه، فعليهم غضب شديد من الله، ولهم عذاب عظيم؛ وذلك بسبب إيثارهم الدنيا وزينتها، وتفضيلهم إياها على الآخرة وثوابها، وأن الله لا يهدي الكافرين، ولا يوفقهم للحق والصواب.');
INSERT INTO Quran__Arabic_Tafseer_Muyassar (QuranChapterID, VerseNumber, Rukuh, Sajdah, Manzil, HizbQuarter, VerseText) VALUES (16, 108, '0', '0', '0', '0', 'أولئك هم الذين ختم الله على قلوبهم بالكفر وإيثار الدنيا على الآخرة، فلا يصل إليها نور الهداية، وأصم سمعهم عن آيات الله فلا يسمعونها سماع تدبُّر، وأعمى أبصارهم، فلا يرون البراهين الدالة على ألوهية الله، وأولئك هم الغافلون عمَّا أعدَّ الله لهم من العذاب.');
INSERT INTO Quran__Arabic_Tafseer_Muyassar (QuranChapterID, VerseNumber, Rukuh, Sajdah, Manzil, HizbQuarter, VerseText) VALUES (16, 109, '0', '0', '0', '0', 'حقًا إنهم في الآخرة هم الخاسرون الهالكون، الذين صرفوا حياتهم إلى ما فيه عذابهم وهلاكهم.');
INSERT INTO Quran__Arabic_Tafseer_Muyassar (QuranChapterID, VerseNumber, Rukuh, Sajdah, Manzil, HizbQuarter, VerseText) VALUES (16, 110, '1', '0', '0', '0', 'ثم إن ربك للمستضعفين في "مكة" الذين عذَّبهم المشركون، حتى وافقوهم على ما هم عليه ظاهرًا، ففتنوهم بالتلفظ بما يرضيهم، وقلوبهم مطمئنة بالإيمان، ولمَّا أمكنهم الخلاص هاجروا إلى "المدينة"، ثم جاهدوا في سبيل الله، وصبروا على مشاق التكاليف، إن ربك -من بعد توبتهم- لَغفور لهم، رحيم بهم.');
INSERT INTO Quran__Arabic_Tafseer_Muyassar (QuranChapterID, VerseNumber, Rukuh, Sajdah, Manzil, HizbQuarter, VerseText) VALUES (16, 111, '0', '0', '0', '1', 'وذكرهم -أيها الرسول- بيوم القيامة حين تأتي كل نفس تخاصم عن ذاتها، وتعتذر بكل المعاذير، ويوفي الله كل نفس جزاء ما عَمِلَتْه من غير ظلم لها، فلا يزيدهم في العقاب، ولا ينقصهم من الثواب.');
INSERT INTO Quran__Arabic_Tafseer_Muyassar (QuranChapterID, VerseNumber, Rukuh, Sajdah, Manzil, HizbQuarter, VerseText) VALUES (16, 112, '0', '0', '0', '0', 'وضرب الله مثلا بلدة "مكة" كانت في أمان من الاعتداء، واطمئنان مِن ضيق العيش، يأتيها رزقها هنيئًا سهلا من كل جهة، فجحد أهلُها نِعَمَ الله عليهم، وأشركوا به، ولم يشكروا له، فعاقبهم الله بالجوع، والخوف من سرايا رسول الله صلى الله عليه وسلم وجيوشه، التي كانت تخيفهم؛ وذلك بسبب كفرهم وصنيعهم الباطل.');
INSERT INTO Quran__Arabic_Tafseer_Muyassar (QuranChapterID, VerseNumber, Rukuh, Sajdah, Manzil, HizbQuarter, VerseText) VALUES (16, 113, '0', '0', '0', '0', 'ولقد أرسل الله إلى أهل "مكة" رسولا منهم، هو النبي محمد صلى الله عليه وسلم، يعرفون نسبه وصدقه وأمانته، فلم يقبلوا ما جاءهم به، ولم يصدقوه، فأخذهم العذاب من الشدائد والجوع والخوف، وقَتْل عظمائهم في "بدر" وهم ظالمون لأنفسهم بالشرك بالله، والصدِّ عن سبيله.');
INSERT INTO Quran__Arabic_Tafseer_Muyassar (QuranChapterID, VerseNumber, Rukuh, Sajdah, Manzil, HizbQuarter, VerseText) VALUES (16, 114, '0', '0', '0', '0', 'فكلوا -أيها المؤمنون- مما رزقكم الله، وجعله لكم حلالا مستطابًا، واشكروا نعمة الله عليكم بالاعتراف بها وصَرْفها في طاعة الله، إن كنتم حقًّا منقادين لأمره سامعين مطيعين له، تعبدونه وحده لا شريك له.');
INSERT INTO Quran__Arabic_Tafseer_Muyassar (QuranChapterID, VerseNumber, Rukuh, Sajdah, Manzil, HizbQuarter, VerseText) VALUES (16, 115, '0', '0', '0', '0', 'إنما حرَّم الله عليكم الميتة من الحيوان، والدم المسفوح من الذبيح عند ذبحه، ولحم الخنزير، وما ذبح لغير الله، لكن مَن ألجأته ضرورة الخوف من الموت إلى أَكْلِ شيء مِن هذه المحرمات وهو غير ظالم، ولا متجاوزٍ حدَّ الضرورة، فإن الله غفور له، رحيم به، لا يعاقبه على ما فعل.');
INSERT INTO Quran__Arabic_Tafseer_Muyassar (QuranChapterID, VerseNumber, Rukuh, Sajdah, Manzil, HizbQuarter, VerseText) VALUES (16, 116, '0', '0', '0', '0', 'ولا تقولوا -أيها المشركون- للكذب الذي تصفه ألسنتكم: هذا حلال لِما حرَّمه الله، وهذا حرام لِما أحَلَّه الله؛ لتختلقوا على الله الكذب بنسبة التحليل والتحريم إليه، إن الذين يختلقون على الله الكذب لا يفوزون بخير في الدنيا ولا في الآخرة.');
INSERT INTO Quran__Arabic_Tafseer_Muyassar (QuranChapterID, VerseNumber, Rukuh, Sajdah, Manzil, HizbQuarter, VerseText) VALUES (16, 117, '0', '0', '0', '0', 'متاعهم في الدنيا متاع زائل ضئيل، ولهم في الآخرة عذاب موجع.');
INSERT INTO Quran__Arabic_Tafseer_Muyassar (QuranChapterID, VerseNumber, Rukuh, Sajdah, Manzil, HizbQuarter, VerseText) VALUES (16, 118, '0', '0', '0', '0', 'وعلى اليهود حَرَّمنا ما أخبرناك به -أيها الرسول- مِن قبل، وهو كل ذي ظُفُر، وشحوم البقر والغنم، إلا ما حَمَلَتْه ظهورها أو أمعاؤها أو كان مختلطًا بعظم، وما ظلمناهم بتحريم ذلك عليهم، ولكن كانوا ظالمين لأنفسهم بالكفر والبغي، فاستحقوا التحريم عقوبة لهم.');
INSERT INTO Quran__Arabic_Tafseer_Muyassar (QuranChapterID, VerseNumber, Rukuh, Sajdah, Manzil, HizbQuarter, VerseText) VALUES (16, 119, '1', '0', '0', '0', 'ثم إن ربك للذين فعلوا المعاصي في حال جهلهم لعاقبتها وإيجابها لسخط الله -فكل عاص لله مخطئًا أو متعمدًا فهو جاهل بهذا الاعتبار وإن كان عالمًا بالتحريم-، ثم رجعوا إلى الله عمَّا كانوا عليه من الذنوب، وأصلحوا نفوسهم وأعمالهم، إن ربك -مِن بعد توبتهم وإصلاحهم- لَغفور لهم، رحيم بهم.');
INSERT INTO Quran__Arabic_Tafseer_Muyassar (QuranChapterID, VerseNumber, Rukuh, Sajdah, Manzil, HizbQuarter, VerseText) VALUES (16, 120, '0', '0', '0', '0', 'إن إبراهيم كان إمامًا في الخير، وكان طائعا خاضعًا لله، لا يميل عن دين الإسلام موحِّدًا لله غير مشرك به، وكان شاكرًا لنعم الله عليه، اختاره الله لرسالته، وأرشده إلى الطريق المستقيم، وهو الإسلام، وآتيناه في الدنيا نعمة حسنة من الثناء عليه في الآخِرين والقدوة به، والولد الصالح، وإنه عند الله في الآخرة لمن الصالحين أصحاب المنازل العالية.');
INSERT INTO Quran__Arabic_Tafseer_Muyassar (QuranChapterID, VerseNumber, Rukuh, Sajdah, Manzil, HizbQuarter, VerseText) VALUES (16, 121, '0', '0', '0', '0', 'إن إبراهيم كان إمامًا في الخير، وكان طائعا خاضعًا لله، لا يميل عن دين الإسلام موحِّدًا لله غير مشرك به، وكان شاكرًا لنعم الله عليه، اختاره الله لرسالته، وأرشده إلى الطريق المستقيم، وهو الإسلام، وآتيناه في الدنيا نعمة حسنة من الثناء عليه في الآخِرين والقدوة به، والولد الصالح، وإنه عند الله في الآخرة لمن الصالحين أصحاب المنازل العالية.');
INSERT INTO Quran__Arabic_Tafseer_Muyassar (QuranChapterID, VerseNumber, Rukuh, Sajdah, Manzil, HizbQuarter, VerseText) VALUES (16, 122, '0', '0', '0', '0', 'إن إبراهيم كان إمامًا في الخير، وكان طائعا خاضعًا لله، لا يميل عن دين الإسلام موحِّدًا لله غير مشرك به، وكان شاكرًا لنعم الله عليه، اختاره الله لرسالته، وأرشده إلى الطريق المستقيم، وهو الإسلام، وآتيناه في الدنيا نعمة حسنة من الثناء عليه في الآخِرين والقدوة به، والولد الصالح، وإنه عند الله في الآخرة لمن الصالحين أصحاب المنازل العالية.');
INSERT INTO Quran__Arabic_Tafseer_Muyassar (QuranChapterID, VerseNumber, Rukuh, Sajdah, Manzil, HizbQuarter, VerseText) VALUES (16, 123, '0', '0', '0', '0', 'ثم أوحينا إليك -أيها الرسول- أن اتبع دين الإسلام كما اتبعه إبراهيم، وأن استقم عليه، ولا تَحِدْ عنه، فإن إبراهيم لم يكن من المشركين مع الله غيره.');
INSERT INTO Quran__Arabic_Tafseer_Muyassar (QuranChapterID, VerseNumber, Rukuh, Sajdah, Manzil, HizbQuarter, VerseText) VALUES (16, 124, '0', '0', '0', '0', 'إنما جعل الله تعظيم يوم السبت بالتفرغ للعبادة فيه على اليهود الذين اختلفوا فيه على نبيهم، واختاروه بدل يوم الجمعة الذي أُمِروا بتعظيمه. فإن ربك -أيها الرسول- لَيحكم بين المختلفين يوم القيامة فيما اختلفوا فيه على نبيهم، ويجازي كلا بما يستحقه.');
INSERT INTO Quran__Arabic_Tafseer_Muyassar (QuranChapterID, VerseNumber, Rukuh, Sajdah, Manzil, HizbQuarter, VerseText) VALUES (16, 125, '0', '0', '0', '0', 'ادعُ -أيها الرسول- أنت ومَنِ اتبعك إلى دين ربك وطريقه المستقيم، بالطريقة الحكيمة التي أوحاها الله إليك في الكتاب والسنة، وخاطِب الناس بالأسلوب المناسب لهم، وانصح لهم نصحًا حسنًا، يرغبهم في الخير، وينفرهم من الشر، وجادلهم بأحسن طرق المجادلة من الرفق واللين. فما عليك إلا البلاغ، وقد بلَّغْتَ، أما هدايتهم فعلى الله وحده، فهو أعلم بمن ضلَّ عن سبيله، وهو أعلم بالمهتدين.');
INSERT INTO Quran__Arabic_Tafseer_Muyassar (QuranChapterID, VerseNumber, Rukuh, Sajdah, Manzil, HizbQuarter, VerseText) VALUES (16, 126, '0', '0', '0', '0', 'وإن أردتم -أيها المؤمنون- القصاص ممن اعتدوا عليكم، فلا تزيدوا عما فعلوه بكم، ولئن صبرتم لهو خير لكم في الدنيا بالنصر، وفي الآخرة بالأجر العظيم.');
INSERT INTO Quran__Arabic_Tafseer_Muyassar (QuranChapterID, VerseNumber, Rukuh, Sajdah, Manzil, HizbQuarter, VerseText) VALUES (16, 127, '0', '0', '0', '0', 'واصبر -أيها الرسول- على ما أصابك مِن أذى في الله حتى يأتيك الفرج، وما صبرك إلا بالله، فهو الذي يعينك عليه ويثبتك، ولا تحزن على مَن خالفك ولم يستجب لدعوتك، ولا تغتم مِن مكرهم وكيدهم؛ فإن ذلك عائد عليهم بالشر والوبال.');
INSERT INTO Quran__Arabic_Tafseer_Muyassar (QuranChapterID, VerseNumber, Rukuh, Sajdah, Manzil, HizbQuarter, VerseText) VALUES (16, 128, '1', '0', '0', '0', 'إن الله سبحانه وتعالى مع الذين اتقوه بامتثال ما أمر واجتناب ما نهى بالنصر والتأييد، ومع الذين يحسنون أداء فرائضه والقيام بحقوقه ولزوم طاعته، بعونه وتوفيقه ونصره.');
INSERT INTO Quran__Arabic_Tafseer_Muyassar (QuranChapterID, VerseNumber, Rukuh, Sajdah, Manzil, HizbQuarter, VerseText) VALUES (17, 1, '0', '0', '1', '1', 'يمجِّد الله نفسه ويعظم شأنه، لقدرته على ما لا يقدر عليه أحد سواه، لا إله غيره، ولا رب سواه، فهو الذي أسرى بعبده محمد صلى الله عليه وسلم زمنًا من الليل بجسده وروحه، يقظة لا منامًا، من المسجد الحرام بـ "مكة" إلى المسجد الأقصى بـ "بيت المقدس" الذي بارك الله حوله في الزروع والثمار وغير ذلك، وجعله محلا لكثير من الأنبياء؛ ليشاهد عجائب قدرة الله وأدلة وحدانيته. إن الله سبحانه وتعالى هو السميع لجميع الأصوات، البصير بكل مُبْصَر، فيعطي كُلا ما يستحقه في الدنيا والآخرة.');
INSERT INTO Quran__Arabic_Tafseer_Muyassar (QuranChapterID, VerseNumber, Rukuh, Sajdah, Manzil, HizbQuarter, VerseText) VALUES (17, 2, '0', '0', '0', '0', 'وكما كرَّم الله محمدًا صلى الله عليه وسلم بالإسراء، كَرَّم موسى عليه السلام بإعطائه التوراة، وجعلها بيانًا للحق وإرشادًا لبني إسرائيل، متضمنة نهيهم عن اتخاذ غير الله تعالى وليًا أو معبودًا يفوضون إليه أمورهم.');
INSERT INTO Quran__Arabic_Tafseer_Muyassar (QuranChapterID, VerseNumber, Rukuh, Sajdah, Manzil, HizbQuarter, VerseText) VALUES (17, 3, '0', '0', '0', '0', 'يا سلالة الذين أنجيناهم وحَمَلْناهم مع نوح في السفينة لا تشركوا بالله في عبادته، وكونوا شاكرين لنعمه، مقتدين بنوح عليه السلام؛ إنه كان عبدًا شكورًا لله بقلبه ولسانه وجوارحه.');
INSERT INTO Quran__Arabic_Tafseer_Muyassar (QuranChapterID, VerseNumber, Rukuh, Sajdah, Manzil, HizbQuarter, VerseText) VALUES (17, 4, '0', '0', '0', '0', 'وأخبرنا بني إسرائيل في التوراة التي أُنزلت عليهم بأنه لا بد أن يقع منهم إفساد مرتين في "بيت المقدس" وما والاه بالظلم، وقَتْل الأنبياء والتكبر والطغيان والعدوان.');
INSERT INTO Quran__Arabic_Tafseer_Muyassar (QuranChapterID, VerseNumber, Rukuh, Sajdah, Manzil, HizbQuarter, VerseText) VALUES (17, 5, '0', '0', '0', '0', 'فإذا وقع منهم الإفساد الأول سَلَّطْنا عليهم عبادًا لنا ذوي شجاعة وقوة شديدة، يغلبونهم ويقتلونهم ويشردونهم، فطافوا بين ديارهم مفسدين، وكان ذلك وعدًا لا بدَّ مِن وقوعه؛ لوجود سببه منهم.');
INSERT INTO Quran__Arabic_Tafseer_Muyassar (QuranChapterID, VerseNumber, Rukuh, Sajdah, Manzil, HizbQuarter, VerseText) VALUES (17, 6, '0', '0', '0', '0', 'ثم رَدَدْنا لكم -يا بني إسرائيل- الغلبة والظهور على أعدائكم الذين سُلِّطوا عليكم، وأكثرنا أرزاقكم وأولادكم، وقَوَّيناكم وجعلناكم أكثر عددًا من عدوكم؛ وذلك بسبب إحسانكم وخضوعكم لله.');
INSERT INTO Quran__Arabic_Tafseer_Muyassar (QuranChapterID, VerseNumber, Rukuh, Sajdah, Manzil, HizbQuarter, VerseText) VALUES (17, 7, '0', '0', '0', '0', 'إن أحسنتم أفعالكم وأقوالكم فقد أحسنتم لأنفسكم؛ لأن ثواب ذلك عائد إليكم، وإن أسأتم فعقاب ذلك عائد عليكم، فإذا حان موعد الإفساد الثاني سَلَّطْنا عليكم أعداءكم مرة أخرى؛ ليذلوكم ويغلبوكم، فتظهر آثار الإهانة والمذلة على وجوهكم، وليدخلوا عليكم "بيت المقدس" فيخرِّبوه، كما خرَّبوه أول مرة، وليدمروا كل ما وقع تحت أيديهم تدميرًا كاملا.');
INSERT INTO Quran__Arabic_Tafseer_Muyassar (QuranChapterID, VerseNumber, Rukuh, Sajdah, Manzil, HizbQuarter, VerseText) VALUES (17, 8, '0', '0', '0', '0', 'عسى ربكم -يا بني إسرائيل- أن يرحمكم بعد انتقامه إن تبتم وأصلحتم، وإن عدتم إلى الإفساد والظلم عُدْنا إلى عقابكم ومذلَّتكم. وجعلنا جهنم لكم وللكافرين عامة سجنًا لا خروج منه أبدا. وفي هذه الآية وما قبلها، تحذير لهذه الأمة من العمل بالمعاصي؛ لئلا يصيبها مثل ما أصاب بني إسرائيل، فسنن الله واحدة لا تبدل ولا تغير.');
INSERT INTO Quran__Arabic_Tafseer_Muyassar (QuranChapterID, VerseNumber, Rukuh, Sajdah, Manzil, HizbQuarter, VerseText) VALUES (17, 9, '0', '0', '0', '0', 'إن هذا القرآن الذي أنزلناه على عبدنا محمد يرشد الناس إلى أحسن الطرق، وهي ملة الإسلام، ويبشر المؤمنين الذين يعملون بما أمرهم الله به، وينتهون عمَّا نهاهم عنه، بأن لهم ثوابًا عظيمًا، وأن الذين لا يصدقون بالدار الآخرة وما فيها من الجزاء أعددنا لهم عذابًا موجعًا في النار.');
INSERT INTO Quran__Arabic_Tafseer_Muyassar (QuranChapterID, VerseNumber, Rukuh, Sajdah, Manzil, HizbQuarter, VerseText) VALUES (17, 10, '1', '0', '0', '0', 'إن هذا القرآن الذي أنزلناه على عبدنا محمد يرشد الناس إلى أحسن الطرق، وهي ملة الإسلام، ويبشر المؤمنين الذين يعملون بما أمرهم الله به، وينتهون عمَّا نهاهم عنه، بأن لهم ثوابًا عظيمًا، وأن الذين لا يصدقون بالدار الآخرة وما فيها من الجزاء أعددنا لهم عذابًا موجعًا في النار.');
INSERT INTO Quran__Arabic_Tafseer_Muyassar (QuranChapterID, VerseNumber, Rukuh, Sajdah, Manzil, HizbQuarter, VerseText) VALUES (17, 11, '0', '0', '0', '0', 'ويدعو الإنسان أحيانًا على نفسه أو ولده أو ماله بالشر، وذلك عند الغضب، مثل ما يدعو بالخير، وهذا من جهل الإنسان وعجلته، ومن رحمة الله به أنه يستجيب له في دعائه بالخير دون الشر؛ لأنه يعلم منه عدم القصد إلى إرادة ذلك، وكان الإنسان بطبعه عجولا.');
INSERT INTO Quran__Arabic_Tafseer_Muyassar (QuranChapterID, VerseNumber, Rukuh, Sajdah, Manzil, HizbQuarter, VerseText) VALUES (17, 12, '0', '0', '0', '0', 'وجعلنا الليل والنهار علامتين دالَّتين على وحدانيتنا وقدرتنا، فمَحَوْنا علامة الليل -وهي القمر- وجعلنا علامة النهار -وهي الشمس- مضيئة؛ ليبصر الإنسان في ضوء النهار كيف يتصرف في شؤون معاشه، ويخلد في الليل إلى السكن والراحة، وليعلم الناس -من تعاقب الليل والنهار- عدد السنين وحساب الأشهر والأيام، فيرتبون عليها ما يشاؤون من مصالحهم. وكل شيء بيَّناه تبيينًا كافيًا.');
INSERT INTO Quran__Arabic_Tafseer_Muyassar (QuranChapterID, VerseNumber, Rukuh, Sajdah, Manzil, HizbQuarter, VerseText) VALUES (17, 13, '0', '0', '0', '0', 'وكل إنسان يجعل الله ما عمله مِن خير أو شر ملازمًا له، فلا يحاسَب بعمل غيره، ولا يحاسَب غيره بعمله، ويخرج الله له يوم القيامة كتابًا قد سُجِّلت فيه أعماله يراه مفتوحًا.');
INSERT INTO Quran__Arabic_Tafseer_Muyassar (QuranChapterID, VerseNumber, Rukuh, Sajdah, Manzil, HizbQuarter, VerseText) VALUES (17, 14, '0', '0', '0', '0', 'يقال له: اقرأ كتاب أعمالك، فيقرأ، وإن لم يكن يعرف القراءة في الدنيا، تكفيك نفسك اليوم محصية عليك عملك، فتعرف ما عليها من جزاء. وهذا من أعظم العدل والإنصاف أن يقال للعبد: حاسِبْ نفسك، كفى بها حسيبًا عليك.');
INSERT INTO Quran__Arabic_Tafseer_Muyassar (QuranChapterID, VerseNumber, Rukuh, Sajdah, Manzil, HizbQuarter, VerseText) VALUES (17, 15, '0', '0', '0', '0', 'من اهتدى فاتبع طريق الحق فإنما يعود ثواب ذلك عليه وحده، ومن حاد واتبع طريق الباطل فإنما يعود عقاب ذلك عليه وحده، ولا تحمل نفس مذنبة إثم نفس مذنبة أخرى. ولا يعذب الله أحدًا إلا بعد إقامة الحجة عليه بإرسال الرسل وإنزال الكتب.');
INSERT INTO Quran__Arabic_Tafseer_Muyassar (QuranChapterID, VerseNumber, Rukuh, Sajdah, Manzil, HizbQuarter, VerseText) VALUES (17, 16, '0', '0', '0', '0', 'وإذا أردنا إهلاك أهل قرية لظلمهم أَمَرْنا مترفيهم بطاعة الله وتوحيده وتصديق رسله، وغيرهم تبع لهم، فعصَوا أمر ربهم وكذَّبوا رسله، فحقَّ عليهم القول بالعذاب الذي لا مردَّ له، فاستأصلناهم بالهلاك التام.');
INSERT INTO Quran__Arabic_Tafseer_Muyassar (QuranChapterID, VerseNumber, Rukuh, Sajdah, Manzil, HizbQuarter, VerseText) VALUES (17, 17, '0', '0', '0', '0', 'وكثيرا أهلكنا من الأمم المكذبة رسلها مِن بعد نبي الله نوح. وكفى بربك -أيها الرسول- أنه عالم بجميع أعمال عباده، لا تخفى عليه خافية.');
INSERT INTO Quran__Arabic_Tafseer_Muyassar (QuranChapterID, VerseNumber, Rukuh, Sajdah, Manzil, HizbQuarter, VerseText) VALUES (17, 18, '0', '0', '0', '0', 'من كان طلبه الدنيا العاجلة، وسعى لها وحدها، ولم يصدِّق بالآخرة، ولم يعمل لها، عجَّل الله له فيها ما يشاؤه اللّه ويريده مما كتبه له في اللوح المحفوظ، ثم يجعل الله له في الآخرة جهنم، يدخلها ملومًا مطرودًا من رحمته عز وجل؛ وذلك بسبب إرادته الدنيا وسعيه لها دون الآخرة.');
INSERT INTO Quran__Arabic_Tafseer_Muyassar (QuranChapterID, VerseNumber, Rukuh, Sajdah, Manzil, HizbQuarter, VerseText) VALUES (17, 19, '0', '0', '0', '0', 'ومَن قصد بعمله الصالح ثواب الدار الآخرة الباقية، وسعى لها بطاعة الله تعالى، وهو مؤمن بالله وثوابه وعظيم جزائه، فأولئك كان عملهم مقبولا مُدَّخرًا لهم عند ربهم، وسيثابون عليه.');
INSERT INTO Quran__Arabic_Tafseer_Muyassar (QuranChapterID, VerseNumber, Rukuh, Sajdah, Manzil, HizbQuarter, VerseText) VALUES (17, 20, '0', '0', '0', '0', 'كل فريق من العاملين للدنيا الفانية، والعاملين للآخرة الباقية نزيده مِن رزقنا، فنرزق المؤمنين والكافرين في الدنيا؛ فإن الرزق مِن عطاء ربك تفضلا منه، وما كان عطاء ربك ممنوعا من أحد مؤمنًا كان أم كافرًا.');
INSERT INTO Quran__Arabic_Tafseer_Muyassar (QuranChapterID, VerseNumber, Rukuh, Sajdah, Manzil, HizbQuarter, VerseText) VALUES (17, 21, '0', '0', '0', '0', 'تأمل -أيها الرسول- في كيفية تفضيل الله بعض الناس على بعض في الدنيا في الرزق والعمل، ولَلآخرة أكبرُ درجات للمؤمنين وأكبر تفضيلا.');
INSERT INTO Quran__Arabic_Tafseer_Muyassar (QuranChapterID, VerseNumber, Rukuh, Sajdah, Manzil, HizbQuarter, VerseText) VALUES (17, 22, '1', '0', '0', '0', 'لا تجعل -أيها الإنسان- مع الله شريكًا له في عبادته، فتبوء بالمذمة والخِذْلان.');
INSERT INTO Quran__Arabic_Tafseer_Muyassar (QuranChapterID, VerseNumber, Rukuh, Sajdah, Manzil, HizbQuarter, VerseText) VALUES (17, 23, '0', '0', '0', '1', 'وأَمَر ربك -أيها الإنسان- وألزم وأوجب أن يفرد سبحانه وتعالى وحده بالعبادة، وأمر بالإحسان إلى الأب والأم، وبخاصة حالةُ الشيخوخة، فلا تضجر ولا تستثقل شيئًا تراه من أحدهما أو منهما، ولا تسمعهما قولا سيئًا، حتى ولا التأفيف الذي هو أدنى مراتب القول السيئ، ولا يصدر منك إليهما فعل قبيح، ولكن ارفق بهما، وقل لهما -دائما- قولا لينًا لطيفًا.');
INSERT INTO Quran__Arabic_Tafseer_Muyassar (QuranChapterID, VerseNumber, Rukuh, Sajdah, Manzil, HizbQuarter, VerseText) VALUES (17, 24, '0', '0', '0', '0', 'وكُنْ لأمك وأبيك ذليلا متواضعًا رحمة بهما، واطلب من ربك أن يرحمهما برحمته الواسعة أحياءً وأمواتًا، كما صبرا على تربيتك طفلا ضعيف الحول والقوة.');
INSERT INTO Quran__Arabic_Tafseer_Muyassar (QuranChapterID, VerseNumber, Rukuh, Sajdah, Manzil, HizbQuarter, VerseText) VALUES (17, 25, '0', '0', '0', '0', 'ربكم -أيها الناس- أعلم بما في ضمائركم من خير وشر. إن تكن إرادتكم ومقاصدكم مرضاة الله وما يقربكم إليه، فإنه كان -سبحانه- للراجعين إليه في جميع الأوقات غفورًا، فمَن عَلِمَ الله أنه ليس في قلبه إلا الإنابة إليه ومحبته، فإنه يعفو عنه، ويغفر له ما يعرض من صغائر الذنوب، مما هو من مقتضى الطبائع البشرية.');
INSERT INTO Quran__Arabic_Tafseer_Muyassar (QuranChapterID, VerseNumber, Rukuh, Sajdah, Manzil, HizbQuarter, VerseText) VALUES (17, 26, '0', '0', '0', '0', 'وأحسِنْ إلى كل مَن له صلة قرابة بك، وأعطه حقه من الإحسان والبر، وأعط المسكين المحتاج والمسافر المنقطع عن أهله وماله، ولا تنفق مالك في غير طاعة الله، أو على وجه الإسراف والتبذير.');
INSERT INTO Quran__Arabic_Tafseer_Muyassar (QuranChapterID, VerseNumber, Rukuh, Sajdah, Manzil, HizbQuarter, VerseText) VALUES (17, 27, '0', '0', '0', '0', 'إن المسرفين والمنفقين أموالهم في معاصي الله هم أشباه الشياطين في الشر والفساد والمعصية، وكان الشيطان كثير الكفران شديدَ الجحود لنعمة ربه.');
INSERT INTO Quran__Arabic_Tafseer_Muyassar (QuranChapterID, VerseNumber, Rukuh, Sajdah, Manzil, HizbQuarter, VerseText) VALUES (17, 28, '0', '0', '0', '0', 'وإن أعرضت عن إعطاء هؤلاء الذين أُمِرْت بإعطائهم؛ لعدم وجود ما تعطيهم منه طلبًا لرزق تنتظره من عند ربك، فقل لهم قولا ليِّنًا لطيفًا، كالدعاء لهم بالغنى وسعة الرزق، وعِدْهم بأن الله إذا أيسر من فضله رزقًا أنك تعطيهم منه.');
INSERT INTO Quran__Arabic_Tafseer_Muyassar (QuranChapterID, VerseNumber, Rukuh, Sajdah, Manzil, HizbQuarter, VerseText) VALUES (17, 29, '0', '0', '0', '0', 'ولا تمسك يدك عن الإنفاق في سبيل الخير، مضيِّقًا على نفسك وأهلك والمحتاجين، ولا تسرف في الإنفاق، فتعطي فوق طاقتك، فتقعد ملومًا يلومك الناس ويذمونك، نادمًا على تبذيرك وضياع مالك.');
INSERT INTO Quran__Arabic_Tafseer_Muyassar (QuranChapterID, VerseNumber, Rukuh, Sajdah, Manzil, HizbQuarter, VerseText) VALUES (17, 30, '1', '0', '0', '0', 'إن ربك يوسِّع الرزق على بعض الناس، ويضيِّقه على بعضهم، وَفْق علمه وحكمته سبحانه وتعالى. إنه هو المطَّلِع على خفايا عباده، لا يغيب عن علمه شيء من أحوالهم.');
INSERT INTO Quran__Arabic_Tafseer_Muyassar (QuranChapterID, VerseNumber, Rukuh, Sajdah, Manzil, HizbQuarter, VerseText) VALUES (17, 31, '0', '0', '0', '0', 'وإذا علمتم أن الرزق بيد الله سبحانه فلا تقتلوا -أيها الناس- أولادكم خوفًا من الفقر؛ فإنه -سبحانه- هو الرزاق لعباده، يرزق الأبناء كما يرزق الآباء، إنَّ قَتْلَ الأولاد ذنب عظيم.');
INSERT INTO Quran__Arabic_Tafseer_Muyassar (QuranChapterID, VerseNumber, Rukuh, Sajdah, Manzil, HizbQuarter, VerseText) VALUES (17, 32, '0', '0', '0', '0', 'ولا تقربوا الزنى ودواعيه؛ كي لا تقعوا فيه، إنه كان فعلا بالغ القبح، وبئس الطريق طريقه.');
INSERT INTO Quran__Arabic_Tafseer_Muyassar (QuranChapterID, VerseNumber, Rukuh, Sajdah, Manzil, HizbQuarter, VerseText) VALUES (17, 33, '0', '0', '0', '0', 'ولا تقتلوا النفس التي حرم الله قَتْلها إلا بالحق الشرعي كالقصاص أو رجم الزاني المحصن أو قتل المرتد. ومن قُتِل بغير حق شرعي فقد جعلنا لولي أمره مِن وارث أو حاكم حجة في طلب قَتْل قاتله أو الدية، ولا يصح لولي أمر المقتول أن يجاوز حدَّ الله في القصاص كأن يقتل بالواحد اثنين أو جماعة، أو يُمَثِّل بالقاتل، إن الله معين وليَّ المقتول على القاتل حتى يتمكن مِن قَتْله قصاصًا.');
INSERT INTO Quran__Arabic_Tafseer_Muyassar (QuranChapterID, VerseNumber, Rukuh, Sajdah, Manzil, HizbQuarter, VerseText) VALUES (17, 34, '0', '0', '0', '0', 'ولا تتصرَّفوا في أموال الأطفال الذين مات آباؤهم، وصاروا في كفالتكم، إلا بالطريقة التي هي أحسن لهم، وهي التثمير والتنمية، حتى يبلغ الطفل اليتيم سنَّ البلوغ، وحسن التصرف في المال، وأتموا الوفاء بكل عهد التزمتم به. إن العهد يسأل الله عنه صاحبه يوم القيامة، فيثيبه إذا أتمه ووفَّاه، ويعاقبه إذا خان فيه.');
INSERT INTO Quran__Arabic_Tafseer_Muyassar (QuranChapterID, VerseNumber, Rukuh, Sajdah, Manzil, HizbQuarter, VerseText) VALUES (17, 35, '0', '0', '0', '0', 'وأتموا الكيل، ولا تنقصوه إذا كِلْتم لغيركم، وزِنوا بالميزان السوي، إن العدل في الكيل والوزن خير لكم في الدنيا، وأحسن عاقبة عند الله في الآخرة.');
INSERT INTO Quran__Arabic_Tafseer_Muyassar (QuranChapterID, VerseNumber, Rukuh, Sajdah, Manzil, HizbQuarter, VerseText) VALUES (17, 36, '0', '0', '0', '0', 'ولا تتبع -أيها الإنسان- ما لا تعلم، بل تأكَّد وتثبَّت. إن الإنسان مسؤول عما استعمَل فيه سمعه وبصره وفؤاده، فإذا استعمَلها في الخير نال الثواب، وإذا استعملها في الشر نال العقاب.');
INSERT INTO Quran__Arabic_Tafseer_Muyassar (QuranChapterID, VerseNumber, Rukuh, Sajdah, Manzil, HizbQuarter, VerseText) VALUES (17, 37, '0', '0', '0', '0', 'ولا تمش في الأرض مختالا متكبرا؛ فإنك لن تَخْرِق الأرض بالمشي عليها، ولن تبلغ الجبال طولا خيلاء وتكبرًا.');
INSERT INTO Quran__Arabic_Tafseer_Muyassar (QuranChapterID, VerseNumber, Rukuh, Sajdah, Manzil, HizbQuarter, VerseText) VALUES (17, 38, '0', '0', '0', '0', 'جميع ما تقدَّم ذِكْرُه من أوامر ونواهٍ، يكره الله سيِّئَه، ولا يرضاه لعباده.');
INSERT INTO Quran__Arabic_Tafseer_Muyassar (QuranChapterID, VerseNumber, Rukuh, Sajdah, Manzil, HizbQuarter, VerseText) VALUES (17, 39, '0', '0', '0', '0', 'ذلك الذي بيَّناه ووضَّحناه من هذه الأحكام الجليلة، من الأمر بمحاسن الأعمال، والنهي عن أراذل الأخلاق مما أوحيناه إليك أيها النبي. ولا تجعل -أيها الإنسان- مع الله تعالى شريكًا له في عبادته، فتُقْذف في نار جهنم تلومك نفسك والناس، وتكون مطرودًا مبعدًا من كل خير.');
INSERT INTO Quran__Arabic_Tafseer_Muyassar (QuranChapterID, VerseNumber, Rukuh, Sajdah, Manzil, HizbQuarter, VerseText) VALUES (17, 40, '1', '0', '0', '0', 'أفخصَّكم ربكم -أيها المشركون- بإعطائكم البنين، واتخذ لنفسه الملائكة بنات؟ إن قولكم هذا بالغ القبح والبشاعة، لا يليق بالله سبحانه وتعالى.');
INSERT INTO Quran__Arabic_Tafseer_Muyassar (QuranChapterID, VerseNumber, Rukuh, Sajdah, Manzil, HizbQuarter, VerseText) VALUES (17, 41, '0', '0', '0', '0', 'ولقد وضَّحْنا ونوَّعْنا في هذا القرآن الأحكام والأمثال والمواعظ؛ ليتعظ الناس ويتدبروا ما ينفعهم فيأخذوه، وما يضرهم فيدَعوه، وما يزيد البيان والتوضيح الظالمين إلا تباعدًا عن الحق، وغفلة عن النظر والاعتبار.');
INSERT INTO Quran__Arabic_Tafseer_Muyassar (QuranChapterID, VerseNumber, Rukuh, Sajdah, Manzil, HizbQuarter, VerseText) VALUES (17, 42, '0', '0', '0', '0', 'قل -أيها الرسول- للمشركين: لو أن مع الله آلهة أخرى، إذًا لطلبَتْ تلك الآلهة طريقًا إلى مغالبة الله ذي العرش العظيم.');
INSERT INTO Quran__Arabic_Tafseer_Muyassar (QuranChapterID, VerseNumber, Rukuh, Sajdah, Manzil, HizbQuarter, VerseText) VALUES (17, 43, '0', '0', '0', '0', 'تنزَّه الله وتقدَّس عَمَّا يقوله المشركون وتعالى علوًا كبيرًا.');
INSERT INTO Quran__Arabic_Tafseer_Muyassar (QuranChapterID, VerseNumber, Rukuh, Sajdah, Manzil, HizbQuarter, VerseText) VALUES (17, 44, '0', '0', '0', '0', 'تُسَبِّح له -سبحانه- السموات السبع والأرضون، ومَن فيهن مِن جميع المخلوقات، وكل شيء في هذا الوجود ينزه الله تعالى تنزيهًا مقرونًا بالثناء والحمد له سبحانه، ولكن لا تدركون -أيها الناس- ذلك. إنه سبحانه كان حليمًا بعباده لا يعاجل مَن عصاه بالعقوبة، غفورًا لهم.');
INSERT INTO Quran__Arabic_Tafseer_Muyassar (QuranChapterID, VerseNumber, Rukuh, Sajdah, Manzil, HizbQuarter, VerseText) VALUES (17, 45, '0', '0', '0', '0', 'وإذا قرأت القرآن فسمعه هؤلاء المشركون، جعلنا بينك وبين الذين لا يؤمنون بالآخرة حجابًا ساترًا يحجب عقولهم عن فَهْمِ القرآن؛ عقابًا لهم على كفرهم وإنكارهم.');
INSERT INTO Quran__Arabic_Tafseer_Muyassar (QuranChapterID, VerseNumber, Rukuh, Sajdah, Manzil, HizbQuarter, VerseText) VALUES (17, 46, '0', '0', '0', '0', 'وجعلنا على قلوب المشركين أغطية؛ لئلا يفهموا القرآن، وجعلنا في آذانهم صممًا؛ لئلا يسمعوه، وإذا ذَكَرْتَ ربك في القرآن داعيًا لتوحيده ناهيًا عن الشرك به رجعوا على أعقابهم نافرين من قولك؛ استكبارًا واستعظامًا من أن يوحِّدوا الله تعالى في عبادته.');
INSERT INTO Quran__Arabic_Tafseer_Muyassar (QuranChapterID, VerseNumber, Rukuh, Sajdah, Manzil, HizbQuarter, VerseText) VALUES (17, 47, '0', '0', '0', '0', 'نحن أعلم بالذي يستمعه رؤساء قريش، إذ يستمعون إليك، ومقاصدهم سيئة، فليس استماعهم لأجل الاسترشاد وقَبول الحق، ونعلم تَناجيهم حين يقولون: ما تتبعون إلا رجلا أصابه السحر فاختلط عقله.');
INSERT INTO Quran__Arabic_Tafseer_Muyassar (QuranChapterID, VerseNumber, Rukuh, Sajdah, Manzil, HizbQuarter, VerseText) VALUES (17, 48, '0', '0', '0', '0', 'تفكر -أيها الرسول- متعجبًا من قولهم: إن محمدًا ساحر شاعر مجنون!! فجاروا وانحرفوا، ولم يهتدوا إلى طريق الحق والصواب.');
INSERT INTO Quran__Arabic_Tafseer_Muyassar (QuranChapterID, VerseNumber, Rukuh, Sajdah, Manzil, HizbQuarter, VerseText) VALUES (17, 49, '0', '0', '0', '0', 'وقال المشركون منكرين أن يُخْلَقوا خَلْقًا جديدًا بعد أن تبلى عظامهم، وتصير فُتاتًا: أئِنا لمبعوثون يوم القيامة بعثًا جديدًا؟');
INSERT INTO Quran__Arabic_Tafseer_Muyassar (QuranChapterID, VerseNumber, Rukuh, Sajdah, Manzil, HizbQuarter, VerseText) VALUES (17, 50, '0', '0', '0', '1', 'قل لهم -أيها الرسول- على جهة التعجيز: كونوا حجارة أو حديدًا في الشدة والقوة -إن قَدَرْتم على ذلك- فإن الله يُعيدكم كما بدأكم، وذلك هيِّن عليه يسير.');
INSERT INTO Quran__Arabic_Tafseer_Muyassar (QuranChapterID, VerseNumber, Rukuh, Sajdah, Manzil, HizbQuarter, VerseText) VALUES (17, 51, '0', '0', '0', '0', 'أو كونوا خلقًا يَعْظُم ويُسْتَبْعَد في عقولكم قبوله للبعث، فالله تعالى قادر على إعادتكم وبعثكم، وحين تقوم عليهم الحجة في قدرة الله على البعث والإحياء فسيقولون -منكرين-: مَن يردُّنا إلى الحياة بعد الموت؟ قل لهم: يعيدكم ويرجعكم الله الذي أنشأكم من العدم أول مرة، وعند سماعهم هذا الرد فسيَهُزُّون رؤوسهم ساخرين متعجبين ويقولون -مستبعدين-: متى يقع هذا البعث؟ قل: هو قريب؛ فإن كل آتٍ قريب.');
INSERT INTO Quran__Arabic_Tafseer_Muyassar (QuranChapterID, VerseNumber, Rukuh, Sajdah, Manzil, HizbQuarter, VerseText) VALUES (17, 52, '1', '0', '0', '0', 'يوم يناديكم خالقكم للخروج من قبوركم، فتستجيبون لأمر الله، وتنقادون له، وله الحمد على كل حال، وتظنون -لهول يوم القيامة- أنكم ما أقمتم في الدنيا إلا زمنًا قليلا؛ لطول لبثكم في الآخرة.');
INSERT INTO Quran__Arabic_Tafseer_Muyassar (QuranChapterID, VerseNumber, Rukuh, Sajdah, Manzil, HizbQuarter, VerseText) VALUES (17, 53, '0', '0', '0', '0', 'وقل لعبادي المؤمنين يقولوا في تخاطبهم وتحاورهم الكلام الحسن الطيب؛ فإنهم إن لم يفعلوا ذلك ألقى الشيطان بينهم العداوة والفساد والخصام. إن الشيطان كان للإنسان عدوًا ظاهر العداوة.');
INSERT INTO Quran__Arabic_Tafseer_Muyassar (QuranChapterID, VerseNumber, Rukuh, Sajdah, Manzil, HizbQuarter, VerseText) VALUES (17, 54, '0', '0', '0', '0', 'ربكم أعلم بكم -أيها الناس- إن يشأ يرحمكم، فيوفقكم للإيمان، أو إن يشأ يمتكم على الكفر، فيعذبكم، وما أرسلناك -أيها الرسول- عليهم وكيلا تدبِّر أمرهم وتجازيهم على أفعالهم، وإنما مهمتك تبليغ ما أُرْسلتَ به، وبيان الصراط المستقيم.');
INSERT INTO Quran__Arabic_Tafseer_Muyassar (QuranChapterID, VerseNumber, Rukuh, Sajdah, Manzil, HizbQuarter, VerseText) VALUES (17, 55, '0', '0', '0', '0', 'وربك -أيها النبي- أعلم بمَن في السموات والأرض. ولقد فَضَّلْنا بعض النبيين على بعض بالفضائل وكثرة الأتباع وإنزال الكتب، وأعطينا داود الزبور.');
INSERT INTO Quran__Arabic_Tafseer_Muyassar (QuranChapterID, VerseNumber, Rukuh, Sajdah, Manzil, HizbQuarter, VerseText) VALUES (17, 56, '0', '0', '0', '0', 'قل -أيها الرسول- لمشركي قومك: إن هذه المعبودات التي تنادونها لكشف الضرِّ عنكم لا تملك ذلك، ولا تقدر على تحويله عنكم إلى غيركم، ولا تقدر على تحويله من حال إلى حال، فالقادر على ذلك هو الله وحده. وهذه الآية عامة في كل ما يُدْعى من دون الله، ميتًا كان أو غائبًا، من الأنبياء والصالحين وغيرهم، بلفظ الاستغاثة أو الدعاء أو غيرهما، فلا معبود بحق إلا الله.');
INSERT INTO Quran__Arabic_Tafseer_Muyassar (QuranChapterID, VerseNumber, Rukuh, Sajdah, Manzil, HizbQuarter, VerseText) VALUES (17, 57, '0', '0', '0', '0', 'أولئك الذين يدعوهم المشركون من الأنبياء والصالحين والملائكة مع الله، يتنافسون في القرب من ربهم بما يقدرون عليه من الأعمال الصالحة، ويأمُلون رحمته ويخافون عذابه، إن عذاب ربك هو ما ينبغي أن يحذره العباد، ويخافوا منه.');
INSERT INTO Quran__Arabic_Tafseer_Muyassar (QuranChapterID, VerseNumber, Rukuh, Sajdah, Manzil, HizbQuarter, VerseText) VALUES (17, 58, '0', '0', '0', '0', 'ويتوعَّد الله الكفار بأنه ما من قريةٍ كافرة مكذبة للرسل إلا وسينزل بها عقابه بالهلاك في الدنيا قبل يوم القيامة أو بالعذاب الشديد لأهلها، كتاب كتبه الله وقضاء أبرمه لا بد مِن وقوعه، وهو مسطور في اللوح المحفوظ.');
INSERT INTO Quran__Arabic_Tafseer_Muyassar (QuranChapterID, VerseNumber, Rukuh, Sajdah, Manzil, HizbQuarter, VerseText) VALUES (17, 59, '0', '0', '0', '0', 'وما منعَنا من إنزال المعجزات التي سألها المشركون إلا تكذيب مَن سبقهم من الأمم، فقد أجابهم الله إلى ما طلبوا فكذَّبوا وهلكوا. وأعطينا ثمود -وهم قوم صالح- معجزة واضحة وهي الناقة، فكفروا بها فأهلكناهم. وما إرسالنا الرسل بالآيات والعبر والمعجزات التي جعلناها على أيديهم إلا تخويف للعباد؛ ليعتبروا ويتذكروا.');
INSERT INTO Quran__Arabic_Tafseer_Muyassar (QuranChapterID, VerseNumber, Rukuh, Sajdah, Manzil, HizbQuarter, VerseText) VALUES (17, 60, '1', '0', '0', '0', 'واذكر -أيها الرسول- حين قلنا لك: إن ربك أحاط بالناس علمًا وقدرة. وما جعلنا الرؤيا التي أريناكها عِيانًا ليلة الإسراء والمعراج من عجائب المخلوقات إلا اختبارًا للناس؛ ليتميز كافرهم من مؤمنهم، وما جعلنا شجرة الزقوم الملعونة التي ذكرت في القرآن إلا ابتلاء للناس. ونخوِّف المشركين بأنواع العذاب والآيات، ولا يزيدهم التخويف إلا تماديًا في الكفر والضلال.');
INSERT INTO Quran__Arabic_Tafseer_Muyassar (QuranChapterID, VerseNumber, Rukuh, Sajdah, Manzil, HizbQuarter, VerseText) VALUES (17, 61, '0', '0', '0', '0', 'واذكر قولنا للملائكة: اسجدوا لآدم تحية وتكريمًا، فسجدوا جميعًا إلا إبليس، استكبر وامتنع عن السجود قائلا على سبيل الإنكار والاستكبار: أأسجد لهذا الضعيف، المخلوق من الطين؟');
INSERT INTO Quran__Arabic_Tafseer_Muyassar (QuranChapterID, VerseNumber, Rukuh, Sajdah, Manzil, HizbQuarter, VerseText) VALUES (17, 62, '0', '0', '0', '0', 'وقال إبليس جراءة على الله وكفرًا به: أرأيت هذا المخلوق الذي ميزته عليَّ؟ لئن أبقيتني حيًا إلى يوم القيامة لأستولينَّ على ذريته بالإغواء والإفساد، إلا المخلصين منهم في الإيمان، وهم قليل.');
INSERT INTO Quran__Arabic_Tafseer_Muyassar (QuranChapterID, VerseNumber, Rukuh, Sajdah, Manzil, HizbQuarter, VerseText) VALUES (17, 63, '0', '0', '0', '0', 'قال الله تعالى مهددًا إبليس وأتباعه: اذهب فمَن تبعك مِن ذرية آدم، فأطاعك، فإن عقابك وعقابهم وافر في نار جهنم.');
INSERT INTO Quran__Arabic_Tafseer_Muyassar (QuranChapterID, VerseNumber, Rukuh, Sajdah, Manzil, HizbQuarter, VerseText) VALUES (17, 64, '0', '0', '0', '0', 'واستَخْفِف كل مَن تستطيع استخفافه منهم بدعوتك إياه إلى معصيتي، واجمع عليهم كل ما تقدر عليه مِن جنودك من كل راكب وراجل، واجعل لنفسك شِرْكة في أموالهم بأن يكسبوها من الحرام، وشِرْكة في الأولاد بتزيين الزنى والمعاصي، ومخالفة أوامر الله حتى يكثر الفجور والفساد، وعِدْ أتباعك مِن ذرية آدم الوعود الكاذبة، فكل وعود الشيطان باطلة وغرور.');
INSERT INTO Quran__Arabic_Tafseer_Muyassar (QuranChapterID, VerseNumber, Rukuh, Sajdah, Manzil, HizbQuarter, VerseText) VALUES (17, 65, '0', '0', '0', '0', 'إن عبادي المؤمنين المخلصين الذين أطاعوني ليس لك قدرة على إغوائهم، وكفى بربك -أيها النبي- عاصمًا وحافظًا للمؤمنين مِن كيد الشيطان وغروره.');
INSERT INTO Quran__Arabic_Tafseer_Muyassar (QuranChapterID, VerseNumber, Rukuh, Sajdah, Manzil, HizbQuarter, VerseText) VALUES (17, 66, '0', '0', '0', '0', 'ربكم -أيها الناس- هو الذي يُسَيِّر لكم السفن في البحر؛ لتطلبوا رزق الله في أسفاركم وتجاراتكم. إن الله سبحانه كان رحيمًا بعباده.');
INSERT INTO Quran__Arabic_Tafseer_Muyassar (QuranChapterID, VerseNumber, Rukuh, Sajdah, Manzil, HizbQuarter, VerseText) VALUES (17, 67, '0', '0', '0', '0', 'وإذا أصابتكم شدة في البحر حتى أشرفتم على الغرق والهلاك، غاب عن عقولكم الذين تعبدونهم من الآلهة، وتذكَّرتم الله القدير وحده؛ ليغيثكم وينقذكم، فأخلصتم له في طلب العون والإغاثة، فأغاثكم ونجَّاكم، فلمَّا نجاكم إلى البر أعرضتم عن الإيمان والإخلاص والعمل الصالح، وهذا من جهل الإنسان وكفره. وكان الإنسان جحودًا لنعم الله عزَّ وجل.');
INSERT INTO Quran__Arabic_Tafseer_Muyassar (QuranChapterID, VerseNumber, Rukuh, Sajdah, Manzil, HizbQuarter, VerseText) VALUES (17, 68, '0', '0', '0', '0', 'أغَفَلْتم -أيها الناس- عن عذاب الله، فأمنتم أن تنهار بكم الأرض خسفًا، أو يُمْطركم الله بحجارة من السماء فتقتلكم، ثم لا تجدوا أحدًا يحفظكم مِن عذابه؟');
INSERT INTO Quran__Arabic_Tafseer_Muyassar (QuranChapterID, VerseNumber, Rukuh, Sajdah, Manzil, HizbQuarter, VerseText) VALUES (17, 69, '0', '0', '0', '0', 'أم أمنتم -أيها الناس- ربكم، وقد كفرتم به أن يعيدكم في البحر مرة أخرى، فيرسل عليكم ريحًا شديدة، تكسِّر كل ما أتت عليه، فيغرقكم بسبب كفركم، ثم لا تجدوا لكم علينا أي تبعة ومطالبة؛ فإن الله لم يظلمكم مثقال ذرة؟');
INSERT INTO Quran__Arabic_Tafseer_Muyassar (QuranChapterID, VerseNumber, Rukuh, Sajdah, Manzil, HizbQuarter, VerseText) VALUES (17, 70, '1', '0', '0', '1', 'ولقد كرَّمنا ذرية آدم بالعقل وإرسال الرسل، وسَخَّرنا لهم جميع ما في الكون، وسَخَّرنا لهم الدواب في البر والسفن في البحر لحملهم، ورزقناهم من طيبات المطاعم والمشارب، وفضَّلناهم على كثير من المخلوقات تفضيلا عظيمًا.');
INSERT INTO Quran__Arabic_Tafseer_Muyassar (QuranChapterID, VerseNumber, Rukuh, Sajdah, Manzil, HizbQuarter, VerseText) VALUES (17, 71, '0', '0', '0', '0', 'اذكر -أيها الرسول- يوم البعث مبشرًا ومخوفًا، حين يدعو الله عز وجل كل جماعة من الناس مع إمامهم الذي كانوا يقتدون به في الدنيا، فمن كان منهم صالحًا، وأُعطي كتاب أعماله بيمينه، فهؤلاء يقرؤون كتاب حسناتهم فرحين مستبشرين، ولا يُنْقَصون من ثواب أعمالهم الصالحة شيئًا، وإن كان مقدارَ الخيط الذي يكون في شَقِّ النواة.');
INSERT INTO Quran__Arabic_Tafseer_Muyassar (QuranChapterID, VerseNumber, Rukuh, Sajdah, Manzil, HizbQuarter, VerseText) VALUES (17, 72, '0', '0', '0', '0', 'ومن كان في هذه الدنيا أعمى القلب عن دلائل قدرة الله فلم يؤمن بما جاء به الرسول محمد صلى الله عليه وسلم فهو في يوم القيامة أشدُّ عمى عن سلوك طريق الجنة، وأضل طريقًا عن الهداية والرشاد.');
INSERT INTO Quran__Arabic_Tafseer_Muyassar (QuranChapterID, VerseNumber, Rukuh, Sajdah, Manzil, HizbQuarter, VerseText) VALUES (17, 73, '0', '0', '0', '0', 'ولقد قارب المشركون أن يصرفوك -أيها الرسول- عن القرآن الذي أنزله الله إليك؛ لتختلق علينا غير ما أوحينا إليك، ولو فعلت ما أرادوه لاتخذوك حبيبًا خالصًا.');
INSERT INTO Quran__Arabic_Tafseer_Muyassar (QuranChapterID, VerseNumber, Rukuh, Sajdah, Manzil, HizbQuarter, VerseText) VALUES (17, 74, '0', '0', '0', '0', 'ولولا أن ثبَّتناك على الحق، وعصمناك عن موافقتهم، لَقاربْتَ أن تميل إليهم ميلا قليلا من كثرة المعالجة ورغبتك في هدايتهم.');
INSERT INTO Quran__Arabic_Tafseer_Muyassar (QuranChapterID, VerseNumber, Rukuh, Sajdah, Manzil, HizbQuarter, VerseText) VALUES (17, 75, '0', '0', '0', '0', 'ولو رَكَنت -أيها الرسول- إلى هؤلاء المشركين ركونًا قليلا فيما سألوك، إذًا لأذقناك مِثْلَي عذاب الحياة في الدنيا ومثْلَي عذاب الممات في الآخرة؛ وذلك لكمال نعمة الله عليك وكمال معرفتك، ثم لا تجد أحدًا ينصرك ويدفع عنك عذابنا.');
INSERT INTO Quran__Arabic_Tafseer_Muyassar (QuranChapterID, VerseNumber, Rukuh, Sajdah, Manzil, HizbQuarter, VerseText) VALUES (17, 76, '0', '0', '0', '0', 'ولقد قارب الكفار أن يخرجوك من "مكة" بإزعاجهم إيَّاك، ولو أخرجوك منها لم يمكثوا فيها بعدك إلا زمنًا قليلا حتى تحل بهم العقوبة العاجلة.');
INSERT INTO Quran__Arabic_Tafseer_Muyassar (QuranChapterID, VerseNumber, Rukuh, Sajdah, Manzil, HizbQuarter, VerseText) VALUES (17, 77, '1', '0', '0', '0', 'تلك سنة الله تعالى في إهلاك الأمة التي تُخرج رسولها من بينها، ولن تجد -أيها الرسول- لسنتنا تغييرًا، فلا خلف في وعدنا.');
INSERT INTO Quran__Arabic_Tafseer_Muyassar (QuranChapterID, VerseNumber, Rukuh, Sajdah, Manzil, HizbQuarter, VerseText) VALUES (17, 78, '0', '0', '0', '0', 'أقم الصلاة تامة من وقت زوال الشمس عند الظهيرة إلى وقت ظلمة الليل، ويدخل في هذا صلاة الظهر والعصر والمغرب والعشاء، وأقم صلاة الفجر، وأَطِلِ القراءة فيها؛ إن صلاة الفجر تحضرها ملائكة الليل وملائكة النهار.');
INSERT INTO Quran__Arabic_Tafseer_Muyassar (QuranChapterID, VerseNumber, Rukuh, Sajdah, Manzil, HizbQuarter, VerseText) VALUES (17, 79, '0', '0', '0', '0', 'وقم -أيها النبي- من نومك بعض الليل، فاقرأ القرآن في صلاة الليل؛ لتكون صلاة الليل زيادة لك في علو القدر ورفع الدرجات، عسى أن يبعثك الله شافعًا للناس يوم القيامة؛ ليرحمهم الله مما يكونون فيه، وتقوم مقامًا يحمدك فيه الأولون والآخرون.');
INSERT INTO Quran__Arabic_Tafseer_Muyassar (QuranChapterID, VerseNumber, Rukuh, Sajdah, Manzil, HizbQuarter, VerseText) VALUES (17, 80, '0', '0', '0', '0', 'وقل: ربِّ أدخلني فيما هو خير لي مدخل صدق، وأخرجني مما هو شر لي مخرج صدق، واجعل لي مِن لدنك حجة ثابتة، تنصرني بها على جميع مَن خالفني.');
INSERT INTO Quran__Arabic_Tafseer_Muyassar (QuranChapterID, VerseNumber, Rukuh, Sajdah, Manzil, HizbQuarter, VerseText) VALUES (17, 81, '0', '0', '0', '0', 'وقل -أيها الرسول- للمشركين: جاء الإسلام وذهب الشرك، إن الباطل لا بقاء له ولا ثبات، والحق هو الثابت الباقي الذي لا يزول.');
INSERT INTO Quran__Arabic_Tafseer_Muyassar (QuranChapterID, VerseNumber, Rukuh, Sajdah, Manzil, HizbQuarter, VerseText) VALUES (17, 82, '0', '0', '0', '0', 'وننزل من آيات القرآن العظيم ما يشفي القلوب مِنَ الأمراض، كالشك والنفاق والجهالة، وما يشفي الأبدان برُقْيتها به، وما يكون سببًا للفوز برحمة الله بما فيه من الإيمان، ولا يزيد هذا القرآن الكفار عند سماعه إلا كفرًا وضلالا؛ لتكذيبهم به وعدم إيمانهم.');
INSERT INTO Quran__Arabic_Tafseer_Muyassar (QuranChapterID, VerseNumber, Rukuh, Sajdah, Manzil, HizbQuarter, VerseText) VALUES (17, 83, '0', '0', '0', '0', 'وإذا أنعمنا على الإنسان من حيث هو بمال وعافية ونحوهما، تولَّى وتباعد عن طاعة ربه، وإذا أصابته شدة مِن فقر أو مرض كان قنوطًا؛ لأنه لا يثق بفضل الله تعالى، إلا من عصم الله في حالتي سرَّائه وضرَّائه.');
INSERT INTO Quran__Arabic_Tafseer_Muyassar (QuranChapterID, VerseNumber, Rukuh, Sajdah, Manzil, HizbQuarter, VerseText) VALUES (17, 84, '1', '0', '0', '0', 'قل -أيها الرسول- للناس: كل واحد منكم يعمل على ما يليق به من الأحوال، فربكم أعلم بمن هو أهدى طريقًا إلى الحق.');
INSERT INTO Quran__Arabic_Tafseer_Muyassar (QuranChapterID, VerseNumber, Rukuh, Sajdah, Manzil, HizbQuarter, VerseText) VALUES (17, 85, '0', '0', '0', '0', 'ويسألك الكفار عن حقيقة الروح تعنتًا، فأجبهم بأن حقيقة الروح وأحوالها من الأمور التي استأثر الله بعلمها، وما أُعطيتم أنتم وجميع الناس من العلم إلا شيئًا قليلا.');
INSERT INTO Quran__Arabic_Tafseer_Muyassar (QuranChapterID, VerseNumber, Rukuh, Sajdah, Manzil, HizbQuarter, VerseText) VALUES (17, 86, '0', '0', '0', '0', 'ولئن شئنا مَحْوَ القرآن من قلبك لَقدَرْنا على ذلك، ثم لا تجد لنفسك ناصرًا يمنعنا من فعل ذلك، أو يرد عليك القرآن.');
INSERT INTO Quran__Arabic_Tafseer_Muyassar (QuranChapterID, VerseNumber, Rukuh, Sajdah, Manzil, HizbQuarter, VerseText) VALUES (17, 87, '0', '0', '0', '0', 'لكنَّ الله رحمك، فأثبت ذلك في قلبك، إن فضله كان عليك عظيمًا؛ فقد أعطاك هذا القرآن العظيم، والمقام المحمود، وغير ذلك مما لم يؤته أحدًا من العالمين.');
INSERT INTO Quran__Arabic_Tafseer_Muyassar (QuranChapterID, VerseNumber, Rukuh, Sajdah, Manzil, HizbQuarter, VerseText) VALUES (17, 88, '0', '0', '0', '0', 'قل: لو اتفقت الإنس والجن على محاولة الإتيان بمثل هذا القرآن المعجز لا يستطيعون الإتيان به، ولو تعاونوا وتظاهروا على ذلك.');
INSERT INTO Quran__Arabic_Tafseer_Muyassar (QuranChapterID, VerseNumber, Rukuh, Sajdah, Manzil, HizbQuarter, VerseText) VALUES (17, 89, '0', '0', '0', '0', 'ولقد بيَّنَّا ونَوَّعنا للناس في هذا القرآن من كل مثل ينبغي الاعتبار به؛ احتجاجًا بذلك عليهم؛ ليتبعوه ويعملوا به، فأبى أكثر الناس إلا جحودًا للحق وإنكارًا لحجج الله وأدلته.');
INSERT INTO Quran__Arabic_Tafseer_Muyassar (QuranChapterID, VerseNumber, Rukuh, Sajdah, Manzil, HizbQuarter, VerseText) VALUES (17, 90, '0', '0', '0', '0', 'ولما أعجز القرآن المشركين وغلبهم أخذوا يطلبون معجزات وَفْق أهوائهم فقالوا: لن نصدقك -أيها الرسول- ونعمل بما تقول حتى تفجر لنا من أرض "مكة" عينًا جارية.');
INSERT INTO Quran__Arabic_Tafseer_Muyassar (QuranChapterID, VerseNumber, Rukuh, Sajdah, Manzil, HizbQuarter, VerseText) VALUES (17, 91, '0', '0', '0', '0', 'أو تكون لك حديقة فيها أنواع النخيل والأعناب، وتجعل الأنهار تجري في وسطها بغزارة.');
INSERT INTO Quran__Arabic_Tafseer_Muyassar (QuranChapterID, VerseNumber, Rukuh, Sajdah, Manzil, HizbQuarter, VerseText) VALUES (17, 92, '0', '0', '0', '0', 'أو تسقط السماء علينا قطعًا كما زَعَمْتَ، أو تأتي لنا بالله وملائكته، فنشاهدهم مقابلة وعِيانًا.');
INSERT INTO Quran__Arabic_Tafseer_Muyassar (QuranChapterID, VerseNumber, Rukuh, Sajdah, Manzil, HizbQuarter, VerseText) VALUES (17, 93, '1', '0', '0', '0', 'أو يكون لك بيت من ذهب، أو تصعد في درج إلى السماء، ولن نصدِّقك في صعودك حتى تعود، ومعك كتاب من الله منشور نقرأ فيه أنك رسول الله حقا. قل -أيها الرسول- متعجبًا مِن تعنُّت هؤلاء الكفار: سبحان ربي!! هل أنا إلا عبد من عباده مبلِّغ رسالته؟ فكيف أقدر على فعل ما تطلبون؟');
INSERT INTO Quran__Arabic_Tafseer_Muyassar (QuranChapterID, VerseNumber, Rukuh, Sajdah, Manzil, HizbQuarter, VerseText) VALUES (17, 94, '0', '0', '0', '0', 'وما منع الكفارَ من الإيمان بالله ورسوله وطاعتهما، حين جاءهم البيان الكافي من عند الله، إلا قولهم جهلا وإنكارًا: أبعث الله رسولا من جنس البشر؟');
INSERT INTO Quran__Arabic_Tafseer_Muyassar (QuranChapterID, VerseNumber, Rukuh, Sajdah, Manzil, HizbQuarter, VerseText) VALUES (17, 95, '0', '0', '0', '0', 'قل -أيها الرسول- ردًّا على المشركين إنكارهم أن يكون الرسول من البشر: لو كان في الأرض ملائكة يمشون عليها مطمئنين، لأرسلنا إليهم رسولا من جنسهم، ولكنَّ أهل الأرض بشر، فالرسول إليهم ينبغي أن يكون من جنسهم؛ ليمكنهم مخاطبته وفَهْم كلامه.');
INSERT INTO Quran__Arabic_Tafseer_Muyassar (QuranChapterID, VerseNumber, Rukuh, Sajdah, Manzil, HizbQuarter, VerseText) VALUES (17, 96, '0', '0', '0', '0', 'قل لهم: كفى بالله شهيدًا بيني وبينكم على صِدْقي وحقيقة نبوَّتي. إنه سبحانه خبير بأحوال عباده، بصير بأعمالهم، وسيجازيهم عليها.');
INSERT INTO Quran__Arabic_Tafseer_Muyassar (QuranChapterID, VerseNumber, Rukuh, Sajdah, Manzil, HizbQuarter, VerseText) VALUES (17, 97, '0', '0', '0', '0', 'ومن يهده الله فهو المهتدي إلى الحق، ومن يضلله فيخذلْه ويَكِلْه إلى نفسه فلا هادي له من دون الله، وهؤلاء الضُّلال يبعثهم الله يوم القيامة، ويحشرهم على وجوههم، وهم لا يرون ولا ينطقون ولا يسمعون، مصيرهم إلى نار جهنم الملتهبة، كلما سكن لهيبها، وخمدت نارها، زدناهم نارًا ملتهبة متأججة.');
INSERT INTO Quran__Arabic_Tafseer_Muyassar (QuranChapterID, VerseNumber, Rukuh, Sajdah, Manzil, HizbQuarter, VerseText) VALUES (17, 98, '0', '0', '0', '0', 'هذا الذي وُصِف من العذاب عقاب للمشركين؛ بسبب كفرهم بآيات الله وحججه، وتكذيبهم رسله الذين دَعَوْهم إلى عبادته، وقولهم استنكارًا - إذا أُمروا بالتصديق بالبعث -: أإذا متنا وصِرْنا عظامًا بالية وأجزاءً متفتتة نُبعث بعد ذلك خَلْقًا جديدًا؟');
INSERT INTO Quran__Arabic_Tafseer_Muyassar (QuranChapterID, VerseNumber, Rukuh, Sajdah, Manzil, HizbQuarter, VerseText) VALUES (17, 99, '0', '0', '0', '1', 'أَغَفَل هؤلاء المشركون، فلم يتبصروا ويعلموا أن الله الذي خلق السموات والأرض وما فيهن من المخلوقات على غير مثال سابق، قادر على أن يخلق أمثالهم بعد فنائهم؟ وقد جعل الله لهؤلاء المشركين وقتًا محددًا لموتهم وعذابهم، لا شك أنه آتيهم، ومع وضوح الحق ودلائله أبى الكافرون إلا جحودًا لدين الله عزَّ وجلَّ.');
INSERT INTO Quran__Arabic_Tafseer_Muyassar (QuranChapterID, VerseNumber, Rukuh, Sajdah, Manzil, HizbQuarter, VerseText) VALUES (17, 100, '1', '0', '0', '0', 'قل -أيها الرسول- لهؤلاء المشركين: لو كنتم تملكون خزائن رحمة ربي التي لا تنفد ولا تبيد إذًا لبخلتم بها، فلم تعطوا منها غيركم خوفًا مِن نفادها فتصبحوا فقراء. ومن شأن الإنسان أنه بخيل بما في يده إلا مَن عصم الله بالإيمان.');
INSERT INTO Quran__Arabic_Tafseer_Muyassar (QuranChapterID, VerseNumber, Rukuh, Sajdah, Manzil, HizbQuarter, VerseText) VALUES (17, 101, '0', '0', '0', '0', 'ولقد آتينا موسى تسع معجزات واضحات شاهدات على صِدْق نبوته وهي: العصا واليد والسنون ونقص الثمرات والطوفان والجراد والقمل والضفادع والدم، فاسأل -أيها الرسول- اليهود سؤال تقرير حين جاء موسى أسلافهم بمعجزاته الواضحات، فقال فرعون لموسى: إني لأظنك -يا موسى- ساحرا، مخدوعًا مغلوبًا على عقلك بما تأتيه من غرائب الأفعال.');
INSERT INTO Quran__Arabic_Tafseer_Muyassar (QuranChapterID, VerseNumber, Rukuh, Sajdah, Manzil, HizbQuarter, VerseText) VALUES (17, 102, '0', '0', '0', '0', 'فردَّ عليه موسى: لقد تيقَّنتَ -يا فرعون- أنه ما أنزل تلك المعجزات التسع الشاهدة على صدق نبوتي إلا رب السموات والأرض؛ لتكون دلالات يَستدِل بها أولو البصائر على وحدانية الله تعالى في ربوبيته وألوهيته، وإني لعلى يقين أنك -يا فرعون- هالك ملعون مغلوب.');
INSERT INTO Quran__Arabic_Tafseer_Muyassar (QuranChapterID, VerseNumber, Rukuh, Sajdah, Manzil, HizbQuarter, VerseText) VALUES (17, 103, '0', '0', '0', '0', 'فأراد فرعون أن يزعج موسى ويخرجه مع بني إسرائيل مِن أرض "مصر"، فأغرقناه ومَن معه مِن جندٍ في البحر عقابًا لهم.');
INSERT INTO Quran__Arabic_Tafseer_Muyassar (QuranChapterID, VerseNumber, Rukuh, Sajdah, Manzil, HizbQuarter, VerseText) VALUES (17, 104, '0', '0', '0', '0', 'وقلنا من بعد هلاك فرعون وجنده لبني إسرائيل: اسكنوا أرض "الشام"، فإذا جاء يوم القيامة جئنا بكم جميعًا مِن قبوركم إلى موقف الحساب.');
INSERT INTO Quran__Arabic_Tafseer_Muyassar (QuranChapterID, VerseNumber, Rukuh, Sajdah, Manzil, HizbQuarter, VerseText) VALUES (17, 105, '0', '0', '0', '0', 'وبالحق أنزلنا هذا القرآن على محمد صلى الله عليه وسلم لأمْرِ العباد ونهيهم وثوابهم وعقابهم، وبالصدق والعدل والحفظ من التغيير والتبديل نزل. وما أرسلناك -أيها الرسول- إلا مبشرًا بالجنة لمن أطاع، ومخوفًا بالنار لمن عصى وكفر.');
INSERT INTO Quran__Arabic_Tafseer_Muyassar (QuranChapterID, VerseNumber, Rukuh, Sajdah, Manzil, HizbQuarter, VerseText) VALUES (17, 106, '0', '0', '0', '0', 'وأنزلنا إليك -أيها الرسول- قرآنًا بيَّناه وأحكمناه وفَصَّلناه فارقًا بين الهدى والضلال والحق والباطل؛ لتقرأه على الناس في تؤدة وتمهُّل، ونَزَّلْناه مفرَّقًا، شيئًا بعد شيء، على حسب الحوادث ومقتضيات الأحوال.');
INSERT INTO Quran__Arabic_Tafseer_Muyassar (QuranChapterID, VerseNumber, Rukuh, Sajdah, Manzil, HizbQuarter, VerseText) VALUES (17, 107, '0', '0', '0', '0', 'قل -أيها الرسول- لهؤلاء المكذبين: آمِنوا بالقرآن أو لا تؤمنوا؛ فإن إيمانكم لا يزيده كمالا وتكذيبكم لا يُلْحِق به نقصًا. إن العلماء الذين أوتوا الكتب السابقة مِن قبل القرآن، وعرفوا حقيقة الوحي، إذا قرئ عليهم القرآن يخشعون، فيسجدون على وجوههم لله سبحانه وتعالى.');
INSERT INTO Quran__Arabic_Tafseer_Muyassar (QuranChapterID, VerseNumber, Rukuh, Sajdah, Manzil, HizbQuarter, VerseText) VALUES (17, 108, '0', '0', '0', '0', 'ويقول هؤلاء الذين أوتوا العلم عند سماع القرآن: تنزيهًا لربنا وتبرئة له مما يصفه المشركون به، ما كان وعد الله تعالى من ثواب وعقاب إلا واقعًا حقًا.');
INSERT INTO Quran__Arabic_Tafseer_Muyassar (QuranChapterID, VerseNumber, Rukuh, Sajdah, Manzil, HizbQuarter, VerseText) VALUES (17, 109, '0', '1', '0', '0', 'ويقع هؤلاء ساجدين على وجوههم، يبكون تأثرًا بمواعظ القرآن، ويزيدهم سماع القرآن ومواعظه خضوعًا لأمر الله وعظيم قدرته.');
INSERT INTO Quran__Arabic_Tafseer_Muyassar (QuranChapterID, VerseNumber, Rukuh, Sajdah, Manzil, HizbQuarter, VerseText) VALUES (17, 110, '0', '0', '0', '0', 'قل -أيها الرسول- لمشركي قومك الذين أنكروا عليك الدعاء بقولك: يا الله يا رحمن، ادعوا الله، أو ادعوا الرحمن، فبأي أسمائه دعوتموه فإنكم تدعون ربًا واحدًا؛ لأن أسماءه كلها حسنى. ولا تجهر بالقراءة في صلاتك، فيسمعك المشركون، ولا تُسِرَّ بها فلا يسمعك أصحابك، وكن وسطًا بين الجهر والهمس.');
INSERT INTO Quran__Arabic_Tafseer_Muyassar (QuranChapterID, VerseNumber, Rukuh, Sajdah, Manzil, HizbQuarter, VerseText) VALUES (17, 111, '1', '0', '0', '0', 'وقل -أيها الرسول-: الحمد لله الذي له الكمال والثناء، الذي تنزَّه عن الولد والشريك في ألوهيته، ولا يكون له سبحانه وليٌّ مِن خلقه فهو الغني القوي، وهم الفقراء المحتاجون إليه، وعظِّمه تعظيمًا تامًا بالثناء عليه وعبادته وحده لا شريك له، وإخلاص الدين كله له.');
INSERT INTO Quran__Arabic_Tafseer_Muyassar (QuranChapterID, VerseNumber, Rukuh, Sajdah, Manzil, HizbQuarter, VerseText) VALUES (18, 1, '0', '0', '0', '0', 'الثناء على الله بصفاته التي كلُّها أوصاف كمال، وبنعمه الظاهرة والباطنة، الدينية والدنيوية، الذي تفضَّل فأنزل على عبده ورسوله محمد صلى الله عليه وسلم القرآن، ولم يجعل فيه شيئًا من الميل عن الحق.');
INSERT INTO Quran__Arabic_Tafseer_Muyassar (QuranChapterID, VerseNumber, Rukuh, Sajdah, Manzil, HizbQuarter, VerseText) VALUES (18, 2, '0', '0', '0', '0', 'جعله الله كتابًا مستقيمًا، لا اختلاف فيه ولا تناقض؛ لينذر الكافرين من عذاب شديد من عنده، ويبشر المصدقين بالله ورسوله الذين يعملون الأعمال الصالحات، بأن لهم ثوابًا جزيلا هو الجنة، يقيمون في هذا النعيم لا يفارقونه أبدًا.');
INSERT INTO Quran__Arabic_Tafseer_Muyassar (QuranChapterID, VerseNumber, Rukuh, Sajdah, Manzil, HizbQuarter, VerseText) VALUES (18, 3, '0', '0', '0', '0', 'جعله الله كتابًا مستقيمًا، لا اختلاف فيه ولا تناقض؛ لينذر الكافرين من عذاب شديد من عنده، ويبشر المصدقين بالله ورسوله الذين يعملون الأعمال الصالحات، بأن لهم ثوابًا جزيلا هو الجنة، يقيمون في هذا النعيم لا يفارقونه أبدًا.');
INSERT INTO Quran__Arabic_Tafseer_Muyassar (QuranChapterID, VerseNumber, Rukuh, Sajdah, Manzil, HizbQuarter, VerseText) VALUES (18, 4, '0', '0', '0', '0', 'وينذر به المشركين الذين قالوا: اتخذ الله ولدا.');
INSERT INTO Quran__Arabic_Tafseer_Muyassar (QuranChapterID, VerseNumber, Rukuh, Sajdah, Manzil, HizbQuarter, VerseText) VALUES (18, 5, '0', '0', '0', '0', 'ليس عند هؤلاء المشركين شيء من العلم على ما يَدَّعونه لله من اتخاذ الولد، كما لم يكن عند أسلافهم الذين قلَّدوهم، عَظُمت هذه المقالة الشنيعة التي تخرج من أفواههم، ما يقولون إلا قولا كاذبًا.');
INSERT INTO Quran__Arabic_Tafseer_Muyassar (QuranChapterID, VerseNumber, Rukuh, Sajdah, Manzil, HizbQuarter, VerseText) VALUES (18, 6, '0', '0', '0', '0', 'فلعلك -أيها الرسول- مُهْلِك نفسك غمًّا وحزنًا على أثر تولِّي قومك وإعراضهم عنك، إن لم يصدِّقوا بهذا القرآن ويعملوا به.');
INSERT INTO Quran__Arabic_Tafseer_Muyassar (QuranChapterID, VerseNumber, Rukuh, Sajdah, Manzil, HizbQuarter, VerseText) VALUES (18, 7, '0', '0', '0', '0', 'إنَّا جعلنا ما على وجه الأرض من المخلوقات جَمالا لها، ومنفعة لأهلها؛ لنختبرهم: أيُّهم أحسن عملا بطاعتنا، وأيهم أسوأ عملا بالمعاصي، ونجزي كلا بما يستحق.');
INSERT INTO Quran__Arabic_Tafseer_Muyassar (QuranChapterID, VerseNumber, Rukuh, Sajdah, Manzil, HizbQuarter, VerseText) VALUES (18, 8, '0', '0', '0', '0', 'وإنَّا لجاعلون ما على الأرض من تلك الزينة عند انقضاء الدنيا ترابًا، لا نبات فيه.');
INSERT INTO Quran__Arabic_Tafseer_Muyassar (QuranChapterID, VerseNumber, Rukuh, Sajdah, Manzil, HizbQuarter, VerseText) VALUES (18, 9, '0', '0', '0', '0', 'لا تظن -أيها الرسول- أن قصة أصحاب الكهف واللوح الذي كُتِبت فيه أسماؤهم من آياتنا عجيبة وغريبة؛ فإن خلق السموات والأرض وما فيهما أعجب من ذلك.');
INSERT INTO Quran__Arabic_Tafseer_Muyassar (QuranChapterID, VerseNumber, Rukuh, Sajdah, Manzil, HizbQuarter, VerseText) VALUES (18, 10, '0', '0', '0', '0', 'اذكر -أيها الرسول- حين لجأ الشبَّان المؤمنون إلى الكهف؛ خشية من فتنة قومهم لهم، وإرغامهم على عبادة الأصنام، فقالوا: ربنا أعطنا مِن عندك رحمة، تثبتنا بها، وتحفظنا من الشر، ويسِّر لنا الطريق الصواب الذي يوصلنا إلى العمل الذي تحب، فنكون راشدين غير ضالين.');
INSERT INTO Quran__Arabic_Tafseer_Muyassar (QuranChapterID, VerseNumber, Rukuh, Sajdah, Manzil, HizbQuarter, VerseText) VALUES (18, 11, '0', '0', '0', '0', 'فألقينا عليهم النوم العميق، فبقوا في الكهف سنين كثيرة.');
INSERT INTO Quran__Arabic_Tafseer_Muyassar (QuranChapterID, VerseNumber, Rukuh, Sajdah, Manzil, HizbQuarter, VerseText) VALUES (18, 12, '1', '0', '0', '0', 'ثم أيقظناهم مِن نومهم؛ لنُظهر للناس ما علمناه في الأزل؛ فتتميَّز أي الطائفتين المتنازعتين في مدة لبثهم أضبط في الإحصاء، وهل لبثوا يومًا أو بعض يوم، أو مدة طويلة؟');
INSERT INTO Quran__Arabic_Tafseer_Muyassar (QuranChapterID, VerseNumber, Rukuh, Sajdah, Manzil, HizbQuarter, VerseText) VALUES (18, 13, '0', '0', '0', '0', 'نحن نقصُّ عليك -أيها الرسول- خبرهم بالصدق. إن أصحاب الكهف شُبَّان صدَّقوا ربهم وامتثلوا أمره، وزِدْناهم هدى وثباتًا على الحق.');
INSERT INTO Quran__Arabic_Tafseer_Muyassar (QuranChapterID, VerseNumber, Rukuh, Sajdah, Manzil, HizbQuarter, VerseText) VALUES (18, 14, '0', '0', '0', '0', 'وقوَّينا قلوبهم بالإيمان، وشددنا عزيمتهم به، حين قاموا بين يدي الملك الكافر، وهو يلومهم على تَرْكِ عبادة الأصنام فقالوا له: ربنا الذي نعبده هو رب السموات والأرض، لن نعبد غيره من الآلهة، لو قلنا غير هذا لكُنَّا قد قلنا قولا جائرًا بعيدًا عن الحق.');
INSERT INTO Quran__Arabic_Tafseer_Muyassar (QuranChapterID, VerseNumber, Rukuh, Sajdah, Manzil, HizbQuarter, VerseText) VALUES (18, 15, '0', '0', '0', '0', 'ثم قال بعضهم لبعض: هؤلاء قومنا اتخذوا لهم آلهة غير الله، فهلا أتَوْا على عبادتهم لها بدليل واضح، فلا أحد أشد ظلمًا ممن اختلق على الله الكذب بنسبة الشريك إليه في عبادته.');
INSERT INTO Quran__Arabic_Tafseer_Muyassar (QuranChapterID, VerseNumber, Rukuh, Sajdah, Manzil, HizbQuarter, VerseText) VALUES (18, 16, '0', '0', '0', '0', 'وحين فارقتم قومكم بدينكم، وتركتم ما يعبدون من الآلهة إلا عبادة الله، فالجؤوا إلى الكهف في الجبل لعبادة ربكم وحده، يَبْسطْ لكم ربكم من رحمته ما يستركم به في الدارين، ويسهل لكم من أمركم ما تنتفعون به في حياتكم من أسباب العيش.');
INSERT INTO Quran__Arabic_Tafseer_Muyassar (QuranChapterID, VerseNumber, Rukuh, Sajdah, Manzil, HizbQuarter, VerseText) VALUES (18, 17, '1', '0', '0', '1', 'فلما فعلوا ذلك ألقى الله عليهم النوم وحَفِظهم. وترى -أيها المشاهد لهم- الشمس إذا طلعت من المشرق تميل عن مكانهم إلى جهة اليمين، وإذا غربت تتركهم إلى جهة اليسار، وهم في متسع من الكهف، فلا تؤذيهم حرارة الشمس ولا ينقطع عنهم الهواء، ذلك الذي فعلناه بهؤلاء الفتية من دلائل قدرة الله. من يوفقه الله للاهتداء بآياته فهو الموفَّق إلى الحق، ومن لم يوفقه لذلك فلن تجد له معينًا يرشده لإصابة الحق؛ لأن التوفيق والخِذْلان بيد الله وحده.');
INSERT INTO Quran__Arabic_Tafseer_Muyassar (QuranChapterID, VerseNumber, Rukuh, Sajdah, Manzil, HizbQuarter, VerseText) VALUES (18, 18, '0', '0', '0', '0', 'وتظن -أيها الناظر- أهل الكهف أيقاظًا، وهم في الواقع نيام، ونتعهدهم بالرعاية، فنُقَلِّبهم حال نومهم مرة للجنب الأيمن ومرة للجنب الأيسر؛ لئلا تأكلهم الأرض، وكلبهم الذي صاحَبهم مادٌّ ذراعيه بفناء الكهف، لو عاينتهم لأدبرت عنهم هاربًا، ولَمُلِئَتْ نفسك منهم فزعًا.');
INSERT INTO Quran__Arabic_Tafseer_Muyassar (QuranChapterID, VerseNumber, Rukuh, Sajdah, Manzil, HizbQuarter, VerseText) VALUES (18, 19, '0', '0', '0', '0', 'وكما أنمناهم وحفظناهم هذه المدة الطويلة أيقظناهم مِن نومهم على هيئتهم دون تغيُّر؛ لكي يسأل بعضهم بعضًا: كم من الوقت مكثنا نائمين هنا؟ فقال بعضهم: مكثنا يوما أو بعض يوم، وقال آخرون التبس عليهم الأمر: فَوِّضوا عِلْم ذلك لله، فربكم أعلم بالوقت الذي مكثتموه، فأرسِلوا أحدكم بنقودكم الفضية هذه إلى مدينتنا فلينظر: أيَّ أهل المدينة أحلُّ وأطيب طعامًا؟ فليأتكم بقوت منه، وليتلطف في شرائه مع البائع حتى لا ننكشف، ويظهر أمرنا، ولا يُعْلِمَنَّ بكم أحدًا من الناس.');
INSERT INTO Quran__Arabic_Tafseer_Muyassar (QuranChapterID, VerseNumber, Rukuh, Sajdah, Manzil, HizbQuarter, VerseText) VALUES (18, 20, '0', '0', '0', '0', 'إن قومكم إن يطَّلعوا عليكم يرجموكم بالحجارة، فيقتلوكم، أو يردوكم إلى دينهم، فتصيروا كفارًا، ولن تفوزوا بمطلبكم مِن دخول الجنة -إن فعلتم ذلك- أبدًا.');
INSERT INTO Quran__Arabic_Tafseer_Muyassar (QuranChapterID, VerseNumber, Rukuh, Sajdah, Manzil, HizbQuarter, VerseText) VALUES (18, 21, '0', '0', '0', '0', 'وكما أنمناهم سنين كثيرة، وأيقظناهم بعدها، أطْلَعنا عليهم أهل ذلك الزمان، بعد أن كشف البائع نوع الدراهم التي جاء بها مبعوثهم؛ ليعلم الناس أنَّ وَعْدَ الله بالبعث حق، وأن القيامة آتية لا شك فيها، إذ يتنازع المطَّلِعون على أصحاب الكهف في أمر القيامة: فمِن مُثْبِتٍ لها ومِن مُنْكِر، فجعل الله إطْلاعهم على أصحاب الكهف حجة للمؤمنين على الكافرين. وبعد أن انكشف أمرهم، وماتوا قال فريق من المطَّلِعين عليهم: ابنوا على باب الكهف بناءً يحجبهم، واتركوهم وشأنهم، ربهم أعلم بحالهم، وقال أصحاب الكلمة والنفوذ فيهم: لنتخذنَّ على مكانهم مسجدًا للعبادة. وقد نهى رسول الله صلى الله عليه وسلم عن اتخاذ قبور الأنبياء والصالحين مساجد، ولعن مَن فَعَلَ ذلك في آخر وصاياه لأمته، كما أنه نهى عن البناء على القبور مطلقًا، وعن تجصيصها والكتابة عليها؛ لأن ذلك من الغلو الذي قد يؤدي إلى عبادة مَن فيها.');
INSERT INTO Quran__Arabic_Tafseer_Muyassar (QuranChapterID, VerseNumber, Rukuh, Sajdah, Manzil, HizbQuarter, VerseText) VALUES (18, 22, '1', '0', '0', '0', 'سيقول بعض الخائضين في شأنهم من أهل الكتاب: هم ثلاثةٌ، رابعهم كلبهم، ويقول فريق آخر: هم خمسة، سادسهم كلبهم، وكلام الفريقين قول بالظن من غير دليل، وتقول جماعة ثالثة: هم سبعة، وثامنهم كلبهم، قل -أيها الرسول-: ربي هو الأعلم بعددهم، ما يعلم عددهم إلا قليل من خلقه. فلا تجادل أهل الكتاب في عددهم إلا جدالا ظاهرًا لا عمق فيه، بأن تَقُصَّ عليهم ما أخبرك به الوحي فحسب، ولا تسألهم عن عددهم وأحوالهم؛ فإنهم لا يعلمون ذلك.');
INSERT INTO Quran__Arabic_Tafseer_Muyassar (QuranChapterID, VerseNumber, Rukuh, Sajdah, Manzil, HizbQuarter, VerseText) VALUES (18, 23, '0', '0', '0', '0', 'ولا تقولنَّ لشيء تعزم على فعله: إني فاعل ذلك الشيء غدًا إلا أن تُعَلِّق قولك بالمشيئة، فتقول: إن شاء الله. واذكر ربك عند النسيان بقول: إن شاء الله، وكلما نسيت فاذكر الله؛ فإن ذِكْرَ الله يُذهِب النسيان، وقل: عسى أن يهديني ربي لأقرب الطرق الموصلة إلى الهدى والرشاد.');
INSERT INTO Quran__Arabic_Tafseer_Muyassar (QuranChapterID, VerseNumber, Rukuh, Sajdah, Manzil, HizbQuarter, VerseText) VALUES (18, 24, '0', '0', '0', '0', 'ولا تقولنَّ لشيء تعزم على فعله: إني فاعل ذلك الشيء غدًا إلا أن تُعَلِّق قولك بالمشيئة، فتقول: إن شاء الله. واذكر ربك عند النسيان بقول: إن شاء الله، وكلما نسيت فاذكر الله؛ فإن ذِكْرَ الله يُذهِب النسيان، وقل: عسى أن يهديني ربي لأقرب الطرق الموصلة إلى الهدى والرشاد.');
INSERT INTO Quran__Arabic_Tafseer_Muyassar (QuranChapterID, VerseNumber, Rukuh, Sajdah, Manzil, HizbQuarter, VerseText) VALUES (18, 25, '0', '0', '0', '0', 'ومكث الشُّبَّان نيامًا في كهفهم ثلاثمائة سنة وتسع سنين.');
INSERT INTO Quran__Arabic_Tafseer_Muyassar (QuranChapterID, VerseNumber, Rukuh, Sajdah, Manzil, HizbQuarter, VerseText) VALUES (18, 26, '0', '0', '0', '0', 'وإذا سُئلت -أيها الرسول- عن مدة لبثهم في الكهف، وليس عندك علم في ذلك وتوقيف من الله، فلا تتقدم فيه بشيء، بل قل: الله أعلم بمدة لبثهم، له غيب السموات والأرض، أَبْصِرْ به وأسمع، أي: تعجب من كمال بصره وسمعه وإحاطته بكل شيء. ليس للخلق أحد غيره يتولى أمورهم، وليس له شريك في حكمه وقضائه وتشريعه، سبحانه وتعالى.');
INSERT INTO Quran__Arabic_Tafseer_Muyassar (QuranChapterID, VerseNumber, Rukuh, Sajdah, Manzil, HizbQuarter, VerseText) VALUES (18, 27, '0', '0', '0', '0', 'واتل -أيها الرسول- ما أوحاه الله إليك من القرآن، فإنه الكتاب الذي لا مبدِّل لكلماته لصدقها وعدلها، ولن تجد من دون ربك ملجأً تلجأ إليه، ولا معاذًا تعوذ به.');
INSERT INTO Quran__Arabic_Tafseer_Muyassar (QuranChapterID, VerseNumber, Rukuh, Sajdah, Manzil, HizbQuarter, VerseText) VALUES (18, 28, '0', '0', '0', '0', 'واصبر نفسك -أيها النبي- مع أصحابك مِن فقراء المؤمنين الذين يعبدون ربهم وحده، ويدعونه في الصباح والمساء، يريدون بذلك وجهه، واجلس معهم وخالطهم، ولا تصرف نظرك عنهم إلى غيرهم من الكفار لإرادة التمتع بزينة الحياة الدنيا، ولا تُطِعْ من جعلنا قلبه غافلا عن ذكرنا، وآثَرَ هواه على طاعة مولاه، وصار أمره في جميع أعماله ضياعًا وهلاكًا.');
INSERT INTO Quran__Arabic_Tafseer_Muyassar (QuranChapterID, VerseNumber, Rukuh, Sajdah, Manzil, HizbQuarter, VerseText) VALUES (18, 29, '0', '0', '0', '0', 'وقل لهؤلاء الغافلين: ما جئتكم به هو الحق من ربكم، فمن أراد منكم أن يصدق ويعمل به، فليفعل فهو خير له، ومن أراد أن يجحد فليفعل، فما ظَلَم إلا نفسه. إنا أعتدنا للكافرين نارًا شديدة أحاط بهم سورها، وإن يستغث هؤلاء الكفار في النار بطلب الماء مِن شدة العطش، يُؤتَ لهم بماء كالزيت العَكِر شديد الحرارة يشوي وجوههم. قَبُح هذا الشراب الذي لا يروي ظمأهم بل يزيده، وقَبُحَتْ النار منزلا لهم ومقامًا. وفي هذا وعيد وتهديد شديد لمن أعرض عن الحق، فلم يؤمن برسالة محمد صلى الله عليه وسلم، ولم يعمل بمقتضاها.');
INSERT INTO Quran__Arabic_Tafseer_Muyassar (QuranChapterID, VerseNumber, Rukuh, Sajdah, Manzil, HizbQuarter, VerseText) VALUES (18, 30, '0', '0', '0', '0', 'إن الذين آمنوا بالله ورسوله وعملوا الأعمال الصالحات لهم أعظم المثوبة، إنا لا نضيع أجورهم، ولا ننقصها على ما أحسنوه من العمل.');
INSERT INTO Quran__Arabic_Tafseer_Muyassar (QuranChapterID, VerseNumber, Rukuh, Sajdah, Manzil, HizbQuarter, VerseText) VALUES (18, 31, '1', '0', '0', '0', 'أولئك الذين آمنوا لهم جنات يقيمون فيها دائمًا، تجري من تحت غرفهم ومنازلهم الأنهار العذبة، يُحَلَّون فيها بأساور الذهب، ويَلْبَسون ثيابًا ذات لون أخضر نسجت من رقيق الحرير وغليظه، يتكئون فيها على الأسِرَّة المزدانة بالستائر الجميلة، نِعْمَ الثواب ثوابهم، وحَسُنتِ الجنة منزلا ومكانًا لهم.');
INSERT INTO Quran__Arabic_Tafseer_Muyassar (QuranChapterID, VerseNumber, Rukuh, Sajdah, Manzil, HizbQuarter, VerseText) VALUES (18, 32, '0', '0', '0', '1', 'واضرب -أيها الرسول- لكفار قومك مثلا رجلين من الأمم السابقة: أحدهما مؤمن، والآخر كافر، وقد جعلنا للكافر حديقتين من أعناب، وأحطناهما بنخل كثير، وأنبتنا وسطهما زروعًا مختلفة نافعة.');
INSERT INTO Quran__Arabic_Tafseer_Muyassar (QuranChapterID, VerseNumber, Rukuh, Sajdah, Manzil, HizbQuarter, VerseText) VALUES (18, 33, '0', '0', '0', '0', 'وقد أثمرت كل واحدة من الحديقتين ثمرها، ولم تُنْقِص منه شيئًا، وشققنا بينهما نهرًا لسقيهما بسهولة ويسر.');
INSERT INTO Quran__Arabic_Tafseer_Muyassar (QuranChapterID, VerseNumber, Rukuh, Sajdah, Manzil, HizbQuarter, VerseText) VALUES (18, 34, '0', '0', '0', '0', 'وكان لصاحب الحديقتين ثمر وأموال أخرى، فقال لصاحبه المؤمن، وهو يحاوره في الحديث، والغرور يملؤه: أنا أكثر منك مالا وأعز أنصارًا وأعوانًا.');
INSERT INTO Quran__Arabic_Tafseer_Muyassar (QuranChapterID, VerseNumber, Rukuh, Sajdah, Manzil, HizbQuarter, VerseText) VALUES (18, 35, '0', '0', '0', '0', 'ودخل حديقته، وهو ظالم لنفسه بالكفر بالبعث، وشكه في قيام الساعة، فأعجبته ثمارها وقال: ما أعتقد أن تَهْلِك هذه الحديقة مدى الحياة، وما أعتقد أن القيامة واقعة، وإن فُرِضَ وقوعها -كما تزعم أيها المؤمن- ورُجعتُ إلى ربي لأجدنَّ عنده أفضل من هذه الحديقة مرجعًا ومردًا؛ لكرامتي ومنزلتي عنده.');
INSERT INTO Quran__Arabic_Tafseer_Muyassar (QuranChapterID, VerseNumber, Rukuh, Sajdah, Manzil, HizbQuarter, VerseText) VALUES (18, 36, '0', '0', '0', '0', 'ودخل حديقته، وهو ظالم لنفسه بالكفر بالبعث، وشكه في قيام الساعة، فأعجبته ثمارها وقال: ما أعتقد أن تَهْلِك هذه الحديقة مدى الحياة، وما أعتقد أن القيامة واقعة، وإن فُرِضَ وقوعها -كما تزعم أيها المؤمن- ورُجعتُ إلى ربي لأجدنَّ عنده أفضل من هذه الحديقة مرجعًا ومردًا؛ لكرامتي ومنزلتي عنده.');
INSERT INTO Quran__Arabic_Tafseer_Muyassar (QuranChapterID, VerseNumber, Rukuh, Sajdah, Manzil, HizbQuarter, VerseText) VALUES (18, 37, '0', '0', '0', '0', 'قال له صاحبه المؤمن، وهو يحاوره واعظًا له: كيف تكفر بالله الذي خلقك مِن تراب، ثم مِن نطفة الأبوين، ثم سَوَّاك بشرًا معتدل القامة والخَلْق؟ وفي هذه المحاورة دليل على أن القادر على ابتداء الخلق، قادر على إعادتهم.');
INSERT INTO Quran__Arabic_Tafseer_Muyassar (QuranChapterID, VerseNumber, Rukuh, Sajdah, Manzil, HizbQuarter, VerseText) VALUES (18, 38, '0', '0', '0', '0', 'لكن أنا لا أقول بمقالتك الدالة على كفرك، وإنما أقول: المنعم المتفضل هو الله ربي وحده، ولا أشرك في عبادتي له أحدًا غيره.');
INSERT INTO Quran__Arabic_Tafseer_Muyassar (QuranChapterID, VerseNumber, Rukuh, Sajdah, Manzil, HizbQuarter, VerseText) VALUES (18, 39, '0', '0', '0', '0', 'وهلا حين دخَلْتَ حديقتك فأعجبتك حَمِدت الله، وقلت: هذا ما شاء الله لي، لا قوة لي على تحصيله إلا بالله. إن كنت تراني أقل منك مالا وأولادًا، فعسى ربي أن يعطيني أفضل من حديقتك، ويسلبك النعمة بكفرك، ويرسل على حديقتك عذابا من السماء، فتصبح أرضًا ملساء جرداء لا تثبت عليها قدم، ولا ينبت فيها نبات، أو يصير ماؤها الذي تُسقى منه غائرًا في الأرض، فلا تقدر على إخراجه.');
INSERT INTO Quran__Arabic_Tafseer_Muyassar (QuranChapterID, VerseNumber, Rukuh, Sajdah, Manzil, HizbQuarter, VerseText) VALUES (18, 40, '0', '0', '0', '0', 'وهلا حين دخَلْتَ حديقتك فأعجبتك حَمِدت الله، وقلت: هذا ما شاء الله لي، لا قوة لي على تحصيله إلا بالله. إن كنت تراني أقل منك مالا وأولادًا، فعسى ربي أن يعطيني أفضل من حديقتك، ويسلبك النعمة بكفرك، ويرسل على حديقتك عذابا من السماء، فتصبح أرضًا ملساء جرداء لا تثبت عليها قدم، ولا ينبت فيها نبات، أو يصير ماؤها الذي تُسقى منه غائرًا في الأرض، فلا تقدر على إخراجه.');
INSERT INTO Quran__Arabic_Tafseer_Muyassar (QuranChapterID, VerseNumber, Rukuh, Sajdah, Manzil, HizbQuarter, VerseText) VALUES (18, 41, '0', '0', '0', '0', 'وهلا حين دخَلْتَ حديقتك فأعجبتك حَمِدت الله، وقلت: هذا ما شاء الله لي، لا قوة لي على تحصيله إلا بالله. إن كنت تراني أقل منك مالا وأولادًا، فعسى ربي أن يعطيني أفضل من حديقتك، ويسلبك النعمة بكفرك، ويرسل على حديقتك عذابا من السماء، فتصبح أرضًا ملساء جرداء لا تثبت عليها قدم، ولا ينبت فيها نبات، أو يصير ماؤها الذي تُسقى منه غائرًا في الأرض، فلا تقدر على إخراجه.');
INSERT INTO Quran__Arabic_Tafseer_Muyassar (QuranChapterID, VerseNumber, Rukuh, Sajdah, Manzil, HizbQuarter, VerseText) VALUES (18, 42, '0', '0', '0', '0', 'وتحَقَّقَ ما قاله المؤمن، ووقع الدمار بالحديقة، فهلك كل ما فيها، فصار الكافر يُقَلِّب كفيه حسرةً وندامة على ما أنفق فيها، وهي خاوية قد سقط بعضها على بعض، ويقول: يا ليتني عرفت نِعَمَ الله وقدرته فلم أشرك به أحدًا. وهذا ندم منه حين لا ينفعه الندم.');
INSERT INTO Quran__Arabic_Tafseer_Muyassar (QuranChapterID, VerseNumber, Rukuh, Sajdah, Manzil, HizbQuarter, VerseText) VALUES (18, 43, '0', '0', '0', '0', 'ولم تكن له جماعة ممن افتخر بهم يمنعونه مِن عقاب الله النازل به، وما كان ممتنعًا بنفسه وقوته.');
INSERT INTO Quran__Arabic_Tafseer_Muyassar (QuranChapterID, VerseNumber, Rukuh, Sajdah, Manzil, HizbQuarter, VerseText) VALUES (18, 44, '1', '0', '0', '0', 'في مثل هذه الشدائد تكون الولاية والنصرة لله الحق، هو خير جزاءً، وخير عاقبة لمن تولاهم من عباده المؤمنين.');
INSERT INTO Quran__Arabic_Tafseer_Muyassar (QuranChapterID, VerseNumber, Rukuh, Sajdah, Manzil, HizbQuarter, VerseText) VALUES (18, 45, '0', '0', '0', '0', 'واضرب أيها الرسول للناس -وبخاصة ذوو الكِبْر منهم - صفة الدنيا التي اغترُّوا بها في بهجتها وسرعة زوالها، فهي كماء أنزله الله من السماء فخرج به النبات بإذنه، وصار مُخْضرًّا، وما هي إلا مدة يسيرة حتى صار هذا النبات يابسًا متكسرًا تنسفه الرياح إلى كل جهة. وكان الله على كل شيء مقتدرًا، أي: ذا قدرة عظيمة على كل شيء.');
INSERT INTO Quran__Arabic_Tafseer_Muyassar (QuranChapterID, VerseNumber, Rukuh, Sajdah, Manzil, HizbQuarter, VerseText) VALUES (18, 46, '0', '0', '0', '0', 'الأموال والأولاد جَمال وقوة في هذه الدنيا الفانية، والأعمال الصالحة -وبخاصة التسبيحُ والتحميد والتكبير والتهليل- أفضل أجرًا عند ربك من المال والبنين، وهذه الأعمال الصالحة أفضل ما يرجو الإنسان من الثواب عند ربه، فينال بها في الآخرة ما كان يأمُله في الدنيا.');
INSERT INTO Quran__Arabic_Tafseer_Muyassar (QuranChapterID, VerseNumber, Rukuh, Sajdah, Manzil, HizbQuarter, VerseText) VALUES (18, 47, '0', '0', '0', '0', 'واذكر لهم يوم نُزيل الجبال عن أماكنها، وتبصر الأرض ظاهرة، ليس عليها ما يسترها مما كان عليها من المخلوقات، وجمعنا الأولين والآخِرين لموقف الحساب، فلم نترك منهم أحدًا.');
INSERT INTO Quran__Arabic_Tafseer_Muyassar (QuranChapterID, VerseNumber, Rukuh, Sajdah, Manzil, HizbQuarter, VerseText) VALUES (18, 48, '0', '0', '0', '0', 'وعُرِضوا جميعًا على ربك مصطَفِّين لا يُحجب منهم أحد، لقد بعثناكم، وجئتم إلينا فرادى لا مال معكم ولا ولد، كما خلقناكم أول مرة، بل ظننتم أن لن نجعل لكم موعدًا نبعثكم فيه، ونجازيكم على أعمالكم.');
INSERT INTO Quran__Arabic_Tafseer_Muyassar (QuranChapterID, VerseNumber, Rukuh, Sajdah, Manzil, HizbQuarter, VerseText) VALUES (18, 49, '1', '0', '0', '0', 'ووُضِع كتاب أعمال كل واحد في يمينه أو في شماله، فتبصر العصاة خائفين مما فيه بسبب ما قدموه من جرائمهم، ويقولون حين يعاينونه: يا هلاكنا! ما لهذا الكتاب لم يترك صغيرة مِن أفعالنا ولا كبيرة إلا أثبتها؟! ووجدوا كل ما عملوه في الدنيا حاضرًا مثبتًا. ولا يظلم ربك أحدًا مثقال ذرة، فلا يُنقَص طائع من ثوابه، ولا يُزاد عاص في عقابه.');
INSERT INTO Quran__Arabic_Tafseer_Muyassar (QuranChapterID, VerseNumber, Rukuh, Sajdah, Manzil, HizbQuarter, VerseText) VALUES (18, 50, '0', '0', '0', '0', 'واذكر حين أمرنا الملائكة بالسجود لآدم، تحية له لا عبادة، وأمرنا إبليس بما أُمِروا به، فسجد الملائكة جميعًا، لكن إبليس الذي كان من الجن خرج عن طاعة ربه، ولم يسجد كِبرًا وحسدًا. أفتجعلونه -أيها الناس- وذريته أعوانًا لكم تطيعونهم وتتركون طاعتي، وهم ألد أعدائكم؟ قَبُحَتْ طاعة الظالمين للشيطان بدلا عن طاعة الرحمن.');
INSERT INTO Quran__Arabic_Tafseer_Muyassar (QuranChapterID, VerseNumber, Rukuh, Sajdah, Manzil, HizbQuarter, VerseText) VALUES (18, 51, '0', '0', '0', '1', 'ما أحضرتُ إبليس وذريته -الذين أطعتموهم- خَلْقَ السموات والأرض، فأستعين بهم على خلقهما، ولا أشهدتُ بعضهم على خَلْق بعض، بل تفردتُ بخلق جميع ذلك، بغير معين ولا ظهير، وما كنت متخذ المضلِّين من الشياطين وغيرهم أعوانًا. فكيف تصرفون إليهم حقي، وتتخذونهم أولياء من دوني، وأنا خالق كل شيء؟');
INSERT INTO Quran__Arabic_Tafseer_Muyassar (QuranChapterID, VerseNumber, Rukuh, Sajdah, Manzil, HizbQuarter, VerseText) VALUES (18, 52, '0', '0', '0', '0', 'واذكر لهم إذ يقول الله للمشركين يوم القيامة: نادوا شركائي الذين كنتم تزعمون أنهم شركاء لي في العبادة؛ لينصروكم اليوم مني، فاستغاثوا بهم فلم يغيثوهم، وجعلنا بين العابدين والمعبودين مهلكًا في جهنم يهلكون فيه جميعًا.');
INSERT INTO Quran__Arabic_Tafseer_Muyassar (QuranChapterID, VerseNumber, Rukuh, Sajdah, Manzil, HizbQuarter, VerseText) VALUES (18, 53, '1', '0', '0', '0', 'وشاهد المجرمون النار، فأيقنوا أنهم واقعون فيها لا محالة، ولم يجدوا عنها معدلا للانصراف عنها إلى غيرها.');
INSERT INTO Quran__Arabic_Tafseer_Muyassar (QuranChapterID, VerseNumber, Rukuh, Sajdah, Manzil, HizbQuarter, VerseText) VALUES (18, 54, '0', '0', '0', '0', 'ولقد وضَّحنا ونوَّعنا في هذا القرآن للناس أنواعًا كثيرة من الأمثال؛ ليتعظوا بها ويؤمنوا. وكان الإنسان أكثر المخلوقات خصومة وجدلا.');
INSERT INTO Quran__Arabic_Tafseer_Muyassar (QuranChapterID, VerseNumber, Rukuh, Sajdah, Manzil, HizbQuarter, VerseText) VALUES (18, 55, '0', '0', '0', '0', 'وما منع الناس من الإيمان -حين جاءهم الرسول محمد صلى الله عليه وسلم ومعه القرآن-، واستغفار ربهم طالبين عفوه عنهم، إلا تحدِّيهم للرسول، وطلبهم أن تصيبهم سنة الله في إهلاك السابقين عليهم، أو يصيبهم عذاب الله عِيانًا.');
INSERT INTO Quran__Arabic_Tafseer_Muyassar (QuranChapterID, VerseNumber, Rukuh, Sajdah, Manzil, HizbQuarter, VerseText) VALUES (18, 56, '0', '0', '0', '0', 'وما نبعث الرسل إلى الناس إلا ليكونوا مبشرين بالجنة لأهل الإيمان والعمل الصالح، ومخوِّفين بالنار لأهل الكفر والعصيان، ومع وضوح الحق يخاصم الذين كفروا رسلهم بالباطل تعنتًا؛ ليزيلوا بباطلهم الحق الذي جاءهم به الرسول، واتخذوا كتابي وحججي وما خُوّفوا به من العذاب سخرية واستهزاء.');
INSERT INTO Quran__Arabic_Tafseer_Muyassar (QuranChapterID, VerseNumber, Rukuh, Sajdah, Manzil, HizbQuarter, VerseText) VALUES (18, 57, '0', '0', '0', '0', 'ولا أحد أشد ظلمًا ممن وُعِظ بآيات ربه الواضحة، فانصرف عنها إلى باطله، ونسي ما قدَّمته يداه من الأفعال القبيحة فلم يرجع عنها، إنَّا جعلنا على قلوبهم أغطية، فلم يفهموا القرآن، ولم يدركوا ما فيه من الخير، وجعلنا في آذانهم ما يشبه الصمم، فلم يسمعوه ولم ينتفعوا به، وإن تَدْعُهم إلى الإيمان فلن يستجيبوا لك، ولن يهتدوا إليه أبدًا.');
INSERT INTO Quran__Arabic_Tafseer_Muyassar (QuranChapterID, VerseNumber, Rukuh, Sajdah, Manzil, HizbQuarter, VerseText) VALUES (18, 58, '0', '0', '0', '0', 'وربك الغفور لذنوب عباده إذا تابوا، ذو الرحمة بهم، لو يعاقب هؤلاء المعرضين عن آياته بما كسبوا من الذنوب والآثام لعجَّل لهم العذاب، ولكنه تعالى حليم لا يعجل بالعقوبة، بل لهم موعد يجازون فيه بأعمالهم، لا مندوحة لهم عنه ولا محيد.');
INSERT INTO Quran__Arabic_Tafseer_Muyassar (QuranChapterID, VerseNumber, Rukuh, Sajdah, Manzil, HizbQuarter, VerseText) VALUES (18, 59, '1', '0', '0', '0', 'وتلك القرى القريبة منكم -كقرى قوم هود وصالح ولوط وشعيب- أهلكناها حين ظلم أهلها بالكفر، وجعلنا لهلاكهم ميقاتًا وأجلا حين بلغوه جاءهم العذاب فأهلكهم الله به.');
INSERT INTO Quran__Arabic_Tafseer_Muyassar (QuranChapterID, VerseNumber, Rukuh, Sajdah, Manzil, HizbQuarter, VerseText) VALUES (18, 60, '0', '0', '0', '0', 'واذكر حين قال موسى لخادمه يوشع بن نون: لا أزال أتابع السير حتى أصل إلى ملتقى البحرين، أو أسير زمنًا طويلا حتى أصل إلى العبد الصالح؛ لأتعلم منه ما ليس عندي من العلم.');
INSERT INTO Quran__Arabic_Tafseer_Muyassar (QuranChapterID, VerseNumber, Rukuh, Sajdah, Manzil, HizbQuarter, VerseText) VALUES (18, 61, '0', '0', '0', '0', 'وجَدَّا في السَّيْر، فلما وصلا ملتقى البحرين جلسا عند صخرة، ونسيا حوتهما الذي أُمر موسى بأخذه معه قوتًا لهما، وحمله يوشع في مِكْتَل، فإذا الحوت يصبح حيًّا وينحدر في البحر، ويتخذ له فيه طريقًا مفتوحًا.');
INSERT INTO Quran__Arabic_Tafseer_Muyassar (QuranChapterID, VerseNumber, Rukuh, Sajdah, Manzil, HizbQuarter, VerseText) VALUES (18, 62, '0', '0', '0', '0', 'فلما فارقا المكان الذي نسيا فيه الحوت وشعر موسى بالجوع، قال لخادمه: أحضر إلينا غداءنا، لقد لقينا من سفرنا هذا تعبًا.');
INSERT INTO Quran__Arabic_Tafseer_Muyassar (QuranChapterID, VerseNumber, Rukuh, Sajdah, Manzil, HizbQuarter, VerseText) VALUES (18, 63, '0', '0', '0', '0', 'قال له خادمه: أتذكر حين لجأنا إلى الصخرة التي استرحنا عندها؟ فإني نسيت أن أخبرك ما كان من الحوت، وما أنساني أن أذكر ذلك لك إلا الشيطان، فإن الحوت الميت دبَّتْ فيه الحياة، وقفز في البحر، واتخذ له فيه طريقًا، وكان أمره مما يُعْجَبُ منه.');
INSERT INTO Quran__Arabic_Tafseer_Muyassar (QuranChapterID, VerseNumber, Rukuh, Sajdah, Manzil, HizbQuarter, VerseText) VALUES (18, 64, '0', '0', '0', '0', 'قال موسى: ما حصل هو ما كنا نطلبه، فإنه علامة لي على مكان العبد الصالح، فرجعا يقصان آثار مشيهما حتى انتهيا إلى الصخرة.');
INSERT INTO Quran__Arabic_Tafseer_Muyassar (QuranChapterID, VerseNumber, Rukuh, Sajdah, Manzil, HizbQuarter, VerseText) VALUES (18, 65, '0', '0', '0', '0', 'فوجدا هناك عبدًا صالحًا من عبادنا هو الخَضِر عليه السلام -وهو نبي من أنبياء الله توفاه الله-، آتيناه رحمة من عندنا، وعَلَّمْناه مِن لدنَّا علمًا عظيمًا.');
INSERT INTO Quran__Arabic_Tafseer_Muyassar (QuranChapterID, VerseNumber, Rukuh, Sajdah, Manzil, HizbQuarter, VerseText) VALUES (18, 66, '0', '0', '0', '0', 'فسلَّم عليه موسى، وقال له: أتأذن لي أن أتبعك؛ لتعلمني من العلم الذي علمك الله إياه ما أسترشد به وأنتفع؟');
INSERT INTO Quran__Arabic_Tafseer_Muyassar (QuranChapterID, VerseNumber, Rukuh, Sajdah, Manzil, HizbQuarter, VerseText) VALUES (18, 67, '0', '0', '0', '0', 'قال له الخَضِر: إنك -يا موسى- لن تطيق أن تصبر على اتباعي وملازمتي.');
INSERT INTO Quran__Arabic_Tafseer_Muyassar (QuranChapterID, VerseNumber, Rukuh, Sajdah, Manzil, HizbQuarter, VerseText) VALUES (18, 68, '0', '0', '0', '0', 'وكيف لك الصبر على ما سأفعله من أمور تخفى عليك مما علمنيه الله تعالى؟');
INSERT INTO Quran__Arabic_Tafseer_Muyassar (QuranChapterID, VerseNumber, Rukuh, Sajdah, Manzil, HizbQuarter, VerseText) VALUES (18, 69, '0', '0', '0', '0', 'قال له موسى: ستجدني إن شاء الله صابرًا على ما أراه منك، ولا أخالف لك أمرًا تأمرني به.');
INSERT INTO Quran__Arabic_Tafseer_Muyassar (QuranChapterID, VerseNumber, Rukuh, Sajdah, Manzil, HizbQuarter, VerseText) VALUES (18, 70, '1', '0', '0', '0', 'فوافق الخَضِر وقال له: فإنْ صاحَبتني فلا تسألني عن شيء تنكره، حتى أبيِّن لك من أمره ما خفي عليك دون سؤال منك.');
INSERT INTO Quran__Arabic_Tafseer_Muyassar (QuranChapterID, VerseNumber, Rukuh, Sajdah, Manzil, HizbQuarter, VerseText) VALUES (18, 71, '0', '0', '0', '0', 'فانطلقا يمشيان على الساحل، فمرت بهما سفينة، فطلبا من أهلها أن يركبا معهم، فلما ركبا قَلَعَ الخَضِر لوحًا من السفينة فخرقها، فقال له موسى: أَخَرَقْتَ السفينة؛ لتُغرِق أهلَها، وقد حملونا بغير أجر؟ لقد فعلت أمرًا منكرًا.');
INSERT INTO Quran__Arabic_Tafseer_Muyassar (QuranChapterID, VerseNumber, Rukuh, Sajdah, Manzil, HizbQuarter, VerseText) VALUES (18, 72, '0', '0', '0', '0', 'قال له الخَضِر: لقد قلت لك من أول الأمر: إنك لن تستطيع الصبر على صحبتي.');
INSERT INTO Quran__Arabic_Tafseer_Muyassar (QuranChapterID, VerseNumber, Rukuh, Sajdah, Manzil, HizbQuarter, VerseText) VALUES (18, 73, '0', '0', '0', '0', 'قال موسى معتذرًا: لا تؤاخذني بنسياني شرطك عليَّ، ولا تكلفني مشقةً في تعلُّمي منك، وعاملني بيسر ورفق.');
INSERT INTO Quran__Arabic_Tafseer_Muyassar (QuranChapterID, VerseNumber, Rukuh, Sajdah, Manzil, HizbQuarter, VerseText) VALUES (18, 74, '0', '0', '0', '0', 'فقبل الخَضِر عذره، ثم خرجا من السفينة، فبينما هما يمشيان على الساحل إذ أبصرا غلامًا يلعب مع الغلمان، فقتله الخَضِر، فأنكر موسى عليه وقال: كيف قتلت نفسًا طاهرة لم تبلغ حدَّ التكليف، ولم تقتل نفسًا، حتى تستحق القتل بها؟ لقد فَعَلْتَ أمرًا منكرًا عظيمًا.');
INSERT INTO Quran__Arabic_Tafseer_Muyassar (QuranChapterID, VerseNumber, Rukuh, Sajdah, Manzil, HizbQuarter, VerseText) VALUES (18, 75, '0', '0', '0', '1', 'قال الخَضِر لموسى معاتبًا ومذكرًا: ألم أقل لك إنك لن تستطيع معي صبرًا على ما ترى من أفعالي مما لم تحط به خُبْرًا؟');
INSERT INTO Quran__Arabic_Tafseer_Muyassar (QuranChapterID, VerseNumber, Rukuh, Sajdah, Manzil, HizbQuarter, VerseText) VALUES (18, 76, '0', '0', '0', '0', 'قال موسى له: إن سألتك عن شيء بعد هذه المرة فاتركني ولا تصاحبني، قد بلغتَ العذر في شأني ولم تقصر؛ حيث أخبرتَني أني لن أستطيع معك صبرًا.');
INSERT INTO Quran__Arabic_Tafseer_Muyassar (QuranChapterID, VerseNumber, Rukuh, Sajdah, Manzil, HizbQuarter, VerseText) VALUES (18, 77, '0', '0', '0', '0', 'فذهب موسى والخَضِر حتى أتيا أهل قرية، فطلبا منهم طعامًا على سبيل الضيافة، فامتنع أهل القرية عن ضيافتهما، فوجدا فيها حائطًا مائلا يوشك أن يسقط، فعدَّل الخَضِر مَيْلَه حتى صار مستويًا، قال له موسى: لو شئت لأخذت على هذا العمل أجرًا تصرفه في تحصيل طعامنا حيث لم يضيفونا.');
INSERT INTO Quran__Arabic_Tafseer_Muyassar (QuranChapterID, VerseNumber, Rukuh, Sajdah, Manzil, HizbQuarter, VerseText) VALUES (18, 78, '0', '0', '0', '0', 'قال الخَضِر لموسى: هذا وقت الفراق بيني وبينك، سأخبرك بما أنكرت عليَّ من أفعالي التي فعلتها، والتي لم تستطع صبرًا على ترك السؤال عنها والإنكار عليَّ فيها.');
INSERT INTO Quran__Arabic_Tafseer_Muyassar (QuranChapterID, VerseNumber, Rukuh, Sajdah, Manzil, HizbQuarter, VerseText) VALUES (18, 79, '0', '0', '0', '0', 'أما السفينة التي خرقتها فإنها كانت لأناس مساكين يعملون في البحر عليها سعيًا وراء الرزق، فأردت أن أعيبها بذلك الخرق؛ لأن أمامهم ملكًا يأخذ كل سفينة صالحة غصبًا من أصحابها.');
INSERT INTO Quran__Arabic_Tafseer_Muyassar (QuranChapterID, VerseNumber, Rukuh, Sajdah, Manzil, HizbQuarter, VerseText) VALUES (18, 80, '0', '0', '0', '0', 'وأما الغلام الذي قتلته فكان في علم الله كافرًا، وكان أبوه وأمه مؤمِنَيْن، فخشينا لو بقي الغلام حيًا لَحمل والديه على الكفر والطغيان؛ لأجل محبتهما إياه أو للحاجة إليه.');
INSERT INTO Quran__Arabic_Tafseer_Muyassar (QuranChapterID, VerseNumber, Rukuh, Sajdah, Manzil, HizbQuarter, VerseText) VALUES (18, 81, '0', '0', '0', '0', 'فأردنا أن يُبْدِل الله أبويه بمن هو خير منه صلاحًا ودينًا وبرًا بهما.');
INSERT INTO Quran__Arabic_Tafseer_Muyassar (QuranChapterID, VerseNumber, Rukuh, Sajdah, Manzil, HizbQuarter, VerseText) VALUES (18, 82, '1', '0', '0', '0', 'وأما الحائط الذي عدَّلتُ مَيْلَه حتى استوى فإنه كان لغلامين يتيمين في القرية التي فيها الجدار، وكان تحته كنز لهما من الذهب والفضة، وكان أبوهما رجلا صالحًا، فأراد ربك أن يكبَرا ويبلغا قوتهما، ويستخرجا كنزهما رحمة من ربك بهما، وما فعلتُ يا موسى جميع الذي رأيتَني فعلتُه عن أمري ومن تلقاء نفسي، وإنما فعلته عن أمر الله، ذلك الذي بَيَّنْتُ لك أسبابه هو عاقبة الأمور التي لم تستطع صبرًا على ترك السؤال عنها والإنكار عليَّ فيها.');
INSERT INTO Quran__Arabic_Tafseer_Muyassar (QuranChapterID, VerseNumber, Rukuh, Sajdah, Manzil, HizbQuarter, VerseText) VALUES (18, 83, '0', '0', '0', '0', 'ويسألك -أيها الرسول- هؤلاء المشركون من قومك عن خبر ذي القرنين الملك الصالح، قل لهم: سأقصُّ عليكم منه ذِكْرًا تتذكرونه، وتعتبرون به.');
INSERT INTO Quran__Arabic_Tafseer_Muyassar (QuranChapterID, VerseNumber, Rukuh, Sajdah, Manzil, HizbQuarter, VerseText) VALUES (18, 84, '0', '0', '0', '0', 'إنا مكَّنَّا له في الأرض، وآتيناه من كل شيء أسبابًا وطرقًا، يتوصل بها إلى ما يريد مِن فَتْح المدائن وقهر الأعداء وغير ذلك.');
INSERT INTO Quran__Arabic_Tafseer_Muyassar (QuranChapterID, VerseNumber, Rukuh, Sajdah, Manzil, HizbQuarter, VerseText) VALUES (18, 85, '0', '0', '0', '0', 'فأخذ بتلك الأسباب والطرق بجد واجتهاد.');
INSERT INTO Quran__Arabic_Tafseer_Muyassar (QuranChapterID, VerseNumber, Rukuh, Sajdah, Manzil, HizbQuarter, VerseText) VALUES (18, 86, '0', '0', '0', '0', 'حتى إذا وصل ذو القرنين إلى مغرب الشمس وجدها في مرأى العين كأنها تغرب في عين حارة ذات طين أسود، ووجد عند مغربها قومًا. قلنا: يا ذا القرنين إما أن تعذبهم بالقتل أو غيره، إن لم يقروا بتوحيد الله، وإما أن تحسن إليهم، فتعلمهم الهدى وتبصرهم الرشاد.');
INSERT INTO Quran__Arabic_Tafseer_Muyassar (QuranChapterID, VerseNumber, Rukuh, Sajdah, Manzil, HizbQuarter, VerseText) VALUES (18, 87, '0', '0', '0', '0', 'قال ذو القرنين: أمَّا مَن ظلم نفسه منهم فكفر بربه، فسوف نعذبه في الدنيا، ثم يرجع إلى ربه، فيعذبه عذابًا عظيمًا في نار جهنم.');
INSERT INTO Quran__Arabic_Tafseer_Muyassar (QuranChapterID, VerseNumber, Rukuh, Sajdah, Manzil, HizbQuarter, VerseText) VALUES (18, 88, '0', '0', '0', '0', 'وأما مَن آمن منهم بربه فصدَّق به ووحَّده وعمل بطاعته فله الجنة ثوابًا من الله، وسنحسن إليه، ونلين له في القول ونيسِّر له المعاملة.');
INSERT INTO Quran__Arabic_Tafseer_Muyassar (QuranChapterID, VerseNumber, Rukuh, Sajdah, Manzil, HizbQuarter, VerseText) VALUES (18, 89, '0', '0', '0', '0', 'ثم رجع ذو القرنين إلى المشرق متبعًا الأسباب التي أعطاه الله إياها.');
INSERT INTO Quran__Arabic_Tafseer_Muyassar (QuranChapterID, VerseNumber, Rukuh, Sajdah, Manzil, HizbQuarter, VerseText) VALUES (18, 90, '0', '0', '0', '0', 'حتى إذا وصل إلى مطلع الشمس وجدها تطلع على قوم ليس لهم بناء يسترهم، ولا شجر يظلهم من الشمس.');
INSERT INTO Quran__Arabic_Tafseer_Muyassar (QuranChapterID, VerseNumber, Rukuh, Sajdah, Manzil, HizbQuarter, VerseText) VALUES (18, 91, '0', '0', '0', '0', 'كذلك وقد أحاط عِلْمُنا بما عنده من الخير والأسباب العظيمة، حيثما توجَّه وسار.');
INSERT INTO Quran__Arabic_Tafseer_Muyassar (QuranChapterID, VerseNumber, Rukuh, Sajdah, Manzil, HizbQuarter, VerseText) VALUES (18, 92, '0', '0', '0', '0', 'ثم سار ذو القرنين آخذًا بالطرق والأسباب التي منحناها إياه.');
INSERT INTO Quran__Arabic_Tafseer_Muyassar (QuranChapterID, VerseNumber, Rukuh, Sajdah, Manzil, HizbQuarter, VerseText) VALUES (18, 93, '0', '0', '0', '0', 'حتى إذا وصل إلى ما بين الجبلين الحاجزين لما وراءهما، وجد من دونهما قومًا، لا يكادون يعرفون كلام غيرهم.');
INSERT INTO Quran__Arabic_Tafseer_Muyassar (QuranChapterID, VerseNumber, Rukuh, Sajdah, Manzil, HizbQuarter, VerseText) VALUES (18, 94, '0', '0', '0', '0', 'قالوا يا ذا القرنين: إنَّ يأجوج ومأجوج -وهما أمَّتان عظيمتان من بني آدم- مفسدون في الأرض بإهلاك الحرث والنسل، فهل نجعل لك أجرًا، ونجمع لك مالا على أن تجعل بيننا وبينهم حاجزًا يحول بيننا وبينهم؟');
INSERT INTO Quran__Arabic_Tafseer_Muyassar (QuranChapterID, VerseNumber, Rukuh, Sajdah, Manzil, HizbQuarter, VerseText) VALUES (18, 95, '0', '0', '0', '0', 'قال ذو القرنين: ما أعطانيه ربي من الملك والتمكين خير لي مِن مالكم، فأعينوني بقوة منكم أجعل بينكم وبينهم سدًا.');
INSERT INTO Quran__Arabic_Tafseer_Muyassar (QuranChapterID, VerseNumber, Rukuh, Sajdah, Manzil, HizbQuarter, VerseText) VALUES (18, 96, '0', '0', '0', '0', 'أعطوني قطع الحديد، حتى إذا جاؤوا به ووضعوه وحاذوا به جانبي الجبلين، قال للعمال: أجِّجوا النار، حتى إذا صار الحديد كله نارًا، قال: أعطوني نحاسًا أُفرغه عليه.');
INSERT INTO Quran__Arabic_Tafseer_Muyassar (QuranChapterID, VerseNumber, Rukuh, Sajdah, Manzil, HizbQuarter, VerseText) VALUES (18, 97, '0', '0', '0', '0', 'فما استطاعت يأجوج ومأجوج أن تصعد فوق السد؛ لارتفاعه وملاسته، وما استطاعوا أن ينقبوه من أسفله لبعد عرضه وقوته.');
INSERT INTO Quran__Arabic_Tafseer_Muyassar (QuranChapterID, VerseNumber, Rukuh, Sajdah, Manzil, HizbQuarter, VerseText) VALUES (18, 98, '0', '0', '0', '0', 'قال ذو القرنين: هذا الذي بنيته حاجزًا عن فساد يأجوج ومأجوج رحمة من ربي بالناس، فإذا جاء وعد ربي بخروج يأجوج ومأجوج جعله دكاء منهدمًا مستويًا بالأرض، وكان وعد ربي حقًّا.');
INSERT INTO Quran__Arabic_Tafseer_Muyassar (QuranChapterID, VerseNumber, Rukuh, Sajdah, Manzil, HizbQuarter, VerseText) VALUES (18, 99, '0', '0', '0', '1', 'وتركنا يأجوج ومأجوج -يوم يأتيهم وَعْدُنا- يموج بعضهم في بعض مختلطين؛ لكثرتهم، ونفخ في "القرن" للبعث، فجمعنا الخلق جميعًا للحساب والجزاء.');
INSERT INTO Quran__Arabic_Tafseer_Muyassar (QuranChapterID, VerseNumber, Rukuh, Sajdah, Manzil, HizbQuarter, VerseText) VALUES (18, 100, '0', '0', '0', '0', 'وعرضنا جهنم للكافرين، وأبرزناها لهم لنريهم سوء عاقبتهم.');
INSERT INTO Quran__Arabic_Tafseer_Muyassar (QuranChapterID, VerseNumber, Rukuh, Sajdah, Manzil, HizbQuarter, VerseText) VALUES (18, 101, '1', '0', '0', '0', 'الذين كانت أعينهم في الدنيا في غطاء عن ذكري فلا تبصر آياتي، وكانوا لا يطيقون سماع حججي الموصلة إلى الإيمان بي وبرسولي.');
INSERT INTO Quran__Arabic_Tafseer_Muyassar (QuranChapterID, VerseNumber, Rukuh, Sajdah, Manzil, HizbQuarter, VerseText) VALUES (18, 102, '0', '0', '0', '0', 'أفظن الذين كفروا بي أن يتخذوا عبادي آلهة من غيري؛ ليكونوا أولياء لهم؟ إنا أعتدنا نار جهنم للكافرين منزلا.');
INSERT INTO Quran__Arabic_Tafseer_Muyassar (QuranChapterID, VerseNumber, Rukuh, Sajdah, Manzil, HizbQuarter, VerseText) VALUES (18, 103, '0', '0', '0', '0', 'قل -أيها الرسول- للناس محذرًا: هل نُخبركم بأخسر الناس أعمالا؟');
INSERT INTO Quran__Arabic_Tafseer_Muyassar (QuranChapterID, VerseNumber, Rukuh, Sajdah, Manzil, HizbQuarter, VerseText) VALUES (18, 104, '0', '0', '0', '0', 'إنهم الذين ضلَّ عملهم في الحياة الدنيا -وهم مشركو قومك وغيرهم ممن ضلَّ سواء السبيل، فلم يكن على هدى ولا صواب- وهم يظنون أنهم محسنون في أعمالهم.');
INSERT INTO Quran__Arabic_Tafseer_Muyassar (QuranChapterID, VerseNumber, Rukuh, Sajdah, Manzil, HizbQuarter, VerseText) VALUES (18, 105, '0', '0', '0', '0', 'أولئك الأخسرون أعمالا هم الذين جحدوا بآيات ربهم وكذَّبوا بها، وأنكروا لقاءه يوم القيامة، فبطلت أعمالهم؛ بسبب كفرهم، فلا نقيم لهم يوم القيامة قدرًا.');
INSERT INTO Quran__Arabic_Tafseer_Muyassar (QuranChapterID, VerseNumber, Rukuh, Sajdah, Manzil, HizbQuarter, VerseText) VALUES (18, 106, '0', '0', '0', '0', 'ذلك المذكور مِن حبوط أعمالهم جزاؤهم نار جهنم؛ بسبب كفرهم بالله واتخاذهم آياته وحجج رسله استهزاءً وسخرية.');
INSERT INTO Quran__Arabic_Tafseer_Muyassar (QuranChapterID, VerseNumber, Rukuh, Sajdah, Manzil, HizbQuarter, VerseText) VALUES (18, 107, '0', '0', '0', '0', 'إن الذين آمنوا بي، وصدَّقوا رسلي، وعملوا الصالحات، لهم أعلى الجنة وأفضلها منزلا.');
INSERT INTO Quran__Arabic_Tafseer_Muyassar (QuranChapterID, VerseNumber, Rukuh, Sajdah, Manzil, HizbQuarter, VerseText) VALUES (18, 108, '0', '0', '0', '0', 'خالدين فيها أبدًا، لا يريدون عنها تحوُّلا؛ لرغبتهم فيها وحبهم لها.');
INSERT INTO Quran__Arabic_Tafseer_Muyassar (QuranChapterID, VerseNumber, Rukuh, Sajdah, Manzil, HizbQuarter, VerseText) VALUES (18, 109, '0', '0', '0', '0', 'قل -أيها الرسول-: لو كان ماء البحر حبرًا للأقلام التي يكتب بها كلام الله، لنفِد ماء البحر قبل أن تنفد كلمات الله، ولو جئنا بمثل البحر بحارًا أخرى مددًا له. وفي الآية إثبات صفة الكلام لله -تعالى- حقيقة كما يليق بجلاله وكماله.');
INSERT INTO Quran__Arabic_Tafseer_Muyassar (QuranChapterID, VerseNumber, Rukuh, Sajdah, Manzil, HizbQuarter, VerseText) VALUES (18, 110, '1', '0', '0', '0', 'قل -أيها الرسول- لهؤلاء المشركين: إنما أنا بشر مثلكم يوحى إليَّ من ربي أنما إلهكم إله واحد، فمَن كان يخاف عذاب ربه ويرجو ثوابه يوم لقائه، فليعمل عملا صالحًا لربه موافقًا لشرعه، ولا يشرك في العبادة معه أحدًا غيره.');
INSERT INTO Quran__Arabic_Tafseer_Muyassar (QuranChapterID, VerseNumber, Rukuh, Sajdah, Manzil, HizbQuarter, VerseText) VALUES (19, 1, '0', '0', '0', '0', '(كهيعص) سبق الكلام على الحروف المقطَّعة في أول سورة البقرة.');
INSERT INTO Quran__Arabic_Tafseer_Muyassar (QuranChapterID, VerseNumber, Rukuh, Sajdah, Manzil, HizbQuarter, VerseText) VALUES (19, 2, '0', '0', '0', '0', 'هذا ذِكْر رحمة ربك عبده زكريا، سنقصه عليك، فإن في ذلك عبرة للمعتبرين.');
INSERT INTO Quran__Arabic_Tafseer_Muyassar (QuranChapterID, VerseNumber, Rukuh, Sajdah, Manzil, HizbQuarter, VerseText) VALUES (19, 3, '0', '0', '0', '0', 'إذ دعا ربه سرًا؛ ليكون أكمل وأتم إخلاصًا لله، وأرجى للإجابة.');
INSERT INTO Quran__Arabic_Tafseer_Muyassar (QuranChapterID, VerseNumber, Rukuh, Sajdah, Manzil, HizbQuarter, VerseText) VALUES (19, 4, '0', '0', '0', '0', 'قال: رب إني كَبِرْتُ، وضعف عظمي، وانتشر الشيب في رأسي، ولم أكن من قبل محرومًا من إجابة الدعاء.');
INSERT INTO Quran__Arabic_Tafseer_Muyassar (QuranChapterID, VerseNumber, Rukuh, Sajdah, Manzil, HizbQuarter, VerseText) VALUES (19, 5, '0', '0', '0', '0', 'وإني خفت أقاربي وعصبتي مِن بعد موتي أن لا يقوموا بدينك حق القيام، ولا يدعوا عبادك إليك، وكانت زوجتي عاقرًا لا تلد، فارزقني مِن عندك ولدًا وارثًا ومعينًا.');
INSERT INTO Quran__Arabic_Tafseer_Muyassar (QuranChapterID, VerseNumber, Rukuh, Sajdah, Manzil, HizbQuarter, VerseText) VALUES (19, 6, '0', '0', '0', '0', 'يرث نبوَّتي ونبوة آل يعقوب، واجعل هذا الولد مرضيًا منك ومن عبادك.');
INSERT INTO Quran__Arabic_Tafseer_Muyassar (QuranChapterID, VerseNumber, Rukuh, Sajdah, Manzil, HizbQuarter, VerseText) VALUES (19, 7, '0', '0', '0', '0', 'يا زكريا إنَّا نبشرك بإجابة دعائك، قد وهبنا لك غلامًا اسمه يحيى، لم نُسَمِّ أحدًا قبله بهذا الاسم.');
INSERT INTO Quran__Arabic_Tafseer_Muyassar (QuranChapterID, VerseNumber, Rukuh, Sajdah, Manzil, HizbQuarter, VerseText) VALUES (19, 8, '0', '0', '0', '0', 'قال زكريا متعجبًا: ربِّ كيف يكون لي غلام، وكانت امرأتي عاقرًا لا تلد، وأنا قد بلغت النهاية في الكبر ورقة العظم؟');
INSERT INTO Quran__Arabic_Tafseer_Muyassar (QuranChapterID, VerseNumber, Rukuh, Sajdah, Manzil, HizbQuarter, VerseText) VALUES (19, 9, '0', '0', '0', '0', 'قال المَلَك مجيبًا زكريا عمَّا تعجَّب منه: هكذا الأمر كما تقول مِن كون امرأتك عاقرًا، وبلوغك من الكبر عتيًا، ولكنَّ ربك قال: خَلْقُ يحيى على هذه الكيفية أمر سهل هيِّن عليَّ، ثم ذكر الله سبحانه لزكريا ما هو أعجب مما سأل عنه فقال: وقد خلقتك أنت من قبل يحيى، ولم تكُ شيئًا مذكورًا ولا موجودًا.');
INSERT INTO Quran__Arabic_Tafseer_Muyassar (QuranChapterID, VerseNumber, Rukuh, Sajdah, Manzil, HizbQuarter, VerseText) VALUES (19, 10, '0', '0', '0', '0', 'قال زكريا زيادة في اطمئنانه: ربِّ اجعل لي علامة على تحقُّق ما بَشَّرَتْني به الملائكة، قال: علامتك أن لا تقدر على كلام الناس مدة ثلاث ليال وأيامها، وأنت صحيح معافى.');
INSERT INTO Quran__Arabic_Tafseer_Muyassar (QuranChapterID, VerseNumber, Rukuh, Sajdah, Manzil, HizbQuarter, VerseText) VALUES (19, 11, '0', '0', '0', '0', 'فخرج زكريا على قومه مِن مصلاه، وهو المكان الذي بُشِّر فيه بالولد، فأشار إليهم: أن سَبِّحوا الله صباحًا ومساءً شكرًا له تعالى.');
INSERT INTO Quran__Arabic_Tafseer_Muyassar (QuranChapterID, VerseNumber, Rukuh, Sajdah, Manzil, HizbQuarter, VerseText) VALUES (19, 12, '0', '0', '0', '0', 'فلما ولد يحيى، وبلغ مبلغًا يفهم فيه الخطاب، أمره اللَّه أن يأخذ التوراة بجدٍّ واجتهاد بقوله: يا يحيى خذ التوراة بجد واجتهاد بحفظ ألفاظها، وفهم معانيها، والعمل بها، وأعطيناه الحكمة وحسن الفهم، وهو صغير السن.');
INSERT INTO Quran__Arabic_Tafseer_Muyassar (QuranChapterID, VerseNumber, Rukuh, Sajdah, Manzil, HizbQuarter, VerseText) VALUES (19, 13, '0', '0', '0', '0', 'وآتيناه رحمة ومحبة من عندنا وطهارة من الذنوب، وكان خائفًا مطيعًا لله تعالى، مؤديًا فرائضه، مجتنبًا محارمه.');
INSERT INTO Quran__Arabic_Tafseer_Muyassar (QuranChapterID, VerseNumber, Rukuh, Sajdah, Manzil, HizbQuarter, VerseText) VALUES (19, 14, '0', '0', '0', '0', 'وكان بارًّا بوالديه مطيعًا لهما، ولم يكن متكبرًا عن طاعة ربه، ولا عن طاعة والديه، ولا عاصيًا لربه، ولا لوالديه.');
INSERT INTO Quran__Arabic_Tafseer_Muyassar (QuranChapterID, VerseNumber, Rukuh, Sajdah, Manzil, HizbQuarter, VerseText) VALUES (19, 15, '1', '0', '0', '0', 'وسلام من الله على يحيى وأمان له يوم وُلِد، ويوم يموت، ويوم يُبعث مِن قبره حيًا.');
INSERT INTO Quran__Arabic_Tafseer_Muyassar (QuranChapterID, VerseNumber, Rukuh, Sajdah, Manzil, HizbQuarter, VerseText) VALUES (19, 16, '0', '0', '0', '0', 'واذكر - أيها الرسول - في هذا القرآن خبر مريم إذ تباعدت عن أهلها، فاتخذت لها مكانًا مما يلي الشرق عنهم.');
INSERT INTO Quran__Arabic_Tafseer_Muyassar (QuranChapterID, VerseNumber, Rukuh, Sajdah, Manzil, HizbQuarter, VerseText) VALUES (19, 17, '0', '0', '0', '0', 'فجعلت مِن دون أهلها سترًا يسترها عنهم وعن الناس، فأرسلنا إليها الملَك جبريل، فتمثَّل لها في صورة إنسان تام الخَلْق.');
INSERT INTO Quran__Arabic_Tafseer_Muyassar (QuranChapterID, VerseNumber, Rukuh, Sajdah, Manzil, HizbQuarter, VerseText) VALUES (19, 18, '0', '0', '0', '0', 'قالت مريم له: إني أستجير بالرحمن منك أن تنالني بسوء إن كنت ممن يتقي الله.');
INSERT INTO Quran__Arabic_Tafseer_Muyassar (QuranChapterID, VerseNumber, Rukuh, Sajdah, Manzil, HizbQuarter, VerseText) VALUES (19, 19, '0', '0', '0', '0', 'قال لها المَلَك: إنما أنا رسول ربك بعثني إليك؛ لأهب لك غلامًا طاهرًا من الذنوب.');
INSERT INTO Quran__Arabic_Tafseer_Muyassar (QuranChapterID, VerseNumber, Rukuh, Sajdah, Manzil, HizbQuarter, VerseText) VALUES (19, 20, '0', '0', '0', '0', 'قالت مريم للمَلَك: كيف يكون لي غلام، ولم يمسسني بشر بنكاحٍ حلال، ولم أكُ زانية؟');
INSERT INTO Quran__Arabic_Tafseer_Muyassar (QuranChapterID, VerseNumber, Rukuh, Sajdah, Manzil, HizbQuarter, VerseText) VALUES (19, 21, '0', '0', '0', '0', 'قال لها المَلَك: هكذا الأمر كما تصفين من أنه لم يمسسك بشر، ولم تكوني بَغِيًّا، ولكن ربك قال: الأمر عليَّ سهل؛ وليكون هذا الغلام علامة للناس تدل على قدرة الله تعالى، ورحمة منَّا به وبوالدته وبالناس، وكان وجود عيسى على هذه الحالة قضاء سابقًا مقدَّرًا، مسطورًا في اللوح المحفوظ، فلا بد مِن نفوذه.');
INSERT INTO Quran__Arabic_Tafseer_Muyassar (QuranChapterID, VerseNumber, Rukuh, Sajdah, Manzil, HizbQuarter, VerseText) VALUES (19, 22, '0', '0', '0', '1', 'فحملت مريم بالغلام بعد أن نفخ جبريل في جَيْب قميصها، فوصلت النفخة إلى رَحِمِها، فوقع الحمل بسبب ذلك، فتباعدت به إلى مكان بعيد عن الناس.');
INSERT INTO Quran__Arabic_Tafseer_Muyassar (QuranChapterID, VerseNumber, Rukuh, Sajdah, Manzil, HizbQuarter, VerseText) VALUES (19, 23, '0', '0', '0', '0', 'فألجأها طَلْقُ الحمل إلى جذع النخلة فقالت: يا ليتني متُّ قبل هذا اليوم، وكنت شيئًا لا يُعْرَف، ولا يُذْكَر، ولا يُدْرَى مَن أنا؟');
INSERT INTO Quran__Arabic_Tafseer_Muyassar (QuranChapterID, VerseNumber, Rukuh, Sajdah, Manzil, HizbQuarter, VerseText) VALUES (19, 24, '0', '0', '0', '0', 'فناداها جبريل أو عيسى: أن لا تَحزني، قد جعل ربك تحتك جَدْول ماء.');
INSERT INTO Quran__Arabic_Tafseer_Muyassar (QuranChapterID, VerseNumber, Rukuh, Sajdah, Manzil, HizbQuarter, VerseText) VALUES (19, 25, '0', '0', '0', '0', 'وحَرِّكي جذع النخلة تُسَاقِطْ عليك رطبًا غَضًّا جُنِيَ مِن ساعته.');
INSERT INTO Quran__Arabic_Tafseer_Muyassar (QuranChapterID, VerseNumber, Rukuh, Sajdah, Manzil, HizbQuarter, VerseText) VALUES (19, 26, '0', '0', '0', '0', 'فكلي من الرطب، واشربي من الماء وطيـبي نفسًا بالمولود، فإن رأيت من الناس أحدًا فسألك عن أمرك فقولي له: إني أَوْجَبْتُ على نفسي لله سكوتًا، فلن أكلم اليوم أحدًا من الناس. والسكوت كان تعبدًا في شرعهم، دون شريعة محمد صلى الله عليه وسلم.');
INSERT INTO Quran__Arabic_Tafseer_Muyassar (QuranChapterID, VerseNumber, Rukuh, Sajdah, Manzil, HizbQuarter, VerseText) VALUES (19, 27, '0', '0', '0', '0', 'فأتت مريم قومها تحمل مولودها من المكان البعيد، فلما رأوها كذلك قالوا لها: يا مريم لقد جئت أمرًا عظيمًا مفترى.');
INSERT INTO Quran__Arabic_Tafseer_Muyassar (QuranChapterID, VerseNumber, Rukuh, Sajdah, Manzil, HizbQuarter, VerseText) VALUES (19, 28, '0', '0', '0', '0', 'يا أخت الرجل الصالح هارون ما كان أبوك رجل سوء يأتي الفواحش، وما كانت أمك امرأة سوء تأتي البِغاء.');
INSERT INTO Quran__Arabic_Tafseer_Muyassar (QuranChapterID, VerseNumber, Rukuh, Sajdah, Manzil, HizbQuarter, VerseText) VALUES (19, 29, '0', '0', '0', '0', 'فأشارت مريم إلى مولودها عيسى ليسألوه ويكلموه، فقالوا منكرين عليها: كيف نكلم مَن لا يزال في مهده طفلا رضيعًا؟');
INSERT INTO Quran__Arabic_Tafseer_Muyassar (QuranChapterID, VerseNumber, Rukuh, Sajdah, Manzil, HizbQuarter, VerseText) VALUES (19, 30, '0', '0', '0', '0', 'قال عيسى وهو في مهده يرضع: إني عبد الله، قضى بإعطائي الكتاب، وهو الإنجيل، وجعلني نبيًا.');
INSERT INTO Quran__Arabic_Tafseer_Muyassar (QuranChapterID, VerseNumber, Rukuh, Sajdah, Manzil, HizbQuarter, VerseText) VALUES (19, 31, '0', '0', '0', '0', 'وجعلني عظيم الخير والنفع حيثما وُجِدْتُ، وأوصاني بالمحافظة على الصلاة وإيتاء الزكاة ما بقيت حيًا.');
INSERT INTO Quran__Arabic_Tafseer_Muyassar (QuranChapterID, VerseNumber, Rukuh, Sajdah, Manzil, HizbQuarter, VerseText) VALUES (19, 32, '0', '0', '0', '0', 'وجعلني بارًّا بوالدتي، ولم يجعلني متكبرًا ولا شقيًا، عاصيًا لربي.');
INSERT INTO Quran__Arabic_Tafseer_Muyassar (QuranChapterID, VerseNumber, Rukuh, Sajdah, Manzil, HizbQuarter, VerseText) VALUES (19, 33, '0', '0', '0', '0', 'والسلامة والأمان عليَّ من الله يوم وُلِدْتُ، ويوم أموت، ويوم أُبعث حيًا يوم القيامة.');
INSERT INTO Quran__Arabic_Tafseer_Muyassar (QuranChapterID, VerseNumber, Rukuh, Sajdah, Manzil, HizbQuarter, VerseText) VALUES (19, 34, '0', '0', '0', '0', 'ذلك الذي قصصنا عليك - أيها الرسول - صفتَه وخبرَه هو عيسى ابن مريم، مِن غير شك ولا مرية، بل هو قولُ الحق الذي شك فيه اليهود والنصارى.');
INSERT INTO Quran__Arabic_Tafseer_Muyassar (QuranChapterID, VerseNumber, Rukuh, Sajdah, Manzil, HizbQuarter, VerseText) VALUES (19, 35, '0', '0', '0', '0', 'ما كان لله تعالى ولا يليق به أن يتخذ مِن عباده وخَلْقه ولدًا، تنزَّه وتقدَّس عن ذلك، إذا قضى أمرًا من الأمور وأراده، صغيرًا أو كبيرًا، لم يمتنع عليه، وإنما يقول له: "كن"، فيكون كما شاءه وأراده.');
INSERT INTO Quran__Arabic_Tafseer_Muyassar (QuranChapterID, VerseNumber, Rukuh, Sajdah, Manzil, HizbQuarter, VerseText) VALUES (19, 36, '0', '0', '0', '0', 'وقال عيسى لقومه: وإن الله الذي أدعوكم إليه هو وحده ربي وربكم فاعبدوه وحده لا شريك له، فأنا وأنتم سواء في العبودية والخضوع له، هذا هو الطريق الذي لا اعوجاج فيه.');
INSERT INTO Quran__Arabic_Tafseer_Muyassar (QuranChapterID, VerseNumber, Rukuh, Sajdah, Manzil, HizbQuarter, VerseText) VALUES (19, 37, '0', '0', '0', '0', 'فاختلفت الفِرَق من أهل الكتاب فيما بينهم في أمر عيسى عليه السلام، فمنهم غال فيه وهم النصارى، فمنهم من قال: هو الله، ومنهم من قال: هو ابن الله، ومنهم من قال: ثالث ثلاثة - تعالى الله عما يقولون -، ومنهم جافٍ عنه وهم اليهود، قالوا: ساحر، وقالوا: ابن يوسف النجار، فهلاك للذين كفروا مِن شهود يوم عظيم الهول، وهو يوم القيامة.');
INSERT INTO Quran__Arabic_Tafseer_Muyassar (QuranChapterID, VerseNumber, Rukuh, Sajdah, Manzil, HizbQuarter, VerseText) VALUES (19, 38, '0', '0', '0', '0', 'ما أشدَّ سمعَهم وبصرهم يوم القيامة، يوم يَقْدُمون على الله، حين لا ينفعهم ذلك!! لكنِ الظالمون اليوم في هذه الدنيا في ذهابٍ بيِّنٍ عن الحق.');
INSERT INTO Quran__Arabic_Tafseer_Muyassar (QuranChapterID, VerseNumber, Rukuh, Sajdah, Manzil, HizbQuarter, VerseText) VALUES (19, 39, '0', '0', '0', '0', 'وأنذر - أيها الرسول - الناس يوم الندامة حين يُقضى الأمر، ويُجَاءُ بالموت كأنَّه كبش أملح، فيُذْبَح، ويُفصل بين الخلق، فيصير أهل الإيمان إلى الجنة، وأهل الكفر إلى النار، وهم اليوم في هذه الدنيا في غفلة عمَّا أُنذروا به، فهم لا يصدقون، ولا يعملون العمل الصالح.');
INSERT INTO Quran__Arabic_Tafseer_Muyassar (QuranChapterID, VerseNumber, Rukuh, Sajdah, Manzil, HizbQuarter, VerseText) VALUES (19, 40, '1', '0', '0', '0', 'إنا نحن الوارثون للأرض ومَن عليها بفنائهم وبقائنا بعدهم وحُكْمنا فيهم، وإلينا مصيرهم وحسابهم، فنجازيهم على أعمالهم.');
INSERT INTO Quran__Arabic_Tafseer_Muyassar (QuranChapterID, VerseNumber, Rukuh, Sajdah, Manzil, HizbQuarter, VerseText) VALUES (19, 41, '0', '0', '0', '0', 'واذكر - أيها الرسول - لقومك في هذا القرآن قصة إبراهيم - عليه السلام - إنه كان عظيم الصدق، ومِن أرفع أنبياء الله تعالى منزلة.');
INSERT INTO Quran__Arabic_Tafseer_Muyassar (QuranChapterID, VerseNumber, Rukuh, Sajdah, Manzil, HizbQuarter, VerseText) VALUES (19, 42, '0', '0', '0', '0', 'إذ قال لأبيه آزر: يا أبت لأي شيء تعبد من الأصنام ما لا يسمع ولا يبصر، ولا يدفع عنك شيئًا من دون الله؟');
INSERT INTO Quran__Arabic_Tafseer_Muyassar (QuranChapterID, VerseNumber, Rukuh, Sajdah, Manzil, HizbQuarter, VerseText) VALUES (19, 43, '0', '0', '0', '0', 'يا أبت، إن الله أعطاني من العلم ما لم يعطك، فاقبل مني، واتبعني إلى ما أدعوك إليه، أرشدك إلى الطريق السوي الذي لا تضلُّ فيه.');
INSERT INTO Quran__Arabic_Tafseer_Muyassar (QuranChapterID, VerseNumber, Rukuh, Sajdah, Manzil, HizbQuarter, VerseText) VALUES (19, 44, '0', '0', '0', '0', 'يا أبت، لا تطع الشيطان فتعبد هذه الأصنام؛ إن الشيطان كان للرحمن مخالفًا مستكبرًا عن طاعة الله.');
INSERT INTO Quran__Arabic_Tafseer_Muyassar (QuranChapterID, VerseNumber, Rukuh, Sajdah, Manzil, HizbQuarter, VerseText) VALUES (19, 45, '0', '0', '0', '0', 'يا أبت، إني أخاف أن تموت على كفرك، فيمَسَّك عذاب من الرحمن، فتكون للشيطان قرينًا في النار.');
INSERT INTO Quran__Arabic_Tafseer_Muyassar (QuranChapterID, VerseNumber, Rukuh, Sajdah, Manzil, HizbQuarter, VerseText) VALUES (19, 46, '0', '0', '0', '0', 'قال أبو إبراهيم لابنه: أمعرض أنت عن عبادة آلهتي يا إبراهيم؟ لئن لم تنته عن سَبِّها لأقتلنَّك رميًا بالحجارة، واذهب عني فلا تلقني، ولا تكلمني زمانًا طويلا من الدهر.');
INSERT INTO Quran__Arabic_Tafseer_Muyassar (QuranChapterID, VerseNumber, Rukuh, Sajdah, Manzil, HizbQuarter, VerseText) VALUES (19, 47, '0', '0', '0', '0', 'قال إبراهيم لأبيه: سلام عليك مني فلا ينالك مني ما تكره، وسوف أدعو الله لك بالهداية والمغفرة. إن ربي كان رحيمًا رؤوفًا بحالي يجيبني إذا دعوته.');
INSERT INTO Quran__Arabic_Tafseer_Muyassar (QuranChapterID, VerseNumber, Rukuh, Sajdah, Manzil, HizbQuarter, VerseText) VALUES (19, 48, '0', '0', '0', '0', 'وأفارقكم وآلهتكم التي تعبدونها من دون الله، وأدعو ربي مخلصًا، عسى أن لا أشقى بدعاء ربي، فلا يعطيني ما أسأله.');
INSERT INTO Quran__Arabic_Tafseer_Muyassar (QuranChapterID, VerseNumber, Rukuh, Sajdah, Manzil, HizbQuarter, VerseText) VALUES (19, 49, '0', '0', '0', '0', 'فلما فارقهم وآلهتهم التي يعبدونها من دون الله رزقناه من الولد: إسحاق، ويعقوب بن إسحاق، وجعلناهما نبيَّين.');
INSERT INTO Quran__Arabic_Tafseer_Muyassar (QuranChapterID, VerseNumber, Rukuh, Sajdah, Manzil, HizbQuarter, VerseText) VALUES (19, 50, '1', '0', '0', '0', 'ووهبنا لهم جميعا من رحمتنا فضلا لا يحصى، وجعلنا لهم ذكرًا حسنًا، وثناءً جميلا باقيًا في الناس.');
INSERT INTO Quran__Arabic_Tafseer_Muyassar (QuranChapterID, VerseNumber, Rukuh, Sajdah, Manzil, HizbQuarter, VerseText) VALUES (19, 51, '0', '0', '0', '0', 'واذكر - أيها الرسول - في القرآن قصة موسى - عليه السلام - إنه كان مصطفى مختارًا، وكان رسولا نبيًا مِن أولي العزم من الرسل.');
INSERT INTO Quran__Arabic_Tafseer_Muyassar (QuranChapterID, VerseNumber, Rukuh, Sajdah, Manzil, HizbQuarter, VerseText) VALUES (19, 52, '0', '0', '0', '0', 'ونادينا موسى من ناحية جبل طور "سيناء" اليمنى من موسى، وقرَّبناه فشرَّفناه بمناجاتنا له. وفي هذا إثبات صفة الكلام لله - تعالى - كما يليق بجلاله وكماله.');
INSERT INTO Quran__Arabic_Tafseer_Muyassar (QuranChapterID, VerseNumber, Rukuh, Sajdah, Manzil, HizbQuarter, VerseText) VALUES (19, 53, '0', '0', '0', '0', 'ووهبنا لموسى من رحمتنا أخاه هارون نبيًا يؤيده ويؤازره.');
INSERT INTO Quran__Arabic_Tafseer_Muyassar (QuranChapterID, VerseNumber, Rukuh, Sajdah, Manzil, HizbQuarter, VerseText) VALUES (19, 54, '0', '0', '0', '0', 'واذكر - أيها الرسول - في هذا القرآن خبر إسماعيل عليه السلام، إنه كان صادقًا في وعده فلم يَعِد شيئًا إلا وفَّى به، وكان رسولا نبيًا.');
INSERT INTO Quran__Arabic_Tafseer_Muyassar (QuranChapterID, VerseNumber, Rukuh, Sajdah, Manzil, HizbQuarter, VerseText) VALUES (19, 55, '0', '0', '0', '0', 'وكان يأمر أهله بإقام الصلاة وإيتاء الزكاة، وكان عند ربه عز وجل مرضيًا عنه.');
INSERT INTO Quran__Arabic_Tafseer_Muyassar (QuranChapterID, VerseNumber, Rukuh, Sajdah, Manzil, HizbQuarter, VerseText) VALUES (19, 56, '0', '0', '0', '0', 'واذكر - أيها الرسول - في هذا القرآن خبر إدريس عليه السلام، إنه كان عظيم الصدق في قوله وعمله، نبيًا يوحى إليه.');
INSERT INTO Quran__Arabic_Tafseer_Muyassar (QuranChapterID, VerseNumber, Rukuh, Sajdah, Manzil, HizbQuarter, VerseText) VALUES (19, 57, '0', '0', '0', '0', 'ورفَعْنا ذِكْره في العالمين، ومنزلته بين المقربين، فكان عالي الذكر، عالي المنزلة.');
INSERT INTO Quran__Arabic_Tafseer_Muyassar (QuranChapterID, VerseNumber, Rukuh, Sajdah, Manzil, HizbQuarter, VerseText) VALUES (19, 58, '0', '1', '0', '0', 'هؤلاء الذين قصصتُ عليك خبرهم أيها الرسول، هم الذين أنعم الله عليهم بفضله وتوفيقه، فجعلهم أنبياء من ذرية آدم، ومِن ذرية مَن حملنا مع نوح في السفينة، ومن ذرية إبراهيم، ومن ذرية يعقوب، وممَّن هدينا للإيمان واصطفينا للرسالة والنبُوَّة، إذا تتلى عليهم آيات الرحمن المتضمنة لتوحيده وحججه خرُّوا ساجدين لله خضوعًا، واستكانة، وبكَوْا من خشيته سبحانه وتعالى.');
INSERT INTO Quran__Arabic_Tafseer_Muyassar (QuranChapterID, VerseNumber, Rukuh, Sajdah, Manzil, HizbQuarter, VerseText) VALUES (19, 59, '0', '0', '0', '1', 'فأتى مِن بعد هؤلاء المنعَم عليهم أتباع سَوْء تركوا الصلاة كلها، أو فوتوا وقتها، أو تركوا أركانها وواجباتها، واتبعوا ما يوافق شهواتهم ويلائمها، فسوف يلقون شرًا وضلالا وخيبة في جهنم.');
INSERT INTO Quran__Arabic_Tafseer_Muyassar (QuranChapterID, VerseNumber, Rukuh, Sajdah, Manzil, HizbQuarter, VerseText) VALUES (19, 60, '0', '0', '0', '0', 'لكن مَن تاب منهم مِن ذنبه وآمن بربه وعمل صالحًا تصديقًا لتوبته، فأولئك يقبل الله توبتهم، ويدخلون الجنة مع المؤمنين ولا يُنقَصون شيئًا من أعمالهم الصالحة.');
INSERT INTO Quran__Arabic_Tafseer_Muyassar (QuranChapterID, VerseNumber, Rukuh, Sajdah, Manzil, HizbQuarter, VerseText) VALUES (19, 61, '0', '0', '0', '0', 'جنات خلد وإقامة دائمة، وهي التي وعد الرحمن بها عباده بالغيب فآمَنوا بها ولم يروها، إن وعد الله لعباده بهذه الجنة آتٍ لا محالة.');
INSERT INTO Quran__Arabic_Tafseer_Muyassar (QuranChapterID, VerseNumber, Rukuh, Sajdah, Manzil, HizbQuarter, VerseText) VALUES (19, 62, '0', '0', '0', '0', 'لا يسمع أهل الجنة فيها كلامًا باطلا لكن يسمعون سلاما تحية لهم، ولهم رزقهم فيها من الطعام والشراب دائمًا، كلما شاؤوا بكرة وعشيًا.');
INSERT INTO Quran__Arabic_Tafseer_Muyassar (QuranChapterID, VerseNumber, Rukuh, Sajdah, Manzil, HizbQuarter, VerseText) VALUES (19, 63, '0', '0', '0', '0', 'تلك الجنة الموصوفة بتلك الصفات، هي التي نورثها ونعطيها عبادنا المتقين لنا، بامتثال أوامرنا واجتناب نواهينا.');
INSERT INTO Quran__Arabic_Tafseer_Muyassar (QuranChapterID, VerseNumber, Rukuh, Sajdah, Manzil, HizbQuarter, VerseText) VALUES (19, 64, '0', '0', '0', '0', 'وقل - يا جبريل - لمحمد: وما نتنزل - نحن الملائكة - من السماء إلى الأرض إلا بأمر ربك لنا، له ما بين أيدينا مما يستقبل من أمر الآخرة، وما خلفنا مما مضى من الدنيا، وما بين الدنيا والآخرة، فله الأمر كله في الزمان والمكان، وما كان ربك ناسيًا لشيء من الأشياء.');
INSERT INTO Quran__Arabic_Tafseer_Muyassar (QuranChapterID, VerseNumber, Rukuh, Sajdah, Manzil, HizbQuarter, VerseText) VALUES (19, 65, '1', '0', '0', '0', 'فهو الله رب السموات والأرض وما بينهما، ومالك ذلك كله وخالقه ومدبره، فاعبده وحده - أيها النبي - واصبر على طاعته أنت ومَن تبعك، ليس كمثله شيء في ذاته وأسمائه وصفاته وأفعاله.');
INSERT INTO Quran__Arabic_Tafseer_Muyassar (QuranChapterID, VerseNumber, Rukuh, Sajdah, Manzil, HizbQuarter, VerseText) VALUES (19, 66, '0', '0', '0', '0', 'ويقول الإنسان الكافر منكرًا للبعث بعد الموت: أإذا ما مِتُّ وفَنِيتُ لسوف أُخرَج من قبري حيًا؟!');
INSERT INTO Quran__Arabic_Tafseer_Muyassar (QuranChapterID, VerseNumber, Rukuh, Sajdah, Manzil, HizbQuarter, VerseText) VALUES (19, 67, '0', '0', '0', '0', 'كيف نسي هذا الإنسان الكافر نفسه؟ أولا يَذْكُر أنا خلقناه أول مرة، ولم يكُ شيئًا موجودًا؟');
INSERT INTO Quran__Arabic_Tafseer_Muyassar (QuranChapterID, VerseNumber, Rukuh, Sajdah, Manzil, HizbQuarter, VerseText) VALUES (19, 68, '0', '0', '0', '0', 'فوربك - أيها الرسول - لنجمعن هؤلاء المنكرين للبعث يوم القيامة مع الشياطين، ثم لنأتين بهم أجمعين حول جهنم باركين على رُكَبهم؛ لشدة ما هم فيه من الهول، لا يقدرون على القيام.');
INSERT INTO Quran__Arabic_Tafseer_Muyassar (QuranChapterID, VerseNumber, Rukuh, Sajdah, Manzil, HizbQuarter, VerseText) VALUES (19, 69, '0', '0', '0', '0', 'ثم لنأخذنَّ مِن كل طائفة أشدَّهم تمردًا وعصيانًا لله، فنبدأ بعذابهم.');
INSERT INTO Quran__Arabic_Tafseer_Muyassar (QuranChapterID, VerseNumber, Rukuh, Sajdah, Manzil, HizbQuarter, VerseText) VALUES (19, 70, '0', '0', '0', '0', 'ثم لنحن أعلم بالذين هم أَوْلى بدخول النار ومقاساة حرها.');
INSERT INTO Quran__Arabic_Tafseer_Muyassar (QuranChapterID, VerseNumber, Rukuh, Sajdah, Manzil, HizbQuarter, VerseText) VALUES (19, 71, '0', '0', '0', '0', 'وما منكم - أيها الناس - أحد إلا وارد النار بالمرور على الصراط المنصوب على متن جهنم، كل بحسب عمله، كان ذلك أمرًا محتومًا، قضى الله - سبحانه - وحكم أنه لا بد من وقوعه لا محالة.');
INSERT INTO Quran__Arabic_Tafseer_Muyassar (QuranChapterID, VerseNumber, Rukuh, Sajdah, Manzil, HizbQuarter, VerseText) VALUES (19, 72, '0', '0', '0', '0', 'ثم ننجي الذين اتقوا ربهم بطاعته والبعد عن معصيته، ونترك الظالمين لأنفسهم بالكفر بالله في النار باركين على رُكَبهم.');
INSERT INTO Quran__Arabic_Tafseer_Muyassar (QuranChapterID, VerseNumber, Rukuh, Sajdah, Manzil, HizbQuarter, VerseText) VALUES (19, 73, '0', '0', '0', '0', 'وإذا تتلى على الناس آياتنا المنزلات الواضحات قال الكفار بالله للمؤمنين به: أيُّ الفريقين منَّا ومنكم أفضل منزلا وأحسن مجلسًا؟');
INSERT INTO Quran__Arabic_Tafseer_Muyassar (QuranChapterID, VerseNumber, Rukuh, Sajdah, Manzil, HizbQuarter, VerseText) VALUES (19, 74, '0', '0', '0', '0', 'وكثيرًا أهلكنا قبل كفار قومك - أيها الرسول - من الأمم كانوا أحسن متاعًا منهم وأجمل منظرًا.');
INSERT INTO Quran__Arabic_Tafseer_Muyassar (QuranChapterID, VerseNumber, Rukuh, Sajdah, Manzil, HizbQuarter, VerseText) VALUES (19, 75, '0', '0', '0', '0', 'قل - أيها الرسول - لهم: من كان ضالا عن الحق غير متبع طريق الهدى، فالله يمهله ويملي له في ضلاله، حتى إذا رأى - يقينا - ما توعَّده الله به: إما العذاب العاجل في الدنيا، وإما قيام الساعة، فسيعلم - حينئذ - مَن هو شر مكانًا ومستقرًا، وأضعف قوة وجندًا.');
INSERT INTO Quran__Arabic_Tafseer_Muyassar (QuranChapterID, VerseNumber, Rukuh, Sajdah, Manzil, HizbQuarter, VerseText) VALUES (19, 76, '0', '0', '0', '0', 'ويزيد الله عباده الذين اهتدوا لدينه هدى على هداهم بما يتجدد لهم من الإيمان بفرائض الله، والعمل بها. والأعمالُ الباقيات الصالحات خير ثوابًا عند الله في الآخرة، وخير مرجعًا وعاقبة.');
INSERT INTO Quran__Arabic_Tafseer_Muyassar (QuranChapterID, VerseNumber, Rukuh, Sajdah, Manzil, HizbQuarter, VerseText) VALUES (19, 77, '0', '0', '0', '0', 'أعَلِمْت - أيها الرسول - وعجبت من هذا الكافر "العاص بن وائل" وأمثاله؟ إذ كفر بآيات الله وكذَّب بها وقال: لأعطينَّ في الآخرة أموالا وأولادًا.');
INSERT INTO Quran__Arabic_Tafseer_Muyassar (QuranChapterID, VerseNumber, Rukuh, Sajdah, Manzil, HizbQuarter, VerseText) VALUES (19, 78, '0', '0', '0', '0', 'أطَّلَع الغيب، فرأى أن له مالا وولدًا، أم له عند الله عهد بذلك؟');
INSERT INTO Quran__Arabic_Tafseer_Muyassar (QuranChapterID, VerseNumber, Rukuh, Sajdah, Manzil, HizbQuarter, VerseText) VALUES (19, 79, '0', '0', '0', '0', 'ليس الأمر كما يزعم ذلك الكافر، فلا علم له ولا عهد عنده، سنكتب ما يقول مِن كذب وافتراء على الله، ونزيده في الآخرة من أنواع العقوبات، كما ازداد من الغيِّ والضلال.');
INSERT INTO Quran__Arabic_Tafseer_Muyassar (QuranChapterID, VerseNumber, Rukuh, Sajdah, Manzil, HizbQuarter, VerseText) VALUES (19, 80, '0', '0', '0', '0', 'ونرثه مالَه وولده، ويأتينا يوم القيامة فردًا وحده، لا مال معه ولا ولد.');
INSERT INTO Quran__Arabic_Tafseer_Muyassar (QuranChapterID, VerseNumber, Rukuh, Sajdah, Manzil, HizbQuarter, VerseText) VALUES (19, 81, '0', '0', '0', '0', 'واتخذ المشركون آلهة يعبدونها من دون الله؛ لتنصرهم، ويعتزوا بها.');
INSERT INTO Quran__Arabic_Tafseer_Muyassar (QuranChapterID, VerseNumber, Rukuh, Sajdah, Manzil, HizbQuarter, VerseText) VALUES (19, 82, '1', '0', '0', '0', 'ليس الأمر كما يزعمون، لن تكون لهم الآلهة عزًا، بل ستكفر هذه الآلهة في الآخرة بعبادتهم لها، وتكون عليهم أعوانًا في خصومتهم وتكذيبهم بخلاف ما ظنوه فيها.');
INSERT INTO Quran__Arabic_Tafseer_Muyassar (QuranChapterID, VerseNumber, Rukuh, Sajdah, Manzil, HizbQuarter, VerseText) VALUES (19, 83, '0', '0', '0', '0', 'ألم تر - أيها الرسول - أنَّا سلَّطْنا الشياطين على الكافرين بالله ورسله؛ لتغويهم، وتدفعهم عن الطاعة إلى المعصية؟');
INSERT INTO Quran__Arabic_Tafseer_Muyassar (QuranChapterID, VerseNumber, Rukuh, Sajdah, Manzil, HizbQuarter, VerseText) VALUES (19, 84, '0', '0', '0', '0', 'فلا تستعجل - أيها الرسول - بطلب العذاب على هؤلاء الكافرين، إنما نحصي أعمارهم وأعمالهم إحصاءً لا تفريط فيه ولا تأخير.');
INSERT INTO Quran__Arabic_Tafseer_Muyassar (QuranChapterID, VerseNumber, Rukuh, Sajdah, Manzil, HizbQuarter, VerseText) VALUES (19, 85, '0', '0', '0', '0', 'يوم نجمع المتقين إلى ربهم الرحيم بهم وفودًا مكرمين. ونسوق الكافرين بالله سوقًا شديدًا إلى النار مشاة عِطاشًا.');
INSERT INTO Quran__Arabic_Tafseer_Muyassar (QuranChapterID, VerseNumber, Rukuh, Sajdah, Manzil, HizbQuarter, VerseText) VALUES (19, 86, '0', '0', '0', '0', 'يوم نجمع المتقين إلى ربهم الرحيم بهم وفودًا مكرمين. ونسوق الكافرين بالله سوقًا شديدًا إلى النار مشاة عِطاشًا.');
INSERT INTO Quran__Arabic_Tafseer_Muyassar (QuranChapterID, VerseNumber, Rukuh, Sajdah, Manzil, HizbQuarter, VerseText) VALUES (19, 87, '0', '0', '0', '0', 'لا يملك هؤلاء الكفار الشفاعة لأحد، إنما يملكها مَنِ اتخذ عند الرحمن عهدًا بذلك، وهم المؤمنون بالله ورسله.');
INSERT INTO Quran__Arabic_Tafseer_Muyassar (QuranChapterID, VerseNumber, Rukuh, Sajdah, Manzil, HizbQuarter, VerseText) VALUES (19, 88, '0', '0', '0', '0', 'وقال هؤلاء الكفار: اتخذ الرحمن ولدًا.');
INSERT INTO Quran__Arabic_Tafseer_Muyassar (QuranChapterID, VerseNumber, Rukuh, Sajdah, Manzil, HizbQuarter, VerseText) VALUES (19, 89, '0', '0', '0', '0', 'لقد جئتم - أيها القائلون - بهذه المقالة شيئا عظيمًا منكرًا.');
INSERT INTO Quran__Arabic_Tafseer_Muyassar (QuranChapterID, VerseNumber, Rukuh, Sajdah, Manzil, HizbQuarter, VerseText) VALUES (19, 90, '0', '0', '0', '0', 'تكاد السموات يتشقَّقْنَ مِن فظاعة ذلكم القول، وتتصدع الأرض، وتسقط الجبال سقوطًا شديدًا غضبًا لله لِنِسْبَتِهم له الولد. تعالى الله عن ذلك علوًا كبيرًا.');
INSERT INTO Quran__Arabic_Tafseer_Muyassar (QuranChapterID, VerseNumber, Rukuh, Sajdah, Manzil, HizbQuarter, VerseText) VALUES (19, 91, '0', '0', '0', '0', 'تكاد السموات يتشقَّقْنَ مِن فظاعة ذلكم القول، وتتصدع الأرض، وتسقط الجبال سقوطًا شديدًا غضبًا لله لِنِسْبَتِهم له الولد. تعالى الله عن ذلك علوًا كبيرًا.');
INSERT INTO Quran__Arabic_Tafseer_Muyassar (QuranChapterID, VerseNumber, Rukuh, Sajdah, Manzil, HizbQuarter, VerseText) VALUES (19, 92, '0', '0', '0', '0', 'وما يصلح للرحمن، ولا يليق بعظمته، أن يتخذ ولدًا؛ لأن اتخاذ الولد يدل على النقص والحاجة، والله هو الغني الحميد المبرأ عن كل النقائص.');
INSERT INTO Quran__Arabic_Tafseer_Muyassar (QuranChapterID, VerseNumber, Rukuh, Sajdah, Manzil, HizbQuarter, VerseText) VALUES (19, 93, '0', '0', '0', '0', 'ما كل مَن في السموات من الملائكة، ومَن في الأرض من الإنس والجن، إلا سيأتي ربه يوم القيامة عبدًا ذليلا خاضعًا مقرًا له بالعبودية.');
INSERT INTO Quran__Arabic_Tafseer_Muyassar (QuranChapterID, VerseNumber, Rukuh, Sajdah, Manzil, HizbQuarter, VerseText) VALUES (19, 94, '0', '0', '0', '0', 'لقد أحصى الله سبحانه وتعالى خَلْقَه كلهم، وعلم عددهم، فلا يخفى عليه أحد منهم.');
INSERT INTO Quran__Arabic_Tafseer_Muyassar (QuranChapterID, VerseNumber, Rukuh, Sajdah, Manzil, HizbQuarter, VerseText) VALUES (19, 95, '0', '0', '0', '0', 'وسوف يأتي كل فرد من الخلق ربه يوم القيامة وحده، لا مال له ولا ولد معه.');
INSERT INTO Quran__Arabic_Tafseer_Muyassar (QuranChapterID, VerseNumber, Rukuh, Sajdah, Manzil, HizbQuarter, VerseText) VALUES (19, 96, '0', '0', '0', '0', 'إن الذين آمنوا بالله واتَّبَعوا رسله وعملوا الصالحات وَفْق شرعه، سيجعل لهم الرحمن محبة ومودة في قلوب عباده.');
INSERT INTO Quran__Arabic_Tafseer_Muyassar (QuranChapterID, VerseNumber, Rukuh, Sajdah, Manzil, HizbQuarter, VerseText) VALUES (19, 97, '0', '0', '0', '0', 'فإنما يسَّرنا هذا القرآن بلسانك العربي أيها الرسول؛ لتبشر به المتقين من أتباعك، وتخوِّف به المكذبين شديدي الخصومة بالباطل.');
INSERT INTO Quran__Arabic_Tafseer_Muyassar (QuranChapterID, VerseNumber, Rukuh, Sajdah, Manzil, HizbQuarter, VerseText) VALUES (19, 98, '1', '0', '0', '0', 'وكثيرًا أهلكنا - أيها الرسول - من الأمم السابقة قبل قومك، ما ترى منهم أحدًا وما تسمع لهم صوتًا، فكذلك الكفار من قومك، نهلكهم كما أهلكنا السابقين من قبلهم. وفي هذا تهديد ووعيد بإهلاك المكذبين المعاندين.');
INSERT INTO Quran__Arabic_Tafseer_Muyassar (QuranChapterID, VerseNumber, Rukuh, Sajdah, Manzil, HizbQuarter, VerseText) VALUES (20, 1, '0', '0', '0', '1', '(طه) سبق الكلام على الحروف المقطَّعة في أول سورة البقرة.');
INSERT INTO Quran__Arabic_Tafseer_Muyassar (QuranChapterID, VerseNumber, Rukuh, Sajdah, Manzil, HizbQuarter, VerseText) VALUES (20, 2, '0', '0', '0', '0', 'ما أنزلنا عليك - أيها الرسول - القرآن؛ لتشقى بما لا طاقة لك به من العمل.');
INSERT INTO Quran__Arabic_Tafseer_Muyassar (QuranChapterID, VerseNumber, Rukuh, Sajdah, Manzil, HizbQuarter, VerseText) VALUES (20, 3, '0', '0', '0', '0', 'لكن أنزلناه موعظة؛ ليتذكر به مَن يخاف عقاب الله، فيتقيه بأداء الفرائض واجتناب المحارم.');
INSERT INTO Quran__Arabic_Tafseer_Muyassar (QuranChapterID, VerseNumber, Rukuh, Sajdah, Manzil, HizbQuarter, VerseText) VALUES (20, 4, '0', '0', '0', '0', 'هذا القرآن تنزيل من الله الذي خلق الأرض والسموات العلى.');
INSERT INTO Quran__Arabic_Tafseer_Muyassar (QuranChapterID, VerseNumber, Rukuh, Sajdah, Manzil, HizbQuarter, VerseText) VALUES (20, 5, '0', '0', '0', '0', 'الرحمن على العرش استوى أي ارتفع وعلا استواء يليق بجلاله وعظمته.');
INSERT INTO Quran__Arabic_Tafseer_Muyassar (QuranChapterID, VerseNumber, Rukuh, Sajdah, Manzil, HizbQuarter, VerseText) VALUES (20, 6, '0', '0', '0', '0', 'له ما في السموات وما في الأرض وما بينهما وما تحت الأرض، خَلْقًا ومُلْكًا وتدبيرًا.');
INSERT INTO Quran__Arabic_Tafseer_Muyassar (QuranChapterID, VerseNumber, Rukuh, Sajdah, Manzil, HizbQuarter, VerseText) VALUES (20, 7, '0', '0', '0', '0', 'وإن تجهر - أيها الرسول - بالقول، فتعلنه أو تخفه، فإن الله لا يخفى عليه شيء، يعلم السر وما هو أخفى من السر مما تحدِّث به نفسك.');
INSERT INTO Quran__Arabic_Tafseer_Muyassar (QuranChapterID, VerseNumber, Rukuh, Sajdah, Manzil, HizbQuarter, VerseText) VALUES (20, 8, '0', '0', '0', '0', 'الله الذي لا معبود بحق إلا هو، له وحده الأسماء الكاملة في الحسن.');
INSERT INTO Quran__Arabic_Tafseer_Muyassar (QuranChapterID, VerseNumber, Rukuh, Sajdah, Manzil, HizbQuarter, VerseText) VALUES (20, 9, '0', '0', '0', '0', 'وهل أتاك - أيها الرسول - خبر موسى بن عمران عليه السلام؟');
INSERT INTO Quran__Arabic_Tafseer_Muyassar (QuranChapterID, VerseNumber, Rukuh, Sajdah, Manzil, HizbQuarter, VerseText) VALUES (20, 10, '0', '0', '0', '0', 'حين رأى في الليل نارًا موقدة فقال لأهله: انتظروا لقد أبصرت نارًا، لعلي أجيئكم منها بشعلة تستدفئون بها، وتوقدون بها نارًا أخرى، أو أجد عندها هاديًا يدلنا على الطريق.');
INSERT INTO Quran__Arabic_Tafseer_Muyassar (QuranChapterID, VerseNumber, Rukuh, Sajdah, Manzil, HizbQuarter, VerseText) VALUES (20, 11, '0', '0', '0', '0', 'فلما أتى موسى تلك النار ناداه الله: يا موسى، إني أنا ربك فاخلع نعليك، إنك الآن بوادي "طوى" الذي باركته، وذلك استعدادًا لمناجاة ربه.');
INSERT INTO Quran__Arabic_Tafseer_Muyassar (QuranChapterID, VerseNumber, Rukuh, Sajdah, Manzil, HizbQuarter, VerseText) VALUES (20, 12, '0', '0', '0', '0', 'فلما أتى موسى تلك النار ناداه الله: يا موسى، إني أنا ربك فاخلع نعليك، إنك الآن بوادي "طوى" الذي باركته، وذلك استعدادًا لمناجاة ربه.');
INSERT INTO Quran__Arabic_Tafseer_Muyassar (QuranChapterID, VerseNumber, Rukuh, Sajdah, Manzil, HizbQuarter, VerseText) VALUES (20, 13, '0', '0', '0', '0', 'وإني اخترتك يا موسى لرسالتي، فاستمع لما يوحى إليك مني.');
INSERT INTO Quran__Arabic_Tafseer_Muyassar (QuranChapterID, VerseNumber, Rukuh, Sajdah, Manzil, HizbQuarter, VerseText) VALUES (20, 14, '0', '0', '0', '0', 'إنني أنا الله لا معبود بحق إلا أنا، لا شريك لي، فاعبدني وحدي، وأقم الصلاة لتذكرني فيها.');
INSERT INTO Quran__Arabic_Tafseer_Muyassar (QuranChapterID, VerseNumber, Rukuh, Sajdah, Manzil, HizbQuarter, VerseText) VALUES (20, 15, '0', '0', '0', '0', 'إن الساعة التي يُبعث فيها الناس آتية لا بد من وقوعها، أكاد أخفيها من نفسي، فكيف يعلمها أحد من المخلوقين؛ لكي تُجزى كل نفس بما عملت في الدنيا من خير أو شر.');
INSERT INTO Quran__Arabic_Tafseer_Muyassar (QuranChapterID, VerseNumber, Rukuh, Sajdah, Manzil, HizbQuarter, VerseText) VALUES (20, 16, '0', '0', '0', '0', 'فلا يصرفنَّك - يا موسى - عن الإيمان بها والاستعداد لها مَن لا يصدق بوقوعها ولا يعمل لها، واتبع هوى نفسه، فكذَّب بها، فتهلك.');
INSERT INTO Quran__Arabic_Tafseer_Muyassar (QuranChapterID, VerseNumber, Rukuh, Sajdah, Manzil, HizbQuarter, VerseText) VALUES (20, 17, '0', '0', '0', '0', 'وما هذه التي في يمينك يا موسى؟');
INSERT INTO Quran__Arabic_Tafseer_Muyassar (QuranChapterID, VerseNumber, Rukuh, Sajdah, Manzil, HizbQuarter, VerseText) VALUES (20, 18, '0', '0', '0', '0', 'قال موسى: هي عصاي أعتمد عليها في المشي، وأهزُّ بها الشجر؛ لترعى غنمي ما يتساقط من ورقه، ولي فيها منافع أخرى.');
INSERT INTO Quran__Arabic_Tafseer_Muyassar (QuranChapterID, VerseNumber, Rukuh, Sajdah, Manzil, HizbQuarter, VerseText) VALUES (20, 19, '0', '0', '0', '0', 'قال الله لموسى: ألق عصاك.');
INSERT INTO Quran__Arabic_Tafseer_Muyassar (QuranChapterID, VerseNumber, Rukuh, Sajdah, Manzil, HizbQuarter, VerseText) VALUES (20, 20, '0', '0', '0', '0', 'فألقاها موسى على الأرض، فانقلبت بإذن الله حية تسعى، فرأى موسى أمرًا عظيمًا وولى هاربًا.');
INSERT INTO Quran__Arabic_Tafseer_Muyassar (QuranChapterID, VerseNumber, Rukuh, Sajdah, Manzil, HizbQuarter, VerseText) VALUES (20, 21, '0', '0', '0', '0', 'قال الله لموسى: خذ الحية، ولا تَخَفْ منها، سوف نعيدها عصًا كما كانت في حالتها الأولى. واضمم يدك إلى جنبك تحت العَضُد تخرج بيضاء كالثلج من غير برص؛ لتكون لك علامة أخرى.');
INSERT INTO Quran__Arabic_Tafseer_Muyassar (QuranChapterID, VerseNumber, Rukuh, Sajdah, Manzil, HizbQuarter, VerseText) VALUES (20, 22, '0', '0', '0', '0', 'قال الله لموسى: خذ الحية، ولا تَخَفْ منها، سوف نعيدها عصًا كما كانت في حالتها الأولى. واضمم يدك إلى جنبك تحت العَضُد تخرج بيضاء كالثلج من غير برص؛ لتكون لك علامة أخرى.');
INSERT INTO Quran__Arabic_Tafseer_Muyassar (QuranChapterID, VerseNumber, Rukuh, Sajdah, Manzil, HizbQuarter, VerseText) VALUES (20, 23, '0', '0', '0', '0', 'فعلنا ذلك؛ لكي نريك - يا موسى - من أدلتنا الكبرى ما يدلُّ على قدرتنا، وعظيم سلطاننا، وصحة رسالتك.');
INSERT INTO Quran__Arabic_Tafseer_Muyassar (QuranChapterID, VerseNumber, Rukuh, Sajdah, Manzil, HizbQuarter, VerseText) VALUES (20, 24, '1', '0', '0', '0', 'اذهب - يا موسى - إلى فرعون؛ إنه قد تجاوز قدره وتمرَّد على ربه، فادعه إلى توحيد الله وعبادته.');
INSERT INTO Quran__Arabic_Tafseer_Muyassar (QuranChapterID, VerseNumber, Rukuh, Sajdah, Manzil, HizbQuarter, VerseText) VALUES (20, 25, '0', '0', '0', '0', 'قال موسى: رب وسِّع لي صدري، وسَهِّل لي أمري، وأطلق لساني بفصيح المنطق؛ ليفهموا كلامي. واجعل لي معينا من أهلي، هارون أخي. قَوِّني به وشدَّ به ظهري، وأشركه معي في النبوة وتبليغ الرسالة؛ كي ننزهك بالتسبيح كثيرًا، ونذكرك كثيرا فنحمدك. إنك كنت بنا بصيرًا، لا يخفى عليك شيء من أفعالنا.');
INSERT INTO Quran__Arabic_Tafseer_Muyassar (QuranChapterID, VerseNumber, Rukuh, Sajdah, Manzil, HizbQuarter, VerseText) VALUES (20, 26, '0', '0', '0', '0', 'قال موسى: رب وسِّع لي صدري، وسَهِّل لي أمري، وأطلق لساني بفصيح المنطق؛ ليفهموا كلامي. واجعل لي معينا من أهلي، هارون أخي. قَوِّني به وشدَّ به ظهري، وأشركه معي في النبوة وتبليغ الرسالة؛ كي ننزهك بالتسبيح كثيرًا، ونذكرك كثيرا فنحمدك. إنك كنت بنا بصيرًا، لا يخفى عليك شيء من أفعالنا.');
