INSERT INTO Quran__Arabic_Tafseer_Muyassar (QuranChapterID, VerseNumber, Rukuh, Sajdah, Manzil, HizbQuarter, VerseText) VALUES (26, 79, '0', '0', '0', '0', 'قال إبراهيم: أفأبصرتم بتدبر ما كنتم تعبدون من الأصنام التي لا تسمع ولا تنفع ولا تضر، أنتم وآباؤكم الأقدمون من قبلكم؟ فإن ما تعبدونهم من دون الله أعداء لي، لكن رب العالمين ومالك أمرهم هو وحده الذي أعبده. هو الذي خلقني في أحسن صورة فهو يرشدني إلى مصالح الدنيا والآخرة، وهو الذي ينعم عليَّ بالطعام والشراب، وإذا أصابني مرض فهو الذي يَشْفيني ويعافيني منه، وهو الذي يميتني في الدينا بقبض روحي، ثم يحييني يوم القيامة، لا يقدر على ذلك أحد سواه، والذي أطمع أن يتجاوز عن ذنبي يوم الجزاء.');
INSERT INTO Quran__Arabic_Tafseer_Muyassar (QuranChapterID, VerseNumber, Rukuh, Sajdah, Manzil, HizbQuarter, VerseText) VALUES (26, 80, '0', '0', '0', '0', 'قال إبراهيم: أفأبصرتم بتدبر ما كنتم تعبدون من الأصنام التي لا تسمع ولا تنفع ولا تضر، أنتم وآباؤكم الأقدمون من قبلكم؟ فإن ما تعبدونهم من دون الله أعداء لي، لكن رب العالمين ومالك أمرهم هو وحده الذي أعبده. هو الذي خلقني في أحسن صورة فهو يرشدني إلى مصالح الدنيا والآخرة، وهو الذي ينعم عليَّ بالطعام والشراب، وإذا أصابني مرض فهو الذي يَشْفيني ويعافيني منه، وهو الذي يميتني في الدينا بقبض روحي، ثم يحييني يوم القيامة، لا يقدر على ذلك أحد سواه، والذي أطمع أن يتجاوز عن ذنبي يوم الجزاء.');
INSERT INTO Quran__Arabic_Tafseer_Muyassar (QuranChapterID, VerseNumber, Rukuh, Sajdah, Manzil, HizbQuarter, VerseText) VALUES (26, 81, '0', '0', '0', '0', 'قال إبراهيم: أفأبصرتم بتدبر ما كنتم تعبدون من الأصنام التي لا تسمع ولا تنفع ولا تضر، أنتم وآباؤكم الأقدمون من قبلكم؟ فإن ما تعبدونهم من دون الله أعداء لي، لكن رب العالمين ومالك أمرهم هو وحده الذي أعبده. هو الذي خلقني في أحسن صورة فهو يرشدني إلى مصالح الدنيا والآخرة، وهو الذي ينعم عليَّ بالطعام والشراب، وإذا أصابني مرض فهو الذي يَشْفيني ويعافيني منه، وهو الذي يميتني في الدينا بقبض روحي، ثم يحييني يوم القيامة، لا يقدر على ذلك أحد سواه، والذي أطمع أن يتجاوز عن ذنبي يوم الجزاء.');
INSERT INTO Quran__Arabic_Tafseer_Muyassar (QuranChapterID, VerseNumber, Rukuh, Sajdah, Manzil, HizbQuarter, VerseText) VALUES (26, 82, '0', '0', '0', '0', 'قال إبراهيم: أفأبصرتم بتدبر ما كنتم تعبدون من الأصنام التي لا تسمع ولا تنفع ولا تضر، أنتم وآباؤكم الأقدمون من قبلكم؟ فإن ما تعبدونهم من دون الله أعداء لي، لكن رب العالمين ومالك أمرهم هو وحده الذي أعبده. هو الذي خلقني في أحسن صورة فهو يرشدني إلى مصالح الدنيا والآخرة، وهو الذي ينعم عليَّ بالطعام والشراب، وإذا أصابني مرض فهو الذي يَشْفيني ويعافيني منه، وهو الذي يميتني في الدينا بقبض روحي، ثم يحييني يوم القيامة، لا يقدر على ذلك أحد سواه، والذي أطمع أن يتجاوز عن ذنبي يوم الجزاء.');
INSERT INTO Quran__Arabic_Tafseer_Muyassar (QuranChapterID, VerseNumber, Rukuh, Sajdah, Manzil, HizbQuarter, VerseText) VALUES (26, 83, '0', '0', '0', '0', 'قال إبراهيم داعيًا ربه: ربِّ امنحني العلم والفهم، وألحقني بالصالحين، واجمع بيني وبينهم في الجنة.');
INSERT INTO Quran__Arabic_Tafseer_Muyassar (QuranChapterID, VerseNumber, Rukuh, Sajdah, Manzil, HizbQuarter, VerseText) VALUES (26, 84, '0', '0', '0', '0', 'واجعل لي ثناء حسنًا وذكرًا جميلا في الذين يأتون بعدي إلى يوم القيامة.');
INSERT INTO Quran__Arabic_Tafseer_Muyassar (QuranChapterID, VerseNumber, Rukuh, Sajdah, Manzil, HizbQuarter, VerseText) VALUES (26, 85, '0', '0', '0', '0', 'واجعلني من عبادك الذين تورثهم نعيم الجنة.');
INSERT INTO Quran__Arabic_Tafseer_Muyassar (QuranChapterID, VerseNumber, Rukuh, Sajdah, Manzil, HizbQuarter, VerseText) VALUES (26, 86, '0', '0', '0', '0', 'وهذا دعاء من إبراهيم عليه السلام أن ينقذ الله أباه من الضلال إلى الهدى، فيغفر له ويتجاوز عنه، كما وعد إبراهيم أباه بالدعاء له، فلما تبيَّن له أنه مستمر في الكفر والشرك إلى أن يموت تبرَّأ منه.');
INSERT INTO Quran__Arabic_Tafseer_Muyassar (QuranChapterID, VerseNumber, Rukuh, Sajdah, Manzil, HizbQuarter, VerseText) VALUES (26, 87, '0', '0', '0', '0', 'ولا تُلْحق بي الذل، يوم يخرج الناس من القبور للحساب والجزاء، يوم لا ينفع المال والبنون أحدًا من العباد، إلا مَن أتى الله بقلب سليم من الكفر والنفاق والرذيلة.');
INSERT INTO Quran__Arabic_Tafseer_Muyassar (QuranChapterID, VerseNumber, Rukuh, Sajdah, Manzil, HizbQuarter, VerseText) VALUES (26, 88, '0', '0', '0', '0', 'ولا تُلْحق بي الذل، يوم يخرج الناس من القبور للحساب والجزاء، يوم لا ينفع المال والبنون أحدًا من العباد، إلا مَن أتى الله بقلب سليم من الكفر والنفاق والرذيلة.');
INSERT INTO Quran__Arabic_Tafseer_Muyassar (QuranChapterID, VerseNumber, Rukuh, Sajdah, Manzil, HizbQuarter, VerseText) VALUES (26, 89, '0', '0', '0', '0', 'ولا تُلْحق بي الذل، يوم يخرج الناس من القبور للحساب والجزاء، يوم لا ينفع المال والبنون أحدًا من العباد، إلا مَن أتى الله بقلب سليم من الكفر والنفاق والرذيلة.');
INSERT INTO Quran__Arabic_Tafseer_Muyassar (QuranChapterID, VerseNumber, Rukuh, Sajdah, Manzil, HizbQuarter, VerseText) VALUES (26, 90, '0', '0', '0', '0', 'وقُرِّبت الجنة للذين اجتنبوا الكفر والمعاصي، وأقبلوا على الله بالطاعة.');
INSERT INTO Quran__Arabic_Tafseer_Muyassar (QuranChapterID, VerseNumber, Rukuh, Sajdah, Manzil, HizbQuarter, VerseText) VALUES (26, 91, '0', '0', '0', '0', 'وأُظهرت النار للكافرين الذين ضَلُّوا عن الهدى، وتجرَّؤوا على محارم الله وكذَّبوا رسله.');
INSERT INTO Quran__Arabic_Tafseer_Muyassar (QuranChapterID, VerseNumber, Rukuh, Sajdah, Manzil, HizbQuarter, VerseText) VALUES (26, 92, '0', '0', '0', '0', 'وقيل لهم توبيخًا: أين آلهتكم التي كنتم تعبدونها مِن دون الله، وتزعمون أنها تشفع لكم اليوم؟ هل ينصرونكم، فيدفعون العذاب عنكم، أو ينتصرون بدفع العذاب عن أنفسهم؟ لا شيء من ذلك.');
INSERT INTO Quran__Arabic_Tafseer_Muyassar (QuranChapterID, VerseNumber, Rukuh, Sajdah, Manzil, HizbQuarter, VerseText) VALUES (26, 93, '0', '0', '0', '0', 'وقيل لهم توبيخًا: أين آلهتكم التي كنتم تعبدونها مِن دون الله، وتزعمون أنها تشفع لكم اليوم؟ هل ينصرونكم، فيدفعون العذاب عنكم، أو ينتصرون بدفع العذاب عن أنفسهم؟ لا شيء من ذلك.');
INSERT INTO Quran__Arabic_Tafseer_Muyassar (QuranChapterID, VerseNumber, Rukuh, Sajdah, Manzil, HizbQuarter, VerseText) VALUES (26, 94, '0', '0', '0', '0', 'فجُمِعوا وألقُوا في جهنم، هم والذين أضلوهم وأعوان إبليس الذين زيَّنوا لهم الشر، لم يُفْلِت منهم أحد.');
INSERT INTO Quran__Arabic_Tafseer_Muyassar (QuranChapterID, VerseNumber, Rukuh, Sajdah, Manzil, HizbQuarter, VerseText) VALUES (26, 95, '0', '0', '0', '0', 'فجُمِعوا وألقُوا في جهنم، هم والذين أضلوهم وأعوان إبليس الذين زيَّنوا لهم الشر، لم يُفْلِت منهم أحد.');
INSERT INTO Quran__Arabic_Tafseer_Muyassar (QuranChapterID, VerseNumber, Rukuh, Sajdah, Manzil, HizbQuarter, VerseText) VALUES (26, 96, '0', '0', '0', '0', 'قالوا معترفين بخطئهم، وهم يتنازعون في جهنم مع مَن أضلوهم، تالله إننا كنا في الدنيا في ضلال واضح لا خفاء فيه؛ إذ نسويكم برب العالمين المستحق للعبادة وحده. وما أوقعنا في هذا المصير السيِّئ إلا المجرمون الذين دعونا إلى عبادة غير الله فاتبعناهم.');
INSERT INTO Quran__Arabic_Tafseer_Muyassar (QuranChapterID, VerseNumber, Rukuh, Sajdah, Manzil, HizbQuarter, VerseText) VALUES (26, 97, '0', '0', '0', '0', 'قالوا معترفين بخطئهم، وهم يتنازعون في جهنم مع مَن أضلوهم، تالله إننا كنا في الدنيا في ضلال واضح لا خفاء فيه؛ إذ نسويكم برب العالمين المستحق للعبادة وحده. وما أوقعنا في هذا المصير السيِّئ إلا المجرمون الذين دعونا إلى عبادة غير الله فاتبعناهم.');
INSERT INTO Quran__Arabic_Tafseer_Muyassar (QuranChapterID, VerseNumber, Rukuh, Sajdah, Manzil, HizbQuarter, VerseText) VALUES (26, 98, '0', '0', '0', '0', 'قالوا معترفين بخطئهم، وهم يتنازعون في جهنم مع مَن أضلوهم، تالله إننا كنا في الدنيا في ضلال واضح لا خفاء فيه؛ إذ نسويكم برب العالمين المستحق للعبادة وحده. وما أوقعنا في هذا المصير السيِّئ إلا المجرمون الذين دعونا إلى عبادة غير الله فاتبعناهم.');
INSERT INTO Quran__Arabic_Tafseer_Muyassar (QuranChapterID, VerseNumber, Rukuh, Sajdah, Manzil, HizbQuarter, VerseText) VALUES (26, 99, '0', '0', '0', '0', 'قالوا معترفين بخطئهم، وهم يتنازعون في جهنم مع مَن أضلوهم، تالله إننا كنا في الدنيا في ضلال واضح لا خفاء فيه؛ إذ نسويكم برب العالمين المستحق للعبادة وحده. وما أوقعنا في هذا المصير السيِّئ إلا المجرمون الذين دعونا إلى عبادة غير الله فاتبعناهم.');
INSERT INTO Quran__Arabic_Tafseer_Muyassar (QuranChapterID, VerseNumber, Rukuh, Sajdah, Manzil, HizbQuarter, VerseText) VALUES (26, 100, '0', '0', '0', '0', 'فلا أحدَ يشفع لنا، ويخلِّصنا من العذاب، ولا مَن يَصْدُق في مودتنا ويشفق علينا.');
INSERT INTO Quran__Arabic_Tafseer_Muyassar (QuranChapterID, VerseNumber, Rukuh, Sajdah, Manzil, HizbQuarter, VerseText) VALUES (26, 101, '0', '0', '0', '0', 'فلا أحدَ يشفع لنا، ويخلِّصنا من العذاب، ولا مَن يَصْدُق في مودتنا ويشفق علينا.');
INSERT INTO Quran__Arabic_Tafseer_Muyassar (QuranChapterID, VerseNumber, Rukuh, Sajdah, Manzil, HizbQuarter, VerseText) VALUES (26, 102, '0', '0', '0', '0', 'فليت لنا رجعة إلى الدنيا، فنصير من جملة المؤمنين الناجين.');
INSERT INTO Quran__Arabic_Tafseer_Muyassar (QuranChapterID, VerseNumber, Rukuh, Sajdah, Manzil, HizbQuarter, VerseText) VALUES (26, 103, '0', '0', '0', '0', 'إن في نبأ إبراهيم السابق لَعبرة لِمن يعتبر، وما صار أكثر الذين سمعوا هذا النبأ مؤمنين. وإن ربك لهو العزيز القادر على الانتقام من المكذبين، الرحيم بعباده المؤمنين.');
INSERT INTO Quran__Arabic_Tafseer_Muyassar (QuranChapterID, VerseNumber, Rukuh, Sajdah, Manzil, HizbQuarter, VerseText) VALUES (26, 104, '1', '0', '0', '0', 'إن في نبأ إبراهيم السابق لَعبرة لِمن يعتبر، وما صار أكثر الذين سمعوا هذا النبأ مؤمنين. وإن ربك لهو العزيز القادر على الانتقام من المكذبين، الرحيم بعباده المؤمنين.');
INSERT INTO Quran__Arabic_Tafseer_Muyassar (QuranChapterID, VerseNumber, Rukuh, Sajdah, Manzil, HizbQuarter, VerseText) VALUES (26, 105, '0', '0', '0', '0', 'كَذَّبت قوم نوح رسالة نبيهم، فكانوا بهذا مكذبين لجميع الرسل؛ لأن كل رسول يأمر بتصديق جميع الرسل. إذ قال لهم أخوهم نوح: ألا تخشون الله بترك عبادة غيره؟ إني لكم رسول أمين فيما أبلغكم، فاجعلوا الإيمان وقاية لكم من عذاب الله وأطيعوني فيما آمركم به من عبادته وحده. وما أطلب منكم أجرًا على تبليغ الرسالة، ما أجري إلا على رب العالمين، المتصرف في خلقه، فاحذروا عقابه، وأطيعوني بامتثال أوامره، واجتناب نواهيه.');
INSERT INTO Quran__Arabic_Tafseer_Muyassar (QuranChapterID, VerseNumber, Rukuh, Sajdah, Manzil, HizbQuarter, VerseText) VALUES (26, 106, '0', '0', '0', '0', 'كَذَّبت قوم نوح رسالة نبيهم، فكانوا بهذا مكذبين لجميع الرسل؛ لأن كل رسول يأمر بتصديق جميع الرسل. إذ قال لهم أخوهم نوح: ألا تخشون الله بترك عبادة غيره؟ إني لكم رسول أمين فيما أبلغكم، فاجعلوا الإيمان وقاية لكم من عذاب الله وأطيعوني فيما آمركم به من عبادته وحده. وما أطلب منكم أجرًا على تبليغ الرسالة، ما أجري إلا على رب العالمين، المتصرف في خلقه، فاحذروا عقابه، وأطيعوني بامتثال أوامره، واجتناب نواهيه.');
