INSERT INTO Quran__Arabic_Tafseer_Muyassar (QuranChapterID, VerseNumber, Rukuh, Sajdah, Manzil, HizbQuarter, VerseText) VALUES (68, 15, '0', '0', '0', '0', 'ولا تطع -أيها الرسول- كلَّ إنسانٍ كثير الحلف كذاب حقير، مغتاب للناس، يمشي بينهم بالنميمة، وينقل حديث بعضهم إلى بعض على وجه الإفساد بينهم، بخيل بالمال ضنين به عن الحق، شديد المنع للخير، متجاوز حدَّه في العدوان على الناس وتناول المحرمات، كثير الآثام، شديد في كفره، فاحش لئيم، منسوب لغير أبيه. ومن أجل أنه كان صاحب مال وبنين طغى وتكبر عن الحق، فإذا قرأ عليه أحد آيات القرآن كذَّب بها، وقال: هذا أباطيل الأولين وخرافاتهم. وهذه الآيات وإن نزلت في بعض المشركين كالوليد بن المغيرة، إلا أن فيها تحذيرًا للمسلم من موافقة من اتصف بهذه الصفات الذميمة.');
INSERT INTO Quran__Arabic_Tafseer_Muyassar (QuranChapterID, VerseNumber, Rukuh, Sajdah, Manzil, HizbQuarter, VerseText) VALUES (68, 16, '0', '0', '0', '0', 'سنجعل على أنفه علامة لازمة لا تفارقه عقوبة له؛ ليكون مفتضحًا بها أمام الناس.');
INSERT INTO Quran__Arabic_Tafseer_Muyassar (QuranChapterID, VerseNumber, Rukuh, Sajdah, Manzil, HizbQuarter, VerseText) VALUES (68, 17, '0', '0', '0', '0', 'إنا اختبرنا أهل "مكة" بالجوع والقحط، كما اختبرنا أصحاب الحديقة حين حلفوا فيما بينهم، ليقطعُنَّ ثمار حديقتهم مبكِّرين في الصباح، فلا يَطْعَم منها غيرهم من المساكين ونحوهم، ولم يقولوا: إن شاء الله.');
INSERT INTO Quran__Arabic_Tafseer_Muyassar (QuranChapterID, VerseNumber, Rukuh, Sajdah, Manzil, HizbQuarter, VerseText) VALUES (68, 18, '0', '0', '0', '0', 'إنا اختبرنا أهل "مكة" بالجوع والقحط، كما اختبرنا أصحاب الحديقة حين حلفوا فيما بينهم، ليقطعُنَّ ثمار حديقتهم مبكِّرين في الصباح، فلا يَطْعَم منها غيرهم من المساكين ونحوهم، ولم يقولوا: إن شاء الله.');
INSERT INTO Quran__Arabic_Tafseer_Muyassar (QuranChapterID, VerseNumber, Rukuh, Sajdah, Manzil, HizbQuarter, VerseText) VALUES (68, 19, '0', '0', '0', '0', 'فأنزل الله عليها نارًا أحرقتها ليلا وهم نائمون، فأصبحت محترقة سوداء كالليل المظلم.');
INSERT INTO Quran__Arabic_Tafseer_Muyassar (QuranChapterID, VerseNumber, Rukuh, Sajdah, Manzil, HizbQuarter, VerseText) VALUES (68, 20, '0', '0', '0', '0', 'فأنزل الله عليها نارًا أحرقتها ليلا وهم نائمون، فأصبحت محترقة سوداء كالليل المظلم.');
INSERT INTO Quran__Arabic_Tafseer_Muyassar (QuranChapterID, VerseNumber, Rukuh, Sajdah, Manzil, HizbQuarter, VerseText) VALUES (68, 21, '0', '0', '0', '0', 'فنادى بعضهم بعضًا وقت الصباح: أن اذهبوا مبكرين إلى زرعكم، إن كنتم مصرِّين على قطع الثمار.');
INSERT INTO Quran__Arabic_Tafseer_Muyassar (QuranChapterID, VerseNumber, Rukuh, Sajdah, Manzil, HizbQuarter, VerseText) VALUES (68, 22, '0', '0', '0', '0', 'فنادى بعضهم بعضًا وقت الصباح: أن اذهبوا مبكرين إلى زرعكم، إن كنتم مصرِّين على قطع الثمار.');
INSERT INTO Quran__Arabic_Tafseer_Muyassar (QuranChapterID, VerseNumber, Rukuh, Sajdah, Manzil, HizbQuarter, VerseText) VALUES (68, 23, '0', '0', '0', '0', 'فاندفعوا مسرعين، وهم يتسارُّون بالحديث فيما بينهم: بأن لا تمكِّنوا اليوم أحدا من المساكين من دخول حديقتكم.');
INSERT INTO Quran__Arabic_Tafseer_Muyassar (QuranChapterID, VerseNumber, Rukuh, Sajdah, Manzil, HizbQuarter, VerseText) VALUES (68, 24, '0', '0', '0', '0', 'فاندفعوا مسرعين، وهم يتسارُّون بالحديث فيما بينهم: بأن لا تمكِّنوا اليوم أحدا من المساكين من دخول حديقتكم.');
INSERT INTO Quran__Arabic_Tafseer_Muyassar (QuranChapterID, VerseNumber, Rukuh, Sajdah, Manzil, HizbQuarter, VerseText) VALUES (68, 25, '0', '0', '0', '0', 'وساروا في أول النهار إلى حديقتهم على قصدهم السيِّئ في منع المساكين من ثمار الحديقة، وهم في غاية القدرة على تنفيذه في زعمهم.');
INSERT INTO Quran__Arabic_Tafseer_Muyassar (QuranChapterID, VerseNumber, Rukuh, Sajdah, Manzil, HizbQuarter, VerseText) VALUES (68, 26, '0', '0', '0', '0', 'فلما رأوا حديقتهم محترقة أنكروها، وقالوا: لقد أخطأنا الطريق إليها، فلما عرفوا أنها هي جنتهم، قالوا: بل نحن محرومون خيرها؛ بسبب عزمنا على البخل ومنع المساكين. قال أعدلهم: ألم أقل لكم هلا تستثنون وتقولون: إن شاء الله؟ قالوا بعد أن عادوا إلى رشدهم: تنزَّه الله ربنا عن الظلم فيما أصابنا، بل نحن كنا الظالمين لأنفسنا بترك الاستثناء وقصدنا السيِّئ. فأقبل بعضهم على بعض، يلوم كل منهم الآخر على تركهم الاستثناء وعلى قصدهم السيِّئ، قالوا: يا ويلنا إنَّا كنا متجاوزين الحد في منعنا الفقراء ومخالفة أمر الله، عسى ربنا أن يعطينا أفضل من حديقتنا؛ بسبب توبتنا واعترافنا بخطيئتنا. إنا إلى ربنا وحده راغبون، راجون العفو، طالبون الخير. مثل ذلك العقاب الذي عاقبنا به أهل الحديقة يكون عقابنا في الدنيا لكل مَن خالف أمر الله، وبخل بما آتاه الله من النعم فلم يؤدِّ حق الله فيها، ولَعذاب الآخرة أعظم وأشد مِن عذاب الدنيا، لو كانوا يعلمون لانزجروا عن كل سبب يوجب العقاب.');
INSERT INTO Quran__Arabic_Tafseer_Muyassar (QuranChapterID, VerseNumber, Rukuh, Sajdah, Manzil, HizbQuarter, VerseText) VALUES (68, 27, '0', '0', '0', '0', 'فلما رأوا حديقتهم محترقة أنكروها، وقالوا: لقد أخطأنا الطريق إليها، فلما عرفوا أنها هي جنتهم، قالوا: بل نحن محرومون خيرها؛ بسبب عزمنا على البخل ومنع المساكين. قال أعدلهم: ألم أقل لكم هلا تستثنون وتقولون: إن شاء الله؟ قالوا بعد أن عادوا إلى رشدهم: تنزَّه الله ربنا عن الظلم فيما أصابنا، بل نحن كنا الظالمين لأنفسنا بترك الاستثناء وقصدنا السيِّئ. فأقبل بعضهم على بعض، يلوم كل منهم الآخر على تركهم الاستثناء وعلى قصدهم السيِّئ، قالوا: يا ويلنا إنَّا كنا متجاوزين الحد في منعنا الفقراء ومخالفة أمر الله، عسى ربنا أن يعطينا أفضل من حديقتنا؛ بسبب توبتنا واعترافنا بخطيئتنا. إنا إلى ربنا وحده راغبون، راجون العفو، طالبون الخير. مثل ذلك العقاب الذي عاقبنا به أهل الحديقة يكون عقابنا في الدنيا لكل مَن خالف أمر الله، وبخل بما آتاه الله من النعم فلم يؤدِّ حق الله فيها، ولَعذاب الآخرة أعظم وأشد مِن عذاب الدنيا، لو كانوا يعلمون لانزجروا عن كل سبب يوجب العقاب.');
