INSERT INTO Quran__Arabic_Tafseer_Muyassar (QuranChapterID, VerseNumber, Rukuh, Sajdah, Manzil, HizbQuarter, VerseText) VALUES (74, 45, '0', '0', '0', '0', 'كل نفس بما كسبت من أعمال الشر والسوء محبوسة مرهونة بكسبها، لا تُفَكُّ حتى تؤدي ما عليها من الحقوق والعقوبات، إلا المسلمين المخلصين أصحاب اليمين الذين فكُّوا رقابهم بالطاعة، هم في جنات لا يُدْرَك وصفها، يسأل بعضهم بعضًا عن الكافرين الذين أجرموا في حق أنفسهم: ما الذي أدخلكم جهنم، وجعلكم تذوقون سعيرها؟ قال المجرمون: لم نكن من المصلِّين في الدنيا، ولم نكن نتصدق ونحسن للفقراء والمساكين، وكنا نتحدث بالباطل مع أهل الغَواية والضلالة، وكنا نكذب بيوم الحساب والجزاء، حتى جاءنا الموت، ونحن في تلك الضلالات والمنكرات.');
INSERT INTO Quran__Arabic_Tafseer_Muyassar (QuranChapterID, VerseNumber, Rukuh, Sajdah, Manzil, HizbQuarter, VerseText) VALUES (74, 46, '0', '0', '0', '0', 'كل نفس بما كسبت من أعمال الشر والسوء محبوسة مرهونة بكسبها، لا تُفَكُّ حتى تؤدي ما عليها من الحقوق والعقوبات، إلا المسلمين المخلصين أصحاب اليمين الذين فكُّوا رقابهم بالطاعة، هم في جنات لا يُدْرَك وصفها، يسأل بعضهم بعضًا عن الكافرين الذين أجرموا في حق أنفسهم: ما الذي أدخلكم جهنم، وجعلكم تذوقون سعيرها؟ قال المجرمون: لم نكن من المصلِّين في الدنيا، ولم نكن نتصدق ونحسن للفقراء والمساكين، وكنا نتحدث بالباطل مع أهل الغَواية والضلالة، وكنا نكذب بيوم الحساب والجزاء، حتى جاءنا الموت، ونحن في تلك الضلالات والمنكرات.');
INSERT INTO Quran__Arabic_Tafseer_Muyassar (QuranChapterID, VerseNumber, Rukuh, Sajdah, Manzil, HizbQuarter, VerseText) VALUES (74, 47, '0', '0', '0', '0', 'كل نفس بما كسبت من أعمال الشر والسوء محبوسة مرهونة بكسبها، لا تُفَكُّ حتى تؤدي ما عليها من الحقوق والعقوبات، إلا المسلمين المخلصين أصحاب اليمين الذين فكُّوا رقابهم بالطاعة، هم في جنات لا يُدْرَك وصفها، يسأل بعضهم بعضًا عن الكافرين الذين أجرموا في حق أنفسهم: ما الذي أدخلكم جهنم، وجعلكم تذوقون سعيرها؟ قال المجرمون: لم نكن من المصلِّين في الدنيا، ولم نكن نتصدق ونحسن للفقراء والمساكين، وكنا نتحدث بالباطل مع أهل الغَواية والضلالة، وكنا نكذب بيوم الحساب والجزاء، حتى جاءنا الموت، ونحن في تلك الضلالات والمنكرات.');
INSERT INTO Quran__Arabic_Tafseer_Muyassar (QuranChapterID, VerseNumber, Rukuh, Sajdah, Manzil, HizbQuarter, VerseText) VALUES (74, 48, '0', '0', '0', '0', 'فما تنفعهم شفاعة الشافعين جميعًا من الملائكة والنبيين وغيرهم؛ لأن الشفاعة إنما تكون لمن ارتضاه الله، وأذن لشفيعه.');
INSERT INTO Quran__Arabic_Tafseer_Muyassar (QuranChapterID, VerseNumber, Rukuh, Sajdah, Manzil, HizbQuarter, VerseText) VALUES (74, 49, '0', '0', '0', '0', 'فما لهؤلاء المشركين عن القرآن وما فيه من المواعظ منصرفين؟ كأنهم حمر وحشية شديدة النِّفار، فرَّت من أسد كاسر.');
INSERT INTO Quran__Arabic_Tafseer_Muyassar (QuranChapterID, VerseNumber, Rukuh, Sajdah, Manzil, HizbQuarter, VerseText) VALUES (74, 50, '0', '0', '0', '0', 'فما لهؤلاء المشركين عن القرآن وما فيه من المواعظ منصرفين؟ كأنهم حمر وحشية شديدة النِّفار، فرَّت من أسد كاسر.');
INSERT INTO Quran__Arabic_Tafseer_Muyassar (QuranChapterID, VerseNumber, Rukuh, Sajdah, Manzil, HizbQuarter, VerseText) VALUES (74, 51, '0', '0', '0', '0', 'فما لهؤلاء المشركين عن القرآن وما فيه من المواعظ منصرفين؟ كأنهم حمر وحشية شديدة النِّفار، فرَّت من أسد كاسر.');
INSERT INTO Quran__Arabic_Tafseer_Muyassar (QuranChapterID, VerseNumber, Rukuh, Sajdah, Manzil, HizbQuarter, VerseText) VALUES (74, 52, '0', '0', '0', '0', 'بل يطمع كل واحد من هؤلاء المشركين أن يُنزل الله عليه كتابًا من السماء منشورًا، كما أنزل على محمد صلى الله عليه وسلم. ليس الأمر كما زعموا، بل الحقيقة أنهم لا يخافون الآخرة، ولا يصدِّقون بالبعث والجزاء.');
INSERT INTO Quran__Arabic_Tafseer_Muyassar (QuranChapterID, VerseNumber, Rukuh, Sajdah, Manzil, HizbQuarter, VerseText) VALUES (74, 53, '0', '0', '0', '0', 'بل يطمع كل واحد من هؤلاء المشركين أن يُنزل الله عليه كتابًا من السماء منشورًا، كما أنزل على محمد صلى الله عليه وسلم. ليس الأمر كما زعموا، بل الحقيقة أنهم لا يخافون الآخرة، ولا يصدِّقون بالبعث والجزاء.');
INSERT INTO Quran__Arabic_Tafseer_Muyassar (QuranChapterID, VerseNumber, Rukuh, Sajdah, Manzil, HizbQuarter, VerseText) VALUES (74, 54, '0', '0', '0', '0', 'حقًّا أنَّ القرآن موعظة بليغة كافية لاتِّعاظهم، فمن أراد الاتعاظ اتعظ بما فيه وانتفع بهداه، وما يتعظون به إلا أن يشاء الله لهم الهدى. هو سبحانه أهلٌ لأن يُتقى ويطاع، وأهلٌ لأن يغفر لمن آمن به وأطاعه.');
INSERT INTO Quran__Arabic_Tafseer_Muyassar (QuranChapterID, VerseNumber, Rukuh, Sajdah, Manzil, HizbQuarter, VerseText) VALUES (74, 55, '0', '0', '0', '0', 'حقًّا أنَّ القرآن موعظة بليغة كافية لاتِّعاظهم، فمن أراد الاتعاظ اتعظ بما فيه وانتفع بهداه، وما يتعظون به إلا أن يشاء الله لهم الهدى. هو سبحانه أهلٌ لأن يُتقى ويطاع، وأهلٌ لأن يغفر لمن آمن به وأطاعه.');
INSERT INTO Quran__Arabic_Tafseer_Muyassar (QuranChapterID, VerseNumber, Rukuh, Sajdah, Manzil, HizbQuarter, VerseText) VALUES (74, 56, '1', '0', '0', '0', 'حقًّا أنَّ القرآن موعظة بليغة كافية لاتِّعاظهم، فمن أراد الاتعاظ اتعظ بما فيه وانتفع بهداه، وما يتعظون به إلا أن يشاء الله لهم الهدى. هو سبحانه أهلٌ لأن يُتقى ويطاع، وأهلٌ لأن يغفر لمن آمن به وأطاعه.');
INSERT INTO Quran__Arabic_Tafseer_Muyassar (QuranChapterID, VerseNumber, Rukuh, Sajdah, Manzil, HizbQuarter, VerseText) VALUES (75, 1, '0', '0', '0', '1', 'أقسم الله سبحانه بيوم الحساب والجزاء، وأقسم بالنفس المؤمنة التقية التي تلوم صاحبها على ترك الطاعات وفِعْل الموبقات، أن الناس يبعثون. أيظنُّ هذا الإنسان الكافر أن لن نقدر على جَمْع عظامه بعد تفرقها؟ بلى سنجمعها، قادرين على أن نجعل أصابعه أو أنامله -بعد جمعها وتأليفها- خَلْقًا سويًّا، كما كانت قبل الموت.');
INSERT INTO Quran__Arabic_Tafseer_Muyassar (QuranChapterID, VerseNumber, Rukuh, Sajdah, Manzil, HizbQuarter, VerseText) VALUES (75, 2, '0', '0', '0', '0', 'أقسم الله سبحانه بيوم الحساب والجزاء، وأقسم بالنفس المؤمنة التقية التي تلوم صاحبها على ترك الطاعات وفِعْل الموبقات، أن الناس يبعثون. أيظنُّ هذا الإنسان الكافر أن لن نقدر على جَمْع عظامه بعد تفرقها؟ بلى سنجمعها، قادرين على أن نجعل أصابعه أو أنامله -بعد جمعها وتأليفها- خَلْقًا سويًّا، كما كانت قبل الموت.');
INSERT INTO Quran__Arabic_Tafseer_Muyassar (QuranChapterID, VerseNumber, Rukuh, Sajdah, Manzil, HizbQuarter, VerseText) VALUES (75, 3, '0', '0', '0', '0', 'أقسم الله سبحانه بيوم الحساب والجزاء، وأقسم بالنفس المؤمنة التقية التي تلوم صاحبها على ترك الطاعات وفِعْل الموبقات، أن الناس يبعثون. أيظنُّ هذا الإنسان الكافر أن لن نقدر على جَمْع عظامه بعد تفرقها؟ بلى سنجمعها، قادرين على أن نجعل أصابعه أو أنامله -بعد جمعها وتأليفها- خَلْقًا سويًّا، كما كانت قبل الموت.');
INSERT INTO Quran__Arabic_Tafseer_Muyassar (QuranChapterID, VerseNumber, Rukuh, Sajdah, Manzil, HizbQuarter, VerseText) VALUES (75, 4, '0', '0', '0', '0', 'أقسم الله سبحانه بيوم الحساب والجزاء، وأقسم بالنفس المؤمنة التقية التي تلوم صاحبها على ترك الطاعات وفِعْل الموبقات، أن الناس يبعثون. أيظنُّ هذا الإنسان الكافر أن لن نقدر على جَمْع عظامه بعد تفرقها؟ بلى سنجمعها، قادرين على أن نجعل أصابعه أو أنامله -بعد جمعها وتأليفها- خَلْقًا سويًّا، كما كانت قبل الموت.');
INSERT INTO Quran__Arabic_Tafseer_Muyassar (QuranChapterID, VerseNumber, Rukuh, Sajdah, Manzil, HizbQuarter, VerseText) VALUES (75, 5, '0', '0', '0', '0', 'بل ينكر الإنسان البعث، يريد أن يبقى على الفجور فيما يستقبل من أيام عمره، يسأل هذا الكافر مستبعدًا قيام الساعة: متى يكون يوم القيامة؟');
INSERT INTO Quran__Arabic_Tafseer_Muyassar (QuranChapterID, VerseNumber, Rukuh, Sajdah, Manzil, HizbQuarter, VerseText) VALUES (75, 6, '0', '0', '0', '0', 'بل ينكر الإنسان البعث، يريد أن يبقى على الفجور فيما يستقبل من أيام عمره، يسأل هذا الكافر مستبعدًا قيام الساعة: متى يكون يوم القيامة؟');
INSERT INTO Quran__Arabic_Tafseer_Muyassar (QuranChapterID, VerseNumber, Rukuh, Sajdah, Manzil, HizbQuarter, VerseText) VALUES (75, 7, '0', '0', '0', '0', 'فإذا تحيَّر البصر ودُهش فزعًا مما رأى من أهوال يوم القيامة، وذهب نور القمر، وجُمِع بين الشمس والقمر في ذهاب الضوء، فلا ضوء لواحد منهما، يقول الإنسان وقتها: أين المهرب من العذاب؟');
INSERT INTO Quran__Arabic_Tafseer_Muyassar (QuranChapterID, VerseNumber, Rukuh, Sajdah, Manzil, HizbQuarter, VerseText) VALUES (75, 8, '0', '0', '0', '0', 'فإذا تحيَّر البصر ودُهش فزعًا مما رأى من أهوال يوم القيامة، وذهب نور القمر، وجُمِع بين الشمس والقمر في ذهاب الضوء، فلا ضوء لواحد منهما، يقول الإنسان وقتها: أين المهرب من العذاب؟');
INSERT INTO Quran__Arabic_Tafseer_Muyassar (QuranChapterID, VerseNumber, Rukuh, Sajdah, Manzil, HizbQuarter, VerseText) VALUES (75, 9, '0', '0', '0', '0', 'فإذا تحيَّر البصر ودُهش فزعًا مما رأى من أهوال يوم القيامة، وذهب نور القمر، وجُمِع بين الشمس والقمر في ذهاب الضوء، فلا ضوء لواحد منهما، يقول الإنسان وقتها: أين المهرب من العذاب؟');
INSERT INTO Quran__Arabic_Tafseer_Muyassar (QuranChapterID, VerseNumber, Rukuh, Sajdah, Manzil, HizbQuarter, VerseText) VALUES (75, 10, '0', '0', '0', '0', 'فإذا تحيَّر البصر ودُهش فزعًا مما رأى من أهوال يوم القيامة، وذهب نور القمر، وجُمِع بين الشمس والقمر في ذهاب الضوء، فلا ضوء لواحد منهما، يقول الإنسان وقتها: أين المهرب من العذاب؟');
INSERT INTO Quran__Arabic_Tafseer_Muyassar (QuranChapterID, VerseNumber, Rukuh, Sajdah, Manzil, HizbQuarter, VerseText) VALUES (75, 11, '0', '0', '0', '0', 'ليس الأمر كما تتمناه- أيها الإنسان- مِن طلب الفرار، لا ملجأ لك ولا منجى. إلى الله وحده مصير الخلائق يوم القيامة ومستقرهم، فيجازي كلا بما يستحق.');
INSERT INTO Quran__Arabic_Tafseer_Muyassar (QuranChapterID, VerseNumber, Rukuh, Sajdah, Manzil, HizbQuarter, VerseText) VALUES (75, 12, '0', '0', '0', '0', 'ليس الأمر كما تتمناه- أيها الإنسان- مِن طلب الفرار، لا ملجأ لك ولا منجى. إلى الله وحده مصير الخلائق يوم القيامة ومستقرهم، فيجازي كلا بما يستحق.');
INSERT INTO Quran__Arabic_Tafseer_Muyassar (QuranChapterID, VerseNumber, Rukuh, Sajdah, Manzil, HizbQuarter, VerseText) VALUES (75, 13, '0', '0', '0', '0', 'يُخَبَّر الإنسان في ذلك اليوم بجميع أعماله: من خير وشر، ما قدَّمه منها في حياته وما أخَّره.');
INSERT INTO Quran__Arabic_Tafseer_Muyassar (QuranChapterID, VerseNumber, Rukuh, Sajdah, Manzil, HizbQuarter, VerseText) VALUES (75, 14, '0', '0', '0', '0', 'بل الإنسان حجة واضحة على نفسه تلزمه بما فعل أو ترك، ولو جاء بكل معذرة يعتذر بها عن إجرامه، فإنه لا ينفعه ذلك.');
INSERT INTO Quran__Arabic_Tafseer_Muyassar (QuranChapterID, VerseNumber, Rukuh, Sajdah, Manzil, HizbQuarter, VerseText) VALUES (75, 15, '0', '0', '0', '0', 'بل الإنسان حجة واضحة على نفسه تلزمه بما فعل أو ترك، ولو جاء بكل معذرة يعتذر بها عن إجرامه، فإنه لا ينفعه ذلك.');
INSERT INTO Quran__Arabic_Tafseer_Muyassar (QuranChapterID, VerseNumber, Rukuh, Sajdah, Manzil, HizbQuarter, VerseText) VALUES (75, 16, '0', '0', '0', '0', 'لا تحرك -أيها النبي- بالقرآن لسانك حين نزول الوحي؛ لأجل أن تتعجل بحفظه، مخافة أن يتفلَّت منك. إن علينا جَمْعه في صدرك، ثم أن تقرأه بلسانك متى شئت. فإذا قرأه عليك رسولنا جبريل فاستمِعْ لقراءته وأنصت له، ثم اقرأه كما أقرأك إياه، ثم إن علينا توضيح ما أشكل عليك فهمه من معانيه وأحكامه.');
INSERT INTO Quran__Arabic_Tafseer_Muyassar (QuranChapterID, VerseNumber, Rukuh, Sajdah, Manzil, HizbQuarter, VerseText) VALUES (75, 17, '0', '0', '0', '0', 'لا تحرك -أيها النبي- بالقرآن لسانك حين نزول الوحي؛ لأجل أن تتعجل بحفظه، مخافة أن يتفلَّت منك. إن علينا جَمْعه في صدرك، ثم أن تقرأه بلسانك متى شئت. فإذا قرأه عليك رسولنا جبريل فاستمِعْ لقراءته وأنصت له، ثم اقرأه كما أقرأك إياه، ثم إن علينا توضيح ما أشكل عليك فهمه من معانيه وأحكامه.');
INSERT INTO Quran__Arabic_Tafseer_Muyassar (QuranChapterID, VerseNumber, Rukuh, Sajdah, Manzil, HizbQuarter, VerseText) VALUES (75, 18, '0', '0', '0', '0', 'لا تحرك -أيها النبي- بالقرآن لسانك حين نزول الوحي؛ لأجل أن تتعجل بحفظه، مخافة أن يتفلَّت منك. إن علينا جَمْعه في صدرك، ثم أن تقرأه بلسانك متى شئت. فإذا قرأه عليك رسولنا جبريل فاستمِعْ لقراءته وأنصت له، ثم اقرأه كما أقرأك إياه، ثم إن علينا توضيح ما أشكل عليك فهمه من معانيه وأحكامه.');
INSERT INTO Quran__Arabic_Tafseer_Muyassar (QuranChapterID, VerseNumber, Rukuh, Sajdah, Manzil, HizbQuarter, VerseText) VALUES (75, 19, '0', '0', '0', '0', 'لا تحرك -أيها النبي- بالقرآن لسانك حين نزول الوحي؛ لأجل أن تتعجل بحفظه، مخافة أن يتفلَّت منك. إن علينا جَمْعه في صدرك، ثم أن تقرأه بلسانك متى شئت. فإذا قرأه عليك رسولنا جبريل فاستمِعْ لقراءته وأنصت له، ثم اقرأه كما أقرأك إياه، ثم إن علينا توضيح ما أشكل عليك فهمه من معانيه وأحكامه.');
INSERT INTO Quran__Arabic_Tafseer_Muyassar (QuranChapterID, VerseNumber, Rukuh, Sajdah, Manzil, HizbQuarter, VerseText) VALUES (75, 20, '0', '0', '0', '0', 'ليس الأمر كما زعمتم- يا معشر المشركين- أن لا بعث ولا جزاء، بل أنتم قوم تحبون الدنيا وزينتها، وتتركون الآخرة ونعيمها.');
INSERT INTO Quran__Arabic_Tafseer_Muyassar (QuranChapterID, VerseNumber, Rukuh, Sajdah, Manzil, HizbQuarter, VerseText) VALUES (75, 21, '0', '0', '0', '0', 'ليس الأمر كما زعمتم- يا معشر المشركين- أن لا بعث ولا جزاء، بل أنتم قوم تحبون الدنيا وزينتها، وتتركون الآخرة ونعيمها.');
INSERT INTO Quran__Arabic_Tafseer_Muyassar (QuranChapterID, VerseNumber, Rukuh, Sajdah, Manzil, HizbQuarter, VerseText) VALUES (75, 22, '0', '0', '0', '0', 'وجوه أهل السعادة يوم القيامة مشرقة حسنة ناعمة، ترى خالقها ومالك أمرها، فتتمتع بذلك.');
INSERT INTO Quran__Arabic_Tafseer_Muyassar (QuranChapterID, VerseNumber, Rukuh, Sajdah, Manzil, HizbQuarter, VerseText) VALUES (75, 23, '0', '0', '0', '0', 'وجوه أهل السعادة يوم القيامة مشرقة حسنة ناعمة، ترى خالقها ومالك أمرها، فتتمتع بذلك.');
INSERT INTO Quran__Arabic_Tafseer_Muyassar (QuranChapterID, VerseNumber, Rukuh, Sajdah, Manzil, HizbQuarter, VerseText) VALUES (75, 24, '0', '0', '0', '0', 'ووجوه الأشقياء يوم القيامة عابسة كالحة، تتوقع أن تنزل بها مصيبة عظيمة، تقصم فَقَار الظَّهْر.');
INSERT INTO Quran__Arabic_Tafseer_Muyassar (QuranChapterID, VerseNumber, Rukuh, Sajdah, Manzil, HizbQuarter, VerseText) VALUES (75, 25, '0', '0', '0', '0', 'ووجوه الأشقياء يوم القيامة عابسة كالحة، تتوقع أن تنزل بها مصيبة عظيمة، تقصم فَقَار الظَّهْر.');
INSERT INTO Quran__Arabic_Tafseer_Muyassar (QuranChapterID, VerseNumber, Rukuh, Sajdah, Manzil, HizbQuarter, VerseText) VALUES (75, 26, '0', '0', '0', '0', 'حقًّا إذا وصلت الروح إلى أعالي الصدر، وقال بعض الحاضرين لبعض: هل مِن راق يَرْقيه ويَشْفيه مما هو فيه؟ وأيقن المحتضر أنَّ الذي نزل به هو فراق الدنيا؛ لمعاينته ملائكة الموت، واتصلت شدة آخر الدنيا بشدة أول الآخرة، إلى الله تعالى مساق العباد يوم القيامة: إما إلى الجنة وإما إلى النار.');
INSERT INTO Quran__Arabic_Tafseer_Muyassar (QuranChapterID, VerseNumber, Rukuh, Sajdah, Manzil, HizbQuarter, VerseText) VALUES (75, 27, '0', '0', '0', '0', 'حقًّا إذا وصلت الروح إلى أعالي الصدر، وقال بعض الحاضرين لبعض: هل مِن راق يَرْقيه ويَشْفيه مما هو فيه؟ وأيقن المحتضر أنَّ الذي نزل به هو فراق الدنيا؛ لمعاينته ملائكة الموت، واتصلت شدة آخر الدنيا بشدة أول الآخرة، إلى الله تعالى مساق العباد يوم القيامة: إما إلى الجنة وإما إلى النار.');
