INSERT INTO Quran__Arabic_Tafseer_Muyassar (QuranChapterID, VerseNumber, Rukuh, Sajdah, Manzil, HizbQuarter, VerseText) VALUES (77, 15, '0', '0', '0', '0', 'فإذا النجوم طُمست وذهب ضياؤها، وإذا السماء تصدَّعت، وإذا الجبال تطايرت وتناثرت وصارت هباء تَذْروه الرياح، وإذا الرسل عُيِّن لهم وقت وأجل للفصل بينهم وبين الأمم، يقال: لأيِّ يوم عظيم أخِّرت الرسل؟ أخِّرت ليوم القضاء والفصل بين الخلائق. وما أعلمك -أيها الإنسان- أيُّ شيء هو يوم الفصل وشدته وهوله؟ هلاك عظيم في ذلك اليوم للمكذبين بهذا اليوم الموعود.');
INSERT INTO Quran__Arabic_Tafseer_Muyassar (QuranChapterID, VerseNumber, Rukuh, Sajdah, Manzil, HizbQuarter, VerseText) VALUES (77, 16, '0', '0', '0', '0', 'ألم نهلك السابقين من الأمم الماضية؛ بتكذيبهم للرسل كقوم نوح وعاد وثمود؟ ثم نلحق بهم المتأخرين ممن كانوا مثلهم في التكذيب والعصيان. مِثل ذلك الإهلاك الفظيع نفعل بهؤلاء المجرمين من كفار "مكة"؛ لتكذيبهم الرسول صلى الله عليه وسلم.');
INSERT INTO Quran__Arabic_Tafseer_Muyassar (QuranChapterID, VerseNumber, Rukuh, Sajdah, Manzil, HizbQuarter, VerseText) VALUES (77, 17, '0', '0', '0', '0', 'ألم نهلك السابقين من الأمم الماضية؛ بتكذيبهم للرسل كقوم نوح وعاد وثمود؟ ثم نلحق بهم المتأخرين ممن كانوا مثلهم في التكذيب والعصيان. مِثل ذلك الإهلاك الفظيع نفعل بهؤلاء المجرمين من كفار "مكة"؛ لتكذيبهم الرسول صلى الله عليه وسلم.');
INSERT INTO Quran__Arabic_Tafseer_Muyassar (QuranChapterID, VerseNumber, Rukuh, Sajdah, Manzil, HizbQuarter, VerseText) VALUES (77, 18, '0', '0', '0', '0', 'ألم نهلك السابقين من الأمم الماضية؛ بتكذيبهم للرسل كقوم نوح وعاد وثمود؟ ثم نلحق بهم المتأخرين ممن كانوا مثلهم في التكذيب والعصيان. مِثل ذلك الإهلاك الفظيع نفعل بهؤلاء المجرمين من كفار "مكة"؛ لتكذيبهم الرسول صلى الله عليه وسلم.');
INSERT INTO Quran__Arabic_Tafseer_Muyassar (QuranChapterID, VerseNumber, Rukuh, Sajdah, Manzil, HizbQuarter, VerseText) VALUES (77, 19, '0', '0', '0', '0', 'هلاك وعذاب شديد يوم القيامة لكل مكذِّب بأن الله هو الإله الحق وحده لا شريك له، والنبوةِ والبعث والحساب.');
INSERT INTO Quran__Arabic_Tafseer_Muyassar (QuranChapterID, VerseNumber, Rukuh, Sajdah, Manzil, HizbQuarter, VerseText) VALUES (77, 20, '0', '0', '0', '0', 'ألم نخلقكم- يا معشر الكفار- من ماء ضعيف حقير وهو النطفة، فجعلنا هذا الماء في مكان حصين، وهو رحم المرأة، إلى وقت محدود ومعلوم عند الله تعالى؟ فقدرنا على خلقه وتصويره وإخراجه، فنعم القادرون نحن.');
INSERT INTO Quran__Arabic_Tafseer_Muyassar (QuranChapterID, VerseNumber, Rukuh, Sajdah, Manzil, HizbQuarter, VerseText) VALUES (77, 21, '0', '0', '0', '0', 'ألم نخلقكم- يا معشر الكفار- من ماء ضعيف حقير وهو النطفة، فجعلنا هذا الماء في مكان حصين، وهو رحم المرأة، إلى وقت محدود ومعلوم عند الله تعالى؟ فقدرنا على خلقه وتصويره وإخراجه، فنعم القادرون نحن.');
INSERT INTO Quran__Arabic_Tafseer_Muyassar (QuranChapterID, VerseNumber, Rukuh, Sajdah, Manzil, HizbQuarter, VerseText) VALUES (77, 22, '0', '0', '0', '0', 'ألم نخلقكم- يا معشر الكفار- من ماء ضعيف حقير وهو النطفة، فجعلنا هذا الماء في مكان حصين، وهو رحم المرأة، إلى وقت محدود ومعلوم عند الله تعالى؟ فقدرنا على خلقه وتصويره وإخراجه، فنعم القادرون نحن.');
INSERT INTO Quran__Arabic_Tafseer_Muyassar (QuranChapterID, VerseNumber, Rukuh, Sajdah, Manzil, HizbQuarter, VerseText) VALUES (77, 23, '0', '0', '0', '0', 'ألم نخلقكم- يا معشر الكفار- من ماء ضعيف حقير وهو النطفة، فجعلنا هذا الماء في مكان حصين، وهو رحم المرأة، إلى وقت محدود ومعلوم عند الله تعالى؟ فقدرنا على خلقه وتصويره وإخراجه، فنعم القادرون نحن.');
INSERT INTO Quran__Arabic_Tafseer_Muyassar (QuranChapterID, VerseNumber, Rukuh, Sajdah, Manzil, HizbQuarter, VerseText) VALUES (77, 24, '0', '0', '0', '0', 'هلاك وعذاب شديد يوم القيامة للمكذبين بقدرتنا.');
INSERT INTO Quran__Arabic_Tafseer_Muyassar (QuranChapterID, VerseNumber, Rukuh, Sajdah, Manzil, HizbQuarter, VerseText) VALUES (77, 25, '0', '0', '0', '0', 'ألم نجعل هذه الأرض التي تعيشون عليها، تضم على ظهرها أحياء لا يحصون، وفي بطنها أمواتًا لا يحصرون، وجعلنا فيها جبالا ثوابت عاليات؛ لئلا تضطرب بكم، وأسقيناكم ماءً عذبًا سائغًا؟');
INSERT INTO Quran__Arabic_Tafseer_Muyassar (QuranChapterID, VerseNumber, Rukuh, Sajdah, Manzil, HizbQuarter, VerseText) VALUES (77, 26, '0', '0', '0', '0', 'ألم نجعل هذه الأرض التي تعيشون عليها، تضم على ظهرها أحياء لا يحصون، وفي بطنها أمواتًا لا يحصرون، وجعلنا فيها جبالا ثوابت عاليات؛ لئلا تضطرب بكم، وأسقيناكم ماءً عذبًا سائغًا؟');
INSERT INTO Quran__Arabic_Tafseer_Muyassar (QuranChapterID, VerseNumber, Rukuh, Sajdah, Manzil, HizbQuarter, VerseText) VALUES (77, 27, '0', '0', '0', '0', 'ألم نجعل هذه الأرض التي تعيشون عليها، تضم على ظهرها أحياء لا يحصون، وفي بطنها أمواتًا لا يحصرون، وجعلنا فيها جبالا ثوابت عاليات؛ لئلا تضطرب بكم، وأسقيناكم ماءً عذبًا سائغًا؟');
INSERT INTO Quran__Arabic_Tafseer_Muyassar (QuranChapterID, VerseNumber, Rukuh, Sajdah, Manzil, HizbQuarter, VerseText) VALUES (77, 28, '0', '0', '0', '0', 'هلاك ودمار يوم القيامة للمكذبين بهذه النعم.');
INSERT INTO Quran__Arabic_Tafseer_Muyassar (QuranChapterID, VerseNumber, Rukuh, Sajdah, Manzil, HizbQuarter, VerseText) VALUES (77, 29, '0', '0', '0', '0', 'يقال للكافرين يوم القيامة: سيروا إلى عذاب جهنم الذي كنتم به تكذبون في الدنيا، سيروا، فاستظلوا بدخان جهنم يتفرع منه ثلاث قطع، لا يُظِل ذلك الظل من حر ذلك اليوم، ولا يدفع من حر اللهب شيئًا. إن جهنم تقذف من النار بشرر عظيم، كل شرارة منه كالبناء المشيد في العِظم والارتفاع. كأن شرر جهنم المتطاير منها إبل سود يميل لونها إلى الصُّفْرة.');
INSERT INTO Quran__Arabic_Tafseer_Muyassar (QuranChapterID, VerseNumber, Rukuh, Sajdah, Manzil, HizbQuarter, VerseText) VALUES (77, 30, '0', '0', '0', '0', 'يقال للكافرين يوم القيامة: سيروا إلى عذاب جهنم الذي كنتم به تكذبون في الدنيا، سيروا، فاستظلوا بدخان جهنم يتفرع منه ثلاث قطع، لا يُظِل ذلك الظل من حر ذلك اليوم، ولا يدفع من حر اللهب شيئًا. إن جهنم تقذف من النار بشرر عظيم، كل شرارة منه كالبناء المشيد في العِظم والارتفاع. كأن شرر جهنم المتطاير منها إبل سود يميل لونها إلى الصُّفْرة.');
INSERT INTO Quran__Arabic_Tafseer_Muyassar (QuranChapterID, VerseNumber, Rukuh, Sajdah, Manzil, HizbQuarter, VerseText) VALUES (77, 31, '0', '0', '0', '0', 'يقال للكافرين يوم القيامة: سيروا إلى عذاب جهنم الذي كنتم به تكذبون في الدنيا، سيروا، فاستظلوا بدخان جهنم يتفرع منه ثلاث قطع، لا يُظِل ذلك الظل من حر ذلك اليوم، ولا يدفع من حر اللهب شيئًا. إن جهنم تقذف من النار بشرر عظيم، كل شرارة منه كالبناء المشيد في العِظم والارتفاع. كأن شرر جهنم المتطاير منها إبل سود يميل لونها إلى الصُّفْرة.');
INSERT INTO Quran__Arabic_Tafseer_Muyassar (QuranChapterID, VerseNumber, Rukuh, Sajdah, Manzil, HizbQuarter, VerseText) VALUES (77, 32, '0', '0', '0', '0', 'يقال للكافرين يوم القيامة: سيروا إلى عذاب جهنم الذي كنتم به تكذبون في الدنيا، سيروا، فاستظلوا بدخان جهنم يتفرع منه ثلاث قطع، لا يُظِل ذلك الظل من حر ذلك اليوم، ولا يدفع من حر اللهب شيئًا. إن جهنم تقذف من النار بشرر عظيم، كل شرارة منه كالبناء المشيد في العِظم والارتفاع. كأن شرر جهنم المتطاير منها إبل سود يميل لونها إلى الصُّفْرة.');
INSERT INTO Quran__Arabic_Tafseer_Muyassar (QuranChapterID, VerseNumber, Rukuh, Sajdah, Manzil, HizbQuarter, VerseText) VALUES (77, 33, '0', '0', '0', '0', 'يقال للكافرين يوم القيامة: سيروا إلى عذاب جهنم الذي كنتم به تكذبون في الدنيا، سيروا، فاستظلوا بدخان جهنم يتفرع منه ثلاث قطع، لا يُظِل ذلك الظل من حر ذلك اليوم، ولا يدفع من حر اللهب شيئًا. إن جهنم تقذف من النار بشرر عظيم، كل شرارة منه كالبناء المشيد في العِظم والارتفاع. كأن شرر جهنم المتطاير منها إبل سود يميل لونها إلى الصُّفْرة.');
INSERT INTO Quran__Arabic_Tafseer_Muyassar (QuranChapterID, VerseNumber, Rukuh, Sajdah, Manzil, HizbQuarter, VerseText) VALUES (77, 34, '0', '0', '0', '0', 'هلاك وعذاب شديد يوم القيامة للمكذبين بوعيد الله.');
INSERT INTO Quran__Arabic_Tafseer_Muyassar (QuranChapterID, VerseNumber, Rukuh, Sajdah, Manzil, HizbQuarter, VerseText) VALUES (77, 35, '0', '0', '0', '0', 'هذا يوم القيامة الذي لا ينطق فيه المكذبون بكلام ينفعهم، ولا يكون لهم إذن في الكلام فيعتذرون؛ لأنه لا عذر لهم.');
INSERT INTO Quran__Arabic_Tafseer_Muyassar (QuranChapterID, VerseNumber, Rukuh, Sajdah, Manzil, HizbQuarter, VerseText) VALUES (77, 36, '0', '0', '0', '0', 'هذا يوم القيامة الذي لا ينطق فيه المكذبون بكلام ينفعهم، ولا يكون لهم إذن في الكلام فيعتذرون؛ لأنه لا عذر لهم.');
INSERT INTO Quran__Arabic_Tafseer_Muyassar (QuranChapterID, VerseNumber, Rukuh, Sajdah, Manzil, HizbQuarter, VerseText) VALUES (77, 37, '0', '0', '0', '0', 'هلاك وعذاب شديد يومئذ للمكذبين بهذا اليوم وما فيه.');
INSERT INTO Quran__Arabic_Tafseer_Muyassar (QuranChapterID, VerseNumber, Rukuh, Sajdah, Manzil, HizbQuarter, VerseText) VALUES (77, 38, '0', '0', '0', '0', 'هذا يوم يفصل الله فيه بين الخلائق، ويتميز فيه الحق من الباطل، جمعناكم فيه -يا معشر كفار هذه الأمة- مع الكفار الأولين من الأمم الماضية، فإن كان لكم حيلة في الخلاص من العذاب فاحتالوا، وأنقذوا أنفسكم مِن بطش الله وانتقامه.');
INSERT INTO Quran__Arabic_Tafseer_Muyassar (QuranChapterID, VerseNumber, Rukuh, Sajdah, Manzil, HizbQuarter, VerseText) VALUES (77, 39, '0', '0', '0', '0', 'هذا يوم يفصل الله فيه بين الخلائق، ويتميز فيه الحق من الباطل، جمعناكم فيه -يا معشر كفار هذه الأمة- مع الكفار الأولين من الأمم الماضية، فإن كان لكم حيلة في الخلاص من العذاب فاحتالوا، وأنقذوا أنفسكم مِن بطش الله وانتقامه.');
INSERT INTO Quran__Arabic_Tafseer_Muyassar (QuranChapterID, VerseNumber, Rukuh, Sajdah, Manzil, HizbQuarter, VerseText) VALUES (77, 40, '1', '0', '0', '0', 'هلاك ودمار يوم القيامة للمكذبين بيوم القيامة.');
INSERT INTO Quran__Arabic_Tafseer_Muyassar (QuranChapterID, VerseNumber, Rukuh, Sajdah, Manzil, HizbQuarter, VerseText) VALUES (77, 41, '0', '0', '0', '0', 'إن الذين خافوا ربهم في الدنيا، واتقوا عذابه بامتثال أوامره واجتناب نواهيه، هم يوم القيامة في ظلال الأشجار الوارفة وعيون الماء الجارية، وفواكه كثيرة مما تشتهيه أنفسهم يتنعمون. يقال لهم: كلوا أكلا لذيذًا، واشربوا شربًا هنيئًا؛ بسبب ما قدمتم في الدنيا من صالح الأعمال. إنا بمثل ذلك الجزاء العظيم نجزي أهل الإحسان في أعمالهم وطاعتهم لنا. هلاك وعذاب شديد يوم القيامة للمكذبين بيوم الجزاء والحساب وما فيه من النعيم والعذاب.');
INSERT INTO Quran__Arabic_Tafseer_Muyassar (QuranChapterID, VerseNumber, Rukuh, Sajdah, Manzil, HizbQuarter, VerseText) VALUES (77, 42, '0', '0', '0', '0', 'إن الذين خافوا ربهم في الدنيا، واتقوا عذابه بامتثال أوامره واجتناب نواهيه، هم يوم القيامة في ظلال الأشجار الوارفة وعيون الماء الجارية، وفواكه كثيرة مما تشتهيه أنفسهم يتنعمون. يقال لهم: كلوا أكلا لذيذًا، واشربوا شربًا هنيئًا؛ بسبب ما قدمتم في الدنيا من صالح الأعمال. إنا بمثل ذلك الجزاء العظيم نجزي أهل الإحسان في أعمالهم وطاعتهم لنا. هلاك وعذاب شديد يوم القيامة للمكذبين بيوم الجزاء والحساب وما فيه من النعيم والعذاب.');
INSERT INTO Quran__Arabic_Tafseer_Muyassar (QuranChapterID, VerseNumber, Rukuh, Sajdah, Manzil, HizbQuarter, VerseText) VALUES (77, 43, '0', '0', '0', '0', 'إن الذين خافوا ربهم في الدنيا، واتقوا عذابه بامتثال أوامره واجتناب نواهيه، هم يوم القيامة في ظلال الأشجار الوارفة وعيون الماء الجارية، وفواكه كثيرة مما تشتهيه أنفسهم يتنعمون. يقال لهم: كلوا أكلا لذيذًا، واشربوا شربًا هنيئًا؛ بسبب ما قدمتم في الدنيا من صالح الأعمال. إنا بمثل ذلك الجزاء العظيم نجزي أهل الإحسان في أعمالهم وطاعتهم لنا. هلاك وعذاب شديد يوم القيامة للمكذبين بيوم الجزاء والحساب وما فيه من النعيم والعذاب.');
INSERT INTO Quran__Arabic_Tafseer_Muyassar (QuranChapterID, VerseNumber, Rukuh, Sajdah, Manzil, HizbQuarter, VerseText) VALUES (77, 44, '0', '0', '0', '0', 'إن الذين خافوا ربهم في الدنيا، واتقوا عذابه بامتثال أوامره واجتناب نواهيه، هم يوم القيامة في ظلال الأشجار الوارفة وعيون الماء الجارية، وفواكه كثيرة مما تشتهيه أنفسهم يتنعمون. يقال لهم: كلوا أكلا لذيذًا، واشربوا شربًا هنيئًا؛ بسبب ما قدمتم في الدنيا من صالح الأعمال. إنا بمثل ذلك الجزاء العظيم نجزي أهل الإحسان في أعمالهم وطاعتهم لنا. هلاك وعذاب شديد يوم القيامة للمكذبين بيوم الجزاء والحساب وما فيه من النعيم والعذاب.');
INSERT INTO Quran__Arabic_Tafseer_Muyassar (QuranChapterID, VerseNumber, Rukuh, Sajdah, Manzil, HizbQuarter, VerseText) VALUES (77, 45, '0', '0', '0', '0', 'إن الذين خافوا ربهم في الدنيا، واتقوا عذابه بامتثال أوامره واجتناب نواهيه، هم يوم القيامة في ظلال الأشجار الوارفة وعيون الماء الجارية، وفواكه كثيرة مما تشتهيه أنفسهم يتنعمون. يقال لهم: كلوا أكلا لذيذًا، واشربوا شربًا هنيئًا؛ بسبب ما قدمتم في الدنيا من صالح الأعمال. إنا بمثل ذلك الجزاء العظيم نجزي أهل الإحسان في أعمالهم وطاعتهم لنا. هلاك وعذاب شديد يوم القيامة للمكذبين بيوم الجزاء والحساب وما فيه من النعيم والعذاب.');
INSERT INTO Quran__Arabic_Tafseer_Muyassar (QuranChapterID, VerseNumber, Rukuh, Sajdah, Manzil, HizbQuarter, VerseText) VALUES (77, 46, '0', '0', '0', '0', 'ثم هدَّد الله الكافرين فقال: كلوا من لذائذ الدنيا، واستمتعوا بشهواتها الفانية زمنًا قليلا؛ إنكم مجرمون بإشراككم بالله.');
INSERT INTO Quran__Arabic_Tafseer_Muyassar (QuranChapterID, VerseNumber, Rukuh, Sajdah, Manzil, HizbQuarter, VerseText) VALUES (77, 47, '0', '0', '0', '0', 'هلاك وعذاب شديد يوم القيامة للمكذبين بيوم الحساب والجزاء.');
INSERT INTO Quran__Arabic_Tafseer_Muyassar (QuranChapterID, VerseNumber, Rukuh, Sajdah, Manzil, HizbQuarter, VerseText) VALUES (77, 48, '0', '0', '0', '0', 'وإذا قيل لهؤلاء المشركين: صلُّوا لله، واخشعوا له، لا يخشعون ولا يصلُّون، بل يصرُّون على استكبارهم.');
INSERT INTO Quran__Arabic_Tafseer_Muyassar (QuranChapterID, VerseNumber, Rukuh, Sajdah, Manzil, HizbQuarter, VerseText) VALUES (77, 49, '0', '0', '0', '0', 'هلاك وعذاب شديد يوم القيامة للمكذبين بآيات الله، إن لم يؤمنوا بهذا القرآن، فبأي كتاب وكلام بعده يؤمنون؟ وهو المبيِّن لكل شيء، الواضح في حكمه وأحكامه وأخباره، المعجز في ألفاظه ومعانيه.');
INSERT INTO Quran__Arabic_Tafseer_Muyassar (QuranChapterID, VerseNumber, Rukuh, Sajdah, Manzil, HizbQuarter, VerseText) VALUES (77, 50, '1', '0', '0', '0', 'هلاك وعذاب شديد يوم القيامة للمكذبين بآيات الله، إن لم يؤمنوا بهذا القرآن، فبأي كتاب وكلام بعده يؤمنون؟ وهو المبيِّن لكل شيء، الواضح في حكمه وأحكامه وأخباره، المعجز في ألفاظه ومعانيه.');
INSERT INTO Quran__Arabic_Tafseer_Muyassar (QuranChapterID, VerseNumber, Rukuh, Sajdah, Manzil, HizbQuarter, VerseText) VALUES (78, 1, '0', '0', '0', '1', 'عن أيِّ شيء يسأل بعض كفار قريش بعضا؟ يتساءلون عن الخبر العظيم الشأن، وهو القرآن العظيم الذي ينبئ عن البعث الذي شك فيه كفار قريش وكذَّبوا به.');
INSERT INTO Quran__Arabic_Tafseer_Muyassar (QuranChapterID, VerseNumber, Rukuh, Sajdah, Manzil, HizbQuarter, VerseText) VALUES (78, 2, '0', '0', '0', '0', 'عن أيِّ شيء يسأل بعض كفار قريش بعضا؟ يتساءلون عن الخبر العظيم الشأن، وهو القرآن العظيم الذي ينبئ عن البعث الذي شك فيه كفار قريش وكذَّبوا به.');
INSERT INTO Quran__Arabic_Tafseer_Muyassar (QuranChapterID, VerseNumber, Rukuh, Sajdah, Manzil, HizbQuarter, VerseText) VALUES (78, 3, '0', '0', '0', '0', 'عن أيِّ شيء يسأل بعض كفار قريش بعضا؟ يتساءلون عن الخبر العظيم الشأن، وهو القرآن العظيم الذي ينبئ عن البعث الذي شك فيه كفار قريش وكذَّبوا به.');
INSERT INTO Quran__Arabic_Tafseer_Muyassar (QuranChapterID, VerseNumber, Rukuh, Sajdah, Manzil, HizbQuarter, VerseText) VALUES (78, 4, '0', '0', '0', '0', 'ما الأمر كما يزعم هؤلاء المشركون، سيعلم هؤلاء المشركون عاقبة تكذيبهم، ويظهر لهم ما الله فاعل بهم يوم القيامة، ثم سيتأكد لهم ذلك، ويتأكد لهم صدق ما جاء به محمد صلى الله عليه وسلم، من القرآن والبعث. وهذا تهديد ووعيد لهم.');
INSERT INTO Quran__Arabic_Tafseer_Muyassar (QuranChapterID, VerseNumber, Rukuh, Sajdah, Manzil, HizbQuarter, VerseText) VALUES (78, 5, '0', '0', '0', '0', 'ما الأمر كما يزعم هؤلاء المشركون، سيعلم هؤلاء المشركون عاقبة تكذيبهم، ويظهر لهم ما الله فاعل بهم يوم القيامة، ثم سيتأكد لهم ذلك، ويتأكد لهم صدق ما جاء به محمد صلى الله عليه وسلم، من القرآن والبعث. وهذا تهديد ووعيد لهم.');
INSERT INTO Quran__Arabic_Tafseer_Muyassar (QuranChapterID, VerseNumber, Rukuh, Sajdah, Manzil, HizbQuarter, VerseText) VALUES (78, 6, '0', '0', '0', '0', 'ألم نجعل الأرض ممهدة لكم كالفراش؟');
INSERT INTO Quran__Arabic_Tafseer_Muyassar (QuranChapterID, VerseNumber, Rukuh, Sajdah, Manzil, HizbQuarter, VerseText) VALUES (78, 7, '0', '0', '0', '0', 'والجبال رواسي؛ كي لا تتحرك بكم الأرض؟');
INSERT INTO Quran__Arabic_Tafseer_Muyassar (QuranChapterID, VerseNumber, Rukuh, Sajdah, Manzil, HizbQuarter, VerseText) VALUES (78, 8, '0', '0', '0', '0', 'وخلقناكم أصنافا ذكرا وأنثى؟');
INSERT INTO Quran__Arabic_Tafseer_Muyassar (QuranChapterID, VerseNumber, Rukuh, Sajdah, Manzil, HizbQuarter, VerseText) VALUES (78, 9, '0', '0', '0', '0', 'وجعلنا نومكم راحة لأبدانكم، فيه تهدؤون وتسكنون؟');
INSERT INTO Quran__Arabic_Tafseer_Muyassar (QuranChapterID, VerseNumber, Rukuh, Sajdah, Manzil, HizbQuarter, VerseText) VALUES (78, 10, '0', '0', '0', '0', 'وجعلنا الليل لباسًا تَلْبَسكم ظلمته وتغشاكم، كما يستر الثوب لابسه؟');
INSERT INTO Quran__Arabic_Tafseer_Muyassar (QuranChapterID, VerseNumber, Rukuh, Sajdah, Manzil, HizbQuarter, VerseText) VALUES (78, 11, '0', '0', '0', '0', 'وجعلنا النهار معاشا تنتشرون فيه لمعاشكم، وتسعَون فيه لمصالحكم؟');
INSERT INTO Quran__Arabic_Tafseer_Muyassar (QuranChapterID, VerseNumber, Rukuh, Sajdah, Manzil, HizbQuarter, VerseText) VALUES (78, 12, '0', '0', '0', '0', 'وبنينا فوقكم سبع سموات متينة البناء محكمة الخلق، لا صدوع لها ولا فطور؟');
INSERT INTO Quran__Arabic_Tafseer_Muyassar (QuranChapterID, VerseNumber, Rukuh, Sajdah, Manzil, HizbQuarter, VerseText) VALUES (78, 13, '0', '0', '0', '0', 'وجعلنا الشمس سراجًا وقَّادًا مضيئًا؟');
INSERT INTO Quran__Arabic_Tafseer_Muyassar (QuranChapterID, VerseNumber, Rukuh, Sajdah, Manzil, HizbQuarter, VerseText) VALUES (78, 14, '0', '0', '0', '0', 'وأنزلنا من السحب الممطرة ماء منصَبّا بكثرة، لنخرج به حبًا مما يقتات به الناس وحشائش مما تأكله الدَّواب، وبساتين ملتفة بعضها ببعض لتشعب أغصانها؟');
INSERT INTO Quran__Arabic_Tafseer_Muyassar (QuranChapterID, VerseNumber, Rukuh, Sajdah, Manzil, HizbQuarter, VerseText) VALUES (78, 15, '0', '0', '0', '0', 'وأنزلنا من السحب الممطرة ماء منصَبّا بكثرة، لنخرج به حبًا مما يقتات به الناس وحشائش مما تأكله الدَّواب، وبساتين ملتفة بعضها ببعض لتشعب أغصانها؟');
INSERT INTO Quran__Arabic_Tafseer_Muyassar (QuranChapterID, VerseNumber, Rukuh, Sajdah, Manzil, HizbQuarter, VerseText) VALUES (78, 16, '0', '0', '0', '0', 'وأنزلنا من السحب الممطرة ماء منصَبّا بكثرة، لنخرج به حبًا مما يقتات به الناس وحشائش مما تأكله الدَّواب، وبساتين ملتفة بعضها ببعض لتشعب أغصانها؟');
INSERT INTO Quran__Arabic_Tafseer_Muyassar (QuranChapterID, VerseNumber, Rukuh, Sajdah, Manzil, HizbQuarter, VerseText) VALUES (78, 17, '0', '0', '0', '0', 'إن يوم الفصل بين الخلق، وهو يوم القيامة، كان وقتًا وميعادًا محددًا للأولين والآخرين، يوم ينفخ المَلَك في "القرن" إيذانًا بالبعث فتأتون أممًا، كل أمة مع إمامهم.');
INSERT INTO Quran__Arabic_Tafseer_Muyassar (QuranChapterID, VerseNumber, Rukuh, Sajdah, Manzil, HizbQuarter, VerseText) VALUES (78, 18, '0', '0', '0', '0', 'إن يوم الفصل بين الخلق، وهو يوم القيامة، كان وقتًا وميعادًا محددًا للأولين والآخرين، يوم ينفخ المَلَك في "القرن" إيذانًا بالبعث فتأتون أممًا، كل أمة مع إمامهم.');
INSERT INTO Quran__Arabic_Tafseer_Muyassar (QuranChapterID, VerseNumber, Rukuh, Sajdah, Manzil, HizbQuarter, VerseText) VALUES (78, 19, '0', '0', '0', '0', 'وفُتحت السماء، فكانت ذات أبواب كثيرة لنزول الملائكة.');
INSERT INTO Quran__Arabic_Tafseer_Muyassar (QuranChapterID, VerseNumber, Rukuh, Sajdah, Manzil, HizbQuarter, VerseText) VALUES (78, 20, '0', '0', '0', '0', 'ونسفت الجبال بعد ثبوتها، فكانت كالسراب.');
INSERT INTO Quran__Arabic_Tafseer_Muyassar (QuranChapterID, VerseNumber, Rukuh, Sajdah, Manzil, HizbQuarter, VerseText) VALUES (78, 21, '0', '0', '0', '0', 'إن جهنم كانت يومئذ ترصد أهل الكفر الذين أُعِدَّت لهم، للكافرين مرجعًا، ماكثين فيها دهورًا متعاقبة لا تنقطع، لا يَطْعَمون فيها ما يُبْرد حرَّ السعير عنهم، ولا شرابًا يرويهم، إلا ماءً حارًا، وصديد أهل النار، يجازَون بذلك جزاء عادلا موافقًا لأعمالهم التي كانوا يعملونها في الدنيا.');
INSERT INTO Quran__Arabic_Tafseer_Muyassar (QuranChapterID, VerseNumber, Rukuh, Sajdah, Manzil, HizbQuarter, VerseText) VALUES (78, 22, '0', '0', '0', '0', 'إن جهنم كانت يومئذ ترصد أهل الكفر الذين أُعِدَّت لهم، للكافرين مرجعًا، ماكثين فيها دهورًا متعاقبة لا تنقطع، لا يَطْعَمون فيها ما يُبْرد حرَّ السعير عنهم، ولا شرابًا يرويهم، إلا ماءً حارًا، وصديد أهل النار، يجازَون بذلك جزاء عادلا موافقًا لأعمالهم التي كانوا يعملونها في الدنيا.');
