INSERT INTO Quran__Arabic_Tafseer_Muyassar (QuranChapterID, VerseNumber, Rukuh, Sajdah, Manzil, HizbQuarter, VerseText) VALUES (79, 8, '0', '0', '0', '0', 'قلوب الكفار يومئذ مضطربة من شدة الخوف، أبصار أصحابها ذليلة من هول ما ترى.');
INSERT INTO Quran__Arabic_Tafseer_Muyassar (QuranChapterID, VerseNumber, Rukuh, Sajdah, Manzil, HizbQuarter, VerseText) VALUES (79, 9, '0', '0', '0', '0', 'قلوب الكفار يومئذ مضطربة من شدة الخوف، أبصار أصحابها ذليلة من هول ما ترى.');
INSERT INTO Quran__Arabic_Tafseer_Muyassar (QuranChapterID, VerseNumber, Rukuh, Sajdah, Manzil, HizbQuarter, VerseText) VALUES (79, 10, '0', '0', '0', '0', 'يقول هؤلاء المكذبون بالبعث: أنُرَدُّ بعد موتنا إلى ما كنا عليه أحياء في الأرض؟ أنردُّ وقد صرنا عظامًا بالية؟ قالوا: رجعتنا تلك ستكون إذًا خائبة كاذبة.');
INSERT INTO Quran__Arabic_Tafseer_Muyassar (QuranChapterID, VerseNumber, Rukuh, Sajdah, Manzil, HizbQuarter, VerseText) VALUES (79, 11, '0', '0', '0', '0', 'يقول هؤلاء المكذبون بالبعث: أنُرَدُّ بعد موتنا إلى ما كنا عليه أحياء في الأرض؟ أنردُّ وقد صرنا عظامًا بالية؟ قالوا: رجعتنا تلك ستكون إذًا خائبة كاذبة.');
INSERT INTO Quran__Arabic_Tafseer_Muyassar (QuranChapterID, VerseNumber, Rukuh, Sajdah, Manzil, HizbQuarter, VerseText) VALUES (79, 12, '0', '0', '0', '0', 'يقول هؤلاء المكذبون بالبعث: أنُرَدُّ بعد موتنا إلى ما كنا عليه أحياء في الأرض؟ أنردُّ وقد صرنا عظامًا بالية؟ قالوا: رجعتنا تلك ستكون إذًا خائبة كاذبة.');
INSERT INTO Quran__Arabic_Tafseer_Muyassar (QuranChapterID, VerseNumber, Rukuh, Sajdah, Manzil, HizbQuarter, VerseText) VALUES (79, 13, '0', '0', '0', '0', 'فإنما هي نفخة واحدة، فإذا هم أحياء على وجه الأرض بعد أن كانوا في بطنها.');
INSERT INTO Quran__Arabic_Tafseer_Muyassar (QuranChapterID, VerseNumber, Rukuh, Sajdah, Manzil, HizbQuarter, VerseText) VALUES (79, 14, '0', '0', '0', '0', 'فإنما هي نفخة واحدة، فإذا هم أحياء على وجه الأرض بعد أن كانوا في بطنها.');
INSERT INTO Quran__Arabic_Tafseer_Muyassar (QuranChapterID, VerseNumber, Rukuh, Sajdah, Manzil, HizbQuarter, VerseText) VALUES (79, 15, '0', '0', '0', '0', 'هل أتاك -أيها الرسول- خبر موسى؟');
INSERT INTO Quran__Arabic_Tafseer_Muyassar (QuranChapterID, VerseNumber, Rukuh, Sajdah, Manzil, HizbQuarter, VerseText) VALUES (79, 16, '0', '0', '0', '0', 'حين ناداه ربه بالوادي المطهَّر المبارك "طوى"، فقال له: اذهب إلى فرعون، إنه قد أفرط في العصيان، فقل له: أتودُّ أن تطهِّر نفسك من النقائص وتحليها بالإيمان، وأُرشدك إلى طاعة ربك، فتخشاه وتتقيه؟');
INSERT INTO Quran__Arabic_Tafseer_Muyassar (QuranChapterID, VerseNumber, Rukuh, Sajdah, Manzil, HizbQuarter, VerseText) VALUES (79, 17, '0', '0', '0', '0', 'حين ناداه ربه بالوادي المطهَّر المبارك "طوى"، فقال له: اذهب إلى فرعون، إنه قد أفرط في العصيان، فقل له: أتودُّ أن تطهِّر نفسك من النقائص وتحليها بالإيمان، وأُرشدك إلى طاعة ربك، فتخشاه وتتقيه؟');
INSERT INTO Quran__Arabic_Tafseer_Muyassar (QuranChapterID, VerseNumber, Rukuh, Sajdah, Manzil, HizbQuarter, VerseText) VALUES (79, 18, '0', '0', '0', '0', 'حين ناداه ربه بالوادي المطهَّر المبارك "طوى"، فقال له: اذهب إلى فرعون، إنه قد أفرط في العصيان، فقل له: أتودُّ أن تطهِّر نفسك من النقائص وتحليها بالإيمان، وأُرشدك إلى طاعة ربك، فتخشاه وتتقيه؟');
INSERT INTO Quran__Arabic_Tafseer_Muyassar (QuranChapterID, VerseNumber, Rukuh, Sajdah, Manzil, HizbQuarter, VerseText) VALUES (79, 19, '0', '0', '0', '0', 'حين ناداه ربه بالوادي المطهَّر المبارك "طوى"، فقال له: اذهب إلى فرعون، إنه قد أفرط في العصيان، فقل له: أتودُّ أن تطهِّر نفسك من النقائص وتحليها بالإيمان، وأُرشدك إلى طاعة ربك، فتخشاه وتتقيه؟');
INSERT INTO Quran__Arabic_Tafseer_Muyassar (QuranChapterID, VerseNumber, Rukuh, Sajdah, Manzil, HizbQuarter, VerseText) VALUES (79, 20, '0', '0', '0', '0', 'فأرى موسى فرعونَ العلامة العظمى: العصا واليد، فكذب فرعون نبيَّ الله موسى عليه السلام، وعصى ربه عزَّ وجلَّ، ثم ولَّى معرضًا عن الإيمان مجتهدًا في معارضة موسى.');
INSERT INTO Quran__Arabic_Tafseer_Muyassar (QuranChapterID, VerseNumber, Rukuh, Sajdah, Manzil, HizbQuarter, VerseText) VALUES (79, 21, '0', '0', '0', '0', 'فأرى موسى فرعونَ العلامة العظمى: العصا واليد، فكذب فرعون نبيَّ الله موسى عليه السلام، وعصى ربه عزَّ وجلَّ، ثم ولَّى معرضًا عن الإيمان مجتهدًا في معارضة موسى.');
INSERT INTO Quran__Arabic_Tafseer_Muyassar (QuranChapterID, VerseNumber, Rukuh, Sajdah, Manzil, HizbQuarter, VerseText) VALUES (79, 22, '0', '0', '0', '0', 'فأرى موسى فرعونَ العلامة العظمى: العصا واليد، فكذب فرعون نبيَّ الله موسى عليه السلام، وعصى ربه عزَّ وجلَّ، ثم ولَّى معرضًا عن الإيمان مجتهدًا في معارضة موسى.');
INSERT INTO Quran__Arabic_Tafseer_Muyassar (QuranChapterID, VerseNumber, Rukuh, Sajdah, Manzil, HizbQuarter, VerseText) VALUES (79, 23, '0', '0', '0', '0', 'فجمع أهل مملكته وناداهم، فقال: أنا ربكم الذي لا ربَّ فوقه، فانتقم الله منه بالعذاب في الدنيا والآخرة، وجعله عبرة ونكالا لأمثاله من المتمردين. إن في فرعون وما نزل به من العذاب لموعظةً لمن يتعظ وينزجر.');
INSERT INTO Quran__Arabic_Tafseer_Muyassar (QuranChapterID, VerseNumber, Rukuh, Sajdah, Manzil, HizbQuarter, VerseText) VALUES (79, 24, '0', '0', '0', '0', 'فجمع أهل مملكته وناداهم، فقال: أنا ربكم الذي لا ربَّ فوقه، فانتقم الله منه بالعذاب في الدنيا والآخرة، وجعله عبرة ونكالا لأمثاله من المتمردين. إن في فرعون وما نزل به من العذاب لموعظةً لمن يتعظ وينزجر.');
INSERT INTO Quran__Arabic_Tafseer_Muyassar (QuranChapterID, VerseNumber, Rukuh, Sajdah, Manzil, HizbQuarter, VerseText) VALUES (79, 25, '0', '0', '0', '0', 'فجمع أهل مملكته وناداهم، فقال: أنا ربكم الذي لا ربَّ فوقه، فانتقم الله منه بالعذاب في الدنيا والآخرة، وجعله عبرة ونكالا لأمثاله من المتمردين. إن في فرعون وما نزل به من العذاب لموعظةً لمن يتعظ وينزجر.');
INSERT INTO Quran__Arabic_Tafseer_Muyassar (QuranChapterID, VerseNumber, Rukuh, Sajdah, Manzil, HizbQuarter, VerseText) VALUES (79, 26, '1', '0', '0', '0', 'فجمع أهل مملكته وناداهم، فقال: أنا ربكم الذي لا ربَّ فوقه، فانتقم الله منه بالعذاب في الدنيا والآخرة، وجعله عبرة ونكالا لأمثاله من المتمردين. إن في فرعون وما نزل به من العذاب لموعظةً لمن يتعظ وينزجر.');
INSERT INTO Quran__Arabic_Tafseer_Muyassar (QuranChapterID, VerseNumber, Rukuh, Sajdah, Manzil, HizbQuarter, VerseText) VALUES (79, 27, '0', '0', '0', '0', 'أبَعْثُكم أيها الناس- بعد الموت أشد في تقديركم أم خلق السماء؟ رفعها فوقكم كالبناء، وأعلى سقفها في الهواء لا تفاوت فيها ولا فطور، وأظلم ليلها بغروب شمسها، وأبرز نهارها بشروقها. والأرض بعد خلق السماء بسطها، وأودع فيها منافعها، وفجَّر فيها عيون الماء، وأنبت فيها ما يُرعى من النباتات، وأثبت فيها الجبال أوتادًا لها. خلق سبحانه كل هذه النعم منفعة لكم ولأنعامكم. (إن إعادة خلقكم يوم القيامة أهون على الله من خلق هذه الأشياء، وكله على الله هين يسير).');
INSERT INTO Quran__Arabic_Tafseer_Muyassar (QuranChapterID, VerseNumber, Rukuh, Sajdah, Manzil, HizbQuarter, VerseText) VALUES (79, 28, '0', '0', '0', '0', 'أبَعْثُكم أيها الناس- بعد الموت أشد في تقديركم أم خلق السماء؟ رفعها فوقكم كالبناء، وأعلى سقفها في الهواء لا تفاوت فيها ولا فطور، وأظلم ليلها بغروب شمسها، وأبرز نهارها بشروقها. والأرض بعد خلق السماء بسطها، وأودع فيها منافعها، وفجَّر فيها عيون الماء، وأنبت فيها ما يُرعى من النباتات، وأثبت فيها الجبال أوتادًا لها. خلق سبحانه كل هذه النعم منفعة لكم ولأنعامكم. (إن إعادة خلقكم يوم القيامة أهون على الله من خلق هذه الأشياء، وكله على الله هين يسير).');
