INSERT INTO Quran__Arabic_Tafseer_Muyassar (QuranChapterID, VerseNumber, Rukuh, Sajdah, Manzil, HizbQuarter, VerseText) VALUES (79, 34, '0', '0', '0', '0', 'فإذا جاءت القيامة الكبرى والشدة العظمى وهي النفخة الثانية، عندئذ يُعْرَض على الإنسان كل عمله من خير وشر، فيتذكره ويعترف به، وأُظهرت جهنم لكل مُبْصِر تُرى عِيانًا.');
INSERT INTO Quran__Arabic_Tafseer_Muyassar (QuranChapterID, VerseNumber, Rukuh, Sajdah, Manzil, HizbQuarter, VerseText) VALUES (79, 35, '0', '0', '0', '0', 'فإذا جاءت القيامة الكبرى والشدة العظمى وهي النفخة الثانية، عندئذ يُعْرَض على الإنسان كل عمله من خير وشر، فيتذكره ويعترف به، وأُظهرت جهنم لكل مُبْصِر تُرى عِيانًا.');
INSERT INTO Quran__Arabic_Tafseer_Muyassar (QuranChapterID, VerseNumber, Rukuh, Sajdah, Manzil, HizbQuarter, VerseText) VALUES (79, 36, '0', '0', '0', '0', 'فإذا جاءت القيامة الكبرى والشدة العظمى وهي النفخة الثانية، عندئذ يُعْرَض على الإنسان كل عمله من خير وشر، فيتذكره ويعترف به، وأُظهرت جهنم لكل مُبْصِر تُرى عِيانًا.');
INSERT INTO Quran__Arabic_Tafseer_Muyassar (QuranChapterID, VerseNumber, Rukuh, Sajdah, Manzil, HizbQuarter, VerseText) VALUES (79, 37, '0', '0', '0', '0', 'فأمَّا مَن تمرد على أمر الله، وفضل الحياة الدنيا على الآخرة، فإن مصيره إلى النار.');
INSERT INTO Quran__Arabic_Tafseer_Muyassar (QuranChapterID, VerseNumber, Rukuh, Sajdah, Manzil, HizbQuarter, VerseText) VALUES (79, 38, '0', '0', '0', '0', 'فأمَّا مَن تمرد على أمر الله، وفضل الحياة الدنيا على الآخرة، فإن مصيره إلى النار.');
INSERT INTO Quran__Arabic_Tafseer_Muyassar (QuranChapterID, VerseNumber, Rukuh, Sajdah, Manzil, HizbQuarter, VerseText) VALUES (79, 39, '0', '0', '0', '0', 'فأمَّا مَن تمرد على أمر الله، وفضل الحياة الدنيا على الآخرة، فإن مصيره إلى النار.');
INSERT INTO Quran__Arabic_Tafseer_Muyassar (QuranChapterID, VerseNumber, Rukuh, Sajdah, Manzil, HizbQuarter, VerseText) VALUES (79, 40, '0', '0', '0', '0', 'وأمَّا مَنْ خاف القيام بين يدي الله للحساب، ونهى النفس عن الأهواء الفاسدة، فإن الجنة هي مسكنه.');
INSERT INTO Quran__Arabic_Tafseer_Muyassar (QuranChapterID, VerseNumber, Rukuh, Sajdah, Manzil, HizbQuarter, VerseText) VALUES (79, 41, '0', '0', '0', '0', 'وأمَّا مَنْ خاف القيام بين يدي الله للحساب، ونهى النفس عن الأهواء الفاسدة، فإن الجنة هي مسكنه.');
INSERT INTO Quran__Arabic_Tafseer_Muyassar (QuranChapterID, VerseNumber, Rukuh, Sajdah, Manzil, HizbQuarter, VerseText) VALUES (79, 42, '0', '0', '0', '0', 'يسألك المشركون أيها الرسول- استخفافا- عن وقت حلول الساعة التي تتوعدهم بها. لستَ في شيء مِن علمها، بل مرد ذلك إلى الله عز وجل، وإنما شأنك في أمر الساعة أن تحذر منها مَن يخافها. كأنهم يوم يرون قيام الساعة لم يلبثوا في الحياة الدنيا؛ لهول الساعة إلا ما بين الظهر إلى غروب الشمس، أو ما بين طلوع الشمس إلى نصف النهار.');
INSERT INTO Quran__Arabic_Tafseer_Muyassar (QuranChapterID, VerseNumber, Rukuh, Sajdah, Manzil, HizbQuarter, VerseText) VALUES (79, 43, '0', '0', '0', '0', 'يسألك المشركون أيها الرسول- استخفافا- عن وقت حلول الساعة التي تتوعدهم بها. لستَ في شيء مِن علمها، بل مرد ذلك إلى الله عز وجل، وإنما شأنك في أمر الساعة أن تحذر منها مَن يخافها. كأنهم يوم يرون قيام الساعة لم يلبثوا في الحياة الدنيا؛ لهول الساعة إلا ما بين الظهر إلى غروب الشمس، أو ما بين طلوع الشمس إلى نصف النهار.');
INSERT INTO Quran__Arabic_Tafseer_Muyassar (QuranChapterID, VerseNumber, Rukuh, Sajdah, Manzil, HizbQuarter, VerseText) VALUES (79, 44, '0', '0', '0', '0', 'يسألك المشركون أيها الرسول- استخفافا- عن وقت حلول الساعة التي تتوعدهم بها. لستَ في شيء مِن علمها، بل مرد ذلك إلى الله عز وجل، وإنما شأنك في أمر الساعة أن تحذر منها مَن يخافها. كأنهم يوم يرون قيام الساعة لم يلبثوا في الحياة الدنيا؛ لهول الساعة إلا ما بين الظهر إلى غروب الشمس، أو ما بين طلوع الشمس إلى نصف النهار.');
