INSERT INTO Quran__Arabic_Tafseer_Muyassar (QuranChapterID, VerseNumber, Rukuh, Sajdah, Manzil, HizbQuarter, VerseText) VALUES (79, 46, '1', '0', '0', '0', 'يسألك المشركون أيها الرسول- استخفافا- عن وقت حلول الساعة التي تتوعدهم بها. لستَ في شيء مِن علمها، بل مرد ذلك إلى الله عز وجل، وإنما شأنك في أمر الساعة أن تحذر منها مَن يخافها. كأنهم يوم يرون قيام الساعة لم يلبثوا في الحياة الدنيا؛ لهول الساعة إلا ما بين الظهر إلى غروب الشمس، أو ما بين طلوع الشمس إلى نصف النهار.');
INSERT INTO Quran__Arabic_Tafseer_Muyassar (QuranChapterID, VerseNumber, Rukuh, Sajdah, Manzil, HizbQuarter, VerseText) VALUES (80, 1, '0', '0', '0', '1', 'ظهر التغير والعبوس في وجه الرسول صلى الله عليه وسلم، وأعرض لأجل أن الأعمى عبد الله بن أم مكتوم جاءه مسترشدا، وكان الرسول صلى الله عليه وسلم منشغلا بدعوة كبار قريش إلى الإسلام.');
INSERT INTO Quran__Arabic_Tafseer_Muyassar (QuranChapterID, VerseNumber, Rukuh, Sajdah, Manzil, HizbQuarter, VerseText) VALUES (80, 2, '0', '0', '0', '0', 'ظهر التغير والعبوس في وجه الرسول صلى الله عليه وسلم، وأعرض لأجل أن الأعمى عبد الله بن أم مكتوم جاءه مسترشدا، وكان الرسول صلى الله عليه وسلم منشغلا بدعوة كبار قريش إلى الإسلام.');
INSERT INTO Quran__Arabic_Tafseer_Muyassar (QuranChapterID, VerseNumber, Rukuh, Sajdah, Manzil, HizbQuarter, VerseText) VALUES (80, 3, '0', '0', '0', '0', 'وأيُّ شيء يجعلك عالمًا بحقيقة أمره؟ لعله بسؤاله تزكو نفسه وتطهر، أو يحصل له المزيد من الاعتبار والازدجار.');
INSERT INTO Quran__Arabic_Tafseer_Muyassar (QuranChapterID, VerseNumber, Rukuh, Sajdah, Manzil, HizbQuarter, VerseText) VALUES (80, 4, '0', '0', '0', '0', 'وأيُّ شيء يجعلك عالمًا بحقيقة أمره؟ لعله بسؤاله تزكو نفسه وتطهر، أو يحصل له المزيد من الاعتبار والازدجار.');
INSERT INTO Quran__Arabic_Tafseer_Muyassar (QuranChapterID, VerseNumber, Rukuh, Sajdah, Manzil, HizbQuarter, VerseText) VALUES (80, 5, '0', '0', '0', '0', 'أما مَن استغنى عن هديك، فأنت تتعرض له وتصغي لكلامه، وأي شيء عليك ألا يتطهر من كفره؟');
INSERT INTO Quran__Arabic_Tafseer_Muyassar (QuranChapterID, VerseNumber, Rukuh, Sajdah, Manzil, HizbQuarter, VerseText) VALUES (80, 6, '0', '0', '0', '0', 'أما مَن استغنى عن هديك، فأنت تتعرض له وتصغي لكلامه، وأي شيء عليك ألا يتطهر من كفره؟');
INSERT INTO Quran__Arabic_Tafseer_Muyassar (QuranChapterID, VerseNumber, Rukuh, Sajdah, Manzil, HizbQuarter, VerseText) VALUES (80, 7, '0', '0', '0', '0', 'أما مَن استغنى عن هديك، فأنت تتعرض له وتصغي لكلامه، وأي شيء عليك ألا يتطهر من كفره؟');
INSERT INTO Quran__Arabic_Tafseer_Muyassar (QuranChapterID, VerseNumber, Rukuh, Sajdah, Manzil, HizbQuarter, VerseText) VALUES (80, 8, '0', '0', '0', '0', 'وأمَّا من كان حريصا على لقائك، وهو يخشى الله من التقصير في الاسترشاد، فأنت عنه تتشاغل. ليس الأمر كما فعلت أيها الرسول، إن هذه السورة موعظة لك ولكل من شاء الاتعاظ. فمن شاء ذكر الله وَأْتَمَّ بوحيه. هذا الوحي، وهو القرآن في صحف معظمة، موقرة، عالية القدر مطهرة من الدنس والزيادة والنقص، بأيدي ملائكة كتبة، سفراء بين الله وخلقه، كرام الخلق، أخلاقهم وأفعالهم بارة طاهرة.');
INSERT INTO Quran__Arabic_Tafseer_Muyassar (QuranChapterID, VerseNumber, Rukuh, Sajdah, Manzil, HizbQuarter, VerseText) VALUES (80, 9, '0', '0', '0', '0', 'وأمَّا من كان حريصا على لقائك، وهو يخشى الله من التقصير في الاسترشاد، فأنت عنه تتشاغل. ليس الأمر كما فعلت أيها الرسول، إن هذه السورة موعظة لك ولكل من شاء الاتعاظ. فمن شاء ذكر الله وَأْتَمَّ بوحيه. هذا الوحي، وهو القرآن في صحف معظمة، موقرة، عالية القدر مطهرة من الدنس والزيادة والنقص، بأيدي ملائكة كتبة، سفراء بين الله وخلقه، كرام الخلق، أخلاقهم وأفعالهم بارة طاهرة.');
