INSERT INTO Quran__Arabic_Tafseer_Muyassar (QuranChapterID, VerseNumber, Rukuh, Sajdah, Manzil, HizbQuarter, VerseText) VALUES (80, 33, '0', '0', '0', '0', 'فإذا جاءت صيحة يوم القيامة التي تصمُّ مِن هولها الأسماع، يوم يفرُّ المرء لهول ذلك اليوم من أخيه، وأمه وأبيه، وزوجه وبنيه. لكل واحد منهم يومئذٍ أمر يشغله ويمنعه من الانشغال بغيره.');
INSERT INTO Quran__Arabic_Tafseer_Muyassar (QuranChapterID, VerseNumber, Rukuh, Sajdah, Manzil, HizbQuarter, VerseText) VALUES (80, 34, '0', '0', '0', '0', 'فإذا جاءت صيحة يوم القيامة التي تصمُّ مِن هولها الأسماع، يوم يفرُّ المرء لهول ذلك اليوم من أخيه، وأمه وأبيه، وزوجه وبنيه. لكل واحد منهم يومئذٍ أمر يشغله ويمنعه من الانشغال بغيره.');
