INSERT INTO Quran__Arabic_Tafseer_Muyassar (QuranChapterID, VerseNumber, Rukuh, Sajdah, Manzil, HizbQuarter, VerseText) VALUES (80, 38, '0', '0', '0', '0', 'وجوه أهل النعيم في ذلك اليوم مستنيرة، مسرورة فرحة، ووجوه أهل الجحيم مظلمة مسودَّة، تغشاها ذلَّة. أولئك الموصوفون بهذا الوصف هم الذين كفروا بنعم الله وكذَّبوا بآياته، وتجرؤوا على محارمه بالفجور والطغيان.');
INSERT INTO Quran__Arabic_Tafseer_Muyassar (QuranChapterID, VerseNumber, Rukuh, Sajdah, Manzil, HizbQuarter, VerseText) VALUES (80, 39, '0', '0', '0', '0', 'وجوه أهل النعيم في ذلك اليوم مستنيرة، مسرورة فرحة، ووجوه أهل الجحيم مظلمة مسودَّة، تغشاها ذلَّة. أولئك الموصوفون بهذا الوصف هم الذين كفروا بنعم الله وكذَّبوا بآياته، وتجرؤوا على محارمه بالفجور والطغيان.');
