INSERT INTO Quran__Arabic_Tafseer_Muyassar (QuranChapterID, VerseNumber, Rukuh, Sajdah, Manzil, HizbQuarter, VerseText) VALUES (83, 33, '0', '0', '0', '0', 'إن الذين أجرموا كانوا في الدنيا يستهزئون بالمؤمنين، وإذا مروا بهم يتغامزون سخرية بهم، وإذا رجع الذين أجرموا إلى أهلهم وذويهم تفكهوا معهم بالسخرية من المؤمنين. وإذا رأى هؤلاء الكفار أصحاب محمد صلى الله عليه وسلم، وقد اتبعوا الهدى قالوا: إن هؤلاء لتائهون في اتباعهم محمدًا صلى الله عليه وسلم، وما بُعث هؤلاء المجرمون رقباء على أصحاب محمد صلى الله عليه وسلم. فيوم القيامة يسخر الذين صدقوا الله ورسوله وعملوا بشرعه من الكفار، كما سخر الكافرون منهم في الدنيا.');
INSERT INTO Quran__Arabic_Tafseer_Muyassar (QuranChapterID, VerseNumber, Rukuh, Sajdah, Manzil, HizbQuarter, VerseText) VALUES (83, 34, '0', '0', '0', '0', 'إن الذين أجرموا كانوا في الدنيا يستهزئون بالمؤمنين، وإذا مروا بهم يتغامزون سخرية بهم، وإذا رجع الذين أجرموا إلى أهلهم وذويهم تفكهوا معهم بالسخرية من المؤمنين. وإذا رأى هؤلاء الكفار أصحاب محمد صلى الله عليه وسلم، وقد اتبعوا الهدى قالوا: إن هؤلاء لتائهون في اتباعهم محمدًا صلى الله عليه وسلم، وما بُعث هؤلاء المجرمون رقباء على أصحاب محمد صلى الله عليه وسلم. فيوم القيامة يسخر الذين صدقوا الله ورسوله وعملوا بشرعه من الكفار، كما سخر الكافرون منهم في الدنيا.');
INSERT INTO Quran__Arabic_Tafseer_Muyassar (QuranChapterID, VerseNumber, Rukuh, Sajdah, Manzil, HizbQuarter, VerseText) VALUES (83, 35, '0', '0', '0', '0', 'على المجالس الفاخرة ينظر المؤمنون إلى ما أعطاهم الله من الكرامة والنعيم في الجنة، ومن أعظم ذلك النظر إلى وجه الله الكريم. هل جوزي الكفار - إذ فُعل بهم ذلك- جزاءً وفاق ما كانوا يفعلونه في الدنيا من الشرور والآثام؟');
INSERT INTO Quran__Arabic_Tafseer_Muyassar (QuranChapterID, VerseNumber, Rukuh, Sajdah, Manzil, HizbQuarter, VerseText) VALUES (83, 36, '1', '0', '0', '0', 'على المجالس الفاخرة ينظر المؤمنون إلى ما أعطاهم الله من الكرامة والنعيم في الجنة، ومن أعظم ذلك النظر إلى وجه الله الكريم. هل جوزي الكفار - إذ فُعل بهم ذلك- جزاءً وفاق ما كانوا يفعلونه في الدنيا من الشرور والآثام؟');
INSERT INTO Quran__Arabic_Tafseer_Muyassar (QuranChapterID, VerseNumber, Rukuh, Sajdah, Manzil, HizbQuarter, VerseText) VALUES (84, 1, '0', '0', '0', '1', 'إذا السماء تصدَّعت، وتفطَّرت بالغمام يوم القيامة، وأطاعت أمر ربها فيما أمرها به من الانشقاق، وحُقَّ لها أن تنقاد لأمره. وإذا الأرض بُسطت وَوُسِّعت، ودكت جبالها في ذلك اليوم، وقذفت ما في بطنها من الأموات، وتخلَّتْ عنهم، وانقادت لربها فيما أمرها به، وحُقَّ لها أن تنقاد لأمره.');
INSERT INTO Quran__Arabic_Tafseer_Muyassar (QuranChapterID, VerseNumber, Rukuh, Sajdah, Manzil, HizbQuarter, VerseText) VALUES (84, 2, '0', '0', '0', '0', 'إذا السماء تصدَّعت، وتفطَّرت بالغمام يوم القيامة، وأطاعت أمر ربها فيما أمرها به من الانشقاق، وحُقَّ لها أن تنقاد لأمره. وإذا الأرض بُسطت وَوُسِّعت، ودكت جبالها في ذلك اليوم، وقذفت ما في بطنها من الأموات، وتخلَّتْ عنهم، وانقادت لربها فيما أمرها به، وحُقَّ لها أن تنقاد لأمره.');
INSERT INTO Quran__Arabic_Tafseer_Muyassar (QuranChapterID, VerseNumber, Rukuh, Sajdah, Manzil, HizbQuarter, VerseText) VALUES (84, 3, '0', '0', '0', '0', 'إذا السماء تصدَّعت، وتفطَّرت بالغمام يوم القيامة، وأطاعت أمر ربها فيما أمرها به من الانشقاق، وحُقَّ لها أن تنقاد لأمره. وإذا الأرض بُسطت وَوُسِّعت، ودكت جبالها في ذلك اليوم، وقذفت ما في بطنها من الأموات، وتخلَّتْ عنهم، وانقادت لربها فيما أمرها به، وحُقَّ لها أن تنقاد لأمره.');
INSERT INTO Quran__Arabic_Tafseer_Muyassar (QuranChapterID, VerseNumber, Rukuh, Sajdah, Manzil, HizbQuarter, VerseText) VALUES (84, 4, '0', '0', '0', '0', 'إذا السماء تصدَّعت، وتفطَّرت بالغمام يوم القيامة، وأطاعت أمر ربها فيما أمرها به من الانشقاق، وحُقَّ لها أن تنقاد لأمره. وإذا الأرض بُسطت وَوُسِّعت، ودكت جبالها في ذلك اليوم، وقذفت ما في بطنها من الأموات، وتخلَّتْ عنهم، وانقادت لربها فيما أمرها به، وحُقَّ لها أن تنقاد لأمره.');
INSERT INTO Quran__Arabic_Tafseer_Muyassar (QuranChapterID, VerseNumber, Rukuh, Sajdah, Manzil, HizbQuarter, VerseText) VALUES (84, 5, '0', '0', '0', '0', 'إذا السماء تصدَّعت، وتفطَّرت بالغمام يوم القيامة، وأطاعت أمر ربها فيما أمرها به من الانشقاق، وحُقَّ لها أن تنقاد لأمره. وإذا الأرض بُسطت وَوُسِّعت، ودكت جبالها في ذلك اليوم، وقذفت ما في بطنها من الأموات، وتخلَّتْ عنهم، وانقادت لربها فيما أمرها به، وحُقَّ لها أن تنقاد لأمره.');
INSERT INTO Quran__Arabic_Tafseer_Muyassar (QuranChapterID, VerseNumber, Rukuh, Sajdah, Manzil, HizbQuarter, VerseText) VALUES (84, 6, '0', '0', '0', '0', 'يا أيها الإنسان إنك ساعٍ إلى الله، وعامل أعمالا من خير أو شر، ثم تلاقي الله يوم القيامة، فيجازيك بعملك بفضله أو عدله.');
INSERT INTO Quran__Arabic_Tafseer_Muyassar (QuranChapterID, VerseNumber, Rukuh, Sajdah, Manzil, HizbQuarter, VerseText) VALUES (84, 7, '0', '0', '0', '0', 'فأما من أعطي صحيفة أعماله بيمينه، وهو مؤمن بربه، فسوف يحاسب حسابًا سهلا ويرجع إلى أهله في الجنة مسرورًا.');
INSERT INTO Quran__Arabic_Tafseer_Muyassar (QuranChapterID, VerseNumber, Rukuh, Sajdah, Manzil, HizbQuarter, VerseText) VALUES (84, 8, '0', '0', '0', '0', 'فأما من أعطي صحيفة أعماله بيمينه، وهو مؤمن بربه، فسوف يحاسب حسابًا سهلا ويرجع إلى أهله في الجنة مسرورًا.');
INSERT INTO Quran__Arabic_Tafseer_Muyassar (QuranChapterID, VerseNumber, Rukuh, Sajdah, Manzil, HizbQuarter, VerseText) VALUES (84, 9, '0', '0', '0', '0', 'فأما من أعطي صحيفة أعماله بيمينه، وهو مؤمن بربه، فسوف يحاسب حسابًا سهلا ويرجع إلى أهله في الجنة مسرورًا.');
INSERT INTO Quran__Arabic_Tafseer_Muyassar (QuranChapterID, VerseNumber, Rukuh, Sajdah, Manzil, HizbQuarter, VerseText) VALUES (84, 10, '0', '0', '0', '0', 'وأمَّا مَن أُعطي صحيفة أعماله من وراء ظهره، وهو الكافر بالله، فسوف يدعو بالهلاك والثبور، ويدخل النار مقاسيًا حرها. إنه كان في أهله في الدنيا مسرورًا مغرورًا، لا يفكر في العواقب، إنه ظنَّ أن لن يرجع إلى خالقه حيا للحساب. بلى سيعيده الله كما بدأه ويجازيه على أعماله، إن ربه كان به بصيرًا عليمًا بحاله من يوم خلقه إلى أن بعثه.');
INSERT INTO Quran__Arabic_Tafseer_Muyassar (QuranChapterID, VerseNumber, Rukuh, Sajdah, Manzil, HizbQuarter, VerseText) VALUES (84, 11, '0', '0', '0', '0', 'وأمَّا مَن أُعطي صحيفة أعماله من وراء ظهره، وهو الكافر بالله، فسوف يدعو بالهلاك والثبور، ويدخل النار مقاسيًا حرها. إنه كان في أهله في الدنيا مسرورًا مغرورًا، لا يفكر في العواقب، إنه ظنَّ أن لن يرجع إلى خالقه حيا للحساب. بلى سيعيده الله كما بدأه ويجازيه على أعماله، إن ربه كان به بصيرًا عليمًا بحاله من يوم خلقه إلى أن بعثه.');
