INSERT INTO Quran__Arabic_Tafseer_Muyassar (QuranChapterID, VerseNumber, Rukuh, Sajdah, Manzil, HizbQuarter, VerseText) VALUES (85, 22, '1', '0', '0', '0', 'هل بلغك -أيها الرسول- خبر الجموع الكافرة المكذبة لأنبيائها، فرعون وثمود، وما حلَّ بهم من العذاب والنكال، لم يعتبر القوم بذلك، بل الذين كفروا في تكذيب متواصل كدأب مَن قبلهم، والله قد أحاط بهم علما وقدرة، لا يخفى عليه منهم ومن أعمالهم شيء. وليس القرآن كما زعم المكذبون المشركون بأنه شعر وسحر، فكذَّبوا به، بل هو قرآن عظيم كريم، في لوح محفوظ، لا يناله تبديل ولا تحريف.');
INSERT INTO Quran__Arabic_Tafseer_Muyassar (QuranChapterID, VerseNumber, Rukuh, Sajdah, Manzil, HizbQuarter, VerseText) VALUES (86, 1, '0', '0', '0', '0', 'أقسم الله سبحانه بالسماء والنجم الذي يطرق ليلا وما أدراك ما عِظَمُ هذا النجم؟ هو النجم المضيء المتوهِّج. ما كل نفس إلا أوكل بها مَلَك رقيب يحفظ عليها أعمالها لتحاسب عليها يوم القيامة.');
INSERT INTO Quran__Arabic_Tafseer_Muyassar (QuranChapterID, VerseNumber, Rukuh, Sajdah, Manzil, HizbQuarter, VerseText) VALUES (86, 2, '0', '0', '0', '0', 'أقسم الله سبحانه بالسماء والنجم الذي يطرق ليلا وما أدراك ما عِظَمُ هذا النجم؟ هو النجم المضيء المتوهِّج. ما كل نفس إلا أوكل بها مَلَك رقيب يحفظ عليها أعمالها لتحاسب عليها يوم القيامة.');
INSERT INTO Quran__Arabic_Tafseer_Muyassar (QuranChapterID, VerseNumber, Rukuh, Sajdah, Manzil, HizbQuarter, VerseText) VALUES (86, 3, '0', '0', '0', '0', 'أقسم الله سبحانه بالسماء والنجم الذي يطرق ليلا وما أدراك ما عِظَمُ هذا النجم؟ هو النجم المضيء المتوهِّج. ما كل نفس إلا أوكل بها مَلَك رقيب يحفظ عليها أعمالها لتحاسب عليها يوم القيامة.');
INSERT INTO Quran__Arabic_Tafseer_Muyassar (QuranChapterID, VerseNumber, Rukuh, Sajdah, Manzil, HizbQuarter, VerseText) VALUES (86, 4, '0', '0', '0', '0', 'أقسم الله سبحانه بالسماء والنجم الذي يطرق ليلا وما أدراك ما عِظَمُ هذا النجم؟ هو النجم المضيء المتوهِّج. ما كل نفس إلا أوكل بها مَلَك رقيب يحفظ عليها أعمالها لتحاسب عليها يوم القيامة.');
INSERT INTO Quran__Arabic_Tafseer_Muyassar (QuranChapterID, VerseNumber, Rukuh, Sajdah, Manzil, HizbQuarter, VerseText) VALUES (86, 5, '0', '0', '0', '0', 'فلينظر الإنسان المنكر للبعث مِمَّ خُلِقَ؟ ليعلم أن إعادة خلق الإنسان ليست أصعب من خلقه أوّلا خلق من منيٍّ منصبٍّ بسرعة في الرحم، يخرج من بين صلب الرجل وصدر المرأة. إن الذي خلق الإنسان من هذا الماء لَقادر على رجعه إلى الحياة بعد الموت.');
INSERT INTO Quran__Arabic_Tafseer_Muyassar (QuranChapterID, VerseNumber, Rukuh, Sajdah, Manzil, HizbQuarter, VerseText) VALUES (86, 6, '0', '0', '0', '0', 'فلينظر الإنسان المنكر للبعث مِمَّ خُلِقَ؟ ليعلم أن إعادة خلق الإنسان ليست أصعب من خلقه أوّلا خلق من منيٍّ منصبٍّ بسرعة في الرحم، يخرج من بين صلب الرجل وصدر المرأة. إن الذي خلق الإنسان من هذا الماء لَقادر على رجعه إلى الحياة بعد الموت.');
INSERT INTO Quran__Arabic_Tafseer_Muyassar (QuranChapterID, VerseNumber, Rukuh, Sajdah, Manzil, HizbQuarter, VerseText) VALUES (86, 7, '0', '0', '0', '0', 'فلينظر الإنسان المنكر للبعث مِمَّ خُلِقَ؟ ليعلم أن إعادة خلق الإنسان ليست أصعب من خلقه أوّلا خلق من منيٍّ منصبٍّ بسرعة في الرحم، يخرج من بين صلب الرجل وصدر المرأة. إن الذي خلق الإنسان من هذا الماء لَقادر على رجعه إلى الحياة بعد الموت.');
INSERT INTO Quran__Arabic_Tafseer_Muyassar (QuranChapterID, VerseNumber, Rukuh, Sajdah, Manzil, HizbQuarter, VerseText) VALUES (86, 8, '0', '0', '0', '0', 'فلينظر الإنسان المنكر للبعث مِمَّ خُلِقَ؟ ليعلم أن إعادة خلق الإنسان ليست أصعب من خلقه أوّلا خلق من منيٍّ منصبٍّ بسرعة في الرحم، يخرج من بين صلب الرجل وصدر المرأة. إن الذي خلق الإنسان من هذا الماء لَقادر على رجعه إلى الحياة بعد الموت.');
INSERT INTO Quran__Arabic_Tafseer_Muyassar (QuranChapterID, VerseNumber, Rukuh, Sajdah, Manzil, HizbQuarter, VerseText) VALUES (86, 9, '0', '0', '0', '0', 'يوم تُخْتَبر السرائر فيما أخفته، ويُمَيَّز الصالح منها من الفاسد، فما للإنسان من قوة يدفع بها عن نفسه، وما له من ناصر يدفع عنه عذاب الله.');
INSERT INTO Quran__Arabic_Tafseer_Muyassar (QuranChapterID, VerseNumber, Rukuh, Sajdah, Manzil, HizbQuarter, VerseText) VALUES (86, 10, '0', '0', '0', '0', 'يوم تُخْتَبر السرائر فيما أخفته، ويُمَيَّز الصالح منها من الفاسد، فما للإنسان من قوة يدفع بها عن نفسه، وما له من ناصر يدفع عنه عذاب الله.');
INSERT INTO Quran__Arabic_Tafseer_Muyassar (QuranChapterID, VerseNumber, Rukuh, Sajdah, Manzil, HizbQuarter, VerseText) VALUES (86, 11, '0', '0', '0', '0', 'والسماء ذات المطر المتكرر، والأرض ذات التشقق بما يتخللها من نبات، إن القرآن لقول فصل بَيْنَ الحق والباطل، وما هو بالهزل. ولا يجوز للمخلوق أن يقسم بغير الله، وإلا فقد أشرك.');
INSERT INTO Quran__Arabic_Tafseer_Muyassar (QuranChapterID, VerseNumber, Rukuh, Sajdah, Manzil, HizbQuarter, VerseText) VALUES (86, 12, '0', '0', '0', '0', 'والسماء ذات المطر المتكرر، والأرض ذات التشقق بما يتخللها من نبات، إن القرآن لقول فصل بَيْنَ الحق والباطل، وما هو بالهزل. ولا يجوز للمخلوق أن يقسم بغير الله، وإلا فقد أشرك.');
INSERT INTO Quran__Arabic_Tafseer_Muyassar (QuranChapterID, VerseNumber, Rukuh, Sajdah, Manzil, HizbQuarter, VerseText) VALUES (86, 13, '0', '0', '0', '0', 'والسماء ذات المطر المتكرر، والأرض ذات التشقق بما يتخللها من نبات، إن القرآن لقول فصل بَيْنَ الحق والباطل، وما هو بالهزل. ولا يجوز للمخلوق أن يقسم بغير الله، وإلا فقد أشرك.');
INSERT INTO Quran__Arabic_Tafseer_Muyassar (QuranChapterID, VerseNumber, Rukuh, Sajdah, Manzil, HizbQuarter, VerseText) VALUES (86, 14, '0', '0', '0', '0', 'والسماء ذات المطر المتكرر، والأرض ذات التشقق بما يتخللها من نبات، إن القرآن لقول فصل بَيْنَ الحق والباطل، وما هو بالهزل. ولا يجوز للمخلوق أن يقسم بغير الله، وإلا فقد أشرك.');
INSERT INTO Quran__Arabic_Tafseer_Muyassar (QuranChapterID, VerseNumber, Rukuh, Sajdah, Manzil, HizbQuarter, VerseText) VALUES (86, 15, '0', '0', '0', '0', 'إن المكذبين للرسول صلى الله عليه وسلم، وللقرآن، يكيدون ويدبرون؛ ليدفعوا بكيدهم الحق ويؤيدوا الباطل، وأكيد كيدًا لإظهار الحق، ولو كره الكافرون، فلا تستعجل لهم -أيها الرسول- بطلب إنزال العقاب بهم، بل أمهلهم وأنظرهم قليلا ولا تستعجل لهم، وسترى ما يحلُّ بهم من العذاب والنكال والعقوبة والهلاك.');
INSERT INTO Quran__Arabic_Tafseer_Muyassar (QuranChapterID, VerseNumber, Rukuh, Sajdah, Manzil, HizbQuarter, VerseText) VALUES (86, 16, '0', '0', '0', '0', 'إن المكذبين للرسول صلى الله عليه وسلم، وللقرآن، يكيدون ويدبرون؛ ليدفعوا بكيدهم الحق ويؤيدوا الباطل، وأكيد كيدًا لإظهار الحق، ولو كره الكافرون، فلا تستعجل لهم -أيها الرسول- بطلب إنزال العقاب بهم، بل أمهلهم وأنظرهم قليلا ولا تستعجل لهم، وسترى ما يحلُّ بهم من العذاب والنكال والعقوبة والهلاك.');
INSERT INTO Quran__Arabic_Tafseer_Muyassar (QuranChapterID, VerseNumber, Rukuh, Sajdah, Manzil, HizbQuarter, VerseText) VALUES (86, 17, '1', '0', '0', '0', 'إن المكذبين للرسول صلى الله عليه وسلم، وللقرآن، يكيدون ويدبرون؛ ليدفعوا بكيدهم الحق ويؤيدوا الباطل، وأكيد كيدًا لإظهار الحق، ولو كره الكافرون، فلا تستعجل لهم -أيها الرسول- بطلب إنزال العقاب بهم، بل أمهلهم وأنظرهم قليلا ولا تستعجل لهم، وسترى ما يحلُّ بهم من العذاب والنكال والعقوبة والهلاك.');
INSERT INTO Quran__Arabic_Tafseer_Muyassar (QuranChapterID, VerseNumber, Rukuh, Sajdah, Manzil, HizbQuarter, VerseText) VALUES (87, 1, '0', '0', '0', '1', 'نَزِّه اسم ربك الأعلى عن الشريك والنقائص تنزيهًا يليق بعظمته سبحانه، الذي خلق المخلوقات، فأتقن خلقها، وأحسنه، والذي قدَّر جميع المقدرات، فهدى كل خلق إلى ما يناسبه، والذي أنبت الكلأ الأخضر، فجعله بعد ذلك هشيمًا جافًا متغيرًا.');
INSERT INTO Quran__Arabic_Tafseer_Muyassar (QuranChapterID, VerseNumber, Rukuh, Sajdah, Manzil, HizbQuarter, VerseText) VALUES (87, 2, '0', '0', '0', '0', 'نَزِّه اسم ربك الأعلى عن الشريك والنقائص تنزيهًا يليق بعظمته سبحانه، الذي خلق المخلوقات، فأتقن خلقها، وأحسنه، والذي قدَّر جميع المقدرات، فهدى كل خلق إلى ما يناسبه، والذي أنبت الكلأ الأخضر، فجعله بعد ذلك هشيمًا جافًا متغيرًا.');
INSERT INTO Quran__Arabic_Tafseer_Muyassar (QuranChapterID, VerseNumber, Rukuh, Sajdah, Manzil, HizbQuarter, VerseText) VALUES (87, 3, '0', '0', '0', '0', 'نَزِّه اسم ربك الأعلى عن الشريك والنقائص تنزيهًا يليق بعظمته سبحانه، الذي خلق المخلوقات، فأتقن خلقها، وأحسنه، والذي قدَّر جميع المقدرات، فهدى كل خلق إلى ما يناسبه، والذي أنبت الكلأ الأخضر، فجعله بعد ذلك هشيمًا جافًا متغيرًا.');
INSERT INTO Quran__Arabic_Tafseer_Muyassar (QuranChapterID, VerseNumber, Rukuh, Sajdah, Manzil, HizbQuarter, VerseText) VALUES (87, 4, '0', '0', '0', '0', 'نَزِّه اسم ربك الأعلى عن الشريك والنقائص تنزيهًا يليق بعظمته سبحانه، الذي خلق المخلوقات، فأتقن خلقها، وأحسنه، والذي قدَّر جميع المقدرات، فهدى كل خلق إلى ما يناسبه، والذي أنبت الكلأ الأخضر، فجعله بعد ذلك هشيمًا جافًا متغيرًا.');
INSERT INTO Quran__Arabic_Tafseer_Muyassar (QuranChapterID, VerseNumber, Rukuh, Sajdah, Manzil, HizbQuarter, VerseText) VALUES (87, 5, '0', '0', '0', '0', 'نَزِّه اسم ربك الأعلى عن الشريك والنقائص تنزيهًا يليق بعظمته سبحانه، الذي خلق المخلوقات، فأتقن خلقها، وأحسنه، والذي قدَّر جميع المقدرات، فهدى كل خلق إلى ما يناسبه، والذي أنبت الكلأ الأخضر، فجعله بعد ذلك هشيمًا جافًا متغيرًا.');
INSERT INTO Quran__Arabic_Tafseer_Muyassar (QuranChapterID, VerseNumber, Rukuh, Sajdah, Manzil, HizbQuarter, VerseText) VALUES (87, 6, '0', '0', '0', '0', 'سنقرئك -أيها الرسول- هذا القرآن قراءة لا تنساها، إلا ما شاء الله مما اقتضت حكمته أن ينسيه لمصلحة يعلمها. إنه - سبحانه- يعلم الجهر من القول والعمل، وما يخفى منهما.');
INSERT INTO Quran__Arabic_Tafseer_Muyassar (QuranChapterID, VerseNumber, Rukuh, Sajdah, Manzil, HizbQuarter, VerseText) VALUES (87, 7, '0', '0', '0', '0', 'سنقرئك -أيها الرسول- هذا القرآن قراءة لا تنساها، إلا ما شاء الله مما اقتضت حكمته أن ينسيه لمصلحة يعلمها. إنه - سبحانه- يعلم الجهر من القول والعمل، وما يخفى منهما.');
INSERT INTO Quran__Arabic_Tafseer_Muyassar (QuranChapterID, VerseNumber, Rukuh, Sajdah, Manzil, HizbQuarter, VerseText) VALUES (87, 8, '0', '0', '0', '0', 'ونيسرك لليسرى في جميع أمورك، ومن ذلك تسهيل تَلَقِّي أعباء الرسالة، وجعل دينك يسرًا لا عسر فيه.');
INSERT INTO Quran__Arabic_Tafseer_Muyassar (QuranChapterID, VerseNumber, Rukuh, Sajdah, Manzil, HizbQuarter, VerseText) VALUES (87, 9, '0', '0', '0', '0', 'فعظ قومك -أيها الرسول- حسبما يسرناه لك بما يوحى إليك، واهدهم إلى ما فيه خيرهم. وخُصَّ بالتذكير من يرجى منه التذكُّر، ولا تتعب نفسك في تذكير من لا يورثه التذكر إلا عتوًّا ونفورًا.');
INSERT INTO Quran__Arabic_Tafseer_Muyassar (QuranChapterID, VerseNumber, Rukuh, Sajdah, Manzil, HizbQuarter, VerseText) VALUES (87, 10, '0', '0', '0', '0', 'سيتعظ الذي يخاف ربه، ويبتعد عن الذكرى الأشقى الذي لا يخشى ربه، الذي سيدخل نار جهنم العظمى يقاسي حرَّها، ثم لا يموت فيها فيستريح، ولا يحيا حياة تنفعه.');
INSERT INTO Quran__Arabic_Tafseer_Muyassar (QuranChapterID, VerseNumber, Rukuh, Sajdah, Manzil, HizbQuarter, VerseText) VALUES (87, 11, '0', '0', '0', '0', 'سيتعظ الذي يخاف ربه، ويبتعد عن الذكرى الأشقى الذي لا يخشى ربه، الذي سيدخل نار جهنم العظمى يقاسي حرَّها، ثم لا يموت فيها فيستريح، ولا يحيا حياة تنفعه.');
INSERT INTO Quran__Arabic_Tafseer_Muyassar (QuranChapterID, VerseNumber, Rukuh, Sajdah, Manzil, HizbQuarter, VerseText) VALUES (87, 12, '0', '0', '0', '0', 'سيتعظ الذي يخاف ربه، ويبتعد عن الذكرى الأشقى الذي لا يخشى ربه، الذي سيدخل نار جهنم العظمى يقاسي حرَّها، ثم لا يموت فيها فيستريح، ولا يحيا حياة تنفعه.');
INSERT INTO Quran__Arabic_Tafseer_Muyassar (QuranChapterID, VerseNumber, Rukuh, Sajdah, Manzil, HizbQuarter, VerseText) VALUES (87, 13, '0', '0', '0', '0', 'سيتعظ الذي يخاف ربه، ويبتعد عن الذكرى الأشقى الذي لا يخشى ربه، الذي سيدخل نار جهنم العظمى يقاسي حرَّها، ثم لا يموت فيها فيستريح، ولا يحيا حياة تنفعه.');
INSERT INTO Quran__Arabic_Tafseer_Muyassar (QuranChapterID, VerseNumber, Rukuh, Sajdah, Manzil, HizbQuarter, VerseText) VALUES (87, 14, '0', '0', '0', '0', 'قد فاز مَن طهر نفسه من الأخلاق السيئة، وذكر الله، فوحَّده ودعاه وعمل بما يرضيه، وأقام الصلاة في أوقاتها؛ ابتغاء رضوان الله وامتثالا لشرعه.');
INSERT INTO Quran__Arabic_Tafseer_Muyassar (QuranChapterID, VerseNumber, Rukuh, Sajdah, Manzil, HizbQuarter, VerseText) VALUES (87, 15, '0', '0', '0', '0', 'قد فاز مَن طهر نفسه من الأخلاق السيئة، وذكر الله، فوحَّده ودعاه وعمل بما يرضيه، وأقام الصلاة في أوقاتها؛ ابتغاء رضوان الله وامتثالا لشرعه.');
INSERT INTO Quran__Arabic_Tafseer_Muyassar (QuranChapterID, VerseNumber, Rukuh, Sajdah, Manzil, HizbQuarter, VerseText) VALUES (87, 16, '0', '0', '0', '0', 'إنكم -أيها الناس- تفضِّلون زينة الحياة الدنيا على نعيم الآخرة.');
INSERT INTO Quran__Arabic_Tafseer_Muyassar (QuranChapterID, VerseNumber, Rukuh, Sajdah, Manzil, HizbQuarter, VerseText) VALUES (87, 17, '0', '0', '0', '0', 'والدار الآخرة بما فيها من النعيم المقيم، خير من الدنيا وأبقى.');
INSERT INTO Quran__Arabic_Tafseer_Muyassar (QuranChapterID, VerseNumber, Rukuh, Sajdah, Manzil, HizbQuarter, VerseText) VALUES (87, 18, '0', '0', '0', '0', 'إن ما أخبرتم به في هذه السورة هو مما ثبت معناه في الصُّحف التي أنزلت قبل القرآن، وهي صُحف إبراهيم وموسى عليهما السلام.');
INSERT INTO Quran__Arabic_Tafseer_Muyassar (QuranChapterID, VerseNumber, Rukuh, Sajdah, Manzil, HizbQuarter, VerseText) VALUES (87, 19, '1', '0', '0', '0', 'إن ما أخبرتم به في هذه السورة هو مما ثبت معناه في الصُّحف التي أنزلت قبل القرآن، وهي صُحف إبراهيم وموسى عليهما السلام.');
INSERT INTO Quran__Arabic_Tafseer_Muyassar (QuranChapterID, VerseNumber, Rukuh, Sajdah, Manzil, HizbQuarter, VerseText) VALUES (88, 1, '0', '0', '0', '0', 'هل أتاك -أيها الرسول- خبر القيامة التي تغشى الناس بأهوالها؟');
INSERT INTO Quran__Arabic_Tafseer_Muyassar (QuranChapterID, VerseNumber, Rukuh, Sajdah, Manzil, HizbQuarter, VerseText) VALUES (88, 2, '0', '0', '0', '0', 'وجوه الكفار يومئذ ذليلة بالعذاب، مجهدة بالعمل متعبة، تصيبها نار شديدة التوهج، تُسقى من عين شديدة الحرارة. ليس لأصحاب النار طعام إلا من نبت ذي شوك لاصق بالأرض، وهو مِن شر الطعام وأخبثه، لا يُسْمن بدن صاحبه من الهُزال، ولا يسدُّ جوعه ورمقه.');
INSERT INTO Quran__Arabic_Tafseer_Muyassar (QuranChapterID, VerseNumber, Rukuh, Sajdah, Manzil, HizbQuarter, VerseText) VALUES (88, 3, '0', '0', '0', '0', 'وجوه الكفار يومئذ ذليلة بالعذاب، مجهدة بالعمل متعبة، تصيبها نار شديدة التوهج، تُسقى من عين شديدة الحرارة. ليس لأصحاب النار طعام إلا من نبت ذي شوك لاصق بالأرض، وهو مِن شر الطعام وأخبثه، لا يُسْمن بدن صاحبه من الهُزال، ولا يسدُّ جوعه ورمقه.');
INSERT INTO Quran__Arabic_Tafseer_Muyassar (QuranChapterID, VerseNumber, Rukuh, Sajdah, Manzil, HizbQuarter, VerseText) VALUES (88, 4, '0', '0', '0', '0', 'وجوه الكفار يومئذ ذليلة بالعذاب، مجهدة بالعمل متعبة، تصيبها نار شديدة التوهج، تُسقى من عين شديدة الحرارة. ليس لأصحاب النار طعام إلا من نبت ذي شوك لاصق بالأرض، وهو مِن شر الطعام وأخبثه، لا يُسْمن بدن صاحبه من الهُزال، ولا يسدُّ جوعه ورمقه.');
