INSERT INTO Quran__Arabic_Tafseer_Muyassar (QuranChapterID, VerseNumber, Rukuh, Sajdah, Manzil, HizbQuarter, VerseText) VALUES (91, 15, '1', '0', '0', '0', 'كذَّبت ثمود نبيها ببلوغها الغاية في العصيان، إذ نهض أكثر القبيلة شقاوة لعقر الناقة، فقال لهم رسول الله صالح عليه السلام: احذروا أن تمسوا الناقة بسوء؛ فإنها آية أرسلها الله إليكم، تدل على صدق نبيكم، واحذروا أن تعتدوا على سقيها، فإن لها شِرْب يوم ولكم شِرْب يوم معلوم. فشق عليهم ذلك، فكذبوه فيما توعَّدهم به فنحروها، فأطبق عليهم ربهم العقوبة بجرمهم، فجعلها عليهم على السواء فلم يُفْلِت منهم أحد. ولا يخاف- جلت قدرته- تبعة ما أنزله بهم من شديد العقاب.');
INSERT INTO Quran__Arabic_Tafseer_Muyassar (QuranChapterID, VerseNumber, Rukuh, Sajdah, Manzil, HizbQuarter, VerseText) VALUES (92, 1, '0', '0', '0', '0', 'أقسم الله سبحانه بالليل عندما يغطي بظلامه الأرض وما عليها، وبالنهار إذا انكشف عن ظلام الليل بضيائه، وبخلق الزوجين: الذكر والأنثى. إن عملكم لمختلف بين عامل للدنيا وعامل للآخرة.');
INSERT INTO Quran__Arabic_Tafseer_Muyassar (QuranChapterID, VerseNumber, Rukuh, Sajdah, Manzil, HizbQuarter, VerseText) VALUES (92, 2, '0', '0', '0', '0', 'أقسم الله سبحانه بالليل عندما يغطي بظلامه الأرض وما عليها، وبالنهار إذا انكشف عن ظلام الليل بضيائه، وبخلق الزوجين: الذكر والأنثى. إن عملكم لمختلف بين عامل للدنيا وعامل للآخرة.');
INSERT INTO Quran__Arabic_Tafseer_Muyassar (QuranChapterID, VerseNumber, Rukuh, Sajdah, Manzil, HizbQuarter, VerseText) VALUES (92, 3, '0', '0', '0', '0', 'أقسم الله سبحانه بالليل عندما يغطي بظلامه الأرض وما عليها، وبالنهار إذا انكشف عن ظلام الليل بضيائه، وبخلق الزوجين: الذكر والأنثى. إن عملكم لمختلف بين عامل للدنيا وعامل للآخرة.');
INSERT INTO Quran__Arabic_Tafseer_Muyassar (QuranChapterID, VerseNumber, Rukuh, Sajdah, Manzil, HizbQuarter, VerseText) VALUES (92, 4, '0', '0', '0', '0', 'أقسم الله سبحانه بالليل عندما يغطي بظلامه الأرض وما عليها، وبالنهار إذا انكشف عن ظلام الليل بضيائه، وبخلق الزوجين: الذكر والأنثى. إن عملكم لمختلف بين عامل للدنيا وعامل للآخرة.');
INSERT INTO Quran__Arabic_Tafseer_Muyassar (QuranChapterID, VerseNumber, Rukuh, Sajdah, Manzil, HizbQuarter, VerseText) VALUES (92, 5, '0', '0', '0', '0', 'فأمَّا من بذل من ماله واتقى الله في ذلك، وصدَّق بـ"لا إله إلا الله" وما دلت عليه، وما ترتب عليها من الجزاء، فسنرشده ونوفقه إلى أسباب الخير والصلاح ونيسِّر له أموره.');
INSERT INTO Quran__Arabic_Tafseer_Muyassar (QuranChapterID, VerseNumber, Rukuh, Sajdah, Manzil, HizbQuarter, VerseText) VALUES (92, 6, '0', '0', '0', '0', 'فأمَّا من بذل من ماله واتقى الله في ذلك، وصدَّق بـ"لا إله إلا الله" وما دلت عليه، وما ترتب عليها من الجزاء، فسنرشده ونوفقه إلى أسباب الخير والصلاح ونيسِّر له أموره.');
INSERT INTO Quran__Arabic_Tafseer_Muyassar (QuranChapterID, VerseNumber, Rukuh, Sajdah, Manzil, HizbQuarter, VerseText) VALUES (92, 7, '0', '0', '0', '0', 'فأمَّا من بذل من ماله واتقى الله في ذلك، وصدَّق بـ"لا إله إلا الله" وما دلت عليه، وما ترتب عليها من الجزاء، فسنرشده ونوفقه إلى أسباب الخير والصلاح ونيسِّر له أموره.');
INSERT INTO Quran__Arabic_Tafseer_Muyassar (QuranChapterID, VerseNumber, Rukuh, Sajdah, Manzil, HizbQuarter, VerseText) VALUES (92, 8, '0', '0', '0', '0', 'وأما مَن بخل بماله واستغنى عن جزاء ربه، وكذَّب بـ"لا إله إلا الله" وما دلت عليه، وما ترتب عليها من الجزاء، فسنُيَسِّر له أسباب الشقاء، ولا ينفعه ماله الذي بخل به إذا وقع في النار.');
INSERT INTO Quran__Arabic_Tafseer_Muyassar (QuranChapterID, VerseNumber, Rukuh, Sajdah, Manzil, HizbQuarter, VerseText) VALUES (92, 9, '0', '0', '0', '0', 'وأما مَن بخل بماله واستغنى عن جزاء ربه، وكذَّب بـ"لا إله إلا الله" وما دلت عليه، وما ترتب عليها من الجزاء، فسنُيَسِّر له أسباب الشقاء، ولا ينفعه ماله الذي بخل به إذا وقع في النار.');
INSERT INTO Quran__Arabic_Tafseer_Muyassar (QuranChapterID, VerseNumber, Rukuh, Sajdah, Manzil, HizbQuarter, VerseText) VALUES (92, 10, '0', '0', '0', '0', 'وأما مَن بخل بماله واستغنى عن جزاء ربه، وكذَّب بـ"لا إله إلا الله" وما دلت عليه، وما ترتب عليها من الجزاء، فسنُيَسِّر له أسباب الشقاء، ولا ينفعه ماله الذي بخل به إذا وقع في النار.');
INSERT INTO Quran__Arabic_Tafseer_Muyassar (QuranChapterID, VerseNumber, Rukuh, Sajdah, Manzil, HizbQuarter, VerseText) VALUES (92, 11, '0', '0', '0', '0', 'وأما مَن بخل بماله واستغنى عن جزاء ربه، وكذَّب بـ"لا إله إلا الله" وما دلت عليه، وما ترتب عليها من الجزاء، فسنُيَسِّر له أسباب الشقاء، ولا ينفعه ماله الذي بخل به إذا وقع في النار.');
INSERT INTO Quran__Arabic_Tafseer_Muyassar (QuranChapterID, VerseNumber, Rukuh, Sajdah, Manzil, HizbQuarter, VerseText) VALUES (92, 12, '0', '0', '0', '0', 'إن علينا بفضلنا وحكمتنا أن نبيِّن طريق الهدى الموصل إلى الله وجنته من طريق الضلال، وإن لنا ملك الحياة الآخرة والحياة الدنيا.');
INSERT INTO Quran__Arabic_Tafseer_Muyassar (QuranChapterID, VerseNumber, Rukuh, Sajdah, Manzil, HizbQuarter, VerseText) VALUES (92, 13, '0', '0', '0', '0', 'إن علينا بفضلنا وحكمتنا أن نبيِّن طريق الهدى الموصل إلى الله وجنته من طريق الضلال، وإن لنا ملك الحياة الآخرة والحياة الدنيا.');
INSERT INTO Quran__Arabic_Tafseer_Muyassar (QuranChapterID, VerseNumber, Rukuh, Sajdah, Manzil, HizbQuarter, VerseText) VALUES (92, 14, '0', '0', '0', '0', 'فحذَّرتكم- أيها الناس- وخوَّفتكم نارًا تتوهج، وهي نار جهنم.');
INSERT INTO Quran__Arabic_Tafseer_Muyassar (QuranChapterID, VerseNumber, Rukuh, Sajdah, Manzil, HizbQuarter, VerseText) VALUES (92, 15, '0', '0', '0', '0', 'لا يدخلها إلا مَن كان شديد الشقاء، الذي كذَّب نبي الله محمدًا صلى الله عليه وسلم، وأعرض عن الإيمان بالله ورسوله، وطاعتهما.');
INSERT INTO Quran__Arabic_Tafseer_Muyassar (QuranChapterID, VerseNumber, Rukuh, Sajdah, Manzil, HizbQuarter, VerseText) VALUES (92, 16, '0', '0', '0', '0', 'لا يدخلها إلا مَن كان شديد الشقاء، الذي كذَّب نبي الله محمدًا صلى الله عليه وسلم، وأعرض عن الإيمان بالله ورسوله، وطاعتهما.');
INSERT INTO Quran__Arabic_Tafseer_Muyassar (QuranChapterID, VerseNumber, Rukuh, Sajdah, Manzil, HizbQuarter, VerseText) VALUES (92, 17, '0', '0', '0', '0', 'وسيُزحزَح عنها شديد التقوى، الذي يبذل ماله ابتغاء المزيد من الخير. وليس إنفاقه ذاك مكافأة لمن أسدى إليه معروفا، لكنه يبتغي بذلك وجه ربه الأعلى ورضاه، ولسوف يعطيه الله في الجنة ما يرضى به.');
INSERT INTO Quran__Arabic_Tafseer_Muyassar (QuranChapterID, VerseNumber, Rukuh, Sajdah, Manzil, HizbQuarter, VerseText) VALUES (92, 18, '0', '0', '0', '0', 'وسيُزحزَح عنها شديد التقوى، الذي يبذل ماله ابتغاء المزيد من الخير. وليس إنفاقه ذاك مكافأة لمن أسدى إليه معروفا، لكنه يبتغي بذلك وجه ربه الأعلى ورضاه، ولسوف يعطيه الله في الجنة ما يرضى به.');
